(window.webpackJsonp=window.webpackJsonp||[]).push([[100],{452:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1067-di-tich-cho-sau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1067-di-tich-cho-sau"}},[n._v("#")]),n._v(" Chương 1067: Di tích chỗ sâu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1067: Di tích chỗ sâu"),t("br"),t("br"),n._v("“Khưu Ngô cổ thánh? Hắn là ai?”"),t("br"),t("br"),n._v("Một vị Chiến sư nhìn lại."),t("br"),t("br"),n._v("Dính dáng Thượng Cổ lịch sử, không ít chân chính danh sư đều biết không nhiều, huống chi Chiến sư."),t("br"),t("br"),n._v("“Khưu Ngô cổ thánh là năm đó Khổng sư tùy tùng, cùng Khổng sư chinh chiến nam bắc, cùng Dị Linh tộc đại chiến, giết đến bảy vào bảy ra, là Danh Sư đường trong lịch sử, nổi danh nhất cổ thánh một trong, khi còn sống thực lực vượt qua Thánh vực cửu trọng, đạt đến để cho người ta ngước nhìn cảnh giới...”"),t("br"),t("br"),n._v("Ngô sư giải thích."),t("br"),t("br"),n._v("“Khổng sư tùy tùng?”"),t("br"),t("br"),n._v("“Vượt qua Thánh vực cửu trọng?”"),t("br"),t("br"),n._v("Trước đó không biết, đồng thời líu lưỡi."),t("br"),t("br"),n._v("Không nói cái khác, riêng là hai thứ này, liền làm vô số người ngắm mà lùi bước."),t("br"),t("br"),n._v("Tại Khổng sư bên cạnh, tiếp nhận hắn hun đúc, cùng cấp bậc tất nhiên cực mạnh không nói, càng là vượt qua Thánh vực cửu trọng siêu cấp cường giả... Khó trách bố trí di tích, khủng bố như thế, để bọn hắn suýt chút nữa toàn quân bị diệt."),t("br"),t("br"),n._v("“Khưu Ngô cổ thánh, không chỉ có thực lực mạnh, càng làm người hơn tộc làm ra cực lớn cống hiến, chúng ta trong lòng đất ngăn cản Dị Linh tộc nhân phong ấn, cơ bản đều là hắn năm đó lưu lại... Chúng ta phân bộ cẩu đường chủ, càng là hắn trung thành cầm giữ độn, nếu như biết cái này di tích vì hắn lưu lại, khẳng định đích thân đến...”"),t("br"),t("br"),n._v("Ngô sư nói."),t("br"),t("br"),n._v("“Sức một mình ngăn cản Dị Linh tộc nhân không biết bao nhiêu năm, vị này cổ thánh, quả thực là ta Danh Sư đường kiệt xuất nhất tiền bối một trong...”"),t("br"),t("br"),n._v("“Đúng vậy a, mặc dù công lao cùng Khổng sư so, còn hơi có không bằng, nhưng cũng đủ để tên lưu Thiên Cổ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được Khưu Ngô cổ thánh rất nhiều chuyện, mọi người tất cả đều hai mắt tỏa ánh sáng, lộ ra nồng đậm vẻ sùng bái."),t("br"),t("br"),n._v("Mặc kệ là Chiến sư vẫn là danh sư, trọng yếu nhất chính là bảo hộ nhân tộc, không bị Dị Linh tộc nhân xâm hại, nếu như không phải vị tiền bối này phong ấn, đủ loại chiến đấu chỉ sợ không biết duy trì liên tục đã bao nhiêu năm, nhân loại cũng không có khả năng trải qua như vậy bình yên ổn định sinh hoạt."),t("br"),t("br"),n._v("Vốn cho rằng, nơi này chỉ là một cái bình thường cường giả Thánh vực lưu lại di tích, nằm mơ đều không có nghĩ đến, lại là vị này Thượng Cổ đại năng."),t("br"),t("br"),n._v("“Nếu như chỗ này di tích, có tiền bối pho tượng, ta sẽ cung kính dập mấy cái đầu...” Phùng Huân cũng đầy là kích động."),t("br"),t("br"),n._v("Đối với vị tiền bối này, hắn cũng là hết sức kính trọng, không nghĩ tới tự mình đến đến đối phương lưu lại di tích."),t("br"),t("br"),n._v("“Ta cũng thế...”"),t("br"),t("br"),n._v("Cái khác rất nhiều Chiến sư, đồng thời gật đầu."),t("br"),t("br"),n._v("“Khưu Ngô tiền bối, nếu đã lưu lại như vậy thật lớn di tích, khẳng định sẽ có lưu lại ý niệm, chúng ta tiếp tục hướng phía trước, căn cứ Hồng Diệp Vương nói, Chương Dẫn Khâu lão viện trưởng đám người cũng không có chết, chỉ là bị vây ở nơi nào đó! Cùng bị nhốt lại còn có Thiên Diệp Vương, chúng ta nhanh lên một chút đi, miễn cho xuất hiện cái gì không cần thiết biến cố.”"),t("br"),t("br"),n._v("Cố nén đem cái này bia đá móc đi bán lấy tiền xúc động, Trương Huyền nói."),t("br"),t("br"),n._v("Cái này bia đá mặc dù quý giá, nhưng nhìn rất nhiều danh sư cùng Chiến sư sùng bái bộ dáng, thật muốn móc đi, khẳng định sẽ cùng bản thân liều mạng, cân nhắc liên tục, thôi được rồi."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Mọi người gật đầu, đi thẳng về phía trước."),t("br"),t("br"),n._v("Mới vừa đi mấy bước, một vị Chiến sư đột nhiên ánh mắt sáng lên, ngừng lại, chỉ về phía trước: “Các ngươi nhìn...”"),t("br"),t("br"),n._v("Thuận ngón tay hắn nhìn lại, chỉ thấy một chuôi trường thương nghiêng cắm ở cách đó không xa trên vách tường, nửa đoạn lộ ở bên ngoài."),t("br"),t("br"),n._v("Ngô sư đi lên phía trước, đem trường thương nhẹ nhàng rút ra."),t("br"),t("br"),n._v("“Đây là... Bán Thánh khí?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Chuôi này trường thương thế mà cùng Băng Vũ kiếm đồng dạng, đạt đến Bán Thánh cấp bậc."),t("br"),t("br"),n._v("Loại trình độ này vũ khí, tại Hồng Viễn đế quốc vô cùng ít thấy, liền xem như Thanh Nguyên đế quốc, cũng sẽ không quá nhiều, ở đây thế mà nghiêng cắm ở trên vách tường, thoạt nhìn cùng rác rưởi đồng dạng, có phần quá khoa trương chút."),t("br"),t("br"),n._v("“Nơi đó còn có!”"),t("br"),t("br"),n._v("Đang tại rung động, lại có người hô lên, ngay sau đó chỉ thấy hắn theo mặt khác một bên trên vách tường, rút ra một chuôi Bán Thánh cấp bậc trường kiếm."),t("br"),t("br"),n._v("“Nơi này cũng có...”"),t("br"),t("br"),n._v("“Cái này lại là hạ phẩm Thánh khí...”"),t("br"),t("br"),n._v("Nương theo tiếp tục hướng phía trước, càng ngày càng nhiều kinh hô vang lên, thời gian không dài, hầu như mỗi người đều chiếm được một kiện binh khí, thấp nhất đều đạt đến Bán Thánh cấp bậc, cao thậm chí đạt đến hạ phẩm Thánh khí."),t("br"),t("br"),n._v("Hạ phẩm Thánh khí, Kim Nguyên đỉnh trước đó chính là cái này cấp bậc, mà ở đây, ném đâu đâu cũng có, suy nghĩ một chút đều để người khiếp sợ."),t("br"),t("br"),n._v("“Những này Thánh khí linh tính, dường như bị trọng kích, đã biến mất... Nói cách khác, những này Thánh khí, Bán Thánh khí mặc dù đồng dạng sắc bén, lại không linh trí, không phát huy ra trước đó uy lực!”"),t("br"),t("br"),n._v("Đem những binh khí này toàn bộ nhìn một lần, Ngô sư nói."),t("br"),t("br"),n._v("Mặc dù những vật này, cấp bậc rất cao, cũng rất sắc bén cứng cỏi, nhưng không có linh tính, sức chiến đấu chắc chắn thật to hao tổn."),t("br"),t("br"),n._v("“Không phải gặp phải trọng kích, mà là niên đại lâu, linh tính bị thời gian tiêu diệt...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Binh khí linh tính, mặc dù so với thọ mệnh của nhân loại dài, vẫn như cũ có lưu quang suy, nương theo thời gian trôi qua, dần dần biến mất, không có khả năng vĩnh tồn thế gian, nếu không, một chút lợi hại người, hoàn toàn có thể đem nguyên thần của mình phong ấn tại trong binh khí, vĩnh viễn còn sống sót."),t("br"),t("br"),n._v("Kim Nguyên đỉnh có thể tuổi thọ lâu như vậy, là bởi vì một mực ngủ say, tỉnh táo thời gian, không hề dài."),t("br"),t("br"),n._v("Cái này liên lụy đến linh hồn bí ẩn, liền xem như danh sư cũng có rất nhiều không biết được."),t("br"),t("br"),n._v("“Tiếp tục đi thôi!”"),t("br"),t("br"),n._v("Đem những binh khí này cất kỹ, Trương Huyền đám người tiếp tục hướng phía trước, lại phát hiện không ít rải rác thuốc, dược liệu các loại bảo vật."),t("br"),t("br"),n._v("Chỉ tiếc, cùng trước đó vũ khí đồng dạng, thời gian quá lâu, không ít đều mất đi hiệu quả, dù vậy, đem những vật này thu lại, cũng là một món tài sản khổng lồ, vượt xa quá trước đó cái kia linh thạch thượng phẩm cấp bậc bia đá."),t("br"),t("br"),n._v("Cũng không biết là bởi vì nắm giữ bản đầy đủ bản đồ nguyên nhân, vẫn là nguyên nhân khác, trên đường đi không có bất kỳ cái gì nguy hiểm, đi một hồi, một cái rộng lớn quảng trường xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Trên quảng trường đâu đâu cũng có cái hố nhỏ cùng vỡ vụn dấu vết, còn có mấy chục cái thi thể, rải rác phân tán tại bốn phía."),t("br"),n._v("“Nơi này trải qua chiến đấu...” Mọi người con ngươi thu hẹp, vội vã đi tới trước mặt."),t("br"),t("br"),n._v("“Đây là... Hồng Viễn danh sư học viện đã từng Mộ Nhân Hư phó viện trưởng, Chương Dẫn Khâu phụ tá đắc lực!”"),t("br"),t("br"),n._v("Đi tới một cỗ thi thể trước mặt, Ô Thiên Khung nhận ra được, vội vàng nói."),t("br"),t("br"),n._v("Mặc dù thi thể chủ nhân đã chết không ngắn thời gian, nhưng tu vi đạt tới Thánh vực, thân thể siêu phàm thoát tục, cũng sẽ không dễ dàng như vậy hư thối, thoạt nhìn vẫn như cũ sinh động như thật, cùng khi còn sống không khác nhau quá nhiều."),t("br"),t("br"),n._v("“Mộ Nhân Hư phó viện trưởng?” Trương Huyền đi tới trước mặt."),t("br"),t("br"),n._v("Trở thành viện trưởng về sau, xem qua học viện kỹ càng giới thiệu, nghe nói qua vị này."),t("br"),t("br"),n._v("Hồng Viễn danh sư học viện, vốn loại trừ Chương Dẫn Khâu vị này đang viện trưởng, còn có bốn vị phó viện trưởng, chỉ bất quá, đều theo hắn đi di tích, một đi không trở lại, nguyên nhân chính là như vậy, Lục Phong vị này luyện đan sư học viện viện trưởng, mới vượt quá chức phận, làm thay việc của người khác, thành thay mặt viện trưởng."),t("br"),t("br"),n._v("Vị này Mộ Nhân Hư, chính là bốn vị phó viện trưởng bên trong, thực lực mạnh nhất một cái, không nghĩ tới chết tại nơi này."),t("br"),t("br"),n._v("Cẩn thận tìm một vòng, cũng không phát hiện lão viện trưởng Chương Dẫn Khâu thi thể, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Mặc dù hắn chưa thấy qua vị này lão viện trưởng, nhưng học viện Tàng Thư khố bên trong có hắn chân dung, Tử Dương thú cũng nói rõ chi tiết qua, mấy chục cái trong thi thể, cũng không có một cái nào phù hợp."),t("br"),t("br"),n._v("“Nơi này tổng cộng có mười ba cái danh sư, cùng hai mươi mốt con Dị Linh tộc nhân thi thể!”"),t("br"),t("br"),n._v("Kiểm kê xong số lượng, Ngô sư tràn đầy thương cảm."),t("br"),t("br"),n._v("Cái này mười ba vị, gần như là Thánh vực nhất trọng lục tinh danh sư, toàn bộ chết ở chỗ này, lặng yên không một tiếng động, đối danh sư học viện, Danh Sư đường đều là một tổn thất lớn."),t("br"),t("br"),n._v("“Xem ra lúc ấy chiến đấu rất quyết liệt...” Trương Huyền cũng có chút buồn bã."),t("br"),t("br"),n._v("Nhớ tới viện trưởng lăng bên trong, Chương Dẫn Khâu lưu lại cái kia mộ chôn quần áo và di vật."),t("br"),t("br"),n._v("Hắn biết hẳn phải chết, những này cùng hắn tới danh sư tất nhiên cũng biết, vẫn như cũ việc nghĩa chẳng từ nan tới, phần này tinh thần, khiến cho người khâm phục."),t("br"),t("br"),n._v("“Đem những thi thể này mang về đi, khi còn sống không cách nào trở về quê cũ, sau khi chết cũng có thể trở lại học viện!”"),t("br"),t("br"),n._v("Bàn tay một trảo, đem những danh sư này thi thể, thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("Lá rụng về cội, bọn họ chết rồi, thi thể lại không thể một mực ở lại chỗ này."),t("br"),t("br"),n._v("“Bọn họ tại sao lại ở đây chiến đấu? Cuối cùng cũng đều đi nơi nào?”"),t("br"),t("br"),n._v("Thấy hắn dẹp xong thi thể, Hàn hội trưởng mở miệng."),t("br"),t("br"),n._v("“Không biết, có điều, đoán không sai, hẳn là Chương Dẫn Khâu viện trưởng đám người, biết Dị Linh tộc nhân sẽ đi vào di tích, đi theo vào, ở đây gặp gỡ, cuối cùng ra tay đánh nhau!”"),t("br"),t("br"),n._v("Trương Huyền phỏng đoán."),t("br"),t("br"),n._v("Căn cứ viện trưởng lăng trung chương dẫn khâu lưu lại ngữ, hẳn là bị bức bất đắc dĩ mới tới."),t("br"),t("br"),n._v("Như vậy đó có thể thấy được, nhất định là Dị Linh tộc nhân trước thời hạn biết di tích, bọn họ theo đuôi phía sau."),t("br"),t("br"),n._v("Đương nhiên, tình huống cụ thể như thế nào, không có tận mắt nhìn thấy, bằng vào suy đoán, không thể nào biết được."),t("br"),t("br"),n._v("“Mọi người tới đây một chút...”"),t("br"),t("br"),n._v("Đang tại phỏng đoán, một vị danh sư hô lên, đồng loạt nhìn lại, chỉ thấy hắn đứng tại quảng trường một bên, đang nhìn trước mắt một cái cực lớn tảng đá, tựa hồ có chút mê hoặc."),t("br"),t("br"),n._v("Là Thanh Trúc học viện một vị danh sư, thực lực không cao, lại hết sức cẩn thận."),t("br"),t("br"),n._v("Mọi người đi tới, đi tới trước mặt, nhìn một cái, cũng đồng thời cau mày."),t("br"),t("br"),n._v("Là một cái chưởng ấn, cũng không biết là giao chiến người vẫn là thiết kế di tích người lưu lại."),t("br"),t("br"),n._v("“Đây là kích hoạt một loại nào đó phong ấn cơ quan, ta trước đó gặp qua!” Trương Huyền đi tới cái trước mặt."),t("br"),t("br"),n._v("Vô Cương giới thời điểm, từng tại bia đá một bên gặp qua thứ này, nói là thủ ấn, nhưng thật ra là cái cái hố nhỏ, chỉ cần bàn tay để vào trong đó, rót vào chân khí, liền có thể kích hoạt trong đó ấn ký, cởi ra một loại nào đó phong ấn."),t("br"),t("br"),n._v("Hẳn là Khưu Ngô cổ thánh lưu lại."),t("br"),t("br"),n._v("“Cơ quan?” Ngô sư nhìn kỹ một hồi, nhẹ gật đầu: “Chư vị lui về phía sau, ta thử một chút...”"),t("br"),t("br"),n._v("Cơ quan, có an toàn cũng gặp nguy hiểm, ai cũng không biết đụng vào đi qua, một giây sau sẽ phát sinh cái gì, vẫn là cẩn thận một chút cho thỏa đáng."),t("br"),t("br"),n._v("Mọi người đồng loạt lui về phía sau, làm xong phòng ngự."),t("br"),t("br"),n._v("Ngô sư vẻ mặt nghiêm túc, bàn tay nhẹ nhàng nhấn một cái."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một tiếng nổ vang, trước mắt mặt tường lập tức nứt ra, xuất hiện chín cái không lớn viên môn, đều có chiều cao hơn một người, mang theo phong ấn khí tức, vừa nhìn liền biết, bên trong vẫn như cũ là không gian gấp, không biết sâu bao nhiêu."),t("br"),t("br"),n._v("“Làm sao nhiều như vậy?”"),t("br"),t("br"),n._v("Thấy không có nguy hiểm, mọi người đi tới trước mặt, tất cả đều tràn đầy kỳ quái."),t("br"),t("br"),n._v("“Chín số lượng chữ cực hạn, chín cái cửa, hẳn là đại biểu số lượng rất nhiều, vô cùng vô tận.” Hàn hội trưởng giải thích."),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Chín, kể chi cực cũng, lấy Lục Hào tam tam diễn sinh số lượng."),t("br"),t("br"),n._v("Thế giới này, rất nhiều công pháp, đều lấy chín vi tôn, tỷ như, võ giả cửu trọng, Hóa Phàm cửu trọng, Thánh vực cửu trọng."),t("br"),t("br"),n._v("Cửu thiên, Cửu Châu, chín nghi, cửu trọng, chín đầu, chín cù, chín hợp, chín trôi qua, chín đóng..."),t("br"),t("br"),n._v("“Chín cái cửa, đoán chừng cùng trận pháp đồng dạng, có sinh ra chết, có cảnh có tổn thương, chúng ta cái kia chọn cái nào?”"),t("br"),t("br"),n._v("Một vị Chiến sư, nhịn không được mở miệng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Bị hỏi như thế, Hàn hội trưởng sững sờ, trả lời không ra, nhịn không được nhìn về phía cách đó không xa Trương Huyền."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1075"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1077"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);