(window.webpackJsonp=window.webpackJsonp||[]).push([[1007],{1362:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1881-tu-lo-co-thanh-hau-nhan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1881-tu-lo-co-thanh-hau-nhan"}},[n._v("#")]),n._v(" Chương 1881: Tử Lộ Cổ Thánh hậu nhân")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trọng Khánh là Tử Lộ Cổ Thánh hậu nhân, lần này đấu vòng loại, Đại Nho đường người phụ trách, trước đó trên không trung tuyên đọc quy tắc Bất Hủ cảnh trung niên nhân, đúng là hắn."),t("br"),t("br"),n._v("Cùng các lão sư khác khác biệt, hắn mở ra sơn phong trận pháp về sau, liền trở về phòng, cũng không tại đào thải khu đợi chờ."),t("br"),t("br"),n._v("Một hồi tân sinh tuyển chọn mà thôi, biết kết quả là tốt, lấy thân phận của hắn, còn không đáng gặp thời khắc chú ý."),t("br"),t("br"),n._v("Cầm lấy bút lông, chậm rãi tại công văn bên trên viết, mùi mực bốn phía, thể chữ tinh tế mà ẩn chứa ý vị, vừa nhìn liền biết tâm cảnh của hắn yên bình như nước, không có chút nào sóng lớn."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một bóng người xuất hiện tại gian phòng, nhìn hắn chậm rãi viết chữ, lông mày nhíu một cái: “Trọng huynh, ngươi làm sao còn ở nơi này? Mau qua tới xem một chút đi, lần khảo hạch này có chút không đúng...”"),t("br"),t("br"),n._v("Nếu như Trương Huyền tại đây, khẳng định có thể nhận ra, vừa mới tiến tới đây người thân phận, chính là lão bằng hữu... Nam Cung Nguyên Phong!"),t("br"),t("br"),n._v("“Nguyên Phong, ngươi lại gấp gáp! Ta một mực nói ngươi, tâm cảnh không đủ vững, còn không tin. Bọn tiểu bối khảo hạch mà thôi, có gì có thể khẩn trương? Ngươi nếu là không thay đổi được loại cục diện này, trường kỳ đã qua, muốn tiến thêm một bước, gần như không có khả năng!”"),t("br"),t("br"),n._v("Đầu lâu không nhấc, chỉ biết người trước mắt này thân phận, Trọng Khánh trong tay bút lông chữ không ngừng, tiếng vận thản nhiên."),t("br"),t("br"),n._v("“Trọng huynh dạy phải... Nhưng mà, bây giờ căn bản không an tĩnh được tâm ah! Đấu vòng loại, đã triệt để loạn...”"),t("br"),t("br"),n._v("Nam Cung Nguyên Phong vội nói."),t("br"),t("br"),n._v("“Loạn? Có gì có thể loạn?” Vẫn không có ngẩng đầu, Trọng Khánh lãnh đạm."),t("br"),t("br"),n._v("“Bị đào thải quá nhanh, quá nhiều, ta sợ không kiên trì được ba canh giờ, liền sẽ hoàn tất... Hơn nữa, không ít chúng ta trước đó để ý, hơn nữa thực lực mạnh đều bị đào thải rơi mất, chân chính lưu lại, chưa chắc là thực lực xếp hạng trước một trăm...”"),t("br"),t("br"),n._v("Nam Cung Nguyên Phong có chút lo lắng."),t("br"),t("br"),n._v("Hắn tại hiện trường, nhìn rõ rõ ràng ràng."),t("br"),t("br"),n._v("Nhan Nhất Tiếu loại này đứng đầu nhất hạt giống, đều bị đào thải rơi mất, cái khác trước đó để ý cao thủ, cũng nhao nhao té ngựa..."),t("br"),t("br"),n._v("Nguyên nhân chỉ có một cái... Gặp Tử Trì Cổ Thánh hậu nhân!"),t("br"),t("br"),n._v("Chỉ cần gặp được, khẳng định sẽ bị đào thải, đã thành thiết luật, như vậy trải qua, tuyển chọn một trăm người, không còn là công bằng, mà là xem vận khí có thể hay không gặp được bọn họ..."),t("br"),t("br"),n._v("Một hồi khảo hạch, diễn biến thành như vậy, để hắn làm sao không gấp gáp?"),t("br"),t("br"),n._v("“Ồ? Như vậy chẳng phải càng tốt hơn!”"),t("br"),t("br"),n._v("Dường như đã sớm biết điểm ấy, Trọng Khánh cũng không ngoài ý muốn, bút lông lần nữa dính lên mực, dưới ngòi bút rồng bay phượng múa, nhẹ nhàng cười một tiếng: “Chư Tử bách gia rất nhiều hậu nhân, chênh lệch cũng không lớn, chúng ta chỉ là muốn một trăm người danh ngạch mà thôi, còn là ai, cũng không trọng yếu! Chỉ cần có thể đi qua khảo hạch, đi qua Đại Nho đường bồi dưỡng, đều sẽ càng đi càng xa...”"),t("br"),t("br"),n._v("“Cũng đúng...”"),t("br"),t("br"),n._v("Thấy đối phương như vậy bình tĩnh, Nam Cung Nguyên Phong lòng khẩn trương cảnh cũng giảm bớt ra."),t("br"),t("br"),n._v("Bọn họ muốn chỉ là trước một trăm mà thôi, còn là ai... Có quan trọng không?"),t("br"),t("br"),n._v("Đấu vòng loại, là công bằng, ngươi bị đào thải, chỉ nói rõ không có thực lực, không trách được những người khác."),t("br"),t("br"),n._v("“Tuy là lý là cái này lý, nhưng là bây giờ phần lớn tu luyện giả đều bị cùng một nhóm người đào thải, hơn nữa càng ngày càng nhiều, bị đào thải thậm chí thành lập liên nghị hội...”"),t("br"),t("br"),n._v("Nam Cung Nguyên Phong nói tiếp."),t("br"),t("br"),n._v("“Bị cùng một nhóm người đào thải? Ai? Nhan Nhất Tiếu, vẫn là Đoan Mộc Hiểu?” Trọng Khánh hỏi."),t("br"),t("br"),n._v("“Cũng không phải, là Tử Trì Cổ Thánh hậu nhân...” Nam Cung Nguyên Phong nói."),t("br"),t("br"),n._v("“Tử Trì Cổ Thánh hậu nhân?”"),t("br"),t("br"),n._v("Hiển nhiên cũng không nghĩ tới, Trọng Khánh sửng sốt một chút: “Phàn Tiểu Húc, Phàn Tiểu Phong bọn họ?”"),t("br"),t("br"),n._v("Đối với Chư Tử bách gia báo đưa danh ngạch, đặc biệt nhìn qua, nhớ tới mỗi một cái tham gia người tham gia khảo hạch họ tên."),t("br"),t("br"),n._v("“Đúng...” Nam Cung Nguyên Phong gật đầu."),t("br"),t("br"),n._v("“Tử Trì Cổ Thánh hậu nhân, một mực không có quá nhiều dẫn trước, lần này có thể trổ hết tài năng, cũng coi là hậu tích bạc phát đi! Quên đi, để cho bọn họ chơi đùa a, tất cả mọi người đều có cực hạn, hiện tại dẫn trước, không có nghĩa là một mực dẫn trước! Chúng ta cần phải làm là... Nhìn chính là, không cần thiết lẫn vào!”"),t("br"),t("br"),n._v("Cảm xúc một câu, Trọng Khánh vẫn như cũ vẻ mặt không thay đổi, trong tay bút lông tiếp tục viết, mắt thấy một bộ thư pháp liền muốn hoàn thành."),t("br"),n._v("“Tốt a, nếu ngươi có lời này, tâm cảnh lại như vậy vững, ta cũng không có gì có thể nói...”"),t("br"),t("br"),n._v("Thấy trước mắt cái này như vậy trầm ổn, Nam Cung Nguyên Phong thở phào nhẹ nhõm, vừa định rời đi đột nhiên nghĩ tới một việc, xoay đầu lại: “A, đúng, Tử Trì Cổ Thánh hậu nhân đào thải liên nghị hội hội trưởng, hình như là ngươi cháu trai ruột, Trọng Tử Huân!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Bút lông bỗng nhiên cắm vào trên tờ giấy trắng, vạch ra một đạo đen kịt mực ngấn, Trọng Khánh ngẩng đầu lên."),t("br"),t("br"),n._v("Hắn cháu trai ruột, năm nay cũng tham gia khảo hạch, vốn cảm thấy, nhất định có thể lưu đến cuối cùng, cái này... Đào thải liên nghị hội trưởng, có ý tứ gì?"),t("br"),t("br"),n._v("“Hắn bị đào thải?”"),t("br"),t("br"),n._v("Tuy là khó tin, trên mặt vẫn là giữ vững bình tĩnh, Trọng Khánh nhịn không được nói."),t("br"),t("br"),n._v("“Đúng vậy a, bị đào thải rất sớm, cho nên mười phần tự hào, lại thêm thân là tổ chức người một trong, phần lớn đều lấy hắn như Thiên Lôi sai đâu đánh đó!” Nam Cung Nguyên Phong giải thích."),t("br"),t("br"),n._v("“Súc sinh!”"),t("br"),t("br"),n._v("Trọng Khánh nắm đấm xiết chặt."),t("br"),t("br"),n._v("Làm sao sinh ra như vậy một cái bại gia ngoạn ý?"),t("br"),t("br"),n._v("Bị đào thải, không chỉ có không lấy lấy làm hổ thẹn, còn lấy chi làm vinh..."),t("br"),t("br"),n._v("Trở về nhất định phải cố gắng dạy dỗ!"),t("br"),t("br"),n._v("“Quên đi, tùy hắn đi đi!”"),t("br"),t("br"),n._v("Tức giận chợt lóe lên, Trọng Khánh ngay sau đó lắc đầu: “Dù sao lần này ta phái tới khảo hạch hậu bối, chừng tám vị nhiều, Trọng Tử Huân cái tên này, bình thường bị ta làm hư, mới có loại này không ổn trọng hành động, những người khác, chắc chắn sẽ không xuất hiện loại vấn đề này...”"),t("br"),t("br"),n._v("“Những người khác?”"),t("br"),t("br"),n._v("Nam Cung Nguyên Phong khóe miệng giật một cái: “Ngươi nói, nhưng mà Trọng Tử Phong, Trọng Tử Dư, Trọng Tử Hiên đám người?”"),t("br"),t("br"),n._v("Trọng Khánh gật đầu: “Mấy người bọn họ thận trọng...”"),t("br"),t("br"),n._v("“Bọn họ cũng sớm đã bị đào thải, là đám đầu tiên gia nhập liên nghị hội thành viên, nếu không phải bọn họ bỏ phiếu, Trọng Tử Huân thế nào lên làm hội trưởng...”"),t("br"),t("br"),n._v("Nói đến đây, Nam Cung Nguyên Phong cảm xúc: “Nói thật, các ngươi Tử Lộ nhất mạch, thật sự là quá đoàn kết, tâm cảnh cũng quá tốt, bị đào thải, người khác đều đau lòng, các ngươi có thể tổ chức thành liên nghị hội, ta cũng là mười phần bội phục...”"),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Một tiếng vang giòn, Trọng Khánh trong tay bút lông bị bóp gãy, vẻ mặt xanh như là cỏ xanh: “Một đám nghiệt súc, ta hiện tại liền đi đánh chết bọn họ...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trong nháy mắt, Trọng Khánh thân ảnh từ gian phòng biến mất."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Nhìn mới vừa rồi còn trầm ổn như băng Trọng huynh, trực tiếp nổ tung đồng dạng bay đi, Nam Cung Nguyên Phong gãi đầu một cái, một mặt im lặng."),t("br"),t("br"),n._v("Náo loạn hồi lâu, cái gọi là tâm cảnh trầm ổn, là cùng bản thân không quan hệ, có liên quan rồi, cũng không vững vàng..."),t("br"),t("br"),n._v("Bất quá, không thể không nói, Phàn Tiểu Húc, Phàn Tiểu Phong, Phàn Tiểu Tinh ba cái, thật đúng là diệu nhân."),t("br"),t("br"),n._v("Một mạch đem Tử Lộ Cổ Thánh khảo hạch tám người toàn bộ đào thải, thật chỉnh tề, thật là tuyệt!"),t("br"),t("br"),n._v("Hơn nữa, mấu chốt nhất là, bất kể là ai, mặc kệ sau lưng quan hệ, chỉ cần gặp lên, liền đào thải, không có chút nào lưu tình, làm như vậy, thật liền không sợ đi vào Đại Nho đường bị gây khó dễ?"),t("br"),t("br"),n._v("Phải biết, Đại Nho đường, mặc dù là bảy mươi hai Cổ Thánh liên thủ xây dựng, mỗi một vị Cổ Thánh, đều có hậu nhân phân bố trong đó, nhưng thực lực cũng không cân đối."),t("br"),t("br"),n._v("Không nói cái khác, tựa như Trọng Khánh vị trí Tử Lộ Cổ Thánh nhất mạch, ở trong đó liền có rất lớn quyền lên tiếng, cường giả rất nhiều."),t("br"),t("br"),n._v("Tùy tâm đào thải, không quan tâm... Đằng sau khẳng định không có thuận lợi như vậy, có điều, nhìn ba người này hành động, không có chút nào ăn thiệt thòi, đến cùng sẽ phát sinh cái gì, thật đúng là đáng để mong chờ ah!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1892"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1894"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);