(window.webpackJsonp=window.webpackJsonp||[]).push([[1008],{1364:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1882-đi-qua-khao-hach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1882-đi-qua-khao-hach"}},[n._v("#")]),n._v(" Chương 1882: Đi qua khảo hạch")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thình thịch thình thịch bành!"),t("br"),t("br"),n._v("Liên tục đánh bại hơn mười vị liên hợp cùng một chỗ Cổ Thánh hậu nhân, đồng thời đem đào thải, Phàn Tiểu Tinh hưng phấn mà con mắt đỏ lên."),t("br"),t("br"),n._v("Cùng Tiểu Húc học được chưa tới một canh giờ, chỉ bằng vào chính hắn, đã đào thải không dưới ba trăm người!"),t("br"),t("br"),n._v("Loại này tốc độ tiến bộ, đổi lại trước kia, nằm mơ cũng không dám nghĩ, mà bây giờ nhưng chân chính thực hiện."),t("br"),t("br"),n._v("Bất quá, cùng Phàn Tiểu Phong so, vẫn là kém không ít, cái tên này đã đào thải không dưới bảy trăm người!"),t("br"),t("br"),n._v("Còn Tiểu Húc, căn bản là không có ra tay, một mực đi theo phía sau bọn họ, chỉ cần mình hai người, hơi làm không đúng, liền buột miệng quát tháo, nguyên nhân chính là như vậy, mới có thể trong khoảng thời gian ngắn, nắm giữ to lớn như vậy tiến bộ."),t("br"),t("br"),n._v("“Lúc này mới giống cái bộ dáng, tại rèn luyện một chút, tu vi chân chính củng cố, hẳn là liền có thể đột phá...”"),t("br"),t("br"),n._v("Đem hai vị này, đem cuối cùng một nhóm người đánh bại, Trương Huyền thoả mãn gật gật đầu."),t("br"),t("br"),n._v("Tuy là hai người này có chút ngốc, nhưng lấy năng lực của hắn, vẫn là thành công để cho bọn họ thực lực, tăng vọt không ít."),t("br"),t("br"),n._v("Lần khảo hạch này sau đó, bất luận tâm cảnh vẫn là thực lực, hai cái này đều nhất định có bay vọt về chất, còn cuối cùng có thể đạt tới cảnh giới gì, cũng không phải là hắn có thể đi nắm trong tay."),t("br"),t("br"),n._v("“Đấu vòng loại chỉ còn lại có một trăm người, khảo hạch trước thời hạn kết thúc!”"),t("br"),t("br"),n._v("Kèm theo âm thanh vang dội, mọi người ngay sau đó cảm thấy bị một cỗ đặc thù lực lượng bao phủ, sau một khắc, đồng loạt xuất hiện tại sơn phong trước mặt quảng trường."),t("br"),t("br"),n._v("Trương Huyền nhìn quanh hai bên, phát hiện bốn phía không nhiều không ít vừa vặn một trăm người, loại trừ ba người bọn họ bên ngoài, hầu như người người đều có thương tích, thoạt nhìn có chút chật vật."),t("br"),t("br"),n._v("Có thể tại hơn hai ngàn người đấu vòng loại bên trong, lưu lại, những người này thoạt nhìn phổ thông, thật ra thì từng cái đều có đặc thù bản lĩnh, đương nhiên, điều kiện tiên quyết là... Không có gặp được ba người bọn hắn."),t("br"),t("br"),n._v("“Chúc mừng chư vị, trở thành Đại Nho đường học sinh mới của năm nay!”"),t("br"),t("br"),n._v("Trọng Khánh con mắt đảo qua Trương Huyền đám người, da mặt co lại, tuy là có loại muốn xông tới đánh người ham muốn, cuối cùng vẫn ngừng lại."),t("br"),t("br"),n._v("Bị người đào thải, chỉ có thể nói là bản thân hậu bối tài nghệ không bằng người, đi làm khó người thắng trận, coi như hắn tại Đại Nho đường địa vị kính trọng, cũng khinh thường đi làm."),t("br"),t("br"),n._v("Trọng Khánh nói tiếp: “Một lát nữa, mỗi người sẽ dựa vào ngọc bài đổi lấy một cái trữ vật giới chỉ, bên trong có đi vào Đại Nho đường con đường cùng phương pháp, cùng xung kích Kiến Thần Bất Hoại cảnh giới công pháp, tài nguyên tu luyện! Ba ngày thời gian bên trong, dựa vào thực lực bản thân đi vào Đại Nho đường, liền là chân chính học viên, cái này không tính khảo hạch, đối với các ngươi tới nói, hết sức dễ dàng, nhưng trước thời hạn nói cho một tiếng, càng sớm đi vào, càng có thể lựa chọn kĩ càng lão sư, đối tu luyện về sau có càng lớn trợ giúp.”"),t("br"),t("br"),n._v("Đại Nho đường nếu là học viện, có lẽ cùng học viện khác đồng dạng, lão sư lựa chọn học sinh đồng thời, học sinh cũng có thể lựa chọn lão sư."),t("br"),t("br"),n._v("Mỗi cái lão sư học sinh danh ngạch là có hạn, trước tiến vào người, có cơ hội lựa chọn tốt hơn lão sư, được tài nguyên cũng khẳng định không giống."),t("br"),t("br"),n._v("“Tốt, đem trước ngực ngọc bài giao lên đi!”"),t("br"),t("br"),n._v("Kèm theo một tiếng la lên, mọi người trước ngực ngọc bài đồng loạt bay lên, sau một khắc, một trăm viên trữ vật giới chỉ hướng mọi người bay đi."),t("br"),t("br"),n._v("Trương Huyền ngón tay nhẹ nhàng bóp một cái, nhẫn rơi vào lòng bàn tay, tinh thần khẽ động, đồ vật bên trong, đi vào trong óc."),t("br"),t("br"),n._v("Quả nhiên là đột phá Kiến Thần Bất Hoại cảnh giới công pháp, danh sư đại lục, không có thống nhất pháp quyết, nơi này đồng dạng không có, nhưng môn công pháp này, chỉ rõ phương hướng, chỉ cần chăm chú tu luyện, đột phá liền sẽ trở nên dễ dàng rất nhiều."),t("br"),t("br"),n._v("Loại trừ công pháp bên ngoài còn có một cái mượt mà đan dược, cấp chín tố lực đan."),t("br"),t("br"),n._v("Sau khi phục dụng, có thể để người ta thân thể cùng chân khí, lần nữa rèn luyện, từ đó cảm nhận được Kiến Thần Bất Hoại cảnh giới, vì đột phá, tăng thêm mười phần trăm tỷ lệ."),t("br"),t("br"),n._v("Cuối cùng, thì là một cái ngọc bài."),t("br"),t("br"),n._v("Đem lấy ra, nhẹ nhàng điểm một cái, cái gì đều không có cảm ứng được."),t("br"),t("br"),n._v("“Cái ngọc bài này phía trên có trận pháp, muốn nhìn rõ nội dung bên trong, nhất định phải đem trận pháp phá giải mới được, thật ra thì cũng là Đại Nho đường một loại khảo hạch!”"),t("br"),n._v(" Thấy hắn hoạt động, Phàn Tiểu Phong dường như trước thời hạn biết chút ít cái gì, giải thích nói."),t("br"),t("br"),n._v("“Khảo hạch? Nếu như, có ít người không am hiểu trận pháp đâu?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Có người đối với trận pháp tinh thông, liền có người không am hiểu, đối với loại người này dùng loại này khảo hạch, có chút không quá công bằng đi!"),t("br"),t("br"),n._v("“Trên ngọc bài trận pháp, cũng không phức tạp, khó tại tinh tế, coi như tinh thông trận pháp cũng không có quá lớn ưu thế, cần khống chế chân khí từng chút một phá giải, tinh điêu tế trác, nói là khảo hạch, trên thực tế cũng là đốc xúc đi vào Đại Nho đường người, không chỉ đối chân khí mạnh mẽ coi trọng, độ chính xác, cũng có rất lớn yêu cầu!”"),t("br"),t("br"),n._v("Phàn Tiểu Phong nói tiếp."),t("br"),t("br"),n._v("Trương Huyền hiểu được."),t("br"),t("br"),n._v("Phàn Tiểu Phong cùng Phàn Tiểu Tinh, từ đi vào sơn phong đến bây giờ, tu vi cũng không tăng thêm, lượng chân khí cũng không có tăng cường, nhưng sức chiến đấu nhưng chợt tăng không chỉ một lần, để vô số cường giả chịu thiệt, trọng yếu nhất chính là đối lực lượng độ chính xác, khống chế đến trong gang tấc."),t("br"),t("br"),n._v("Dư thừa lực lượng không lãng phí, tấn công không kém mảy may, sức chiến đấu tự nhiên gấp bội tăng lên."),t("br"),t("br"),n._v("Thần thức đảo qua ngọc bài, quả nhiên ở phía trên thấy được vô số bí văn, một đạo tiếp một đạo, muốn giải khai, cần đem chân khí khống chế cùng cái này đồng dạng hết sức nhỏ."),t("br"),t("br"),n._v("Đối với những người khác tới nói, rất khó, đối với hắn mà nói, không tính là gì, bàn tay nhẹ nhàng khẽ vỗ."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Phong ấn lập tức mở ra, nội dung trong đó đi vào trong óc."),t("br"),t("br"),n._v("Chính là Đại Nho đường chuẩn xác vị trí cùng phương pháp đi vào."),t("br"),t("br"),n._v("Đại Nho đường quả nhiên không tại chung quanh đây, mà là khoảng cách cái này chừng một vạn dặm sa mạc chỗ, muốn đi vào trong đó, cần phải mượn ngọc trong tay bài, tiến lên đặc thù lộ tuyến."),t("br"),t("br"),n._v("“Tốt, hiện tại chư vị có thể phá giải ngọc bài bên trong trận pháp...”"),t("br"),t("br"),n._v("Thấy tất cả mọi người lấy ra ngọc bài, trên không Trọng Khánh nhìn quanh một vòng, nói: “Đại Nho đường bao năm qua đến nay, tốc độ nhanh nhất một vị tiên tổ, từng ba canh giờ liền phá giải phong ấn, thành công tiến vào bên trong. Gần trăm năm nay, tốc độ nhanh nhất là Khổng sư hậu nhân, Khổng Thi Dao, ba cái nửa canh giờ, hoàn thành phá giải. Sau đó là Tử Uyên Cổ Thánh hậu nhân, Nhan Tiết, bốn canh giờ! Trong các ngươi, chỉ cần có một ngày phía trong có thể hoàn thành, coi như đột phá cực hạn!”"),t("br"),t("br"),n._v("Ngọc bài bên trong trận pháp, thoạt nhìn đơn giản, trên thực tế thao tác rất khó, không có ổn định tâm cảnh, cùng đối chân khí siêu cường lực khống chế, muốn làm được, gần như không có khả năng."),t("br"),t("br"),n._v("Cho dù hiện tại Đại Nho đường đệ nhất thiên tài Khổng Thi Dao, cũng dùng trọn vẹn ba cái nửa canh giờ."),t("br"),t("br"),n._v("“Trước thời hạn khuyên bảo một câu, không nên gấp tại cầu thành, nếu không nóng lòng khí nóng nảy, ba ngày không hoàn thành được, cho dù thông qua được tuyển chọn, cũng không cách nào trở thành Đại Nho đường thế hệ sau!”"),t("br"),t("br"),n._v("Trọng Khánh hừ một tiếng, bàn tay nhẹ nhàng vạch một cái."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trước mắt một cái tựa như mặt kính bình chướng xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("“Đây là đặc thù truyền tống trận, chỉ cần các ngươi phá giải ngọc bài bên trong phong ấn, liền sẽ bị hút vào trong đó, sau đó dựa theo phương pháp đặc thù đi vào Đại Nho đường, hiện tại bắt đầu phá giải phong ấn a, ta muốn nhìn, trong các ngươi nhanh nhất phải hao phí bao lâu...”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, lời còn chưa dứt, Trọng Khánh đột nhiên nhìn thấy bình chướng một hồi lay động."),t("br"),t("br"),n._v("Ngay sau đó, một bóng người chậm rãi bay lên, bàn chân nhẹ nhàng một bước, thẳng tắp chui vào."),t("br"),t("br"),n._v("“Đã phá giải?”"),t("br"),t("br"),n._v("Bốn phía, lặng ngắt như tờ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1893"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1895"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);