(window.webpackJsonp=window.webpackJsonp||[]).push([[1011],{1366:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1885-hung-manh-phan-tieu-huc-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1885-hung-manh-phan-tieu-huc-thuong"}},[n._v("#")]),n._v(" Chương 1885: Hung mãnh Phàn Tiểu Húc (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nhìn tới trên ngọc bài viết quy tắc cũng không phải thật."),t("br"),t("br"),n._v("Dựa theo phía trên viết, đi vào Đại Nho đường, sẽ có lão sư nghênh đón, sau đó có cơ hội lựa chọn, mà tới được trước mặt, mới phát hiện chỉ có một cái chiêng đồng, cái gì đều không có."),t("br"),t("br"),n._v("“Hẳn là tới quá nhanh, còn không người phản ứng lại...”"),t("br"),t("br"),n._v("Nói thầm một tiếng, Trương Huyền không tại tiếp tục chờ người, thẳng tắp hướng về phía trước bay đi, rất nhanh biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Vừa tiến vào Đại Nho đường, đến thăm lấy cảm thụ trước mắt không gian pháp tắc cùng nồng độ linh khí, không có chú ý chiêng đồng phía dưới có người, một cước đạp bên dưới, dùng lực lượng cũng không lớn... Cho nên, một đám Đại Nho, cứ việc dùng hết toàn lực, lại không để hắn chú ý tới..."),t("br"),t("br"),n._v("Liền giống như voi dẫm lên kiến, cũng không cảm giác được người sau dùng hết toàn lực phản kháng, chênh lệch quá lớn, đã có thể tự động không để ý đến."),t("br"),t("br"),n._v("Vèo!"),t("br"),t("br"),n._v("Trương Huyền biến mất không lâu, Trọng Khánh xuất hiện tại chiêng đồng phía trước, mê man nhìn một vòng, lông mày đột nhiên nhảy dựng."),t("br"),t("br"),n._v("Vội vàng hướng mặt đất bắt đi, qua không biết bao lâu, chiêng đồng mới được lần nữa nhấc lên."),t("br"),t("br"),n._v("Phía dưới hơn mười vị Đại Nho, phảng phất dùng hết lực lượng, nằm trên mặt đất không có chút nào hình ảnh, từng cái vẻ mặt trắng bệch, khóe miệng tràn đầy máu tươi."),t("br"),t("br"),n._v("“Mật Tuyên... Đây rốt cuộc chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Nhìn mọi người sắp tắt thở, Trọng Khánh cũng nhịn không được nữa."),t("br"),t("br"),n._v("Những đại nho này, đều là Danh Sư đường siêu cấp cường giả, dựa theo tình huống bình thường, hẳn là bày ra bản lĩnh, nghênh đón tân sinh, làm sao này đều nằm tại chiêng đồng phía dưới, cá chết đồng dạng?"),t("br"),t("br"),n._v("“Có phải hay không... Có người phá giải phong ấn, đi vào?”"),t("br"),t("br"),n._v("Giẫy giụa đứng dậy, Mật Tuyên một mặt muốn khóc biểu lộ."),t("br"),t("br"),n._v("“Ừm, Tử Trì Cổ Thánh hậu nhân Phàn Tiểu Húc!” Sửng sốt một chút, Trọng Khánh con mắt trợn tròn, tràn đầy khiếp sợ: “Đây là... Hắn làm? Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Mật Tuyên tràn đầy khó xử giải thích: “Ta đem chiêng đồng tế ra đến, vốn định tìm kiếm một cái, có thể ở phía trên kiên trì mười cái hô hấp người, không nghĩ tới bị người một cước đá xuống tới... Vì phòng ngừa bị lật đổ, tất cả mọi người liều mạng ngăn cản, kết quả... Ngươi cũng thấy đấy!”"),t("br"),t("br"),n._v("“Một cước đem chiêng đồng đá bay, các ngươi nhiều người như vậy, đều không có chặn lại?”"),t("br"),t("br"),n._v("Trọng Khánh có chút sụp đổ, sắp dọa sợ: “Cái này sao có thể? Người tới, có thể chỉ có Thánh vực cửu trọng đỉnh phong...”"),t("br"),t("br"),n._v("Chiêng đồng là Đại Thánh chí bảo, một cước bước lên, phá vỡ Mật Tuyên cùng hơn mười vị Đại Nho lực lượng... Hắn ba cái tính gộp lại đều không làm được, đối phương một cái Thánh vực cửu trọng đỉnh phong... Đùa giỡn đi!"),t("br"),t("br"),n._v("“Thánh vực cửu trọng tuyệt đối không có loại lực lượng này, thậm chí Đại Thánh đều không làm được, đây là Cổ Thánh mới có...”"),t("br"),t("br"),n._v("Mật Tuyên lắc đầu."),t("br"),t("br"),n._v("Cổ Thánh nặng nề vô cùng, tựa như một cái thế giới, nếu như thêm vào tốc độ phi hành lời nói, rất dễ dàng có thể làm ra vừa rồi cử động, thậm chí bản thân cũng không hiểu rõ tình hình."),t("br"),t("br"),n._v("Vị này nếu như là Cổ Thánh, xuất hiện loại tình huống này, rất có thể, Thánh vực cửu trọng lời nói, tuyệt đối không thể."),t("br"),t("br"),n._v("Trọng Khánh cau mày: “Ta tự mình đo lường qua, vị này Phàn Tiểu Húc là Tử Trì Cổ Thánh huyết mạch, hơn nữa, Phàn Tiểu Phong đám người có thể làm chứng, không thể giả được. Trọng yếu nhất chính là, thật muốn có Cổ Thánh xông tới, Đại Nho đường trận pháp sẽ tự động kích hoạt, ngủ say Cổ Thánh cũng sẽ tỉnh táo, nhưng bây giờ động tĩnh gì đều không có...”"),t("br"),t("br"),n._v(" Đại Nho đường cùng Danh Sư đường cùng đại gia tộc đồng dạng, phần lớn thời gian Cổ Thánh đều là ngủ say, một khi có cùng cấp bậc người xông tới, liền sẽ kích động lực lượng, để hắn tỉnh táo."),t("br"),t("br"),n._v("Giờ phút này, yên tĩnh như nước, cũng liền có thể chứng minh, đồng thời không có Cổ Thánh xông vào."),t("br"),t("br"),n._v("Nhưng... Không phải lời nói, tình huống trước mắt lại như thế nào giải thích?"),t("br"),t("br"),n._v("“Bất kể là ai, tìm được trước lại nói!”"),t("br"),t("br"),n._v("Biết nói nhiều hơn nữa vô dụng, nhất định phải tìm tới người, mới có thể xác định, Mật Tuyên lại không nói nhảm, thu hồi chiêng đồng, thẳng tắp hướng về phía trước rất nhiều kiến trúc bay đi, Trọng Khánh đám người theo sát phía sau."),t("br"),t("br"),n._v("Thời gian không dài, nhìn thấy một thân ảnh, đứng tại cách đó không xa trên quảng trường, một bên vò đầu, một bên tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Chính là vừa rồi đi vào Phàn Tiểu Húc."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Mọi người rơi xuống, đem hắn vây vào giữa."),t("br"),t("br"),n._v("“Các ngươi là...”"),t("br"),n._v(" Trong ánh mắt tràn đầy mê man, Phàn Tiểu Húc một bộ ta tại cái kia, ta vì cái gì ở đây biểu lộ."),t("br"),t("br"),n._v("“Nơi này là Đại Nho đường, ngươi vừa đi qua khảo hạch, phá giải phong ấn đi tới nơi này, hơn nữa để chúng ta bị thiệt lớn... Ngươi đến cùng là ai?”"),t("br"),t("br"),n._v("Trong đám người một vị Đại Nho, cũng nhịn không được nữa."),t("br"),t("br"),n._v("“Đại Nho đường? Đi qua khảo hạch... Ta khảo hạch hết rồi?” Phàn Tiểu Húc giật nảy mình, càng thêm mê mang: “Chịu thiệt, cái gì chịu thiệt?”"),t("br"),t("br"),n._v("“Giả ngu vô dụng, là thực lực gì, ta vừa đo liền biết!”"),t("br"),t("br"),n._v("Gặp hắn bộ biểu tình này, cùng mất trí nhớ đồng dạng, lại một vị Đại Nho cười lạnh, ngón tay một chút, một quyển sách xuất hiện ở trên không, trang sách mở ra, ánh sáng bao phủ xuống."),t("br"),t("br"),n._v("Đại Nho đường chí bảo 《 Chính Thân bí điển 》!"),t("br"),t("br"),n._v("Có thể dò xét một người tu vi thật sự cùng huyết mạch, không cách nào giả mạo, nghe nói là năm đó Bốc Thương Cổ Thánh dựa theo Minh Lý Chi Nhãn nguyên lý, viết mà thành, nắm giữ loại bỏ Hư Vọng năng lực."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Phàn Tiểu Húc giống như là ** chỉ riêng bao phủ, chân khí trong cơ thể lưu chuyển phương hướng cùng cường độ, rõ ràng hiển lộ ra, thậm chí huyết mạch màu sắc cùng đẳng cấp cũng có thể thấy rõ ràng."),t("br"),t("br"),n._v("“Quả thực là Tử Trì Cổ Thánh huyết mạch, Thánh vực cửu trọng đỉnh phong!”"),t("br"),t("br"),n._v("Mọi người từng cái mày nhăn lại, càng thêm kì quái."),t("br"),t("br"),n._v("Đi qua chính bản thân bí điển dò xét, người trước mắt này thân phận, không có sai lầm chút nào, không thể giả được."),t("br"),t("br"),n._v("Có thể... Thánh vực cửu trọng đỉnh phong, thật có thể đem bọn hắn nhiều người như vậy, chấn không hề có lực hoàn thủ? Một cái hô hấp liền phá vỡ phong ấn đi vào nơi đây, càng là chỉ điểm người khác, tuỳ tiện đào thải hơn một ngàn người?"),t("br"),t("br"),n._v("“Để cho ta thử một chút thực lực của ngươi!”"),t("br"),t("br"),n._v("Mọi người tràn đầy không hiểu, Trọng Khánh bước ra một bước."),t("br"),t("br"),n._v("Lần này, hắn cũng không đem tu vi áp chế ở Thánh vực cửu trọng, mà là Đại Thánh một tầng Kiến Thần Bất Hoại."),t("br"),t("br"),n._v("Phàn Tiểu Phong, Phàn Tiểu Tinh hai người cùng cấp bậc đều để hắn chịu thiệt, vị này chỉ điểm người, đề cao một cái tu vi thăm dò một chút, dù sao lấy hắn đối lực lượng năng lực chưởng khống, đối phương coi như muốn bị thương cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Không cho Phàn Tiểu Húc bất kỳ phản ứng nào cơ hội, Trọng Khánh một chưởng vỗ xuống dưới, tinh thần rủ xuống, sông lớn sôi trào, cứ việc đem tu vi áp chế đến Kiến Thần Bất Hoại, lấy tu vi của hắn, bốn phía mọi người, đồng dạng cảm thấy hô hấp gấp gáp, đồng loạt lui về phía sau."),t("br"),t("br"),n._v("Phàn Tiểu Húc càng là biến sắc, sợ tới mức toàn thân run rẩy."),t("br"),t("br"),n._v("Trí nhớ của hắn, chỉ dừng lại ở sơn phong bên ngoài, bị người kêu đi, sau đó... Tỉnh lại là đến nơi này, một vị Đại Nho không nói hai lời liền nặng tay... Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Giờ phút này không cho phép hắn suy nghĩ nhiều, vội vàng lui về phía sau."),t("br"),t("br"),n._v("Đối phương là Kiến Thần Bất Hoại thực lực, tuyệt không phải hắn có thể chống lại, không tránh, vô cùng có khả năng tại chỗ bị đập thành bánh thịt."),t("br"),t("br"),n._v("Vèo!"),t("br"),t("br"),n._v("Còn không có phản ứng lại, thân thể liền bay ngược mà ra, một chút bước ra bảy, tám trăm mét, thuấn di đồng dạng."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Trọng Khánh công kích rơi trên mặt đất, đập ra một cái lỗ to."),t("br"),t("br"),n._v("“Cái này... Là chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Không có phản ứng đến bản thân lại có tốc độ như vậy, Phàn Tiểu Húc sắc mặt trắng nhợt, thân thể cũng lại không nắm được, dừng bước, không tự chủ được té ngã trên đất, đặt mông ngồi dưới đất."),t("br"),t("br"),n._v("Hắn chỉ muốn lui về phía sau một bước, dựa theo tình huống bình thường, có thể có hơn mười mét là tốt lắm rồi, hiện tại trực tiếp chợt tăng gấp mấy chục lần, đi đến bảy, tám trăm mét..."),t("br"),t("br"),n._v("Làm sao làm được?"),t("br"),t("br"),n._v("Ta là ai? Đây là đâu?"),t("br"),t("br"),n._v("Phàn Tiểu Húc lại bắt đầu mê mang."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1896"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1898"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);