(window.webpackJsonp=window.webpackJsonp||[]).push([[1012],{1368:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1886-hung-manh-phan-tieu-huc-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1886-hung-manh-phan-tieu-huc-ha"}},[n._v("#")]),n._v(" Chương 1886: Hung mãnh Phàn Tiểu Húc (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thân pháp này...”"),t("br"),t("br"),n._v("Mật Tuyên mấy người cũng tất cả đều đồng tử co rụt lại."),t("br"),t("br"),n._v("Bọn họ có thể tuỳ tiện nhìn ra, trước mắt vị này, chỉ vận dụng Thánh vực cửu trọng lực lượng, có thể nhoáng lên, Đại Thánh một tầng đều khó mà đuổi kịp, tốc độ nhanh chóng, nghe rợn cả người."),t("br"),t("br"),n._v("Một chiêu đánh hụt, Trọng Khánh không có quá bất cẩn bên ngoài."),t("br"),t("br"),n._v("Cùng cấp bậc, liền Phàn Tiểu Phong cùng Phàn Tiểu Tinh đều đánh không lại, vị này chỉ điểm bọn hắn người, có thể tránh né, cũng không kỳ lạ, phun ra một ngụm trọc khí, đạp chân xuống, theo sát tới, như bóng với hình, bàn tay nâng lên, phối hợp thân hình, mạn thiên phi vũ, tựa như bão tuyết bay tán loạn, để một mặt mê man Phàn Tiểu Húc, rơi vào trong đó, chạy trốn không xong."),t("br"),t("br"),n._v("Mật Tuyên vẻ mặt nghiêm túc: “Là Trọng Khánh Đại Nho độc môn tuyệt kỹ, Đại Tuyết Mạn Thiên... Đừng nói cùng cấp bậc, coi như áp chế đến Thánh vực nhị trọng, đều không chống đỡ được!”"),t("br"),t("br"),n._v("Hắn am hiểu lực lượng, dốc hết sức phá mười biết, tu luyện võ kỹ đều mười phần đơn giản, trước mắt chiêu số quá mức lóa mắt phức tạp, liếc mắt nhìn liền choáng đầu hoa mắt, đổi lại hắn, chỉ có thể chạy trốn tránh né, căn bản không có cách nào ngăn cản."),t("br"),t("br"),n._v("Những người khác nghe nói như thế đồng thời gật đầu, từng cái cau mày, dường như cũng suy nghĩ phương pháp phá giải, rất nhanh, tất cả đều bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Trừ phi tu vi vượt qua đối phương, nếu không... Khó giải!"),t("br"),t("br"),n._v("Đồng loạt hướng đối diện chỉ có Thánh vực cửu trọng thanh niên nhìn sang, chỉ thấy hắn đồng dạng một mặt mê man, đang không biết như thế nào cho phải, nên chạy trốn hay là nên nhận thua, đột nhiên thân thể run lên, nghĩ tới cái gì, bỗng nhiên một bước, đi vào trong bông tuyết."),t("br"),t("br"),n._v("Rõ ràng chui vào đầy trời tuyết lớn, có thể đầy trời tuyết, lại không nửa điểm dính vào trên người, sau một khắc, năm ngón tay lật qua lật lại, đè xuống."),t("br"),t("br"),n._v("Bông tuyết trong nháy mắt liền bị dẫn dắt, giống như là tạo thành một cái to lớn tuyết cầu."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Trọng Khánh bay ngược mà ra, trong miệng máu tươi phun mạnh."),t("br"),t("br"),n._v("Chỉ trong một chiêu... Thua!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Chung quanh Đại Nho tất cả đều lông mày nhảy loạn, triệt để bối rối."),t("br"),t("br"),n._v("Thân là Đại Nho đường siêu cấp cường giả, đè thấp tu vi, thậm chí cao nhất cái cấp bậc cùng tân sinh đối chiến, đều bị đánh bại dễ dàng... Cái tên này đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Đối phương chiêu số, mọi người nhìn, bình thường không có gì lạ, nhưng mỗi một chiêu đều nhắm thẳng vào đại đạo, đổi lại bọn họ, muốn phòng ngự, cũng đều không làm được..."),t("br"),t("br"),n._v("Trên thế giới lại có loại này lợi hại võ công... Vị này Phàn Tiểu Húc, cùng ai học?"),t("br"),t("br"),n._v("Không chỉ bọn họ khiếp sợ, Phàn Tiểu Húc nhìn thấy bàn tay cũng một mặt khó tin."),t("br"),t("br"),n._v("Sơn phong trước mặt, hắn bị người kêu đi, sau đó đã hôn mê, tỉnh lại, là đến nơi này."),t("br"),t("br"),n._v("Vừa rồi bản cảm thấy Trọng Khánh đột nhiên ra tay, có chút không thể nói lý, bản thân khẳng định không đỡ được, lại tại đột nhiên phát giác trong đầu nhiều ra một đoạn ký ức."),t("br"),t("br"),n._v("Liên quan tới kỹ xảo chiến đấu ký ức."),t("br"),t("br"),n._v("Dựa theo nội dung trong đó sử dụng, một lần đem cao hơn một cấp cái khác Trọng Khánh, nhẹ nhõm đánh bại."),t("br"),t("br"),n._v("“Ta là Trương Huyền, mượn ngươi thân thể dùng một chút, xử lý một số chuyện, đoạn này ký ức là đền bù, nhìn không muốn tiết ra ngoài!”"),t("br"),t("br"),n._v("Ngay tại nghi ngờ, trong đầu vang lên một cái thanh âm nhàn nhạt."),t("br"),t("br"),n._v("Chính là lúc trước đem hắn mê đi người kia lưu lại."),t("br"),t("br"),n._v("“Trương Huyền? Chẳng lẽ là... Cổ Thánh?”"),t("br"),t("br"),n._v("Chấn động trong lòng."),t("br"),t("br"),n._v("Có thể trong lòng hắn có lưu lại ký ức, hơn nữa mảy may đều không tổn thương... Loại trừ Cổ Thánh, không có người có thể làm được!"),t("br"),t("br"),n._v("Thậm chí, gia tộc bọn họ Cổ Thánh, đều không làm được, có thể nói, vị này, thực lực mạnh mẽ, tại Cổ Thánh bên trong, đều hàng bên trên đỉnh phong."),t("br"),t("br"),n._v("Loại người này hoàn toàn có thể nhất niệm đem hắn xoá bỏ, lại không làm như vậy, đưa cho chỗ tốt... Chứng minh cũng không phải là làm ác, đã như vậy, cứ việc nghi ngờ đối phương đi vào Đại Nho đường mục đích, cũng liền không cần thiết nói ra."),t("br"),t("br"),n._v("Cổ Thánh sự tình, tự có Cổ Thánh xử lý, chỉ là Thánh vực cửu trọng, thật nói, cũng chưa chắc có người tin tưởng."),t("br"),t("br"),n._v("Đem trong đầu đối phương lưu lại ký ức hoàn toàn tiêu hóa, Phàn Tiểu Húc tràn đầy vui sướng lần nữa nhìn về phía trước mắt mọi người: “Còn có muốn thăm dò ta tu vi sao? Ra tay đi! Không yên lòng... Các ngươi cùng tiến lên cũng được!”"),t("br"),n._v(" Có những này kỹ xảo chiến đấu, vừa rồi lại thí nghiệm, không có sai lầm chút nào, chiến ý lập tức đại tăng, tốt kiểm tra một chút, đến cùng tiến bộ bao nhiêu."),t("br"),t("br"),n._v("Thấy một cái tân sinh như vậy tùy tiện, Mật Tuyên đám người liếc mắt nhìn nhau, riêng phần mình đè thấp tu vi, đồng loạt xông tới."),t("br"),t("br"),n._v("Hơn ba mươi hô hấp về sau, một đám Đại Nho toàn bộ nằm trên mặt đất."),t("br"),t("br"),n._v("Nhìn hai tay của mình, Phàn Tiểu Húc kích động hốc mắt chuyển hồng."),t("br"),t("br"),n._v("Thiên phú của hắn cùng thực lực, tại Chư Tử bách gia rất nhiều người trẻ tuổi bên trong, đều chỉ có thể được cho đồng dạng, tình huống bình thường, lần khảo hạch này, rất khó đi qua, không nghĩ tới được Cổ Thánh ưu ái, truyền thụ kỹ xảo chiến đấu, đem một đám Đại Nho, đè xuống đất cọ xát!"),t("br"),t("br"),n._v("Loại này thành tựu, trước đó tuyệt không dám nghĩ."),t("br"),t("br"),n._v("Tùy tiện tiếp nhận ký ức, cứ như vậy lợi hại, vậy vị này Cổ Thánh thực lực, rốt cuộc mạnh bao nhiêu?"),t("br"),t("br"),n._v("“Trương Huyền Cổ Thánh, đến cùng là vị nào?”"),t("br"),t("br"),n._v("Nhịn không được tâm đãng thần trì, hướng về không ngớt."),t("br"),t("br"),n._v("Chư Tử bách gia đồng thời không họ tấm người tồn tại, đương nhiên, cái tên này đến cùng phải hay không thật, hắn cũng không rõ ràng, chỉ biết mình học được đối phương tài nghệ, tương đương với tiếp nhận đối phương ân tình."),t("br"),t("br"),n._v("“Từ nay về sau, ngươi chính là ta cả đời chi sư, sẽ không đi thay đổi...”"),t("br"),t("br"),n._v("Ngầm hạ quyết định."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một bóng người tại Đại Nho đường chậm rãi hướng về phía trước, rõ ràng đi trong đám người, lại không một người phát hiện, hình như không gian bốn phía, đều xuất hiện vặn vẹo, ánh sáng căn bản chiếu không tới trên người hắn."),t("br"),t("br"),n._v("Chính là đi vào Đại Nho đường Trương Huyền."),t("br"),t("br"),n._v("Mục đích của hắn là lặng yên không một tiếng động đi vào nơi này, hiện tại đã đi tới, cũng liền không cần thiết tiếp tục ngụy trang."),t("br"),t("br"),n._v("Cho nên, đi không bao xa, liền đem thật Phàn Tiểu Húc phóng thích ra ngoài, sợ chuyện lúc trước để lộ, còn đặc biệt cho một đoạn ký ức."),t("br"),t("br"),n._v("Được bản đầy đủ vu hồn sư truyền thừa, cắm vào ký ức, mà không làm thương hại đến bản tôn, đối với hắn mà nói, dễ như trở bàn tay."),t("br"),t("br"),n._v("Còn người sau dùng đoạn này ký ức, có thể chiến thắng bao nhiêu người, cuối cùng có thể nắm giữ dạng gì sức chiến đấu, cũng liền chẳng muốn so đo."),t("br"),t("br"),n._v("Loại này quán đỉnh thức truyền thụ, tuy là có thể khiến người ta trong thời gian ngắn sức chiến đấu đại tăng, nhưng cẩn thận nói đến, vẫn là không bằng Phàn Tiểu Phong đám người chịu đựng vô số thực chiến tới vững chắc."),t("br"),t("br"),n._v("Nói cách khác, trong thời gian ngắn, Phàn Tiểu Húc có thể vượt qua hai người, nhưng kèm theo thời gian trôi qua, hai người vô cùng có khả năng vượt qua hắn, càng ngày càng mạnh."),t("br"),t("br"),n._v("Đương nhiên, lúc này hắn, có thể đã dò xét đến Khổng sư bí mật, rời đi nơi đây."),t("br"),t("br"),n._v("Không đi nghĩ chuyện này, tiếp tục hướng phía trước, dọc theo Đại Nho đường tựa như thành thị giống như kiến trúc dạo qua một vòng, con mắt rơi vào một cái cao lớn trước gian phòng."),t("br"),t("br"),n._v("“Thánh Nhân từ đường... Nếu có bí mật lời nói, hẳn là ở đây!”"),t("br"),t("br"),n._v("Lông mày giương lên."),t("br"),t("br"),n._v("Xuất hiện ở trước mắt, là Danh Sư đường không có đặc thù kiến trúc, Thánh Nhân từ đường."),t("br"),t("br"),n._v("Bên trong chỉ trưng bày Khổng sư một người đồ vật cùng tượng, dùng để tế tự."),t("br"),t("br"),n._v("Muốn nói có cái gì bí mật muốn dò xét lời nói, hẳn là chỉ có nơi này."),t("br"),t("br"),n._v("Rõ ràng điểm ấy, hít sâu một hơi, Trương Huyền chậm rãi hướng về phía trước, thẳng tắp hướng từ đường đi tới."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, liền cảm thấy một đạo hào quang rơi xuống, dường như có trận pháp, dò xét đến không gian phong cấm bí ẩn, phát hiện ẩn giấu ở hư không bên trong hắn."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngay sau đó, một cái bàn tay khổng lồ, xé rách không gian, hướng về phía hắn bắt đến, còn chưa tới đến trước mặt, không gian đều không ngừng sụp đổ, toàn bộ thế giới, đều như là sắp bạo liệt."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1897"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1899"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);