(window.webpackJsonp=window.webpackJsonp||[]).push([[1013],{1367:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1887-thanh-nhan-tu-đuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1887-thanh-nhan-tu-đuong"}},[n._v("#")]),n._v(" Chương 1887: Thánh Nhân từ đường")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không nghĩ tới che giấu tốt như vậy, đều bị phát giác, vẻ mặt ngưng lại, Trương Huyền bỗng nhiên co rụt lại, một quyền hướng về phía trước chặn đánh mà lên."),t("br"),t("br"),n._v("Hắn cứ việc vẫn như cũ không có đột phá đến Cổ Thánh cấp bậc, nhưng sức chiến đấu đã có thể so với tích huyết trùng sinh, chân khí dọc theo kinh mạch bắn ra, ở trước mắt tạo thành một cái to lớn thiên la địa võng."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Quyền chưởng đối đầu, Trương Huyền lắc lư một cái."),t("br"),t("br"),n._v("Cái này nắm đấm chủ nhân, vậy mà mảy may đều không yếu hơn hắn, cũng là tích huyết trùng sinh cảnh giới siêu cấp cường giả."),t("br"),t("br"),n._v("“Không thể ngạnh chiến...”"),t("br"),t("br"),n._v("Tới đây mục đích là tìm kiếm Khổng sư bắt lấy “Thần Linh” quá khứ, cũng không phải là tới quấy rối, tránh khỏi phiền phức, nhất định phải tốc chiến tốc thắng, nếu không, bị Chư Tử bách gia Cổ Thánh phát giác, muốn thuận lợi rời đi, liền không dễ dàng như vậy."),t("br"),t("br"),n._v("Bàn tay run lên, Long Cốt thần thương xuất hiện tại lòng bàn tay, ẩn chứa toàn thân khí tức, phá không hướng nắm đấm đâm tới."),t("br"),t("br"),n._v("Trong tích tắc, không gian xuất hiện nhất đạo nhỏ bé vết rách, tựa như cây kim đâm vào làn da, uy lực tuy là cực mạnh, nhưng không có mảy may năng lượng tán dật ra ngoài, thậm chí rời xa từ đường mười mét bên ngoài, không tận mắt thấy, đều phát hiện không ra, có Cổ Thánh cấp bậc cường giả, ở đây chiến đấu."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Nắm đấm cùng thần thương đối đầu, người sau dường như bị thương, bàn tay lùi về, Trương Huyền bỗng nhiên vọt tới, một cước đạp đi vào."),t("br"),t("br"),n._v("Xuất hiện ở trước mắt chính là cái rộng rãi đại điện, chính giữa đứng sừng sững lấy Khổng sư tượng, phía trước một người mặc kim sắc khôi giáp binh sĩ, Chính Nhất mặt thật thà nhìn về phía hắn, trong mắt ẩn chứa sát khí."),t("br"),t("br"),n._v("Vừa rồi công kích, đúng là hắn thả ra."),t("br"),t("br"),n._v("“Đây là... Sư chữ kim binh?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Vốn cho rằng là thủ hộ từ đường Đại Nho đường cao thủ, không nghĩ tới, chỉ là một cái do thể chữ biến hóa mà thành giáp vàng hộ vệ."),t("br"),t("br"),n._v("Thứ này, trước đó tại Nhiễm Cầu Cổ Thánh di tích nhìn thấy qua một lần."),t("br"),t("br"),n._v("“Một chữ, liền có Cổ Thánh tam trọng sức chiến đấu, chẳng lẽ là... Khổng sư lưu lại?”"),t("br"),t("br"),n._v("Trong lòng khiếp sợ."),t("br"),t("br"),n._v("Khổng sư thực lực, cổ kim thứ nhất, hắn cũng không phủ nhận, thật không nghĩ qua thế mà mạnh như vậy!"),t("br"),t("br"),n._v("Viết một chữ, liền nắm giữ Cổ Thánh tam trọng tích huyết trùng sinh lực lượng, cùng mình đối đầu, đều không rơi vào thế hạ phong... Nếu như viết lên một mảng lớn, chẳng phải thiên hạ lại không có gì có thể lấy ngăn cản?"),t("br"),t("br"),n._v("Ý nghĩ này vừa nhô ra, ngay sau đó lắc đầu."),t("br"),t("br"),n._v("Sư chữ kim binh, cứ việc rất lợi hại, nhưng cũng không phải tùy tiện liền có thể viết ra, không chỉ đối viết người đối thư pháp lý giải, có yêu cầu cực lớn, còn cần chân khí cùng tinh huyết tẩm bổ, lấy thực lực của hắn bây giờ, cũng nhiều nhất viết ra Bất Hủ cảnh đại viên mãn cấp bậc thể chữ, số lượng không cao hơn ba cái."),t("br"),t("br"),n._v("Nhiều hơn nữa, cũng làm không được."),t("br"),t("br"),n._v("“Mạnh hơn cũng chỉ là một chữ!”"),t("br"),t("br"),n._v("Thần thức lan ra, phát hiện chiến đấu mới vừa rồi, cũng không dẫn tới chú ý của những người khác, Trương Huyền thở phào nhẹ nhõm, lông mày nâng lên, cũng không chậm trễ, tiến về phía trước một bước, lại một thương đâm tới."),t("br"),t("br"),n._v("Liên tục vài cái, mũi thương lực lượng hội tụ, hình thành một đoàn xoay tròn dòng xoáy."),t("br"),t("br"),n._v("Thương pháp của hắn, phù hợp đại đạo, thoạt nhìn đơn giản, thực tế thi triển đi ra, nhưng thiên biến vạn hóa, để cho người ta không thể nào phòng ngự."),t("br"),t("br"),n._v("Vì tốc chiến tốc thắng, Trương Huyền lần này không có biện pháp dự phòng, toàn thân chân khí dọc theo đúc lại kinh mạch lao nhanh, tản mát ra có thể so với phá toái hư không cường giả lực lượng, không đến hai cái hô hấp, trước mặt là sư chữ kim binh liền bị đánh tan, lần nữa biến thành một bãi mực nước, biến mất ở trước mắt."),t("br"),t("br"),n._v("Vội vàng ngẩng đầu, ngay sau đó nhìn thấy biến thành một cái to lớn “Nho” chữ, rơi vào tượng sau lưng trên vách tường."),t("br"),t("br"),n._v("Thấy cái chữ này, tấn công xong xuôi, tại không có cái khác công kích, cũng không có trận pháp, Trương Huyền đi tới tượng Khổng sư phía trước bái vài cái, lúc này mới hướng bốn phía nhìn lại."),t("br"),n._v("Vừa rồi sư chữ kim binh, có lẽ cùng Nhiễm Cầu Cổ Thánh di tích đồng dạng, thuộc về một loại khảo hạch, đem hắn đánh tan, mới có tư cách ở lại chỗ này."),t("br"),t("br"),n._v("Trong từ đường trống rỗng, chỉ có một cái tượng, hướng bên cạnh cửa bên đi đến, là cái hành lang, trên vách tường tràn đầy phù điêu."),t("br"),t("br"),n._v("“Đây là... Khổng sư cuộc đời?”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Trước mắt phù điêu, điêu khắc chính là Khổng sư cuộc đời, từ lúc vừa ra đời bắt đầu, dọc theo thời gian tuyến, chậm rãi hướng về phía trước."),t("br"),t("br"),n._v("Con mắt rơi vào phía trên, Trương Huyền cả người linh hồn phảng phất rơi vào trong đó, xuyên qua thời gian cùng không gian ràng buộc, kèm theo Khổng sư, trải qua hắn ầm ầm sóng dậy một đời."),t("br"),t("br"),n._v("Khổng sư giáng sinh, không hề giống trong truyền thuyết đồng dạng, là trời sinh Thánh vực, bất luận từ nơi nào nhìn, đều hết sức bình thường, nhiều nhất thông minh hiếu học, thích xem sách."),t("br"),t("br"),n._v("Lúc đó, Dị Linh tộc thống trị đại lục, nhân tộc địa vị cực thấp, từ mới đầu bình thường, đến đột nhiên phảng phất khai ngộ đồng dạng, tu vi đột nhiên tăng mạnh, trong một năm, đột phá võ giả cửu trọng, Hóa Phàm cửu trọng thậm chí Thánh vực cửu trọng!"),t("br"),t("br"),n._v("Tốc độ tiến bộ, so với hắn, đều không hề yếu!"),t("br"),t("br"),n._v("Tu vi một đường trưởng thành, Khổng sư không ngừng bước, chinh chiến các quốc gia, đem nhân tộc từ Dị Linh tộc trong tay giải cứu ra, trong lúc đó, truyền bá sư đạo lý niệm, thu nhận học sinh, thành lập Danh Sư đường, truyền thụ thiên hạ."),t("br"),t("br"),n._v("Dị Linh tộc cảm nhận được uy hiếp, đem vây quét, cùng rất nhiều môn đồ, vây ở Trần Thái mấy tháng lâu, suýt chút nữa không cách nào chạy trốn, nguy cơ phía dưới, sáng chế Xuân Thu đại điển, đột phá Cổ Thánh, một lần phá vỡ khốn khó, thậm chí thiết kế phục sát Ngoan Nhân, đem Dị Linh tộc nhân triệt để đuổi vào cổ chiến trường, phong ấn trong đó, lại không cách nào làm ác."),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("“Xem ra, Khổng sư đột phá Cổ Thánh, cũng không phải là đơn giản lĩnh ngộ thời gian pháp tắc...”"),t("br"),t("br"),n._v("Một đường tiến lên, Trương Huyền một bên suy tư."),t("br"),t("br"),n._v("Xuân Thu đại điển, ẩn chứa thời gian bí ẩn, Khổng miếu hắn cũng đi qua, bên trong tốc độ thời gian trôi qua, cùng ngoại giới xác thực không quá tương đồng."),t("br"),t("br"),n._v("Nhiễm thời kì Cổ Thánh đột phá, lĩnh ngộ hẳn là thương pháp, Khưu Ngô Cổ Thánh lĩnh ngộ là không gian... Trước đó một mực tại suy đoán, Khổng sư lĩnh ngộ là cái gì, hiện tại xem ra, hẳn là thời gian, mà không chỉ là thời gian!"),t("br"),t("br"),n._v("Trương gia tiên tổ, chính là lĩnh ngộ thời gian thành tựu Cổ Thánh, thực lực đồng dạng cực mạnh, có thể cùng vị này vạn thế chi sư so, kém không biết bao nhiêu."),t("br"),t("br"),n._v("Trong lòng nghi ngờ, tiếp tục hướng xuống nhìn lại."),t("br"),t("br"),n._v("Đem Dị Linh tộc nhân đuổi vào cổ chiến trường, bọn họ tế tự triệu hồi ra “Thần Linh”, sau đó Khổng sư đem hắn bắt lấy."),t("br"),t("br"),n._v("Nhìn thấy cái này, Trương Huyền hô hấp hơi có chút gấp rút."),t("br"),t("br"),n._v("Tới đây mục đích, chính là vì tìm tới cái này, không nghĩ tới sắp biết đáp án."),t("br"),t("br"),n._v("Vội vàng nhìn xuống dưới, trên phù điêu điêu khắc, Khổng sư mang theo “Thần Linh” rời đi danh sư đại lục hạch tâm nhất vị trí, Danh Sư đường tổng bộ, đi tới một chỗ vắng vẻ vị trí."),t("br"),t("br"),n._v("Nơi này có chút hoang vu, linh khí cũng rất là mỏng manh, hầu như không nhìn thấy ra dáng kiến trúc cùng thành thị."),t("br"),t("br"),n._v("Đi tới nơi này, “Thần Linh” cũng không biết là bị giết, vẫn là cái gì, không thấy tung tích, Khổng sư dường như bị thương, dừng ở tại chỗ tu dưỡng."),t("br"),t("br"),n._v("Phía sau một chỗ phù điêu, thì điêu khắc đi vào một cái trong tiểu thế giới, đoán không sai, hẳn là cái này Chư Tử bách gia vị trí Côn Hư cảnh."),t("br"),t("br"),n._v("Đây là cái cuối cùng."),t("br"),t("br"),n._v("“Côn Hư cảnh, chẳng lẽ...”"),t("br"),t("br"),n._v("Nhìn thấy cái cuối cùng bia đá là xuất hiện Côn Hư cảnh, Trương Huyền lần nữa nhìn về phía Khổng sư đem “Thần Linh” dẫn đi hoang vu vị trí, nhìn kỹ phút chốc, nheo mắt."),t("br"),t("br"),n._v("Nơi này hắn thế mà nhận thức..."),t("br"),t("br"),n._v("Chính là ngoại giới... Thiên Huyền vương quốc!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1898"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1900"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);