(window.webpackJsonp=window.webpackJsonp||[]).push([[1016],{1370:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1888-gap-lai-nhan-thanh-co-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1888-gap-lai-nhan-thanh-co-thanh"}},[n._v("#")]),n._v(" Chương 1888: Gặp lại Nhan Thanh Cổ Thánh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Chẳng lẽ cái này Chư Tử bách gia vị trí Côn Hư cảnh, cũng không phải là Khổng sư sáng tạo?”"),t("br"),t("br"),n._v("Trương Huyền trong lòng hơi động."),t("br"),t("br"),n._v("Chư Tử bách gia, rời đi danh sư đại lục, sinh hoạt tại Khổng sư làm ra trong tiểu thế giới, trước khi đến chỉ biết, bởi vậy tại Thiên Huyền vương quốc phá vỡ phong ấn sau khi đi vào, cũng không cảm thấy kỳ lạ, nhìn thấy trước mắt phù điêu, giờ mới hiểu được, dường như... Cũng không phải là như vậy."),t("br"),t("br"),n._v("Tiểu thế giới này, hình như tuyên cổ tồn tại, cũng không phải là Khổng sư sáng tạo mà thành."),t("br"),t("br"),n._v("Khổng sư ghi chép, từ nơi này biến mất, chẳng lẽ... Cùng đi vào Thượng Thương có liên hệ gì?"),t("br"),t("br"),n._v("Tiếp tục đi đến phía trước."),t("br"),t("br"),n._v("Phù điêu sau đó, là cái càng thêm gian phòng cực lớn, trên mặt tường tràn đầy bích hoạ, viết Chư Tử bách gia cường giả, đi tới tiểu thế giới này về sau, hiện ra tới rất nhiều làm ra cống hiến to lớn cường giả."),t("br"),t("br"),n._v("Cùng đoán đồng dạng, Thanh Nha mạch quả thực là một vị Cổ Thánh bồi dưỡng ra tới, chẳng qua cũng không phải là Tử Trì Cổ Thánh, mà là hắn một vị hậu nhân, trải qua hơn vạn năm cải tiến, mới có hôm nay hiệu quả, dù là không tu luyện, trưởng thành đều có thể đi đến cảnh giới tông sư."),t("br"),t("br"),n._v("Thiên phú cao hơn, tốc độ tu luyện cũng càng nhanh."),t("br"),t("br"),n._v("Đem bích hoạ xem xong, Trương Huyền càng thêm nghi ngờ."),t("br"),t("br"),n._v("Nơi này không còn Khổng sư sau khi mất tích bất kỳ ghi lại nào, hình như là triệt để đi, không còn bất cứ tin tức gì."),t("br"),t("br"),n._v("Gian phòng phần cuối không có vật gì khác nữa, lại chuyển một hồi, Trương Huyền tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Nơi này, cũng không phát hiện càng nhiều manh mối."),t("br"),t("br"),n._v("Đang muốn rời đi từ đường, đi tìm cái khác manh mối, thân thể đột nhiên thoáng một cái, giấu ở cửa bên đằng sau, đồng thời tại thân thể xung quanh sắp đặt ra mười cái phong ấn, trong chốc lát, thân ảnh biến mất tại nguyên chỗ, hình như chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Vừa nấp kỹ, tiếng bước chân vang lên, vài bóng người đi vào."),t("br"),t("br"),n._v("Xuyên thấu qua cửa bên, Trương Huyền lặng lẽ nhìn lại."),t("br"),t("br"),n._v("Cầm đầu lại là người quen biết cũ, Nhan Thanh Cổ Thánh!"),t("br"),t("br"),n._v("Sau lưng mấy người, cũng đều biết, Khổng miếu lúc trước vây quét Thần Dung Hoàng những cái kia."),t("br"),t("br"),n._v("Thực lực của hắn bây giờ, thêm vào đối không gian lĩnh ngộ, ẩn núp, chỉ cần không chủ động phóng thích khí tức hoặc là cùng người khác câu thông, đủ có thể khiến những này Cổ Thánh không phát hiện được."),t("br"),t("br"),n._v("“Bọn họ hiện tại trạng thái, dường như không tốt lắm...”"),t("br"),t("br"),n._v("Cùng trước đó nhìn thấy khác biệt, ban đầu ở Khổng miếu, mấy vị này, mỗi một cái đều khí tức ngập trời, có Cổ Thánh bất khả kháng hoành lực lượng cùng uy nghiêm, mà bây giờ, từng cái khí lực suy bại, bất luận dung mạo vẫn là chân khí, đều cho người ta một loại tùy thời sụp đổ cảm giác."),t("br"),t("br"),n._v("Loại cảm giác này, cùng trước đó Trương Hoằng Thiên có chút tương tự, phảng phất tuổi thọ đi đến cuối con đường."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Tràn đầy kỳ lạ."),t("br"),t("br"),n._v("Khổng miếu bên trong, bọn họ vây quét Thần Dung Hoàng, tuy là chiến đấu quyết liệt, không ít người bị thương, đều không tính quá nặng, cấp cho đầy đủ thời gian, có thể nhẹ nhõm khôi phục, không đến mức lưu lại quá lớn tai hoạ ngầm, làm sao ngắn ngủi mấy tháng không thấy, tất cả đều bộ dáng này?"),t("br"),t("br"),n._v("Đặc biệt là Nhan Thanh Cổ Thánh, một mặt già yếu, ánh mắt cũng ảm đạm xuống, gần đất xa trời, hình như bất cứ lúc nào cũng sẽ tử vong."),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Trong lòng đang kỳ lạ, thanh âm ho khan vang lên, Nhan Thanh Cổ Thánh khóe miệng tràn ra máu tươi."),t("br"),t("br"),n._v("“Nhan Thanh...”"),t("br"),t("br"),n._v("Sau lưng một vị Cổ Thánh, tràn đầy lo lắng nhìn qua."),t("br"),t("br"),n._v("“Còn chịu đựng được!”"),t("br"),t("br"),n._v("Lắc đầu, cưỡng ép ép lại thương thế trên người, Nhan Thanh Cổ Thánh xoay đầu lại: “Tế bái về sau, liền bắt đầu đi!”"),t("br"),t("br"),n._v("“Khẳng định muốn làm như thế?”"),t("br"),t("br"),n._v("“Ừm, chỉ có thể như vậy, đây là chúng ta Chư Tử bách gia sứ mệnh, cũng là trách nhiệm.”"),t("br"),t("br"),n._v("Lắc đầu, Nhan Thanh Cổ Thánh trên mặt không vui không buồn, không nói thêm lời, khom người hướng về phía trước mắt Khổng sư tượng hành lễ, tiến hành tế bái."),t("br"),t("br"),n._v("“Chí Thánh tiên sư, tại hạ là Tử Uyên Cổ Thánh thứ bảy mươi ba thế hệ huyền tôn, Nhan Thanh, tuân thủ cổ huấn, tận hết chức vụ, chưa hề có một ngày lười biếng, hiện tại Khổng miếu mở ra, đã đi đến tổ tông truyền miệng bên trong điểm tới hạn, không cho phép lùi bước, chúng ta nguyện ý làm dâng hiến sinh mệnh, mong rằng tiên sư ở trên thương nhận được tin tức, phù hộ hậu nhân!”"),t("br"),n._v("Quỳ trên mặt đất, thần thái cung kính."),t("br"),t("br"),n._v("Tượng Khổng sư hình như không có bất kỳ cái gì ý thức, không nhúc nhích, vẫn như cũ yên tĩnh."),t("br"),t("br"),n._v("Tế bái xong, mọi người đứng dậy, Nhan Thanh Cổ Thánh thoạt nhìn càng thêm suy yếu một chút: “Đi qua đi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trong mắt mọi người đều lộ ra bất đắc dĩ chi ý, lại đều không nói gì, đồng thời xoay người."),t("br"),t("br"),n._v("“Điểm giới hạn? Dâng hiến sinh mệnh?”"),t("br"),t("br"),n._v("Thấy bọn họ rời đi, Trương Huyền bỏ đi chung quanh thân thể cấm chế, không hiểu ra sao."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, những này Cổ Thánh, hẳn là ngủ say, làm sao tới nơi này nói ra những lời này? Thương thế trên người, lại là chuyện gì xảy ra?"),t("br"),t("br"),n._v("Trước đó không phải nghe nói, bọn họ tại Khổng miếu tiến hành chữa trị, chẳng lẽ chữa trị Khổng miếu, cũng sẽ thụ thương?"),t("br"),t("br"),n._v("Tràn đầy kỳ lạ, theo sát ở phía sau đi ra ngoài."),t("br"),t("br"),n._v("“Long Cốt thần thương, giúp ta che giấu khí tức!”"),t("br"),t("br"),n._v("Bàn tay run lên, thần thương lần nữa biến thành đai lưng, đem toàn thân bao phủ, trong chớp mắt cả người giống như là rút nhỏ đồng dạng, lại không nửa điểm khí tức tiết lộ, tích huyết trùng sinh cường giả đỉnh phong, đều không thể phát giác."),t("br"),t("br"),n._v("Trương Huyền hiện tại sức chiến đấu, cứ việc có thể so với tích huyết trùng sinh, dù sao không có đột phá đến Cổ Thánh, đối lực lượng khống chế bên trên, còn có chút lực có không bằng, duy trì Bất Hủ cảnh đại viên mãn không khiến người ta phát hiện ngược lại cũng thôi, theo dõi đám này đại lục đỉnh phong nhất cường giả, rất dễ dàng bị dò xét đến."),t("br"),t("br"),n._v("Long Cốt thần thương thôn phệ long huyết, chỉ thiếu chút nữa, liền có thể đột phá đến phá toái hư không, chân chính sức chiến đấu, so Nhan Thanh Cổ Thánh đều cường đại hơn không ít, do nó hỗ trợ, đối phương lại nhạy bén cũng không có cách nào biết."),t("br"),t("br"),n._v("Rời đi Thánh Nhân từ đường về sau, nhìn thấy Nhan Thanh Cổ Thánh đám người, thẳng tắp hướng lên bầu trời bay đi, xé rách không gian, theo sát phía sau."),t("br"),t("br"),n._v("Phi hành không biết bao xa, đi tới một chỗ đài cao."),t("br"),t("br"),n._v("Phía trên rỗng tuếch, không có cái gì."),t("br"),t("br"),n._v("“Nơi này không gian... Di động tính thật nhanh!”"),t("br"),t("br"),n._v("Rơi vào phía trên, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Nhìn bằng mắt thường đứng dậy, trước mắt mười phần yên tĩnh, nhưng đối không gian lĩnh ngộ đi đến chân giải tình trạng, có thể tuỳ tiện cảm nhận được, trước mắt vùng trời này, nhanh chóng di động, không ngừng biến hóa, hình như bất cứ lúc nào cũng sẽ không chịu nổi, triệt để sụp đổ."),t("br"),t("br"),n._v("Mấu chốt nhất là, không gian sau lưng, cho người ta một loại rùng mình cảm giác, tựa như một loại đặc thù lực lượng, đến từ linh hồn."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Đứng vững phương vị, Nhan Thanh Cổ Thánh nói."),t("br"),t("br"),n._v("Mọi người gật gật đầu, đồng thời cắn răng, vừa định thi triển lực lượng, nơi xa một cơn gió mạnh gào thét, hai bóng người thẳng tắp bay tới."),t("br"),t("br"),n._v("Nhìn thấy lúc này có người tới, mọi người dừng lại, đồng loạt nhìn lại, Trương Huyền cũng xoay đầu lại."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thân ảnh hạ xuống."),t("br"),t("br"),n._v("Đi đầu là cái nữ hài, người cao thon, mái tóc đen nhánh, bồng bềnh tại bên hông, cho người ta một loại phiêu dật cảm giác, thoạt nhìn hai mươi tuổi hình dáng, một đôi trong đôi mắt đẹp mang theo lo lắng."),t("br"),t("br"),n._v("Phía sau nàng là cái hai mươi mấy tuổi thanh niên, một thân áo xanh, Trương Huyền vừa vặn biết, chính là vị kia tại Nhiễm Cầu Cổ Thánh di tích, cùng hắn tranh đoạt truyền thế mẫu phù Chư Tử bách gia cường giả, Nhan Tiết!"),t("br"),t("br"),n._v("“Các ngươi sao lại tới đây?”"),t("br"),t("br"),n._v("Thấy là hai người, Nhan Thanh Cổ Thánh sầm mặt lại."),t("br"),t("br"),n._v("“Ta thân là Khổng sư hậu nhân, xuất hiện loại tình huống này, làm sao có thể không được...”"),t("br"),t("br"),n._v("Tiến về phía trước một bước, nữ hài thanh âm bên trong mang theo không cho cự tuyệt mùi vị."),t("br"),t("br"),n._v("“Khổng sư hậu nhân?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ: “Chẳng lẽ chính là vị kia trong truyền thuyết... Khổng Thi Dao?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1899"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1901"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);