(window.webpackJsonp=window.webpackJsonp||[]).push([[1017],{1371:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1889-nhan-hoi-thi-the"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1889-nhan-hoi-thi-the"}},[n._v("#")]),n._v(" Chương 1889: Nhan Hồi thi thể")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Khổng Thi Dao cái tên này, hắn nghe rất nhiều người nói qua, Chư Tử bách gia cường đại nhất yêu nghiệt, tu vi thiên phú, đều đi đến tuyệt đỉnh, nghe nói so với hắn, đều không hề yếu."),t("br"),t("br"),n._v("Lúc trước, từng một lần cho rằng, Lạc Nhược Hi chính là đối phương, về sau mới biết được, cả hai không có bất cứ quan hệ nào."),t("br"),t("br"),n._v("Trước đó chỉ là nổi tiếng, giờ phút này đứng tại phía trước, kìm lòng không được nhìn kỹ lại."),t("br"),t("br"),n._v("Quả nhiên cùng trong truyền thuyết đồng dạng, không chỉ dung mạo mỹ lệ, hoàn mỹ không một tì vết, trên người còn có một loại đặc biệt khí chất, như tiên như linh, để cho người ta nhìn lên một cái, liền khó mà tự kềm chế."),t("br"),t("br"),n._v("Lạc Nhược Hi kèm theo vắng lặng mùi vị, người lạ chớ quấy rầy, không dễ dàng tiếp cận; Mà trước mắt vị này, có loại làm cho người tin phục, nguyện ý đi theo khí chất, dù là chỉ thấy bên trên một mặt, liền có thể làm ném đầu lâu vẩy nhiệt huyết."),t("br"),t("br"),n._v("“Lợi hại danh sư, dù là không nói lời nào, đứng tại chỗ, liền có thể kìm lòng không được thu hút chú ý của mọi người, không khiến người ta phân tán tinh thần... Vị này Khổng Thi Dao, hiển nhiên có loại này đặc biệt khí chất, hẳn là Khổng sư huyết mạch duyên cớ...”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Không hổ là Khổng sư hậu nhân, nhìn một cái, liền không tầm thường."),t("br"),t("br"),n._v("Còn tu vi, vị này Khổng Thi Dao cũng giống như mình, đều là Bất Hủ cảnh đại viên mãn, cũng không đột phá đến Cổ Thánh cấp bậc, nhưng chân khí trong cơ thể cho người ta một loại nặng nề cảm giác, chứa mà không phát, chân chính sức chiến đấu, so với bản thân, chỉ sợ đều không hề yếu."),t("br"),t("br"),n._v("Cùng nhau đi tới, còn là lần đầu tiên nhìn thấy, cùng cấp bậc cùng mình có chút tương đương cường giả."),t("br"),t("br"),n._v("“Nếu ngươi biết thân là Khổng sư hậu nhân, nên rõ ràng, trên bờ vai gánh vác trách nhiệm, chúng ta có thể hi sinh, ngươi... Không được!” Nhan Thanh Cổ Thánh lắc đầu."),t("br"),t("br"),n._v("“Khổng sư hậu nhân, không e ngại hi sinh!”"),t("br"),t("br"),n._v("Khổng Thi Dao trong ánh mắt mang theo kiên định: “Ta nắm giữ Khổng sư lưu truyền tới nay, tinh thuần nhất huyết mạch, lần nữa ổn định nơi này thích hợp nhất, cũng đừng có tranh giành!”"),t("br"),t("br"),n._v("Thấy nàng quyết tuyệt như vậy, Nhan Thanh Cổ Thánh than thở một tiếng: “Thi Dao, ngươi còn trẻ, về sau muốn đi đường rất dài, ta đã gần đất xa trời, chuyện này không cần lại nói...”"),t("br"),t("br"),n._v("Trong lời nói, đột nhiên ra tay."),t("br"),t("br"),n._v("Chạy như điên lực lượng, chớp mắt liền đem Khổng Thi Dao bao phủ ở bên trong, đem hắn cố định tại nguyên chỗ, khẽ động cũng không thể động."),t("br"),t("br"),n._v("“Nhan Thanh Cổ Thánh...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn làm như vậy, Khổng Thi Dao tràn đầy gấp gáp, nước mắt chảy chảy mà xuống."),t("br"),t("br"),n._v("Không hề bị lay động, Nhan Thanh Cổ Thánh quay đầu nhìn về phía một bên Nhan Tiết, trong ánh mắt mang theo nghiêm khắc: “Chăm sóc tốt nàng, ngươi có thể chết, nàng không thể có bất kỳ tổn thương gì!”"),t("br"),t("br"),n._v("“Vâng!” Nhan Tiết gật đầu."),t("br"),t("br"),n._v("Lúc này mới thở phào nhẹ nhõm, Nhan Thanh Cổ Thánh nhìn quanh một vòng: “Bắt đầu đi!”"),t("br"),t("br"),n._v("Kèm theo lời nói, một cỗ kiên quyết mà cường đại khí tức, từ trên người hắn tuôn ra, bốn phía rất nhiều Cổ Thánh lực lượng phối hợp đài cao, thời gian nháy mắt hình thành một cái to lớn cột sáng, xông vào trời cao."),t("br"),t("br"),n._v("Nguyên bản yên tĩnh bầu trời trong chớp mắt, sấm sét vang dội, xuất hiện từng đạo vết rách to lớn, vô số sức mạnh mạnh mẽ, dường như muốn từ trong vết nứt, chui ra ngoài, tràn vào thế giới này."),t("br"),t("br"),n._v("“Đây là... Dị Linh tộc sát lục chi khí?”"),t("br"),t("br"),n._v("Cảm nhận được cỗ lực lượng này, giấu ở một bên Trương Huyền, nhịn không được sững sờ, ngay sau đó lông mày nâng lên: “Không đúng, so sát lục chi khí càng thêm nặng nề, cùng thủy ngân đồng dạng, nếu như ta hấp thu, kinh mạch sẽ rất khó nhận nhận phụ tải...”"),t("br"),t("br"),n._v("Sầm mặt lại."),t("br"),t("br"),n._v("Khe hở đằng sau che giấu lực lượng, cùng sát lục chi khí có chút tương tự, nhưng càng thêm cổ quái, cho dù hắn loại tu vi này, cũng không dám hấp thu."),t("br"),t("br"),n._v("Bình thường linh khí, cùng dòng nước đồng dạng, ống nước có thể thừa nhận, đổi thành thủy ngân, lại không được, đồng dạng là chất lỏng, đừng nói vận chuyển di động, có thể vừa mới đi vào, liền sẽ bị tươi sống căng nứt."),t("br"),t("br"),n._v("Mà cỗ này tựa như thủy ngân đồng dạng đặc thù khí tức, liền ẩn giấu ở khe hở đằng sau, bất cứ lúc nào cũng sẽ lan ra mà tới."),t("br"),t("br"),n._v("Có thể đoán được, một khi tự nhiên xuống, toàn bộ Chư Tử bách gia tu luyện giả, làm không cẩn thận đều sẽ bạo thể, sống không bằng chết."),t("br"),t("br"),n._v("“Đến cùng là chuyện gì xảy ra?”"),t("br"),n._v(" Trương Huyền tràn đầy ngạc nhiên."),t("br"),t("br"),n._v("Trên không lại có loại này cổ quái lực lượng, hơn nữa nhìn bộ dáng, bất cứ lúc nào cũng sẽ phong ấn không được, tuôn trào ra!"),t("br"),t("br"),n._v("“Chặn lại!”"),t("br"),t("br"),n._v("Trong lòng của hắn kỳ lạ, trong đài cao ở giữa Nhan Thanh Cổ Thánh thân thể chậm rãi lơ lửng lên, tóc tại cuồng phong thổi bên dưới, bốn phía tản ra, lực lượng hùng hồn không ngừng hướng lên bầu trời tuôn ra, sấm sét càng ngày càng nhiều, áp lực cũng càng ngày càng nặng, phảng phất bầu trời sẽ không chịu nổi, rơi xuống."),t("br"),t("br"),n._v("Bốn phía rất nhiều Cổ Thánh, cũng từng cái khuôn mặt trắng bệch, tóc bạch càng nhiều."),t("br"),t("br"),n._v("“Bọn họ đây là tại thiêu đốt sinh mệnh...”"),t("br"),t("br"),n._v("Trương Huyền sắc mặt nghiêm túc."),t("br"),t("br"),n._v("Khó trách từ lần trước nhìn thấy những người này, đến bây giờ, chẳng qua ba cái tháng sau, nhưng già lợi hại như thế, nguyên lai là dùng sinh mệnh tại duy trì trước mắt phong ấn."),t("br"),t("br"),n._v("Kèm theo trên không lôi đình càng ngày càng dày đặc, Nhan Thanh Cổ Thánh cắt ra đầu ngón tay, máu tươi hướng sâu trong hư không bay đi, ngay sau đó, bầu trời đen kịt, một cái to lớn quan tài, như ẩn như hiện."),t("br"),t("br"),n._v("Nhìn thấy cái này quan tài, bốn phía vết rách trở nên suy yếu không ít, bất cứ lúc nào cũng sẽ lật đổ mà xuống thủy ngân lực lượng, cũng giống là có chút kiêng kị cùng sợ hãi, không còn trước đó cảm giác áp bách."),t("br"),t("br"),n._v("“Cái này quan tài...”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Trên không xuất hiện quan tài, vốn là để cho người ta không hiểu, chớ nói chi là cái này quan tài hình dáng, cùng trước đó đi vào nơi này lúc, chứa đựng Nhiễm Cầu Cổ Thánh thi thể cỗ kia, giống nhau như đúc."),t("br"),t("br"),n._v("“Không đúng, cái này quan tài là phong ấn trận tâm!”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, Trương Huyền ngay sau đó giật mình."),t("br"),t("br"),n._v("Trước mắt cái này quan tài, cũng không phải là đơn giản chứa đựng thi thể, mà là trên không phong ấn trận tâm, nguyên nhân chính là có hắn trấn áp, như thủy ngân lực lượng, mới không còn từ trên trời giáng xuống, đem người áp bức mà chết."),t("br"),t("br"),n._v("“Chỉ sợ trong này chứa đựng một vị không kém gì Nhiễm Cầu Cổ Thánh mạnh mẽ Cổ Thánh!”"),t("br"),t("br"),n._v("Một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Có thể ngăn cản như vậy nặng nề lực lượng hạ xuống, phong ấn mạnh mẽ, không thể nghi ngờ, làm trận tâm, tự nhiên càng thêm bất phàm."),t("br"),t("br"),n._v("Trước đó Nhiễm Cầu Cổ Thánh thi thể xem như trận tâm, phá toái hư không cường giả lực lượng liền phá giải không ra, trước mắt cái này phong ấn, càng thêm cường đại, thi thể lại nên mạnh cỡ nào?"),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Đang tràn đầy rung động, trên không quan tài, một hồi kịch liệt nổ vang, chậm rãi rách ra một cái khe."),t("br"),t("br"),n._v("“Mở!”"),t("br"),t("br"),n._v("Nhìn thấy xuất hiện khe hở, Nhan Thanh Cổ Thánh trên đầu gân xanh nứt ra, đầu ngón tay máu tươi không ngừng tự nhiên, nhất đạo máu chảy, thẳng tắp tràn vào quan tài trong khe hở."),t("br"),t("br"),n._v("Mất máu quá nhiều, sắc mặt của hắn trở nên trắng bệch, thân thể kìm lòng không được run rẩy, trên đầu tóc trắng càng nhiều, làn da cũng càng thêm buông lỏng, hình như trong nháy mắt vừa già hơn mười tuổi."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Kèm theo huyết dịch tràn vào càng ngày càng nhiều, trên quan tài khe hở quả nhiên càng lúc càng lớn, một cái cao lớn thi thể, chậm rãi hiện ra ở trước mắt của tất cả mọi người."),t("br"),t("br"),n._v("Cùng đoán đồng dạng, cái này quan tài bên trong, quả nhiên chứa đựng một vị Cổ Thánh thi thể, người mặc trường bào, dung mạo bình thản, tuy là ngủ say tại trong phong ấn, vài vạn năm, nhưng cùng vừa mới tử vong đồng dạng, làn da tràn đầy co dãn, không có chút nào mục nát."),t("br"),t("br"),n._v("“Đây chẳng lẽ là...”"),t("br"),t("br"),n._v("Nhìn kỹ hướng vị này Cổ Thánh thi thể, Trương Huyền trong đầu lập tức tung ra tên của một người, thân thể cứng đờ: “Tử Uyên Cổ Thánh... Nhan Hồi?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1900"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1902"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);