(window.webpackJsonp=window.webpackJsonp||[]).push([[1018],{1373:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1890-khong-thi-dao-thuc-luc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1890-khong-thi-dao-thuc-luc"}},[n._v("#")]),n._v(" Chương 1890: Khổng Thi Dao thực lực")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Tuy là chưa có xem qua Tử Uyên Cổ Thánh tượng, nhưng không có nghĩa là đối vị này vang danh Thiên Cổ Cổ Thánh hoàn toàn không biết gì cả."),t("br"),t("br"),n._v("Thân là Khổng sư đệ nhất thân truyền, thực lực của hắn, có thể không bằng Nhiễm Cầu Cổ Thánh, nhưng đức hạnh, phụ tu, Khổng sư hệ thống tu luyện truyền thừa, cùng đại lục rất nhiều chức nghiệp quả thực dựng lên, tuyệt đối làm người ta kính nể, không người không vì chi thần phục."),t("br"),t("br"),n._v("Trước mắt bộ này thi thể, không có Nhiễm Cầu Cổ Thánh thi thể bén nhọn như vậy, mang theo sắc bén, nhưng giống như là biển cả đồng dạng, có thể dung nạp bách xuyên, duy trì trận pháp, để mạnh mẽ thủy ngân lực lượng, mảy may đều lan ra không đến."),t("br"),t("br"),n._v("Khổng sư thân truyền bảy mươi hai hiền, có thể làm được cái này, chỉ sợ chỉ có vị đại sư này huynh!"),t("br"),t("br"),n._v("Lại thêm vừa rồi Nhan Thanh Cổ Thánh huyết dịch, hướng bên trong lan ra, không ngừng tẩm bổ cỗ thi thể này, không cần đoán, cũng có thể rõ ràng, đến cùng là ai."),t("br"),t("br"),n._v("“Tử Uyên Cổ Thánh thi thể, Nhiễm Cầu Cổ Thánh thi thể...”"),t("br"),t("br"),n._v("Trương Huyền toàn thân rét run."),t("br"),t("br"),n._v("Hai vị này Cổ Thánh, danh hào vang vọng đất trời, vốn cho rằng đi theo Khổng sư rời đi đại lục, nào ngờ, thi thể đều trưng bày ở đây, chưa hề rời đi."),t("br"),t("br"),n._v("“Hiến tế!”"),t("br"),t("br"),n._v("Ngay tại khiếp sợ, trên không Nhan Thanh Cổ Thánh hét lớn một tiếng, ngay sau đó thân thể giống như là đồ sứ giống như vỡ ra, vô số máu tươi, từ mạch máu bắn tung toé mà ra, rất nhanh hướng quan tài bên trong thi thể vọt tới."),t("br"),t("br"),n._v("Hấp thu huyết dịch, thi thể trở nên càng thêm mềm mại, hình như bất cứ lúc nào cũng sẽ sống lại, không chỉ có như vậy, lực lượng cũng biến thành mạnh hơn, trước đó còn có chút lay động phong ấn, tại đây chút lực lượng gia trì bên dưới, trở nên càng ngày càng củng cố."),t("br"),t("br"),n._v("“Nhan Thanh Cổ Thánh là Tử Uyên Cổ Thánh hậu nhân, trong cơ thể nắm giữ của hắn huyết mạch truyền thừa, làm như vậy, là muốn tiếp tục duy trì Cổ Thánh lực lượng, để phong ấn không đến mức tán loạn...”"),t("br"),t("br"),n._v("Trương Huyền rốt cuộc minh bạch tới."),t("br"),t("br"),n._v("Khó trách vị này Nhan Thanh Cổ Thánh, luôn miệng nói muốn hi sinh."),t("br"),t("br"),n._v("Trận tâm là hắn tiên tổ, dùng hậu bối huyết mạch tẩm bổ, có thể lần nữa củng cố."),t("br"),t("br"),n._v("Còn Khổng Thi Dao, nắm giữ Khổng sư huyết mạch, hẳn là đối Tử Uyên Cổ Thánh thi thể, cũng có rất tốt hiệu quả, thậm chí so Nhan Thanh Cổ Thánh mạnh hơn."),t("br"),t("br"),n._v("“Quả nhiên ổn định...”"),t("br"),t("br"),n._v("Thấy kèm theo huyết dịch truyền vào, phong ấn dần dần ổn định, cái loại này bất cứ lúc nào cũng sẽ áp bách xuống khí tức, chậm chạp suy giảm, Nhan Thanh Cổ Thánh thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Hắn lúc này, huyết dịch đã tiêu hao không sai biệt lắm, có chút không kiên trì nổi, bất cứ lúc nào cũng sẽ từ không trung rơi xuống, triệt để mất đi sinh mệnh."),t("br"),t("br"),n._v("Bị phong cấm Khổng Thi Dao, thấy cảnh này, trong mắt tràn đầy nước mắt, không ngừng lắc đầu, nhưng không thể động đậy."),t("br"),t("br"),n._v("Nhan Tiết cũng nắm đấm xiết chặt."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nhưng vào lúc này, đột nhiên xảy ra dị biến."),t("br"),t("br"),n._v("Mắt thấy tinh huyết truyền vào bên dưới, Tử Uyên Cổ Thánh thi thể càng ngày càng mạnh, lập tức liền muốn đem phong ấn triệt để củng cố, đột nhiên, một cái bàn tay khổng lồ, từ trong vết nứt đưa ra ngoài."),t("br"),t("br"),n._v("Một màn này, cùng lúc trước Thần Linh Hoàng tế tự Thượng Thương, Thần Linh giáng thế có chút tương tự."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Vươn ra bàn tay, trên không trung nhẹ nhàng bóp một cái, ngay tại cho Tử Uyên Cổ Thánh thi thể truyền vào máu tươi Nhan Thanh Cổ Thánh, lập tức bị trọng kích, cũng lại không chịu nổi, thẳng tắp từ không trung rớt xuống, trùng trùng điệp điệp ngã xuống đất."),t("br"),t("br"),n._v("Phốc! Phốc! Phốc!"),t("br"),t("br"),n._v("Bốn phía trợ giúp hắn rất nhiều Cổ Thánh cũng đồng thời máu tươi phun mạnh, từng cái té ngã trên đất."),t("br"),t("br"),n._v("“Phá toái hư không cường giả...”"),t("br"),t("br"),n._v("Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Cái này bàn tay chủ nhân, thực lực so với trước đó Thiên đạo chi sách đập chết vị kia, tựa hồ cũng cường đại hơn."),t("br"),t("br"),n._v("Không có tế đàn triệu hoán, cách không gian liền muốn xông lại."),t("br"),t("br"),n._v("Chẳng lẽ... Phong ấn bên kia thế giới, đúng là hắn muốn đi tới... Thượng Thương?"),t("br"),t("br"),n._v("Nghi ngờ bên trong, trên không bàn tay liên tục đập xuống, vừa mới gia cố phong ấn, tại lực lượng trùng kích vào, trở nên lung lay sắp đổ, dường như so trước đó càng thêm không bằng."),t("br"),t("br"),n._v("“Mau ngăn cản!”"),t("br"),t("br"),n._v("Không nghĩ tới lập tức liền lớn hơn công hoàn thành, đột nhiên toát ra như vậy một cái bàn tay, Nhan Thanh Cổ Thánh lại không để ý tới khôi phục, tiếng gào thét bên trong, lần nữa hướng trên không xông tới, đồng thời cổ tay khẽ đảo, một thanh trường đao, hướng về phía bàn tay lớn liền bổ xuống."),t("br"),t("br"),n._v("Hắn biết, muốn tiếp tục duy trì phong ấn, nhất định phải đem cái này bàn tay chém xuống, thậm chí đem hắn chủ nhân chém giết."),t("br"),n._v(" Không phải, không có những biện pháp khác."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Cảm nhận được sát ý của hắn, bàn tay chủ nhân, năm ngón tay mở ra, nhẹ nhàng bóp một cái."),t("br"),t("br"),n._v("Đao sắc bén mang liền bị bóp tại đầu ngón tay, tựa như một đầu bị bắt bảy tấc rắn độc, bất kể như thế nào đấu tranh, cũng không có cách nào."),t("br"),t("br"),n._v("“Cùng tiến lên!”"),t("br"),t("br"),n._v("Cái khác Cổ Thánh cũng rõ ràng tình huống nghiêm trọng, không để ý tới nói nhiều, đồng loạt đánh tới."),t("br"),t("br"),n._v("Mấy vị Cổ Thánh đồng thời ra tay, trước đó liền không vững chắc thiên địa, xuất hiện lần nữa từng đạo vết rách, vô số ngọn lửa màu đen, từ không trung hướng phía dưới đập xuống, thời gian nháy mắt, đài cao tựa như biến thành địa ngục nhân gian."),t("br"),t("br"),n._v("“Nhan Tiết, thả ta ra, không phải tất cả mọi người muốn chết!”"),t("br"),t("br"),n._v("Thân thể không ngừng run rẩy, Khổng Thi Dao bỗng nhiên nhìn về phía thanh niên trước mắt."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Nhan Tiết vẻ mặt có chút vặn vẹo, trong lòng tràn đầy đấu tranh."),t("br"),t("br"),n._v("“Nhanh lên một chút, không phải, vài vạn năm cơ nghiệp, đều sẽ hủy hoại chỉ trong chốc lát...”"),t("br"),t("br"),n._v("Gặp hắn do dự, Khổng Thi Dao khí sắc mặt chuyển hồng."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Biết tình cảnh nguy cấp, không đem phóng thích, một khi lão tổ cùng rất nhiều Cổ Thánh toàn bộ ngã xuống, phong ấn lại kiên trì không được, Côn Hư cảnh quả thực lại bởi vậy hủy diệt, ngón tay liên tục điểm tới."),t("br"),t("br"),n._v("Nhan Tiết tu vi, cứ việc chỉ là Bất Hủ cảnh đại viên mãn, nhưng phong ấn là lão tổ Ngạn Thanh Cổ Thánh lưu lại, đồng căn đồng nguyên, giải quyết, cũng không phức tạp."),t("br"),t("br"),n._v("Rất nhanh, ánh sáng lóe lên, giam cầm tại Khổng Thi Dao bốn phía lực lượng biến mất không thấy hình bóng."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Một tiếng khẽ kêu, Khổng Thi Dao đôi mi thanh tú bỗng nhiên nâng lên, cả người giống như là kích hoạt lên huyết mạch, thời gian nháy mắt khí tức cường đại, tuôn trào ra."),t("br"),t("br"),n._v("Thân thể thoáng một cái, đi tới trên không, lòng bàn tay một thanh trường kiếm, bất ngờ xuất hiện, thẳng tắp hướng bàn tay đâm tới."),t("br"),t("br"),n._v("“Thật mạnh!”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Cùng đoán đồng dạng, vị này Khổng Thi Dao thoạt nhìn tuổi không lớn lắm, tu vi cũng không cao, sức chiến đấu nhưng không thể phủ nhận mạnh mẽ."),t("br"),t("br"),n._v("Cùng mình vậy mà không kém bao nhiêu, cũng có thể so với tích huyết trùng sinh."),t("br"),t("br"),n._v("Đương nhiên, nàng chuôi kiếm này cũng hết sức lợi hại, so với Long Cốt thần thương, đều chỉ mạnh không yếu, hai hai điệp gia, để hắn phát huy ra có thể so với tích huyết trùng sinh đỉnh phong mạnh mẽ sức chiến đấu."),t("br"),t("br"),n._v("Đinh đinh đinh!"),t("br"),t("br"),n._v("Kiếm khí rơi vào trên bàn tay, khuấy động ra vô số tia lửa."),t("br"),t("br"),n._v("Khổng Thi Dao không tức giận chút nào, tiếp tục chém xuống."),t("br"),t("br"),n._v("“Không biết sống chết!”"),t("br"),t("br"),n._v("Bàn tay chủ nhân, thấy hạ giới tiểu lâu la, cũng dám động thủ với hắn, tức giận hừ thanh âm vang lên, cong ngón tay búng một cái."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Khổng Thi Dao bị đánh trúng, bay ngược mà ra, trùng trùng điệp điệp ngã xuống đất, khóe miệng máu tươi tràn ra."),t("br"),t("br"),n._v("Phá toái hư không cùng tích huyết trùng sinh, cứ việc đều là Cổ Thánh, có thể chênh lệch thực sự quá lớn, lúc trước Trương Huyền cùng mọi người liên thủ, cũng không phải vị kia “Thần Linh” đối thủ, cũng có thể thấy được."),t("br"),t("br"),n._v("Căn bản không thể so sánh nổi."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Một chỉ bắn bay Khổng Thi Dao, chấn Nhan Thanh Cổ Thánh đám người không cách nào khá cao, bàn tay bỗng nhiên một trảo, một tiếng gấm lụa xé rách thanh âm vang lên, vốn cũng không củng cố trên không khe hở trở nên càng lớn, lại một cái bàn tay ló ra, ngay sau đó toát ra một cái to lớn đầu lâu."),t("br"),t("br"),n._v("Người này cùng lúc trước “Thần Linh” đồng dạng, tính toán từ một thế giới khác, cứ thế mà chui vào."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1901"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1903"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);