(window.webpackJsonp=window.webpackJsonp||[]).push([[1019],{1374:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1891-mot-loi-thanh-quy-tac-van-phap-toi-đi-theo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1891-mot-loi-thanh-quy-tac-van-phap-toi-đi-theo"}},[n._v("#")]),n._v(" Chương 1891: Một lời thành quy tắc, vạn pháp tới đi theo")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Kèm theo cái này đầu người xuất hiện, trên không như thủy ngân lực lượng, càng thêm nồng nặc, Trương Huyền vội vàng phong bế toàn thân lỗ chân lông, không dám hấp thu một tia."),t("br"),t("br"),n._v("Những này như thủy ngân lực lượng, cùng sát lục chi khí tương đồng, thuộc về một loại đặc thù thể khí, mặc cho hắn tràn vào trong cơ thể, có thể kinh mạch toàn thân đều sẽ bị xé rách, tu vi tổn hao nhiều, nghiêm trọng thậm chí có sinh mệnh nguy hiểm."),t("br"),t("br"),n._v("“Giết!”"),t("br"),t("br"),n._v("Không nghĩ tới không có ngăn cản vị này “Thần Linh” còn để hắn đem đầu lâu duỗi tới, Nhan Thanh Cổ Thánh đám người tất cả đều vẻ mặt trở nên khó coi, đồng thời gầm thét, tiếp tục hướng phía trước vọt tới."),t("br"),t("br"),n._v("Đủ loại lực lượng cuốn lên, phong ấn không ngừng lay động, không gian cũng tại từng khối nghiền nát, “Thần Linh” duỗi ra ngón tay, chặn lại mọi người công kích, cái tay còn lại, thì cào lấy khe hở tiếp tục hướng bên ngoài chui ra, rất nhanh, toàn bộ bả vai liền lộ ra."),t("br"),t("br"),n._v("Đối phó mọi người vây công đồng thời, còn có lực lượng, hướng ra phía ngoài chui ra."),t("br"),t("br"),n._v("Chỉ bằng vào điểm này, vị này quả thực muốn so trước đó bị một sách đập chết gia hỏa cường đại không ít."),t("br"),t("br"),n._v("“Tiếp tục như vậy, không ngăn cản được!”"),t("br"),t("br"),n._v("Liên tục đâm mấy trăm kiếm, lực lượng trong cơ thể tiêu hao sắp hầu như không còn, Khổng Thi Dao răng ngà cắn chặt, bờ môi đều mơ hồ có chút tơ máu, đột nhiên tú mi hơi giương, âm thanh trong trẻo vang vọng đài cao: “Nhan Thanh Cổ Thánh, trước không vội tấn công, dùng hết toàn lực đem Tử Uyên Cổ Thánh thi thể mang tới...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh run lên."),t("br"),t("br"),n._v("“Không có biện pháp khác, lại do dự mà nói, chúng ta đều có thể chết ở chỗ này!”"),t("br"),t("br"),n._v("Gặp hắn không có động tác, Khổng Thi Dao quát tháo."),t("br"),t("br"),n._v("Một khi trước mắt vị này xuyên qua khe hở tới, bọn họ tất cả mọi người sẽ bị tuỳ tiện chém giết không nói, Chư Tử bách gia cũng có thể bị tai nạn, không thể quay lại đường sống."),t("br"),t("br"),n._v("Kế trước mắt, chỉ có thể liều mạng, dù là... Bỏ mình!"),t("br"),t("br"),n._v("“Nhưng... Ngươi là Khổng sư trong hậu bối, huyết mạch duy nhất độ tinh thuần vượt qua ‘Tám’...”"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh cắn răng."),t("br"),t("br"),n._v("Hắn biết ý nghĩ của đối phương, nhưng... Làm như vậy, có khả năng hi sinh!"),t("br"),t("br"),n._v("Tuổi tác hắn lớn, chết thì chết tai, trước mắt vị này, nhưng mà Khổng sư huyết mạch, ngàn năm qua, tinh thuần nhất một vị, hơn nữa đánh sâu vào trọn ba lần lôi kiếp, còn không có đột phá, chỉ cần cấp cho đầy đủ thời gian, vô cùng có khả năng xung kích phá toái hư không cảnh giới, thật muốn chết ở chỗ này... Thế nào xứng đáng liệt tổ liệt tông?"),t("br"),t("br"),n._v("“Hắn một khi đi ra, mọi người tất cả đều chết, huyết mạch tinh khiết đến đâu cũng vô dụng...”"),t("br"),t("br"),n._v("Khổng Thi Dao một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Mãnh liệt!"),t("br"),t("br"),n._v("Máu tươi xuất hiện, lập tức ở trên không thiêu đốt lên, không gian bốn phía, thời gian giống như là bị giam cầm ở, ngay cả muốn bò ra tới “Thần Linh”, cũng ngừng lại, tại không có động tác khác."),t("br"),t("br"),n._v("Giống bỗng chốc bị người ấn tạm dừng phím."),t("br"),t("br"),n._v("Phun ra máu tươi, Khổng Thi Dao sắc mặt tái nhợt không ít, trên trán lít nha lít nhít xuất hiện một tầng mồ hôi lạnh, nhìn về phía cách đó không xa huyền phù thạch quan, một tiếng quát tháo: “Còn không qua đây!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Quan tài tựa hồ nghe đến mệnh lệnh, phát ra trầm muộn nổ vang, lắc lư một cái, chậm rãi tới gần, thời gian qua một lát liền đến đến trước mặt."),t("br"),t("br"),n._v("“Cái này... Sư ngôn thiên bẩm?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Khổng Thi Dao phun ra máu tươi, vận dụng huyết mạch lực lượng, một mực suy đoán, Khổng sư hậu nhân sẽ có loại lực lượng nào, không nghĩ tới lại là... Danh sư cũng biết sư ngôn thiên bẩm."),t("br"),t("br"),n._v("Có thể hay không quá đơn giản chút?"),t("br"),t("br"),n._v("Trương gia thời gian lực lượng, Lạc gia không gian lực lượng, đều cường đại đến cực hạn, sư ngôn thiên bẩm... Thấy thế nào đều không phù hợp Khổng sư hậu nhân thân phận."),t("br"),t("br"),n._v("“Không đúng!”"),t("br"),n._v(" Tràn đầy kỳ lạ, trong mắt hoa văn nhúc nhích, trong lòng chấn động mạnh một cái."),t("br"),t("br"),n._v("“Đây không phải sư ngôn thiên bẩm, mà là...”"),t("br"),t("br"),n._v("Sư ngôn thiên bẩm, là muốn truyền thụ kiến thức được thiên địa công nhận, mới có thể để cho tu luyện giả kìm lòng không được nghe theo mệnh lệnh, hắn lĩnh ngộ sư giả chi tâm, cứ việc có thể làm cho một ít đồ vật, nghe theo chỉ huy, nhưng đầu tiên cũng muốn cho đối phương khải linh."),t("br"),t("br"),n._v("Nói cách khác, muốn sư ngôn thiên bẩm, nhất định phải có linh tính, có thể tu luyện, hơn nữa, nói ra công pháp, còn muốn thích hợp đối phương, thoạt nhìn dễ dàng, điều kiện lại hết sức nghiêm khắc."),t("br"),t("br"),n._v("Còn lấy hắn làm thí dụ, muốn sư ngôn thiên bẩm, để cho người ta nghe theo mệnh lệnh, thứ nhất, đối phương tâm cảnh thấp hơn bản thân; Thứ hai, truyền thụ công pháp, đối hắn có rất lớn dẫn dắt... Hai thứ này, thiếu một thứ cũng không được."),t("br"),t("br"),n._v("Thời khắc này Khổng Thi Dao, cứ việc sức chiến đấu không yếu, tu vi chân chính, cũng chỉ có Bất Hủ cảnh đại viên mãn, coi như tâm cảnh đã đột phá đến Cổ Thánh cấp bậc, có thể... Đối diện thạch quan là không có sinh mệnh, Tử Uyên Cổ Thánh thi thể, cũng không có bất kỳ ý thức nào, hét lớn phía dưới, có thể tới, chỉ có một khả năng!"),t("br"),t("br"),n._v("“Là... Ngôn xuất pháp tùy!”"),t("br"),t("br"),n._v("Không ngừng run rẩy, Trương Huyền trên mặt viết đầy ngạc nhiên."),t("br"),t("br"),n._v("Ngôn xuất pháp tùy, nói là ra mà nói, có thể tự động hình thành pháp tắc, để cho người ta không tự chủ được tuân thủ, nhìn từ bề ngoài, cùng sư ngôn thiên bẩm có chút tương tự, nhưng... Trên thực tế kinh khủng nhiều."),t("br"),t("br"),n._v("Một lời thành quy tắc, vạn pháp tới đi theo... Thời gian nào pháp tắc, không gian pháp tắc, hết thảy không tính là gì."),t("br"),t("br"),n._v("Có thể đoán được, nếu như vận chuyển Trương gia huyết mạch, tới chiến đấu, trước mắt vị này, không cần động thủ, chỉ cần nói một câu “Định”, bản thân liền sẽ dừng ở tại chỗ, lại không cách nào động đậy."),t("br"),t("br"),n._v("Đây không phải khống chế thời gian, cũng không phải khống chế không gian, mà là khống chế ngươi cả người, để ngươi đứng tại chỗ, thành một loại mới quy tắc."),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("Không nghĩ tới Khổng sư đột phá Cổ Thánh, lĩnh ngộ lại là cái này... Khó trách vô địch thiên hạ, vạn cổ đệ nhất!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Bên này khiếp sợ, bay đến trước mặt thạch quan, lần nữa mở ra, Tử Uyên Cổ Thánh thi thể, bay ra, đứng lơ lửng giữa không trung."),t("br"),t("br"),n._v("Cứ việc hai mắt nhắm nghiền, nhưng trên người tản mát ra không cho người đến gần khí tức, tựa như phục sinh đồng dạng."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Làm xong những này, Khổng Thi Dao lần nữa một ngụm máu tươi phun ra, khuôn mặt trở nên càng thêm trắng bệch, thân thể mềm mại lắc lư vài cái, tựa hồ có chút không kiên trì nổi."),t("br"),t("br"),n._v("Khổng sư huyết mạch lực lượng, cứ việc có thể ngôn xuất pháp tùy, để cho người ta nghe theo mệnh lệnh, nhưng nàng tu vi vẫn là quá thấp, cưỡng ép vận dụng Tử Uyên Cổ Thánh thi thể, vẫn là thương tổn tới căn cơ."),t("br"),t("br"),n._v("“Ta tới!”"),t("br"),t("br"),n._v("Gặp Khổng Thi Dao đã động thủ, Nhan Thanh Cổ Thánh không nói thêm lời, gào lên một tiếng, thân thể bỗng nhiên sụp đổ."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một cái nháy mắt, thân thể bắn nhanh ra vô số huyết khí, thẳng tắp hướng Tử Uyên Cổ Thánh thi thể, vọt tới."),t("br"),t("br"),n._v("Kèm theo huyết dịch tràn vào càng ngày càng nhiều, Cổ Thánh thi thể trên người toả ra khí tức càng ngày càng mạnh."),t("br"),t("br"),n._v("“Cho rằng mượn một cái thi thể, liền có thể vượt qua ta sao? Đừng có nằm mộng...”"),t("br"),t("br"),n._v("Khe hở bò ra tới “Thần Linh” cười lạnh, bàn tay bỗng nhiên bổ tới."),t("br"),t("br"),n._v("Hắn lúc này, đã lộ ra ngực, xem ra, khoảng cách triệt để leo ra, đã không cần tiêu phí quá nhiều thời gian."),t("br"),t("br"),n._v("Bàn tay thẳng đối Tử Uyên Cổ Thánh, còn không có rơi xuống, không gian bốn phía liền sụp vào cùng một chỗ, như là từng cái lỗ đen."),t("br"),t("br"),n._v("“Liều mạng với ngươi!”"),t("br"),t("br"),n._v("Một tiếng la hét, còn lại mấy vị Cổ Thánh vọt thẳng tới, nghênh tiếp bàn tay."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Ép xuống lực lượng, thủy triều giống như vọt tới, mấy vị Cổ Thánh còn không có phản ứng lại, liền biến thành từng cỗ khung xương, thân thể bị bốc hơi sạch sẽ."),t("br"),t("br"),n._v("Bất quá, dù vậy, bọn họ cũng không có lùi bước chút nào, vẫn như cũ gắt gao bảo vệ trên không, không cho bàn tay rơi xuống."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1902"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1904"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);