(window.webpackJsonp=window.webpackJsonp||[]).push([[1021],{1375:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1893-muon-kiem"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1893-muon-kiem"}},[n._v("#")]),n._v(" Chương 1893: Mượn kiếm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Vốn cho rằng lần này hẳn phải chết không nghi ngờ, làm sao cũng không ngờ tới, vị này cùng bọn hắn cũng không tính hòa thuận Trương Huyền, bất ngờ xuất hiện, không chần chờ chút nào xông tới."),t("br"),t("br"),n._v("Nhìn trên không rực rỡ một thương, dốc hết cả đời lực lượng đều không thi triển ra được, Nhan Thanh Cổ Thánh giật mình, trên mặt vẻ xấu hổ: “Đúng vậy a, hắn là danh sư, vì nhân tộc, ân oán cá nhân không tính là gì, là ta lòng tiểu nhân!”"),t("br"),t("br"),n._v("Vị này, vì nhân tộc, tình nguyện mang tiếng xấu, không tiếc giả chết, lẻn vào Dị Linh tộc, đem bình định, chỉ bằng vào điểm ấy, chỉ biết đức hạnh cao, so với Khổng sư, đều không kém chút nào."),t("br"),t("br"),n._v("Cao thượng như vậy người, thấy được cục diện trước mắt, lại thế nào có thể thấy chết không cứu?"),t("br"),t("br"),n._v("Ngạn Thanh Cổ Thánh xấu hổ, Khổng Thi Dao đám người thì khiếp sợ nói không ra lời, các nàng không cách nào diễn tả bằng ngôn từ một thương này mạnh mẽ, hoàn mỹ phù hợp Thiên đạo, cho người ta một loại hỗn nhược thiên thành cảm giác, trong tích tắc, đau đớn trên người, suy yếu, tựa hồ cũng không coi vào đâu, dường như toàn bộ tinh thần, đều mất phương hướng tại đây một thương bên trong, không cách nào tự kềm chế."),t("br"),t("br"),n._v("Lơ lửng giữa không trung “Thần Linh” dường như cũng ngây người, không nghĩ tới, nơi này còn ẩn tàng một vị mạnh như thế người, so với hắn đều không hề yếu, do dự phía dưới, một thương đã đi tới cổ họng trước mặt."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Máu tươi biểu bay, trường thương đâm rách da của hắn, ghim đi vào, có điều, đi vào vẫn chưa tới một chỉ, cũng cảm giác giống như là đâm vào trên miếng sắt, lại không cách nào tiến lên mảy may."),t("br"),t("br"),n._v("“Không nghĩ tới linh khí như vậy mỏng manh địa phương, lại còn có thể sinh ra mạnh như thế người, không tệ, không tệ, ngươi, ta cũng muốn...”"),t("br"),t("br"),n._v("Hàn mang chợt lóe, “Thần Linh” cười lạnh, lập tức hướng cổ họng trước trường thương bắt tới."),t("br"),t("br"),n._v("Trương Huyền trường thương lắc một cái, đen nhánh thân thương, lập tức hóa thành một đầu màu đen cự long, trong nháy mắt đem “Thần Linh” toàn thân quấn quanh ở phía trong."),t("br"),t("br"),n._v("Không nghĩ tới chuôi này thương cường đại như thế, “Thần Linh” sửng sốt một chút, ngay sau đó tràn đầy nổi giận."),t("br"),t("br"),n._v("Đường đường Thượng Thương cường giả, lại bị hạ giới một tiểu nhân vật trói lại, quả thực chính là vô cùng nhục nhã: “Ngươi tự tìm cái chết!”"),t("br"),t("br"),n._v("Hai tay dùng sức, bắp thịt toàn thân nhô lên, đang định đem Hắc Long chấn vỡ, mắt tối sầm lại, một cái to lớn viên gạch từ không trung rớt xuống, hướng về phía khuôn mặt liền đập xuống."),t("br"),t("br"),n._v("đọc ngantruyen.com/"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Kim Nguyên đỉnh bị Trương Huyền luyện chế thành Đại Thánh chí bảo, tại Dị Linh tộc hơn một tháng, để hắn hấp thu Cổ Thánh lực lượng, đã đột phá đến Cổ Thánh cấp bậc, cứ việc khoảng cách phá toái hư không không biết kém bao nhiêu, nhưng nó mạnh mẽ thể trọng, phối hợp cứng rắn chất liệu, coi như “Thần Linh” siêu phàm thoát tục, bị chính giữa đầu, cũng cảm thấy hoa mắt ù tai, suýt chút nữa không có tại chỗ ngất đi."),t("br"),t("br"),n._v("Đương nhiên, trọng yếu nhất chính là, sỉ nhục!"),t("br"),t("br"),n._v("Thượng Thương siêu cấp cường giả, bị một cái thổ dân dùng viên gạch đập mặt... Mãnh liệt buồn bực, sắp nổ."),t("br"),t("br"),n._v("Đang muốn tức giận, liền nghe “Viên gạch” thanh âm vang lên: “Trước thời hạn tuyên bố, ta không phải viên gạch, ta là lô đỉnh, đập ở ngươi trên mặt là cái mông của ta!”"),t("br"),t("br"),n._v("Nói xong, còn cố ý dùng cái mông tại trên mặt hắn cọ xát vài cái."),t("br"),t("br"),n._v("Khổng Thi Dao đám người tất cả đều nhìn ngây người."),t("br"),t("br"),n._v("Vị siêu cấp cường giả này, tại sao có thể có như vậy bỉ ổi một kiện binh khí?"),t("br"),t("br"),n._v("Hơn nữa... Cái tên này nói nó là lô đỉnh, cọng lông lô đỉnh ah, rõ ràng chính là cái cỡ lớn viên gạch ah, có điểm nào giống đỉnh?"),t("br"),t("br"),n._v("Chẳng lẽ chúng ta hoa mắt?"),t("br"),t("br"),n._v("“Ah ah ah...”"),t("br"),t("br"),n._v("Cảm nhận được đối phương cái mông ở trên mặt không ngừng cọ lung tung, dường như có xỉ than rơi như con mắt, “Thần Linh” triệt để điên rồi: “Cút ngay...”"),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Khí tức cường đại khuấy động, trói ở trên người Long Cốt thần thương cũng lại không chịu nổi, bị xung kích bay ngược ra ngoài, lần nữa hóa thành một thanh trường thương, bị Trương Huyền bóp tại lòng bàn tay."),t("br"),t("br"),n._v("Đông!"),t("br"),t("br"),n._v("Một quyền đập ở Kim Nguyên đỉnh trên người."),t("br"),t("br"),n._v("“Cái mông của ta...” Rít lên một tiếng, Kim Nguyên đỉnh thẳng tắp hướng lên bầu trời bay đi, thân đỉnh không ngừng xoay tròn, dường như bị hỏa thiêu đồng dạng."),t("br"),t("br"),n._v("“Các ngươi đều phải chết...”"),t("br"),t("br"),n._v("Nghe cái tên này, hô to cái mông, “Thần Linh” da mặt lần nữa lay động, cũng lại kìm nén không được, bỗng nhiên hướng thanh niên trước mắt đánh ra."),t("br"),t("br"),n._v("Thân thể thoáng một cái, Trương Huyền trong cơ thể Trương gia huyết mạch kích hoạt, nhẹ nhàng một chút, bước ra khoảng cách mấy trăm mét, trường thương lắc một cái, lần nữa phá không mà tới."),t("br"),n._v("“Thời gian chân giải?”"),t("br"),t("br"),n._v("Gặp vị này thuấn di đồng dạng tránh thoát công kích, “Thần Linh” hừ lạnh, vừa định đem cái tên này dùng sức mạnh cầm cố lại, liền nghe đến trên không lần nữa “Đinh đinh đang đang” thanh âm vang lên, vội vàng ngẩng đầu, đồng tử kìm lòng không được co rút lại."),t("br"),t("br"),n._v("Chẳng biết lúc nào, đầy trời đều là vũ khí, trường kiếm, đại đao, chuỳ sắt, trường thương... Từng chuôi tính gộp lại, chừng mấy trăm kiện nhiều."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Ánh mắt hoa lên, đổ ập xuống rơi xuống."),t("br"),t("br"),n._v("“Ngươi cho rằng loại này rác rưởi vũ khí có thể thương tổn được ta?”"),t("br"),t("br"),n._v("“Thần Linh” cười lạnh."),t("br"),t("br"),n._v("Tuy chỉ liếc mắt nhìn, nhưng đã nhìn ra, cái này một đống lớn vũ khí, tất cả đều là Đại Thánh cấp bậc, một cái Cổ Thánh cấp bậc đều không có, loại cấp bậc này vũ khí, đừng nói tổn thương hắn, rơi vào trên người, liền làn da đều đâm không phá."),t("br"),t("br"),n._v("“Không có hi vọng có thể thương ngươi...”"),t("br"),t("br"),n._v("Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Những binh khí này, đều là hắn đánh giết Dị Linh tộc cường giả, cùng tại Khổng miếu ở bên trong lấy được, số lượng rất nhiều, nhưng đối với phổ thông Cổ Thánh tác dụng không lớn, chớ nói chi là phá toái hư không cường giả."),t("br"),t("br"),n._v("“Không có hi vọng?”"),t("br"),t("br"),n._v("“Thần Linh” sững sờ, đang muốn nói chuyện, lần nữa cảm thấy trên người lần nữa xiết chặt, vội vàng cúi đầu, liền thấy vừa rồi trường thương, chẳng biết lúc nào, lại biến thành Hắc Long, đem hắn trói chặt chẽ vững vàng, muốn chạy trốn đều không làm được."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, tức giận đến sắp bùng nổ."),t("br"),t("br"),n._v("Náo loạn nửa ngày, đầy trời vũ khí mưa công kích, chỉ là chướng nhãn pháp, chân chính chiêu số ở đây!"),t("br"),t("br"),n._v("Bất quá, trói lại bản thân lại như thế nào?"),t("br"),t("br"),n._v("Chỉ cần dùng hết toàn lực, rất nhanh liền có thể tránh thoát, mà đầu này Hắc Long, lại có thể kiên trì mấy lần?"),t("br"),t("br"),n._v("Nó mặc dù là cái vũ khí, trải qua nhiều tầng rèn luyện, có thể tu vi không bằng bản thân, cưỡng ép trói lại, mỗi một lần tránh thoát liền sẽ nhận một lần tổn thương, lấy ánh mắt của hắn đó có thể thấy được, cái tên này tuyệt đối chịu không được liên tục năm lần!"),t("br"),t("br"),n._v("Đang muốn dùng hết lực lượng đem chuôi này trường thương lần nữa bỏ qua, chỉ thấy thanh niên trước mắt, bỗng nhiên cúi đầu: “Khổng Thi Dao đúng không, trường kiếm cho ta mượn dùng một chút!”"),t("br"),t("br"),n._v("“Trường kiếm?”"),t("br"),t("br"),n._v("Khổng Thi Dao sững sờ, biết tình cảnh nguy cấp, cũng không có do dự, cổ tay rung lên."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lòng bàn tay kiếm lập tức hướng Trương Huyền bay đi."),t("br"),t("br"),n._v("“Ta chuôi kiếm này, là chúng ta Khổng gia truyền thừa bảo vật, không có Khổng gia huyết mạch, không cách nào luyện hóa, ngươi muốn sử dụng, ta có thể mượn một giọt máu tươi cho ngươi...”"),t("br"),t("br"),n._v("Gặp thanh niên nắm lên trường kiếm liền muốn sử dụng, Khổng Thi Dao cắn răng, nhanh chóng nói."),t("br"),t("br"),n._v("Thanh trường kiếm này, là trong tộc một vị tiền bối luyện chế mà thành, thân kiếm đã đạt đến tích huyết trùng sinh đỉnh phong cảnh giới, khoảng cách phá toái hư không chỉ có cách nhau một đường, huyết mạch không đủ, không cách nào khởi động, nàng cũng là luyện hóa tiếp cận ba năm, mới phá giải một phần lực lượng, không cách nào vận dụng toàn bộ."),t("br"),t("br"),n._v("Trước mắt vị này, thật muốn sử dụng, chỉ có thể mượn máu của mình."),t("br"),t("br"),n._v("“Không còn kịp rồi...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền cổ tay rung lên."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trường kiếm một tiếng sắc nhọn vang lên, sau một khắc, ngoan ngoãn nghe lời, như là nuôi nhốt gia khuyển."),t("br"),t("br"),n._v("“Luyện, luyện hóa?” Khổng Thi Dao miệng khó mà khép lại."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1904"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1906"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);