(window.webpackJsonp=window.webpackJsonp||[]).push([[1029],{1384:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1899-co-thanh-luc-luong-nguon-goc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1899-co-thanh-luc-luong-nguon-goc"}},[n._v("#")]),n._v(" Chương 1899: Cổ Thánh lực lượng nguồn gốc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đúng vậy a!” Nhan Thanh Cổ Thánh không có phủ nhận, mà là gật gật đầu: “Côn Hư cảnh, Khổng sư phát hiện thời điểm, chỉ là cái phổ thông thông đạo, cỗ lực lượng này cũng không mạnh, nhân loại hấp thu, hẳn phải chết không nghi ngờ, nhưng Dị Linh tộc nhân, có khả năng sống sót, hơn nữa trở nên càng thêm cường đại! Bởi vậy, mới sinh ra Ngoan Nhân như vậy nghịch thiên nhân vật!”"),t("br"),t("br"),n._v("“Vì phòng ngừa nhân loại bị triệt để diệt tộc, Tử Uyên Cổ Thánh tự nguyện dùng sinh mệnh phủ kín, có trước mắt cái này Huyền Quan Bổ Thiên đại trận!”"),t("br"),t("br"),n._v("Trương Huyền giật mình, lần nữa nhìn tới, trong giọng nói mang theo hỏi thăm: “Cái kia Thanh Nha mạch cùng phổ thông bách tính thể chất...”"),t("br"),t("br"),n._v("Biết hắn muốn hỏi thăm cái gì, Nhan Thanh Cổ Thánh nói: “Đi qua nghiên cứu, Khổng sư biết được, muốn luyện hóa loại khí tức này mà không tử vong, chỉ có một cái phương pháp, đó chính là Tiên Thiên thể chất mạnh mẽ, mà thay đổi nhân loại thể chất, là cái quanh năm suốt tháng quá trình, không có khả năng một lần liền thành. Tử Trì Cổ Thánh cùng con cháu đời sau, đi qua không biết bao nhiêu năm nghiên cứu, mới bồi dưỡng ra Thanh Nha mạch. Loại thực vật này, sau khi phục dụng, thể chất lại không ngừng tăng cường. Trải qua hơn vạn năm tẩm bổ, thân thể của nhân loại, quả thật có bay vọt về chất, chỉ bất quá khoảng cách Dị Linh tộc nhân, còn có khoảng cách nhất định! Vẫn như cũ không thể trực tiếp hấp thu!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước đó không hiểu sự tình, giờ phút này toàn bộ hiểu."),t("br"),t("br"),n._v("Khó trách Chư Tử bách gia, muốn đợi tại đây cái Côn Hư cảnh bên trong không ra, nguyên lai là dùng sinh mệnh tại trấn thủ phong ấn, để như thủy ngân lực lượng không đến mức tiết ra ngoài."),t("br"),t("br"),n._v("Đồng thời bồi dưỡng ra nhân loại càng mạnh mẽ hơn, thích ứng loại lực lượng này, làm cho nhân loại đi càng xa."),t("br"),t("br"),n._v("Cùng Danh Sư đường đồng dạng, cứ việc phương thức khác biệt, nhưng mục đích chỉ có một cái... Vì nhân tộc!"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh nói tiếp: “Đây chỉ là cái tiểu thế giới, tài nguyên có hạn, lại thêm nhân loại thể chất không ngừng tăng cường, đối với linh khí cùng bảo vật dùng lượng, cũng tăng cường không ít, cho nên... Cũng không phải là người người đều có tư cách tu luyện! Chỉ có thiên tài chân chính, mới có thể càng đi càng xa.”"),t("br"),t("br"),n._v("Côn Hư cảnh vì nuôi sống tốt Thanh Nha mạch, không thể cùng danh sư đại lục câu thông, bởi vậy, cho dù tiểu thế giới này rất lớn, thời gian lâu dài, kèm theo tu luyện giả càng ngày càng nhiều, lực lượng vẫn như cũ sẽ khô héo."),t("br"),t("br"),n._v("Liền cùng phong kín đại bằng đồng dạng, dù là đường kính mấy cây số, loại nhiều hơn nữa cây cối, cũng không thể nào làm được, tự cấp tự túc, không cần ngoại giới lực lượng."),t("br"),t("br"),n._v("Kiếp trước liền có nhà khoa học đặc biệt làm qua thí nghiệm, bố trí một cái to lớn nhà ấm, gieo trồng đủ loại cây cối, nuôi dưỡng không biết bao nhiêu động vật, kết quả không đến một năm, vòng sinh thái liền triệt để sụp đổ."),t("br"),t("br"),n._v("Côn Hư cảnh cũng là như vậy, cứ việc khu vực rất lớn, đường kính vượt qua trăm vạn cây số, dù sao chỉ là cái tiểu thế giới, người bình thường trưởng thành liền nắm giữ Tông Sư cảnh thực lực, đối toàn bộ thế giới, đều có cực lớn tiêu hao, cho nên, tu luyện giả số lượng, nhất định phải kìm lại."),t("br"),t("br"),n._v("Nếu không, nhân loại còn không có thích ứng như thủy ngân linh khí, khả năng thế giới này liền làm trước đã sụp đổ."),t("br"),t("br"),n._v("“Thật ra thì, không chỉ Côn Hư cảnh như vậy, danh sư đại lục cũng giống như vậy, kèm theo cường giả càng ngày càng nhiều, sớm muộn đều sẽ không chịu nổi...” Nhan Thanh Cổ Thánh nói tiếp."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Bất kỳ địa phương nào, hoàn cảnh, đều có một cái không thể thừa nhận điểm giới hạn."),t("br"),t("br"),n._v("Nhân loại hấp thu hoàn cảnh bên trong linh khí, tiến hành tu luyện, liền giống như trên thân trâu con rận, một hai đầu, hàng trăm hàng ngàn đầu, đều không có cái gì, có thể... Mấy vạn, mấy trăm triệu đâu?"),t("br"),t("br"),n._v("Cường đại tới đâu trâu, cũng sẽ bởi vậy tử vong."),t("br"),t("br"),n._v("Côn Hư cảnh ngay tại đối mặt loại vấn đề này, cho nên, nghiêm ngặt khống chế công pháp và tài nguyên, chỉ có chân chính tuyệt đỉnh thiên tài, mới có tư cách tiếp tục tu luyện."),t("br"),t("br"),n._v(" Không như vậy hạn chế lời nói, đừng nói nhân tộc càng ngày càng mạnh mẽ, khả năng không đợi đến, sinh thái liền làm trước sụp đổ, thế giới cũng xong rồi."),t("br"),t("br"),n._v("“Nói như vậy... Danh sư đại lục không cách nào sinh ra Cổ Thánh, có thể hay không cũng là nguyên nhân này?”"),t("br"),t("br"),n._v("Hiểu được, Trương Huyền đột nhiên mở lời."),t("br"),t("br"),n._v("Bất luận danh sư đại lục vẫn là trước mắt cái này Côn Hư cảnh, một vạn năm trước, đều không thể sinh ra Cổ Thánh, có thể hay không cũng là bởi vì cái này?"),t("br"),n._v("“Có nguyên nhân này, cũng không có liên quan quá nhiều!”"),t("br"),t("br"),n._v("Lắc đầu, gặp Trương Huyền tràn đầy nghi ngờ, Nhan Thanh Cổ Thánh giải thích nói: “Muốn đột phá Cổ Thánh, nhất định phải có Cổ Thánh lực lượng! Mà Cổ Thánh lực lượng, bất luận danh sư đại lục vẫn là Côn Hư cảnh, đều khó có khả năng tự chủ đản sinh... Đây là Thượng Thương mới có đặc thù lực lượng! Như thủy ngân linh khí bị phong cấm, loại lực lượng này không cách nào đi vào đại lục, kèm theo càng hấp thu càng ít, cuối cùng một tia đều không có, cũng liền lại không cách nào sinh ra Cổ Thánh!”"),t("br"),t("br"),n._v("“Cổ Thánh lực lượng, vậy mà ẩn giấu ở như thủy ngân linh khí bên trong?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Tại đài cao thời điểm, hắn hấp thu qua một tia, vừa tiến vào trong cơ thể, lập tức nặng nề vô cùng, kinh mạch suýt chút nữa vỡ vụn, ngay sau đó liền phong bế huyệt đạo, không dám hấp thu mảy may."),t("br"),t("br"),n._v("Nơi này có Cổ Thánh lực lượng?"),t("br"),t("br"),n._v("Ngón tay một chút, nhịn không được lần nữa hấp thu một tia."),t("br"),t("br"),n._v("Cỗ lực lượng này, vẫn nặng nề như cũ, dò xét phút chốc, quả nhiên ở trong đó cảm nhận được một tia nhỏ yếu Cổ Thánh lực lượng."),t("br"),t("br"),n._v("Cứ việc rất ít, nhưng hấp thu nhiều, góp gió thành bão, đồng dạng có thể để người ta đột phá cuối cùng ràng buộc, trở thành đại lục đỉnh phong nhất cường giả."),t("br"),t("br"),n._v("Chỉ là..."),t("br"),t("br"),n._v("Cỗ lực lượng này thực sự quá mạnh, hắn đều không hấp thu được, huống chi những người khác."),t("br"),t("br"),n._v("“Không phong ấn Thượng Thương mà đến lực lượng, nhân loại sẽ diệt vong, Dị Linh tộc nhân sẽ trở nên càng thêm mạnh mẽ... Phong ấn, nhiều nhất không xuất hiện Cổ Thánh, lựa chọn ra sao, không cần nhiều lời!”"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh cười khổ."),t("br"),t("br"),n._v("Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("Nhân tộc là muốn càng nhiều Cổ Thánh, nhưng không giải quyết như thủy ngân lực lượng, tương đương với tạo phúc Dị Linh tộc nhân, một khi từ bỏ phong ấn, không chỉ “Thần Linh” sẽ tới, nhân loại cũng sẽ bởi vậy diệt tuyệt."),t("br"),t("br"),n._v("“Dị Linh tộc nhân thật chẳng lẽ cùng trong truyền thuyết đồng dạng, là Thần tộc hậu duệ?”"),t("br"),t("br"),n._v("Trong lòng kỳ lạ, Trương Huyền hỏi lần nữa."),t("br"),t("br"),n._v("Dị Linh tộc nhân có thể hấp thu cỗ lực lượng này, mà bọn họ nhưng không làm được, thật chẳng lẽ chỉ là thể chất vấn đề?"),t("br"),t("br"),n._v("“Cụ thể ta cũng không rõ ràng, năm đó Khổng sư từng nói qua, Dị Linh tộc nhân dường như là bị trục xuất Thần tộc! Bởi vì danh sư đại lục linh khí quá yếu, mới biến thành bộ dáng này, chỉ cần cấp cho hấp thu càng tinh thuần linh lực, sẽ lập tức khôi phục như lúc ban đầu, trở nên càng thêm cường đại!”"),t("br"),t("br"),n._v("Nói đến đây, Nhan Thanh Cổ Thánh nhìn về phía trước mắt hai vị thanh niên, tràn đầy cười khổ: “Bọn họ chính là như vậy! Bắt tới thời điểm, chỉ là phổ thông Dị Linh tộc, không có cái gì đặc thù, nhưng mà hấp thu Thượng Thương thủy ngân linh khí, không đến mười năm liền thuế biến đến loại trạng thái này, thực lực trở nên cường đại như thế! Thậm chí vượt qua nhân loại khổ tu người, hơn nữa dung mạo cũng biến thành cùng người bình thường giống như đúc...”"),t("br"),t("br"),n._v("Nhân loại, nhọc nhằn khổ sở tu luyện mấy chục năm, mấy trăm năm, lại thêm trác tuyệt thiên phú, cũng mới có loại thực lực này, mà đối phương, chỉ hấp thu thủy ngân linh khí không chết, ổn định tu luyện, liền cùng dạng có thể đi đến..."),t("br"),t("br"),n._v("Người so với người, tức chết người."),t("br"),t("br"),n._v("“Đã phong ấn đằng sau, có thể có ‘Thần Linh’ đi qua, lại sẽ tiết lộ Thượng Thương, đặc thù thủy ngân linh khí, chẳng lẽ... Phong ấn sau lưng chính là Thượng Thương? Chỉ cần chui qua, liền có thể đi vào một thế giới khác?”"),t("br"),t("br"),n._v("Trong lòng hơi động, đè nén kích động trong lòng, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1911"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1913"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);