(window.webpackJsonp=window.webpackJsonp||[]).push([[1030],{1385:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1900-hi-sinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1900-hi-sinh"}},[n._v("#")]),n._v(" Chương 1900: Hi sinh")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Tiêu phí vô số đại giới đi vào Côn Hư cảnh mục đích, chính là vì tìm kiếm đi vào Thượng Thương thông đạo, từ đó tìm Lạc Nhược Hi tung tích."),i("br"),i("br"),n._v("Trên đài cao thông đạo, “Thần Linh” có thể đi qua, lại có Thượng Thương đặc thù linh khí, có thể hay không chính là tiến vào bên trong cầu thang?"),i("br"),i("br"),n._v("“Ta không rõ ràng!”"),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh lắc đầu."),i("br"),i("br"),n._v("“Vậy... Khổng sư, là như thế nào rời đi Côn Hư cảnh, rời đi danh sư đại lục?”"),i("br"),i("br"),n._v("“Cái này...” Ngạn Thanh Cổ Thánh hơi đỏ mặt."),i("br"),i("br"),n._v("Khổng sư năm đó rời đi, liền hắn rất nhiều thân truyền cũng không quá rõ ràng, huống chi hắn vị này không biết bao nhiêu thế hệ hậu nhân."),i("br"),i("br"),n._v("“Tiên tổ là trước đạt đến phá toái hư không cảnh giới, sau đó rời đi nơi này, cụ thể đi ở đâu, gia tộc sách không có ghi chép, nhưng ta nghiên cứu qua hắn cuộc đời, có lẽ cùng cái này phong ấn liên quan đến!”"),i("br"),i("br"),n._v("Một bên Khổng Thi Dao đột nhiên mở lời."),i("br"),i("br"),n._v("Trương Huyền thở phào nhẹ nhõm."),i("br"),i("br"),n._v("Nhìn tới, muốn đi vào Thượng Thương, tám chín phần mười muốn đi vào trên đài cao thông đạo, nhưng... Muốn làm được điểm ấy, đầu tiên muốn có phá toái hư không thực lực!"),i("br"),i("br"),n._v("Hắn, rõ ràng còn chưa đủ!"),i("br"),i("br"),n._v("“Trương sư, Côn Hư cảnh là Khổng sư tự thân lưu lại phong ấn, không cách nào ra vào, lần trước sở dĩ có thể rời đi nơi đây đi vào danh sư đại lục, là bởi vì Khổng miếu xuất hiện, chúng ta mượn trong đó lực lượng, ngươi... Là như thế nào đi vào?”"),i("br"),i("br"),n._v("Trò chuyện một hồi, Nhan Thanh Cổ Thánh tràn đầy nghi ngờ nhìn qua."),i("br"),i("br"),n._v("Mọi người cũng đều có chút kỳ quái."),i("br"),i("br"),n._v("Bọn họ thân là Côn Hư cảnh sinh mệnh, muốn rời khỏi, đều không làm được, lần trước là mượn Khổng miếu lực lượng, mới lấy thành công, mà trước mắt vị này, đến cùng thế nào đi vào?"),i("br"),i("br"),n._v("Không có gì có thể giấu diếm, Trương Huyền nói: “Ta... Tại Thiên Huyền vương quốc phát hiện một cái do Nhiễm Cầu Cổ Thánh thi thể trấn thủ phong ấn, sau đó... Phá vỡ liền đến nơi này!”"),i("br"),i("br"),n._v("“Phá vỡ? Ngươi phá vỡ đạo phong ấn kia?”"),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh đám người khuôn mặt biến đổi."),i("br"),i("br"),n._v("“Làm sao vậy?”"),i("br"),i("br"),n._v("“Đi qua nhìn một chút!”"),i("br"),i("br"),n._v("Không để ý tới giải thích, Nhan Thanh Cổ Thánh trên không trung một chém, nhảy vào, mọi người theo sát phía sau."),i("br"),i("br"),n._v("Thời gian qua một lát, đến Trương Huyền đám người đi vào địa phương."),i("br"),i("br"),n._v("Phong ấn vẫn như cũ đứng sừng sững ở trước mặt, trước đó mọi người chui vào khe hở, giờ phút này không ngừng lay động, từng đoàn từng đoàn hắc hỏa không ngừng thiêu đốt, dường như có một cỗ lực lượng khổng lồ không ngừng nghiền ép, bất cứ lúc nào cũng sẽ không chịu nổi, triệt để phá vỡ."),i("br"),i("br"),n._v("Trương Huyền sửng sốt: “Đây là có chuyện gì?”"),i("br"),i("br"),n._v("Phong ấn mạnh mẽ hắn biết rất rõ ràng, mượn siêu cường lôi binh kiếp, mới phá vỡ một vết nứt, làm sao đột nhiên, xuất hiện cường đại như vậy hắc hỏa?"),i("br"),i("br"),n._v("Hơn nữa cùng Thiên Hỏa có chút tương tự?"),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh vẻ mặt nghiêm túc: “Đây là Thiên đạo phản phệ!”"),i("br"),i("br"),n._v("“Thiên đạo?”"),i("br"),i("br"),n._v("“Côn Hư cảnh, tuy là thoát ly danh sư đại lục, trên thực tế còn thuộc về trong đó một bộ phận, bồi dưỡng Thanh Nha mạch, làm cho nhân loại vừa thành niên liền nắm giữ cảnh giới tông sư thực lực, phá hủy sinh mệnh pháp tắc... Nhiễm Cầu Cổ Thánh thi thể ở lại bên ngoài, nói là phong ấn, càng nhiều hơn chính là vì ngăn cản Thiên đạo dò xét... Bây giờ bị ngươi phá hoại, Thiên đạo biết, tự nhiên muốn điên cuồng công kích, sợ rằng sẽ đem... Toàn bộ Côn Hư cảnh, đều triệt để hủy diệt!”"),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh ánh mắt lộ ra lo âu nồng đậm."),i("br"),i("br"),n._v("Thân thể cứng đờ, Trương Huyền hiểu được."),i("br"),i("br"),n._v("Thiên đạo có quy tắc của mình, ai dám phá hoại, liền muốn nhận trừng phạt."),i("br"),n._v(" Liền giống như tu luyện giả xung kích cảnh giới cao hơn, xung kích Cổ Thánh, muốn được Thiên đạo trừng trị."),i("br"),i("br"),n._v("Không nói thế giới này, liền nói kiếp trước, Vương Mãng làm người xuyên việt, muốn thay đổi thế giới, kết quả, Thiên đạo liền phái ra vị diện chi tử Lưu Tú, dễ như trở bàn tay đem hắn hủy diệt."),i("br"),i("br"),n._v("Quy tắc không thể phá hỏng, Cổ Thánh cũng không được."),i("br"),i("br"),n._v("Vốn cho rằng, danh sư đại lục người người tìm không thấy Côn Hư cảnh, là Chư Tử bách gia siêu thoát, hiện tại xem ra, là vì tránh né Thiên đạo dò xét."),i("br"),i("br"),n._v("Một khi cho bọn hắn biết vị trí, hàng năm đều có cường giả tới, Thiên đạo cũng rất dễ dàng phát giác, cuối cùng, đừng nói Thanh Nha mạch, thế giới này có thể hay không giữ vững được, cũng chưa biết chừng."),i("br"),i("br"),n._v("“Làm sao bây giờ?”"),i("br"),i("br"),n._v("Biết là bản thân dẫn tới tai hoạ, Trương Huyền nhìn qua: “Có hay không trận đồ, ta lần nữa tu bổ trận pháp, ngăn cản đối phương dò xét!”"),i("br"),i("br"),n._v("“Trận đồ là có, nhưng đã không còn tác dụng!”"),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh lắc đầu: “Không chỉ phong ấn phá giải, phía ngoài huyễn trận, cũng phá vỡ, trước đó Thiên đạo dò xét, trong quan tài, sẽ hiện ra Khổng sư thân ảnh, để hắn cho rằng Khổng sư đã chết, lại không tiếp tục đuổi kiểm tra. Hiện tại bình chướng vỡ ra, trận pháp mất đi tác dụng, tương đương với đối phương đã biết tất cả... Bổ sung lại, còn có cái gì tác dụng?”"),i("br"),i("br"),n._v("Trương Huyền lặng im."),i("br"),i("br"),n._v("Tới thời điểm, bên ngoài loại trừ bia đá bên ngoài, có hai cái thạch quan, cái thứ nhất là Nhiễm Cầu Cổ Thánh thi thể, dùng để duy trì huyễn trận."),i("br"),i("br"),n._v("Chiếc thứ hai, Trịnh Dương nhìn thấy thời điểm, là Khổng sư thi thể, mà bọn họ nhìn thấy thời điểm, là Trịnh Dương... Tự nghĩ biện pháp phá giải, mới phát hiện bình chướng."),i("br"),i("br"),n._v("Hiện tại hai thứ này đều đã phá vỡ, danh sư đại lục Thiên đạo đều “Thăm dò” đến nơi này, phong ấn tu không chữa trị, tự nhiên không còn bất cứ ý nghĩa gì."),i("br"),i("br"),n._v("“Chẳng lẽ chỉ có thể nhìn phong ấn bị xé nứt, sau đó Côn Hư cảnh hủy hoại chỉ trong chốc lát?”"),i("br"),i("br"),n._v("“Quả thực không có biện pháp tốt, trừ phi... Cùng thời kỳ Thượng Cổ đồng dạng, lừa gạt Thiên đạo!”"),i("br"),i("br"),n._v("“Lừa gạt?”"),i("br"),i("br"),n._v("“Ừm, năm đó Khổng sư mở ra thế giới này thời điểm, lực lượng tiết lộ, đưa tới Thiên đạo ham muốn, Nhiễm Cầu Cổ Thánh tự nguyện hiến thân, duy trì huyễn trận, cuối cùng che giấu Thiên đạo!”"),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh vẻ mặt nghiêm túc: “Bây giờ muốn giải quyết tai hoạ ngầm, cũng chỉ có cái này một cái phương pháp... Chính là tìm tới một vị phá toái hư không cường giả, cam nguyện bị Thiên đạo chém giết! Tạo ra toàn bộ thế giới bị diệt giả tượng... Thiên đạo, cứ việc sẽ trừng phạt không tuân thủ quy tắc, lại không phải có tự chủ linh trí, chỉ cần thủ đoạn cao minh, thiết kế hợp lý, là có cơ hội thành công!”"),i("br"),i("br"),n._v("Trương Huyền lông mày nhăn lại."),i("br"),i("br"),n._v("Toàn bộ danh sư đại lục thêm vào Côn Hư cảnh, giờ phút này không có một vị phá toái hư không cường giả, hắn phối hợp Long Cốt thần thương, chỉ là sức chiến đấu tiếp cận, thực lực chân chính, nhưng có rất lớn khoảng cách, Thiên đạo hẳn là rất dễ dàng nhìn ra!"),i("br"),i("br"),n._v("“Ta tới đi, dù sao không còn sống lâu nữa! Coi như không thể triệt để lừa qua đối phương, có thể kiên trì một ngày là một ngày!”"),i("br"),i("br"),n._v("Ngẩng đầu nhìn về phía phía trước, Nhan Thanh Cổ Thánh trong ánh mắt mang theo kiên định."),i("br"),i("br"),n._v("“Ngươi?”"),i("br"),i("br"),n._v("“Ừm, ta tuy là không có đi đến phá toái hư không, nhưng cũng cực kỳ tiếp cận, linh hồn cùng thân thể tự bạo, hẳn là có thể tạm thời tản mát ra phá toái hư không lực lượng, có lẽ có thể, tạm thời giấu diếm được đối phương!”"),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh gật đầu."),i("br"),i("br"),n._v("“Tự bạo?” Khổng Thi Dao tràn đầy do dự: “Một khi linh hồn thân thể tự vệ, sẽ vạn kiếp bất phục, lại không phục sinh khả năng...”"),i("br"),i("br"),n._v("Đi đến tích huyết trùng sinh cảnh giới, bị chém giết thành thịt vụn, cũng có thể phục sinh, nhưng linh hồn tự bạo, thật sự tương đương hoàn toàn chết đi, hồn phi phách tán."),i("br"),i("br"),n._v("Dù là Cổ Thánh lực lượng lại đầy đủ, cũng không có cách nào quay về."),i("br"),i("br"),n._v("“Ta biết!” Nhan Thanh Cổ Thánh gật gật đầu: “Chỉ cần có thể bảo vệ Côn Hư cảnh... Triệt để tử vong lại có cái gì!”"),i("br"),i("br"),n._v("Thân là nhân loại Cổ Thánh, đã sớm làm xong hi sinh chuẩn bị, nếu như mình chết, có thể đổi lấy càng nhiều người sinh, giá trị tuyệt đối."),i("br"),i("br"),n._v("“Trước không vội nghĩ đến hi sinh, ta xem trước một chút, có hay không những biện pháp khác!”"),i("br"),i("br"),n._v("Thấy đối phương tùy thời đều muốn xông tới, Trương Huyền nhíu nhíu mày, đi tới ngọn lửa màu đen trước mặt, ngón tay điểm một cái duỗi tới."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1912"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1914"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);