(window.webpackJsonp=window.webpackJsonp||[]).push([[1032],{1386:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1902-than-con"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1902-than-con"}},[n._v("#")]),n._v(" Chương 1902: Thần côn")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Khó xử."),i("br"),i("br"),n._v("Trương Huyền vò đầu."),i("br"),i("br"),n._v("Ở đây, Nhan Thanh Cổ Thánh đám người, đều là sống trên vạn năm nhân vật, để cho bọn họ bái sư, rõ ràng không có khả năng, cũng không tiện mở lời, Nhan Tiết cùng hắn có mâu thuẫn, duy nhất điều kiện phù hợp Khổng Thi Dao, vậy mà không có chút gì do dự, liền trực tiếp cự tuyệt."),i("br"),i("br"),n._v("Thật ra thì cũng không trách đối phương, thân là Khổng sư huyết mạch, tu tập Khổng sư truyền thừa, bản thân liền đứng ở cự nhân trên bờ vai, Nhan Thanh Cổ Thánh cũng không có tư cách làm nàng lão sư, huống chi người khác!"),i("br"),i("br"),n._v("Dù là trước mắt vị này, cũng là một cái trời nhận danh sư."),i("br"),i("br"),n._v("Thiên phú không bằng đối phương, điểm ấy ngạo khí, vẫn phải có."),i("br"),i("br"),n._v("“Việc quan hệ có thể hay không giải quyết Côn Hư cảnh phiền phức...” Trương Huyền mặt dạn mày dày giải thích: “Hơn nữa cũng không phải thu làm thân truyền, trong lòng thừa nhận ta là lão sư là được!”"),i("br"),i("br"),n._v("Thiên đạo chi sách, thí nghiệm qua nhiều lần, chỉ cần trong lòng thừa nhận bản thân là lão sư, chân tâm thật ý cảm kích, liền có thể hình thành, cũng không phải là chỉ định thân truyền."),i("br"),i("br"),n._v("“Bái ngươi làm thầy, có thể giải quyết cục diện trước mắt?” Khổng Thi Dao cau mày."),i("br"),i("br"),n._v("Nếu không phải đối phương vừa mới chém giết Thần Linh, giải quyết phiền toái lớn, thật sự cho rằng là lường gạt."),i("br"),i("br"),n._v("“Ừm, như vậy đi, ta trước cho ngươi chỉ điểm, bái sư hay không, bản thân quyết định!”"),i("br"),i("br"),n._v("Nhìn nhìn trên đầu mây đen, hội tụ tốc độ càng lúc càng nhanh, biết thiên kiếp, lần nữa sẽ hạ xuống, Trương Huyền nói."),i("br"),i("br"),n._v("Thời gian không cho phép cẩn thận giải thích, lại nói, Thiên đạo chi sách, dính dáng thư viện, cũng không thể giải thích, cùng hắn như vậy còn không bằng, trực tiếp dùng kiến thức, để hắn thuyết phục."),i("br"),i("br"),n._v("Nhìn về phía cô bé trước mắt, cũng không đợi đối phương mở lời, tiếp tục mở miệng: “Trong cơ thể ngươi nắm giữ Khổng sư tinh thuần nhất huyết mạch, bất kỳ cái gì võ kỹ, công pháp, đều là vừa học liền biết, một hồi liền tinh, tốc độ tu luyện vượt xa người thường, hơn nữa nếu như không nhìn lầm, ngươi tuy là đi đến Bất Hủ cảnh đại viên mãn không cao hơn ba năm, nhưng đã đưa tới ba lần thiên kiếp, hơn nữa đều không có xung kích Cổ Thánh, cưỡng ép đem tu vi áp chế lại!”"),i("br"),i("br"),n._v("Khổng Thi Dao gật đầu."),i("br"),i("br"),n._v("Những việc này, không phải bí mật, chỉ cần là Chư Tử bách gia cường giả, hầu như đều biết, đối phương có thể nhìn ra, chẳng có gì lạ."),i("br"),i("br"),n._v("“Ba lần xung kích thiên kiếp, tu vi cứ việc một lần so một lần mạnh, đối về sau xung kích phá toái hư không, tránh thoát Thiên đạo có sự giúp đỡ to lớn, nhưng... Ngươi có phải hay không cũng phát hiện, kèm theo xung kích số lần biến nhiều, cũng xuất hiện rất lớn tai hoạ ngầm!”"),i("br"),i("br"),n._v("Trương Huyền tiếp tục nói: “Đó chính là... Thiên kiếp lực lượng, đã hòa vào ngươi cốt tủy, trong cơ thể ngươi, đến bây giờ cũng còn còn sót lại lấy không cách nào quét sạch lực lượng, như có gai ở sau lưng! Không nhìn lầm... Ngươi lần thứ ba xung kích thiên kiếp, hẳn là tại hai năm trước, nói cách khác, trong một năm đánh sâu vào ba lần lôi đình, lần thứ tư, nhưng lặng im trọn hai năm, đều không đi làm, nhìn tình huống hiện tại, hẳn không phải là không có đi, mà là không dám đi đi...”"),i("br"),i("br"),n._v("Khổng Thi Dao sắc mặt trắng nhợt, không tự chủ được lui về sau một bước, trong mắt tràn đầy khó tin."),i("br"),i("br"),n._v("Chuyện này, là trong nội tâm nàng bí mật lớn nhất, một mực không dám nói ra, nằm mơ đều không có nghĩ đến, bị trước mắt vị này một cái vạch trần."),i("br"),i("br"),n._v("Ba năm trước đây đi đến Bất Hủ cảnh đại viên mãn, nàng muốn đi đến tiên tổ huy hoàng, xung kích bốn lần thiên kiếp, lại đột phá Cổ Thánh... Ba lần trước tuy là nguy hiểm, nhưng cuối cùng đều hữu kinh vô hiểm, có thể thành công, có thể... Ba lần sau đó, đột nhiên phát hiện, thiên kiếp lực lượng đã hòa vào cốt tủy, bất kể như thế nào đều khu trừ không xong!"),i("br"),i("br"),n._v("Nếu như lại xung kích mà nói, coi như hạo kiếp không cường đại, cũng sẽ đồng hóa lực lượng trong cơ thể, để nàng triệt để biến thành thiên kiếp một bộ phận, đừng nói xung kích Cổ Thánh, làm không cẩn thận sẽ còn mất đi bản thân, triệt để tử vong."),i("br"),i("br"),n._v("Chính là bởi vì biết loại tình huống này, một năm xung kích ba lần lôi kiếp nàng, triệt để ngừng lại, không dám tiếp tục, trọn hai năm đều không có tìm tới biện pháp tốt hơn, thậm chí kèm theo thời gian trôi qua, lực lượng bị đồng hóa càng ngày càng nghiêm trọng."),i("br"),i("br"),n._v("Vì giải quyết cái phiền toái này, tra duyệt không biết bao nhiêu thư tịch, đều không có kết quả, đối phương một cái nói ra, chẳng lẽ... Có phương pháp giải quyết?"),i("br"),n._v(" “Ta đã có thể nhìn ra, tự nhiên biết phương pháp giải quyết cùng mấu chốt!” Trương Huyền cười cười, nói: “Ngươi hẳn là đó có thể thấy được, ta cũng đánh sâu vào nhiều lần thiên kiếp, nhưng trong cơ thể, nhưng không có loại bệnh trạng này!”"),i("br"),i("br"),n._v("Khổng Thi Dao giật mình, con mắt kìm lòng không được sáng lên."),i("br"),i("br"),n._v("Đối phương giống như nàng, đều là Bất Hủ cảnh đại viên mãn thực lực, lại có thể cùng phá toái hư không cường giả chiến đấu, đều không hề rơi xuống hạ phong một chút nào."),i("br"),i("br"),n._v("Loại trừ trời nhận danh sư cái thân phận này bên ngoài, khẳng định cũng cũng giống như mình, đánh sâu vào nhiều lần Cổ Thánh kiếp, mà áp chế tu vi không làm đột phá."),i("br"),i("br"),n._v("“Không biết... Ta loại tình huống này, như thế nào giải quyết? Nếu như chính xác, bái ngươi làm thầy, cũng không sao...” Hàm răng cắn chặt, Khổng Thi Dao nói."),i("br"),i("br"),n._v("Nếu là có thể giải quyết tai hoạ ngầm, có thể xung kích bốn lần lôi kiếp, chẳng khác nào có thể đuổi theo tiên tổ, cuối cùng đột phá phá toái hư không!"),i("br"),i("br"),n._v("Có loại thực lực này, phong ấn lại xuất hiện vấn đề, cũng không cần lo lắng."),i("br"),i("br"),n._v("Thậm chí, còn có thể truy đuổi tiên tổ bước chân, đi vào cao hơn thế giới, càng đi càng xa."),i("br"),i("br"),n._v("“Thật ra thì rất đơn giản, ngươi trải qua Cổ Thánh kiếp, cũng không phải là danh sư đại lục Thiên đạo thi triển ra, mà là Côn Hư cảnh tự có lực lượng, thế giới này vì Khổng sư mượn thông đạo sáng tạo, ẩn chứa Khổng sư lực lượng ở bên trong... Đối với những khác người mà nói, cùng ngoại giới thiên kiếp không có gì khác biệt, nhưng đối với ngươi mà nói, bản chất bản nguyên, rất dễ dàng dung hợp, lại không cách nào khu trừ!”"),i("br"),i("br"),n._v("Trương Huyền nói."),i("br"),i("br"),n._v("Côn Hư cảnh, hai đại phong ấn ngăn cách danh sư đại lục Thiên đạo thăm dò, xung kích Cổ Thánh dẫn tới Cổ Thánh kiếp, tự nhiên cũng không phải Thiên đạo thả ra."),i("br"),i("br"),n._v("Sở dĩ thoạt nhìn đồng dạng, tất cả đều là Khổng sư lưu lại."),i("br"),i("br"),n._v("Đồng dạng lấy nhà ấm đại bằng làm thí dụ, cái thiên kiếp này, tương đương với đại bằng nội bộ mưa nhân tạo hoặc là nhân tạo can thiệp thiên tượng, đối với cây cối tới nói, cùng ngoại giới không khác, nhưng đối mưa xuống người tới nói, cũng có chút khó xử."),i("br"),i("br"),n._v("Trải qua thiên kiếp càng nhiều, đồng nguyên lực lượng liền càng mạnh, hấp thu cũng càng nhiều, tự nhiên mà vậy, lại khó xung kích cao hơn."),i("br"),i("br"),n._v("“Ta nên làm cái gì?”"),i("br"),i("br"),n._v("Khổng Thi Dao vội vàng nhìn tới."),i("br"),i("br"),n._v("Đối phương không nói, còn không rõ ràng lắm, giờ phút này nói rõ, nhất thời ý thức được nguyên nhân, trong lòng tràn đầy khủng hoảng."),i("br"),i("br"),n._v("“Muốn giải quyết triệt để kỳ thật cũng không khó, đó chính là... Tại danh sư đại lục, lần nữa xung kích Cổ Thánh kiếp! Đương nhiên, ngươi bây giờ tu vi, đã thâm hậu như thế, nếu như lần nữa xung kích mà nói, làm không cẩn thận liền sẽ tử vong! Đây là một đạo kiếm hai lưỡi, không xung kích, lực lượng càng trầm tích càng nhiều, cuối cùng triệt để mất đi xung kích cơ hội, xung kích mà nói, danh sư đại lục lôi kiếp, chưa hề tiếp xúc qua, rất dễ dàng bị tại chỗ xoá bỏ!”"),i("br"),i("br"),n._v("Trương Huyền cười nhìn qua: “Bất quá, nếu như ngươi bái ta làm thầy, ta có thể truyền thụ cho ngươi, đối phó danh sư đại lục Cổ Thánh kiếp phương pháp!”"),i("br"),i("br"),n._v("“Cái này... Tốt, ta bái ngươi làm thầy, nhưng hi vọng ngươi không được giấu dốt!” Khổng Thi Dao không tại nói nhảm, trực tiếp quỳ gối, liên tục dập đầu mấy cái vang tiếng."),i("br"),i("br"),n._v("Liếc mắt nhìn, phát hiện xuất hiện Thiên đạo chi sách, biết đối phương cũng không phải là chân chính cảm kích, Trương Huyền than thở một tiếng, nói: “Thật ra thì, xung kích danh sư đại lục Cổ Thánh kiếp, cũng không phức tạp, trọng yếu nhất chính là, muốn đem đối phương xem như sủng vật, bảo vệ nó, che chở nó, khắp nơi vì nó suy nghĩ, để tâm cảm thụ, dùng yêu sưởi ấm...”"),i("br"),i("br"),n._v("“...” Khổng Thi Dao khuôn mặt vặn vẹo, hận không thể đem kẻ trước mắt này một quyền đánh bại."),i("br"),i("br"),n._v("Làm sao... Giống như thần côn đâu?"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1914"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1916"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);