(window.webpackJsonp=window.webpackJsonp||[]).push([[1034],{1390:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1904-khong-thi-dao-xung-kich-co-thanh-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1904-khong-thi-dao-xung-kich-co-thanh-ha"}},[n._v("#")]),n._v(" Chương 1904: Khổng Thi Dao xung kích Cổ Thánh (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ngươi không được, ngươi dạng này đi lên mà nói, sẽ bị tại chỗ đánh chết!”"),t("br"),t("br"),n._v("Không có trực tiếp trả lời, mà là nhìn về phía Nhan Tiết, Trương Huyền hai tay chắp sau lưng, lần nữa nhìn về phía trên không, trong ánh mắt mang theo lãnh đạm."),t("br"),t("br"),n._v("“Vì cái gì ta không được?” Nhan Tiết cau mày."),t("br"),t("br"),n._v("“Thật ra thì, không riêng gì ngươi, ngay cả ta, đều không làm được như vậy!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Khổng Thi Dao, trong cơ thể ẩn chứa Khổng sư huyết mạch, lại tại Côn Hư cảnh ra đời lớn lên, trải qua hạo kiếp... Trong cơ thể sớm đã hiện đầy tiểu thế giới này lạc ấn!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Vừa rồi vị này đã nói, nữ hài những việc trải qua ba lần Cổ Thánh kiếp, trong cơ thể sớm đã ẩn chứa năng lượng, không cách nào khu trừ."),t("br"),t("br"),n._v("Một khi xung kích lần thứ tư, rất có thể sẽ bị lôi đình đồng hóa."),t("br"),t("br"),n._v("“Mà tiểu thế giới này là... Danh sư đại lục Thiên đạo, chỗ không cho phép!” Trương Huyền nói tiếp: “Dựa theo bình thường độ kiếp phương pháp, nàng dẫn tới lôi đình về sau, liều mạng chống cự, làm như vậy, tương đương với chọc giận hạo kiếp, không những không thể diệt đi, sẽ còn dẫn tới bắn ngược, để hắn càng ngày càng cường đại!”"),t("br"),t("br"),n._v("“Khổng Thi Dao xung kích Cổ Thánh, bản thân liền là khiêu chiến Thiên đạo uy nghiêm, đem nàng so sánh tội phạm mà nói, trong cơ thể ẩn chứa tiểu thế giới lực lượng, đồng dạng là tội phạm, hơn nữa tội danh càng nặng!”"),t("br"),t("br"),n._v("“Lúc này, mạnh mẽ chống đỡ hạo kiếp, tương đương với lấy tội phạm thân phận, bao che một cái khác tội danh càng nặng gia hỏa, tự nhiên sẽ nhận chấp pháp giả càng mạnh hơn phản kích...”"),t("br"),t("br"),n._v("“Mà ta để nàng hấp thu lôi đình, tương đương với mở rộng đại môn, để chấp pháp giả đi vào, phối hợp đối phương chấp pháp... Như vậy, tội danh liền sẽ nhỏ rất nhiều! Nhận phản phệ cũng sẽ trở nên rất nhỏ, càng quan trọng hơn là... Còn có thể nhân cơ hội khu trừ trong cơ thể tiểu thế giới lực lượng, nhất cử lưỡng tiện!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người hàm răng run lên."),t("br"),t("br"),n._v("Từ danh sư đại lục Thiên đạo muốn hủy diệt Côn Hư cảnh, đó có thể thấy được cả hai quả thực thủy hỏa bất dung."),t("br"),t("br"),n._v("Có thể liếc mắt liền nhìn ra điểm ấy, hơn nữa mượn Cổ Thánh kiếp, làm hai bên lưỡng bại câu thương..."),t("br"),t("br"),n._v("Làm tội phạm, liên thông chấp pháp giả, một khi làm không cẩn thận, khả năng ngọc đá cùng vỡ, vạn kiếp bất phục!"),t("br"),t("br"),n._v("“Vậy thì giống lấy độc trị độc, ngộ nhỡ... Thu hút hạo kiếp lực lượng nhiều, trong cơ thể nàng không có nhiều như vậy tiểu thế giới lực lượng, có thể hay không đồng dạng bị xoá bỏ?”"),t("br"),t("br"),n._v("Tuy là hiểu được, Nhan Thanh Cổ Thánh vẫn như cũ tràn đầy lo lắng."),t("br"),t("br"),n._v("Nói đến đơn giản, làm lại hết sức phức tạp."),t("br"),t("br"),n._v("Nữ hài trong cơ thể tiểu thế giới lực lượng, là nhất định, hấp thu ít, không cách nào triệt để quét sạch, hấp thu nhiều, cũng có thể dẫn đến bản thân cũng bị giết chết..."),t("br"),t("br"),n._v("“Cho nên, ta để nàng để tâm cảm thụ!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Thân là Khổng sư hậu nhân, Côn Hư cảnh đệ nhất thiên tài, nếu như ngay cả điểm ấy đều cảm thụ không ra, hấp thu bao nhiêu đều không nắm được... Cho dù chết, cũng không có cách nào! Dù sao, loại năng lực này, coi như lần này có thể sống sót, lần sau độ kiếp, đồng dạng không thể thừa nhận!”"),t("br"),t("br"),n._v("Sự tình vừa rồi, không thể nói thẳng, một khi nói, trong lòng có suy tính, thân thể rất dễ dàng làm ra bản năng phản ứng, như thế ngược lại sẽ hoàn toàn ngược lại."),t("br"),t("br"),n._v("Đồng dạng lấy tội phạm làm thí dụ, đem chấp pháp giả mang tới, khẳng định cũng là điềm nhiên như không có việc gì làm, một khi hữu ý, bị một cái khác nhìn thấy, chỉ sợ cái trước còn chưa tới đến, bản thân liền sẽ trước bị làm chết."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người giờ mới hiểu được tới, lần nữa nhìn về phía thanh niên, tràn đầy bội phục."),t("br"),t("br"),n._v("Tâm lý, thiên kiếp thuộc tính, trạng thái tinh thần... Trong nháy mắt đều bị hắn tính toán ở bên trong, không có chút nào khác biệt, loại này nhãn lực cùng kiến thức..."),t("br"),t("br"),n._v("Không hổ là trời nhận danh sư, quả nhiên có liền Cổ Thánh đều không thể với tới năng lực."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Phía dưới mọi người nghe xong giải thích, khiếp sợ tột đỉnh, trên không Khổng Thi Dao, tự mình cảm thụ, đối vị này tuổi tác thoạt nhìn so với nàng còn nhỏ trời nhận danh sư, xem như chân chính triệt để bội phục."),t("br"),t("br"),n._v("Giờ phút này, không cần nghe giải thích, cũng rõ ràng đối phương khổ tâm."),t("br"),t("br"),n._v("Danh sư đại lục Cổ Thánh kiếp, lôi đình phối hợp Thiên Hỏa, tại trong cơ thể nàng không ngừng xung kích, đem trước tích tụ tại chỗ sâu tiểu thế giới lực lượng, từng chút một tiêu hao, ma diệt!"),t("br"),t("br"),n._v("Hai hai đối đầu, không những không có việc gì, ngược lại cẩn thận cảm ngộ lực lượng, vô luận tu vi hay là kiến thức, đều có tăng lên cực lớn."),t("br"),t("br"),n._v("Không biết qua bao lâu, trong cơ thể tích tụ Côn Hư cảnh thiên kiếp lực lượng, triệt để bị tiêu hao sạch sẽ, nữ hài biết, tiếp tục hấp thu, khẳng định sẽ bị nổ chết, lúc này toàn thân huyệt đạo phong bế, máu trong cơ thể thiêu đốt!"),t("br"),t("br"),n._v("“Lui!”"),t("br"),t("br"),n._v("Một tiếng quát tháo."),t("br"),t("br"),n._v("Một lời thành quy tắc, vạn pháp tới đi theo!"),t("br"),t("br"),n._v("Bầu trời Cổ Thánh kiếp, lực lượng bản thân liền tiêu hao không sai biệt lắm, giờ phút này cũng lại không chịu nổi thiên phú của nàng lực lượng, chỉ thoáng cái, đầy trời mây đen liền tán loạn hơn phân nửa, ngay sau đó chậm rãi tiêu tán."),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mây đen rời đi, nữ hài cũng lại kìm lại không được lực lượng trong cơ thể, đột phá Bất Hủ cảnh đại viên mãn ràng buộc, đạt đến Cổ Thánh cấp bậc."),t("br"),t("br"),n._v("Hơn nữa, đột phá Cổ Thánh về sau, bởi vì tích lũy quá mức hùng hậu, cũng không ngừng, một đường hướng về phía trước."),t("br"),t("br"),n._v("Huyết mạch kéo dài sơ kỳ!"),t("br"),t("br"),n._v("Huyết mạch kéo dài trung kỳ!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thời gian nháy mắt liền đạt đến Cổ Thánh tầng thứ nhất huyết mạch kéo dài đỉnh phong, tiếp tục xung kích."),t("br"),t("br"),n._v("Chư Tử bách gia sơ kỳ, trung kỳ... Đỉnh phong!"),t("br"),t("br"),n._v("Không đến một phút, vậy mà đạt đến Cổ Thánh tầng thứ hai Chư Tử bách gia đỉnh phong, lực lượng mới bắt đầu dần dần suy giảm."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh hàm răng run lên."),t("br"),t("br"),n._v("Bọn họ đột phá Cổ Thánh, bình thường đều chỉ là đi đến huyết mạch kéo dài sơ kỳ, có thể cực kỳ xung kích trung kỳ, chính là thiên tài bên trong thiên tài, Khổng Thi Dao vậy mà một hơi vượt qua hai cái đại cấp bậc, đi đến tầng thứ hai đỉnh phong..."),t("br"),t("br"),n._v("Côn Hư cảnh vài vạn năm trong lịch sử, đều cơ hồ không có xuất hiện qua!"),t("br"),t("br"),n._v("“Có thể làm được điểm ấy, đều bởi vì vị này...”"),t("br"),t("br"),n._v("Lần nữa nhìn về phía Trương Huyền, đã không thể dùng bội phục để hình dung."),t("br"),t("br"),n._v("“Tiếp tục xung kích, ngươi tích lũy, hoàn toàn có thể trực tiếp trùng kích tích huyết trùng sinh!”"),t("br"),t("br"),n._v("Không thèm để ý vẻ mặt của mọi người, Trương Huyền lông mày giương lên, nhìn về phía trên không."),t("br"),t("br"),n._v("“Xung kích tích huyết trùng sinh?”"),t("br"),t("br"),n._v("Cũng sửng sốt một chút, Khổng Thi Dao cuối cùng lắc đầu: “Trong cơ thể ta lực lượng đã đi đến cực hạn...”"),t("br"),t("br"),n._v("Cứ việc nàng đã trải qua bốn lần Cổ Thánh kiếp, đem tu vi áp chế đến cực hạn, có thể một hơi đi đến Cổ Thánh tầng thứ hai đỉnh phong, đã hao phí toàn bộ tích góp, lại không có cách nào tinh tiến mảy may."),t("br"),t("br"),n._v("“Cực hạn, không có gì cực hạn!”"),t("br"),t("br"),n._v("Trương Huyền cong ngón tay búng một cái, một giọt máu xuất hiện tại đầu ngón tay, thẳng tắp hướng đối phương bắn tới: “Nuốt vào!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thời khắc này Khổng Thi Dao, đối với hắn trăm phần trăm tin tưởng và nghe theo, không chần chờ há miệng liền đem giọt máu này nuốt xuống."),t("br"),t("br"),n._v("Huyết dịch vừa tiến vào cổ họng, lập tức cảm thấy tràn đầy hừng hực, phảng phất sắp thiêu đốt lên."),t("br"),t("br"),n._v("“Đây là... Vị kia ‘Thần Linh’ huyết dịch?”"),t("br"),t("br"),n._v("Đôi mắt thanh tú trợn tròn."),t("br"),t("br"),n._v("Tích huyết trùng sinh cường giả tinh huyết, nàng những năm này tu luyện, hấp thu qua không ít, bằng không thì cũng không có khả năng tích lũy đến thực lực như thế, nhưng đối phương cấp cho huyết dịch càng thêm cường đại, năng lượng càng đầy, so tích huyết trùng sinh mạnh mẽ không chỉ gấp mười lần!"),t("br"),t("br"),n._v("Chỉ có một khả năng... Phá toái hư không cường giả huyết dịch!"),t("br"),t("br"),n._v("Trân quý như thế đồ vật, vậy mà trực tiếp cho nàng một cái..."),t("br"),t("br"),n._v("Hơi đỏ mặt, biết giờ phút này không phải suy nghĩ nhiều thời điểm, Khổng Thi Dao trong cơ thể lực lượng bỗng nhiên vận chuyển, rất nhanh hấp thu."),t("br"),t("br"),n._v("Sau nửa canh giờ, ràng buộc lần nữa đánh vỡ, thuận lợi đột phá đến tích huyết trùng sinh cảnh giới, tại trung kỳ trái phải ngừng lại."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Cảm nhận được trên người lực lượng, thẳng tắp bay đến trước mặt, đầu gối mềm nhũn quỳ rạp xuống đất."),t("br"),t("br"),n._v("Lần này, chân chính vui lòng phục tùng!"),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trương Huyền trong đầu chấn động, một bản kim sắc thư tịch, xuất hiện tại thư viện."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1916"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1918"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);