(window.webpackJsonp=window.webpackJsonp||[]).push([[1035],{1389:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1905-tieu-su-muoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1905-tieu-su-muoi"}},[n._v("#")]),n._v(" Chương 1905: Tiểu sư muội")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thu Khổng Thi Dao làm đồ đệ mục đích, chính là tạo ra Thiên đạo chi sách, trước đó nàng tuy là bái sư, nhưng không có chân chính cảm kích, giờ phút này, tâm tình kích động, cái này thần khí nghịch thiên, quả nhiên thuận lợi hình thành."),t("br"),t("br"),n._v("“Đứng lên đi!”"),t("br"),t("br"),n._v("Đem nữ hài đỡ dậy, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Có thứ này, có lẽ liền có thể giải quyết, Côn Hư cảnh gặp phải vấn đề."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("“Vị này là tiểu sư muội ư?”"),t("br"),t("br"),n._v("Triệu Nhã đám người vây quanh, từng cái tràn đầy kích động."),t("br"),t("br"),n._v("Xung kích Cổ Thánh to lớn như vậy lôi đình, toàn bộ Thiên Huyền vương quốc đều chấn động, thân là Cổ Thánh cường giả, Triệu Nhã đám người, lại phát hiện không được, thật sự choáng váng."),t("br"),t("br"),n._v("“Tiểu sư muội?”"),t("br"),t("br"),n._v("Khổng Thi Dao đầy mặt rầu rĩ."),t("br"),t("br"),n._v("Nàng là Côn Hư cảnh đệ nhất thiên tài, Khổng sư hậu nhân... Kêu một đám so với chính mình tuổi tác đều tiểu nhân thiếu nam thiếu nữ, sư ca sư tỷ... Thực sự có chút không cách nào mở lời."),t("br"),t("br"),n._v("Đang không biết như thế nào cho phải, liền nghe đến đối diện Trương sư nói: “Vẫn còn không tính là tiểu sư muội, nàng cũng không phải là ta thân truyền! Chỉ là tùy tiện thu học sinh!”"),t("br"),t("br"),n._v("“Tùy tiện thu...” Khổng Thi Dao thân thể mềm mại run rẩy."),t("br"),t("br"),n._v("Mới vừa rồi còn cảm thấy, làm tiểu sư muội có chút khó xử, kết quả... Náo loạn nửa ngày, liền làm tiểu sư muội tư cách đều không có..."),t("br"),t("br"),n._v("Khí hàm răng cắn chặt, đang muốn nói chuyện, chỉ thấy Trương sư nhìn lại: “Thế nào, cảm thấy ta tại khuất nhục ngươi?”"),t("br"),t("br"),n._v("“Học sinh không dám!” Khổng Thi Dao cắn răng."),t("br"),t("br"),n._v("Bất kể nói thế nào, nàng đều là Côn Hư cảnh Phượng Hoàng, liền tiểu sư muội đều tư cách làm, quả thực là khinh người quá đáng."),t("br"),t("br"),n._v("“Không có gì không dám, ta biết ngươi ý nghĩ, như vậy đi, vị này Triệu Nhã, là ta học sinh thân truyền, bàn về tuổi tác, hẳn là nhỏ hơn ngươi không ít!”"),t("br"),t("br"),n._v("Trương Huyền một chỉ Triệu Nhã: “Các ngươi cũng không cần vũ khí, chỉ bằng vào thực lực chiến đấu, nếu như có thể thắng được... Thu ngươi làm thân truyền, thậm chí để ngươi làm đại sư tỷ, đều không có cái gì!”"),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Liếc mắt nhìn cách đó không xa nữ hài, Khổng Thi Dao đôi mi thanh tú vừa nhấc, trong mắt lấp lánh ra nồng đậm chiến ý."),t("br"),t("br"),n._v("Đối phương chỉ có mười bảy, tám tuổi bộ dạng, nhỏ hơn nàng không ít, nếu như vậy đều thắng không nổi, còn có cái gì tư cách, làm Khổng sư hậu nhân?"),t("br"),t("br"),n._v("“Cố gắng chiến đấu!”"),t("br"),t("br"),n._v("Thấy nàng đáp ứng, Trương Huyền cũng không nói nhảm, nhìn Triệu Nhã liếc mắt."),t("br"),t("br"),n._v("“Vâng!” Gật gật đầu, nữ hài thân thể mềm mại run rẩy, đi tới mênh mông chỗ: “Mời!”"),t("br"),t("br"),n._v("Khổng Thi Dao theo sát tới."),t("br"),t("br"),n._v("Hai nữ chiến đấu, không có quá nhiều khuyếch đại, trực tiếp bắt đầu."),t("br"),t("br"),n._v("Khổng Thi Dao không vận dụng huyết mạch lực lượng, nhưng thân là siêu cấp thiên tài, lại thêm đột phá đến tích huyết trùng sinh cảnh giới, đủ loại võ kỹ, tiện tay thi triển, cả người tựa như hồ điệp, không ngừng tung bay, ánh sáng chói mắt."),t("br"),t("br"),n._v("Triệu Nhã thể chất triệt để mở ra, đầy trời đều bị hàn khí bao phủ, bông tuyết bay lả tả."),t("br"),t("br"),n._v("Tu vi của nàng, đánh lén mới Dung Hoàng Lưu Dương thời điểm, vừa mới đột phá Cổ Thánh nhị trọng Chư Tử bách gia sơ kỳ."),t("br"),t("br"),n._v("Một tháng qua, lão sư tự thân chỉ điểm, lại thêm, tích huyết trùng sinh Cổ Thánh chi huyết, không giữ lại chút nào cung cấp, đã đạt đến cảnh giới này đỉnh phong."),t("br"),t("br"),n._v("Cứ việc cùng Khổng Thi Dao chênh lệch cực lớn, vậy do mượn đối với chiến đấu lĩnh ngộ cùng kinh nghiệm, trong thời gian ngắn, vậy mà mảy may đều không rơi vào thế hạ phong."),t("br"),t("br"),n._v("Chiến đấu một hồi, Khổng Thi Dao ánh mắt lộ ra gấp gáp."),t("br"),t("br"),n._v("Nàng tu vi so với đối phương cao hai cái tiểu cấp bậc, càng là Khổng sư về sau, công bằng chiến đấu, đồng dạng không có thi triển binh khí dưới tình huống, vậy mà không chiếm được trên một điểm phong..."),t("br"),t("br"),n._v("Trương Huyền vị này chỉ có mười bảy, tám tuổi thân truyền, làm sao lại cường đại như vậy?"),t("br"),t("br"),n._v("“Không được, tiếp tục nữa, coi như sẽ chiến thắng, cũng tương đương thua...”"),t("br"),t("br"),n._v("Tùy ý tiếp tục như vậy, coi như may mắn thắng được, khẳng định cũng cùng thua không có gì khác nhau, dù sao tu vi của nàng cao hơn."),t("br"),n._v(" Cắn răng, Khổng Thi Dao thân pháp bỗng nhiên biến đổi, bàn tay trong chốc lát giống như là nhiều hơn mấy cái."),t("br"),t("br"),n._v("Nàng mạnh nhất võ kỹ, Khổng sư tự thân sáng chế để lại cho hậu nhân... Bát Tí Phù Đồ!"),t("br"),t("br"),n._v("Lộc cộc lộc cộc đạp!"),t("br"),t("br"),t("br"),n._v("Bị võ kỹ công kích, Triệu Nhã không thể kiên trì được nữa, liên tục lui về phía sau."),t("br"),t("br"),n._v("Trước mắt vị này cứ việc vừa đột phá, nhưng dù sao cũng là đã trải qua bốn lần Cổ Thánh kiếp siêu tuyệt nhân vật, lại thêm tu vi bản thân liền mạnh, nếu không phải mình kinh nghiệm chiến đấu phong phú, khẳng định đã sớm thất bại."),t("br"),t("br"),n._v("“Không nên gấp gáp, ngươi bây giờ chỉ là lực lượng không bằng... Đã như vậy, liền thuận tiện đột phá một chút đi!”"),t("br"),t("br"),n._v("Cong ngón tay búng một cái, Trương Huyền đồng dạng đem một giọt phá toái hư không cường giả huyết dịch, đối Triệu Nhã bắn tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đem huyết dịch nuốt vào cổ họng, Triệu Nhã lực lượng trong cơ thể, lập tức nhanh chóng tăng lên."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Không đến hai mươi cái hô hấp, quả nhiên phá tan Chư Tử bách gia ràng buộc, đạt đến tích huyết trùng sinh cảnh giới."),t("br"),t("br"),n._v("Tu vi đột phá, lực lượng đại tăng, cuồn cuộn mà xuống, Khổng Thi Dao không thể kiên trì được nữa, bị đánh trúng ngực, bay ngược ra ngoài, trùng trùng điệp điệp ngã xuống đất."),t("br"),t("br"),n._v("“Ta thua...”"),t("br"),t("br"),n._v("Sắc mặt tái nhợt, Khổng Thi Dao răng ngà cắn chặt."),t("br"),t("br"),n._v("Cứ việc nàng còn có rất nhiều tuyệt chiêu không có thi triển đi ra, chân chính vật lộn sống mái, mượn huyết mạch chi lực, đối phương khẳng định chống lại không được... Nhưng lúc này, thực sự xấu hổ lấy ra."),t("br"),t("br"),n._v("Đối phương võ kỹ, cứ việc không có nàng tu luyện cao minh, nhưng mỗi một chiêu một thức, đều nhắm thẳng vào đại đạo, cho người ta một loại đại đạo chí giản cảm giác, mấu chốt nhất là, dường như có thể nhìn ra nàng chiến đấu bên trong thiếu hụt, mỗi một chiêu đều đánh vào nàng không thể không phòng, hơn nữa lại không cách nào phòng vệ địa phương."),t("br"),t("br"),n._v("Trước đó, còn có thể dựa dẫm tu vi cao, tạm thời áp chế, thực lực đối phương cũng sau khi tăng lên, liền lại không có cái gì biện pháp."),t("br"),t("br"),n._v("Có thể nói... Vừa bắt đầu tỷ thí cũng đã nhất định thất bại, buồn cười nàng không dám thừa nhận thôi!"),t("br"),t("br"),n._v("“Thế nào? Hiện tại còn nguyện ý làm tiểu sư muội?”"),t("br"),t("br"),n._v("Thấy vị này Côn Hư cảnh kiêu ngạo nhất thiên tài, từ nội tâm chỗ sâu thừa nhận thất bại, Trương Huyền cười nhạt một tiếng: “Đương nhiên, nếu như ngươi muốn cho rằng, Triệu Nhã là trong đám người mạnh nhất, còn có thể khiêu chiến những người khác, chỉ bất quá, muốn đè thấp tu vi tới chiến đấu! Không phải, cũng không đủ công bằng!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Khổng Thi Dao hướng còn lại mấy người nhìn lại."),t("br"),t("br"),n._v("Trịnh Dương, Viên Đào, Ngụy Như Yên đám người từng cái ánh mắt lộ ra hừng hực, tựa hồ cũng muốn xông lại tới tỷ thí, thuận tiện bắt nạt một cái, để nàng biết trời cao đất rộng."),t("br"),t("br"),n._v("“Không được, ta nguyện ý làm tiểu sư muội...”"),t("br"),t("br"),n._v("Khổng Thi Dao quỳ gối."),t("br"),t("br"),n._v("Đã những người này đều là một cái lão sư giáo dục đi ra, phương thức chiến đấu bên trên, khẳng định cũng tương đồng, vừa rồi nàng ỷ vào tu vi cao, có thể áp chế Triệu Nhã, cùng cấp bậc đối mặt mấy cái này mà nói, khẳng định không chiếm được mảy may thượng phong!"),t("br"),t("br"),n._v("Làm không cẩn thận, đồng dạng thất bại."),t("br"),t("br"),n._v("Cùng hắn như thế mất mặt, còn không bằng trực tiếp thừa nhận."),t("br"),t("br"),n._v("“Vậy thì được!”"),t("br"),t("br"),n._v("Thấy nàng thừa nhận, Trương Huyền thở phào nhẹ nhõm, mỉm cười: “Triệu Nhã, Trịnh Dương, còn có các ngươi mấy cái, từ hôm nay trở đi, Khổng Thi Dao chính là các ngươi tiểu sư muội, lão sư vị thứ chín thân truyền! Về sau sư huynh đệ muốn cùng nhau trông coi, không cần thiết hoạ từ trong nhà!”"),t("br"),t("br"),n._v("“Vâng! Xin nghe lão sư dạy bảo!”"),t("br"),t("br"),n._v("Mọi người đồng loạt ôm quyền."),t("br"),t("br"),n._v("Thấy Khổng sư hậu nhân đều bái Trương Huyền vi sư, Nhan Thanh Cổ Thánh nói không ra lời, qua nửa ngày, mở miệng nói: “Khổng Thi Dao đột phá Cổ Thánh, càng là đạt đến tích huyết trùng sinh cảnh giới, có tư cách quản lý Côn Hư cảnh cùng Chư Tử bách gia...”"),t("br"),t("br"),n._v("Côn Hư cảnh vốn là Khổng sư lưu lại, tự nhiên muốn về hắn hậu nhân quản lý."),t("br"),t("br"),n._v("Những năm này, hắn sử dụng quyền lực, cũng chỉ là thay mặt khống chế thôi!"),t("br"),t("br"),n._v("Hiện tại Khổng Thi Dao triệt để trưởng thành, cũng nên đem quyền lợi trả lại."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1917"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1919"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);