(window.webpackJsonp=window.webpackJsonp||[]).push([[1036],{1391:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1906-lao-su-đi-đau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1906-lao-su-đi-đau"}},[n._v("#")]),n._v(" Chương 1906: Lão sư đi đâu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chư Tử bách gia từ trước tới nay lấy Khổng sư như Thiên Lôi sai đâu đánh đó, Khổng Thi Dao đột phá Cổ Thánh, nói trắng ra là, cũng không có gì có thể giao tiếp, tự nhiên mà vậy liền thành bọn họ cao nhất địa vị thủ lĩnh."),t("br"),t("br"),n._v("Như vậy trải qua, Trương Huyền cửu đại đệ tử, triệt để đem nhân tộc, Dị Linh tộc, Chư Tử bách gia, đều quản lý ở bên trong."),t("br"),t("br"),n._v("“Lão sư, Côn Hư cảnh cửa vào phong ấn sự tình, xử lý như thế nào?”"),t("br"),t("br"),n._v("Biết trên bờ vai trách nhiệm, Khổng Thi Dao cũng không từ chối, nhìn về phía lão sư."),t("br"),t("br"),n._v("Lão sư nói qua, chỉ cần bái hắn làm thầy, liền có biện pháp giải quyết."),t("br"),t("br"),n._v("“Ta thử một chút!”"),t("br"),t("br"),n._v("Trương Huyền lần nữa trở lại phong ấn trước mặt, tinh thần khẽ động, Thiên đạo chi sách từ mi tâm bay ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Cùng bình chướng khe hở dung hợp lại cùng nhau, thời gian nháy mắt, tạo thành một cái cao lớn xa xăm cửa."),t("br"),t("br"),n._v("Thứ này vừa xuất hiện, trên bầu trời sôi trào lôi đình, lập tức giống như là thấy được “Thần Linh” kim thân đồng dạng, nhanh chóng tiêu tán, hình như chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("“Vậy thì giải quyết?”"),t("br"),t("br"),n._v("Mọi người tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Còn muốn lấy, đi đâu tìm phá toái hư không cường giả, không nghĩ tới đơn giản như vậy, một quyển sách liền hoàn thành!"),t("br"),t("br"),n._v("Sớm biết dễ dàng như vậy, làm gì không sớm chút lấy ra?"),t("br"),t("br"),n._v("“Suy đoán là đúng...”"),t("br"),t("br"),n._v("Không để ý tới ý nghĩ của mọi người, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thiên đạo thư viện, trước đó không cách nào nhìn thấu Cổ Thánh cấp bậc thiếu hụt, nói cách khác, không cách nào nhìn thấu, vượt qua danh sư đại lục Thiên đạo đồ vật."),t("br"),t("br"),n._v("Về sau, Lạc Nhược Hi rời đi, đem Xuân Thu đại điển dung hợp ở bên trong, loại tình huống này thuận lợi giải quyết."),t("br"),t("br"),n._v("Lúc đó liền nghi ngờ, có phải hay không trong thư viện Thiên đạo, đã vượt qua danh sư đại lục, đi qua vừa rồi thí nghiệm, quả thật như vậy."),t("br"),t("br"),n._v("Cùng “Thần Linh” vượt qua Thiên đạo, sẽ không hạ xuống lôi kiếp đồng dạng."),t("br"),t("br"),n._v("Thư viện vượt qua danh sư đại lục Thiên đạo, chế tạo ra Thiên đạo chi sách, mới có thể phủ kín Côn Hư cảnh, mà để người sau không dám xét xử."),t("br"),t("br"),n._v("Thiên đạo chi sách làm cửa, trấn áp danh sư đại lục Thiên đạo không dám tới, Côn Hư cảnh che giấu tại không che giấu cũng không tính cái gì, Trương Huyền lần nữa tiến vào bên trong, dặn dò Khổng Thi Dao: “Ta nghĩ đi Khổng gia nhìn một chút!”"),t("br"),t("br"),n._v("Không có cự tuyệt, Khổng Thi Dao đi đầu dẫn đường."),t("br"),t("br"),n._v("Khổng gia ngay tại Đại Nho đường lân cận, tiến vào bên trong, Trương Huyền chậm rãi mà đi."),t("br"),t("br"),n._v("Khổng gia tuân theo Khổng sư truyền thừa, đem hắn tư tưởng cùng công pháp, phát triển đến cực hạn, văn hóa không khí vô cùng nồng đậm, thong thả trong đó, từng bước một tiến lên, Trương Huyền giống như là đi theo Khổng sư bước chân, cảm ngộ tinh thần của hắn cùng tư tưởng."),t("br"),t("br"),n._v("Xem xong Khổng gia, Trương Huyền rời đi Côn Hư cảnh, lần này không mang rất nhiều đệ tử, mà là một mình tiến lên."),t("br"),t("br"),n._v("Khổng Tử đi khắp các nước, từ đó có cảm giác ngộ, mới luyện chế ra Xuân Thu đại điển như vậy nghịch thiên chi vật, hắn cũng muốn tận mắt chứng kiến một cái, danh sư đại lục phồn hoa, tìm đến thứ thuộc về chính mình."),t("br"),t("br"),n._v("Mỗi một tòa thành thị, hắn đều sẽ lưu lại một đoạn thời gian."),t("br"),t("br"),n._v("Dị Linh tộc nhân giải quyết, đại lục thái bình, Danh Sư đường lúc này cũng dựa theo Dương sư ý nghĩ thay đổi, lại không phân công thắt, chỉ vì truyền thụ thiên hạ."),t("br"),t("br"),n._v("Trương Huyền rất nhiều đệ tử, tại hắn gợi ý bên dưới, truyền thụ tứ phương, Hồng Viễn danh sư học viện rất nhiều học sinh, cũng chia vải các nơi, tiến hành dạy học, tất cả nghe qua Trương Huyền chương trình học, đều càng thêm nổi bật, thành “Trương học” tuyên truyền người, trong lúc nhất thời, vị này thiên hạ đệ nhất danh sư danh khí, càng lúc càng lớn, thậm chí vượt qua Khổng sư."),t("br"),t("br"),n._v("Người người đều trông đợi hắn có thể lần nữa giảng bài, nhưng vị này để cho người ta ngưỡng mộ Trương sư, nhưng giống như là từ biến mất khỏi thế gian đồng dạng, triệt để mất đi tung tích."),t("br"),t("br"),n._v("Không chỉ Danh Sư đường, không cách nào dò xét, ngay cả hắn rất nhiều thân truyền, cũng không biết ân sư, đến cùng đi nơi nào, truyền tin tức không trở về, phảng phất triệt để biến mất khỏi thế giới này."),t("br"),t("br"),n._v("“Lão sư đi nơi nào?”"),t("br"),t("br"),n._v("Trịnh Dương từ danh sư đại lục cực nam chi đỉnh bay trở về, một mặt phong trần mệt mỏi, cũng không phát hiện bất kỳ tung tích nào."),t("br"),t("br"),n._v("Triệu Nhã từ đại lục tận cùng phía Bắc trở về, cũng một mặt buồn bã, không thu hoạch được gì."),t("br"),t("br"),n._v("Cửu đại đệ tử, phân tán thiên hạ, tìm trọn hơn bốn tháng, hầu như tất cả địa phương đều tìm khắp cả, thủy chung không có phát hiện."),t("br"),n._v(" “Lão sư không liên hệ chúng ta, hẳn là muốn sáng chế con đường của mình, tốt hơn đi lại, chúng ta cũng không cần quá phận lo lắng!”"),t("br"),t("br"),n._v("Vương Dĩnh an ủi."),t("br"),t("br"),n._v("“Vương Dĩnh nói không sai, chúng ta việc cấp bách, cũng không phải là tìm kiếm, mà là xin nghe lão sư dạy bảo, đem hắn kiến thức, truyền thụ đến thế giới các ngõ ngách! Hơn nữa không được rơi xuống tu vi, tranh thủ sớm ngày đột phá đến tích huyết trùng sinh đỉnh phong, vì hắn chia sẻ ưu sầu!”"),t("br"),t("br"),n._v("Lộ Xung nói."),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu, không nói thêm lời."),t("br"),t("br"),n._v("Lão sư thực lực, danh sư đại lục lại không người có thể làm bị thương, đã như vậy, không có gì có thể lo lắng."),t("br"),t("br"),n._v("Còn không bằng chân thật hoàn thành hắn giao phó sự tình, cố gắng tăng cao tu vi, không đến mức lão sư lại cùng cái gọi là Thần Linh lúc chiến đấu, kéo chân sau."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người khắp nơi tìm kiếm Trương sư tung tích, mà lúc này, danh sư đại lục một cái xa xôi tiểu trấn, Trương Huyền chậm rãi tiến lên."),t("br"),t("br"),n._v("Lúc này khoảng cách rời đi Chư Tử bách gia, đã hơn nửa năm, khoảng cách đi tới thế giới này, cũng đã trọn hai năm."),t("br"),t("br"),n._v("Hơn nửa năm qua, một đường tiến lên, thấy được vô số vui buồn li hợp, có người vui vẻ, có người đau khổ, có người vui vẻ, có người mất mác..."),t("br"),t("br"),n._v("Đem bọn hắn cảm xúc cùng trải qua, nhìn ở trong mắt, Trương Huyền giống như là trải qua lấy lần lượt cuộc sống khác."),t("br"),t("br"),n._v("Đi tới cái trấn nhỏ này, đã ba ngày, cũng nên rời đi."),t("br"),t("br"),n._v("Dọc theo đường phố, muốn đi ra đi, nghe được toàn bộ đường phố lập tức xôn xao, vô số người kích động nhanh chóng hướng một cái phương hướng xông tới."),t("br"),t("br"),n._v("“Nhanh lên một chút, có danh sư tới giảng bài!”"),t("br"),t("br"),n._v("“Nghe nói lần này giảng bài, muốn truyền thụ Trương sư kiến thức, nhất định phải cố gắng nhớ kỹ! Tranh thủ trở nên nổi bật, ăn Thanh Nha mạch!”"),t("br"),t("br"),n._v("“Thứ này là Chư Tử bách gia gieo trồng, danh sư đại lục không thể loại, mười phần trân quý, lần trước thôn bên cạnh thợ rèn chính là ăn một bữa, trực tiếp có cảm giác ngộ, đột phá đến Tông Sư cảnh!”"),t("br"),t("br"),n._v("“Đúng vậy a, hắn thật may mắn, có điều, có thể đột phá tông sư, cũng giá trị tuyệt đối...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người nghị luận ầm ĩ."),t("br"),t("br"),n._v("“Giảng bài?”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền cũng không nghĩ tới nhìn hứng thú."),t("br"),t("br"),n._v("Hơn nửa năm đó, đi ngang qua vô số thành trấn, tương tự giảng bài, thấy cũng nhiều."),t("br"),t("br"),n._v("Không ít danh sư, đều đánh tên tuổi của hắn truyền thụ kiến thức, có chính xác, có trăm ngàn chỗ hở, để cho người ta cho rằng là tại dạy hư học sinh."),t("br"),t("br"),n._v("Bất quá, cũng không mở miệng ngăn cản, càng không biểu lộ thân phận."),t("br"),t("br"),n._v("Nếu là đi ra lịch luyện, liền muốn xóa sạch trong lòng phù hoa cùng trước đó vinh dự, an tâm trải nghiệm thế gian muôn màu, người vắng lặng ấm."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Lại không ngừng lại, đang muốn đi ra tiểu trấn, đi vào cái kế tiếp thành thị, con mắt thoáng nhìn ở giữa, nhất thời ngừng lại."),t("br"),t("br"),n._v("“Là nàng?”"),t("br"),t("br"),n._v("Một cái xinh đẹp thân ảnh xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("Cùng hai năm trước đồng dạng mỹ lệ, động lòng người, thướt tha dáng người, để cho người ta hướng về."),t("br"),t("br"),n._v("Chỉ bất quá, giờ phút này, lại không là phổ thông lão sư, tinh anh giáo sư, mà là một vị thực sự danh sư, hơn nữa còn đạt đến tam tinh tình trạng."),t("br"),t("br"),n._v("“Chư vị tốt, ta tên Thẩm Bích Như, là một vị tam tinh danh sư...”"),t("br"),t("br"),n._v("Đứng tại trên đài cao, nữ hài nhìn quanh một vòng, mỉm cười, lộ ra nụ cười mê người."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1918"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1920"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);