(window.webpackJsonp=window.webpackJsonp||[]).push([[1038],{1393:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1907-co-thanh-ta-la-thien-đia-su-thuong"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1907-co-thanh-ta-la-thien-đia-su-thuong"}},[n._v("#")]),n._v(" Chương 1907: Cổ Thánh! Ta là thiên địa sư (thượng)")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Thẩm Bích Như, là Trương Huyền sau khi xuyên việt, Hồng Thiên học viện mỹ nữ lão sư, biết cái thứ nhất cô gái xinh đẹp, rời đi Thiên Huyền vương quốc về sau, lại chưa thấy qua."),g("br"),g("br"),n._v("Nửa năm trước, trở lại học viện, chỉ thấy được Lục Tầm, cũng không tìm tới đối phương, không nghĩ tới ở đây gặp lên, hơn nữa vậy mà đạt đến tam tinh danh sư tình trạng."),g("br"),g("br"),n._v("Mỉm cười, Trương Huyền dừng bước, trên đài nữ hài thanh âm, tiếp tục vang lên: “Ta cùng Trương Huyền Trương sư, từng là đồng nghiệp!”"),g("br"),g("br"),n._v("“Đồng nghiệp?”"),g("br"),g("br"),n._v("“Nói cách khác, nàng biết Trương sư?”"),g("br"),g("br"),n._v("“Khoác lác a, mỗi lần tới giảng bài người, đều nói biết, thậm chí còn bái nhập Trương sư môn hạ, phần lớn đều là cho mình lời tâng bốc!”"),g("br"),g("br"),n._v("“Cũng phải, không nghĩ tới xinh đẹp như vậy người, cũng là lừa đảo!”"),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Kèm theo Trương Huyền danh khí càng lúc càng lớn, giả mạo bọn họ sinh người, không biết bao nhiêu, dù vậy xa xôi tiểu trấn, cũng đều có sức miễn dịch."),g("br"),g("br"),n._v("Thấy vị này trong suy nghĩ, không tranh quyền thế mỹ nữ giáo sư, vậy mà vừa mở miệng liền nhắc tới mình, Trương Huyền lắc đầu, lộ ra vẻ thất vọng."),g("br"),g("br"),n._v("Trước đó còn cho rằng nàng, không màng danh lợi, không nghĩ tới, giống như những người khác mua danh cầu lợi, dùng tên tuổi của mình, nâng cao địa vị của mình."),g("br"),g("br"),n._v("Trong lòng mất mác, đang muốn rời đi, liền nghe đến nữ hài thanh âm tiếp tục vang lên: “Hắn hiện tại đã thành vạn thế chi sư, truyền thụ thiên hạ, vô số người đều lấy có thể nghe được kiến thức của hắn làm vinh... Nhưng, ta cũng không hâm mộ, cũng không cảm thấy cao thượng!”"),g("br"),g("br"),n._v("Trương Huyền dừng lại."),g("br"),g("br"),n._v("Bản cho rằng là lừa đảo, tính toán rời đi mọi người, cũng đều xoay người lại, từng cái mày nhíu lại nhanh."),g("br"),g("br"),n._v("Trương sư, đi sâu nhân tâm, trở thành tín ngưỡng của tất cả mọi người, ngươi nói không “Cao thượng”, có ý tứ gì?"),g("br"),g("br"),n._v("Trong lúc mơ hồ, đã có người bắt đầu không vui."),g("br"),g("br"),n._v("“Thế giới, là đa nguyên hóa, tựa như chúng ta cái trấn nhỏ này, cần đứng tại trong mây, truyền thụ kiến thức danh sư, cũng cần khơi thông lòng đất đường ống phổ thông lao động nhân viên! Càng cần hơn, giết lợn, bán đồ ăn, vận chuyển, bán đồ vật... Các ngành các nghề người. Người không có cao thượng cùng thấp kém phân chia, chức nghiệp cũng không có, có chỉ là, có nguyện ý hay không ở phía trên bỏ ra chân chính tâm huyết cùng tinh lực, có nguyện ý hay không để tâm, đi yêu thích bản thân tham gia sự tình!”"),g("br"),g("br"),n._v("“Trương sư, hắn ưa thích danh sư cái nghề nghiệp này, ưa thích cho người ta truyền thụ kiến thức, càng ưa thích nghiên cứu cao thâm bí ẩn, cho nên... Hắn thành công, đi tới danh sư cái nghề nghiệp này đỉnh phong!”"),g("br"),g("br"),n._v("“Ta chỉ là người bình thường, không có cái kia loại thiên phú và năng lực, nhưng cũng không đại biểu, ta còn kém rất rất xa đối phương, ta đồng dạng làm gương tốt, đồng dạng dạy dỗ học sinh, để cho mình học sinh, nghiêm ngặt chuẩn mực, cố gắng học tập! Ta không cách nào truyền thụ thiên hạ, để càng nhiều người biết, nhưng có thể bảo đảm, bản thân dạy dỗ kiến thức, sẽ không dạy hư học sinh, bảo đảm truyền thụ bất kỳ một chút học vấn, đều là bản thân tự mình thí nghiệm, chuẩn xác không sai... Vậy thì đủ!”"),g("br"),g("br"),n._v("“Người, muốn thấy rõ năng lực của mình, đi mình am hiểu đường! Những năm này, ta bốn phía dạy học, thấy không ít người, đều lâm vào chè chén say sưa, chúng ta vị trí thời kì, quả thực sinh ra một vị, vạn thế chi sư Trương Huyền, cùng năm đó Khổng sư đồng dạng, đại biểu nhân tộc đại hưng... Chúng ta sinh tồn ở tốt nhất thời kì, nhưng... Vậy thì đại biểu, ngươi nhất định sẽ trổ hết tài năng, trở thành cường giả ư?”"),g("br"),g("br"),n._v("“Không, cũng không phải là như vậy, cường giả, vô luận là ở đâu cái thời kì, đều chỉ là số ít người mới có thể đi đến!”"),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Tràn đầy tức giận mọi người, tất cả đều nói không ra lời."),g("br"),g("br"),n._v("Bất kỳ thời kì, đều khó có khả năng người người đều là cường giả."),g("br"),g("br"),n._v("Người người như rồng, chỉ là một loại trông đợi, không thể nào là sự thật."),g("br"),g("br"),n._v("Nếu không, tài nguyên cũng không đủ!"),g("br"),g("br"),n._v("Lực lượng bởi vì nắm giữ tại số ít người trong tay, mới được gọi là cường giả, mới có thể thu hút vô số người hướng về."),g("br"),g("br"),n._v("“Không nghĩ tới lại có loại này lĩnh ngộ...” Trương Huyền cũng là sững sờ."),g("br"),g("br"),n._v("Còn tưởng rằng, nữ hài lấy ra bản thân, là muốn nâng cao thân phận của mình, không nghĩ tới, vậy mà trình bày loại này đạo lý."),g("br"),g("br"),n._v("Người, đáng sợ nhất không phải năng lực không đủ, mà là, không cách nào nhận rõ bản thân."),g("br"),n._v("Rõ ràng chỉ đủ làm cái tiểu binh, nhất định muốn làm nguyên soái, cuối cùng làm hại không chỉ chính mình."),g("br"),g("br"),n._v("“Ít nghe nàng nói bậy, ta có thể nghe nói, Trương sư dạy dỗ học sinh bên trong, cũng có hay không quá tốt thiên phú, đồng dạng thành cường giả tuyệt thế!”"),g("br"),g("br"),n._v("“Không sai, lão sư tốt, có thể đi càng xa!”"),g("br"),g("br"),n._v("“Nói là Trương sư đồng nghiệp, chính là? Trương sư bằng hữu, cái nào, không phải nhân vật kinh thiên động địa? Làm sao có thể có tam tinh danh sư bằng hữu!”"),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Sau khi hết khiếp sợ, dưới đài càng thêm xôn xao."),g("br"),g("br"),n._v("Thẩm Bích Như mà nói, quấn tới không ít người hèn mọn nhất chỗ sâu."),g("br"),g("br"),n._v("Rõ ràng thấy không rõ bản thân, thấy không rõ thực tế, hết lần này tới lần khác nói, ta là thời vận không đủ."),g("br"),g("br"),n._v("Do ta viết tác phẩm, hẳn là vạn cổ lưu truyền, bởi vì biên tập có mắt không biết, cho nên, minh châu bị che mờ, cuối cùng ký kết đều không làm được."),g("br"),g("br"),n._v("“Ta biết lời này, khả năng tổn thương không ít người, nhưng... Mọi người cho rằng thật sự là sai ư? Ta không bằng Trương sư, hắn trở thành vạn thế chi sư, truyền thụ thiên hạ, có thể... Các ngươi thật chẳng lẽ cho rằng, hắn qua rất vui?”"),g("br"),g("br"),n._v("Nhìn ra mọi người phẫn nộ, Thẩm Bích Như lắc đầu, nói: “Năng lực càng lớn, trên bờ vai gánh vác trách nhiệm lại càng lớn, một năm qua này, hắn để Danh Sư đường thay đổi quy tắc, đã bình định Dị Linh tộc nhân phản loạn, liền Chư Tử bách gia, đều cúi đầu nghe lệnh... Bên nào, không trải qua cửu tử nhất sinh? Đổi lại các ngươi, chỉ muốn hưởng thụ cao cao tại thượng, sẽ đi mạo hiểm làm những này ư?”"),g("br"),g("br"),n._v("Kêu gào mọi người, nói không ra lời."),g("br"),g("br"),n._v("Đúng vậy a, nếu như bọn họ có Trương sư cái địa vị này cùng thiên phú, chắc chắn sẽ không mạo hiểm xông vào Dị Linh tộc, càng sẽ không đi trêu chọc Chư Tử bách gia..."),g("br"),g("br"),n._v("“Các ngươi cho rằng Trương sư, rất nở mày nở mặt, nhưng... Ta lúc đầu cùng hắn tại cùng một cái trường học thời điểm, hắn là bị người chỉ trích, người người phỉ nhổ lão sư, bởi vì giáo viên khảo hạch, thứ nhất đếm ngược, bất cứ lúc nào cũng sẽ bị khai trừ... Đối diện với mấy cái này, hắn không có chút nào sợ hãi, từng bước một đi đến hiện tại, đổi lại các ngươi, có thể đối phó được trong lòng áp lực?”"),g("br"),g("br"),n._v("Thẩm Bích Như tiếp tục nói."),g("br"),g("br"),n._v("Trương sư cuộc đời, sớm truyền ra, thậm chí đều bị người chỉnh lý thành sách, truyền tụng thiên hạ."),g("br"),g("br"),n._v("Hắn cùng nhau đi tới, đã trải qua bao nhiêu nguy hiểm, bao nhiêu hiểm trở, nhưng không có mảy may ngừng lại, đổi lại bọn họ... Có thể làm được ư?"),g("br"),g("br"),n._v("Không ít người trong lòng cân nhắc một cái, đồng thời lắc đầu."),g("br"),g("br"),n._v("Tâm cảnh kém, Hồng Thiên học viện liền có thể sẽ bị người mắng chết, cũng lại không ngóc đầu lên được."),g("br"),g("br"),n._v("Tâm cảnh tốt, Thiên Vũ vương quốc cũng sẽ dừng bước..."),g("br"),g("br"),n._v("Chớ nói chi là đằng sau ầm ầm sóng dậy nhân sinh."),g("br"),g("br"),n._v("Hâm mộ người khác có thể, nhưng người khác trải qua, ngươi khả năng trải qua không được."),g("br"),g("br"),n._v("Mặc sức tưởng tượng hải dương, lại không cách nào chịu đựng sóng lớn."),g("br"),g("br"),n._v("“Người người đều muốn lựa chọn, nhận rõ bản thân trọng yếu nhất...”"),g("br"),g("br"),n._v("Cùng mọi người lặng im khác biệt, Trương Huyền lòng có cảm xúc, hơn nửa năm tích lũy, giống như là đốt cháy dây dẫn nổ, trong nháy mắt tại nội tâm chỗ sâu nổ tung."),g("br"),g("br"),n._v("Danh sơn đại xuyên, tình người ấm lạnh, nửa năm qua, nhìn qua địa phương, dung hợp thành ký ức, nối thành một mảnh, để cả người hắn, giống như là huyền phù tại danh sư đại lục trên không, nhìn xuống thiên địa, triệt để siêu thoát rồi ngoại vật."),g("br"),g("br"),n._v("Không lấy vật thích, không lấy mình bi!"),g("br"),g("br"),n._v("Trương Huyền trong nháy mắt đi vào cảnh giới vong ngã, trước đó trong lòng rất nhiều mê man, trong chớp mắt phá giải, trong cơ thể giam giữ lại hơn nửa năm tu vi, phá đê nước sông bình thường, cuồn cuộn chảy xuôi."),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1919"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1921"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);