(window.webpackJsonp=window.webpackJsonp||[]).push([[1044],{1400:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1913-đi-vao-thuong-thuong-【-chuong-cuoi-quyen-nay-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1913-đi-vao-thuong-thuong-【-chuong-cuoi-quyen-nay-】"}},[n._v("#")]),n._v(" Chương 1913: Đi vào Thượng Thương 【 chương cuối quyển này 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Cùng “Thần Linh” đối chiến phía sau, Nhan Thanh Cổ Thánh đám người bị tước đoạt thành khung xương, tuổi thọ tiêu hao hầu như không còn, Trương Huyền trước khi đi, thấy tận mắt Khổng Thi Dao đem nó đóng băng, thời gian nửa năm mà thôi, hẳn là sẽ không tử vong."),t("br"),t("br"),n._v("Khổng Thi Dao gật đầu: “Đang tại ngủ say...”"),t("br"),t("br"),n._v("“Dẫn ta ta đi xem một chút!” Trương Huyền xoay người một trảo, đem vô số như thủy ngân linh khí tụ lại tại lòng bàn tay, phân phó nói."),t("br"),t("br"),n._v("Khổng Thi Dao phía trước dẫn đường, thời gian không dài đi tới một cái không gian bịt kín, mấy cái khung xương song song nằm tại đặc thù trên sân khấu, phía dưới đủ loại bảo vật tích tụ, liên tục không ngừng cung cấp sinh cơ, tốt cho bọn hắn duy trì lấy sinh mệnh."),t("br"),t("br"),n._v("Tuy là làm như vậy không thể để cho tuổi thọ tăng thêm, nhưng có thể trì hoãn tử vong ngày, bọn họ sở dĩ có thể sống sót vạn năm, chính là dựa vào cái này."),t("br"),t("br"),n._v("Đi tới Nhan Thanh Cổ Thánh khung xương trước mặt, Trương Huyền ngón tay búng một cái, mở ra viên đài bên trên phong ấn, cái trước khung xương chậm rãi tỉnh lại."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Khung xương khom người."),t("br"),t("br"),n._v("“Ta tìm tới một cái giải quyết ngươi giờ phút này mấu chốt phương pháp, bởi vì không có thí nghiệm qua, thành công, tuổi thọ kéo dài, thực lực tăng nhiều; Thất bại, có thể sẽ trực tiếp tử vong; Tiền bối, có muốn hay không không thử nghiệm một chút?” Không có bất kỳ cái gì dây dưa dài dòng, Trương Huyền nói thẳng."),t("br"),t("br"),n._v("“Ta muốn thử một chút!” Nhan Thanh Cổ Thánh gật đầu."),t("br"),t("br"),n._v("Sống nhiều năm như vậy, hắn đã sớm đem sinh tử không để ý."),t("br"),t("br"),n._v("“Vậy là tốt rồi, cái này có một bộ vận chuyển chân khí công pháp, ngươi bây giờ bắt đầu tu luyện, hơn nữa đang khôi phục thân thể thời điểm, dựa theo cái này kinh mạch hình lần nữa sắp đặt kinh mạch!”"),t("br"),t("br"),n._v("Ngón tay một chút, Thiên đạo pháp quyết nghịch chuyển công pháp và lúc trước hình thành kinh mạch hình, đi vào Nhan Thanh Cổ Thánh trong óc."),t("br"),t("br"),n._v("Hai thứ này cũng không còn tạp, học được không tính khó khăn, hơn mười phút phía sau, Nhan Thanh Cổ Thánh cũng đã triệt để hiểu, trong lòng tuy là kỳ lạ, nhưng tin tưởng Trương sư sẽ không hại hắn, hít sâu một hơi, dựa theo công pháp nội dung, bắt đầu vận chuyển chân khí."),t("br"),t("br"),n._v("Xì xì!"),t("br"),t("br"),n._v("Cong ngón tay búng một cái, Trương Huyền đem vừa rồi bắt lấy như thủy ngân linh khí thả ra ngoài, bao phủ bốn phía."),t("br"),t("br"),n._v("Nhìn thấy loại lực lượng này, Nhan Thanh Cổ Thánh khung xương, tràn đầy căng thẳng, không có quá nhiều rầu rĩ, cắn răng một cái, dựa theo đối phương vận chuyển chân khí phương thức, hấp thu đứng dậy, đang nghĩ ngợi, có thể hay không bị tươi sống đè chết, ai biết thời gian không dài, liền lộ ra kỳ quái biểu lộ."),t("br"),t("br"),n._v("Dựa theo Trương sư cấp cho công pháp tu luyện, như thủy ngân linh khí lại không cách nào lôi kéo kinh mạch, ngược lại cho hắn một loại tẩm bổ thân thể cảm giác, không ngừng hấp thu, sau hai canh giờ, thân thể vậy mà triệt để khôi phục."),t("br"),t("br"),n._v("Bởi vì kinh mạch thay đổi, trong cơ thể cũng thỉnh thoảng tản mát ra một cỗ nồng đậm sát lục chi khí, từ xa nhìn lại, giống như là một đầu Dị Linh tộc nhân."),t("br"),t("br"),n._v("“Rất tốt, thử một chút xung kích phá toái hư không!”"),t("br"),t("br"),n._v("Thấy đối phương thành công, Trương Huyền con mắt sáng lên, phân phó nói."),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh gật đầu, tiếp tục hấp thu."),t("br"),t("br"),n._v("Hắn so Triệu Nhã tích lũy càng thêm hùng hậu, chưa tới một canh giờ, quả nhiên cũng đột phá thành công, đạt đến Cổ Thánh tầng thứ tư, tuổi thọ lần nữa có tăng thêm."),t("br"),t("br"),n._v("Trước đó nguy hiểm đến tính mạng, giải quyết triệt để."),t("br"),t("br"),n._v("“Đa tạ Trương sư...”"),t("br"),t("br"),n._v("Cảm nhận được trong cơ thể dâng trào muốn ra lực lượng, Nhan Thanh Cổ Thánh tràn đầy kích động, cũng lại kìm nén không được, quỳ mọp xuống đất."),t("br"),t("br"),n._v("Vốn cho rằng lần này bị thương nặng, hẳn phải chết không nghi ngờ, nằm mơ đều không có nghĩ đến, vậy mà lấy loại này phương thức kỳ lạ sống sót, thậm chí còn đột phá trước đó tu vi, đạt đến tha thiết ước mơ cảnh giới."),t("br"),t("br"),n._v("“Thủy ngân linh khí quá mạnh, hấp thu vào trong cơ thể, không chỉ tu luyện tăng tốc, tốc độ khôi phục, cũng phải so Cổ Thánh lực lượng nhanh hơn...”"),t("br"),t("br"),n._v("Thấy mình trong lòng ý nghĩ, chuẩn xác không sai, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, Nhan Thanh Cổ Thánh thời khắc này tuổi thọ, coi như Cổ Thánh lực lượng đầy đủ, cũng kiên trì không đến triệt để khôi phục thân thể."),t("br"),t("br"),n._v("Nhưng hấp thu thủy ngân linh khí lại khác biệt."),t("br"),t("br"),n._v("Loại này linh khí, không chỉ nặng nề, ẩn chứa trong đó lực lượng, cũng vượt ra khỏi tưởng tượng, chỉ cần có thể hấp thu, nặng hơn tổn thương, cũng có thể rất nhanh khôi phục."),t("br"),t("br"),n._v("Xác nhận khả thi, Trương Huyền lại không rầu rĩ, lại đem còn lại mấy người đánh thức, đưa đến đài cao, dựa theo đồng dạng bước đi, không tiêu tốn quá lâu, mấy vị bất cứ lúc nào cũng sẽ tử vong Cổ Thánh, phần lớn đều bước ra một bước cuối cùng, thành phá toái hư không cảnh giới cường giả."),t("br"),t("br"),n._v("Đương nhiên, cũng có ngoài ý muốn, trong đó hai vị Cổ Thánh, lúc tu luyện, xuất hiện sai lầm, bị nặng nề linh khí ép khung xương vỡ vụn, tại chỗ tử vong."),t("br"),t("br"),n._v("Liên tục thí nghiệm, biết con đường này có thể thực hiện, Trương Huyền đem rất nhiều đệ tử triệu hoán tới, bàn tay bỗng nhiên vỗ một cái."),t("br"),t("br"),n._v("Lực lượng bao phủ tám vị đệ tử, tất cả mọi người đồng thời máu tươi phun mạnh, kinh mạch trong cơ thể đã bị triệt để chấn vỡ."),t("br"),t("br"),n._v("“Dựa theo cái này tu luyện, tuyệt đối không thể phạm sai lầm, nếu không, chết rồi, đừng trách ta không có nhắc nhở!”"),t("br"),t("br"),n._v("Đem vừa rồi cái kia hai loại, khắc sâu vào mọi người trong óc, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Trịnh Dương đám người, thấy được Nhan Thanh Cổ Thánh đám người thuế biến, biết làm cái gì, lại thêm bọn họ đối thiên đạo tinh giản công pháp, hết sức quen thuộc, cứ việc không có nghịch chuyển tu luyện qua, chỉ khi nào tiếp xúc, rất dễ dàng vào tay."),t("br"),t("br"),n._v("Khổng Thi Dao cái thứ nhất triệt để khôi phục, hơn nữa đột phá đến phá toái hư không."),t("br"),t("br"),n._v("Ngay sau đó là Trịnh Dương, Ngụy Như Yên, Lộ Xung, Vương Dĩnh..."),t("br"),t("br"),n._v("Sau năm ngày, tu vi yếu nhất Trương Cửu Tiêu, cũng thành công đột phá đến phá toái hư không cảnh giới!"),t("br"),t("br"),n._v("Cho tới giờ khắc này, Trương Huyền tính cả chín đại đệ tử, đều biến thành Cổ Thánh tứ trọng sơ kỳ siêu cấp cường giả."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Mọi người thành công, từng cái tràn đầy kích động."),t("br"),t("br"),n._v("Năm đó Khổng sư không làm được sự tình, lão sư thế mà hoàn thành."),t("br"),t("br"),n._v("“Ta tính toán xuyên qua cái này phong ấn, đi vào Thượng Thương, phía trên tình huống không rõ ràng, có thể sẽ trải qua vô số nguy hiểm, thậm chí cửu tử nhất sinh, các ngươi trước không vội đi qua, trấn thủ danh sư đại lục, cùng nơi này, chờ tin tức của ta!”"),t("br"),t("br"),n._v("Đem mọi người triệu tập ở bên cạnh, Trương Huyền đem quyết định của mình nói ra."),t("br"),t("br"),n._v("Rất nhiều đệ tử tu luyện, mấy ngày nay hắn một mực sau khi tự hỏi mặt nên đi nơi nào."),t("br"),t("br"),n._v("Ra kết luận, cũng nên rời đi nơi này, đi cái gọi là Thượng Thương nhìn một chút!"),t("br"),n._v("Muốn tìm kiếm Lạc Nhược Hi, nhất định phải đi đường này."),t("br"),t("br"),n._v("“Lão sư, chúng ta muốn cùng ngươi cùng một chỗ...”"),t("br"),t("br"),n._v("Triệu Nhã tiến về phía trước một bước."),t("br"),t("br"),n._v("“Đúng vậy a, mặc kệ nguy hiểm cỡ nào, lão sư ở đâu, chúng ta ngay tại đâu, sẽ không lùi bước!”"),t("br"),t("br"),n._v("Trịnh Dương cũng gật đầu nói."),t("br"),t("br"),n._v("Mấy vị khác đệ tử, tuy là không lên tiếng, lại đều từng cái mang theo không cho cự tuyệt kiên định."),t("br"),t("br"),n._v("Cùng nhau đi tới, có thể có thành tựu hiện tại, toàn bộ nhờ lão sư, lão sư cần trải qua nguy hiểm, bọn họ lại thế nào khả năng an tâm ở lại đi xuống."),t("br"),t("br"),n._v("“Tâm ý của các ngươi ta biết, nhưng... Thượng Thương tình huống không biết không nói, danh sư đại lục cũng cần các ngươi đóng giữ, yên tâm, chờ ta làm rõ thông đạo tình huống bên kia, sẽ nghĩ biện pháp, mượn Lưu Dương tế tự, báo tin các ngươi!”"),t("br"),t("br"),n._v("Biết rất nhiều học sinh ý nghĩ, Trương Huyền cười cười, nói."),t("br"),t("br"),n._v("Thượng Thương cao thủ nhiều như mây, một mình hắn, bất luận gặp phải nguy hiểm, vẫn là cái gì, đều rất dễ dàng ứng phó, mang theo nhiều như vậy học sinh, liền không có đơn giản như vậy."),t("br"),t("br"),n._v("Huống chi, những đệ tử này, đều có bản thân bên trên trách nhiệm, cũng không phải nói đi liền có thể đi."),t("br"),t("br"),n._v("Tỷ như Lưu Dương, thân là Dị Linh chi Hoàng, Dị Linh tộc vừa mới ổn định, lúc này rời đi, làm không cẩn thận lại sẽ xuất hiện phản loạn, cùng nhân tộc lần nữa tiến hành bất tử bất diệt tranh đấu."),t("br"),t("br"),n._v("“Vậy... Lão sư bảo trọng!”"),t("br"),t("br"),n._v("Biết lão sư tâm ý đã quyết, không có người có thể thay đổi, mọi người vẻ mặt ảm đạm, tất cả đều lộ ra không bỏ chi tình."),t("br"),t("br"),n._v("“Trịnh Dương, Long Cốt thần thương, đi qua thủy ngân linh khí rèn luyện, giờ phút này cũng đột phá đến phá toái hư không cảnh giới, hiện tại liền ban cho ngươi, sau khi ta rời đi, không biết những cái được gọi là ‘Thần Linh’, vẫn sẽ hay không ra, hi vọng ngươi có thể lo liệu bản tâm, bảo hộ nhân tộc!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đem Long Cốt thần thương đưa tới."),t("br"),t("br"),n._v("Đi vào Thượng Thương thông đạo, bởi vì có thế giới cách xa nhau, trên người bảo vật tính cả không gian giới chỉ, cũng không thể mang theo, lại nói, tu vi đi đến hắn loại cảnh giới này, chuôi này trường thương tác dụng đã không lớn."),t("br"),t("br"),n._v("“Vương Dĩnh, ngươi am hiểu là khải linh, nhưng khải linh cần quá trình, Kim Nguyên đỉnh liền ban tặng ngươi, bảo vệ ngươi an nguy, tăng thêm thực lực của ngươi!”"),t("br"),t("br"),n._v("Cổ tay rung lên, đem Kim Nguyên đỉnh đặt ở trước mặt."),t("br"),t("br"),n._v("Cái này viên gạch bộ dáng gia hỏa, trong khoảng thời gian này, đi qua thủy ngân linh khí rèn luyện, cứ việc không có đột phá phá toái hư không, cũng sắp, đã không thể mang theo, không bằng để lại cho trước mắt ý định này tinh tế nữ hài."),t("br"),t("br"),n._v("Đem trên người sở hữu bảo vật, từng cái lấy ra, ban tặng mọi người, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Chín đại đệ tử đều đi đến phá toái hư không, lại thêm những bảo vật này trợ giúp, coi như thật có “Thần Linh” đi tới, hẳn là cũng có thể nhẹ nhõm ứng phó, không đến mức luống cuống tay chân."),t("br"),t("br"),n._v("Còn Ngoan Nhân, ẩn giấu ở Thiên đạo chi sách bên trong, có thể thả lại thư viện, giấu vào trong óc, lại thêm cái tên này, là năm đó Khổng sư đều kiêng kỵ nhân vật, không còn bản thân hạn chế, vạn nhất vì họa một phương, liền phiền toái, cho nên, cũng không lưu lại."),t("br"),t("br"),n._v("Còn có cái hồ lô, giấu ở đan điền, không lấy ra ra, đồng dạng lưu tại trong cơ thể."),t("br"),t("br"),n._v("Còn phân thân, là hắn lớn nhất cơ mật, đi vào trong thư viện Xuân Thu đại điển phía trong."),t("br"),t("br"),n._v("Đây là cái độc lập không gian, cùng phòng đọc đồng dạng, có thể cho phép Trương Huyền hấp thu ngoại giới linh khí, phân thân linh hồn bản chất tương đồng, tự nhiên cũng có thể nhẹ nhõm đi vào."),t("br"),t("br"),n._v("Tất cả chuẩn bị ổn thoả, Trương Huyền phá vỡ lúc trước lưu lại phong ấn, thân thể nhảy lên, thẳng tắp chui vào."),t("br"),t("br"),n._v("Trước đó, cái này phong ấn đâu đâu cũng có thủy ngân linh khí, đối thân thể cùng linh hồn đều có cực mạnh áp bức, giờ phút này, đột phá đến phá toái hư không, lại thêm hấp thu nhiều như vậy, thân thể được cải tạo, lần nữa tiếp xúc, không những không còn khó chịu, ngược lại có loại cá vào biển cả cảm giác."),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Phong ấn nội bộ, từng đạo đen kịt vết rách, không gian vỡ thành vô số loạn lưu, như phong bạo hướng hắn cuộn sạch."),t("br"),t("br"),n._v("“May mắn đạt đến phá toái hư không, nếu không, còn chưa đi xa, liền sẽ bị cuồng bạo không gian, xé thành bột phấn...”"),t("br"),t("br"),n._v("Hướng về phía trước phi hành một khoảng cách, Trương Huyền ánh mắt ngưng trọng."),t("br"),t("br"),n._v("Nơi này không gian phong bạo, so danh sư đại lục mạnh mẽ quá nhiều."),t("br"),t("br"),n._v("Đừng nói tích huyết trùng sinh, coi như phổ thông phá toái hư không, khả năng đều sẽ không chịu nổi, bị trực tiếp xé rách."),t("br"),t("br"),n._v("Thiệt thòi hắn, tích lũy mạnh mẽ, sức chiến đấu vượt xa chân thực cấp bậc, nếu không, khả năng cũng chống lại không được."),t("br"),t("br"),n._v("Dù vậy, toàn thân cao thấp vẫn như cũ bị vết nứt không gian lôi kéo tràn đầy máu tươi, thương thế không nhẹ."),t("br"),t("br"),n._v("Cái lối đi này, không có đường, đâu đâu cũng có mất đi không gian, hắn có Thiên đạo thư viện, có thể phân biệt ra được con đường an toàn, nếu không, rơi vào trong lỗ đen, đến cùng sẽ phát sinh cái gì, liền ai cũng không biết."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Bay về phía trước nửa tháng, một cái to lớn lôi đình xuất hiện tại phía trước, phải đi đường triệt để phong tỏa."),t("br"),t("br"),n._v("Đang suy nghĩ làm sao đi tới, một đạo thiểm điện ngang trời bổ tới, Trương Huyền mắt tối sầm lại, nhất thời ngất đi."),t("br"),t("br"),n._v("P/S:"),t("br"),t("br"),n._v("Quyển 14 Khổng sư dấu chân kết thúc, quyển kế tiếp trực tiếp đi vào một cái hoàn toàn mới bản đồ, Thượng Thương."),t("br"),t("br"),n._v("Đây là đi Thần giới liên tiếp tuyến, cho nên độ dài sẽ không quá dài, Thượng Thương cùng Thần giới, lão nhai vừa mở sách liền thiết kế tốt, một cái chơi vui, đặc biệt trang bức trang bị, vẫn muốn viết, rốt cục có thể viết ra."),t("br"),t("br"),n._v("Giới thiệu vắn tắt bên trên, Chước Dương Đại Đế, Linh Lung tiên tử các loại, đều sẽ xuất hiện tại đây loại chơi vui trang bị bên trong."),t("br"),t("br"),n._v("Mặt khác, lão nhai cũng bắt đầu chuẩn bị tăng tốc chương mới, tranh thủ năm nay hoàn thành."),t("br"),t("br"),n._v("Thật ra thì vốn muốn năm, sáu tháng hoàn thành, đột nhiên phát hiện, trước đó bố cục thực sự quá lớn, thu không trở lại, đành phải dần dần từng bước."),t("br"),t("br"),n._v("Còn kết cục, mọi người không cần lo lắng, mở sách trước liền lối suy nghĩ tốt, ta bình thường đều là mở sách trước đó, trước đem cả quyển sách giả thiết xong xuôi, sau đó lại bắt đầu viết."),t("br"),t("br"),n._v("Cho nên, bao quát thư viện nguồn gốc, Lạc Nhược Hi nguồn gốc, Khổng sư kết cục... Đều sẽ có cái viên mãn kết quả, kính xin mong đợi!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1925"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1927"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);