(window.webpackJsonp=window.webpackJsonp||[]).push([[1045],{1398:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1914-suy-nghi-mot-chut-đeu-cam-thay-kich-thich"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1914-suy-nghi-mot-chut-đeu-cam-thay-kich-thich"}},[n._v("#")]),n._v(" Chương 1914: Suy nghĩ một chút đều cảm thấy kích thích")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Đau đầu muốn nứt, Trương Huyền chậm rãi tỉnh lại."),t("br"),t("br"),n._v("Đập vào mắt chỗ, là cái rộng rãi gian phòng."),t("br"),t("br"),n._v("“Thực lực của ta...”"),t("br"),t("br"),n._v("Chậm rãi ngồi dậy, cúi đầu liếc mắt nhìn, trên người u đầy băng gạc, toàn thân đau đớn kinh khủng, vừa nhìn liền biết thương thế không nhẹ, muốn vận chuyển lực lượng, lại phát hiện chân khí trong cơ thể, ít đến thương cảm."),t("br"),t("br"),n._v("“Thủy ngân chân khí?”"),t("br"),t("br"),n._v("Cẩn thận quan sát, phát hiện còn sót lại chân khí nặng nề vô cùng, vận chuyển một chút, đều tràn đầy khó khăn."),t("br"),t("br"),n._v("Trước đó hấp thu thủy ngân linh khí, đem thuế biến thành tự mình tu luyện chân khí, trên thực tế là tương đương với đem linh khí nồng nặc, thay đổi thành mỏng manh chân khí, mà giờ khắc này, không biết sao, trong cơ thể mạnh mẽ tựa như giống biển cả chân khí, biến mất không thấy gì nữa, chỉ lưu giữ một chút tương tự như thủy ngân chân khí."),t("br"),t("br"),n._v("Dày nặng là dày nặng, có thể hắn hiện tại thân thể điều kiện, muốn điều động, đều rất khó làm được, chớ nói chi là dùng để khôi phục thương thế."),t("br"),t("br"),n._v("“Ta đi vào Thượng Thương thông đạo, bị lôi đình ngăn cản, sau đó... Mất đi tri giác...”"),t("br"),t("br"),n._v("Xem xong trong cơ thể rối bời một mảnh, trước đó trải qua phù hiện ở trong óc, Trương Huyền đồng tử co rụt lại: “Chẳng lẽ... Đã đến Thượng Thương?”"),t("br"),t("br"),n._v("Tình huống bình thường hắn hẳn là lưu động tại không gian thông đạo mới là, bây giờ lại nằm trong phòng, trên người cũng bị băng bó xong xong, chẳng lẽ... Đã thông qua được lôi đình khu vực, thuận lợi đi vào cái gọi là Thượng Thương?"),t("br"),t("br"),n._v("Tinh thần khẽ động, thần thức muốn giãn ra, khóe miệng kìm lòng không được co lại."),t("br"),t("br"),n._v("Trước đó có thể đủ lan ra mấy trăm vạn cây số thần thức, lại bị gắt gao giam cầm ở trong người, sống chết đều lan ra không đi ra!"),t("br"),t("br"),n._v("“Thật mạnh không gian áp bức!”"),t("br"),t("br"),n._v("Không cần tiếp tục, hắn cũng biết, hẳn là đến Thượng Thương, không chỉ thần thức không cách nào lan ra, năng lực phi hành, cũng nhận hạn chế, không gian thật lớn chèn ép xuống, đi bộ đều khó mà duy trì, chớ nói chi là bay lên."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Trong bụng một hồi vang trầm, vậy mà đói bụng."),t("br"),t("br"),n._v("Từ khi tu luyện tới hóa phàm cảnh, hấp thu linh khí, cũng đủ để vì bản thân cung cấp năng lượng, có ăn hay không cơm cũng không trọng yếu, còn là lần đầu tiên cảm thấy như vậy đói khát, dường như toàn bộ thân thể, đều phải bị móc sạch."),t("br"),t("br"),n._v("Thần thức không cách nào lan ra, tĩnh tâm cảm ứng, bốn phía quả nhiên tràn đầy như thủy ngân linh khí, rậm rạp chằng chịt, so trước đó tại Côn Hư cảnh đài cao hạ xuống những cái kia, nồng nặc không chỉ gấp mười lần."),t("br"),t("br"),n._v("Muốn hấp thu, rèn luyện thành chân khí, khôi phục thương thế, nhất thời cảm thấy hơi đỏ mặt, một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("Một lát sau, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Trên người không có tổn thương thời điểm, hấp thu thủy ngân linh khí, có thể dựa vào thực lực bản thân đối kháng, không xuất hiện ngoài ý muốn, giờ phút này toàn thân không có một chỗ hoàn hảo, kinh mạch cũng xuất hiện vô số vết rách, hút một cái thu liền không chịu nổi, chớ nói chi là, chuyển hóa thành nội tức."),t("br"),t("br"),n._v("“Nhìn tới chỉ có thể chờ đợi thương thế khá hơn một chút lại nói...”"),t("br"),t("br"),n._v("Biết gấp gáp cũng vô dụng, Trương Huyền phóng bình tâm thái."),t("br"),t("br"),n._v("Bởi vì bị thương nặng, thêm vào chân khí hầu như từng li từng tí không dư thừa, phá toái hư không thực lực, không thi triển ra được, cả người thoạt nhìn, cùng người bình thường không có khác nhau quá nhiều."),t("br"),t("br"),n._v("“Trước tìm chút gì ăn...”"),t("br"),t("br"),n._v("Cảm giác được trong bụng càng ngày càng đói, giẫy giụa đứng dậy, xoa xoa cái bụng, chậm rãi ra khỏi phòng."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Vừa đẩy cửa ra, ngay sau đó nghe được trong viện một cơn gió tiếng, giương mắt nhìn lại, ngay sau đó nhìn thấy một thiếu niên, cầm trong tay một thanh thiết kiếm, thi triển kiếm pháp."),t("br"),t("br"),n._v("Chân trái của hắn như là gỗ đồng dạng cứng nhắc, cũng không biết là bị thương vẫn là vốn như vậy, đi lại mười phần phiền phức, hoạt động bên trên không có bất kỳ cái gì cuốn hút, kiếm trong tay, cũng không ngừng tung bay, cho người ta một loại vui tai vui mắt cảm giác."),t("br"),t("br"),n._v("“Kiến thức cơ bản còn có thể, kiếm pháp kém chút...”"),t("br"),t("br"),n._v("Liếc mắt nhìn, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Kiếm pháp của đối phương, tùy tiện kéo một cái tam tinh danh sư, đều vượt xa, quả thực không được tốt lắm, nhưng thân thể như vậy không tiện dưới tình huống, còn có thể làm được điểm ấy, đã rất tốt."),t("br"),t("br"),n._v("Trong lòng than thở, nhịn không được làm ra chút tiếng vang, nghe được động tĩnh, đang luyện kiếm thiếu niên ngừng lại, vội vàng quay đầu, vẻ mặt vui mừng: “Ngươi đã tỉnh!”"),t("br"),t("br"),n._v("Vừa quay đầu thấy rõ dung mạo."),t("br"),t("br"),n._v("Thiếu niên đại khái mười sáu, bảy tuổi, cái đầu không cao, trọn vẹn so Trương Huyền thấp một đầu nửa, dựa theo kiếp trước thân cao mà tính lời nói, khả năng còn chưa đủ một mét sáu, bên trái trên gương mặt, một cái tinh hồng sắc bớt, nửa cái lớn chừng bàn tay, đem vốn là không tính thanh tú mặt, che đậy hơn phân nửa."),t("br"),n._v("Từ xa nhìn lại, không những cùng anh tuấn kéo không lên quan hệ, còn có chút vô cùng thê thảm."),t("br"),t("br"),n._v("“Đa tạ ân cứu mạng...”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Không có gì, ta cũng là trong lúc vô tình nhìn thấy, mới đưa ngươi cứu trở về... Vốn cho rằng thương thế nặng như vậy, hẳn phải chết không nghi ngờ, không nghĩ tới vậy mà bản thân khôi phục...”"),t("br"),t("br"),n._v("Thiếu niên gãi đầu một cái, nói chuyện không quá ăn khớp, vừa nhìn liền biết không am hiểu cùng người đi đường tiếp xúc."),t("br"),t("br"),n._v("“Tam thiếu gia...”"),t("br"),t("br"),n._v("Bên ngoài một cái lão giả, đi vào, nhìn thấy cổng Trương Huyền, sửng sốt một chút, ngay sau đó khom người: “Vị công tử này, ngươi đã tỉnh!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu đáp lễ."),t("br"),t("br"),n._v("Nhìn kỹ hướng lão giả, trên quần áo tràn đầy tràn dầu, khóe miệng xé rách ra một đường vết rách, có máu tươi dấu vết, trên gương mặt cũng đầy là máu ứ đọng, vừa nhìn liền biết, vừa bị người khác đánh cho một trận."),t("br"),t("br"),n._v("“Dịch lão, làm sao vậy?”"),t("br"),t("br"),n._v("Được xưng Tam thiếu gia bị què thiếu niên, thấy lão giả bộ dáng này, biến sắc, vội vàng thu kiếm đi tới, tràn đầy lo lắng."),t("br"),t("br"),n._v("“Bẩm báo thiếu gia, không có việc gì...”"),t("br"),t("br"),n._v("Dịch lão vẻ mặt khó xử, không biết trả lời như thế nào, sau một lúc lâu, nói “Là... Phủ thành chủ Nhị tiểu thư tới!”"),t("br"),t("br"),n._v("“Tiết Cầm tới?”"),t("br"),t("br"),n._v("Nghe được vị này Nhị tiểu thư, bị què thiếu niên ánh mắt sáng lên, trong mắt mang theo vẻ kích động."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Nhìn thấy thiếu gia như vậy biểu lộ, Dịch lão vẻ mặt càng thêm khó xử."),t("br"),t("br"),n._v("“Nàng tới... Cùng ngươi bị thương có quan hệ gì?”"),t("br"),t("br"),n._v("Vui vẻ sau đó, bị què thiếu niên ý thức được không đúng."),t("br"),t("br"),n._v("“Ba ngày sau, không phải Lăng Vân kiếm các, thu nhận tạp dịch đệ tử ư?” Dịch lão rầu rĩ một cái, cắn răng một cái: “Tiết Cầm Nhị tiểu thư, dựa dẫm phủ thành chủ quan hệ... Nói trước được danh ngạch...”"),t("br"),t("br"),n._v("“Được danh ngạch?”"),t("br"),t("br"),n._v("Bị què thiếu niên vẻ mặt vui mừng: “Cái kia... Chẳng phải là chuyện tốt?”"),t("br"),t("br"),n._v("Lời còn chưa dứt, cúi đầu liếc mắt nhìn, bản thân bị què, vẻ mặt nhất thời ảm đạm xuống: “Ta... Khẳng định là không có hy vọng...”"),t("br"),t("br"),n._v("“Được danh ngạch, khẳng định muốn đi Lăng Vân Kiếm tông, chẳng lẽ là tới tìm ta tạm biệt?”"),t("br"),t("br"),n._v("Lắc đầu, đem trong lòng không thoải mái dứt bỏ, bị què thiếu niên hỏi."),t("br"),t("br"),n._v("“Nàng... Nàng...”"),t("br"),t("br"),n._v("Dường như không biết trả lời như thế nào, dừng lại phút chốc, Dịch lão lần nữa cắn răng: “Nàng... Là tới cùng ngươi từ hôn!”"),t("br"),t("br"),n._v("“Từ hôn?”"),t("br"),t("br"),n._v("Bị què thiếu niên, vẻ mặt đột nhiên trở nên trắng bệch vô cùng, thân thể lắc lư một cái, suýt chút nữa ngã sấp xuống: “Nàng cũng bởi vì... Thành Lăng Vân kiếm các tạp dịch đệ tử, liền muốn cùng ta từ hôn?”"),t("br"),t("br"),n._v("Bị nữ tử từ hôn, vô luận là ở đâu bên trong, đều là vô cùng đánh mặt sự tình."),t("br"),t("br"),n._v("“Thiên chi kiêu nữ không nhìn trúng vị hôn phu, đến đây từ hôn, bị thương thiếu niên, quyết chí tự cường, ba mươi năm Hà Đông ba mươi năm Hà Tây?”"),t("br"),t("br"),n._v("Đem hai người đối thoại nghe vào trong tai, Trương Huyền lông mày nhảy dựng."),t("br"),t("br"),n._v("Vừa đến Thượng Thương liền gặp phải loại sự tình này..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm giác kích thích ah..."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1926"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1928"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);