(window.webpackJsonp=window.webpackJsonp||[]).push([[1046],{1401:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1915-thien-hieu-thien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1915-thien-hieu-thien"}},[n._v("#")]),n._v(" Chương 1915: Thiện Hiểu Thiên")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Một lát sau, bị què thiếu niên lắc đầu, trong thần sắc để lộ ra nồng đậm mất mác: “Ta quả thực không xứng với nàng, từ hôn, chỉ là sớm muộn thôi...”"),t("br"),t("br"),n._v("Cái gọi là hôn ước, là năm đó tổ tông giao hảo, chỉ phúc vi hôn, về sau, gia cảnh sa sút, bởi vì bị thương, nội tức bị phá hư, không cách nào tiếp tục tu luyện, chân trái phế bỏ, trị liệu không tốt, cả người liền tinh thần sa sút xuống."),t("br"),t("br"),n._v("Huống chi dung mạo của hắn... Cùng đối phương quả thực không phải lương phối."),t("br"),t("br"),n._v("Từ hôn, sớm muộn mà thôi."),t("br"),t("br"),n._v("Tiếng thở dài bên trong, xoay người đi vào gian phòng, một lát sau cầm một cái hộp ngọc, đưa tới."),t("br"),t("br"),n._v("“Đây là năm đó thư đính hôn, đưa cho nàng a, nói cho nàng, từ hôm nay trở đi, chúng ta lại không quan hệ, cũng hi vọng nàng có thể tìm tới phù hợp bản thân...”"),t("br"),t("br"),n._v("“Tam thiếu gia...”"),t("br"),t("br"),n._v("Vẻ mặt đỏ lên, Dịch lão hàm răng cắn bờ môi đều nổi lên đỏ tươi, muốn nói điều gì, cuối cùng vẫn là ngừng lại, gật gật đầu: “Vâng!”"),t("br"),t("br"),n._v("“Vậy thì sợ...”"),t("br"),t("br"),n._v("Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Vốn cho rằng sẽ dưới cơn nóng giận, diễn ra một loại nào đó tức giận phấn đấu vở kịch, không nghĩ tới vị Tam thiếu gia này, trực tiếp đem giấy hôn thú lui về... Tương đương đưa mặt bị đối phương giày xéo... Làm người đứng xem, đều có chút không nhìn nổi."),t("br"),t("br"),n._v("Bất quá, hắn từ trước tới nay khiêm tốn, lại nói, vừa tới Thượng Thương vẫn không rõ tình huống, muốn nói cái gì, cuối cùng ngừng lại."),t("br"),t("br"),n._v("Cầm hôn ước, Dịch lão xoay người rời đi."),t("br"),t("br"),n._v("Giơ trường kiếm lên, tam thiếu gia trong lòng tràn đầy tức giận, bỗng nhiên chém xuống, cách đó không xa nham thạch một hồi lay động, bổ ra một vết nứt."),t("br"),t("br"),n._v("Thi triển ra lực lượng, Trương Huyền cũng nhìn ra thực lực của hắn, Thánh vực lục trọng!"),t("br"),t("br"),n._v("Mười sáu mười bảy tuổi Thánh vực lục trọng, tại danh sư đại lục, tuyệt đối là thiên tài trong thiên tài, mà nhìn vị Tam thiếu gia này bộ dạng, hẳn là thuộc về vô dụng nhất lưu."),t("br"),t("br"),n._v("Bổ tới một kiếm, bị què thiếu niên buồn bực trong lòng được phát tiết, dường như tâm tình tốt một chút, lúc này mới nghĩ tới cách đó không xa còn đứng lấy một cái bản thân cứu thanh niên, hơi đỏ mặt: “Thực sự xấu hổ, bản thân việc vặt, để ngươi chê cười... Đúng, ta còn không biết huynh đài họ tên...”"),t("br"),t("br"),n._v("“Tại hạ Dược... Phi, ta tên Trương Huyền!”"),t("br"),t("br"),n._v("P/S: Lão Nhai nhây vl, định Dược Trần à =) )"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Nguyên lai là Trương huynh...” Bị què thanh niên, nói: “Tại hạ Thiện Hiểu Thiên, Bạch Nghiệp thành Thiện gia tam thiếu gia...”"),t("br"),t("br"),n._v("“Thiện huynh!” Gật gật đầu, Trương Huyền trong mắt mang theo vẻ nghi hoặc: “Vừa rồi nghe Thiện huynh nói, Lăng Vân kiếm các... Chẳng lẽ là cái tông môn?”"),t("br"),t("br"),n._v("“Trương huynh không biết Lăng Vân kiếm các?”"),t("br"),t("br"),n._v("Nghe hắn hỏi như vậy, Thiện Hiểu Thiên sững sờ, khi lấy được đối phương chuẩn xác trả lời chắc chắn phía sau, lúc này mới đầy mặt cười khổ: “Trương huynh không phải tu luyện giả, không biết cũng rất bình thường, chúng ta nơi này, tên là Di Khí đại lục, nghe nói là bị Thần Linh vứt bỏ thế giới! Hết thảy có sáu đại tông môn, Lăng Vân kiếm các chính là một trong số đó! Hàng năm đều có vô số người muốn đi vào trong đó, dù là trở thành một cái tạp dịch đệ tử, sau lưng gia tộc cũng đều sẽ một bước lên trời, trở thành một phương cường hào!”"),t("br"),t("br"),n._v("Vị này “Trương huynh”, cứu trở về thời điểm, thoi thóp, trong cơ thể mảy may không cảm thấy được chân khí chấn động, hẳn là người bình thường, cũng không phải là tu luyện giả."),t("br"),t("br"),n._v("Không phải tu giả, không biết sáu đại tông môn, cũng không kỳ lạ."),t("br"),t("br"),n._v("“Sáu đại tông môn... Nơi này không có Danh Sư đường?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("“Danh Sư đường? Đó là cái gì?”"),t("br"),n._v("Lắc đầu, Thiện Hiểu Thiên lộ ra nghi ngờ."),t("br"),t("br"),n._v("“Ta tùy tiện nói một chút...” Nghe đối phương khẩu khí, hẳn là không có, Trương Huyền tiếp tục nói: “Nói như vậy, Di Khí đại lục lấy sáu đại tông môn vi tôn?”"),t("br"),t("br"),n._v("“Ừm, chẳng qua loại trừ bọn họ bên ngoài, còn có một cái rất lợi hại thế lực, tên là... Thông Thần điện, là vài ngàn năm trước, ngang trời xuất hiện, do một vị cường giả thành lập, khống chế lấy , đại lục ở bên trên tất cả tu luyện giả, muốn trở thành cường giả, muốn hối đoái tài nguyên, đều muốn mượn thứ này!”"),t("br"),t("br"),n._v("“Thông Thần điện?”"),t("br"),t("br"),n._v("“Ừm, hầu như che kín mỗi một tòa thành thị, có thể dựa vào lực lượng, mua sắm thông thần ngọc phù, thứ này chỉ cần tiếp xúc, có thể đem người ý thức, thu nạp trong đó, tiến vào bên trong  giao dịch! Ở đây, chỉ cần có đầy đủ đánh đổi, có thể học tập công pháp, mua dược liệu, nghĩ biện pháp tăng lên lực lượng, thậm chí mua sắm sát thủ... Dù sao chỉ cần ngươi muốn muốn, cái gì cần có đều có! Sáu đại tông môn người, cũng đều đang sử dụng!”"),t("br"),t("br"),n._v("“Thu nạp ý thức?” Trương Huyền tràn đầy tò mò: “Không biết Thiện huynh nơi này nhưng có thứ này?”"),t("br"),t("br"),n._v("“Thực lực của ta yếu, không có thiên phú tu luyện, liền vị hôn thê, đều trực tiếp từ hôn... Nào có tư cách nắm giữ thứ này!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên đầy mặt khó xử."),t("br"),t("br"),n._v("“Là ta lỗ mãng...” Trương Huyền ôm quyền, đổi chủ đề, lại hỏi thăm vài câu."),t("br"),t("br"),n._v("Hắn trải qua lôi đình hôn mê phía sau, hẳn là xé rách không gian, đến nơi này, chân khí trong cơ thể, vì bảo vệ mình, tiêu hao hầu như không còn."),t("br"),t("br"),n._v("Vị Tam thiếu gia này, tuy là không tốt lời nói, ở sâu trong nội tâm cũng có chút tự ti, đáy lòng lại hết sức hiền lành, ra ngoài nhìn thấy trọng thương bản thân, không đành lòng, liền cứu trở về, trên người băng bó, chính là trước đó vị kia “Dịch lão” lưu lại."),t("br"),t("br"),n._v("Từ cứu trở về đến bây giờ, đã qua trọn ba ngày."),t("br"),t("br"),n._v("Di Khí đại lục tôn trọng thực lực, vị này Thiện Hiểu Thiên vị trí Thiện gia, hai mươi năm trước huy hoàng nhất thời, cùng phủ thành chủ nổi danh, thậm chí, phủ thành chủ đều muốn dựa vào, đáng tiếc mười năm trước một lần tai nạn phía sau, gia tộc cường giả hầu như tử vong hầu như không còn, hắn hai vị ca ca, cũng chết đi."),t("br"),t("br"),n._v("Cho dù là hắn, cũng thương tổn tới căn cơ, biến thành hiện tại bộ dáng này."),t("br"),t("br"),n._v("Gia cảnh sa sút, nhận người gạt bỏ, trước đó giao hảo thế lực, tới tấp phản bội, lớn như vậy gia tộc, không mấy năm liền triệt để sụp đổ, hiện tại Thiện gia, loại trừ hắn cùng lão nô Thiện Dịch (Dịch lão), hầu như không còn có cái gì nữa."),t("br"),t("br"),n._v("Hơn ngàn mẫu phủ đệ, cũng chỉ còn lại cái này nội viện cùng phía ngoài sân nhỏ, vinh quang không còn lúc trước."),t("br"),t("br"),n._v("Nếu không phải là bởi vì cùng phủ thành chủ có hôn ước tại, chỉ sợ điểm ấy địa phương cũng không còn sót lại."),t("br"),t("br"),n._v("Bất quá, kèm theo vị này nhị tiểu thư từ hôn, chắc hẳn không bao lâu, trước kia kẻ thù, lần nữa tìm tới cửa, đến lúc đó... Đất cắm dùi, đều đem hoàn toàn không có."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Bất kỳ địa phương nào, bất kỳ cái gì thế lực, không có thực lực cường đại thủ hộ, suy bại là tất nhiên."),t("br"),t("br"),n._v("“Trương huynh, ta tình huống hiện tại ngươi cũng rõ ràng, vốn nghĩ, Lăng Vân kiếm các phá lệ ở đây, thu nhận tạp dịch đệ tử, có lẽ có thể liều một cái, hiện tại xem ra, hoàn toàn không có hi vọng, tại thêm vào Tiết Cầm từ hôn... Nơi này, khẳng định cũng ở không được mấy ngày!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên cười khổ lắc đầu."),t("br"),t("br"),n._v("Hài tử của người nghèo sớm biết lo liệu việc nhà, và Dịch lão sống nương tựa lẫn nhau nhiều năm như vậy, đã sớm nhận rõ tình người ấm lạnh."),t("br"),t("br"),n._v("“Trương huynh nếu đã tỉnh lại, phải nắm chặt thời gian rời đi a, miễn cho nhận liên lụy...”"),t("br"),t("br"),n._v("Nói xong Thiện Hiểu Thiên từ trong túi áo lấy ra hai cái đặc chế kim tệ đưa tới: “Đây là Thông Thần điện lưu thông thông thần tệ, tuy là thiếu chút... Nhưng cũng có thể để ngươi ăn hơn mấy bữa cơm, không đến mức đói bụng...”"),t("br"),t("br"),n._v("“Khách khí...”"),t("br"),t("br"),n._v("Gặp hắn bộ này tình huống, còn muốn lấy bản thân, Trương Huyền trong lòng khen ngợi một tiếng, nhịn không được nhìn lại: “Thiện huynh, có thể hay không đem ngươi kiếm pháp, lại thi triển một lần, để cho ta nhìn một chút?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1927"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1929"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);