(window.webpackJsonp=window.webpackJsonp||[]).push([[1047],{1402:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1916-khuat-nhuc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1916-khuat-nhuc"}},[n._v("#")]),n._v(" Chương 1916: Khuất nhục")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thi triển kiếm pháp?”"),t("br"),t("br"),n._v("Không nghĩ tới vị này Trương huynh, đưa ra yêu cầu này, Thiện Hiểu Thiên tràn đầy không hiểu."),t("br"),t("br"),n._v("Ngươi một cái không hiểu tu luyện, coi như ta thi triển kiếm pháp, cũng xem không hiểu ah!"),t("br"),t("br"),n._v("“Ta vừa rồi nhìn Thiện huynh kiếm pháp, lực lượng nhận hạn chế, giẫm chân tại chỗ, muốn lại quan sát một chút, có lẽ có thể tìm ra phương pháp, ba ngày thời gian, có cơ hội tăng lên lời nói, trở thành Lăng Vân kiếm các đệ tử, cũng không phải không có hi vọng!”"),t("br"),t("br"),n._v("Trương Huyền cười cười."),t("br"),t("br"),n._v("Vừa rồi đối phương thi triển kiếm pháp, dựa vào nhãn lực của hắn, đã nhìn ra một vài vấn đề, hiện tại làm cho đối phương thi triển, là muốn thử xem, thư viện tại đây cái Thượng Thương, có thể hay không dùng."),t("br"),t("br"),n._v("“Trương huynh... Hiểu được kiếm pháp?” Thiện Hiểu Thiên cau mày."),t("br"),t("br"),n._v("“Hiểu sơ một hai!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy được! Lăng Vân kiếm các đệ tử, ta không nghĩ tới...”"),t("br"),t("br"),n._v("Nhìn bản thân hư chân trái liếc mắt, Thiện Hiểu Thiên lắc đầu, nói: “Bất quá, nếu như Trương huynh hiểu kiếm pháp, trao đổi một chút, ngược lại là có thể! Từ khi không cách nào tu luyện phía sau, đều là tự mình tìm tòi, chưa hề cùng người trao đổi qua...”"),t("br"),t("br"),n._v("Hắn bởi vì tự ti, lại không cách nào tu luyện, cái gọi là kiếm pháp, chỉ là bản thân tiêu khiển, chưa hề cùng người bày ra qua, chớ nói chi là tỷ thí."),t("br"),t("br"),n._v("Đã trước mắt vị này, hiểu kiếm pháp, trao đổi một chút, cũng không có gì."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, trường kiếm rung ra kiếm hoa, hướng về phía trước đâm tới."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trương Huyền trong óc khẽ động, một quyển sách xuất hiện."),t("br"),t("br"),n._v("Đem thư tịch mở ra, xem xong nội dung trong đó, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Nhìn tới coi như tại Thượng Thương, thư viện cũng có thể dùng, có lẽ cùng trước đó đoán đồng dạng, Lạc Nhược Hi đem Xuân Thu đại điển để vào trong đó phía sau, cái trước có rất lớn tấn cấp, Thượng Thương Thiên đạo thăm dò lên, cũng không có áp lực chút nào."),t("br"),t("br"),n._v("“Thật đúng là đủ buồn cười, Thiện Hiểu Thiên, ngươi cũng luyện kiếm, thế nào, còn nghĩ, Lăng Vân kiếm các có thể thu ngươi làm đệ tử hay sao?”"),t("br"),t("br"),n._v("Trong lòng đang suy tư, một cái cười lạnh từ bên ngoài vang lên, ngay sau đó hai bóng người, sải bước vào."),t("br"),t("br"),n._v("Đi đầu một cái, là cái mười lăm, mười sáu tuổi nữ hài, một thân màu vàng nhạt váy dài, ánh mắt lộ ra nồng đậm kiêu ngạo, cái cằm hơi hơi nâng lên, tựa như công chúa, lời nói mới rồi chính là từ trong miệng nàng vang lên."),t("br"),t("br"),n._v("Phía sau là cái khoảng bốn mươi tuổi trung niên nhân, một thân áo tang, lông mày đen thui, bên hông buộc lấy thanh trường kiếm, còn không có trước mắt, liền cho người ta một loại kiếm khí áp bức cảm giác."),t("br"),t("br"),n._v("“Tam thiếu gia...”"),t("br"),t("br"),n._v("Dịch lão vội vã từ phía sau chạy tới, tràn đầy áy náy cùng không cam lòng: “Ta ngăn không được...”"),t("br"),t("br"),n._v("Dừng lại trường kiếm, Thiện Hiểu Thiên nhìn về phía cô bé trước mắt, vừa căng thẳng bờ môi có chút nói lắp: “Tiết, Tiết Cầm?”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Ánh mắt lộ ra một tia chán ghét, nữ hài khoát tay áo: “Thế nào, biết rõ, ta tới ý tứ, không bản thân đi ra, cho rằng phái cái hạ nhân, đem hôn ước lấy tới, như vậy liền muốn lừa gạt?”"),t("br"),t("br"),n._v("“Ngươi muốn hủy hôn, ta đem hôn ước đều cho... Còn muốn như thế nào nữa?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên vẻ mặt đỏ lên."),t("br"),t("br"),n._v("“Không ra sao, rất đơn giản, ta muốn ngươi toàn thành công bố, là ngươi không xứng với ta, chủ động yêu cầu từ hôn, ta phủ thành chủ, từ chối liên tục, sau đó tôn trọng ý kiến của ngươi, lúc này mới đem hôn ước kết thúc... Không phải, ngươi trực tiếp đem hôn ước lấy ra, là muốn nói cho tất cả mọi người, ta Tiết Cầm không muốn gả cho ngươi? Làm cho tất cả mọi người đâm phủ thành chủ cột sống ư?”"),t("br"),t("br"),n._v("Hừ một tiếng, Tiết Cầm nói."),t("br"),t("br"),n._v("Nếu như cho người khác biết, là bởi vì nàng thành Lăng Vân kiếm các đệ tử, mới cùng trước mắt vị này từ hôn, tất nhiên sẽ dẫn tới không ít dư luận, đối nàng cực kỳ bất lợi, nhưng đối phương trước tiên lui cưới, danh dự liền bảo vệ, không có một chút tì vết."),t("br"),t("br"),n._v("“Đủ hung ác...”"),t("br"),t("br"),n._v("Một bên Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Từ hôn liền rất để cho người ta nhận vũ nhục, cho hôn ước đều không được, còn muốn toàn thành công bố... Then chốt, chẳng những muốn hủy hôn, còn muốn làm người tốt!"),t("br"),t("br"),n._v("Quả thực chính là trở thành kỹ nữ còn muốn đứng đền thờ."),t("br"),n._v(" Khuôn mặt trắng bệch, Thiện Hiểu Thiên nắm đấm xiết chặt."),t("br"),t("br"),n._v("Trương Huyền một cái liền có thể nghĩ thông suốt, làm người trong cuộc, tự nhiên hiểu thêm, nồng đậm cảm giác nhục nhã, xông lên toàn thân."),t("br"),t("br"),n._v("Khuất nhục!"),t("br"),t("br"),n._v("Ở trước mặt đánh mặt, hơn nữa còn là tay năm tay mười cái chủng loại kia!"),t("br"),t("br"),n._v("Nhưng hắn... Vô lực phản kháng!"),t("br"),t("br"),n._v("“Tốt, lần này tới, không phải cùng ngươi thảo luận, mà là mệnh lệnh! Trong ba ngày, nhất định phải thông cáo toàn thành, có kết quả, nếu không, tự gánh lấy hậu quả!”"),t("br"),t("br"),n._v("Không lên tiếng trung niên nhân, vẻ mặt lạnh lùng nhìn lại."),t("br"),t("br"),n._v("“Ngươi là ai?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên nhìn qua, giống như là bị thương đầu lang."),t("br"),t("br"),n._v("“Ta là nhị tiểu thư kiếm thuật lão sư, Tiết Trầm!” Trung niên nhân thản nhiên nói."),t("br"),t("br"),n._v("“Tiết Trầm? Ta biết ngươi...”"),t("br"),t("br"),n._v("Tựa hồ nghe qua cái tên này, Thiện Hiểu Thiên trên mặt lộ ra tự giễu biểu lộ: “Để phủ thành chủ kiếm thuật tổng giáo tập tới, tự thân nói, quả thực có phân lượng... Chỉ là, không biết cái gọi là kết quả là cái gì?”"),t("br"),t("br"),n._v("Vị này Tiết Trầm, chính là phủ thành chủ tổng giáo tập, một vị Cổ Thánh tam trọng cường giả!"),t("br"),t("br"),n._v("“Không đáp ứng cũng rất đơn giản!”"),t("br"),t("br"),n._v("Tiết Trầm mặt không hề cảm xúc: “Thiện gia những năm này, đắc tội không ít người, nếu như không có cùng phủ thành chủ thông gia, thêm vào bản thân ngươi chính là vô dụng, hẳn là đã sớm diệt tuyệt...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên không nói lời nào."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Nếu như không phải là bởi vì hắn, không cách nào tu luyện, lại cùng phủ thành chủ có hôn ước quan hệ, sao có thể sống đến bây giờ!"),t("br"),t("br"),n._v("Trước đó vô số kẻ thù, khẳng định đã sớm xông lại, đem nơi này san thành bình địa."),t("br"),t("br"),n._v("“Chỉ cần những người này, muốn ra tay, chỉ bằng các ngươi một chủ một tớ, có thể chống đỡ được?” Tiết Trầm tiếp tục nói."),t("br"),t("br"),n._v("Thiện Hiểu Thiên không ngừng run rẩy: “Ngươi đây là giết chúng ta diệt khẩu? Chẳng lẽ liền không sợ nhiều miệng mồm mọi người?”"),t("br"),t("br"),n._v("“Nhiều miệng mồm mọi người?”"),t("br"),t("br"),n._v("Tiết Trầm mí mắt vừa nhấc: “Các ngươi sau khi chết, thành chủ cực kỳ bi thương, sẽ đích thân phái người thay các ngươi báo thù, sau đó tiểu thư, càng sẽ khóc rống thật nhiều ngày, cuối cùng thương tâm rời đi nơi đây, đi vào Lăng Vân kiếm các...”"),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Trái tim sắp bùng nổ, Thiện Hiểu Thiên lại nói không ra lời nói tới."),t("br"),t("br"),n._v("Hắn biết, một khi như vậy, phủ thành chủ danh dự không những không bị đến tổn thương, sẽ còn trở nên càng thêm trọng tình trọng nghĩa, vị này Tiết Cầm tiểu thư, cũng sẽ càng thêm nhận tôn trọng."),t("br"),t("br"),n._v("Đều là Lăng Vân kiếm các tạp dịch đệ tử, lại còn không thoái hôn, còn đau lòng đau khổ..."),t("br"),t("br"),n._v("“Tốt, tốt, ta... Đáp ứng!”"),t("br"),t("br"),n._v("Móng tay cắm vào trong thịt, tất cả đốt ngón tay, biến thành trắng như tuyết chi sắc, Thiện Hiểu Thiên cứ việc lòng tràn đầy không cam lòng, nhưng cũng không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("Mạnh được yếu thua, đây là pháp tắc sinh tồn."),t("br"),t("br"),n._v("“Cái này còn tạm được!” Gặp hắn đồng ý, Tiết Cầm lúc này mới thoả mãn gật gật đầu, vẫy tay một cái, Tiết Trầm từ trong ngực lấy ra một phần văn thư đưa tới: “Đây là của ngươi từ hôn nguyên do cùng thông cáo, ký tên vào, ngày hôm nay toàn bộ người bên trong thành liền sẽ toàn bộ biết! Không cần ngươi đi nói nhiều.”"),t("br"),t("br"),n._v("Nhận lấy văn thư, Thiện Hiểu Thiên nhìn lại."),t("br"),t("br"),n._v("Lý do mười phần đơn giản, nói hắn bị thương, không chỉ có không cách nào tu luyện, còn không cách nào việc người, mười phần phế nhân, không nhớ chậm trễ đối phương, vì vậy thoái thác hôn ước, mong được tha thứ, bởi vì nói qua nhiều lần, phủ thành chủ nhớ tình nghĩa, một mực không đồng ý, mới ra hạ sách này, sớm báo cho toàn thành, mong rằng rộng lòng tha thứ..."),t("br"),t("br"),n._v("Không thể việc người?"),t("br"),t("br"),n._v("Mười phần phế nhân?"),t("br"),t("br"),n._v("Mãnh liệt khuất nhục, để Thiện Hiểu Thiên thân thể run rẩy, huyết dịch trong nháy mắt xông vào trong óc, sắp bùng nổ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1928"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1930"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);