(window.webpackJsonp=window.webpackJsonp||[]).push([[1048],{1403:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-184-đoc-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-184-đoc-su"}},[n._v("#")]),n._v(" Chương 184: Độc sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Nhanh chóng hôn mê, sẽ không chết?” "),t("br"),t("br"),n._v(" Thẩm Truy bệ hạ cùng Lưu Lăng, Trịnh Phi liếc mắt nhìn nhau, trên đầu đều bốc lên dấu chấm hỏi."),t("br"),t("br"),n._v(" Không phải tới cứu người sao?"),t("br"),t("br"),n._v(" Muốn có thể khiến người ta hôn mê đồ vật làm gì?"),t("br"),t("br"),n._v(" Cứu người chỉ nghe nói qua truyền vào chân khí, dùng thuốc, châm cứu huyệt đạo cùng thủ đoạn, còn có phương pháp này?"),t("br"),t("br"),n._v(" Và những người khác kỳ quái không giống, đứng ở một bên Trang Hiền khóe miệng kéo, cảm giác cái mông căng thẳng, một loại táo bón cảm giác dâng lên trên."),t("br"),t("br"),n._v(" Tuy rằng dùng rất nhiều thuốc, nhưng hôm nay mặt vẫn là sưng, trên người hơi động còn có chút đau đớn, đối phương cứ việc giúp hắn thành công đột phá, mang trong lòng cảm kích, có thể... Thủ đoạn cũng không tránh khỏi quá ly kinh phản đạo."),t("br"),t("br"),n._v(" Bình thường danh sư, giúp người đột phá, đều có nhất định quy trình, thậm chí đã hình thành chuẩn mực, cái tên này ngược lại tốt, che đầu một trận ám côn... Hiện đang nghĩ đến, đều lòng vẫn còn sợ hãi."),t("br"),t("br"),n._v(" Đương nhiên, tình huống của hắn đặc thù, có thể thông cảm được, có thể cứu trị sắp chết rồi Thẩm Hồng, muốn có thể khiến người ta hôn mê đồ vật làm gì?"),t("br"),t("br"),n._v(" Sẽ không lại muốn dùng cái gì để người khó có thể lý giải được trị liệu phương thức đi..."),t("br"),t("br"),n._v(" “Nơi này có một loại thuốc bột, ngửi bên trên một ngụm, liền có thể khiến người ta hôn mê...” "),t("br"),t("br"),n._v(" Chần chờ chốc lát, Thẩm Truy bệ hạ nói."),t("br"),t("br"),n._v(" Trong vương cung, đủ loại thuốc cơ bản đều đủ, tìm một loại có thể khiến người ta hôn mê, sẽ không chết đồ vật cũng không khó."),t("br"),t("br"),n._v(" “Tốt, lấy ra đến!” Trương Huyền ánh mắt sáng lên, phân phó nói."),t("br"),t("br"),n._v(" Thời gian không lâu, một cái thái giám liền đem một cái bình ngọc đưa tới."),t("br"),t("br"),n._v(" Kéo ra bình ngọc, quả nhiên thấy bên trong chồng chất màu trắng bột phấn."),t("br"),t("br"),n._v(" “Không sai.” "),t("br"),t("br"),n._v(" Thoả mãn gật gù, Trương Huyền cầm lấy bình ngọc, lần thứ hai đi tới Thẩm Hồng trước mặt, tại mọi người ánh mắt nghi hoặc bên trong, miệng bình nhắm ngay vị này hít vào nhiều thở ra ít vương thất lão tổ, đầy mặt tự tin cùng đại khí: “Ngửi! Đến, ngửi một ngụm!” "),t("br"),t("br"),n._v(" Phù phù!"),t("br"),t("br"),n._v(" Mọi người suýt chút nữa ngất đi."),t("br"),t("br"),n._v(" Vốn tưởng rằng phải cái này dược có tác dụng gì, nghe nói như thế, ba vị danh sư cùng Thẩm Truy bệ hạ không sặc nước bọt mà chết."),t("br"),t("br"),n._v(" Người ta cứu người, đều là uống thuốc bổ, uống linh dịch, hấp thu linh khí... Ngươi để người ngửi mê dược..."),t("br"),t("br"),n._v(" Ngươi này không phải cứu người, tới giết đi người đi!"),t("br"),t("br"),n._v(" Mọi người rung mạnh, vương thất lão tổ càng là một mặt sợ hãi vùng vẫy lắc đầu, nếu không là thân thể không thể động đậy, sợ là sớm đã nhảy lên đào tẩu."),t("br"),t("br"),n._v(" Rất khỏe mạnh thời điểm, ngửi cái này đều sẽ cho thân thể mang đến gánh nặng, hiện tại sắp ngỏm rồi, còn để ngửi, ngươi là cảm thấy ta chết không đủ nhanh? Muốn giúp một chuyện?"),t("br"),t("br"),n._v(" Trong lòng sốt ruột, vội vã nhìn về phía cách đó không xa Thẩm Truy bệ hạ."),t("br"),t("br"),n._v(" Ngươi không phải muốn mời danh sư trị liệu, để ta đột phá kéo dài tuổi thọ sao?"),t("br"),t("br"),n._v(" Chạy tới liền để ta ngửi mê dược làm gì?"),t("br"),t("br"),n._v("  Ngươi này tìm thứ đồ gì?"),t("br"),t("br"),n._v(" Quá hãm hại đi!"),t("br"),t("br"),n._v(" “Khặc khặc, Dương sư... Lão tổ thân thể đã suy yếu đến cực điểm, ta sợ... Thật ngửi một ngụm, sẽ... Không chịu nổi!” "),t("br"),t("br"),n._v(" Nhìn thấy lão tổ nghi vấn ánh mắt, Thẩm Truy bệ hạ khóe miệng kéo, vội vàng về phía trước."),t("br"),t("br"),n._v(" Ngày hôm qua từng trải qua vị này Dương sư thủ đoạn kỳ lạ, vốn tưởng rằng chỉ giới hạn ở đột phá, nằm mơ đều không nghĩ tới, chữa bệnh cũng hắn đây mẹ không thể tưởng tượng nổi."),t("br"),t("br"),n._v(" Xem lão tổ dáng vẻ, có thể kiên trì đến ngươi tới, đã xem như là rất lớn nghị lực, thật muốn ngửi bên trên một ngụm, phỏng chừng vốn là không chết, cũng gần như."),t("br"),t("br"),n._v(" “Không chịu nổi?” Trương Huyền dừng lại."),t("br"),t("br"),n._v(" Cũng đúng, xem cái tên này dáng vẻ, bất cứ lúc nào cũng sẽ tắt thở, toàn dựa vào một luồng niềm tin kiên trì, thật muốn hôn mê, niềm tin đánh mất, làm không cẩn thận thật sẽ tại chỗ tử vong."),t("br"),t("br"),n._v(" Không thể để cho hắn hôn mê, lại không có cách nào đánh quyền?"),t("br"),t("br"),n._v(" Làm sao bây giờ?"),t("br"),t("br"),n._v(" Trương Huyền buồn rầu."),t("br"),t("br"),n._v(" Vốn tưởng rằng vừa đến, mượn thư viện, có thể rất dễ dàng nhìn ra chỗ mấu chốt, coi như không tìm được giải cứu phương pháp, cũng có thể nói cái một, hai ba, hiện tại ngược lại tốt, sống dở chết dở, cái gì thủ đoạn cũng không thể dùng, ngược lại làm cho hắn bó tay toàn tập."),t("br"),t("br"),n._v(" “Cái kia... Có món đồ gì có thể làm cho sức sống của hắn trong thời gian ngắn tăng cường, coi như rơi vào hôn mê cũng chết không được?” "),t("br"),t("br"),n._v(" Chần chờ chốc lát, Trương Huyền nói tiếp."),t("br"),t("br"),n._v(" Nếu hiện tại bất cứ lúc nào cũng sẽ tử vong, dùng mê dược mê ngất khẳng định không được, chỉ có thể nghĩ biện pháp để sức sống của hắn tạm thời tăng cường, bảo đảm hôn mê không chết."),t("br"),t("br"),n._v(" “Hôn mê không chết?” "),t("br"),t("br"),n._v(" Mọi người ngươi nhìn ta ta nhìn ngươi."),t("br"),t("br"),n._v(" Vị này Dương sư xem ra là quyết tâm muốn đem Thẩm Hồng lão tổ làm mê muội mê..."),t("br"),t("br"),n._v(" “Lưu sư, danh sư thủ pháp chẩn đoán bệnh bên trong, có thể có... Để người hôn mê mới có thể chẩn đoán bệnh lời giải thích?” Trang Hiền không nhịn được lặng lẽ truyền âm."),t("br"),t("br"),n._v(" Danh sư xem người, có rất nhiều chẩn đoán bệnh phương pháp, cùng thầy thuốc xem bệnh như thế, thông qua những thủ đoạn này, mới có thể hiểu rõ đối phương tình huống, nhằm thẳng chỗ yếu."),t("br"),t("br"),n._v(" Trước mắt vị này “Cao nhân” cách làm, thực sự quá quỷ dị, coi như là danh sư, cũng lý giải không được."),t("br"),t("br"),n._v(" “Cái này... Đều là đối phương tỉnh táo mới có thể chẩn đoán bệnh, hôn mê... Ta cũng không biết!” Lưu Lăng lắc lắc đầu."),t("br"),t("br"),n._v(" Hắn kiến thức rộng rãi, đã từng có du lịch rất nhiều Vương quốc trải qua, gặp qua người khác chuyên môn đem hôn mê người làm tỉnh lại tốt thuận tiện chẩn đoán bệnh, còn chưa từng nghe nói đem tỉnh chuyên môn mê đi."),t("br"),t("br"),n._v(" Té xỉu người liền ý thức đều không có, cùng thi thể không có gì khác nhau, làm sao tìm được gặp sự cố?"),t("br"),t("br"),n._v("..."),t("br"),n._v("  Mấy vị danh sư đầu óc mơ hồ, Thẩm Truy bệ hạ cũng tràn đầy mộng bức, chần chờ một chút: “Cái này... Lão tổ có thể dùng thuốc bổ, cơ bản đều dùng qua, để người tăng cường sức sống thuốc, đối với hắn đã không còn quá to lớn tác dụng...” "),t("br"),t("br"),n._v(" Thẩm Hồng sau lưng có một cái Vương quốc giúp đỡ, vì cho hắn kéo dài sinh mệnh, đủ loại thuốc, ăn không biết bao nhiêu, trong cơ thể đã có nồng đậm đỡ dược tính, hiện tại coi như cho nhiều hơn nữa thuốc, cũng cơ bản không có gì hiệu quả."),t("br"),t("br"),n._v(" Nghe được lời của đối phương, Trương Huyền càng thêm phiền muộn."),t("br"),t("br"),n._v(" Vốn là có loại thuốc nào, cho hắn ăn, để cho khôi phục chút tinh thần, đánh một bộ quyền xem một cái, kết quả cái tên này là cái ấm sắc thuốc, thuốc vô hiệu..."),t("br"),t("br"),n._v(" Giời ạ, lần này sẽ không thật muốn thất thủ chứ?"),t("br"),t("br"),n._v(" “Ta ngược lại thật ra biết một loại đồ vật, có thể khiến người ta trong thời gian ngắn tinh khí thần tăng lên, cùng vừa nãy Thẩm Truy bệ hạ nói gần như, sau khi uống, có thể làm cho suy yếu người tăng cường sức mạnh, khôi phục tinh thần, đồng thời tác dụng phụ rất nhỏ, bất quá...” "),t("br"),t("br"),n._v(" Lưu Lăng trầm tư một chút mở miệng, nói đến đây, tràn đầy do dự."),t("br"),t("br"),n._v(" “Tuy nhiên làm sao?” "),t("br"),t("br"),n._v(" Trương Huyền nhìn sang."),t("br"),t("br"),n._v(" “Bất quá... Này không phải thuốc bổ, mà là... Độc dược!” Lưu Lăng nói."),t("br"),t("br"),n._v(" “Độc dược?” "),t("br"),t("br"),n._v(" Trương Huyền một trận mê hoặc."),t("br"),t("br"),n._v(" Độc dược không phải muốn đem người giết chết sao? Làm sao phục dùng độc dược, còn có thể để người tinh khí thần tăng cường, hơn nữa tác dụng phụ rất nhỏ?"),t("br"),t("br"),n._v(" Không những là hắn, liền ngay cả Thẩm Truy bệ hạ cũng có chút không hiểu nổi, dùng ánh mắt kỳ quái nhìn sang."),t("br"),t("br"),n._v(" “Người người đều biết độc sư là hạ cửu lưu nghề nghiệp, phối chế thuốc, làm một ít sau lưng bắn tên trộm, bỉ ổi việc, là người khinh thường, nhưng nghề nghiệp này có thể hoàn chỉnh truyền thừa xuống, đủ để chứng minh, tất nhiên có giá trị tồn tại.” "),t("br"),t("br"),n._v(" Một bên Trịnh Phi mở miệng."),t("br"),t("br"),n._v(" Bất kỳ nghề nghiệp nào, có thể truyền thừa, khẳng định có chỗ thích hợp, không thể một mực làm thấp đi."),t("br"),t("br"),n._v(" Độc sư danh tiếng tuy rằng không được, trên thực tế cũng có chỗ thích hợp, tỷ như, người bình thường muốn tiêu diệt hại trùng, lẩn tránh chướng khí, liền cần người như thế trợ giúp."),t("br"),t("br"),n._v(" “Đúng đấy, vật này ta cũng nghe qua, nghe nói là do mười mấy loại kịch độc dựa theo đặc thù tỉ lệ phối hợp mà thành, cùng luyện đan như thế, cực kỳ phức tạp. Chế biến ra đến đồ vật, bởi vì rất nhiều độc tính tương khắc, do đó hình thành một cái hoàn mỹ cân bằng, đối với sinh mạng không những không hại, còn có thể kích hoạt tiềm lực, để người trong thời gian ngắn, khôi phục tinh khí thần, vô cùng thần kỳ.” "),t("br"),t("br"),n._v(" Trang Hiền tựa hồ cũng nghĩ ra đến, gật đầu nói."),t("br"),t("br"),n._v(" Nói xong một mặt kỳ quái nhìn sang: “Độc sư loại nghề nghiệp này tuy rằng lạ, rất hiếm thấy, nhưng phối chế thứ này, tại nhất đẳng Vương quốc, vô cùng thông thường, Dương sư... Không biết?” "),t("br"),t("br"),n._v(" Không những hắn kỳ quái, giờ khắc này liền ngay cả Lưu Lăng, trịnh phỉ cũng tràn đầy nghi hoặc."),t("br"),t("br"),n._v(" Danh sư, đầu tiên muốn bác học nhiều biết, thượng, trung, hạ tam đại cửu lưu nghề nghiệp, cùng với bọn họ một ít năng lực đặc thù, tại sát hạch nhất tinh danh sư thời điểm, sẽ cùng sát hạch luyện đan học đồ như thế, cần tỉ mỉ trả lời câu hỏi."),t("br"),t("br"),n._v(" Chỉ cần là danh sư, cơ bản đều biết."),t("br"),t("br"),n._v(" Trước mắt vị này, có thể cho bọn họ chỉ điểm, cấp bậc rõ ràng cao hơn, làm sao có khả năng liền độc sư cùng với bọn họ phối chế loại dược liệu này đều chưa từng nghe nói?"),t("br"),t("br"),n._v(" Xem ra trái lại cùng lần đầu tiên nghe nói như thế?"),t("br"),t("br"),n._v(" “Ta... Ta không thích độc sư nghề nghiệp này, không có gì nghiên cứu...” "),t("br"),t("br"),n._v(" Nhìn thấy mọi người ánh mắt nghi hoặc, Trương Huyền có chút lúng túng."),t("br"),t("br"),n._v(" Nắm giữ Thiên Đạo thư viện, chỉ điểm người khác dễ dàng, nhưng gặp phải loại này thường thức tính đồ vật, cũng rất dễ dàng lòi."),t("br"),t("br"),n._v(" Làm danh sư, đệ nhất muốn có nhãn lực, có thể xem gặp sự cố, mà để nhãn lực biến lợi hại nguyên nhân chủ yếu chính là bác học, nắm giữ kinh người tri thức đo."),t("br"),t("br"),n._v(" Không có tri thức làm căn cứ, làm sao có khả năng một chút nhìn ra chỗ mấu chốt, làm cho người ta chỉ điểm?"),t("br"),t("br"),n._v(" Mà Trương Huyền, thiếu chính là loại này tích lũy."),t("br"),t("br"),n._v(" Dựa vào thư viện dao động hai câu còn có thể, gặp phải loại này thường thức tính, cũng rất dễ dàng lòi."),t("br"),t("br"),n._v(" “Không thích? Không có gì nghiên cứu?” "),t("br"),t("br"),n._v(" Lưu Lăng, Trang Hiền mấy người hai mặt nhìn nhau, sắc mặt càng thêm quái lạ."),t("br"),t("br"),n._v(" Chỉ cần đi qua nhất đẳng Vương quốc, coi như là người điếc, người mù, đều biết một ít, huống chi cấp bậc cao danh sư."),t("br"),t("br"),n._v(" Lại nói, danh sư hữu giáo vô loại, còn chưa từng nghe nói bởi vì không thích độc sư nghề nghiệp này, mà không nghiên cứu..."),t("br"),t("br"),n._v(" Rõ ràng chính là người thường lời nói."),t("br"),t("br"),n._v(" Nếu không là trước đối phương cho bọn họ chỉ điểm, biết người này đối với tu vi kiến giải, căn bản không sánh được, cũng hoài nghi có phải là tại ngụy trang, trên thực tế căn bản không phải cái gì danh sư."),t("br"),t("br"),n._v(" “Các ngươi ai có loại này dược?” "),t("br"),t("br"),n._v(" Thấy sắc mặt của mọi người, Trương Huyền biết chắc là nơi nào nói lòi, bất quá hắn da mặt dày, xem như là giữ được bình tĩnh, lập tức nói sang chuyện khác."),t("br"),t("br"),n._v(" “Loại này thuốc, nhất định phải độc sư tại chỗ bố trí, không thể bảo tồn, bằng không, một khi một loại thuốc xuất hiện biến hóa, liền có thể có thể xuất hiện dị biến, dẫn đến cân bằng phá hoại, do đó biến thành kịch độc... Chúng ta làm sao có khả năng có vật này!” "),t("br"),t("br"),n._v(" Lưu Lăng lắc đầu, trong mắt nghi hoặc càng ngày càng đậm."),t("br"),t("br"),n._v(" Vị này Dương sư mà nói... Cũng quá Tiểu Bạch rồi đi!"),t("br"),t("br"),n._v(" Chỉ cần là danh sư, cũng không thể hỏi như vậy."),t("br"),t("br"),n._v(" Trang Hiền, Trịnh Phi cũng bốc lên ý tưởng giống nhau, thời khắc này, bọn họ lần thứ hai trong lòng sinh nghi, vị này Dương sư có phải là thật hay không."),t("br"),t("br"),n._v(" “Như vậy a...” Biết đối phương hoài nghi, Trương Huyền biết nói càng nhiều vượt phiền phức, không ở băn khoăn, nhìn về phía cách đó không xa Thẩm Truy bệ hạ: “Nếu cần độc sư bố trí, liền phiền phức bệ hạ tìm người bào chế đi!” "),t("br"),t("br"),n._v(" Nói đến đây, dừng lại một chút, Trương Huyền không để ý chút nào ánh mắt của mọi người, mang theo danh sư đặc hữu uy nghiêm và khí chất: “Ta cần loại nước thuốc này, tới cứu trị nhà các ngươi lão tổ!” "),t("br"),t("br"),n._v(" “Cái này...” "),t("br"),t("br"),n._v(" Lần này, không những ba sư kỳ quái, liền ngay cả Thẩm Truy bệ hạ cũng sung thật kỳ quái nhìn sang: “Chúng ta Thiên Huyền Vương quốc, chỉ là bất nhập lưu Vương quốc, không có đẳng cấp... Căn bản không có độc sư nghề nghiệp này... Dương sư, không thể nào không biết chứ?” "),t("br"),t("br"),n._v(" “Không có?” "),t("br"),t("br"),n._v(" Trương Huyền trong lòng “Hồi hộp!” Một hồi."),t("br"),t("br"),n._v(" (Chương thứ tư đến, lão nhai tiếp tục viết, khoảng cách gấp đôi vé tháng kết thúc chỉ còn dư lại bảy cái nửa giờ, gấp đôi thời điểm, vé tháng hơn nhiều, chúng ta liền có thể bảo vệ tổng bảng mười vị trí đầu vị trí, chỉ cần vé tháng nhiều, ngày hôm nay nhất định sẽ không chỉ canh năm! Sáu lại, bảy lại đều có khả năng, lão nhai gõ chữ, vé tháng liền xin nhờ chư vị, hi vọng theo phòng gian nhỏ đi ra, sẽ có rất lớn tăng cường, mà không phải là bị người khác vượt kéo dài càng xa!) (Chưa xong còn tiếp.) (83 tiếng Trung lưới) "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-192"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-194"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);