(window.webpackJsonp=window.webpackJsonp||[]).push([[105],{458:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1071-đoi-chuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1071-đoi-chuong"}},[n._v("#")]),n._v(" Chương 1071: Đối chưởng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1071: Đối chưởng"),t("br"),t("br"),n._v("“Ừm, bộ chưởng pháp này gọi đại bi Thiên Ma chưởng!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ngươi... Có thể nhìn ra chưởng pháp tên?”"),t("br"),t("br"),n._v("Tượng đá trừng mắt."),t("br"),t("br"),n._v("Hắn đem đủ loại văn tự, dùng phù văn phương thức lưu tại trên tấm bia đá, nhưng mà ghi chép pháp quyết tu luyện bên trong vận chuyển chân khí lộ tuyến, cũng không viết ra công pháp danh xưng, kẻ trước mắt này... Làm sao nhìn ra được?"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, lông mày nhịn không được nhảy lên hai lần."),t("br"),t("br"),n._v("Công pháp tên là thư viện “Nhìn” đi ra, đơn thuần nhìn bia đá tu luyện, chỉ biết là là một cái chưởng pháp, cụ thể tên khẳng định là không thể nào biết được."),t("br"),t("br"),n._v("“Ta xem qua liên quan tới tiền bối ghi chép, biết tiền bối biết một bộ thất truyền chưởng pháp, uy lực vô song, mạnh mẽ vô cùng, cùng cái này có chút tương tự, cho nên phỏng đoán...” Trương Huyền nói."),t("br"),t("br"),n._v("Dù sao Thiên Đạo thư viện, hắn là không biết nói, còn đối phương có tin hay không, liền không quản được nhiều như vậy."),t("br"),t("br"),n._v("“Nhìn qua ghi chép?” Tượng đá sững sờ."),t("br"),t("br"),n._v("Vô Cương giới muốn thu ta làm đồ đệ thời điểm, thế nào không thấy ngươi xem qua liên quan tới ta thư tịch?"),t("br"),t("br"),n._v("Bất quá, thứ này cẩn thận hỏi cũng không có ý nghĩa gì, tượng đá nhịn không được lần nữa xem ra: “Ngươi nói ngươi không chỉ nhìn ra chưởng pháp, còn tu luyện một hồi?”"),t("br"),t("br"),n._v("“Vâng!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Có thể nhìn ra công pháp, coi như ngươi thông qua, trực tiếp tu luyện... Lỗ mãng, quá lỗ mãng!”"),t("br"),t("br"),n._v("Tượng đá lắc đầu."),t("br"),t("br"),n._v("Bộ công pháp này là năm đó hắn tự tay sáng chế, biết độ khó."),t("br"),t("br"),n._v("Coi như trong đám người thực lực mạnh nhất cái kia Thánh vực tứ trọng đỉnh phong, muốn tu luyện, chỉ sợ đều sẽ lực có không bằng, một cái Tòng Thánh, liền nghĩ đi thử, không phải từ tìm phiền toái ư?"),t("br"),t("br"),n._v("“Bộ chưởng pháp này, đạt đến thánh phẩm trung cấp, tu vi không đủ, gượng ép tu luyện sẽ chỉ mang đến tổn thương! Hơn nữa, coi như tu vi đủ rồi, muốn luyện thành, cũng rất khó! Lúc trước ta sáng chế pháp quyết, chỉ riêng nhập môn liền tu luyện tròn một năm linh ba tháng, tinh thông càng là hao phí ba năm lâu, còn tiểu thành... Tiếp cận tám năm! Của ta rất nhiều đệ tử, tu vi nhanh nhất đi đến tiểu thành, cũng dùng bốn mươi năm!”"),t("br"),t("br"),n._v("Nhớ lại chuyện cũ, tượng đá nói."),t("br"),t("br"),n._v("Bộ chưởng pháp này mặc dù là hắn sáng tạo, tu luyện, nhưng cũng không dễ dàng như vậy, theo bắt đầu tu luyện tới tiểu thành, hao phí tới tận mười hai năm lâu."),t("br"),t("br"),n._v("Hắn rất nhiều học sinh, thì tốc độ tiến bộ chậm hơn, không đáng giá nhắc tới."),t("br"),t("br"),n._v("“Ngươi hẳn là tu luyện một hồi, cảm thấy khó mà luyện thành, không tiếp tục nữa chứ? Không tiếp tục là được rồi, không có chỉ điểm của ta, mù quáng tu luyện, sẽ chỉ đi nhầm vào lạc lối...”"),t("br"),t("br"),n._v("Cảm khái một câu, tượng đá lần nữa xem ra, còn không có đem nói cho hết lời, liền thấy phía trước thanh niên mãn thị ngượng ngùng vò đầu: “Ta mới vừa mới tu luyện một hồi... Không cẩn thận, liền tiểu thành...”"),t("br"),t("br"),n._v("“Tiểu thành? Không cẩn thận?”"),t("br"),t("br"),n._v("Tượng đá nhoáng một cái, suýt chút nữa nứt toác."),t("br"),t("br"),n._v("Hắn vừa mới nói bản thân vị này người khai sáng, tu luyện tới tiểu thành đều hao tốn hơn mười hai năm... Ngươi mới tu luyện bao lâu?"),t("br"),t("br"),n._v("Tính toán đâu ra đấy, đều không có ba canh giờ chứ?"),t("br"),t("br"),n._v("Tiểu thành?"),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("“Đúng vậy a, kỳ thật... Bộ chưởng pháp này mặc dù rất tốt, chính là thiếu hụt nhiều lắm, tu luyện rất khó chịu...” Trương Huyền cảm khái."),t("br"),t("br"),n._v("“Ngươi!”"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe xong tượng đá suýt chút nữa tức giận ngất đi."),t("br"),t("br"),n._v("Vô Cương giới cái tên này chính là như vậy nói, kết quả, đem bản thân cái kia đạo ý niệm tức giận tiêu tán, đến nơi này... Lại tới!"),t("br"),t("br"),n._v("Ta nợ ngươi làm gì?"),t("br"),t("br"),n._v("Tu luyện khó chịu đều tiểu thành, ngươi còn muốn làm gì?"),t("br"),t("br"),n._v("“Ngươi tu luyện tới tiểu thành đúng không? Ta bộ chưởng pháp này, có thể theo chưởng lực lớn nhỏ, kiểm nghiệm ra tu luyện đúng đắn hay không, lĩnh ngộ cao thâm!”"),t("br"),t("br"),n._v("Trong lòng không quá cao hứng, tượng đá lạnh lùng nhìn lại: “Như vậy đi, ta đem thực lực áp chế đến Tòng Thánh cấp bậc, đồng thời thi triển tiểu thành cấp bậc đại bi Thiên Ma chưởng, cùng ngươi đối chiêu... Có thể tiếp ở của ta chưởng lực, đã nói lên ngươi không có nói dối, quả thực lĩnh ngộ chiêu này tinh túy!”"),t("br"),t("br"),n._v("Bất kể là ai, liên tục bị một cái chỉ có Tòng Thánh tiểu tử nghi ngờ, cũng sẽ cảm thấy rất không cao hứng."),t("br"),t("br"),n._v("Trước mắt vị này, lại nhiều lần nói như vậy, hắn đã quyết định muốn dạy dỗ một bận, để hắn biết trời cao đất rộng."),t("br"),t("br"),n._v("Nếu không, người trẻ tuổi quá tùy tiện, không áp chế một chút, đối sau này trưởng thành bất lợi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được đối phương muốn cùng hắn đối chưởng, Trương Huyền sửng sốt một chút: “Ngươi là tượng đá, có thể thi triển chưởng pháp?”"),t("br"),t("br"),n._v("“Đương nhiên! Mặc dù ta là tượng đá, nhưng mà ý niệm lưu giữ trong đó, phát huy ra thực lực cường đại, còn có thể làm được!”"),t("br"),t("br"),n._v("Hừ một tiếng, tượng đá nhoáng một cái, cánh tay duỗi tới."),t("br"),t("br"),n._v("Một cỗ vô địch khí tức, lập tức bay lên, cho người ta một loại trên linh hồn nghiền ép."),t("br"),t("br"),n._v("“Khưu Ngô cổ thánh năm đó không đi theo Khổng sư trước đó, liền tên truyền thiên hạ, là nhất đẳng cao thủ, cùng cấp bậc vô địch... Áp chế tu vi, cùng Trương sư đối chưởng, Trương sư đoán chừng phải ăn thiệt thòi đi!”"),t("br"),t("br"),n._v("“Cái kia là khẳng định, bộ chưởng pháp này là cổ thánh sáng tạo, tu luyện không biết bao nhiêu năm, Trương sư mặc dù thoạt nhìn luyện ba canh giờ, kỳ thật trước đó hơn hai canh giờ, cũng không lĩnh ngộ, nhiều nhất chỉ luyện một canh giờ mà thôi... Thời gian ngắn như vậy, coi như thiên tư trác tuyệt, lại có thể lĩnh ngộ bao nhiêu?”"),t("br"),t("br"),n._v("“Đúng vậy a, căn bản cũng không tại một cái cấp bậc bên trên...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Ngô sư đám người lắc đầu."),t("br"),t("br"),n._v("Theo bọn hắn nghĩ, Khưu Ngô cổ thánh ra tay, là đối Trương Huyền khảo nghiệm, cả hai rễ bản không thể so sánh."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),n._v(" Thấy đối phương thái độ kiên quyết, Trương Huyền chỉ thật nhẹ gật đầu: “Được!”"),t("br"),t("br"),n._v("Nói thật, hắn vừa vặn nhưng mà tại thể nội dùng chân khí thôi diễn một lần, cũng không thi triển qua, đã đối phương muốn tỷ thí, mới vừa dễ dàng kiểm tra một chút uy lực như thế nào, nếu quả như thật không sai, về sau thời điểm chiến đấu, cũng là có thể sử dụng."),t("br"),t("br"),n._v("“Vậy thì bắt đầu đi!”"),t("br"),t("br"),n._v("Tượng đá lên tiếng, cánh tay đẩy về phía trước ra."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Còn chưa tới từng tới gần, liền cho người ta một loại hủy diệt cảm giác, tựa như vô số Thiên Ma từ trên trời giáng xuống, không chỉ nghiền ép thân thể, đối tâm linh cũng có rung động thật lớn."),t("br"),t("br"),n._v("Đại bi Thiên Ma chưởng, không chỉ có vật chất công kích, đối linh hồn cùng nguyên thần cũng có công kích tăng thêm hiệu quả."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, tại thượng cổ lúc, đều danh khí cực lớn, tại thánh phẩm trung cấp công pháp bên trong, đều tuyệt đối được cho khá cao."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("“Khoảng cách xa như vậy, hơn nữa công kích người không phải ta, ta đều cảm thấy thân thể cứng ngắc, không cách nào đối kháng!”"),t("br"),t("br"),n._v("“Đúng vậy a, không hổ là Khưu Ngô cổ thánh lưu lại công pháp, nếu như có thể học được, thực lực của ta, tuyệt đối sẽ bạo tăng không chỉ một lần!”"),t("br"),t("br"),n._v("“Bộ chưởng pháp này, liên quan trên linh hồn lực lượng, không phải tốt như vậy tu luyện...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cảm nhận được cái này chưởng uy lực, Ngô sư đám người tất cả đều thân thể cứng ngắc, vẻ mặt lúc xanh lúc đỏ."),t("br"),t("br"),n._v("Quá mạnh!"),t("br"),t("br"),n._v("Tượng đá thi triển chiêu này tốc độ mặc dù không nhanh, chạy theo làm nhìn, cũng không có bất kỳ cái gì tinh diệu, nhưng cho người ta một loại khó mà đối kháng, thậm chí linh hồn đều sẽ bị nghiền ép cảm giác."),t("br"),t("br"),n._v("Đây là bọn họ tu luyện rất nhiều võ kỹ, đều làm không được."),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("“Khó trách cổ thánh nói khó mà tu luyện, chiêu này quả thực không dễ dàng, không biết Trương sư tu luyện đến mấy phần...” Ngô sư cảm khái."),t("br"),t("br"),n._v("Bộ chưởng pháp này, đã thoát ly đơn thuần chiêu số, coi trọng chính là tinh khí thần, đi đến một loại cảnh giới, không có nhiều năm tích lũy, là không cách nào hoàn thành."),t("br"),t("br"),n._v("Trương sư cứ việc thiên phú rất cao, dù sao tuổi tác quá nhỏ, lịch duyệt xã hội ở nơi đó bày biện, có lẽ không lĩnh ngộ được chân chính tinh túy chứ?"),t("br"),t("br"),n._v("Rất nhiều người tu luyện đi lại thế gian, lĩnh ngộ hỉ nộ ái ố, chính là để tâm cảnh càng thêm viên mãn, không có loại này rèn luyện, coi như thiên phú lại cao hơn, cũng rất khó lý giải loại vũ kỹ này tinh túy."),t("br"),t("br"),n._v("Muốn đến nơi này, nhịn không được hướng cách đó không xa thanh niên nhìn sang."),t("br"),t("br"),n._v("Chỉ thấy lúc này Trương Huyền, cũng không gấp gáp ra tay, mà là hai tay chắp sau lưng, nhìn xéo bốn mươi lăm độ, không biết nghĩ tới chuyện gì, lộ ra bi thương cảm xúc."),t("br"),t("br"),n._v("Hình như đã mất đi người yêu, lại hình như tận mắt thấy thân bằng qua đời, cho người ta một loại di thế độc lập, tràn đầy cô độc cảm giác."),t("br"),t("br"),n._v("“Đây là... Kinh hồng sư năng lực!”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Ngô sư nhịn không được kinh hô."),t("br"),t("br"),n._v("Đối phương mặc dù chỉ là một cái thế đứng, nhưng cho người ta một loại, sâu tận xương tủy bi thương... Cái này không riêng gì tâm cảnh, càng là một loại trên linh hồn kỹ xảo."),t("br"),t("br"),n._v("“Kinh hồng sư? Đây không phải là chỉ có khiêu vũ mới có thể thi triển ra công kích chức nghiệp ư?”"),t("br"),t("br"),n._v("Phùng Huân không hiểu."),t("br"),t("br"),n._v("Kinh hồng sư, tại trong ấn tượng của hắn, là mặc lộng lẫy trang phục, thông qua đủ loại động tác, để cho người ta trầm mê đặc thù chức nghiệp, bình thường đều là nữ tử tu luyện... Làm sao vị này Trương sư cũng tu luyện?"),t("br"),t("br"),n._v("Hơn nữa, hắn cũng không thay quần áo, nhưng mà ngơ ngác đứng tại chỗ, làm sao lại là kinh hồng sư năng lực?"),t("br"),t("br"),n._v("“Kinh hồng sư là cần khiêu vũ mới có thể công kích, nhưng... Nghe nói đối hắn lý giải đến cảnh giới nhất định, nhất cử nhất động tự mang thần vận, để người vì đó mê muội... Trương sư, mặc dù không nhúc nhích, nhưng mà chỉ cần ngươi nhìn kỹ, liền sẽ kìm lòng không được cảm nhận được tâm cảnh của hắn, vì đó cộng minh...”"),t("br"),t("br"),n._v("Ngô sư giải thích một câu, tiện tay một chỉ: “Ngươi xem bọn hắn...”"),t("br"),t("br"),n._v("Phùng Huân quay đầu nhìn lại, liền thấy phía sau mình rất nhiều nhìn về phía Trương Huyền Chiến sư, tất cả đều bị cảm xúc lây nhiễm, lệ rơi đầy mặt."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Phùng Huân giật nảy mình."),t("br"),t("br"),n._v("Hắn vốn cho rằng, vị này Trương Huyền, nhưng mà trận pháp mạnh, sức chiến đấu cũng không yếu, nằm mơ đều không nghĩ tới, đối linh hồn công kích cũng đáng sợ như vậy."),t("br"),t("br"),n._v("Sau lưng những này Chiến sư, mặc dù đều là Thánh vực nhất trọng, nhị trọng, nhưng đều có thể vượt cấp chiến đấu, thực lực không tầm thường, cũng đều tiếp thụ qua linh hồn phương diện đơn độc huấn luyện, chỉ nhìn thoáng qua hắn đứng tại chỗ động tác, liền bị lây nhiễm, phần này năng lực, liền xem như hắn, đều cảm thấy tràn đầy sợ hãi."),t("br"),t("br"),n._v("“Mau nhìn, bắt đầu...”"),t("br"),t("br"),n._v("Chính đang khiếp sợ, liền nghe đến Ngô sư tiếng la vang lên lần nữa, vội vàng nhìn lại, chỉ thấy Trương sư ảm đạm một hồi, bàn tay nhẹ nhàng lật ra, đối tượng đá bàn tay nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Động tác của hắn đồng dạng không nhanh, cũng không có bất kỳ cái gì xa hoa động tác, nhưng mà đơn giản đẩy ngang, nhưng đồng dạng cho người ta một loại dời sông lấp biển cảm giác."),t("br"),t("br"),n._v("Trong nháy mắt, thật giống như trước mắt Trương sư, hóa thân thành to lớn Thiên Ma, linh hồn đều hứng chịu tới nghiền ép, không dám cùng chi mạnh mẽ chống đỡ."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Cảm nhận được khí thế của hắn cùng chưởng lực biến hóa, đối diện tượng đá, cũng theo trước đó coi thường trở nên ngưng trọng lên, nhíu mày một cái, bàn tay tiến công tốc độ đột nhiên tăng nhanh."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Hai bàn tay đối đụng nhau, lực lượng đột nhiên nổ tung, khí lãng khổng lồ nhanh chóng xông ra."),t("br"),t("br"),n._v("Sưu!"),t("br"),t("br"),n._v("Tượng đá còn không có kịp phản ứng, thoáng cái lăng không mà lên, thẳng tắp bay rớt ra ngoài, đâm vào một cái trên tấm bia đá, ném chia năm xẻ bảy."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Đồng dạng thực lực, đồng dạng chưởng pháp, đồng dạng cấp bậc... Trương sư một chiêu liền đem Khưu Ngô cổ thánh đánh bại?”"),t("br"),t("br"),n._v("Tất cả mọi người trợn mắt hốc mồm."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1079"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1081"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);