(window.webpackJsonp=window.webpackJsonp||[]).push([[1050],{1406:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1918-co-trung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1918-co-trung"}},[n._v("#")]),n._v(" Chương 1918: Cổ trùng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không chỉ hắn mê man, một bên Dịch lão cũng khóe miệng co giật, cũng lại kìm nén không được: “Trương công tử... Đừng đùa!”"),t("br"),t("br"),n._v("Đã sống chết trước mắt, còn tại nói đùa, sớm biết người này như vậy không đáng tin cậy, liền không cho thiếu gia cứu!"),t("br"),t("br"),n._v("Nhìn thấy hai người biểu lộ, Trương Huyền biết, đối phương khẳng định là không tin, hai tay chắp sau lưng, muốn biểu hiện cao nhân khí độ, kết quả xé rách vết thương, khóe miệng nhịn không được co lại, suýt chút nữa té ngã trên đất."),t("br"),t("br"),n._v("Đình chỉ hoạt động, cố nén đau đớn: “Trong cơ thể ngươi kinh mạch, bởi vì khi còn bé gây thương tích, không cách nào vận chuyển chân khí, đan điền cũng nhận phá hoại, tích góp không được lực lượng, cho nên, mới không cách nào tu luyện! Không biết ta nói đúng hay không?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên gật đầu."),t("br"),t("br"),n._v("Sáu tuổi năm đó, gia tộc gặp biến cố, đan điền, kinh mạch bị lực lượng chấn vỡ, cho tới bây giờ, đều không thể khôi phục, cho nên, tuy là mỗi ngày luyện kiếm, trên thực tế, lực lượng không có bất kỳ cái gì tiến bộ, tất cả chiêu số, đều chỉ là cuốn hút giả kỹ năng thôi."),t("br"),t("br"),n._v("Tìm không biết bao nhiêu danh y nhìn qua, đều nói không có cách nào trị liệu, dần dà, đã thành quen thuộc."),t("br"),t("br"),n._v("“Vậy ngươi có biết, kinh mạch cùng đan điền, là bị cái gì gây thương tích?” Trương Huyền hỏi."),t("br"),t("br"),n._v("“Là một loại lợi hại chân khí, xâm nhập thân thể, có thể còn sống sót, đã rất vui mừng...” Thiện Hiểu Thiên nói."),t("br"),t("br"),n._v("Những năm này nhiều lần chẩn bệnh, trong cơ thể đến cùng xảy ra chuyện gì, biết rõ rõ ràng ràng."),t("br"),t("br"),n._v("“Chân khí?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Nếu như chỉ là chân khí, ngươi mỗi ngày đều kiên trì tu luyện, hi vọng có một ngày, có thể cải thiện tình huống trong cơ thể, có thể nhiều năm như vậy, vì sao không có chút nào thay đổi?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên nói không ra lời."),t("br"),t("br"),n._v("Thật ra thì, đây cũng là hắn kỳ quái."),t("br"),t("br"),n._v("Mười năm qua, đủ loại danh y, đều nhìn qua, dược tề, đan dược, châm cứu, tắm thuốc... Đủ loại thủ đoạn dùng không biết bao nhiêu, nhưng không có mảy may tác dụng, thân thể của hắn liền giống như phá bao tải đồng dạng, linh khí tiến vào bên trong, sớm muộn đều sẽ tản mất, căn bản là không có cách tích góp."),t("br"),t("br"),n._v("Dựa theo không ít danh y lời nói, thật sự là chân khí phá hủy thân thể, như vậy trị liệu, phải có thể chữa trị, có thể mảy may đều không có thấy hiệu quả... Để rất nhiều người, đều cho rằng khó tin."),t("br"),t("br"),n._v("“Nếu như không phải thật sự khí xâm nhập, cái kia lại là cái gì đồ vật phá hủy kinh mạch cùng đan điền, mà sẽ không tử vong? Thậm chí bình thường sinh hoạt, đều không bị ảnh hưởng?”"),t("br"),t("br"),n._v("Trầm mặc phút chốc, Thiện Hiểu Thiên nhìn qua."),t("br"),t("br"),n._v("Thương thế của hắn, loại trừ không thể tu luyện, cái khác một chút ảnh hưởng đều không có, đối phương cái gọi là không thể việc người, là giả, mỗi sáng sớm, đều có thể nhất trụ kình thiên, uy vũ bất phàm."),t("br"),t("br"),n._v("“Là cổ trùng!” Trương Huyền nói: “Ngươi bị người hạ loại vật này, mới đưa đến bất kỳ dược vật vô dụng!”"),t("br"),t("br"),n._v("“Cổ trùng?” Thiện Hiểu Thiên khó tin: “Ngươi ý tứ... Trong cơ thể ta cái này côn trùng, còn sống?”"),t("br"),t("br"),n._v("“Không chỉ sống... Hơn nữa những năm này không ngừng hấp thu, ngươi tu luyện mà ra lực lượng, đã trở nên thập phần cường đại!” Trương Huyền nói."),t("br"),t("br"),n._v("Đi qua thư viện, hắn đã nhìn ra đối phương mấu chốt căn bản nguyên nhân."),t("br"),t("br"),n._v("Cũng không phải là thương tổn tới căn cơ, mà là bị người hạ cổ trùng, hơn nữa đầu này cổ trùng, không ngừng từng bước xâm chiếm hắn tu luyện ra được lực lượng, lúc này mới dẫn đến, mười năm tu luyện, không có tấc công."),t("br"),t("br"),n._v("“Sống cổ trùng...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên cùng một bên Dịch lão liếc mắt nhìn nhau, trong mắt ngạc nhiên."),t("br"),t("br"),n._v("Trước mắt vị này nói thực sự quá mức kinh dị, để cho bọn họ khó tin."),t("br"),t("br"),n._v("Trong cơ thể lại có cổ trùng, hơn nữa đợi mười năm... Nhiều như vậy danh y đều không có điều tra ra, trước mắt vị này, làm sao nhìn ra được?"),t("br"),t("br"),n._v("“Ta biết ngươi không tin, có điều, có thể hiện tại chứng minh!”"),t("br"),t("br"),n._v("Nhìn hai người biểu lộ biết, khẳng định không tin, Trương Huyền cười cười, nói: “Không biết nơi này nhưng có châm bạc các loại đồ vật?”"),t("br"),t("br"),n._v("“Có!”"),t("br"),t("br"),n._v("Dịch lão gật gật đầu, xoay người đi vào gian phòng, thời gian không dài, lấy tới một hộp châm bạc."),t("br"),t("br"),n._v("Bệnh lâu thành y, thiếu gia bệnh, ngày ngày trị liệu, những này chuẩn bị sẵn đồ vật, đều có thể tìm tới."),t("br"),t("br"),n._v("“Ừm, ta hiện tại, liền giúp ngươi đưa nó lấy ra tới...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền tiện tay nhặt lên một cái châm bạc, cong ngón tay búng một cái."),t("br"),t("br"),n._v("Đinh!"),t("br"),t("br"),n._v("Châm bạc rơi tại cách hắn không đủ ba mét trên mặt đất."),t("br"),t("br"),n._v("“Khụ khụ, ta hiện tại không còn khí lực, ngươi tới gần một ít...”"),t("br"),t("br"),n._v("Thấy bốn phía yên tĩnh đồng dạng, Trương Huyền khó xử."),t("br"),t("br"),n._v("Quen thuộc cách không bắn châm, mới nghĩ tới, chân khí mảy may cũng không thể sử dụng, hắn giờ phút này cùng phế nhân không có gì khác biệt."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Tuy là cho rằng trước mắt vị này không quá đáng tin cậy, Thiện Hiểu Thiên chần chờ một chút, vẫn là đi tới."),t("br"),t("br"),n._v("Dù sao đều như vậy, lấy ngựa chết làm ngựa sống đi."),t("br"),t("br"),n._v("Lần nữa lấy ra một cái châm bạc, Trương Huyền hít sâu một hơi, dựa theo thư viện ghi lại vị trí, bỗng nhiên đã đâm tới."),t("br"),t("br"),n._v("Đinh!"),t("br"),t("br"),n._v("Khóe miệng giật một cái."),t("br"),t("br"),n._v("Không còn khí lực hắn, châm bạc vậy mà không có đâm thủng da của đối phương."),t("br"),t("br"),n._v("Thiện Hiểu Thiên cứ việc không cách nào tu luyện, nhưng chân chính tu vi, cũng Thánh vực lục trọng, loại người này quanh năm nhận thủy ngân linh khí tẩm bổ, thân thể đã rèn luyện so sắt thép đều muốn cứng rắn, tuyệt không phải hắn có thể tuỳ tiện đâm thủng qua."),t("br"),t("br"),n._v("“Phiền phức giúp một chút!”"),t("br"),t("br"),n._v("Trương Huyền quay đầu."),t("br"),t("br"),n._v("Một bên Dịch lão đi tới, ngón tay một chút, một đạo chân khí đưa vào châm bạc, thời gian nháy mắt, uốn lượn lên châm nhỏ, trở nên thẳng tắp, “Vèo!” Một chút, đâm vào Thiện Hiểu Thiên trong cơ thể."),t("br"),n._v(" Bành!"),t("br"),t("br"),n._v("Trương Huyền vừa định hướng về phía trước khống chế châm bạc, liền phát hiện, thứ này lấy tốc độ nhanh hơn, bay ngược đi ra, trực tiếp cắm vào trên mặt tường, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("Thiện Hiểu Thiên và Dịch lão đồng thời con mắt trợn tròn, đặc biệt là cái trước, tràn đầy khó tin."),t("br"),t("br"),n._v("Thân là người trong cuộc, hắn biết mình không vận dụng bất kỳ lực lượng nào, có thể châm bạc nhưng lấy tốc độ nhanh như vậy bay ra, chẳng lẽ... Trong cơ thể hắn thật sự có những vật khác tồn tại?"),t("br"),t("br"),n._v("“Ngươi phải cảm nhận được, là cổ trùng đem châm bạc đẩy mà ra...” Trương Huyền giải thích."),t("br"),t("br"),n._v("Lấy hắn lực lượng bây giờ, đánh giết cổ trùng khẳng định là không làm được, có điều, cổ trùng phản kích một lần, cũng có thể làm cho đối phương rõ ràng cảm nhận được, thứ này tồn tại."),t("br"),t("br"),n._v("“Thật sự có cổ trùng?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên nói không ra lời."),t("br"),t("br"),n._v("Muốn nói trước đó, không có chút nào tin tưởng, giờ phút này, có chút dao động."),t("br"),t("br"),n._v("Đừng nói hắn không có thi triển lực lượng, coi như thi triển, cũng tuyệt không có khả năng đem như vậy mảnh khảnh châm bạc, gai ngược vào trong vách đá, dấu vết cũng không còn lại."),t("br"),t("br"),n._v("“Vừa rồi đâm một cái, mặc dù không có làm bị thương mảy may, nhưng tương đương phát hiện vị trí của nó, không có gì bất ngờ xảy ra, thứ này sẽ lần nữa tìm một cái gia viên mới, mà nó di động thời điểm, ngươi sẽ cảm thấy chân khí lực lượng, bị thương chân, cũng có thể được giảm bớt!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Đương nhiên, đây chỉ là ngắn ngủi, chờ nó dừng lại, còn có thể cùng trước kia đồng dạng, không có bất kỳ cái gì thay đổi, duy nhất phương pháp, chính là nghĩ biện pháp đem nó đánh giết!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên có chút không dám tin tưởng, có điều, thời gian không dài, con mắt lập tức trợn tròn, một mặt vẻ mặt như gặp phải quỷ."),t("br"),t("br"),n._v("Bởi vì, trong cơ thể của hắn cùng đối phương nói đồng dạng, quả nhiên cảm nhận được chân khí di động, ngay sau đó, một mực mất cảm giác chân trái, hình như cũng có cảm giác."),t("br"),t("br"),n._v("Nhịn không được đi về phía trước mấy bước, vậy mà cùng người bình thường đồng dạng, không có chút nào trắc trở."),t("br"),t("br"),n._v("Chỉ là loại cảm giác này, chỉ duy trì hơn mười hô hấp, liền ngừng lại, chân lần nữa trở nên cùng gỗ đồng dạng."),t("br"),t("br"),n._v("“Lần này, phải tin tưởng đi...”"),t("br"),t("br"),n._v("Gặp hắn bộ dáng này, Trương Huyền biết mình chẩn bệnh không có sai lầm chút nào, nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Đồ nhi bái kiến lão sư!”"),t("br"),t("br"),n._v("Lại không do dự, Thiện Hiểu Thiên quỳ mọp xuống đất."),t("br"),t("br"),n._v("Nhiều như vậy danh y nhìn qua, không có bất kỳ cái gì hiệu quả, trước mắt vị này, chỉ nhìn liếc mắt, thậm chí đều không có tới gần, tìm ra chỗ mấu chốt, tất cả đều cùng nói giống nhau như đúc, chỉ bằng vào điểm ấy, chỉ biết, là có bản lĩnh lớn người."),t("br"),t("br"),n._v("“Đứng lên đi!”"),t("br"),t("br"),n._v("Đợi đối phương chính thức đi qua lễ bái sư phía sau, Trương Huyền đem nó đỡ dậy."),t("br"),t("br"),n._v("Thu đối phương làm đồ đệ, cũng coi là báo đáp ân cứu mạng."),t("br"),t("br"),n._v("“Lão sư... Không biết đầu này cổ trùng, như thế nào giải quyết?”"),t("br"),t("br"),n._v("Bái sư xong xuôi, Thiện Hiểu Thiên nhìn lại."),t("br"),t("br"),n._v("Trước đó không biết, vẫn không cảm giác được đến cái gì, giờ phút này biết trong cơ thể vậy mà mọc ra một đầu côn trùng, cũng lại không chịu nổi, nếu như không nhanh chóng diệt trừ mà nói, có thể sẽ ăn ngủ không yên."),t("br"),t("br"),n._v("“Chỉ cần ta thương thế khôi phục, giải quyết rất dễ dàng...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Nếu như không phải bị thương nặng, Thiên đạo chân khí tràn vào đối phương trong cơ thể, cái gọi là cổ trùng, tuyệt đối không kiên trì được một cái hô hấp, nhưng bây giờ, chân khí không thi triển ra được, biết rõ thứ này vị trí, cũng giết không được."),t("br"),t("br"),n._v("Then chốt, đối phương còn rất đắc ý đem châm bạc bắn ra, quả thực ngay tại khiêu chiến ranh giới cuối cùng của hắn."),t("br"),t("br"),n._v("“Khôi phục thương thế?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Thiện Hiểu Thiên nói: “Cái này đơn giản, ta chỗ này có không ít trân quý thuốc chữa thương, vốn là định cho ngươi dùng, chỉ bất quá không có tỉnh, không dám sử dụng, hiện tại vừa vặn nuốt, chỉ cần ở trong người tan ra, phải không bao lâu, liền có thể khôi phục hơn phân nửa!”"),t("br"),t("br"),n._v("Lợi hại thuốc chữa thương, dược lực quá mạnh, không thanh tỉnh liền dùng mà nói, ngược lại sẽ hoàn toàn ngược lại, trừ phi có đầy đủ chân khí, hỗ trợ đem dược lực tan ra."),t("br"),t("br"),n._v("Mà hắn, không cách nào tu luyện, cho nên, vẫn luôn không có đi làm."),t("br"),t("br"),n._v("Dịch lão lần nữa đi vào phòng, rất nhanh lấy tới một cái bình ngọc, nhẹ nhàng mở ra, nhất thời một cỗ mùi thuốc nồng nặc, nhẹ nhàng tới."),t("br"),t("br"),n._v("Là cái cấp tám chữa thương đan dược."),t("br"),t("br"),n._v("Trương Huyền nhận lấy, há miệng nuốt xuống, một lát sau, nhịn không được lắc đầu: “Thuốc này, đối ta vô dụng...”"),t("br"),t("br"),n._v("Hắn giờ phút này thoạt nhìn không có thực lực, trên thực tế nhưng là Cổ Thánh tầng thứ tư phá toái hư không cường giả, cấp tám đan dược, đối Thánh vực lục trọng, tuyệt đối là thánh dược chữa thương, đối với hắn mà nói, đã không có bất kỳ chỗ dùng nào."),t("br"),t("br"),n._v("“Vô dụng?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên lại để cho Dịch lão lấy trong nhà thuốc chữa thương tốt nhất vật, kết quả, đều không có quá lớn trợ giúp."),t("br"),t("br"),n._v("“Nơi này nhưng có cái gì cỡ lớn cửa hàng? Có thể mua sắm chữa thương đan dược hoặc là dược liệu?” Trương Huyền nhíu nhíu mày nói."),t("br"),t("br"),n._v("“Cửa hàng là có, bất quá... Bán ra dược liệu cùng đan dược, cấp bậc đều không cao, ta những thuốc này, chính là từ nơi đó mua...” Thiện Hiểu Thiên gãi đầu một cái."),t("br"),t("br"),n._v("Những thuốc này, đối trước mắt vị này một chút tác dụng đều không có, coi như mua nhiều hơn nữa, cũng vô dụng."),t("br"),t("br"),n._v("“Cái kia nơi nào có cấp bậc cao hơn đan dược?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("Thương thế của hắn không khôi phục, liền không cách nào giúp đối phương trị liệu, trị liệu không tốt, đừng nói không kiên trì được ba ngày, tối nay có thể hay không vượt qua, cũng không tốt nói."),t("br"),t("br"),n._v("“Tu luyện giả mua sắm cao hơn đan dược, đều là mượn ! Chỉ cần giao dịch thành công, đồ vật chẳng mấy chốc sẽ đưa tới, sẽ không chậm trễ bao lâu...” Thiện Hiểu Thiên nói."),t("br"),t("br"),n._v("“Thông thần ngọc phù?” Trương Huyền dừng lại một chút, nói: “Chỗ nào có thể tìm được thứ này? Hoặc là, có thể mua được?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1930"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1932"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);