(window.webpackJsonp=window.webpackJsonp||[]).push([[1052],{1407:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1920-nguoi-sai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1920-nguoi-sai"}},[n._v("#")]),n._v(" Chương 1920: Ngươi sai")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("!"),t("br"),t("br"),n._v("“Nhìn là tốt rồi!”"),t("br"),t("br"),n._v("Lần nữa cười một tiếng, Trương Huyền cũng không giải thích, mà là trực tiếp ngẩng đầu nhìn về phía phía trước, hô: “Vị này Lăng Vân kiếm các sư huynh, còn xin dừng bước!”"),t("br"),t("br"),n._v("Tiến lên thanh niên áo xám lông mày nhíu một cái, xoay người nhìn qua."),t("br"),t("br"),n._v("Trương Huyền khóe miệng nâng lên: “Vị sư huynh này, ngươi cho đáp án, cũng không chính xác, liền như thế cầm thông thần ngọc phù rời đi, có sai lầm công bằng a!”"),t("br"),t("br"),n._v("Thanh âm của hắn cực lớn, bốn phía thanh âm lập tức bị đè ép xuống, mọi người cùng xoạt xoạt nhìn qua, giống như là thấy quái vật đồng dạng biểu lộ."),t("br"),t("br"),n._v("Vị này chính là Lăng Vân kiếm các cao thủ, nói thẳng hắn sai..."),t("br"),t("br"),n._v("Không muốn sống ư?"),t("br"),t("br"),n._v("Thanh niên áo xám cũng nhíu nhíu mày, ngừng lại."),t("br"),t("br"),n._v("“Thiện Hiểu Thiên, không muốn chết, liền quản tốt ngươi hạ nhân miệng...”"),t("br"),t("br"),n._v("Khuôn mặt trầm xuống, Tiết Cầm khí sắc mặt phát xanh."),t("br"),t("br"),n._v("Vị này xác ướp, chính là trước đó tại Thiện gia nói nhảm vị kia, bản thân không có chấp nhặt với hắn, không nghĩ tới, còn dám ở đây nói chuyện."),t("br"),t("br"),n._v("“Hắn không phải hạ nhân, là, lão sư ta...” Thiện Hiểu Thiên vừa sốt ruột, dường như lại có chút nói lắp."),t("br"),t("br"),n._v("“Lão sư? Thiện gia bất kể nói thế nào, đều được cho uy phong nhất thời, lúc nào, sa đọa đến cái gì a miêu a cẩu cũng có thể làm lão sư của các ngươi?” Cười nhạo một tiếng, Tiết Cầm tràn đầy khinh thường."),t("br"),t("br"),n._v("“Thiện Hiểu Thiên bái ai là thầy, ngươi hẳn là không xen vào a! Chẳng lẽ... Ngươi còn muốn làm vị hôn thê của hắn?” Trương Huyền cười nhẹ nhàng nhìn qua."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Tiết Cầm thân thể cứng đờ."),t("br"),t("br"),n._v("Thấy nàng biểu lộ, thanh niên áo xám mắt sáng lên, lạnh lùng nhìn về phía trước mắt xác ướp: “Ngươi nói đáp án của ta không chính xác?”"),t("br"),t("br"),n._v("“Không sai!” Trương Huyền nhìn qua: “Có dám đánh cược hay không? Người thua, có thể tùy ý bị xử lý!”"),t("br"),t("br"),n._v("“Cùng ta nói như vậy lời nói, ngươi rất có can đảm...” Thanh niên áo xám mặt không hề cảm xúc nhìn qua: “Ta nghĩ biết, ngươi cái gọi là không chính xác lý do!”"),t("br"),t("br"),n._v("“Không cần lý do! Chỉ cần tìm một vị luyện đan sư, đem những dược liệu này phối tề, dựa theo ngươi vừa cho ra đáp án, luyện chế một lần, cuối cùng thành đan, tìm người nuốt, liền sẽ biết kết quả!”"),t("br"),t("br"),n._v("Trương Huyền nói “Ngươi không tin, thế nào không tìm người đi đường thử một chút?”"),t("br"),t("br"),n._v("“Luyện đan?” Nhíu nhíu mày, thanh niên áo xám nhìn chung quanh một vòng, thấy bốn phía mọi người tuy là không nói gì, nhưng từng cái lộ ra nghi ngờ biểu lộ, ngừng một chút, vẫy tay một cái: “Tìm luyện đan sư tới!”"),t("br"),t("br"),n._v("Sau lưng một thanh niên, xoay người đi ra ngoài, thời gian không dài, cầm thông thần ngọc phù tới trung niên nhân, mang theo hai vị luyện đan sư đi tới."),t("br"),t("br"),n._v("“Dựa theo vừa rồi phương pháp luyện chế a!”"),t("br"),t("br"),n._v("Trung niên nhân dặn dò."),t("br"),t("br"),n._v("Luyện đan sư rất nhanh liền chuẩn bị xong dược liệu, dựa theo phía trên viết luyện đan trình tự, từng loại gia nhập dược vật, thêm đến Chước Hỏa rễ cỏ thời điểm, đổi thành Linh Mẫu thảo."),t("br"),t("br"),n._v("Dược vật vừa tiến vào đan lô, lập tức phát ra “Xì xì” Thanh âm, trong đó một vị luyện đan sư, cũng lại không nắm được, đan lô một hồi kịch liệt lay động, tại chỗ nổ lô."),t("br"),t("br"),n._v("May mắn nói trước có bảo vệ biện pháp, cũng không làm bị thương người đi đường."),t("br"),t("br"),n._v("Chẳng qua, dù vậy, cũng mười phần mạo hiểm, sợ tới mức mọi người toàn bộ lùi về bên tường, từng cái trên mặt vẻ lo lắng."),t("br"),t("br"),n._v("Vị thứ hai luyện đan sư, thực lực mạnh một ít, khống chế được dược vật phản ứng, sau nửa canh giờ, đan dược ra lò, mặt ngoài nhộn nhạo quang mang, thế mà vận khí vô cùng tốt, không chỉ có thành đan, còn đạt đến hoàn mỹ cấp bậc."),t("br"),t("br"),n._v("“Rất tốt!”"),t("br"),t("br"),n._v("Nhìn thấy đan dược, thanh niên áo xám thoả mãn gật gật đầu, xoay người nhìn về phía cách đó không xa một cái khác thanh niên: “Ngươi tu luyện dường như là Thuần Dương công pháp, thử một chút dược lực!”"),t("br"),t("br"),n._v("“Là!”"),t("br"),t("br"),n._v("Há miệng đem đan dược nuốt xuống, dược lực rất nhanh tan ra, vị thanh niên này trên mặt lập tức hiện lên ửng hồng chi sắc, vội vàng khoanh chân ngồi xuống, vận chuyển chân khí muốn đem dược lực hóa giải, ai biết càng áp chế bắn ngược càng lợi hại, thời gian nháy mắt, trên đầu toát ra màu trắng khói mù."),t("br"),t("br"),n._v("Không lâu, con mắt bắt đầu ửng hồng, có chút không chống đỡ được."),t("br"),t("br"),n._v("“Ah... Ta không chịu nổi...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, thanh niên thân thể nhảy lên, vọt tới xem náo nhiệt một vị bác gái trước mặt, đưa tay liền lôi kéo tới."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Quần áo bị xé toang một khối lớn, thanh niên đang muốn xông tới, làm chút không thể miêu tả sự tình, sau gáy tê rần, nhất thời té xỉu trên đất."),t("br"),t("br"),n._v("Lăng Vân kiếm các thanh niên áo xám, xuất thủ."),t("br"),t("br"),n._v("Trước mắt vị này, uống thuốc xong sau biến thành như vậy, đã đủ để chứng minh, hắn cung cấp đan phương, không những không cách nào loại bỏ Bồi Dương đan bên trong tai hoạ ngầm, thậm chí còn ngày càng táo tợn, làm ra càng khó có thể hơn khống chế hiệu quả."),t("br"),t("br"),n._v("[ ngantruyen.com]"),t("br"),n._v("“Ta thay đổi sau phương thuốc, quả thực có chút vấn đề, nhưng... Ngươi thế nào biết được? Đáp án chính xác là cái gì?”"),t("br"),t("br"),n._v("Thanh niên áo xám vẻ mặt khó coi, nhìn lại."),t("br"),t("br"),n._v("Nói thật, cái vấn đề khó khăn này, hắn tới Huyền Giang thành trước đó, liền nghe nói, đặc biệt nghiên cứu rất lâu, cũng không phải là mặt ngoài, liếc mắt nhìn liền giải quyết vấn đề."),t("br"),t("br"),n._v("Sở dĩ làm như vậy, là muốn cho Huyền Giang thành người đi đường, xác lập một cái mạnh mẽ uy danh."),t("br"),t("br"),n._v("Chỉ là không nghĩ tới... Uy danh không có xác lập, ngược lại bị người đi đường ở trước mặt đánh mặt."),t("br"),t("br"),n._v("Tuy là không vui, nhưng cũng không thể nổi giận."),t("br"),t("br"),n._v("Không phải, sẽ chỉ càng mất mặt."),t("br"),t("br"),n._v("“Câu trả lời chính xác, rất đơn giản...” Trương Huyền cười cười, tại Thiện Hiểu Thiên bên tai nói một câu, người sau một mặt kỳ quái nhìn qua, xác nhận không cùng hắn nói đùa, lúc này mới lần nữa đi ra phía trước, gỡ xuống bút lông, rất nhanh viết xong, trao tay đi lên."),t("br"),t("br"),n._v("Đồng dạng hồng quang chiếu rọi."),t("br"),t("br"),n._v("Thanh niên áo xám nhíu nhíu mày: “Chịu trách nhiệm kiểm tra người đi đường ngay ở chỗ này, trực tiếp đọc ra a!”"),t("br"),t("br"),n._v("Hồng nhạn thương hội người phụ trách ngay ở chỗ này, lại đi kiếm đáp án có ý nghĩa gì?"),t("br"),n._v("Không bằng trực tiếp đọc ra, là chính xác vẫn là sai lầm, một biện liền biết."),t("br"),t("br"),n._v("“Là!”"),t("br"),t("br"),n._v("Nhìn lão sư liếc mắt, thấy hắn xác nhận, Thiện Hiểu Thiên lúc này mới một mặt khó xử nói ra vừa rồi viết ra đáp án: “Đem Chước Hỏa rễ cỏ, đổi thành Linh Mẫu thảo!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Ta có nghe lầm hay không?”"),t("br"),t("br"),n._v("“Hình như cùng... Vị cao thủ này đáp án giống nhau như đúc!”"),t("br"),t("br"),n._v("Bốn phía an tĩnh một hồi, nhất thời xôn xao."),t("br"),t("br"),n._v("Thanh niên áo xám con mắt cũng híp lại, trong ánh mắt để lộ ra nồng đậm sát khí: “Ngươi đang đùa ta?”"),t("br"),t("br"),n._v("Lời thề son sắt nói mình sai, kết quả hắn cũng cầm tương đồng đáp án, làm cái gì?"),t("br"),t("br"),n._v("Không chỉ mọi người nổi giận, Thiện Hiểu Thiên cũng trong đầu choáng váng, không biết lão sư trong hồ lô bán cái loại thuốc gì..."),t("br"),t("br"),n._v("Cùng loại này cường giả nói đùa... Nhưng mà sẽ chết người đấy!"),t("br"),t("br"),n._v("“Ngươi cả nghĩ quá rồi...” Trương Huyền lắc đầu, nhìn về phía Thiện Hiểu Thiên: “Đằng sau còn có ghi chú, nói tiếp đi ra ah!”"),t("br"),t("br"),n._v("“A...” Thiện Hiểu Thiên lúc này mới gật gật đầu, nói “Linh Mẫu thảo đằng sau, là dấu ngoặc, không phải bản địa!”"),t("br"),t("br"),n._v("“Không phải bản địa? Có ý tứ gì?”"),t("br"),t("br"),n._v("Lần này tất cả mọi người đều có chút choáng váng, ngay cả thanh niên áo xám cũng có chút không hiểu."),t("br"),t("br"),n._v("Chẳng lẽ, cái này Linh Mẫu thảo bản địa cùng không phải bản địa, còn có khác nhau phải không?"),t("br"),t("br"),n._v("“Đương nhiên là có khác nhau!”"),t("br"),t("br"),n._v("“Cái này Bồi Dương đan, là củng cố Thuần Dương công pháp tu luyện giả, trong cơ thể Thuần Dương thuộc tính, nghĩ biện pháp đưa đến càng thêm củng cố hiệu quả, dưới tình huống bình thường, đem Chước Hỏa rễ cỏ, đổi thành Linh Mẫu thảo, quả thực là thích hợp nhất cách làm, thậm chí, tại cái khác địa phương, như vậy luyện chế đan dược, có thể giải quyết trên vách tường nói tới tai hoạ ngầm!”"),t("br"),t("br"),n._v("Trương Huyền chậm rãi hướng về phía trước, đi tới lô đỉnh trước mặt, ngón tay vê một cái vừa rồi bùng nổ hình thành cặn thuốc, cười nhạt một tiếng: “Nhưng mà... Ở đây không được!”"),t("br"),t("br"),n._v("“Huyền Giang thành, lưng tựa Huyền Giang mà đặt tên! Đầu này sông nối thẳng duyên hải, chảy xuôi cũng không phải là nước ngọt, mà là nước mặn! Là cái thực sự nước mặn dòng sông.”"),t("br"),t("br"),n._v("Mọi người chung quanh đồng thời gật đầu."),t("br"),t("br"),n._v("Huyền Giang thành, tuy là không tới gần duyên hải, nhưng Huyền Giang cùng biển tương thông, chảy xuôi trong đó nước mặn, không thể tưới tiêu, không thể uống, nguyên nhân chính là như vậy, mặc dù giao thông tiện lợi, thành thị nhưng thủy chung phát triển không nổi, tại Lăng Vân kiếm các thống trị khu vực bên trong, chỉ tính bên trên tam lưu thành nhỏ."),t("br"),t("br"),n._v("Cũng không phồn hoa."),t("br"),t("br"),n._v("“Bởi vì toàn bộ sông lớn đều là nước mặn, thành thị bốn phía tất cả cày ruộng, đều mang muối tẩy rửa đặc sắc, sinh trưởng cây cối, cũng đều mang theo muối. Liền lấy vừa rồi luyện đan những dược liệu này làm thí dụ, mọi người có thể nhìn một chút, những thuốc này mẩu vụn, có thể cùng cái khác cặn thuốc, có khác biệt gì?”"),t("br"),t("br"),n._v("Nói xong, Trương Huyền duỗi ra vừa mới vê qua cặn thuốc ngón tay."),t("br"),t("br"),n._v("Mọi người đồng loạt nhìn lại, quả nhiên thấy ngón tay hắn đen kịt ấn ký phía trên, mang theo một tầng màu trắng bột phấn, tuy là thanh đạm, nhưng những người này đều là Thánh vực trở lên cao thủ, rất nhẹ nhàng cũng có thể thấy được."),t("br"),t("br"),n._v("Thanh niên áo xám cũng tới đến cặn thuốc bên cạnh, sờ một cái, đầu ngón tay quả nhiên cũng lưu lại màu trắng bột phấn, dùng đầu lưỡi chạm đến một cái, mang theo nhàn nhạt vị mặn."),t("br"),t("br"),n._v("“Không sai, đây là muối!”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp: “Linh Mẫu thảo sinh trưởng tại đầm lầy chi địa, hơn nữa thích âm không thích dương! Cành lá bên trong, bản thân ẩn chứa nhàn nhạt tính axit, cùng những dược vật khác bên trong muối tẩy rửa vừa tiếp xúc, lập tức trung hoà, phá hủy bản thân thuộc tính âm hàn, như vậy trải qua, không những không thể trung hoà những dược vật khác mang tới nóng nảy lực lượng, thậm chí còn bởi vì mất đi Chước Hỏa rễ cỏ, trở nên càng thêm hung mãnh! Cho nên, vừa rồi vị bằng hữu kia, mới già yếu đều là nên, thậm chí liền lựa chọn đều không muốn đi làm! Bởi vì... Hắn thực sự đã đợi không kịp!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Còn có chuyện như vậy?”"),t("br"),t("br"),n._v("Bốn phía xôn xao."),t("br"),t("br"),n._v("Luyện đan mà thôi, cần phải tinh tế như vậy?"),t("br"),t("br"),n._v("Thanh niên áo xám cũng không nhịn được sửng sốt một chút."),t("br"),t("br"),n._v("Thật chẳng lẽ là bởi vì cái này?"),t("br"),t("br"),n._v("Mấy bước đi tới trước đó luyện đan còn thừa lại vài cây dược liệu trước mặt, đầu ngón tay một chút, một đạo hỏa diễm thiêu đốt lên, lập tức đem hắn đốt thành tro bụi, cúi đầu nhìn lại, cùng trước đó đồng dạng, quả thực có muối lưu lại."),t("br"),t("br"),n._v("“Luyện đan chi đạo, không thể có bất kỳ sai lầm, một vị thuốc, một đạo trình tự làm việc sai, đều có thể dẫn đến cả bàn đều thua, vô số dược liệu hủy hoại chỉ trong chốc lát, nếu biết những này... Khu vực, thành tựu, đan lô, tâm cảnh thậm chí thủ pháp, đều hẳn là cân nhắc đi vào! Sai chính là sai, ta nghĩ... Vị sư huynh này nếu là Lăng Vân kiếm các người đi đường, hẳn là sẽ không thừa nhận a?”"),t("br"),t("br"),n._v("Trương Huyền cười khanh khách đạo."),t("br"),t("br"),n._v("“Sai liền sai, ta sẽ thừa nhận!”"),t("br"),t("br"),n._v("Biết không có cách nào cãi lại, thanh niên áo xám vung một cái ống tay áo, khẽ nói: “Cái này ngọc phù về ngươi!”"),t("br"),t("br"),n._v("Nói xong cũng không cần Tiết Cầm phản ứng, bàn tay lắc một cái, nàng lòng bàn tay ngọc phù liền hướng về phía Trương Huyền bay tới."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Khuôn mặt run rẩy, Tiết Cầm trên mặt đau rát đau."),t("br"),t("br"),n._v("Vừa được bảo vật, một canh giờ cũng chưa tới, liền... Kết thúc."),t("br"),t("br"),n._v("“Vậy ta liền từ chối thì bất kính...”"),t("br"),t("br"),n._v("Đem ngọc phù cất vào trong ngực, Trương Huyền lần nữa nhìn qua, nhẹ nhàng cười một tiếng: “Đúng, vừa rồi ta nói qua, muốn cùng ngươi đánh cược, người thua, có thể bị tùy ý xử lý... Ngươi mặc dù không có rõ ràng đáp ứng, nhưng cũng không có rõ ràng bác bỏ... Đã như vậy, có phải hay không cho rằng, cá cược hữu hiệu?”"),t("br"),t("br"),n._v("Sầm mặt lại, thanh niên áo xám cũng không trả lời, mà là trong mắt mang theo sát cơ."),t("br"),t("br"),n._v("Không nghĩ tới lại bị một cái địa phương nhỏ gia hỏa, tướng quân!"),t("br"),t("br"),n._v("Dường như không thấy đối phương lửa giận, Trương Huyền một chỉ cách đó không xa Nhan Tiết: “Ta cá cược, rất đơn giản, đánh vị này Tiết Cầm ba cái bạt tai liền có thể!”"),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Tiết Cầm vẻ mặt lập tức thay đổi.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1932"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1934"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);