(window.webpackJsonp=window.webpackJsonp||[]).push([[1053],{1408:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1921-tro-chuyen-tren-mang-cung-mua-sam-qua-mang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1921-tro-chuyen-tren-mang-cung-mua-sam-qua-mang"}},[n._v("#")]),n._v(" Chương 1921: Trò chuyện trên mạng cùng mua sắm qua mạng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thật bị đánh, còn mặt mũi nào mà tồn tại!"),t("br"),t("br"),n._v("Thân thể mềm mại run rẩy, Tiết Cầm vội vàng quay đầu nhìn về phía thanh niên áo xám: “Sư huynh, tuyệt đối không nên nghe cái tên này nói bậy, hắn chỉ là thuận miệng nói một chút, không có luyện chế thành đan dược, chưa hẳn chính là câu trả lời chính xác...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, thanh niên áo xám bỗng nhiên xoay người, chính phản tay, liên tục ba bàn tay quất tới."),t("br"),t("br"),n._v("Đùng! Đùng! Đùng!"),t("br"),t("br"),n._v("Mỗi một cái, đều vang vọng gian phòng, nữ hài hoảng sợ ánh mắt bên trong, nguyên bản vô cùng mịn màng làn da, trong chốc lát sưng đỏ lên."),t("br"),t("br"),n._v("“Có thể ư?”"),t("br"),t("br"),n._v("Làm xong những này, thanh niên áo xám hai tay chắp sau lưng, lạnh lùng nhìn qua."),t("br"),t("br"),n._v("Hắn đối luyện đan mười phần tinh thông, lại đặc biệt kiểm tra dược liệu, không cần tiếp tục luyện chế, đã rõ ràng, đối phương nói có chính xác không."),t("br"),t("br"),n._v("Đều biết đáp án, lại không thừa nhận, mất không riêng gì người của hắn, càng là Lăng Vân kiếm các!"),t("br"),t("br"),n._v("“Đánh nàng bạt tai, chỉ là ta thuận miệng nói một chút, một cái nữ hài tử, cùng ta lại không có hôn ước, lại không đặc biệt chạy tới từ hôn, không tính có thù... Đã như vậy, cứ như vậy đi, cá cược cũng coi như thực hiện, sư huynh, mời...”"),t("br"),t("br"),n._v("Trương Huyền khom người."),t("br"),t("br"),n._v("Thanh niên áo xám lạnh lùng nhìn hắn một cái, không nói thêm lời, xoay người rời đi, mọi người đồng loạt đi theo, té xỉu thanh niên, cũng có người đặc biệt trên lưng."),t("br"),t("br"),n._v("Vốn định tới, biểu diễn một điểm năng lực, làm cho cả Huyền Giang thành người, đều lòng sinh sợ hãi, không nghĩ tới, bị ở trước mặt đánh mặt."),t("br"),t("br"),n._v("Lửa giận trong lòng cháy hừng hực."),t("br"),t("br"),n._v("Rất đi mau ra thương hội, quay đầu nhìn về phía vẻ mặt sưng đỏ nữ hài, híp mắt lại: “Đó là ai?”"),t("br"),t("br"),n._v("“Sư huynh, người này ta thật không nhận ra...” Run run một chút, Tiết Cầm vội vàng nói."),t("br"),t("br"),n._v("“Vị kia bị què thiếu niên!” Thanh niên áo xám nói."),t("br"),t("br"),n._v("“Hắn, hắn...” Tiết Cầm vẻ mặt trở nên trắng bệch, vội vàng giải thích: “Hắn... Cùng ta là có hôn ước, là trong tộc lão nhân chỉ phúc vi hôn... Ở giữa không có cái gì, cũng chưa từng thấy qua mấy mặt!”"),t("br"),t("br"),n._v("“Ngươi hẳn phải biết vì cái gì thu ngươi làm tạp dịch đệ tử!”"),t("br"),t("br"),n._v("Thanh niên áo xám hừ lạnh, không mang theo bất kỳ biểu lộ gì: “Nội môn đệ tử, chỉ chú trọng tu luyện, cần phải có người hầu hạ sinh hoạt thường ngày! Ngươi dung nhan còn có thể, hơn nữa thân là xử nữ, tu vi không sai, để ngươi đi vào kiếm các, là hầu hạ người, thậm chí xem như thị thiếp!”"),t("br"),t("br"),n._v("“Ta biết...” Tiết Cầm đầu lâu cúi xuống."),t("br"),t("br"),n._v("“Biết là tốt rồi!”"),t("br"),t("br"),n._v("Khoát tay áo, thanh niên áo xám, xoay người hướng đi cách đó không xa xe ngựa, nhấc chân đi vào, ngay sau đó không có chút nào tình cảm âm thanh từ bên trong tiếp tục vang lên: “Đem sự tình xử lý tốt, không được truyền đến kiếm các! Còn có vừa rồi vị kia trên người tất cả đều là băng gạc gia hỏa, ta đêm nay liền muốn biết lai lịch của hắn cùng thân phận! Ba ngày sau, Lục Vân trưởng lão đi ngang qua nơi đây, ta không hy vọng, xuất hiện không cần thiết biến cố!”"),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Đầu lâu thấp đến trước ngực, Tiết Cầm thở mạnh cũng không dám."),t("br"),t("br"),n._v("Cộc cộc cộc!"),t("br"),t("br"),n._v("Xe ngựa đi xa."),t("br"),t("br"),n._v("Tiết Cầm lúc này mới cảm thấy phía sau lưng tràn đầy mồ hôi, lần nữa nhìn trước trước mắt Hồng Nhạn thương hội, lộ ra nồng đậm sát cơ."),t("br"),t("br"),n._v("“Trở về nói cho lão sư, đêm nay liền động thủ!”"),t("br"),t("br"),n._v("Sau lưng rất nhiều thanh niên đồng thời gật đầu: “Vâng!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Cám ơn lão sư...”"),t("br"),t("br"),n._v("Thấy làm nhục hắn mấy lần Tiết Cầm, bị ở trước mặt đánh mặt mũi bầm dập, nói không ra lời, Thiện Hiểu Thiên biết lão sư là vì bản thân, tràn đầy cảm kích."),t("br"),t("br"),n._v("“Nếu là học trò ta, đương nhiên sẽ không để ngươi nhận oan ức!”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười: “Tốt, giúp lão sư tìm yên tĩnh gian phòng, ta nghĩ thử một chút cái này viên thông thần ngọc phù!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),n._v("Gật gật đầu, Thiện Hiểu Thiên tìm tới trước đó trung niên nhân, thời gian không dài, liền an bài một gian tĩnh thất."),t("br"),t("br"),n._v("Đi vào phòng, Trương Huyền nhìn một vòng, phát hiện bốn phía đều có trận pháp, mười phần yên tĩnh, lúc này mới thoả mãn gật gật đầu, đem ngọc phù lấy ra, cầm tại lòng bàn tay."),t("br"),t("br"),n._v("“Lão sư, ta nghe nói loại này thông thần ngọc phù, cần đặc biệt trình tự mới có thể đi vào trong đó, hơn nữa triệt để kích hoạt cần tiêu phí rất lớn đánh đổi...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên lời còn chưa dứt, chỉ thấy lão sư tại trên ngọc bài liên tục điểm vài cái, ngay sau đó “Ông!” Một tiếng, ngọc bài lấp lánh ra hào quang chói sáng."),t("br"),t("br"),n._v("Triệt để kích hoạt!"),t("br"),t("br"),n._v("Ngay sau đó một giọt máu tươi tuôn ra, cùng ánh sáng hơi dính, lập tức biến thành đặc thù hoa văn, thời gian nháy mắt khắc sâu vào mi tâm."),t("br"),t("br"),n._v("Thiện Hiểu Thiên ngẩn ngơ."),t("br"),t("br"),n._v("Căn cứ hắn biết tin tức, kích hoạt thông thần ngọc phù, không chỉ cần phải đặc biệt trình tự, còn cần vô số thông thần tệ, thậm chí mượn cao thủ, không nghĩ tới lão sư chỉ thoáng cái, liền hoàn thành kích hoạt cùng nhận chủ, hình như trước kia làm qua rất nhiều lần đồng dạng."),t("br"),t("br"),n._v("Không để ý tới hắn khiếp sợ, cẩn thận cảm thụ đi vào mi tâm hoa văn, biết không bất kỳ nguy hại gì, Trương Huyền lúc này mới thở phào nhẹ nhõm, triệt để thả lỏng tinh thần."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Sau một khắc, ý thức giống như là rời đi thân thể, đi vào một cái đặc thù không gian."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Cúi đầu nhìn sang, trước mắt thân thể, vừa là hư ảo, lại hình như là chân thật, cho người ta một loại khó mà nói rõ cảm giác."),t("br"),t("br"),n._v("“Chào đón đi vào Thông Thần điện!”"),t("br"),t("br"),n._v("Đang tại kỳ lạ, trên không một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Thanh âm này...”"),t("br"),t("br"),n._v("Nhướng mày, Trương Huyền thân thể cứng đờ."),t("br"),t("br"),n._v("Thanh âm này, hắn vậy mà đã nghe qua!"),t("br"),t("br"),n._v("“Là Khổng sư!”"),t("br"),t("br"),n._v("Khổng sư âm thanh!"),t("br"),t("br"),n._v("Mặc dù biết Khổng sư khẳng định tới Thượng Thương, có thể làm mộng đều không có nghĩ đến, cùng cái này cả thương thế lực cường đại nhất một trong, Thông Thần điện liên quan đến!"),t("br"),t("br"),n._v("Chẳng lẽ... Thông Thần điện, là Khổng sư sáng tạo?"),t("br"),t("br"),n._v("“Thiện Hiểu Thiên nói, Thông Thần điện là mấy ngàn năm trước, một vị siêu cấp cường giả sáng lập, có thể... Khổng sư đã rời đi danh sư đại lục mấy vạn năm...” Sửng sốt một chút, Trương Huyền lông mày đột nhiên nhảy dựng: “Chẳng lẽ... Thượng Thương tốc độ thời gian trôi qua, cùng danh sư đại lục khác biệt?”"),t("br"),t("br"),n._v("Đi tới liền hôn mê, sau đó tỉnh lại gặp được rất nhiều sự tình, cũng không cảm thụ qua Thượng Thương chân thực tốc độ thời gian trôi qua."),t("br"),t("br"),n._v("Nói như vậy lên, nơi này tốc độ thời gian trôi qua, quả thực cùng danh sư đại lục hoàn toàn khác biệt."),t("br"),t("br"),n._v("Những việc này, đang tại trong óc chảy xuôi, thanh âm mới vừa rồi vang lên lần nữa: “Ta sáng lập Thông Thần điện, chỉ vì tu luyện giả có thể tốt hơn giao lưu, ở đây, có thể tùy ý thay đổi dung mạo cùng khí tức, không cần lo lắng người khác phát hiện! Chỉ cần tiêu phí đủ đánh đổi, liền có thể học tập đến muốn công pháp, mà không cần lo lắng, có người biết thân phận chân thật của ngươi.”"),t("br"),t("br"),n._v("“Thông thần ngọc phù phía trong, mỗi cái đi vào người, đều là tương đồng lực lượng, tương đồng cấp bậc, không tồn tại cao thấp giàu nghèo, cũng có thể bình đẳng giao lưu, chân chính chúng sinh bình đẳng, không cần lo lắng địa vị cùng trên thực lực chênh lệch...”"),t("br"),t("br"),n._v("“... Ta muốn nói chỉ có những này, đi mua sắm, đi giao dịch ngươi muốn đồ vật đi!”"),t("br"),t("br"),n._v("Âm thanh kết thúc."),t("br"),t("br"),n._v("Nghe xong những này tại, Trương Huyền đã xác định."),t("br"),t("br"),n._v("Chỉ có Khổng sư mới có loại này lòng dạ cùng lòng dạ, người người bình đẳng, người người như rồng!"),t("br"),t("br"),n._v("Vốn cho rằng vài vạn năm sau đến Thượng Thương, tìm tới đối phương tin tức, còn cần tiêu phí không biết bao lâu, không nghĩ tới một cái thông thần ngọc phù, chỉ biết."),t("br"),t("br"),n._v("Đè nén kích động trong lòng, Trương Huyền đem vừa rồi Khổng sư mà nói, cẩn thận nhớ lại một lần, linh quang chợt lóe, một cái ý nghĩ cổ quái, bất ngờ từ trong đầu xông ra."),t("br"),t("br"),n._v("“Bình đẳng giao lưu, không tính toán họ tên cùng thực lực, cái này không phải là... Cao cấp hơn trò chuyện trên mạng cùng mua sắm qua mạng ư?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1933"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1935"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);