(window.webpackJsonp=window.webpackJsonp||[]).push([[1057],{1411:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1925-bien-co"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1925-bien-co"}},[n._v("#")]),n._v(" Chương 1925: Biến cố")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Tinh Diệu thành, phủ thành chủ."),t("br"),t("br"),n._v("Hai cái thanh niên vừa nói chuyện, vừa hướng trước mắt sân nhỏ đi tới."),t("br"),t("br"),n._v("“Vân Phi Dương cùng Hoàng Thao, đang làm gì đó đi tới? Hai người này, vừa ra tới liền thần thần bí bí, cái gì tụ hội đều không tham gia. Nếu như biết lần này, có nhiều như vậy cô gái xinh đẹp, đoán chừng hối hận phát điên đi!”"),t("br"),t("br"),n._v("Bên trái một cái thanh niên mặc áo xanh cười nói."),t("br"),t("br"),n._v("Cái tên này thân thể có chút cao gầy, như là thân trúc đồng dạng, cánh tay thật dài."),t("br"),t("br"),n._v("“Bọn họ mới không hối hận, vừa đến cái này, liền mua hai cái thông thần ngọc phù, dùng tên giả cái gì Kinh Đào kiếm khách, chạy tới tìm người khiêu chiến!”"),t("br"),t("br"),n._v("Cái thứ hai thanh niên, có chút bất đắc dĩ nói, nước khác chữ mặt, âm thanh có chút tráng kiện, hiển nhiên đối với hai người cách làm, cũng không đồng ý."),t("br"),t("br"),n._v("“Khiêu chiến?”"),t("br"),t("br"),n._v("Thân trúc thanh niên suýt chút nữa chết cười: “Đường đường Lăng Vân kiếm các ngoại môn đệ tử, chạy tới khiêu chiến những này địa phương nhỏ thổ dân? Còn muốn mặt không? Thắng, cũng không có gì cảm giác thành tựu ah!”"),t("br"),t("br"),n._v("“Thật không biết bọn họ nghĩ như thế nào... Không nói cái khác, coi như Tinh Diệu thành thành chủ, cùng chúng ta tỷ thí kiếm pháp, cũng không có niềm tin tuyệt đối chiến thắng, chạy tới khiêu chiến một chút người bình thường... Xoạt loại này tồn tại cảm, cũng là đủ...”"),t("br"),t("br"),n._v("Bất đắc dĩ lắc đầu, mặt chữ quốc thanh niên cùng thân trúc thanh niên, đã đi tới sân nhỏ trước mặt, đẩy cửa đi vào."),t("br"),t("br"),n._v("Ngay sau đó nhìn thấy, tên là Hoàng Thao gia hỏa, đang ngồi ở trước bàn đá, hai mắt trừng trừng ngây người."),t("br"),t("br"),n._v("“Có phải hay không giết Tinh Diệu thành tu luyện giả, sợ chết khiếp, hô to anh hùng tha mạng?”"),t("br"),t("br"),n._v("Thân trúc thanh niên mỉm cười, đi tới trước mặt."),t("br"),t("br"),n._v("“Ngươi từ trước tới nay có phong độ, lần này lại nhường mấy chiêu?” Mặt chữ quốc cũng vỗ vỗ bờ vai của hắn."),t("br"),t("br"),n._v("Thấy là hai người này, Hoàng Thao tràn đầy khó xử, một chỉ trước mắt mặt bàn, âm thanh mang theo bất đắc dĩ cùng buồn bã: “Ta... Thua!”"),t("br"),t("br"),n._v("Chỉ thấy trên bàn đá, một cái ngọc phù đặt ngang ở phía trên, đã vỡ vụn mấy khối."),t("br"),t("br"),n._v("“Ngươi ở bên trong bị người giết?” Đồng tử co rụt lại, mặt chữ quốc hai người nhất thời rõ ràng chuyện gì xảy ra, tràn đầy khó tin."),t("br"),t("br"),n._v("Bọn họ nhưng mà Lăng Vân kiếm các đệ tử, đồng dạng thực lực, tuyệt đối quét ngang, vốn nghĩ quá khứ diễu võ giương oai, lại bị giết..."),t("br"),t("br"),n._v("Làm sao không quá tin tưởng đâu?"),t("br"),t("br"),n._v("“Ngươi dùng kiếm?” Mặt chữ quốc hỏi."),t("br"),t("br"),n._v("“Dùng, hơn nữa bị đối phương một kiếm đâm xuyên đầu, liền kiếm pháp cũng không kịp thi triển...”"),t("br"),t("br"),n._v("Hoàng Thao muốn đào đất khe hở."),t("br"),t("br"),n._v("“Đồng dạng dùng kiếm, kiếm pháp đều không có thi triển, bị trực tiếp đâm xuyên...” Hai người đều cho rằng có nghe lầm hay không."),t("br"),t("br"),n._v("“Vân Phi Dương sư huynh, còn chưa có đi ra, hẳn là nhìn thấy ta bị giết, chạy tới cùng đối phương tỷ võ, hắn chỉ cần đi ra, hẳn là có thể biết tất cả...”"),t("br"),t("br"),n._v("Biết bọn họ không tin, Hoàng Thao nói."),t("br"),t("br"),n._v("“Phi Dương cũng đi, hắn nhưng là chúng ta bốn người, cực kỳ có cơ hội trở thành nội môn đệ tử, kiếm pháp cực mạnh, chỉ cần hắn ra tay, hẳn là có thể rất nhẹ nhàng giải quyết...”"),t("br"),t("br"),n._v("An ủi một câu, ba người đồng thời đứng dậy, hướng bên cạnh sân trong đi tới, vừa đẩy cửa đi vào, liền thấy trong miệng vị này kiếm pháp cường giả, cùng vừa rồi Hoàng Thao đồng dạng, nhìn về phía phía trước nghiền nát ngọc bài, một mặt mê man cùng đau buồn, thoạt nhìn, đều có chút nghi ngờ nhân sinh."),t("br"),t("br"),n._v("“Ngươi... Chẳng lẽ cũng bị giết?”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, mọi người vội vã đi tới trước mặt."),t("br"),t("br"),n._v("Vị này chính là bốn người bọn họ bên trong, kiếm thuật mạnh nhất, cũng bị giết, động thủ người, nên mạnh bao nhiêu?"),t("br"),t("br"),n._v("“Ta thi triển ra kiếm pháp là , nhưng... Không có chống đỡ một kiếm! Cái kia một kiếm... Thật đẹp!”"),t("br"),t("br"),n._v("Nghĩ tới vừa rồi kiếm pháp, Vân Phi Dương vẫn như cũ chìm đắm trong đó."),t("br"),t("br"),n._v("“Lưu Thủy vô ngân?”"),t("br"),t("br"),n._v("Mọi người kinh hô."),t("br"),t("br"),n._v("Chiêu kiếm pháp này, tại Lăng Vân kiếm các trong ngoại môn đệ tử, được cho đỉnh tiêm phòng ngự phương pháp, một khi thi triển, thân thể bốn phía tựa như bị Lưu Thủy bọc, vô luận như thế nào công kích, đều sẽ bị hơi nước dính lên, hoặc là ngăn tại bên ngoài, hoặc là thay đổi phương hướng."),t("br"),t("br"),n._v("Cùng cấp bậc, không người có thể phá."),t("br"),t("br"),n._v("Sử dụng ra chiêu này, không có chống đỡ một kiếm... Thật đáng sợ như vậy?"),t("br"),t("br"),n._v("Then chốt... Bị giết, còn cho rằng kiếm pháp rất đẹp... Một bộ say mê biểu lộ, làm sao cảm giác được bị điên đâu?"),t("br"),t("br"),n._v("“Ta nghĩ tới kiến thức một chút... Phi Dương, ngươi nơi này nhưng còn có, không có sử dụng thông thần ngọc phù?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, thân trúc cùng mặt chữ quốc, đồng thời tràn đầy tò mò, cũng muốn gặp thức một chút, vị này dùng kiếm rất mạnh gia hỏa."),t("br"),t("br"),n._v("“Ta sau khi tỉnh lại đã phái người đi mua, hẳn là rất nhanh liền có thể cầm về...”"),t("br"),t("br"),n._v("Vân Phi Dương nói."),t("br"),t("br"),n._v("Mỗi một cái Thông Thần điện, nắm giữ thuộc về mình thông thần ngọc phù, sử dụng cái này, có thể đi vào cái này cái gọi là mạng máy tính cục bộ."),t("br"),t("br"),n._v("Thời gian không dài, một cái người hầu vội vã đi tới, lấy ra trọn vẹn tám cái."),t("br"),t("br"),n._v("Một viên thông thần ngọc phù mấy vạn thông thần tệ, đối Thiện Hiểu Thiên tới nói là khoản tiền lớn, đối với mấy cái này thiên chi kiêu tử tới nói, không tính là gì."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Bốn người rất nhanh lần nữa tiến vào Tinh Diệu thành Thông Thần điện, lần nữa tới đến công khai thi đấu tỷ thí đài trước mặt."),t("br"),t("br"),n._v("“Trận thứ bảy, Thiên Nhai chiến thắng!”"),t("br"),t("br"),n._v("“Ta muốn tiến hành trận thứ tám!”"),t("br"),t("br"),n._v("Vừa tới đến, liền thấy vị này Thiên Nhai, đã tỷ thí xong trận thứ bảy, cũng lấy được thắng lợi."),t("br"),t("br"),n._v("Trận thứ bảy tỷ thí, chính là trước đó, danh tiếng cực thịnh Mục Phong giả, cái tên này tốc độ cực nhanh, tựa như múa may theo gió, trên đài thấy không rõ hình bóng, có điều, vị này Thiên Nhai hướng về phía hư không bỗng nhiên một đạp, liền đá trúng dưới háng của hắn, sau đó... Liền không có sau đó."),t("br"),t("br"),n._v("“Còn có hay không tiến hành trận thứ tám khiêu chiến?”"),t("br"),t("br"),n._v("Bốn phía không có người trả lời."),t("br"),t("br"),n._v("Tuy là cùng cao thủ tỷ thí, có thể tăng lên thực lực của mình, nhưng vừa lên đài liền bị giây, chênh lệch quá lớn... Cũng không có người nguyện ý thật lãng phí tiền."),t("br"),t("br"),n._v("“Ta tới!”"),t("br"),t("br"),n._v("Đi lên phía trước, thân trúc thanh niên rất báo tường tên xong xuôi, đi vào tỷ thí đài."),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm."),t("br"),n._v("Liên tục bảy tràng chiến thắng, tuy là kiếm lời không ít, nhưng vẫn là không đủ mua sắm đan dược, một mực không nhân sâm thêm trận thứ tám... Tiền đem không cách nào gom góp."),t("br"),t("br"),n._v("Đứng tại tới lôi đài, thân trúc thanh niên quan sát tỉ mỉ thanh niên trước mắt."),t("br"),t("br"),n._v("Hết sức bình thường, không có bất kỳ cái gì đặc thù, liền hắn liên tục đánh bại hai vị sư huynh đệ?"),t("br"),t("br"),n._v("“Vẫn là dùng kiếm?”"),t("br"),t("br"),n._v("Thấy cái tên này vừa lên ra, liền lấy ra trường kiếm, Trương Huyền hỏi."),t("br"),t("br"),n._v("Thân trúc thanh niên gật gật đầu, đem trường kiếm rút xuất kiếm vỏ, một tiếng hô khẽ, đâm tới."),t("br"),t("br"),n._v("Nghe xong Vân Phi Dương cùng Hoàng Thao mà nói, biết người trước mắt này, là tuyệt đối cao thủ, cũng không nói nhảm, trực tiếp tấn công."),t("br"),t("br"),n._v("Bất quá, trường kiếm còn chưa tới đến trước mặt, trước mắt lập tức xuất hiện một đạo quang mang rực rỡ, tựa như cầu vồng, lại hình như mặt trời mới mọc, chói lóa mắt, để cho người ta trầm mê."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Thi thể nằm trên mặt đất."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Mặt chữ quốc khóe miệng giật một cái."),t("br"),t("br"),n._v("Trước đó còn cho rằng, Vân Phi Dương hai người có phải hay không phóng đại, không nghĩ tới không những không có khoa trương, so với bọn hắn nói càng thêm cường đại!"),t("br"),t("br"),n._v("Vân Phi Dương phòng ngự, trong ngoại môn đệ tử, được cho mạnh nhất, có thể thân trúc tấn công, tuyệt đối xếp tại phía trước, dù vậy, liền đối phương ống tay áo cũng chưa đụng được, liền bị đánh chết..."),t("br"),t("br"),n._v("Đã không thể dùng đáng sợ để hình dung!"),t("br"),t("br"),n._v("“Sư huynh, ngươi có muốn hay không đi lên thử một chút?” Hoàng Thao nhìn qua."),t("br"),t("br"),n._v("“Ta...” Dừng lại một chút, mặt chữ quốc đầy mặt cười khổ: “Hai người bọn họ đều không được, ta khẳng định càng không được... Thôi được rồi!”"),t("br"),t("br"),n._v("Thông thần tệ không là vấn đề, chỉ là trong ngoại môn đệ tử, cường đại nhất hai cái, cũng không phải địch, hắn đi lên, sẽ chỉ kết quả giống nhau."),t("br"),t("br"),n._v("“Tinh Diệu thành, lại có lợi hại như thế dùng kiếm cường giả, chuyện này nhất định phải thông báo trưởng lão!”"),t("br"),t("br"),n._v("Mặt chữ quốc nói."),t("br"),t("br"),n._v("“Ừm!” Còn lại hai người, đồng thời gật gật đầu, ba người rất nhanh biến mất tại nguyên chỗ, rời đi ngọc phù."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Rốt cục tám vị...”"),t("br"),t("br"),n._v("Đem thân trúc đâm chết, Trương Huyền thở phào nhẹ nhõm, còn tưởng rằng không ai dám khiêu chiến, để hắn thu vào giảm nhiều, không nghĩ tới cuối cùng lại tới một cái, không thể không nói, trên đời này, hay là nhiều người tốt ah!"),t("br"),t("br"),n._v("Lại đợi một hồi, không có người tiến hành trận thứ chín, Trương Huyền đành phải tràn đầy bất đắc dĩ đi ra lôi đài."),t("br"),t("br"),n._v("Lần nữa trở lại quầy phục vụ, vừa rồi tiếp đãi phục vụ viên, vẻ mặt đã thay đổi."),t("br"),t("br"),n._v("Động tĩnh như vậy lớn, cái tên này, đã biết."),t("br"),t("br"),n._v("Vốn cho rằng, là người nghèo rớt mồng tơi, gà yếu, nằm mơ đều không có nghĩ đến, lại là lợi hại như thế siêu cấp cường giả!"),t("br"),t("br"),n._v("Trên đầu không ngừng toát mồ hôi lạnh, thân thể có chút run rẩy, sợ đối phương thù dai, chẳng qua nhìn hắn một mặt hiền lành bộ dáng, cũng không vì chuyện vừa rồi tức giận, kìm lòng không được thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Thiên Nhai tiền bối, ngươi mua sắm xong một viên Thanh Dương đan phía sau, trong thẻ còn thừa lại 105500 thông thần tệ!”"),t("br"),t("br"),n._v("Rất mau đem vừa rồi tỷ thí thu vào, cùng mua sắm đan dược tiền tính toán hết, phục vụ viên vội nói."),t("br"),t("br"),n._v("“Mười vạn?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Tỷ thí gấp bội tăng lên, hắn thắng tám tràng, không nên chỉ có 25500 thông thần tệ, mua sắm xong, chỉ còn lại có 5500 ư? Làm sao xuất hiện cái mười vạn?"),t("br"),t("br"),n._v("“Bẩm báo tiền bối, trận thứ năm cùng thứ sáu tràng so với ngươi thí cường giả, kiếm trong tay là bọn họ mua sắm, bị giết phía sau, không có cách nào thu về, tự động trừ hao mòn xử lý cho Thông Thần điện, mỗi một chuôi, đều giá trị năm vạn thông thần tệ, bởi vì là ngươi chiến thắng được, cho nên, giá cả tính ở trên thân thể ngươi, lúc này mới nhiều hơn mười vạn!”"),t("br"),t("br"),n._v("Phục vụ viên nói."),t("br"),t("br"),n._v("“Mua sắm? Kiếm của bọn hắn, thoạt nhìn cùng trên lôi đài phối trí không có gì khác biệt...”"),t("br"),t("br"),n._v("Những này kiếm đều tương đồng thuộc tính, lại không biết càng thêm sắc bén, làm sao đắt như thế?"),t("br"),t("br"),n._v("“Thuộc tính là tương đồng, nhưng bọn hắn kiếm bên trên, có đặc thù hoa văn, vỏ kiếm màu sắc cũng không giống, chỉ riêng những này, liền giá trị năm vạn...” Phục vụ viên giải thích."),t("br"),t("br"),n._v("Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Nguyên lai, nơi này cũng bán làn da, hơn nữa giá cả, cũng không rẻ."),t("br"),t("br"),n._v("Thật là hố."),t("br"),t("br"),n._v("Cũng đúng, hư vinh người, đâu đâu cũng có, có tiền tự nhiên muốn cùng người khác không giống nhau, tu vi cùng thuộc tính bên trên không cách nào thay đổi, quần áo, vũ khí luôn có thể hiển lộ rõ ràng ra không giống tới."),t("br"),t("br"),n._v("“Ta mua đan dược, thế nào lấy ra?”"),t("br"),t("br"),n._v("Lại không rầu rĩ những này, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Tiền bối rời đi Thông Thần điện phía sau, chỉ cần tinh thần câu thông thông thần ngọc phù, phía trên truyền tống trận pháp liền sẽ kích hoạt, tự động đem mua sắm đồ vật đưa đến trước mặt!”"),t("br"),t("br"),n._v("Phục vụ viên nói."),t("br"),t("br"),n._v("“Như vậy tốt nhất!”"),t("br"),t("br"),n._v("Không nghĩ tới đơn giản như vậy, Trương Huyền thoả mãn gật gật đầu."),t("br"),t("br"),n._v("Nên mua đồ vật mua được, không tại ở lại, tinh thần khẽ động, thân thể hóa thành điểm sáng từ Thông Thần điện rời đi, sau một khắc, xuất hiện lần nữa tại tĩnh thất."),t("br"),t("br"),n._v("Khoanh chân ngồi xuống, đem thông thần ngọc phù đặt ở trước mặt, dựa theo phục vụ viên nói tới, tinh thần câu thông."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một tiếng vang nhỏ, quả nhiên thấy một cái bình ngọc xuất hiện ở trước mắt, chính là vừa rồi tại Thông Thần điện nhìn thấy Thanh Dương đan."),t("br"),t("br"),n._v("“Nuốt vào, hẳn là liền sẽ khôi phục...”"),t("br"),t("br"),n._v("Mở ra bình ngọc, đem đan dược đặt ở lòng bàn tay, Trương Huyền đang muốn nuốt vào, đột nhiên lòng bàn tay chợt lạnh, một cái hồ lô bất ngờ xuất hiện, miệng há ra, liền đem đan dược nuốt xuống."),t("br"),t("br"),n._v("“Ta sát...”"),t("br"),t("br"),n._v("Trương Huyền vẻ mặt nhất thời xanh biếc."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1937"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1939"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);