(window.webpackJsonp=window.webpackJsonp||[]).push([[1058],{1416:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1926-thien-hieu-thien-tan-cap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1926-thien-hieu-thien-tan-cap"}},[n._v("#")]),n._v(" Chương 1926: Thiện Hiểu Thiên tấn cấp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Thiện Hiểu Thiên tấn cấp"),t("br"),t("br"),n._v("Cái này hồ lô, chính là trước đó Một mực nương nhờ đan điền không đi gia hỏa, lần trước nhảy ra đem Thần Linh Hoàng tế tự có được long huyết nuốt, bản thân khí sắp bùng nổ, lần này vậy mà sớm không ra, muộn không ra, lại đem chữa thương đan dược nuốt!"),t("br"),t("br"),n._v("Ngươi quỷ chết đói đầu thai a!"),t("br"),t("br"),n._v("Làm sao có như vậy một cái không đáng tin cậy gia hỏa?"),t("br"),t("br"),n._v("Khí thân thể run rẩy, Trương Huyền cắn răng: “Cho ta phun ra!”"),t("br"),t("br"),n._v("Hai tay nắm lại trong hồ lô ở giữa mảnh khảnh vị trí, không ngừng dùng sức."),t("br"),t("br"),n._v("Chỉ bất quá, bị thương nặng phía dưới, lực lượng có hạn."),t("br"),t("br"),n._v("“Đã ăn, kéo ra tới, ngươi có muốn hay không...”"),t("br"),t("br"),n._v("Hồ lô vặn vẹo cái mông."),t("br"),t("br"),n._v("“Kéo ngươi muội...”"),t("br"),t("br"),n._v("Nhấc lên hồ lô mạnh mẽ ngã xuống đất, dùng sức đạp hai cước, lúc này mới tiêu tan khẩu khí, mở miệng cảnh cáo: “Ta dùng đan dược khôi phục thương thế, lại làm loạn, tin hay không hiện tại liền đem ngươi ném vào ao phân?”"),t("br"),t("br"),n._v("“Ngươi bị không gian phong bạo gây thương tích, trong cơ thể ẩn chứa vết nứt không gian, dùng cái này đan dược khôi phục thương thế? Muốn tới năm nào tháng nào?”"),t("br"),t("br"),n._v("Nghe hắn nói muốn khôi phục thương thế, hồ lô khịt mũi coi thường, xoay đến càng mạnh."),t("br"),t("br"),n._v("“Ngươi ý tứ, viên đan dược này khôi phục không được thương thế của ta?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Thư viện không cách nào kiểm tra bản thân tình huống, bởi vì chân khí không cách nào vận dụng, cũng chỉ biết mình tổn thương mười phần nghiêm trọng, đến cùng nặng đến loại trình độ nào, cũng không hiểu rõ tình hình."),t("br"),t("br"),n._v("“Phổ thông thương thế khẳng định có thể, nhưng không gian phong bạo tạo thành... Đan dược này ăn một cái sọt, hiệu quả cũng không lớn!” Hồ lô đạo."),t("br"),t("br"),n._v("“Cái kia... Cần gì cấp bậc dược vật?” Biết kẻ trước mắt này, không có lừa gạt mình cần, lửa giận tiêu tan một ít, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Cần có thể trị liệu không gian thương thế đan dược, chẳng qua, thưa thớt vô cùng, hơn nữa giá trị cực cao, căn bản tìm không thấy, như vậy đi... Vừa rồi cái loại này đan dược, ngươi lấy thêm mấy cái tới, chỉ cần ta ăn no, sẽ nói cho ngươi biết thế nào trị liệu...”"),t("br"),t("br"),n._v("Hồ lô một mặt kiêu ngạo đạo."),t("br"),t("br"),n._v("Lời còn chưa dứt, chỉ thấy thanh niên trước mắt giẫy giụa đi ra tĩnh thất, mở lời la lên: “Thiện Hiểu Thiên, mua cho ta cái dao phay tới!”"),t("br"),t("br"),n._v("“Dao phay?”"),t("br"),t("br"),n._v("“Ừm, ta có cái hồ lô, muốn thay đổi thành làm muôi, cần dùng đao bổ ra... Búa cũng được!” Trương Huyền đạo."),t("br"),t("br"),n._v("“Tốt a, ta cho ngươi biết giải quyết như thế nào...” Thấy hắn đùa thật, thật cầm một thanh búa đi vào, đối với mình chém loạn, hồ lô cũng nhịn không được nữa: “Thật ra thì, hỏi ngươi muốn đan dược, cũng không phải là ta tham ăn, mà là ta hiện tại lực lượng rất yếu, cần dùng dược vật, khôi phục một ít, mới có thể giúp ngươi trị liệu thương thế!”"),t("br"),t("br"),n._v("“Đừng có đùa đa dạng...” Trương Huyền trong tay búa, đè ở hồ lô bên trên."),t("br"),t("br"),n._v("“Sẽ không giở trò gian...” Hồ lô liền vội vàng gật đầu."),t("br"),t("br"),n._v("Trương Huyền lần nữa trở lại thông thần ngọc phù, đem còn lại mười vạn, đều mua sắm thành Thanh Dương đan, hết thảy năm cái, toàn bộ đưa tới."),t("br"),t("br"),n._v("Hồ lô thôn phệ xong xuôi, ợ một cái, lúc này mới lắc lắc đi tới: “Chuẩn bị cái nồi lớn, đem ta đặt ở bên trong nấu, một canh giờ sau, đem bên trong nước uống, thương thế liền có thể khôi phục...”"),t("br"),t("br"),n._v("“Bắt ngươi đun nước uống?”"),t("br"),t("br"),n._v("Trương Huyền ngẩn ngơ."),t("br"),t("br"),n._v("Còn có như vậy phương pháp trị liệu?"),t("br"),t("br"),n._v("“Ừm!” Hồ lô nói xong lười biếng nằm trên mặt đất: “Không nhanh chút, ta liền trở về nghỉ ngơi...”"),t("br"),t("br"),n._v("Biết cái tên này nước tiểu tính cực lớn, nói trở về thật đúng là có thể sẽ đi, Trương Huyền tràn đầy bất đắc dĩ, đành phải lần nữa dặn dò, canh giữ ở phía ngoài Thiện Hiểu Thiên."),t("br"),t("br"),n._v("Chỉ chốc lát, vị này đệ tử thứ mười liền đem nồi cùng nước, mang đi vào."),t("br"),t("br"),n._v("Một bên bưng một bên nghi ngờ... Lão sư không phải nói muốn trị chữa thương thế ư? Lại muốn dao phay, búa, lại muốn nồi hơi, đây là muốn... Ăn lẩu?"),t("br"),t("br"),n._v("Nghĩ đến cái này, nhịn không được mở lời: “Lão sư, thịt dê cùng cá viên các loại còn muốn không? Cần, ta để cho người ta chuẩn bị...”"),t("br"),t("br"),n._v("“...” Trương Huyền."),t("br"),t("br"),n._v("Thật vất vả trấn an đệ tử, Trương Huyền nấu nước đem hồ lô ném vào, cái tên này, trôi lơ lửng ở phía trên, một hồi bơi ngửa, một hồi lặn, chơi quên cả trời đất."),t("br"),t("br"),n._v("Nhìn đối phương vui vẻ hình dáng, Trương Huyền che cái trán, không biết mình nghe nó đến cùng là đúng hay sai."),t("br"),t("br"),n._v("Tại danh sư đại lục, hô phong hoán vũ, đứng tại đỉnh phong nhất, đến nơi này, lại bị một cái hồ lô la lối om sòm..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy tâm tắc."),t("br"),t("br"),n._v("Mấu chốt nhất là... Cái đồ chơi này đến cùng là cái gì?"),t("br"),t("br"),n._v("Người khác bảo vật, đều là hô chi tức tới vung chi liền đi, cái tên này, một đầu đâm vào đan điền của mình, không kêu được, vừa ra tới liền ăn đồ ăn... Không có chút nào nghiêm chỉnh, thật không biết, bản thân như vậy đáng tin cậy người đi đường, làm sao lại làm ra như vậy một cái không đáng tin cậy đồ vật."),t("br"),t("br"),n._v("Một canh giờ thoáng qua liền qua, không cần bản thân vớt, hồ lô lần nữa chui vào đan điền."),t("br"),t("br"),n._v("Đem trong nồi nước, lấy ra, Trương Huyền uống vào mấy ngụm, quả nhiên cảm thấy trong cơ thể một hồi mát mẻ, trước đó có chút đau nhức thương thế, hóa giải không ít."),t("br"),t("br"),n._v("“Thật đúng là hữu dụng...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Vốn cho rằng, đối phương là đang lừa bản thân, trong tay búa đều lần nữa chuẩn bị xong, không nghĩ tới vậy mà thật có hiệu quả."),t("br"),t("br"),n._v("“Động Hư hồ lô... Bản thân liền dính dáng không gian, có lẽ có liên quan với đó...”"),t("br"),t("br"),n._v("Mặc dù biết cái này Động Hư hồ lô, khẳng định không biết Động Hư cảnh, nhưng nghĩ nửa ngày cũng nghĩ không ra nguyên lý, đành phải bản thân an ủi."),t("br"),t("br"),n._v("Chỉ chốc lát, đem một siêu nước toàn bộ uống xong, cái bụng tăng tròn xoe, thương thế bên trong cơ thể, cũng mắt thường có thể thấy được từ từ khôi phục."),t("br"),t("br"),n._v("Không đến nửa canh giờ, chấn động toàn thân, trên người băng vải, phân tán bốn phía ra, rơi xuống trên đất."),t("br"),t("br"),n._v("“Thương thế xem như toàn bộ tốt, chẳng qua, thực lực còn không có khôi phục...”"),t("br"),t("br"),n._v("Xuyên qua không gian thông đạo lưu lại thương thế, giờ phút này hoàn hảo không chút tổn hại, chẳng qua, chân khí tiêu hao sạch sẽ, cũng không triệt để khôi phục."),t("br"),t("br"),n._v("Ngồi dưới đất, Trương Huyền cẩn thận cảm thụ bốn phía thủy ngân khí tức, toàn thân lỗ chân lông mở ra, chậm chạp hấp thu."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Lực lượng rót vào trong cơ thể, chèn ép kinh mạch có chút phát nặng, chẳng qua, không có xé rách cảm giác."),t("br"),n._v("“Không nghĩ tới thương thế khôi phục, thân thể cũng thích ứng loại lực lượng này?”"),t("br"),t("br"),n._v("Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Hắn hiện tại vận chuyển là bình thường Thiên đạo công pháp, cũng không phóng thích sát lục chi khí, ban đầu ở Côn Hư cảnh như vậy tu luyện, căn bản không chịu nổi, không nghĩ tới thương thế tốt đẹp sau, kinh mạch dường như cũng biến thành chắc chắn có thêm, cứ việc có chút phát nặng, nhưng đã không trở ngại."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Linh khí đi vào trong cơ thể, nhanh chóng vận chuyển, thời gian không dài, liền cảm thấy thuế biến thành chân khí, ở trong người bốn phía đi khắp."),t("br"),t("br"),n._v("“Trong ba ngày, hẳn là có thể triệt để khôi phục...”"),t("br"),t("br"),n._v("Cảm nhận được hấp thu linh khí tốc độ, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Đi qua phong Thánh, thiên địa chi sư, chân khí trong cơ thể số lượng dự trữ, sớm đã đạt đến mức độ kinh người, lần này tiêu hao sạch sẽ, cho dù có Thiên đạo công pháp hấp thu thủy ngân linh khí, cũng cần ít nhất ba ngày mới có thể hoàn toàn khôi phục."),t("br"),t("br"),n._v("Chẳng qua, chỉ cần có chân khí, dù là chỉ có mấy đạo, tu vi cũng chờ tại khôi phục được phá toái hư không sơ kỳ, toàn bộ Huyền Giang thành, không có gì lại sợ hãi."),t("br"),t("br"),n._v("Lại điều chỉnh một hồi, trong cơ thể dự trữ trên trăm đạo chân khí, lần nữa đem Thiện Hiểu Thiên hô đi vào."),t("br"),t("br"),n._v("“Lão sư, ngươi khôi phục?”"),t("br"),t("br"),n._v("Nhìn thấy lão sư trên người băng vải diệt hết, cũng mất trước đó suy sụp tinh thần, thiếu niên ánh mắt sáng lên."),t("br"),t("br"),n._v("“Ừm, đánh một quyền cho ta xem một chút, để cho ta xác định cổ trùng vị trí, sau đó giúp ngươi chém giết!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền đạo."),t("br"),t("br"),n._v("Thiện Hiểu Thiên liền vội vàng gật đầu, một bộ quyền pháp thi triển đi ra."),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền xác định vị trí, bàn tay bỗng nhiên đè ép xuống, lăng không điểm ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một đạo Thiên đạo chân khí, lập tức hướng đối phương trong cơ thể vọt tới."),t("br"),t("br"),n._v("Dọc theo kinh mạch chui vào trong cơ thể, sau một khắc liền đến đến cổ trùng ẩn thân địa phương."),t("br"),t("br"),n._v("Xì xì xì xì... Tư!"),t("br"),t("br"),n._v("Tựa như kịch độc ăn mòn, vốn đang nằm, tràn đầy thoải mái cổ trùng, cảm nhận được cỗ lực lượng này, sợ tới mức lập tức trốn ra phía ngoài ra, còn chưa đi xa, lại một đạo chân khí vọt tới, đem hắn bọc lại."),t("br"),t("br"),n._v("Thời gian không dài, hóa thành một đám nước đặc."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Cổ trùng chết một lần, Thiện Hiểu Thiên giống như là biến thành thổi lên bóng bay, thân thể không tự chủ được bành trướng."),t("br"),t("br"),n._v("“Lão sư, ta đây là làm sao vậy...”"),t("br"),t("br"),n._v("Cảm nhận được trong cơ thể, dâng trào muốn ra, bất cứ lúc nào cũng sẽ đem hắn vỡ ra tới lực lượng, Thiện Hiểu Thiên sắc mặt trắng nhợt."),t("br"),t("br"),n._v("“Ta trước đó cùng ngươi đã nói, mười năm qua, tu luyện của ngươi chưa hề ngừng, nhưng tu vi nhưng không có bất kỳ tăng trưởng, cũng không phải là không đủ cố gắng, mà là thu nạp linh khí, đều bị cái này cổ trùng thôn phệ... Mười năm khổ công, thôn phệ bao nhiêu lực lượng, có thể tưởng tượng được!”"),t("br"),t("br"),n._v("Trương Huyền cười cười: “Hiện tại toàn bộ trả cho ngươi, tự nhiên sẽ cảm thấy bành trướng, đừng do dự, dựa theo bộ công pháp này tiến hành rèn luyện hấp thu!”"),t("br"),t("br"),n._v("Ngón tay lần nữa một chút, bản rút gọn Thiên đạo công pháp, đi vào đối phương trong cơ thể."),t("br"),t("br"),n._v("Không kịp nhìn kỹ, nhưng Thiện Hiểu Thiên biết lão sư chắc chắn sẽ không hại hắn, lập tức để chân khí ở trong người vận chuyển lại."),t("br"),t("br"),n._v("Hút một cái thu cỗ lực lượng này, lập tức phát hiện không đúng."),t("br"),t("br"),n._v("Bình thường thôn phệ linh khí, cần chuyển hóa, cần tinh luyện, thậm chí đủ loại thích ứng... Mà cổ trùng sau khi chết chảy vào nhập thể nội lực lượng, hoàn toàn không cần những này bước đi!"),t("br"),t("br"),n._v("Hình như bản thân liền là hắn, có thể rất nhẹ nhàng khống chế, thu nạp Bình thường!"),t("br"),t("br"),n._v("“Bản thân cái này chính là ngươi vất vả tu luyện chân khí, ngươi khống chế lại, tự nhiên như cá gặp nước, hoạt động nhanh lên một chút, trước khi trời tối, có lẽ liền có thể đi đến, mười năm khổ tu, nên được đến cảnh giới!”"),t("br"),t("br"),n._v("Nhìn ra hắn khiếp sợ, Trương Huyền cười nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Không nói thêm lời, Thiện Hiểu Thiên vội vàng nhắm mắt lại nhanh chóng hấp thu."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trong cơ thể kinh mạch nhanh chóng mở ra, lực lượng một ý niệm đi khắp toàn thân, trước đó trì trệ không tiến, không cách nào tiến bộ Thánh vực lục trọng, mắt thường có thể thấy được tăng trưởng."),t("br"),t("br"),n._v("Thánh vực thất trọng!"),t("br"),t("br"),n._v("Thánh vực bát trọng!"),t("br"),t("br"),n._v("Thánh vực cửu trọng!"),t("br"),t("br"),n._v("Không đến nửa canh giờ, liền đạt đến Thánh vực cửu trọng, hơn nữa tiếp tục tiến lên."),t("br"),t("br"),n._v("“Hắn không có thiên kiếp?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Danh sư đại lục, đi đến Thánh vực bát trọng, sẽ dẫn tới Động Hư kiếp, Thánh vực cửu trọng sẽ dẫn tới Vũ Không kiếp, Lúc đó hắn vượt qua, đều hao tốn công phu rất lớn."),t("br"),t("br"),n._v("Trước mắt vị này đệ tử thứ mười, tuỳ tiện xung kích thành công, vậy mà không có bất cứ động tĩnh gì."),t("br"),t("br"),n._v("“Hẳn là Thượng Thương Thiên đạo càng thêm cường đại, loại cảnh giới này, cùng Lúc trước danh sư đại lục tông sư, chí tôn đồng dạng, không đủ để dẫn tới phản phệ...”"),t("br"),t("br"),n._v("Rất nhanh hiểu được."),t("br"),t("br"),n._v("Thượng Thương người bình thường, trưởng thành đều có thể đi đến Thánh vực cửu trọng, loại cảnh giới này liền xuất hiện lôi đình lời nói, Thượng Thương Thiên đạo còn không sống công việc mệt chết?"),t("br"),t("br"),n._v("Đang miên man suy nghĩ, chỉ thấy Thiện Hiểu Thiên đã đột phá đến Đại Thánh cấp bậc."),t("br"),t("br"),n._v("Ngay sau đó, Kiến Thần Bất Hoại, Kim Thân cảnh, Tâm Huyết Lai Triều..."),t("br"),t("br"),n._v("Cho đến Đại Thánh tam trọng đại viên mãn, mới chậm rãi ngừng lại."),t("br"),t("br"),n._v("Cổ trùng thôn phệ hắn mười năm lực lượng, toàn bộ thả ra ngoài, vậy mà để hắn từ Thánh vực lục trọng, trực tiếp tấn thăng sáu cái đại cấp bậc, thực lực cùng chạy tới từ hôn Tiết Cầm, giống nhau như đúc!."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1938"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1940"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);