(window.webpackJsonp=window.webpackJsonp||[]).push([[1060],{1415:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1927-luc-van-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1927-luc-van-truong-lao"}},[n._v("#")]),n._v(" Chương 1927: Lục Vân trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Không tệ!”"),t("br"),t("br"),n._v("Trương Huyền thoả mãn gật đầu."),t("br"),t("br"),n._v("Vị này đệ tử, cùng lúc trước Lộ Xung đồng dạng, có được vô cùng lòng kiên định trí."),t("br"),t("br"),n._v("Không nói cái khác, đổi lại những người khác, gặp thảm biến, khẳng định sớm đã cam chịu, triệt để lưu đày, mà hắn, biết rõ không thể tu luyện dưới tình huống, vẫn như cũ mỗi ngày thu nạp linh khí, chưa từng ngừng."),t("br"),t("br"),n._v("Phần này nghị lực, mới có thành tựu như thế!"),t("br"),t("br"),n._v("Bỏ ra mới có thể có đến, thiên hạ không có uổng phí tới bữa trưa."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Cảm nhận được lực lượng nối liền toàn thân, trước đó tựa như nham thạch đồng dạng chân trái, khôi phục như lúc ban đầu, không còn ràng buộc, Thiện Hiểu Thiên cũng nhịn không được nữa, nước mắt cuồn cuộn chảy xuôi, quỳ rạp xuống đất."),t("br"),t("br"),n._v("Bất kể nói thế nào, hắn đều là mười mấy tuổi thiếu niên."),t("br"),t("br"),n._v("Vốn cho rằng, vị hôn thê từ hôn, nhất định gặp khuất nhục, sau đó chết đi, không nghĩ tới, trước mắt vị lão sư này, hoàn toàn thay đổi nhân sinh."),t("br"),t("br"),t("br"),n._v("“Hảo tâm có báo đáp tốt, ngươi đã cứu ta, đây cũng là ngươi nên được...” Trương Huyền cười cười, lần nữa nhìn tới: “Thực lực ngươi đạt đến Kiến Thần Bất Hoại cảnh giới, vi sư lại truyền thụ một chút võ kỹ phòng thân!”"),t("br"),t("br"),n._v("Chỉ có tu vi, không có võ kỹ, đồng dạng tương đương không có sức chiến đấu."),t("br"),t("br"),n._v("Liền giống như lúc trước Hư Trúc, cầm hai trăm năm công lực, sẽ không sử dụng, không tính là cao thủ, một cái Ô lão đại liền để hắn luống cuống tay chân."),t("br"),t("br"),n._v("“Gặp ngươi một mực luyện kiếm, vừa vặn, vi sư vừa sáng chế ra một bộ kiếm chiêu, ngươi thử một chút có thể hay không học được...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên từ mười tuổi bắt đầu, loại trừ tu luyện chính là luyện kiếm, cứ việc kiếm pháp cũng không như thế nào, nhưng kiện binh khí này, sớm đã sâu tận xương tủy, không cần thiết lại đi thay đổi, lại nói, đem vừa rồi tại thông thần ngọc phù, sáng lập ra kiếm pháp, truyền thụ cho hắn cũng không tệ."),t("br"),t("br"),n._v("Rất mau đem kiếm pháp ảo diệu học được một lần."),t("br"),t("br"),n._v("Lão sư truyền thụ cho chỉ có một chiêu, chính là ném ra trường kiếm phía sau, đi qua đối lực lượng tinh diệu khống chế, đâm vào đối phương sơ hở, đem đối thủ chém giết."),t("br"),t("br"),n._v("Thoạt nhìn đơn giản, nghe qua về sau, mới hiểu được, muốn luyện thành, khó khăn chồng chất."),t("br"),t("br"),n._v("Đầu tiên, muốn đối đối thủ kiếm chiêu, lực lượng, kiếm ý, đều có nhất định tìm hiểu, mới có thể làm đến liệu địch tiên cơ. Thứ hai, đối tốc độ cũng có rất cao yêu cầu."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Tuy là khó khăn, nhưng Thiện Hiểu Thiên bản thân liền là cái kiên nghị người, một chiêu một thức, làm cẩn thận tỉ mỉ, sợ có một chút phạm sai lầm, dẫn đến lão sư mất hứng."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Gặp vị này vừa thu nhận đệ tử, cứ việc không có Triệu Nhã, Viên Đào, Ngụy Như Yên đám người thiên phú, nhưng thắng ở cố gắng, Trương Huyền thoả mãn gật gật đầu."),t("br"),t("br"),n._v("Luyện hơn một canh giờ, gặp hắn đối chiêu kiếm pháp này có nhất định hiểu, hai người lúc này mới rời đi tĩnh thất."),t("br"),t("br"),n._v("“Tiếp tục giả vờ què...” Trương Huyền truyền âm qua."),t("br"),t("br"),n._v("Thiện Hiểu Thiên vội vàng khôi phục trước đó hình dáng, lết lết tiến lên."),t("br"),t("br"),n._v("Tu vi đột phá, chân không chỉ tốt, chiều cao cũng tăng lên không ít, tuy là không bằng Trương Huyền đồ sộ, nhưng cũng cùng người bình thường không khác, giờ phút này biết lão sư dụng ý, cố ý bị què, thân thể cong lên ra, không phải hết sức quen thuộc, căn bản nhìn không ra, từ thương hội một vào một ra, nửa ngày không đến, đã có chất thuế biến cùng bay vọt."),t("br"),t("br"),n._v("Giờ phút này, trời bên ngoài, đã triệt để tối, Dịch lão chạy đến xe ngựa, nhìn rõ ràng thiếu gia hình dáng, nhịn không được sửng sốt."),t("br"),t("br"),n._v("Vừa định nói chuyện, chỉ thấy thiếu gia lắc đầu."),t("br"),t("br"),n._v("Rõ ràng hắn ý tứ, cũng không nhiều lời, Dịch lão trực tiếp quỳ rạp xuống đất, cho Trương Huyền cẩn thận dập đầu mấy cái, lúc này mới lần nữa đứng lên, gọi hai người lên xe."),t("br"),t("br"),n._v("Vốn, thiếu gia muốn cứu vị này, hắn còn cho rằng có chút phiền phức, không nghĩ tới, vậy mà cho thiếu gia mang đến nghiêng trời lệch đất nhân sinh gặp gỡ."),t("br"),t("br"),n._v("“Lão gia, thiếu gia tốt...”"),t("br"),t("br"),n._v("Hốc mắt đỏ lên, Dịch lão tự nói một tiếng, không nói thêm lời, lái xe ngựa, nhanh chóng hướng Thiện gia chạy như bay."),t("br"),t("br"),n._v("Bọn họ vừa rời đi, góc đường lập tức hiện lên mấy đạo nhân ảnh, tựa như khói xanh, thẳng tắp đi theo."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tinh Diệu thành."),t("br"),t("br"),n._v("“Nghe nói không? Thông Thần điện ngày hôm nay tới cái siêu cấp cường giả! Công khai khiêu chiến thi đấu, liên tục tám tràng chiến thắng, bởi vì không còn đối thủ, mới rời khỏi?”"),t("br"),t("br"),n._v("“Liên tục tám tràng? Tinh Diệu thành đã mấy trăm năm không có xuất hiện qua lợi hại như vậy cường giả đi! Khoảng thời gian này, vừa vặn Lăng Vân kiếm các cao thủ tới thu nhận ngoại môn đệ tử, có phải hay không là bọn họ kết quả?”"),t("br"),t("br"),n._v("“Bọn họ kết quả không giả... Nhưng đều bị người này tuỳ tiện đánh chết! Hơn nữa đều là một chiêu mất mạng...”"),t("br"),t("br"),n._v("“Thật hay giả? Làm sao ngươi biết, kiếm các cao thủ kết quả?”"),t("br"),t("br"),n._v("“Kiếm các tới bốn cái cường giả, không phải có một vị gọi Vân Phi Dương ư? Hắn đã không có thay đổi dung mạo, cũng vô dụng ngoại hiệu, còn thi triển ra kiếm các kiếm pháp, kết quả... Một kiếm bị vị kia xuất hiện cao thủ đâm trúng đầu, tại chỗ liền chết...”"),t("br"),t("br"),n._v("“Mạnh như vậy? Cao thủ đại ca tên gì?”"),t("br"),t("br"),n._v("“Gọi... Thiên Nhai!”"),t("br"),t("br"),n._v("“Quả nhiên là tên rất hay, nghe xong liền biết, xa xăm bao la, sâu không lường được!”"),t("br"),t("br"),n._v("“Chẳng lẽ không phải nhai cọng lông?”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tin tức truyền đi rất nhanh, Trương Huyền rời đi Thông Thần điện chưa tới một canh giờ, toàn bộ Tinh Diệu thành tất cả cao thủ, liền hầu như toàn bộ biết."),t("br"),t("br"),n._v("Trong nháy mắt, Thông Thần điện kín người hết chỗ, vô số cao thủ sử dụng kiếm, đều tại tỷ thí trước đài chờ lấy, hi vọng vị kia tên là “Thiên Nhai” siêu cấp cường giả, xuất hiện lần nữa, cũng để cho bọn họ mở mang kiến thức một chút, chân chính kiếm pháp."),t("br"),t("br"),n._v("Tinh Diệu thành, phủ thành chủ."),t("br"),t("br"),n._v("Lăng Vân kiếm các người tới, đều được an bài ở chỗ này."),t("br"),n._v("Rộng lớn sân nhỏ, một vị lão giả, đang tay cầm trường kiếm, chậm chạp vung vẩy."),t("br"),t("br"),n._v("Động tác của hắn rất chậm, thoạt nhìn không có uy lực gì, nhưng mỗi một dưới kiếm ép, đều cho người ta một loại xé rách thiên địa cảm giác, trong viện khí lưu, bị trường kiếm khuấy động lên, không ngừng xoay tròn, tạo thành gió lốc."),t("br"),t("br"),n._v("Trong viện trồng không ít hoa, cỏ cây, gió lốc sau đó, hoa cùng lá cây đều không có bất cứ thương tổn gì, nhưng phía trên giọt nước, nhưng giống như là bị bốc hơi làm đồng dạng."),t("br"),t("br"),n._v("Bỏ đi giọt nước, nhưng không làm thương hại hoa, phần này lực khống chế, quả thực kinh người."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ngừng lại, lão giả đem trường kiếm thu hồi, một ngụm trọc khí phun ra, tựa như lụa trắng, lan tràn hơn mười trượng khoảng cách, cuối cùng biến mất ở trong trời đêm."),t("br"),t("br"),n._v("“Vào đi!”"),t("br"),t("br"),n._v("Rửa sạch sẽ tay, lão giả mở lời."),t("br"),t("br"),n._v("Vừa rồi luyện kiếm thời điểm liền phát hiện, lần này mang tới mấy cái hậu bối, đồng loạt đi tới bên ngoài, bởi vì tu luyện, cũng không để ý tới."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Vân Phi Dương, Hoàng Thao, thân trúc, mặt chữ quốc đám người đi vào, đồng thời khom người: “Bái kiến Lục trưởng lão!”"),t("br"),t("br"),n._v("Vị lão giả này, chính là Lăng Vân kiếm các lần này chịu trách nhiệm thu nhận học sinh Lục Vân trưởng lão!"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Đi tới trong viện bàn đá trước mặt, Lục Vân trưởng lão ngồi xuống: “Tới tìm ta, nhưng có chuyện gì?”"),t("br"),t("br"),n._v("Rời đi tông môn, những người này, đều giống như thả chim tước đồng dạng, bình thường gặp hắn trốn cũng không kịp, đồng loạt xuất hiện tại bên ngoài viện, muốn nói không có việc gì, hắn đều không tin."),t("br"),t("br"),n._v("“Bẩm báo trưởng lão, vừa rồi Vân Phi Dương sư đệ cùng Hoàng Thao sư đệ, đi... Tinh Diệu thành Thông Thần điện, cùng người tiến hành công khai thi đấu tỷ thí...”"),t("br"),t("br"),n._v("Dừng lại một chút, mặt chữ quốc tiến về phía trước một bước, nói."),t("br"),t("br"),n._v("“Tâm tư của các ngươi ta có thể lý giải, học nghệ nhiều năm, muốn khoe khoang một chút, nhưng... Thân là kiếm các thế hệ sau, học tập kiếm pháp mục đích là vì tăng thực lực lên, phát triển kiếm thuật bí ẩn, vì tông môn tranh đến vinh dự, sớm ngày tiến vào nội môn, mà không phải cho các ngươi, dùng để thu hút người khác ánh mắt, thu hoạch được sùng bái...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão mặt sắc trầm xuống."),t("br"),t("br"),n._v("Chức nghiệp cao thủ đến phổ thông câu lạc bộ, tự nhiên nghĩ đến, bày ra mấy tay, thu hoạch ánh mắt của người khác... Nhưng làm như vậy thực sự quá ngây thơ."),t("br"),t("br"),n._v("Vân Phi Dương, Hoàng Thao hai người hơi đỏ mặt."),t("br"),t("br"),n._v("“Lần sau không thể chiếu theo lệ này nữa, lần này cứ như vậy đi!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão khoát tay áo, đột nhiên phản ứng lại: “Trước kia mang đệ tử đi ra, cũng đều có loại tình huống này, không tính vi phạm tông môn quy định, làm sao vậy? Chẳng lẽ, xông cái gì họa?”"),t("br"),t("br"),n._v("Vân Phi Dương khó xử: “Bẩm báo trưởng lão, vừa rồi ta đi vào Thông Thần điện cùng người tỷ thí... Thi triển ra Lưu Thủy vô ngân kiếm chiêu!”"),t("br"),t("br"),n._v("“Lưu Thủy vô ngân? Đối phó Tinh Diệu thành tu luyện giả, còn cần đến chiêu này? Xem ra gặp được cao thủ!”"),t("br"),t("br"),n._v("Sửng sốt một chút, Lục Vân trưởng lão bưng lên một cái ấm trà, đem trước mắt rót đầy chén trà: “Chiêu này, là năm đó ta quan sát Lưu Thủy, trong lúc vô tình sáng lập ra, tuy là đơn giản, nhưng ẩn chứa đại đạo ở bên trong, tiến hành tu hành mười phần không dễ, ngươi có thể thi triển, cũng xem là tốt... Đương nhiên, có thể ép ngươi thi triển ra chiêu số này, đối phương cũng coi như có chút thực lực! Chiêu này thi triển đi ra, đối phương hẳn là triệt để không có cách nào, đẩy kiếm nhận thua đi!”"),t("br"),t("br"),n._v("Đem chén trà đặt ở bên miệng, uống một ngụm, Lục Vân trưởng lão ý cười đầy mặt."),t("br"),t("br"),n._v("Hắn đối với mình sáng lập ra chiêu số, có tuyệt đối tự tin, Tinh Diệu thành tu luyện giả mà thôi, thi triển ra chiêu này, khẳng định là sợ tới mức cúi đầu nhận thua, không dám tiếp tục nói nhảm."),t("br"),t("br"),n._v("Gặp trưởng lão như thế tự chăm sóc mình, Vân Phi Dương càng thêm khó xử, nói: “Ta thi triển ra chiêu này, hắn... Một kiếm đâm xuyên qua đầu của ta...”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Một miệng nước trà toàn bộ phun tại phía trước nhất mặt chữ quốc trên mặt, tôn lên mặt của hắn trở nên càng lớn, Lục Vân trưởng lão trợn mắt lên: “Ngươi nói cái gì?”"),t("br"),t("br"),n._v("“Ta... Cùng hắn vừa giao thủ, liền thi triển ra chiêu này, sau đó... Tại chỗ bị đâm xuyên đầu, chết!”"),t("br"),t("br"),n._v("Vân Phi Dương giải thích."),t("br"),t("br"),n._v("“Cái này, cái này... Làm sao có thể?”"),t("br"),t("br"),n._v("Lục Vân trưởng lão sắp điên rồi."),t("br"),t("br"),n._v("Lợi hại như thế phòng ngự tuyệt chiêu, bị một chiêu phá giải, còn đâm xuyên đầu? Thật hay giả?"),t("br"),t("br"),n._v("“Người kia am hiểu công kích?” Thấy đối phương không giống nói dối, Lục Vân trưởng lão vội nói."),t("br"),t("br"),n._v("“Vâng, năng lực tiến công đặc biệt mạnh!” Hoàng Thao gật đầu, hiện tại cũng lòng còn sợ hãi."),t("br"),t("br"),n._v("“Nếu là năng lực tiến công đặc biệt mạnh, chắc là dùng sai kiếm chiêu, Lưu Thủy vô ngân, phòng ngự là không tệ, nhưng đối mặt tuyệt đối tấn công, chỉ cố thủ mà nói, đồng dạng sẽ xuất hiện sơ hở, liền giống như rút đao đoạn thủy, nước tuy là tung tóe thân đao, có thể người sau, vẫn như cũ bị chém đứt...”"),t("br"),t("br"),n._v("Nghe được tấn công cực mạnh, Lục Vân trưởng lão từ từ khôi phục lại: “Đối phó loại người này, hẳn là chủ động tấn công, sử dụng ta sáng lập ra lưu tinh bước phối hợp phá trúc kiếm pháp, hẳn là có thể nhẹ nhõm tới đối kháng, phá giải bị động cục diện!”"),t("br"),t("br"),n._v("“Cái này... Bẩm báo trưởng lão, ta cũng cùng hắn giao thủ, vừa bắt đầu liền thi triển ra lưu tinh bộ pháp cùng phá trúc kiếm pháp...”"),t("br"),t("br"),n._v("Thân trúc trên mặt ửng hồng."),t("br"),t("br"),n._v("Cùng vị kia Thiên Nhai giao thủ, hắn vừa ra tay liền tấn công, dùng chính là hai cái này chiêu số."),t("br"),t("br"),n._v("Cũng là trước mắt vị trưởng lão này sáng lập ra."),t("br"),t("br"),n._v("“Thế nào? Có hay không thế như chẻ tre, một lần đem hắn đánh bại?” Lục Vân trưởng lão, lần nữa uống một hớp nước."),t("br"),t("br"),n._v("Thân trúc gãi đầu một cái: “Còn chưa tới đến trước mặt... Cũng bị bể đầu...”"),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Mặt chữ quốc trên người cũng ướt, tràn đầy u oán, mặt lớn hơn."),t("br"),t("br"),n._v("“Tấn công, phòng ngự, đều bị đối phương một kiếm chém giết?”"),t("br"),t("br"),n._v("Chén trà rơi trên mặt đất, cũng nhịn không được nữa, Lục Vân trưởng lão đột nhiên đứng dậy."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1939"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1941"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);