(window.webpackJsonp=window.webpackJsonp||[]).push([[1061],{1414:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1928-than-xa-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1928-than-xa-thu"}},[n._v("#")]),n._v(" Chương 1928: Thần xạ thủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Dẫn ta nhìn một chút Thông Thần điện ghi chép hình ảnh!”"),t("br"),t("br"),n._v("Biết mấy cái đệ tử, cùng một chỗ tới, không có khả năng nói dối, Lục Vân trưởng lão nói."),t("br"),t("br"),n._v("Vốn, loại địa phương nhỏ này Thông Thần điện, không thèm để ý, hiện tại bốn cái đệ tử, liên tục bị bể đầu ba cái, hơn nữa còn đều dùng hắn đắc ý nhất chiêu số, bất kể như thế nào, đều muốn nhìn một chút."),t("br"),t("br"),n._v("Nếu quả như thật chỉ dùng kiếm thiên tài, triệu hồi tông môn, làm chiêu sinh trưởng lão, tuyệt đối một cái công lớn."),t("br"),t("br"),n._v("Lấy ra trước đó mua sắm thông thần ngọc phù, năm người lần nữa đi vào Thông Thần điện, rất nhanh mua được trước đó chiến đấu hình ảnh."),t("br"),t("br"),n._v("Công khai thi đấu giao đấu, Thông Thần điện mỗi cuộc tỷ thí đều sẽ ghi chép, thuận tiện bán ra cùng thẩm tra."),t("br"),t("br"),n._v("Chỉ chốc lát, liền đem ba cái đệ tử cùng đối phương chiến đấu hình ảnh xem xong, Lục Vân trưởng lão rất nhanh hiểu được, nhìn về phía trước mặt bốn người, có chút chỉ tiếc mài sắt không nên kim: “Đối phương đây là phát hiện các ngươi chiêu số bên trong chỗ sơ hở, mới có thể mượn cơ hội thành công...”"),t("br"),t("br"),n._v("Vân Phi Dương đám người cúi đầu."),t("br"),t("br"),n._v("Bọn họ cũng biết là đạo lý này, có thể nghĩ muốn tránh né, muốn ngăn cản... Đối mặt một kiếm kia lúc, chính là không làm được!"),t("br"),t("br"),n._v("Kiếm chiêu thoạt nhìn đơn giản, chính là tiện tay ném ra, cắm vào trên đầu... Nhưng chân chính thi triển sau phong tình, tự mình trải nghiệm, mới có thể chân thực cảm nhận được."),t("br"),t("br"),n._v("“Vân Phi Dương Lưu Thủy vô ngân kiếm pháp, thi triển chính là tinh diệu, nhưng chiêu số ăn khớp bên trên, có vấn đề rất lớn, chỉ cần hơi hiểu kiếm thuật, liền có thể tìm tới sơ hở! Hồ bân Lưu Tinh bộ pháp cùng phá trúc kiếm pháp, thoạt nhìn thuần thục, thực tế đem tu vi đè thấp đến Thánh vực một tầng, phát huy không đến một phần mười uy lực, bị giết, cũng liền không trách được người khác!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Lục Vân trưởng lão nói: “Trở về đêm nay đều đừng nghỉ ngơi, đem cái này hai bộ kiếm pháp cùng bộ pháp, mỗi một cái đều luyện bên trên một trăm lần! Thiếu một lần, đừng đến gặp ta!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Bốn người đồng thời cúi đầu, trên mặt nhức nhối."),t("br"),t("br"),n._v("“Còn có, tỉ mỉ chú ý Thông Thần điện, một khi vị này Thiên Nhai đi lên, lập tức cho ta biết!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Lục Vân trưởng lão trên người sinh ra vô địch khí tức: “Tổn thương ta Lăng Vân kiếm các đệ tử, liền muốn rời đi? Nào có chuyện dễ dàng như vậy! Liền xem như mầm mống tốt, cũng phải trước tiên đem ngạo khí san bằng, cho hắn biết, kiếm các kiếm pháp, đồ sộ sâu xa, tuyệt không phải là hư danh!”"),t("br"),t("br"),n._v("“Là...” Nghe hắn nói như vậy, Vân Phi Dương đám người thở phào nhẹ nhõm, con mắt đồng thời sáng lên."),t("br"),t("br"),n._v("Không cần nghĩ cũng biết, trưởng lão đây là tính toán tự thân xuất thủ."),t("br"),t("br"),n._v("Lục Vân trưởng lão, mặc dù chỉ là chiêu sinh trưởng lão, bình thường chịu trách nhiệm ngoại môn đệ tử tu luyện, nhưng đối kiếm thuật hiểu, không thể khinh thường, không nói cái khác, cùng cấp bậc bọn họ, ba cái cũng chưa chắc là đối thủ."),t("br"),t("br"),n._v("Từ hắn tự thân ra tay, vị này Thiên Nhai, khẳng định muốn xui xẻo."),t("br"),t("br"),n._v("Không chỉ Lục Vân trưởng lão đám người, bảo vệ Thông Thần điện, chờ vị này Thiên Nhai xuất hiện, toàn bộ Tinh Diệu thành, thậm chí cấp dưới vô số ba cấp thành trấn bên trong cao thủ, cũng đều nghe được tin tức, từng cái tràn đầy hưng phấn."),t("br"),t("br"),n._v("“Đem Vân Phi Dương sư đệ, một chiêu đánh bại? Có chút ý tứ...”"),t("br"),t("br"),n._v("Huyền Giang thành phủ thành chủ, gỗ tử đàn trước bàn sách, thanh niên áo xám nhìn vừa mới biết tin tức, trong mắt ánh sáng không ngừng lóe lên."),t("br"),t("br"),n._v("Cái này, chính là Trương Huyền trước đó tại Hồng Nhạn thương hội nhìn thấy vị kia."),t("br"),t("br"),n._v("Lăng Vân kiếm các trong ngoại môn đệ tử đại sư huynh, Hoắc Giang Hà!"),t("br"),t("br"),n._v("“Giúp ta nhìn chằm chằm, vị này Thiên Nhai chỉ cần xuất hiện, bất luận ta đang làm gì, đều lập tức tới báo tin... Nông thôn chưa thấy qua việc đời gia hỏa mà thôi, vứt kiếm không xác định quá nhiều, cũng có thể gọi là kiếm thuật? Hừ, nhìn ta dốc hết sức phá đi!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, đứng dậy, lóe lên trong ánh mắt, kiếm khí lóe lên."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Đột nhiên, một đạo kiếm khí không khống chế được bắn đi ra, rơi vào trước bàn sách, tại chỗ đem bàn chém thành hai nửa!"),t("br"),t("br"),n._v("“Thay mới...”"),t("br"),t("br"),n._v("Hoắc Giang Hà dặn dò một tiếng, xoay người đi ra ngoài."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Ah! Nhẹ một chút?”"),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Một cái thanh thúy bạt tai vang lên, Tiết Cầm nhìn bị một chưởng đánh trèo nha hoàn, trong mắt tràn đầy lửa giận."),t("br"),t("br"),n._v("Để cái tên này cho bôi thuốc, lại dám làm đau bản thân, quả thực tội không thể tha."),t("br"),t("br"),n._v("“Tiểu thư...”"),t("br"),t("br"),n._v("Nha hoàn toàn thân run rẩy, vội vàng quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Cút ra ngoài!”"),t("br"),t("br"),n._v("Một chân đá vào nữ hài ngực, đem hắn từ gian phòng đạp ra ngoài, Tiết Cầm quay đầu nhìn về phía trong gương, đã sưng không tưởng nổi thanh tú khuôn mặt, tức giận đến không ngừng run rẩy, nồng đậm hận ý, tựa như thủy triều chảy xuôi: “Thiện Hiểu Thiên...”"),t("br"),t("br"),n._v("Bởi vì cùng như vậy một cái phế vật có hôn ước, từ nhỏ đến lớn đều nhận người khác khó chịu ánh mắt."),t("br"),t("br"),n._v("Thật vất vả trở thành Lăng Vân kiếm các ngoại môn đệ tử, nhận Hoắc Giang Hà sư huynh tán thưởng, kết quả... Lại bị biến thành như vậy!"),t("br"),t("br"),n._v("Cái phế vật này, chẳng lẽ chỉ thấy không chiếm được mình tốt?"),t("br"),t("br"),n._v("Nếu không phải là bởi vì thông gia, hắn đã sớm chết, sao có thể sống đến bây giờ!"),t("br"),t("br"),n._v("đăng nhập https://truyencuatui.ne"),t("br"),n._v("t/ để đọctruyện  Cho sống chi ân, không những không đi báo đáp, còn cùng một cái, không biết từ nơi nào xuất hiện xác ướp, để Hoắc sư huynh quất chính mình mặt... Mặt mũi ném vào, quả thực tội không thể tha!"),t("br"),t("br"),n._v("“Hắn phải chết! Cái kia băng gạc gói thành bánh chưng cũng phải chết! Còn có người hầu kia, Thiện Dịch, cũng phải chết...”"),t("br"),t("br"),n._v("Hàm răng cắn cục cục vang vọng, Tiết Cầm nhìn về phía một bên."),t("br"),t("br"),n._v("“Yên tâm đi, nhị tiểu thư, đã an bài! Bảo đảm để cho bọn họ không nhìn thấy ngày mai mặt trời...” Tiết Trầm đi vào."),t("br"),t("br"),n._v("“Vậy là tốt rồi!” Tiết Cầm thở phào nhẹ nhõm, lần nữa nhìn về phía mình trong gương: “Lão sư, ngươi nói mặt của ta, sẽ không lưu sẹo a? Bị thương thành như vậy, Hoắc sư huynh chẳng lẽ ghét bỏ, lại đối ta không có hứng thú đi...”"),t("br"),t("br"),n._v("“Tiểu thư không cần quấy nhiễu, đây là Hoắc sư huynh phái người đưa tới thuốc trị thương, bôi lên ở trên mặt, rất nhanh liền có thể khôi phục...”"),t("br"),t("br"),n._v("Tiết Trầm cười cười, đưa tới một cái bình ngọc."),t("br"),t("br"),n._v("“Sư huynh cho?” Tiết Cầm con mắt sáng lên: “Xem ra, hắn cũng không ghét bỏ ta...”"),t("br"),t("br"),n._v("Nghe nàng giọng nói, vị này Hoắc sư huynh có hay không ghét bỏ, nàng dung mạo có hay không mỹ lệ, so Thiện Hiểu Thiên tính mạng trọng yếu hơn."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cộc cộc cộc!"),t("br"),t("br"),n._v("Xe ngựa tại trên đường phố nhanh chóng tiến lên."),t("br"),t("br"),n._v("Từ Hồng Nhạn thương hội đi ra, Dịch lão liền cảm nhận được không hiểu đè nén, dường như có nồng đậm nguy hiểm, bất cứ lúc nào cũng sẽ xuất hiện."),t("br"),t("br"),n._v("Nguyên nhân chính là có loại cảm giác này, không dám có ngừng lại, tăng tốc xe ngựa, nhanh chóng hướng phủ đệ chạy như điên."),t("br"),n._v(" Sưu sưu sưu!"),t("br"),t("br"),n._v("Đúng lúc này, liên tiếp phá không âm thanh gấp rút vang lên, vô số mũi tên, từ hai bên đường, bắn tới."),t("br"),t("br"),n._v("Mũi tên bình thường, đối cường giả Thánh vực không có tác dụng gì, nhưng đây đều là đặc thù mũi tên, mỗi một cây bắn ra, có thể so với Thánh vực cửu trọng cường giả một đòn, nhiều như vậy đồng thời bay tới, Đại Thánh cường giả đều không chống đỡ được."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Cảm nhận được mũi tên tiếng xé gió, Thiện Hiểu Thiên biết đối phương động thủ, nắm đấm xiết chặt, nhìn về phía thanh niên trước mắt."),t("br"),t("br"),n._v("“Không được phân tâm, ngươi bây giờ cần phải làm là lĩnh ngộ ta cho ngươi truyền thụ cho kiếm quyết cùng kiếm pháp!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Vâng!” Gặp lão sư nói như vậy, biết chắc có biện pháp, Thiện Hiểu Thiên không tại nhiều nói, không ngừng nhớ lại, lão sư nói qua chương trình học, đối kiếm thuật hiểu, cũng đang bay vọt thăng cấp."),t("br"),t("br"),n._v("“Muốn thương tổn thiếu gia, ta và các ngươi liều mạng...”"),t("br"),t("br"),n._v("Hai người đối thoại, Dịch lão cũng không nghe được, nhìn thấy đầy trời mũi tên, cũng lại kìm nén không được, hét lớn một tiếng, nhấc lên roi ngựa trong tay, vung vẩy hai tay, như muốn toàn bộ chặn lại, không được làm bị thương thiếu gia."),t("br"),t("br"),n._v("Hắn biết mình thực lực, tuy là Thánh vực cửu trọng, có thể những năm này vì chăm sóc thiếu gia, thân thể thiếu hụt, lại thêm năm đó cũng bị trọng thương, có thể phát huy ra Thánh vực bát trọng lực lượng là tốt lắm rồi, đối diện với mấy cái này mũi tên, khả năng liền mấy hơi thở cũng đỡ không nổi."),t("br"),t("br"),n._v("Nhưng, vậy thì thế nào?"),t("br"),t("br"),n._v("Có lẽ thời gian lâu như vậy, thiếu gia liền có thể trốn thoát."),t("br"),t("br"),n._v("Ô ô!"),t("br"),t("br"),n._v("Cả người như là điên cuồng, đứng dậy, đang nghĩ ngợi roi ngựa không ngăn được, dùng thân thể cũng phải chặn lại, chỉ thấy nhanh chóng phóng tới mũi tên, lướt qua xe ngựa liền bay đi."),t("br"),t("br"),n._v("Chung quanh bắn tới đều giống nhau, một mảng lớn mũi tên, vậy mà không có một cái rơi vào trên xe ngựa, thậm chí trước mặt ngựa, đều liền lông tơ không có làm bị thương một cái."),t("br"),t("br"),n._v("“Cái này... Là sát thủ?”"),t("br"),t("br"),n._v("Nháy con mắt, Dịch lão choáng váng."),t("br"),t("br"),n._v("Vốn cho rằng tai kiếp khó thoát, hẳn phải chết không nghi ngờ, không nghĩ tới bắn nhiều như vậy tiễn, một cái đều không có bắn tới, liền giống như những này kiếm, cố ý tránh ra xe ngựa đồng dạng..."),t("br"),t("br"),n._v("Tại sao có thể có như vậy sát thủ?"),t("br"),t("br"),n._v("Không chỉ hắn bối rối, chịu trách nhiệm chặn giết Thiện Hiểu Thiên thủ lĩnh, cũng có chút nghi ngờ nhân sinh."),t("br"),t("br"),n._v("“Cho ta nhắm chuẩn chút!”"),t("br"),t("br"),n._v("Nhịn không được, hét lớn một tiếng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Bên người hơn mười thuộc hạ, lần nữa dựng cung, mũi tên nhanh chóng bắn ra."),t("br"),t("br"),n._v("Ô ô ô!"),t("br"),t("br"),n._v("Sau một khắc, chuyện kỳ quái lần nữa phát sinh, mọi người giống như là bị che lại con mắt đồng dạng, mũi tên lần nữa xẹt qua một cái hình cung, vòng qua xe ngựa."),t("br"),t("br"),n._v("Phốc! Phốc! Phốc!"),t("br"),t("br"),n._v("Lần này không có may mắn như vậy, toàn bộ xuất tại bốn phía mai phục trên thân mọi người, từng cái trên đầu cắm mũi tên, tựa như đóng lấy nhang lớn."),t("br"),t("br"),n._v("“Ta không tin bắn không đến, tiếp tục bắn cho ta...”"),t("br"),t("br"),n._v("Lại ra lệnh một tiếng."),t("br"),t("br"),n._v("Lại một nhóm người, nằm ở trên mặt đất."),t("br"),t("br"),n._v("“Tiếp tục...”"),t("br"),t("br"),n._v("“Lão đại, không thể lại tiếp tục, lại tiếp tục, chúng ta đều sẽ bị bắn chết...”"),t("br"),t("br"),n._v("Một cái thuộc hạ thanh âm bên trong mang theo tiếng khóc nức nở."),t("br"),t("br"),n._v("Thủ lĩnh vội vàng quay đầu, lúc này mới phát hiện, bên người thuộc hạ đã chết gần hết rồi, mỗi một cái đều là trên đầu cắm một cái kiếm, không nghiêng lệch, mười phần tinh chuẩn."),t("br"),t("br"),n._v("Bốn phía bày ra mai phục, cũng mất động tĩnh, hiển nhiên, đã bị bản thân đám người này bắn không sai biệt lắm, mà phía bên mình, cũng bị đối phương bắn không có còn mấy cái."),t("br"),t("br"),n._v("Ta muốn bắn chính là xe ngựa, bắn chính là Thiện Hiểu Thiên..."),t("br"),t("br"),n._v("Bắn người một nhà làm cái quỷ gì?"),t("br"),t("br"),n._v("Khí run rẩy, thủ lĩnh sắp điên rồi."),t("br"),t("br"),n._v("Cũng lại kìm nén không được, tiện tay cầm lấy một cái thuộc hạ cung tên, nhắm chuẩn Dịch lão trái tim, bắn tới."),t("br"),t("br"),n._v("Hắn là Đại Thánh nhị trọng cường giả, bắn ra trường tiễn, bí mật mang theo lấy chân khí, lưu tinh, gào thét mà tới."),t("br"),t("br"),n._v("Dịch lão đồng tử co rụt lại, thân thể cứng đờ, sau đó, lần nữa phát hiện... Từ bên cạnh sát qua."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Kêu thảm một tiếng, mới vừa rồi còn có người không chết hết, giờ phút này, treo."),t("br"),t("br"),n._v("“Làm sao cái tình huống...”"),t("br"),t("br"),n._v("Lần nữa dựng cung, lần này cũng không nhắm chuẩn Dịch lão, mà là hướng về phía bầu trời bắn ra ngoài."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Hai cái hô hấp phía sau, bên cạnh một tiếng kêu thảm, dường như lại có cái may mắn còn sống sót gia hỏa, lần nữa trúng chiêu."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Thủ lĩnh nắm tóc, lại phân biệt hướng sau lưng, bắn hai mũi tên..."),t("br"),t("br"),n._v("Sau đó, lại có hai tiếng kêu thảm."),t("br"),t("br"),n._v("“Ta ngày...”"),t("br"),t("br"),n._v("Thủ lĩnh điên rồi."),t("br"),t("br"),n._v("Hắn không am hiểu bắn tên, nguyên nhân chính là như vậy, lần này đều không có cung tên tới, lúc nào, cũng thành thần tiễn thủ, chỉ cần tuỳ bắn, liền có thể cắm người một nhà?"),t("br"),t("br"),n._v("Quan trọng đằng sau Quỷ Ảnh đều không có, tiễn còn có thể bản thân vượt qua ra, không nghiêng lệch, đều cắm vào thuộc hạ trên đầu..."),t("br"),t("br"),n._v("Thật ngày chó..."),t("br"),t("br"),n._v("Không đúng, ngày chó, cũng cần phân rõ đầu vẫn là cái mông, còn cần nhắm chuẩn, cũng không có tinh như vậy xác thực đi!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1940"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1942"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);