(window.webpackJsonp=window.webpackJsonp||[]).push([[1063],{1420:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1930-tao-thanh-lap-khiep-so"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1930-tao-thanh-lap-khiep-so"}},[n._v("#")]),n._v(" Chương 1930: Tào Thành Lập khiếp sợ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Vốn cho rằng lần này thuê sơn tặc, nhất định dễ như trở bàn tay, đem một cái người sa cơ thất thế, nhẹ nhõm chém giết, đem nhận khuất nhục, nhẹ nhõm vãn hồi..."),t("br"),t("br"),n._v("Không nghĩ tới người tất cả đều chết rồi, hơn nữa, đầu lĩnh cũng hình như điên rồi..."),t("br"),t("br"),n._v("Giết ta người, ngựa đấy! Còn xe..."),t("br"),t("br"),n._v("Ngươi thế nào không nói sĩ, pháo, tượng đâu?"),t("br"),t("br"),n._v("Nói chuyện cẩn thận, cùng ta chơi cờ tướng có phải không?"),t("br"),t("br"),n._v("“Ta hỏi ngươi ai giết ngươi người!” Cũng lại kìm nén không được, Tiết Trầm hỏi."),t("br"),t("br"),n._v("“Ta không phải đã nói rồi sao? Là một con ngựa, còn có... Một chiếc xe!” Thủ lĩnh cái bụng sắp nổ tung."),t("br"),t("br"),n._v("“Ngựa?”"),t("br"),t("br"),n._v("“Xe?”"),t("br"),t("br"),n._v("Tiết Trầm cùng Tiết Cầm càng thêm mê mang."),t("br"),t("br"),n._v("“Thiện Hiểu Thiên chiếc xe ngựa kia, ngựa nắm giữ Đại Thánh nhất trọng thực lực, dễ như trở bàn tay liền đem thuộc hạ của ta giết chết, chiếc xe kia cũng rất lợi hại, chúng ta tên bắn ra một cái đều không thể tới gần...”"),t("br"),t("br"),n._v("Biết rất phóng đãng, thủ lĩnh vẫn là đem vừa rồi chuyện phát sinh, giải thích cặn kẽ một lần."),t("br"),t("br"),n._v("“Biết võ ngựa? Sẽ võ kỹ xe?”"),t("br"),t("br"),n._v("Sắc mặt tái xanh, Tiết Trầm cho rằng trí thông minh bị người đè xuống đất cọ xát: “Ngươi coi như không muốn giết người, cũng tìm lý do hợp lý đi!”"),t("br"),t("br"),n._v("“Ngươi... Ngươi cho rằng ta đây là đang gạt ngươi?”"),t("br"),t("br"),n._v("Thủ lĩnh điên rồi."),t("br"),t("br"),n._v("Vì sao nói ra nói thật, chính là không có người tin tưởng đâu?"),t("br"),t("br"),n._v("“Tốt, đây là một trương thông thần thẻ, bên trong có năm trăm thông thần tệ, đủ bù đắp, tổn thất của ngươi...” Chẳng muốn cùng cái này đã có chút bệnh tâm thần gia hỏa tiếp tục nói nhảm, Tiết Trầm tiện tay ném ra một cái thẻ."),t("br"),t("br"),n._v("Đối phương đơn giản là muốn đòi tiền mà thôi, cho là được!"),t("br"),t("br"),n._v("“Ta hơn ba mươi huynh đệ, ngươi liền cho năm trăm?”"),t("br"),t("br"),n._v("Da mặt lắc một cái, thủ lĩnh sắp tức điên."),t("br"),t("br"),n._v("Chút tiền ấy, những cái kia mũi tên, cung tên, đều không đủ mua."),t("br"),t("br"),n._v("“Không hoàn thành nhiệm vụ, năm trăm đã là nhiều, cút ngay, nếu không, một phân tiền ngươi cũng không chiếm được!”"),t("br"),t("br"),n._v("Tiết Trầm híp mắt lại."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thấy đối phương sát ý sôi trào, biết làm không cẩn thận thật sẽ ra tay, thủ lĩnh rõ ràng hảo hán không ăn thiệt thòi trước mắt, nắm lên thẻ, xoay người rời đi, chỉ chốc lát liền biến mất tại đêm tối."),t("br"),t("br"),n._v("“Lão sư, cái tên này không giết chết làm sao bây giờ?”"),t("br"),t("br"),n._v("Tiết Cầm nhìn qua."),t("br"),t("br"),n._v("“Một đám phế vật vô dụng! Dùng bọn họ chỉ là muốn miễn trừ phiền phức mà thôi!” Tiết Trầm hừ một tiếng: “Về trước đi, ta tự sẽ nghĩ biện pháp! Nhị tiểu thư yên tâm, cái tên này tuyệt đối sẽ không trở thành ngươi chướng ngại vật! Làm xong, còn có thể thành tựu danh tiếng của ngươi, nhất phi trùng thiên!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thủ lĩnh càng nghĩ càng giận, cho rằng cả người đều sắp muốn nổ."),t("br"),t("br"),n._v("Nếu không phải lòng tham nhất thời, cũng không đến mức toàn quân bị diệt, đến bây giờ, chỉ còn lại có cô độc."),t("br"),t("br"),n._v("Một đường phi nhanh, tốc độ cực nhanh, còn không có trở lại sơn trại, liền nghe đến một cái thanh âm nhàn nhạt, tại cách đó không xa vang lên."),t("br"),t("br"),n._v("https://ngantruyen.com/"),t("br"),n._v("“Chết nhiều huynh đệ như vậy, năm trăm thông thần tệ liền đuổi?”"),t("br"),t("br"),n._v("“Ai?”"),t("br"),t("br"),n._v("Xiết chặt trường đao, vội vàng xoay người, thủ lĩnh liên tục dạo qua một vòng, cũng không phát hiện nửa cái bóng người."),t("br"),t("br"),n._v("“Ai ở đây giả thần giả quỷ? Là hảo hán đi ra, ta Tào Thành Lập, một chút nhíu mày, liền không xứng làm đại thủ lĩnh nhiều năm như vậy!”"),t("br"),t("br"),n._v("“Không cần thối lại, tại ngươi ngay phía trước trên chạc cây!”"),t("br"),t("br"),n._v("Thanh âm nhàn nhạt tiếp tục vang lên."),t("br"),t("br"),n._v("Thủ lĩnh Tào Thành Lập vội vàng ngẩng đầu, mượn ánh trăng, nhìn thấy cách đó không xa trên chạc cây, một quyển sách dựng tại phía trên, không ngừng giãy dụa thân thể, tựa như thành tinh."),t("br"),t("br"),n._v("Thanh âm mới vừa rồi chính là từ bên trong mà ra."),t("br"),t("br"),n._v("“Ngươi... Ngươi... Nói chuyện với ta?”"),t("br"),t("br"),n._v("Hàm răng có chút run lên, Tào Thành Lập bờ môi run rẩy."),t("br"),t("br"),n._v("Không giũ không được ah, đêm nay thấy được sẽ chuyển biến tiễn, có thể giết người ngựa, biết võ xe, vốn cho rằng liền đủ ác mộng, không nghĩ tới lại xuất hiện một cái biết nói chuyện sách."),t("br"),t("br"),n._v("“Là ta!” Thư tịch nhảy một cái."),t("br"),t("br"),n._v("“Ta muốn giết ngươi...”"),t("br"),t("br"),n._v("Gào lên một tiếng, Tào Thành Lập trường đao trong tay bỗng nhiên ném ra ngoài, thân thể không tự chủ được hướng ngược lại chạy trốn."),t("br"),t("br"),n._v("Hiện tại đã không quản được nhiều như vậy, chạy trốn mới là vương đạo."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn không có rời đi tại chỗ, dưới chân đột nhiên một cái lảo đảo, một đầu ngã xuống đất, ngay sau đó nhìn thấy vừa rồi trên chạc cây quyển sách kia, chẳng biết lúc nào đã đi tới trước mặt, yên tĩnh lơ lửng giữa không trung."),t("br"),t("br"),n._v("“Muốn chết nhanh một chút, liền chạy!”"),t("br"),t("br"),n._v("Kèm theo nhàn nhạt lời nói, một cỗ nồng đậm uy áp, từ sách bên trong nghiền ép mà ra, Tào Thành Lập vẻ mặt cứng đờ, tựa như rơi vào hầm băng, không tự chủ được run rẩy: “Không dám...”"),t("br"),t("br"),n._v("Cỗ khí tức này nghiền ép xuống, hắn nhất thời rõ ràng, quyển sách này thực lực, tuyệt đối đạt đến Cổ Thánh, thậm chí còn là đỉnh phong nhất... Phá toái hư không!"),t("br"),t("br"),n._v("Một bản nắm giữ phá toái hư không sách?"),t("br"),t("br"),n._v("Trước đó còn nghĩ trốn, hiện tại đã biết rõ, dùng ra tất cả thủ đoạn đều trốn không thoát."),t("br"),t("br"),n._v("“Không dám là tốt rồi!”"),t("br"),t("br"),n._v("Thư tịch lắc lư một cái: “Vừa rồi những người kia cố ý giấu diếm tình báo, để ngươi giết Thiện Hiểu Thiên, vô duyên vô cớ tổn thất nhiều người như vậy, chẳng lẽ không muốn báo thù?”"),t("br"),t("br"),n._v("“Muốn... Ta hận không thể đem bọn hắn chém thành muôn mảnh!”"),t("br"),t("br"),n._v("Tào Thành Lập khuôn mặt vặn vẹo."),t("br"),t("br"),n._v("“Muốn giết người, cũng nên biết thân phận a, ngươi biết thân phận của bọn hắn ư?” Thư tịch hỏi."),t("br"),n._v("“Ta...” Tào Thành Lập sửng sốt."),t("br"),t("br"),n._v("Hắn chỉ biết là đối phương là người quen giới thiệu, có tiền có thể kiếm, cụ thể thân phận cũng không hiểu rõ tình hình."),t("br"),t("br"),n._v("“Hẳn là còn chưa đi xa, theo dõi đi lên...” Thư tịch cười cười: “Làm một cái sơn tặc đầu lĩnh, theo dõi thuận tiện ghi chép loại sự tình này, không khó lắm đi!”"),t("br"),t("br"),n._v("“Ta đã biết...”"),t("br"),t("br"),n._v("Tào Thành Lập gật gật đầu."),t("br"),t("br"),n._v("Hắn là người thông minh, làm sao không rõ ràng."),t("br"),t("br"),n._v("Quyển sách này, cùng con ngựa kia, chiếc xe kia đồng dạng, hẳn là đều thuộc về Thiện Hiểu Thiên, sở dĩ không giết bản thân, khẳng định là cho rằng còn có tác dụng."),t("br"),t("br"),n._v("“Đi đi!”"),t("br"),t("br"),n._v("Thư tịch lắc lư một cái."),t("br"),t("br"),n._v("Tào Thành Lập cắn răng một cái, xoay người hướng vừa rồi lửa trại ra bay lượn mà đi."),t("br"),t("br"),n._v("Tu vi của hắn tuy là còn kém rất rất xa Tiết Trầm đám người, nhưng chuyện xấu làm nhiều rồi, hiểu tiềm hành cùng theo dõi phương pháp, đi theo dấu chân, thời gian không dài, đi tới một cái to lớn phủ đệ trước mặt."),t("br"),t("br"),n._v("“Phủ thành chủ? Là phủ thành chủ, bỏ tiền để cho ta giết người?”"),t("br"),t("br"),n._v("Trợn mắt lên, Tào Thành Lập khó tin."),t("br"),t("br"),n._v("Thành chủ tiết nham, thanh danh vô cùng tốt, từ trước tới nay làm việc công chính, làm sao sẽ làm ra... Mua hung giết người sự tình?"),t("br"),t("br"),n._v("Thân thể run rẩy, rơi vào đầu tường, vừa định đi vào, ngay sau đó liền thấy trong tường, đồng loạt đứng một đám người, mới vừa rồi cùng gặp mặt hắn hai cái, đang đứng tại đám người phía trước."),t("br"),t("br"),n._v("Đè thấp hô hấp, Tào Thành Lập mượn bóng đêm, đem bản thân hoàn mỹ che giấu."),t("br"),t("br"),n._v("Đứng ở phía trước, là một đám người mặc khôi giáp binh sĩ, vừa nhìn liền biết là phủ thành chủ tinh nhuệ nhất quân đội."),t("br"),t("br"),n._v("“Đem những y phục này đổi đi, nghe mệnh lệnh, đi giết người!”"),t("br"),t("br"),n._v("Trung niên nhân thanh âm trầm thấp vang lên, ngay sau đó một binh sĩ, ôm một đống lớn quần áo đi tới."),t("br"),t("br"),n._v("“Vâng!” Rất nhiều binh sĩ, nhận lấy quần áo, từng cái đổi ở trên người, nhìn rõ ràng quần áo bộ dáng, Tào Thành Lập suýt chút nữa không có từ trên tường rơi xuống."),t("br"),t("br"),n._v("Đúng là hắn sơn tặc trang phục!"),t("br"),t("br"),n._v("Đường đường phủ thành chủ tinh nhuệ binh sĩ, vậy mà giả mạo bọn họ sơn tặc, đi giết người!"),t("br"),t("br"),n._v("Không phải tận mắt nhìn thấy, cũng không dám tin tưởng!"),t("br"),t("br"),n._v("“Để cho ta giết người lại là phủ thành chủ...”"),t("br"),t("br"),n._v("Thân thể cứng ngắc, Tào Thành Lập thở mạnh cũng không dám, dùng ghi chép ngọc tinh, đem một màn trước mắt toàn bộ ghi xuống."),t("br"),t("br"),n._v("Rất nhanh, phủ thành chủ binh sĩ, ngụy trang thành sơn tặc lặng lẽ rời đi sân nhỏ, hướng Thiện gia phương hướng chạy như điên mà đi."),t("br"),t("br"),n._v("Rất mau tới đến Thiện gia ngoài tường."),t("br"),t("br"),n._v("“Xông đi vào, một tên cũng không để lại!”"),t("br"),t("br"),n._v("Binh sĩ thủ lĩnh, hạ giọng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người đồng loạt nhảy vào sân trong."),t("br"),t("br"),n._v("Tào Thành Lập theo sát trên đó, leo đến trên một cây đại thụ, mượn ánh trăng nhìn vào bên trong, ngay sau đó liền thấy để hắn cả đời khó quên một màn."),t("br"),t("br"),n._v("Một thiếu niên đứng ở trong viện, trong tay nắm một thanh trường kiếm, nhẹ nhàng lắc một cái, liền có một người đầu rơi trên mặt đất, cả viện tiếng gió rít gào, kiếm khí ngang dọc."),t("br"),t("br"),n._v("Không đến ba phút, phủ thành chủ xông vào năm mươi cái tinh nhuệ, liền toàn bộ bị một người một kiếm tuỳ tiện giết chết."),t("br"),t("br"),n._v("“Hắn chính là Thiện Hiểu Thiên? Hắn không có tu vi?”"),t("br"),t("br"),n._v("Vẻ mặt trắng bệch, Tào Thành Lập cho rằng ngực cuồn cuộn, tùy thời đều sắp muốn phun: “Người của phủ thành chủ, ta thao đại gia mày...”"),t("br"),t("br"),n._v("May mắn trước đó, chỉ là đối phương ngựa cùng xe xuất thủ, nếu không, vị này Thiện Hiểu Thiên ra tay, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("Khả năng đầu trong nháy mắt liền dọn nhà."),t("br"),t("br"),n._v("Kiếm pháp của đối phương, hóa phức tạp thành đơn giản, cao thâm khó dò, hắn từ nhỏ đến lớn, đều không có gặp qua, khủng bố như vậy!"),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Đem tất cả mọi người giết chết, chỉ thấy thiếu niên Thiện Hiểu Thiên tràn đầy khó tin đi tới nhà trước mặt, Tào Thành Lập lúc này mới phát hiện, trước gian phòng mặt, dĩ nhiên thẳng đến ngồi cái thanh niên, hắn từ đầu đến cuối, thế mà cũng không phát hiện!"),t("br"),t("br"),n._v("Thanh niên này hai mươi tuổi hình dáng, nhìn thấy thiếu niên đi tới trước mặt, đặt chén trà xuống, cau mày: “Liền mấy người này, thế mà giết hơn hai phút, bình quân ba cái hô hấp một cái, điểm ấy năng lực lĩnh ngộ, để cho ta làm sao đem cao thâm hơn kiếm thuật truyền thụ? Phạt ngươi tối nay một mực luyện kiếm, không được đi ngủ!”"),t("br"),t("br"),n._v("“Vâng!” Thiếu niên cúi đầu xuống, một mặt xấu hổ."),t("br"),t("br"),n._v("“Ba cái hô hấp giết một người, còn muốn chịu phạt?” Tào Thành Lập run rẩy."),t("br"),t("br"),n._v("Thiếu niên thiên tài, hắn gặp qua không ít, Thiện Hiểu Thiên như vậy tuyệt đối hàng bên trên đỉnh phong nhất, lại còn muốn chịu phạt, bị mắng... Vị lão sư này, cũng quá nghiêm khắc đi!"),t("br"),t("br"),n._v("Ngay tại khiếp sợ, liền thấy vừa rồi đưa hắn thuộc hạ giết đến sợ chết khiếp ngựa, “Tách tách!” Đi tới, một mặt ý cười, cho người thanh niên kia lão sư bưng trà rót nước... Thậm chí còn giơ lên hai cái móng trước, hỗ trợ đấm lưng..."),t("br"),t("br"),n._v("Hàm răng run lên, Tào Thành Lập cảm thấy mình thế giới quan có chút sụp đổ."),t("br"),t("br"),n._v("Cái này mẹ nó... Là ngựa?"),t("br"),t("br"),n._v("Ngựa có thể làm như vậy?"),t("br"),t("br"),n._v("“Thiện gia về sau tuyệt không thể ra, nói dễ nghe đi nữa đều không được...”"),t("br"),t("br"),n._v("Trong lòng làm cái quyết định, vừa định lặng lẽ rời đi, liền nghe đến một cái thanh âm nhàn nhạt ở bên tai vang lên."),t("br"),t("br"),n._v("“Nên nói cái gì, nên làm cái gì, ngươi hẳn phải biết đi?”"),t("br"),t("br"),n._v("Thanh âm này mười phần dịu dàng, nhưng mà nghe vào trong tai, lại làm cho hắn lông tơ nổ lên, vội vàng ngẩng đầu, ngay sau đó nhìn thấy ngồi tại gian phòng phía trước, đang uống trà thanh niên, cười khanh khách nhìn lại, ánh mắt hình như xuyên thấu qua đêm tối cùng che giấu, rõ ràng tìm được hắn ẩn thân vị trí."),t("br"),t("br"),n._v("“Ta, ta...”"),t("br"),t("br"),n._v("Tào Thành Lập nói không ra lời."),t("br"),t("br"),n._v("“Không cần khẩn trương, ngày hôm nay chuyện phát sinh, ngươi tất cả đều thấy được, thế nào đi nói, hẳn là rõ ràng...” Thanh niên bờ môi không động, nhưng âm thanh nhưng rõ ràng ở bên tai vang lên."),t("br"),t("br"),n._v("“Ta biết...” Tào Thành Lập run rẩy."),t("br"),t("br"),n._v("“Đi đi! Ngươi cũng là nhận ủy thác của người, hết lòng vì việc người khác, cho nên, ta không giết ngươi... Bất quá, từ hôm nay trở đi, nếu để cho ta biết ngươi lại làm xằng làm bậy, cẩn thận khó giữ được tính mạng...” Thanh niên âm thanh tiếp tục vang lên."),t("br"),t("br"),n._v("“Vâng!” Tào Thành Lập không dám tiếp tục nói nhiều, xoay người bỏ chạy."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1942"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1944"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);