(window.webpackJsonp=window.webpackJsonp||[]).push([[1066],{1421:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1933-tiet-cam-cha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1933-tiet-cam-cha"}},[n._v("#")]),n._v(" Chương 1933: Tiết Cầm cha")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương trước trở về mục lục chương sau trở về trang sách"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Tiết Cầm suýt chút nữa tức điên."),t("br"),t("br"),n._v("Đường đường phủ thành chủ Nhị tiểu thư, minh châu nhân vật, làm nha hoàn... Quả thực chính là làm nhục!"),t("br"),t("br"),n._v("“Nàng không phải ta hạ nhân, ta không có quyền làm chủ!”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Hoắc Giang Hà đạo."),t("br"),t("br"),n._v("“Vậy thì không có biện pháp, trước đó tới qua ba cái dùng kiếm, đều bị ta một chiêu đâm chết, cảm giác cùng giết gà không có gì khác nhau, ngươi hẳn là cũng không sai biệt lắm, không thể so với cũng thế!”"),t("br"),t("br"),n._v("Trương Huyền duỗi lưng một cái, ngừng lại: “Nếu ngươi không được chủ, quên đi...”"),t("br"),t("br"),n._v("Hàn mang chợt lóe, Hoắc Giang Hà híp mắt lại: “Thân là cao thủ sử dụng kiếm, làm sao có thể lật lọng?”"),t("br"),t("br"),n._v("“Nữ nhân này, vừa rồi xem thường ta, để cho ta trong lòng ấm ức, uất khí khó tiêu, không cho nàng làm nha hoàn, nhẫn không đi xuống cơn giận này! Trong lòng tức giận, kiếm thuật liền không cách nào triệt để phát huy ra, giao đấu cũng liền không còn ý nghĩa... Trọng yếu như vậy sự tình, sao có thể nói ra trở mặt đây?”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền mang theo làm đối thủ suy nghĩ cao thượng đức tính: “Ta làm như vậy, cũng là vì có thể tốt hơn phát huy ra mạnh nhất sức chiến đấu, thi triển ra cường đại nhất kiếm thuật, cho làm đối thủ ngươi, lớn nhất tôn trọng! Ngươi một vị cao thủ sử dụng kiếm... Muốn cùng ta tỷ thí, nhất định là muốn thắng mạnh nhất ta, mà không phải muốn thừa lúc vắng mà vào... Như vậy cho dù thắng cũng không vẻ vang a!”"),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Hoắc Giang Hà hàm răng cắn chặt."),t("br"),t("br"),n._v("Nhìn cái tên này một bộ thổi ngưu bức mà không đỏ mặt hình dáng, làm sao cảm giác nghĩ như vậy đánh người đây?"),t("br"),t("br"),n._v("Thế nào, không đồng ý ngươi vụ cá cược này, ta là được bắt nạt người, coi như chiến thắng cũng thắng mà không vẻ vang gì?"),t("br"),t("br"),n._v("“Sư huynh, ngàn vạn không thể đáp ứng...”"),t("br"),t("br"),n._v("Nghe cái tên này, nói càng ngày càng kéo, Tiết Cầm sợ tới mức vẻ mặt trắng bệch."),t("br"),t("br"),n._v("“Không đáp ứng, quên đi, ta đồng dạng sẽ đáp ứng tỷ thí, suy yếu liền suy yếu a! Dù sao, ngươi chỉ là muốn thắng ta, có công bằng hay không, cũng không trọng yếu...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền khí phách tiêu điều: “Lăng Vân kiếm các cường giả, cùng người so kiếm, vậy mà không quan tâm công bằng, ta cũng coi như thêm kiến thức...”"),t("br"),t("br"),n._v("“Đủ, ta đáp ứng!”"),t("br"),t("br"),n._v("Hoắc Giang Hà bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("Tuy là cái tên này nói đều là không thấy sự tình, nhưng đã đại biểu Lăng Vân kiếm các, đối phương cũng Rõ ràng đoán được, vậy thì công bằng công chính, tuyệt không thể có nửa điểm chỉ trích!"),t("br"),t("br"),n._v("“Sư huynh...”"),t("br"),t("br"),n._v("Tiết Cầm run rẩy."),t("br"),t("br"),n._v("Nàng nhưng mà thành chủ Nhị tiểu thư, thiên chi kiêu tử... Có khả năng đi vào Kiếm Các nhất phi trùng thiên... Bị làm nha hoàn đánh cược, quả thực làm nhục tới cực điểm."),t("br"),t("br"),n._v("Chẳng qua, thanh âm vừa mới vang lên, liền nghe đến trước mắt Hoắc Giang Hà quát tháo tiếng vang lên: “Ngậm miệng! Ngươi là cảm thấy ta không thắng được?”"),t("br"),t("br"),n._v("“Ta...” Tiết Cầm khuôn mặt một trắng: “Không dám... Sư huynh mạnh như thế người, chỉ cần ra tay, nhất định thắng ngay từ trận đầu...”"),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Chẳng muốn tiếp tục nói nhiều, bàn tay một trảo, Tiết Cầm trong ngực ôm trường kiếm, xuất hiện tại lòng bàn tay, Hoắc Giang Hà sợ nói nhiều vài câu, sẽ bị tức chết, bàn chân một chút, đã rơi vào phía trên võ đài, vẫy tay một cái: “Xin mời!”"),t("br"),t("br"),n._v("“Sảng khoái!”"),t("br"),t("br"),n._v("Cười ha ha một tiếng, Trương Huyền theo sát tới."),t("br"),t("br"),n._v("“Ngươi mấy lần trước, cùng ta sư đệ chiến đấu, dùng ra một chiêu ném kiếm kiếm pháp, ta nghĩ mở mang kiến thức một chút chiêu này uy lực!”"),t("br"),t("br"),n._v("Trường kiếm xuất khiếu, trên không trung phát ra tiếng long ngâm, Hoắc Giang Hà đạo."),t("br"),t("br"),n._v("Đã muốn bù lại tông môn mặt mũi, đương nhiên là tại đối phương mạnh nhất kiếm thuật bên dưới, đem hắn đánh bại."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, đi tới giá binh khí trước mặt, lần nữa lấy ra một thanh trường kiếm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Trưởng lão, trưởng lão, vị kia Thiên Nhai xuất hiện...”"),t("br"),t("br"),n._v("Tinh diệu thành, phủ thành chủ, Vân Phi Dương vội vã đi tới gian phòng."),t("br"),t("br"),n._v("“Xuất hiện? Tốt, cùng đi xem nhìn!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão ánh mắt sáng lên, lấy ra trước đó viên kia thông thần ngọc phù, tinh thần khẽ động, tiến vào bên trong."),t("br"),t("br"),n._v("Rất nhanh, Vân Phi Dương, vàng thao mấy người cũng tất cả đều đi theo tới."),t("br"),t("br"),n._v("Đi vào thông thần điện, còn chưa tới đến công khai thắng lôi đài trước mặt, bốn phía huyên náo thanh âm liền vang lên."),t("br"),t("br"),n._v("“Mau qua tới, Thiên Nhai cùng【 Nhất Nhãn Giang Hà】 tỷ thí lập tức bắt đầu!”"),t("br"),t("br"),n._v("“Nhất Nhãn Giang Hà là ai?”"),t("br"),t("br"),n._v("“Ta cũng không biết, nghe nói kiếm thuật cực kỳ cao minh, long tranh hổ đấu, rất nhanh liền có kết quả!”"),t("br"),t("br"),n._v("“Biết rõ vị này Thiên Nhai chiến tích, còn dám tới giao đấu, chứng minh thực lực khẳng định không kém, ai thắng ai thua, còn chưa biết được...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Nhất Nhãn Thiên Nhai? Là đại sư huynh!”"),t("br"),t("br"),n._v("Vân Phi Dương ánh mắt sáng lên."),t("br"),t("br"),n._v("“Giang Hà tiểu tử này không sai, hẳn là biết mấy người các ngươi thua, muốn thay tông môn bù lại danh tiếng!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nhất thời rõ ràng chuyện gì xảy ra, thoả mãn gật gật đầu."),t("br"),t("br"),n._v("“Chúng ta xấu hổ!”"),t("br"),t("br"),n._v("Mấy người vẻ mặt đồng thời đỏ lên."),t("br"),t("br"),n._v("Bọn họ thua, lại làm cho đại sư huynh ra tay, thực sự mất mặt."),t("br"),t("br"),n._v("“Giang Hà, từ trước tới nay thận trọng, kinh nghiệm chiến đấu cũng tương đối phong phú, hắn ra tay, hẳn là có thể giải quyết lần này mang tới ảnh hướng trái chiều...” Lục Vân trưởng lão vuốt vuốt sợi râu, cho dù tại thông thần điện, hắn cũng là lão giả hình ảnh."),t("br"),t("br"),n._v("“Đại sư huynh thực lực chúng ta bội phục, đã ra tay, khẳng định là có cách phá giải!”"),t("br"),t("br"),n._v("“Qua xem một chút đi, hắn chỉ cần ra tay, chắc hẳn kết quả đã chú định...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão gật đầu, mang theo bốn người nhanh chóng hướng công khai thắng lôi đài đi đến."),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, liền nghe đến vô số xôn xao thanh âm xông thẳng lên trời."),t("br"),t("br"),n._v("“Hẳn là có kết quả...”"),t("br"),n._v(" Năm người vội vã đi tới trước mặt, đang muốn nhìn một chút Hoắc Giang Hà thế nào chiến thắng, ngay sau đó nhìn thấy gọi “Nhất Nhãn Giang Hà” Đại sư huynh, chẳng biết lúc nào đã nằm trên mặt đất, trên đầu cắm một thanh trường kiếm."),t("br"),t("br"),n._v("Rất nhanh, thi thể hóa thành điểm sáng chậm rãi biến mất."),t("br"),t("br"),n._v("“Sư huynh...”"),t("br"),t("br"),n._v("Vân Phi Dương đám người bối rối."),t("br"),t("br"),n._v("Lục Vân trưởng lão cũng ngốc tại chỗ."),t("br"),t("br"),n._v("Đây không phải mới bắt đầu chiến đấu ư?"),t("br"),t("br"),n._v("Làm sao một cái chớp mắt liền kết thúc?"),t("br"),t("br"),n._v("Sư huynh cũng không phải đối thủ, bị tuỳ tiện thuấn sát?"),t("br"),t("br"),n._v("Mọi người khiếp sợ, Tiết Cầm thì là toàn thân run rẩy, sắp điên rồi."),t("br"),t("br"),n._v("Vốn cho rằng lần này đánh cược, sư huynh có thể tuỳ tiện chiến thắng, làm sao cũng không ngờ tới, đối phương ném kiếm... Sư huynh ngã xuống đất... Hai cái hô hấp cũng chưa tới, đã kết thúc!"),t("br"),t("br"),n._v("Nói như vậy đứng dậy, bản thân chẳng phải là... Thành đối phương nha hoàn?"),t("br"),t("br"),n._v("“Tiện tỳ, còn không qua đây, cho thiếu gia đấm bóp?”"),t("br"),t("br"),n._v("Tràn đầy rầu rĩ, chỉ thấy trên lôi đài thanh niên đi xuống, đi tới trước mặt, một mặt vui vẻ."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Tiết Cầm khí cắn răng, lật bàn tay một cái rút mạnh tới."),t("br"),t("br"),n._v("Để nàng làm nha hoàn, hạ nhân, nằm mơ..."),t("br"),t("br"),n._v("Bàn tay còn không có rơi vào đối phương trên mặt, liền cảm thấy cổ tay xiết chặt, đã bị đối phương bắt lấy, ngay sau đó trên mặt tê rần."),t("br"),t("br"),n._v("Ba ba ba đùng!"),t("br"),t("br"),n._v("Liên tục bốn bàn tay quất vào trên mặt, nguyên bản tuấn tú vô cùng khuôn mặt, lập tức sưng đỏ đứng dậy, tựa như cái mông."),t("br"),t("br"),n._v("Biết rõ nơi này là giả, cũng không phải là thế giới chân thật, nhưng Tiết Cầm vẫn như cũ cảm thấy, trên mặt giống như Hỏa, có đất khe hở lời nói, hận không thể tại chỗ chui vào."),t("br"),t("br"),n._v("Vốn cho rằng, leo lên trên vị này Hoắc Giang Hà, Lăng Vân kiếm các ngoại môn đại sư huynh, có thể nhất phi trùng thiên, chân chính thực hiện nhân sinh ước muốn, nằm mơ đều không có nghĩ đến..."),t("br"),t("br"),n._v("Trở nên so trước kia, càng thê thảm hơn."),t("br"),t("br"),n._v("“Hạ nhân dám đánh chủ nhân, lá gan thật là đủ lớn!”"),t("br"),t("br"),n._v("Hút xong đối phương, Trương Huyền hừ lạnh."),t("br"),t("br"),n._v("Đối với cái này từ hôn còn muốn giết người hung ác nữ nhân, hắn không có một chút hảo cảm, nếu không phải tính toán để lại cho học sinh dạy dỗ, đã sớm một bàn tay quất chết."),t("br"),t("br"),n._v("“Ta muốn giết ngươi...”"),t("br"),t("br"),n._v("Tiết Cầm tròng mắt đỏ hoe, vừa định cùng trước mắt người này liều mạng, liền nghe một cái quát tháo tiếng vang lên: “Đủ, còn ngại mất mặt mất không đủ?”"),t("br"),t("br"),n._v("Nữ hài thân thể cứng đờ, vội vàng xoay người, ngay sau đó nhìn thấy một người trung niên, chẳng biết lúc nào đi tới cách đó không xa."),t("br"),t("br"),n._v("“Cha...”"),t("br"),t("br"),n._v("Nữ hài đầu gối mềm nhũn quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Gọi hắn cha? Chẳng lẽ vị này chính là... Huyền Giang thành Tiết Càn thành chủ?”"),t("br"),t("br"),n._v("“Tiết Càn thành chủ, nhưng mà Cổ Thánh bốn tầng phá toái hư không cảnh cường giả, không nghĩ tới đích thân tới!”"),t("br"),t("br"),n._v("“Con gái đều thành người khác nha hoàn, có thể ngồi yên mới gọi gặp quỷ!”"),t("br"),t("br"),n._v("“Hắn xuất thủ, vị này Thiên Nhai khẳng định hết rồi...”"),t("br"),t("br"),n._v("“Đúng vậy a, làm nhục thành chủ con gái, hơn nữa đánh bạt tai, chỉ điều này, liền không có người cứu được...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không để ý tới mọi người thảo luận, cũng không đi quản quỳ trên mặt đất con gái, trung niên nhân ngẩng đầu nhìn về phía Trương Huyền: “Vị bằng hữu này, tìm chỗ khoan dung mà độ lượng! Người trẻ tuổi có phong mang là chuyện tốt, nhưng phong mang quá mức, liền hăng quá hoá dở!”"),t("br"),t("br"),n._v("“Đúng vậy a, tìm chỗ khoan dung mà độ lượng, ta từ trước tới nay không thích ép người quá đáng...” Trương Huyền gật gật đầu, đối với đối phương lời nói, biểu thị tán đồng: “Đương nhiên, nếu như không phải người, không tính!”"),t("br"),t("br"),n._v("Trung niên nhân Tiết Càn con mắt lập tức híp lại, trên người sát khí sôi trào."),t("br"),t("br"),n._v("Bốn phía mọi người đã nói ra thân phận của hắn, thân là thành chủ, chủ động chạy tới, rất cho mặt mũi, vậy mà nói như vậy!"),t("br"),t("br"),n._v("Một chút mặt mũi cũng không cho!"),t("br"),t("br"),n._v("Thật ra thì cũng không chỉ có thể trách Trương Huyền, nếu như không phải hắn ra tay, vị này gọi Thiện Hiểu Thiên đệ tử, khẳng định sớm đã bị giết."),t("br"),t("br"),n._v("Cũng bởi vì, có hôn ước, không thoái hôn giết người..."),t("br"),t("br"),n._v("Mấu chốt cái này phủ thành chủ thanh danh còn rất tốt... Như vậy giả dối gia hỏa, ngụy quân tử, liền như thế đơn giản bỏ qua, thật đúng là không làm được."),t("br"),t("br"),n._v("“Xem ra ngươi mười phần tự tin, như vậy... Ta và ngươi tỷ thí!”"),t("br"),t("br"),n._v("Thấy chung quanh xem náo nhiệt càng ngày càng nhiều, biết chuyện ngày hôm nay, không có cách nào thiện, trung niên nhân nhìn qua."),t("br"),t("br"),n._v("“Tốt! Rất đơn giản, tiền đặt cược, bốn mươi vạn thông Thần tệ! Đáp ứng liền so, không đáp ứng, ta trở về đi ngủ, hơn nửa đêm, vừa thu nô tỳ không thân mật, trở về liền đem chuyện này tuyên dương ra ngoài... Đường đường phủ thành chủ, liền con gái đều quản không tốt, sao có thể quản lý mấy triệu người thành trì?”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền trên mặt một bộ lợn chết không sợ bỏng nước sôi biểu lộ."),t("br"),t("br"),n._v("Dù sao các ngươi phủ thành chủ có tiền, có thể nuôi nhiều lính như vậy sĩ, đã như vậy... Cho các ngươi xuất một chút máu."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Ánh mắt như điện, Tiết Càn trước đến giờ không có như vậy khát vọng giết người."),t("br"),t("br"),n._v("Thân là thành chủ, được vạn người ngưỡng mộ, còn lần đầu tiên, gặp phải loại tình huống này."),t("br"),t("br"),n._v("Cái này đã đợi tại chỉ vào cái mũi mắng người, hơn nữa cố ý đánh hắn nữ nhi."),t("br"),t("br"),n._v("“Người trẻ tuổi, đối nhân xử thế lưu một tia, sự tình gì đều không được làm quá tuyệt, thông thần điện cứ việc có thể biến mất họ tên, nhưng cũng không phải tuyệt đối sẽ không tiết lộ tin tức!”"),t("br"),t("br"),n._v("Tiết Càn trong giọng nói mang theo uy hiếp."),t("br"),t("br"),n._v("“Làm sao không dám đánh cược? Vậy thì cuốn xéo... Ít chậm trễ thời gian của ta!” Trương Huyền duỗi lưng một cái."),t("br"),t("br"),n._v("“Ngươi...” Hít sâu một hơi, ép lại lửa giận trong lòng, Tiết Càn hàm răng cắn chặt: “Tốt, ta và ngươi cược!”"),t("br"),t("br"),n._v("Bốn mươi vạn thông Thần tệ, đối với phủ thành chủ cũng là một khoản tiền lớn, nhưng... Nếu như không đánh cược, thật để cho hắn rời đi, tuyên dương khắp chốn, phủ thành chủ mấy chục năm xác lập uy tín, khẳng định triệt để phế."),t("br"),t("br"),n._v("Hơn nữa nhìn đối phương bộ đáng... Thật đúng là sẽ làm như vậy..."),t("br"),t("br"),n._v("Lúc nào phủ thành chủ đắc tội như vậy không có chút nào điểm mấu chốt cao thủ?."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1945"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1947"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);