(window.webpackJsonp=window.webpackJsonp||[]).push([[1068],{1423:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1935-thuong-thuong-tu-luyen-đang-cap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1935-thuong-thuong-tu-luyen-đang-cap"}},[n._v("#")]),n._v(" Chương 1935: Thượng thương tu luyện đẳng cấp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Muốn nhìn tiểu thuyết hay, mời sử dụng Wechat chú ý công chúng hào “Đến trâu đọc sách”."),t("br"),t("br"),n._v("“Ách?”"),t("br"),t("br"),n._v("Tiết Càn một mặt mộng bức."),t("br"),t("br"),n._v("Ta hỏi ngươi dùng mấy thành lực lượng, ngươi cùng ta nói toán học, nói số lẻ... Cái quỷ gì?"),t("br"),t("br"),n._v("Vô tận tuần hoàn... Lại là cái gì?"),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Cũng lại không chịu nổi, hóa thành đầy trời điểm sáng biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Sắp chết, đều là sụp đổ."),t("br"),t("br"),n._v("Bốn phía cũng là một hồi yên tĩnh, tất cả mọi người từng cái lông mày nhảy loạn."),t("br"),t("br"),n._v("Rất hiển nhiên, tất cả mọi người cho rằng, vị này Thiên Nhai, nói không phải nói thực, mà là tại trêu đùa đối phương."),t("br"),t("br"),n._v("Không phải, lực lượng khống chế tại chính xác, cũng không có khả năng, đến số lẻ a?"),t("br"),t("br"),n._v("Còn vô tận tuần hoàn... Theo em gái ngươi ah!"),t("br"),t("br"),n._v("Có thể hay không đáng tin cậy điểm?"),t("br"),t("br"),n._v("Mọi người lần này thái độ, Tiết Cầm thì sợ tới mức vẻ mặt triệt để thay đổi."),t("br"),t("br"),n._v("Hoắc Giang Hà sư huynh, không đỡ được, có thể tiếp nhận, dù sao còn trẻ, mạnh mẽ chính là tiềm lực cùng tương lai!"),t("br"),t("br"),n._v("Có thể cha... Đứng đầu một thành, tu vi càng là đạt đến Cổ Thánh tầng thứ tư sơ kỳ... Đối tu luyện lĩnh ngộ cùng lý giải, sớm đã đi đến, mức lô hỏa thuần thanh, loại tình huống này, cùng cấp bậc đều thắng không nổi vị này Thiên Nhai..."),t("br"),t("br"),n._v("Ở đâu xuất hiện gia hỏa? Làm sao lại mạnh như vậy?"),t("br"),t("br"),n._v("Tinh diệu thành nếu là thật có nhân vật như vậy, khẳng định đã sớm dương danh lập vạn đi!"),t("br"),t("br"),n._v("“Cùng lắm thì gần nhất thong thả đi vào, hắn thực có can đảm chạy đến phủ thành chủ gây rối, liền do ghê gớm...” Hừ lạnh một tiếng, Tiết Cầm nắm đấm xiết chặt."),t("br"),t("br"),n._v("Cái gì nô tỳ không nô tỳ, chỉ cần không đi vào thông thần điện, đối phương có thể đem bản thân thế nào?"),t("br"),t("br"),n._v("Dám đến phủ thành chủ, hiển lộ chân thân lời nói, vài phút giết chết!"),t("br"),t("br"),n._v("Nghĩ đến cái này, thở phào nhẹ nhõm, tinh thần khẽ động."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Quang mang chợt lóe, thân ảnh biến mất, triệt để rời đi."),t("br"),t("br"),n._v("“Ăn ngay nói thật, cũng không được...”"),t("br"),t("br"),n._v("Thấy Tiết Càn cùng mọi người bộ biểu tình này, Trương Huyền biết mới vừa nói, tương đương nói nhảm, bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Hắn chỉ dùng một phần sáu lực lượng, nói cho đúng đứng dậy, đúng lúc là một phẩy sáu sáu thành..."),t("br"),t("br"),n._v("Phải biết ta dùng bao nhiêu lực lượng, nói chuẩn xác lại không tin... Người tốt khó xử ah!"),t("br"),t("br"),n._v("Đi xuống lôi đài, đem đại biểu vị trí cụ thể đồ vật hủy diệt, trở lại trước đài, đem thông thần thẻ đưa tới."),t("br"),t("br"),n._v("Bên trong quả nhiên đã có sáu mươi vạn thông thần tệ (đánh cược thắng năm mươi vạn, Hoắc Giang Hà cùng Tiết Càn đều mang vũ khí, một cái năm vạn)."),t("br"),t("br"),n._v("Đổi một cái trữ vật giới chỉ, Trương Huyền thở phào nhẹ nhõm, đang muốn rời đi, chỉ thấy trước đó bị hắn đánh bại Vân Phi Dương, tiến lên đón, trong tay cầm một cái bái thiếp."),t("br"),t("br"),n._v("“Thiên Nhai tiền bối, Lục trưởng lão, muốn tìm ngươi đơn độc nói chuyện, không biết phải chăng là thuận tiện?”"),t("br"),t("br"),n._v("“Lục trưởng lão?”"),t("br"),t("br"),n._v("Đối phương là Lăng Vân kiếm các người, vậy cái này, chẳng phải là Kiếm Các trưởng lão?"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghĩ tới những thứ này, không dừng lại quá lâu, Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Thượng Thương sáu đại thế lực một trong, đã có thể trở thành trưởng lão, khẳng định bất phàm, biết tin tức cũng nhiều, vừa vặn có thể hiểu rõ hơn một ít Thượng Thương sự tình, hỏi thăm một chút, liên quan tới Linh Thần Cung sự tình."),t("br"),t("br"),n._v("Ban đầu ở Dị Linh tộc, tế tự triệu hoán Linh Thần, tới một vị cường giả, nói Lạc Nhược Hi tại Linh Thần Cung!"),t("br"),t("br"),n._v("Chỉ cần tìm được nơi này, hẳn là có thể tìm tới đối phương."),t("br"),t("br"),n._v("Đi theo Vân Phi Dương sau lưng, rất mau tới đến một cái phòng kín mít."),t("br"),t("br"),n._v("Thông thần điện, có thể cung cấp tỷ thí, cũng có thể cung cấp một ít chỗ đặc thù, để cho người ta an tâm tán gẫu, tránh khỏi quấy rầy, ở đây bất luận nói cái gì, cũng sẽ không tiết lộ đến ngoại giới, tuyệt đối tư mật."),t("br"),t("br"),n._v("Trong phòng, một cái lão giả ngồi ngay ngắn ở trung tâm, trước đó bị hắn Từng cái đánh bại mấy cái, đều đứng tại một bên, biểu lộ hơi có vẻ khó xử."),t("br"),t("br"),n._v("Vị kia “Nhất Nhãn Giang Hà”, Hoắc Giang Hà giờ phút này cũng tìm mới thông thần ngọc phù, lần nữa trong đó."),t("br"),t("br"),n._v("“Tại hạ Lăng Vân kiếm các, ngoại môn trưởng lão, Lục Vân!”"),t("br"),t("br"),n._v("Thấy hắn đi vào, Lục Vân trưởng lão, nói một tiếng, đứng dậy, tự giới thiệu."),t("br"),t("br"),n._v("“Gặp qua Lục trưởng lão!” Trương Huyền ôm quyền, tự giới thiệu: “Tại hạ Thiên Nhai!”"),t("br"),t("br"),n._v("Đối phương cái mục đích gì không rõ, tạm thời còn không muốn bại lộ thân phận."),t("br"),t("br"),n._v("Tại thông thần điện, thực lực của hắn rất mạnh, nhưng đi vào thực tế, Cổ Thánh tầng thứ tư sơ kỳ thực lực, cũng liền Huyền Giang thành uy phong một chút, đi vào tinh diệu thành, cũng không biết có thể toát ra bao nhiêu cường giả, đánh bại dễ dàng."),t("br"),t("br"),n._v("“Không cần cẩn thận!” Thấy hắn không nói ra tên thật của chính mình, mà là ngoại hiệu, biết còn có chút kiêng kị, Lục Vân trưởng lão cũng không để ý, cười cười: “Ngươi cùng bọn hắn tỷ thí, ta đều nhìn qua hình ảnh, đối kiếm thuật lý giải, đã đi đến xuất thần nhập hóa cảnh giới... Chẳng qua, bất kỳ cái gì công pháp đều giống nhau, đi đến loại tình trạng này, tài cao còn gắng sức hơn, khả năng liền cần truyền thừa cùng tài nguyên, mà xem như tán tu, điểm ấy, rất khó hoàn thành...”"),t("br"),t("br"),n._v("“Không biết có hứng thú hay không, gia nhập Lăng Vân kiếm các?”"),t("br"),t("br"),n._v("Từ đối phương kiếm thuật, đó có thể thấy được tâm tính quả quyết, Lục Vân trưởng lão không có quá nhiều vết mực, trực tiếp nói thẳng mục đích đến."),t("br"),t("br"),n._v("“Gia nhập Kiếm Các?” Còn tưởng rằng đối phương muốn hưng sư vấn tội, không nghĩ tới muốn cho hắn gia nhập, Trương Huyền mang theo nghi ngờ."),t("br"),t("br"),n._v("“Hoắc Giang Hà, Tiết Càn, cùng cấp bậc, cũng không phải đối thủ của ngươi, loại thiên phú này cùng năng lực, đi vào tông môn sau, có thể trực tiếp xin nội môn thế hệ sau danh ngạch! Ở đây, có thể học tập cao minh hơn kiếm thuật, có càng đa dụng hơn kiếm đệ tử tỷ thí, đối luyện, tiến bộ khẳng định càng nhanh hơn!” Lục Vân trưởng lão nói."),t("br"),t("br"),n._v("Đối phương liền cái này ngoại môn đại sư huynh cũng có thể nhẹ nhõm đánh bại, đã đã có được tiến vào nội môn tư cách."),t("br"),t("br"),n._v("“Cái này... Ta cần cân nhắc!” Trương Huyền cũng không trực tiếp đáp ứng."),t("br"),t("br"),n._v("Tới Thượng Thương mục đích, là tìm kiếm Lạc Nhược Hi, cái gọi là tông môn các loại, cũng không có hứng thú quá lớn."),t("br"),t("br"),n._v("“Đây là việc lớn, quả thực cần cân nhắc...”"),t("br"),n._v(" Gật gật đầu, Lục Vân trưởng lão cũng không gấp gáp."),t("br"),t("br"),n._v("“Lục trưởng lão, tại hạ muốn hướng ngươi hỏi thăm một ít chuyện, không biết có thuận tiện hay không!” Không tại tiếp tục cái đề tài này, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Mời nói!”"),t("br"),t("br"),n._v("“Không biết... Trưởng lão có thể nghe qua... Linh Thần Cung nơi này?”"),t("br"),t("br"),n._v("Trương Huyền mang theo căng thẳng."),t("br"),t("br"),n._v("“Linh Thần Cung?” Lục Vân trưởng lão dừng lại một chút, ngay sau đó lắc đầu: “Chưa từng nghe qua!”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Ta chỉ là Kiếm Các ngoại môn trưởng lão, bình thường chịu trách nhiệm dạy dỗ học sinh, đại lục ở bên trên sự tình khác biết không nhiều... Tông môn có đặc biệt giới thiệu đại lục rất nhiều môn phái cùng phân chia thế lực thư tịch, chỉ cần thân phận đầy đủ, có thể tùy ý lật xem! Hẳn là có thể tìm được ngươi muốn biết...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão cười khổ một tiếng."),t("br"),t("br"),n._v("Hắn mặc dù mệnh danh Lăng Vân kiếm các trưởng lão, trên thực tế chỉ là cấp thấp nhất cái chủng loại kia, không có tư cách giải càng nhiều chuyện hơn."),t("br"),t("br"),n._v("Đối phương nói tới Linh Thần Cung, đã mang “Thần” Chữ, khẳng định dính dáng cực cao, không phải hắn loại tầng thứ này, có thể giải."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Cũng đối, một cái chịu trách nhiệm thu nhận tạp dịch đệ tử, ngoại môn đệ tử trưởng lão, cũng không khả năng biết, nếu không, Lúc trước triệu hoán vị kia “Thần Linh” Đều không đến mức im miệng không nói."),t("br"),t("br"),n._v("“Vừa vặn, ta còn có một cái trên việc tu luyện nghi vấn, tu luyện giả chia làm, Thánh vực, Đại Thánh cùng Cổ Thánh... Không biết Cổ Thánh phía trên, còn có loại cảnh giới nào?”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("“Cổ Thánh cảnh giới tối cao, vì phá toái hư không, vượt qua loại cảnh giới này, vì Hư Tiên cảnh! Nhất niệm thông suốt, hồn như chân kim, hỏa luyện vô hại! Ta hiện tại chính là Hư Tiên cảnh, tinh diệu thành thành chủ, cũng là tương đồng cảnh giới. Hư Tiên đồng dạng chia làm ban sơ, trung, hậu, đỉnh phong bốn cái cấp bậc, vượt qua cấp bậc này, vì Chân Tiên! Đi đến Chân Tiên, tại toàn bộ vứt bỏ đại lục, đều được cho cao thủ chân chính. Tông môn nội môn trưởng lão, chính là loại cảnh giới này. Lại hướng lên, thì là Tiên Quân cảnh, tiên chi quân vương... Kiếm Các hạch tâm trưởng lão, mới có loại lực lượng này!”"),t("br"),t("br"),n._v("Nói đến đây, Lục Vân trưởng lão cảm xúc một câu, tiếp tục nói: “Ta biết chính là mấy cái này cảnh giới, lại hướng lên khẳng định còn có, nhưng đã không phải là ta có thể tiếp xúc... Tu luyện vô bờ bến, muốn tiến bộ nhanh hơn, đi càng xa, không có tông môn chống đỡ, cũng không đủ tu luyện công pháp cùng kinh nghiệm, thực sự quá khó khăn! Liền giống như, những kiến thức này, là phổ thông đệ tử liền có thể biết, mà ngươi nhưng cần hỏi thăm.”"),t("br"),t("br"),n._v("“Cổ Thánh, Hư Tiên, Chân Tiên, Tiên Quân...”"),t("br"),t("br"),n._v("Trương Huyền điểm đầu, đem cái này bốn cái cảnh giới ghi xuống."),t("br"),t("br"),n._v("“Lấy kiếm thuật của ngươi, lại thêm ta tiến cử, tiến vào nội môn dễ như trở bàn tay, không biết... Ngươi bây giờ ở nơi nào? Thực sự không được, chúng ta gặp mặt đàm phán!” Lục Vân trưởng lão tiếp tục nói."),t("br"),t("br"),n._v("Trong này trò chuyện nhiều hơn nữa đều là hư nhược, nhất định phải nhìn thấy chân nhân, mới có thể xác định đến cùng loại thực lực nào, là nam, là nữ, thậm chí còn có thể là Thánh thú, tiên thú!"),t("br"),t("br"),n._v("“Ta tại Huyền Giang thành... Lục trưởng lão chỉ cần tới, tự sẽ gặp nhau! Hơn nữa, ta khả năng còn có chuyện, cần phiền phức Hoắc sư huynh một chút!” Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Huyền Giang thành?”"),t("br"),t("br"),n._v("Lục Vân trưởng lão liếc mắt nhìn một bên Hoắc Giang Hà, người sau hiển nhiên cũng có chút choáng váng."),t("br"),t("br"),n._v("Như vậy lạc hậu địa phương, lúc nào xuất hiện lợi hại như vậy cường giả?"),t("br"),t("br"),n._v("“Chỉ cần ta có thể làm được, cứ nói đừng ngại!” Hoắc Giang Hà ôm quyền."),t("br"),t("br"),n._v("“Là như thế này...” Trương Huyền rất nói mau sự kiện, giải thích xong, ôm quyền khom người: “Chuyện này liền xin nhờ Hoắc sư huynh, hiện tại sẽ không quấy rầy chư vị, này cáo từ!”"),t("br"),t("br"),n._v("Phóng ra ý tốt, muốn biết cũng hỏi thăm xong, Trương Huyền không tại ở lại."),t("br"),t("br"),n._v("Lần nữa trở lại trước đài, tìm tới phục vụ viên: “Không biết các ngươi nơi này, nhưng có khôi phục nhanh chóng lực lượng dược vật hoặc là bảo vật?”"),t("br"),t("br"),n._v("“Có hạ phẩm tiên nguyên đan bán ra, một cái, mười vạn thông thần tệ!”"),t("br"),t("br"),n._v("Phục vụ viên nói."),t("br"),t("br"),n._v("“Hạ phẩm? Liền muốn mười vạn kim tệ?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Đi qua hỏi thăm, hắn xem như hiểu được."),t("br"),t("br"),n._v("Tiên nguyên đan, là tu luyện giả giao dịch đồng tiền mạnh, cùng trước đó danh sư đại lục linh thạch đồng dạng, chia làm bốn cái cấp bậc, hạ phẩm, trung phẩm, thượng phẩm cùng tuyệt phẩm."),t("br"),t("br"),n._v("Hạ phẩm tiên nguyên đan, đối ứng Hư Tiên phía dưới tu vi, đều có cực lớn công hiệu, trung phẩm đối ứng Chân Tiên, thượng phẩm đối ứng Tiên Quân! Còn tuyệt phẩm, loại đan dược này thực sự quá mức thưa thớt, có thể ngộ nhưng không thể cầu, ngược lại không bao nhiêu người gặp qua."),t("br"),t("br"),n._v("Một cái hạ phẩm liền muốn mười vạn, vốn cho rằng vừa rồi kiếm lời không ít, hiện tại xem ra, vẫn như cũ không đủ xài."),t("br"),t("br"),n._v("Đem tiền còn lại, mua một cái, Trương Huyền có chút hiếu kỳ, lần nữa nhìn lại."),t("br"),t("br"),n._v("“Thông thần điện, có thể bán ra đủ loại đồ vật, công pháp các loại, có phải hay không cũng có thể bán ra?”"),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Phục vụ viên điểm đầu: “Chẳng qua, đều là một ít đại lục thông dụng, cùng một ít vô chủ công pháp, sáu đại tông môn hạch tâm pháp quyết, chúng ta đồng dạng không có!”"),t("br"),t("br"),n._v("Sáu đại tông môn, đều có thuộc về mình tuyệt kỹ, không có khả năng tiết ra ngoài, thậm chí coi như thông thần điện biết, cũng không dám bán ra."),t("br"),t("br"),n._v("Sáu đại tông môn có thể cho phép, ngươi để cho người ta ở bên trong tỷ thí, để cho người ta tu luyện, nhưng thật muốn dao động căn cơ, chỉ có thể diệt sát."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, thông thần điện thành lập nhiều năm như vậy, một mực cùng hắn bình an vô sự."),t("br"),t("br"),n._v("Chính là thủ vững cái này điểm mấu chốt cùng nguyên tắc."),t("br"),t("br"),n._v("“Ta biết, thông dụng, vô chủ công pháp cũng được... Nếu như không mua sắm, chỉ là quan sát, hơn nữa, thời gian rất ngắn lời nói, đại khái tiêu phí bao nhiêu?”"),t("br"),t("br"),n._v("Trong lòng có chút kích động, Trương Huyền dò hỏi."),t("br"),t("br"),n._v("Hắn tuy là đột phá đến phá toái hư không cảnh giới sơ kỳ, nhưng cũng không có cảnh giới này công pháp, chớ nói chi là Hư Tiên, Chân Tiên, Tiên Quân..."),t("br"),t("br"),n._v("Nếu như có thể mua được pháp quyết, tiên nguyên đan đầy đủ, hoàn toàn có thể trong vài canh giờ, liền xung kích đến cảnh giới tối cao, có thực lực, lại tìm kiếm Lạc Nhược Hi tin tức, khẳng định cũng liền đơn giản."),t("br"),t("br"),n._v("“Căn cứ tu luyện Đẳng cấp khác biệt, giá cả cũng không giống, phá toái hư không cảnh giới công pháp, bình thường nhất, đại khái tại năm vạn một bản, bình thường đều là mua sắm, còn không có quan sát nói chuyện...” Phục vụ viên lắc đầu."),t("br"),t("br"),n._v("“Năm vạn một bản? Vẫn là bình thường nhất?” Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Còn tưởng rằng tiện nghi, trực tiếp mua lấy một đống lớn, không nghĩ tới giá cả như vậy “Khả quan”."),t("br"),t("br"),n._v("Năm vạn một bản... Còn bình thường nhất, một lần tu luyện cần hơn ngàn bản, đơn thuần tỷ thí lời nói kiếm tiền, tươi sống mệt chết trên đài, cũng không kiếm được!"),t("br"),t("br"),n._v("Huống chi, đã thu hoạch một nhóm rau hẹ, bên dưới điều rau hẹ còn không biết lúc nào mới có thể xuất hiện."),t("br"),t("br"),n._v("Nhìn tới, mua sắm thư tịch, hội tụ công pháp, có chút không phù hợp thực tế."),t("br"),t("br"),n._v("“Chẳng lẽ... Thật muốn đi tông môn?” Xoa xoa mi tâm, Trương Huyền tràn đầy bất đắc dĩ.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1947"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1949"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);