(window.webpackJsonp=window.webpackJsonp||[]).push([[1069],{1424:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1936-bat"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1936-bat"}},[n._v("#")]),n._v(" Chương 1936: Bắt")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1936: Bắt"),t("br"),t("br"),n._v("Muốn nhìn tiểu thuyết hay, mời sử dụng Wechat chú ý công chúng hào “Đến trâu đọc sách”."),t("br"),t("br"),n._v("Trong tông môn, khẳng định có vô số tu luyện công pháp, đừng nói gom góp một ngàn bản, mấy ngàn bản đều rất dễ dàng... Chính là bởi vì có loại này tài nguyên, mới để cho không biết bao nhiêu tu luyện giả, đều lấy tiến vào bên trong làm vinh."),t("br"),t("br"),n._v("Lấy thiên phú của hắn cùng đối kiếm thuật lý giải, đi vào Lăng Vân kiếm các nội môn, không khó lắm, nhưng... Thiên hạ không có cơm trưa miễn phí, gia nhập tông môn, hưởng thụ được tốt hơn tài nguyên phân phối, nhất định phải bị tông môn quy tắc hạn chế."),t("br"),t("br"),n._v("Phổ thông sự tình ngược lại cũng thôi, ngộ nhỡ cùng tìm kiếm Lạc Nhược Hi lẫn nhau vi phạm, liền làm trái ước nguyện ban đầu."),t("br"),t("br"),n._v("“Không bằng... Để Thiện Hiểu Thiên gia nhập tông môn, ta làm tùy tùng, cũng có thể đi vào...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Lăng Vân Kiếm tông, lần này là tới thu nhận ngoại môn đệ tử cùng tạp dịch đệ tử... Bình thường ngoại môn đệ tử, cũng có thể phân phối mấy vị tùy tùng, nếu là Thiện Hiểu Thiên thành công, bản thân cũng có thể tuỳ tiện lẫn vào trong đó."),t("br"),t("br"),n._v("Đến tông môn, muốn tìm kiếm cái gì, hoàn toàn có thể tùy cơ ứng biến, muốn thu hoạch được tài nguyên, cũng có thể mượn vị này đồ đệ tay, như vậy, làm người giật dây, không cần nhận quy tắc hạn chế."),t("br"),t("br"),n._v("“Cứ làm như thế!”"),t("br"),t("br"),n._v("Cân nhắc một lần, như vậy càng thích đáng, Trương Huyền thở phào nhẹ nhõm, rời đi thông thần điện."),t("br"),t("br"),n._v("Hắn làm ra quyết định, bên trong căn phòng Lục Vân trưởng lão mấy người cũng có quyết định."),t("br"),t("br"),n._v("“Hiện tại liền đi Huyền Giang thành... Chúng ta ngồi nhanh nhất phi hành Thánh thú, cũng cần buổi chiều mới có thể đến đến thẳng, ngày hôm nay liền bắt đầu tuyển chọn, chúng ta đại khái buổi chiều có thể tới, Giang Hà, đừng chờ chúng ta, bình minh liền bắt đầu tuyển chọn, tìm ra vị này Thiên Nhai, nhất định không thể bỏ qua!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão bàn giao."),t("br"),t("br"),n._v("“Là!” Hoắc Giang Hà vội vàng điểm đầu."),t("br"),t("br"),n._v("Lợi hại như vậy thiên tài, thu nhận đi vào tông môn, so với thu nhận mấy chục cái, thậm chí trên trăm cái phổ thông thiên tài đều mạnh hơn, Một khi bỏ lỡ, để những tông môn khác nhặt chỗ tốt... Trở về khẳng định nhận trừng phạt."),t("br"),t("br"),n._v("“Tốt, tất cả nhanh lên một chút a!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Mọi người đồng thời từ thông thần điện biến mất."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Về đến phòng, Trương Huyền lấy ra vừa mua sắm trữ vật giới chỉ cùng hạ phẩm tiên nguyên đan."),t("br"),t("br"),n._v("Đan dược toàn thân nổi màu bạc, ẩn chứa nồng đậm mà tinh thuần thủy ngân linh khí, hấp thu một cái, cũng làm người ta lập tức cảm thấy cơ bắp nhảy lên, kinh mạch xuất hiện run rẩy."),t("br"),t("br"),n._v("Biết đối với hắn khôi phục thực lực, có cực lớn trợ giúp, Trương Huyền không có làm do dự, há miệng liền đem viên thuốc nuốt xuống."),t("br"),t("br"),n._v("Tiên nguyên đan vừa tiến vào trong cơ thể, một cỗ ấm áp khí lưu, lập tức tràn đầy toàn thân, tinh thuần thủy ngân linh khí, dần dần thuế biến thành càng thêm tinh thuần chân khí."),t("br"),t("br"),n._v("Không ngừng tu luyện, Trương Huyền phát hiện, thủy ngân linh khí ngưng tụ mà thành chân khí, uy lực mạnh hơn, thi triển ra công kích, cũng càng thêm mạnh mẽ."),t("br"),t("br"),n._v("Chân khí bên trong đan điền, hoàn toàn khôi phục lời nói, chân khí số lượng dự trữ không có tăng thêm, nếu như nói trước đó là một vạn đạo lời nói, hiện tại vẫn như cũ là một vạn đạo... Nhưng mỗi một đạo lực lượng, có biến hóa, sức chiến đấu, cũng có cực lớn tăng thêm!"),t("br"),t("br"),n._v("Khó trách Thượng Thương tùy tiện xuất hiện một vị cường giả, đều mạnh mẽ cực kỳ, dùng hết tất cả thủ đoạn, mới có thể khó khăn chiến thắng, thủy ngân linh khí, không chỉ cải thiện đối phương thể chất, đối chân khí cũng có tốt hơn rèn luyện hiệu quả."),t("br"),t("br"),n._v("Không ngừng tu luyện, không biết qua bao lâu, tiên nguyên đan lực lượng, triệt để hao hết, Trương Huyền lúc này mới thở phào nhẹ nhõm, từ từ mở mắt."),t("br"),t("br"),n._v("Hạ phẩm tiên nguyên đan dự trữ linh khí, cứ việc hùng hậu, vẫn như cũ không có để hắn triệt để khôi phục."),t("br"),t("br"),n._v("“Xem ra, còn cần một cái mới có thể...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, một mặt bất đắc dĩ."),t("br"),t("br"),n._v("Chỉ là khôi phục thực lực, liền cần hai cái tiên nguyên đan, hai mươi vạn thông thần tệ... Muốn đột phá, lại nên cần bao nhiêu?"),t("br"),t("br"),n._v("Tu luyện quả nhiên là cái động không đáy, lại cao hơn thu vào, cũng vô dụng!"),t("br"),t("br"),n._v("Giương mắt nhìn ra phía ngoài, trời đã sáng lên, ra khỏi phòng, quả nhiên thấy Thiện Hiểu Thiên vẫn tại một chiêu một thức luyện kiếm."),t("br"),t("br"),n._v("Một đêm không có nghỉ ngơi, cũng không có chút mệt mỏi, ngược lại bạo tăng lực lượng, được củng cố, hai mắt mơ hồ, chân khí trong cơ thể, triệt để vận dụng tự nhiên, không có một chút trúc trắc chi ý."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Liếc mắt nhìn, Trương Huyền chỉ biết, đối phương chăm chú dựa theo hắn nói đi làm, không có một chút lười biếng."),t("br"),t("br"),n._v("Để hắn luyện kiếm một đêm, cũng không phải là trừng phạt, mà là một chút đột phá sáu cái đại cấp bậc, bất luận thân thể vẫn là phản ứng, đều sẽ theo không kịp, không nghĩ biện pháp lắng đọng lời nói, sức chiến đấu không có quá tăng nhiều thêm."),t("br"),t("br"),n._v("Không ngừng luyện kiếm, đồng dạng một chiêu, liên tục thi triển mấy chục lần, vài, thậm chí hơn ngàn lần, không ngừng cảm ngộ chân khí trong cơ thể vận chuyển, tự nhiên cũng liền càng khống chế càng tinh tế hơn, càng khống chế càng thuận buồm xuôi gió."),t("br"),t("br"),n._v("Rất hiển nhiên, mục đích của hắn đạt đến."),t("br"),t("br"),n._v("Đi qua một đêm rèn luyện, Thiện Hiểu Thiên triệt để thích ứng Đại Thánh tam trọng, Tâm Huyết Lai Triều cảnh giới."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Nghe được tiếng mở cửa, Thiện Hiểu Thiên vội vàng xoay người, lần nữa quỳ rạp xuống đất, vui lòng phục tùng."),t("br"),t("br"),n._v("Cảm nhận được thân thể biến hóa, ở đâu không biết lão sư dụng ý."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Thấy hắn quỳ xuống, vừa định đem hắn kéo, Trương Huyền cảm thấy trong óc chấn động, một bản kim sắc thư tịch, bất ngờ xuất hiện."),t("br"),t("br"),n._v("Thu làm đệ tử, truyền thụ công pháp, cho tới giờ khắc này, mới xem như chân chính vui lòng phục tùng, lại không hai lòng."),t("br"),t("br"),n._v("“Quả nhiên nơi này, cũng có thể hình thành Thiên đạo chi sách!”"),t("br"),t("br"),n._v("Còn tưởng rằng đi vào Thượng Thương, không cách nào làm được, không nghĩ tới, đồng dạng thành công."),t("br"),t("br"),n._v("Có thứ này, chẳng khác nào có bảo vệ tính mạng át chủ bài, Thiên Đạo bên dưới, không người nào có thể chống cự."),t("br"),t("br"),n._v("“Ta truyền thụ cho ngươi kiếm chiêu thế nào rồi? Thi triển cho ta xem một chút!”"),t("br"),t("br"),n._v("Tâm tình thật tốt, cười nhìn qua."),t("br"),t("br"),n._v("Hôm qua đem thông thần điện sáng lập ra “Ném kiếm” Truyền thụ cho đối phương, cũng không biết một đêm này, tu luyện đến loại cảnh giới nào."),t("br"),t("br"),n._v("“Là!”"),t("br"),t("br"),n._v("Đứng dậy, Thiện Hiểu Thiên tiến về phía trước một bước, trường kiếm trong tay lắc một cái, thẳng tắp bắn ra ngoài."),t("br"),t("br"),n._v("Vèo!"),t("br"),n._v("Mặc dù không có Trương Huyền cái loại này tốc độ, nhưng cũng như là huyễn ảnh, thời gian nháy mắt cắm vào cách đó không xa cây khô, một tiếng nổ vang, đại thụ nổ cành lá bay tứ tung, xuất hiện một cái to lớn cái hố."),t("br"),t("br"),n._v("“Không sai, đã nhập môn! Nếu như muốn trở nên càng thêm cường đại, cần tại thời điểm chiến đấu, nghiên cứu đối thủ sơ hở, chỉ cần làm được liếc mắt nhìn ra, đối thủ chiêu số bên trong sơ hở, kiếm này coi như chân chính đại thành, cùng cấp bậc rất khó tìm đến đối thủ!”"),t("br"),t("br"),n._v("Trương Huyền điểm đầu."),t("br"),t("br"),n._v("Chiêu này ném kiếm, cứ việc đơn giản, trên thực tế nhưng phù hợp Thiên đạo quỹ tích, uy lực cực lớn, chỉ cần tìm đúng đối thủ sơ hở, hầu như không ai cản nổi."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Hoắc Giang Hà cùng Tiết Càn cao thủ như vậy, đều không có biện pháp gì."),t("br"),t("br"),n._v("“Là!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên tràn đầy kích động."),t("br"),t("br"),n._v("Đi qua tu luyện, hắn cũng biết chiêu này đến cùng mạnh cỡ bao nhiêu! Trước đó tu luyện những cái kia tới tỷ thí... Không đáng giá nhắc tới."),t("br"),t("br"),n._v("“Tốt, ta lại truyền thụ cho ngươi một ít, cái khác kiếm thuật, đá ở núi khác, có thể công ngọc, nhiều loại kiếm thuật bổ sung lẫn nhau, mới có thể đi vào bước nhanh hơn...”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền vừa định truyền thụ một ít kiếm pháp trụ cột, liền nghe đến ngoài cửa vang lên rộn rộn ràng ràng thanh âm, ngay sau đó, “Kẹt kẹt!” Một tiếng bị người đụng vỡ, hơn mười người mặc khôi giáp binh sĩ, đẩy cửa đi vào."),t("br"),t("br"),n._v("“Các ngươi ai là tòa phủ đệ này chủ nhân?”"),t("br"),t("br"),n._v("Đi đầu một cái hộ vệ, là cái chừng ba mươi tuổi thanh niên, một thân tu vi, đã đạt đến Cổ Thánh một tầng huyết mạch kéo dài."),t("br"),t("br"),n._v("“Ta!” Nhìn ra là phủ thành chủ binh sĩ, Thiện Hiểu Thiên nhíu nhíu mày, tiến về phía trước một bước."),t("br"),t("br"),n._v("“Người tới, đem hắn bắt lại cho ta!”"),t("br"),t("br"),n._v("Lông mày giương lên, thanh niên vung tay lên."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Sau lưng lập tức lao ra hai cái binh sĩ, đi tới Thiện Hiểu Thiên trước mặt, binh khí trong tay hàn mang lóe lên, trên mặt đằng đằng sát khí."),t("br"),t("br"),n._v("“Ta khuyên ngươi tốt nhất đừng phản kháng, nếu không, bắt giữ tội, có thể làm tràng chém giết!”"),t("br"),t("br"),n._v("Một cái hộ vệ cười lạnh, lấy ra xích sắt, liền muốn cho Thiện Hiểu Thiên còng lại."),t("br"),t("br"),n._v("“Ta phạm vào tội gì? Các ngươi muốn bắt ta?” Không nghĩ tới xuất hiện loại biến cố này, Thiện Hiểu Thiên nhịn không được hỏi."),t("br"),t("br"),n._v("“Đêm qua phủ thành chủ tuần tra, phát hiện tội phạm tào thành lập, dẫn dắt sơn tặc lẻn vào nội thành cùng người tiếp xúc, ý đồ mưu đồ quấy rối! Phái năm mươi tinh nhuệ đi vào vây quét, ai biết... Toàn bộ bị tội phạm chém giết, đi qua vừa giữa trưa điều tra nghe ngóng, phát hiện phủ thành chủ tinh nhuệ, tại đây cái phủ đệ phương hướng biến mất, bởi vậy, ta nghi ngờ ngươi thông đồng sơn tặc, đồ sát binh sĩ!”"),t("br"),t("br"),n._v("Thủ lĩnh thanh niên, lộ ra lạnh lùng chi ý."),t("br"),t("br"),n._v("“Thông đồng tội phạm? Nói vớ nói vẩn...” Thiện Hiểu Thiên sững sờ, đột nhiên giận dữ."),t("br"),t("br"),n._v("Buổi tối hôm qua, nếu không phải lão sư ra tay, hắn đều có thể bị tội phạm chém giết, hắn thông đồng? Nói đùa cái gì?"),t("br"),t("br"),n._v("Thật muốn có người thông đồng, cũng hẳn là là đối phương a!"),t("br"),t("br"),n._v("“Ta biết ngươi sẽ không thừa nhận... Người tới, đem vật chứng dẫn tới!” Thủ lĩnh thanh niên khoát tay chặn lại."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Mấy cái hộ vệ đi lên phía trước, đem vật cầm trong tay tiện tay quăng ra."),t("br"),t("br"),n._v("Là một đống vũ khí cùng mang máu khôi giáp."),t("br"),t("br"),n._v("“Những vật này, là tại ngươi phủ đệ lân cận đào ra, có sơn tặc vũ khí cũng có thành chủ giáp trụ, rất rõ ràng ở đây tiến hành giao chiến, có người cố ý đem vật chứng ẩn núp... Ngươi còn có lời gì nói?”"),t("br"),t("br"),n._v("Thủ lĩnh thanh niên hừ lạnh."),t("br"),t("br"),n._v("“Cái này...” Thiện Hiểu Thiên sững sờ."),t("br"),t("br"),n._v("Những vật này, hôm qua lão sư đều xử lý xong, làm sao sẽ còn có?"),t("br"),t("br"),n._v("“Những này quả thực là sơn tặc vũ khí, những y phục này cũng là, ta lần trước vận hàng bị hắn bọn họ bắt cóc, chắc chắn sẽ không nhận sai!”"),t("br"),t("br"),n._v("“Đúng vậy a, những cái kia là sơn tặc đồ vật, giáp trụ là phủ thành chủ, ta cũng có thể nhận ra...”"),t("br"),t("br"),n._v("“Thông đồng sơn tặc... Tội ác tày trời!”"),t("br"),t("br"),n._v("“Nhất định phải nghiêm thành, thẩm vấn ra mục đích của bọn hắn, thật nếu để cho bọn họ thành công, nội thành cũng không biết có bao nhiêu người phải tao ương...”"),t("br"),t("br"),n._v("“Thiện gia như vậy anh hùng, không nghĩ tới xuống dốc thành như vậy, duy nhất đời sau, không thể tu luyện ngược lại cũng thôi, còn thông đồng sơn tặc, thật sự là mất hết tổ tiên vinh quang!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thiện Hiểu Thiên còn chưa tới cùng giải thích, binh sĩ đằng sau liền vọt tới một đám, nội thành nhân vật có mặt mũi."),t("br"),t("br"),n._v("Thương hội, quán rượu, quán trà, dược liệu thương nghiệp... Hồng nhạn thương hội ông chủ, thình lình xuất hiện."),t("br"),t("br"),n._v("Bọn họ làm ăn, ít nhiều gì đều nhận được sơn tặc lược kiếp, có quyền lên tiếng nhất."),t("br"),t("br"),n._v("“Còn có lời gì nói?”"),t("br"),t("br"),n._v("Những người này xác nhận, thủ lĩnh thanh niên lần nữa xua tay: “Đem hắn nắm chặt phủ thành chủ, cố gắng thẩm vấn!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Cầm trong tay xích sắt binh sĩ đi tới trước mặt."),t("br"),t("br"),n._v("“Muốn gán tội cho người khác...”"),t("br"),t("br"),n._v("Tràn đầy tức giận, Thiện Hiểu Thiên vừa định nói nhiều, đột nhiên vẻ mặt khẽ động: “Tốt, ta liền cùng các ngươi đi phủ thành chủ, hi vọng các ngươi có thể cho ta một cái minh bạch, thật, không thể giả, giả, thật không được!”"),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Thấy hắn không có phản kháng, thủ lĩnh thanh niên lộ ra vẻ thất vọng, tiếp lấy nhìn quanh một vòng: “Thông đồng sơn tặc, phá hoại ổn định, dính dáng cực lớn, chư vị đều nhận được tào thành lập hãm hại, cho nên, hi vọng các ngươi, có thể cùng nhau đi tới phủ thành chủ, cấp cho làm chứng... Cũng có thể thể hiện ra, công chính, công bằng! Chư vị yên tâm, phủ thành chủ, sẽ không bởi vì tới có hôn ước, liền làm việc thiên tư!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Mọi người đồng thời điểm đầu."),t("br"),t("br"),n._v("(Ngày hôm qua một chương, nhìn thấy không ít người đọc chửi bậy, nói một phần sáu không phải một phẩy sáu sáu sáu, phía trước viết, thành chủ hỏi dùng mấy thành lực lượng, 10÷6=1.666, không có bất kỳ cái gì sai lầm ah! Một phẩy sáu sáu thành, lão nhai không có tính toán sai ah! Tuy là ta là giáo viên thể dục, nhưng ta thật dạy qua toán học, xin đừng nên nghi ngờ một vị giáo viên thể dục phẩm đức nghề nghiệp cùng năng lực, cám ơn!)."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1948"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1950"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);