(window.webpackJsonp=window.webpackJsonp||[]).push([[1071],{1426:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1937-muon-gan-toi-cho-nguoi-khac"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1937-muon-gan-toi-cho-nguoi-khac"}},[n._v("#")]),n._v(" Chương 1937: Muốn gán tội cho người khác")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương trước trở về mục lục chương sau trở về trang sách"),t("br"),t("br"),n._v("“Những người còn lại, có thể là đồng mưu, cũng cùng một chỗ bắt...”"),t("br"),t("br"),n._v("Rất nhanh, binh sĩ đem Trương Huyền và Dịch lão cũng khóa lại, mang theo đi ra sân trong."),t("br"),t("br"),n._v("Trương Huyền cũng không phản kháng, mà là lặng lẽ truyền âm qua, nghe được dặn dò, Thiện Hiểu Thiên không tại bối rối, lần nữa khôi phục bị què hình dáng, thân thể co lại thành một đoàn, có vẻ hơi thấp bé, trong cơ thể thực lực, cũng mượn lão sư truyền thụ cho đặc thù pháp quyết ẩn nấp đi, người ngoài nhìn tới, chỉ có Thánh vực lục trọng."),t("br"),t("br"),n._v("Rời đi sân nhỏ, đi vào xe ngựa, thời gian không dài, đến phủ thành chủ."),t("br"),t("br"),n._v("Thời khắc này phủ thành chủ, đã chất đầy người, rậm rạp chằng chịt chừng hơn ngàn nhiều, đều đang nghị luận sơn tặc sự tình."),t("br"),t("br"),n._v("Hiển nhiên, tiết nặng trước kia để cho người ta thả ra tin tức, làm ra hiệu quả."),t("br"),t("br"),n._v("“Sơn tặc vào thành sự tình, phủ thành chủ hết sức quan tâm, đi qua kín đáo điều tra nghe ngóng, phát hiện cùng Thiện gia Thiện Hiểu Thiên liên quan đến, chư vị đều biết, chúng ta Nhị tiểu thư cùng hắn có hôn ước quan hệ, vì tránh hiềm nghi, hiện tại tiến hành công khai thẩm vấn, tuyệt không thiên vị che chở!”"),t("br"),t("br"),n._v("Đứng tại trước đám người mới, tiết nặng lộ ra đại công vô tư biểu lộ."),t("br"),t("br"),n._v("“Không hổ là phủ thành chủ, nhanh như vậy liền tra ra được!”"),t("br"),t("br"),n._v("“Thông đồng sơn tặc, người người phải trừ diệt! Không nghĩ tới vị này Thiện Hiểu Thiên sẽ làm như vậy!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy bắt lấy chính là Thiện Hiểu Thiên, tất cả mọi người thấp giọng."),t("br"),t("br"),n._v("Giữa hai bên thông gia sự tình, nội thành không ít người đều biết, thông đồng sơn tặc, huyên náo như vậy lớn, vậy mà tra được người một nhà, mọi người cũng muốn nhìn một chút, thành chủ đến cùng thái độ gì, có phải hay không còn có thể làm được, công bằng công chính."),t("br"),t("br"),n._v("Nhìn trước mắt khập khiễng đi tới thiếu niên, tiết nặng mí mắt vừa nhấc: “Truyền vật chứng!”"),t("br"),t("br"),n._v("Tiếng nói kết thúc, mấy cái binh sĩ đem trước giáp trụ cùng cung tên để dưới đất, ngay sau đó một người trung niên đi tới: “Hồi bẩm đại nhân, những này ta đều đã kiểm tra qua, cung tên quả thực vì sơn tặc Tào Thành Lập đám người sử dụng, mặt trên còn có bọn họ lưu lại vân tay. Còn những này giáp trụ, là phủ thành chủ phổ thông binh sĩ phân phối, thống nhất chế ngự, phía trên huyết dịch, là tối hôm qua lưu lại!”"),t("br"),t("br"),n._v("“Hồi bẩm đại nhân, ta đêm qua lúc tu luyện, nghe phía bên ngoài có động tĩnh, lặng lẽ đi qua cửa sổ liếc mắt nhìn, phát hiện không ít người mặc quần áo màu đen người, thừa dịp lúc ban đêm lẻn vào Thiện gia, xem ra giống như là đang tiến hành giao dịch nào đó!”"),t("br"),t("br"),n._v("Ngay sau đó một người trung niên đi tới."),t("br"),t("br"),n._v("Thiện Hiểu Thiên liếc mắt nhìn, nhận ra đối phương."),t("br"),t("br"),n._v("Là cách hắn phủ đệ không xa một nhà hộ gia đình, bình thường ở chung cũng không tệ lắm."),t("br"),t("br"),n._v("“Đêm qua ta cũng nhìn thấy, có chừng mấy chục người, ta Lúc đó rất kỳ quái, vừa vặn mang theo ghi chép ngọc tinh, liền đem tình huống lúc đó, ghi xuống!”"),t("br"),t("br"),n._v("đọc ｔruyện ở https://ngantruyen.com/"),t("br"),n._v("Lại một người cũng đi tới, cũng là Thiện Hiểu Thiên hàng xóm."),t("br"),t("br"),n._v("Nói xong, kích hoạt lên ghi chép ngọc tinh, phía trên ghi chép hình ảnh, lập tức hiện ra ở tất cả mọi người phía trước."),t("br"),t("br"),n._v("Đêm trăng, một đám dạ hành nhân, quần áo sơn tặc thống nhất trang phục, lặng yên không tiếng động đi vào Thiện Hiểu Thiên phủ đệ, sau đó... Lại không có đi ra, cũng không có bất kỳ động tĩnh."),t("br"),t("br"),n._v("“Ta trước đó còn tưởng rằng, có phải hay không có cái gì mâu thuẫn, không nghĩ tới là thật!”"),t("br"),t("br"),n._v("“Thiện Hiểu Thiên vì sao muốn thông đồng sơn tặc?”"),t("br"),t("br"),n._v("“Ta cũng cảm thấy kỳ quái, Thiện gia tuy là xuống dốc, ấm no các loại không tính là gì, vì sao muốn mạo hiểm?”"),t("br"),t("br"),n._v("Nhìn rõ ràng ngọc tinh bên trong ghi chép nội dung, bốn phía một hồi xôn xao."),t("br"),t("br"),n._v("Trước đó còn cảm thấy nghi ngờ, giờ phút này tất cả đều nhìn về phía trung tâm bị bắt lại Thiện Hiểu Thiên, tràn đầy khó tin."),t("br"),t("br"),n._v("Thiện gia, hơn mười năm trước, là Huyền Giang thành gia tộc lớn nhất, không có cái thứ hai!"),t("br"),t("br"),n._v("Trong tộc cường giả nhiều vô số kể, phá toái hư không cảnh giới, liền có trọn vẹn ba vị nhiều, ngay cả thành chủ phủ, cũng không dám tới là địch, chỉ có thể thông gia."),t("br"),t("br"),n._v("Tuy là trong vòng một đêm, gặp biến cố, thảm tao diệt môn, cường giả hầu như toàn bộ chết hết, nhưng gia nghiệp còn tại, vì sao muốn làm ra thông đồng sơn tặc sự tình?"),t("br"),t("br"),n._v("Thật là khiến người khó hiểu."),t("br"),t("br"),n._v("“Nhị tiểu thư...”"),t("br"),t("br"),n._v("Mọi người ở đây nghi ngờ thời điểm, một cái gấp gáp thanh âm vang lên, mọi người đồng loạt nhìn lại, ngay sau đó nhìn thấy Nhị tiểu thư Tiết Cầm, vội vã đi tới."),t("br"),t("br"),n._v("Thấy là hắn, mọi người tất cả đều lộ ra vẻ cổ quái."),t("br"),t("br"),n._v("Thiện Hiểu Thiên tới có hôn ước, giờ phút này hẳn là tránh hiềm nghi, nhưng vọt thẳng đi ra, chẳng lẽ có sự tình gì?"),t("br"),t("br"),n._v("“Vì cái gì?”"),t("br"),t("br"),n._v("Không để ý tới nha hoàn ngăn cản, nhìn về phía trong đại sảnh ở giữa Thiện Hiểu Thiên hốc mắt ửng hồng, Tiết Cầm tràn đầy biểu tình không dám tin tưởng."),t("br"),t("br"),n._v("“Ngươi ta từ nhỏ có hôn ước, ngươi tuy là không thể tu luyện, nhưng ta chưa hề ghét bỏ, thủy chung như một! Lần này, Lăng Vân kiếm các đến đây chiêu thu đệ tử, ta cảm thấy là cơ hội, đi qua cố gắng, thông qua được Hoắc sư huynh tuyển chọn, trở thành tạp dịch đệ tử... Vốn nghĩ đến, chỉ cần đi vào tông môn, liền có khả năng tìm tới tốt hơn dược vật, giúp ngươi khôi phục thương thế, để ngươi có thể một lần nữa tu luyện, khôi phục gia tộc uy danh...”"),t("br"),t("br"),n._v("Tiết Cầm lộ ra bi ai: “Vốn cho rằng là tin tức tốt, phái người báo tin ngươi! Có thể ngươi vì sao muốn thông đồng sơn tặc, ngăn cản chuyện này? Thậm chí còn đem ta bắt lấy, đối ta đánh, để cho ta thay đổi chủ ý... Ngươi chẳng lẽ cho rằng, ta đi vào tông môn, liền sẽ đem ngươi quên? Cố ý muốn để cho ta mất đi cơ hội lần này? Có thể ngươi có biết hay không... Ta sở dĩ muốn đi vào tông môn, cũng là vì ngươi ah...”"),t("br"),t("br"),n._v("Nói xong, nước mắt từ trong hốc mắt chảy ra, trong mắt ai oán, ta thấy mà yêu."),t("br"),t("br"),n._v("“Nguyên lai là Nhị tiểu thư, thông qua được Lăng Vân kiếm các tuyển chọn, vị này Thiện Hiểu Thiên lòng sinh tự ti, tính toán ngăn cản chuyện này!”"),t("br"),t("br"),n._v("“Thiện gia kẻ thù không ít, vị này Thiện Hiểu Thiên sở dĩ có thể sống đến hiện tại, chủ yếu bởi vì phủ thành chủ cùng Nhị tiểu thư che chở, Một khi Nhị tiểu thư rời đi, đi vào tông môn, phía sau hắn sẽ trôi qua thế nào, ai cũng không biết, khả năng lo lắng gặp phải nguy hiểm, lúc này mới bí quá hoá liều, làm ra loại sự tình này a!”"),t("br"),t("br"),n._v("“Chỉ cần Nhị tiểu thư mất tích, không cách nào cùng Hoắc sư huynh đám người rời đi, chẳng khác nào mất đi cơ hội lần này, từ đó một mực ở lại phủ thành chủ, thậm chí tới kết hôn... Như vậy trải qua, hắn cũng liền an toàn, sẽ không xuất hiện bất kỳ biến cố!”"),t("br"),t("br"),n._v("“Nguyên nhân tại đây, khó trách... Ngươi nhìn Nhị tiểu thư mặt, tựa hồ có chút sưng đỏ, khẳng định bị những sơn tặc kia bắt cóc, bị không ít tội!”"),t("br"),t("br"),n._v("“Đúng vậy a, không phải, đường đường phủ thành chủ Nhị tiểu thư, ai dám đánh nàng?”"),t("br"),t("br"),n._v("“Sơn tặc chỉ cần đưa tiền, liền sẽ làm việc, ai cũng nghi ngờ không đến trên đầu của hắn, thật đúng là kế sách hay!”"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được khóc lóc kể lể, tất cả mọi người mới chợt hiểu ra, lần nữa nhìn về phía Thiện Hiểu Thiên tràn đầy chán ghét."),t("br"),t("br"),n._v("Một nữ nhân, vì ngươi làm nhiều như vậy, thế mà không biết cảm ơn, còn làm ra loại sự tình này, mất mặt xấu hổ!"),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Bị khóa ở một bên Trương Huyền, vốn buồn ngủ, nhìn thấy vị này Nhị tiểu thư vừa xuất hiện, lập tức đem mọi người cảm xúc điều động, nhịn không được cảm xúc một tiếng."),t("br"),t("br"),n._v("Người lợi hại cỡ này, kiếp trước không làm cái bóng dáng, đều có tài mà không phát huy được."),t("br"),t("br"),n._v("Người người đều có đồng cảm kẻ yếu cùng tâm lý nữ nhân, huống chi nữ nhân xinh đẹp, nước mắt như mưa."),t("br"),t("br"),n._v("Vốn, cái gọi là vật chứng, nhân chứng, còn chưa đủ cấp cho Thiện Hiểu Thiên định tội, dù sao, động cơ của hắn không đủ, vì sao muốn đắc tội Thành chủ phủ, đi thông đồng giết người như ngóe sơn tặc?"),t("br"),t("br"),n._v("Giờ phút này, tất cả lý do đều đầy đủ."),t("br"),t("br"),n._v("Vừa vặn cũng giải thích, Nhị tiểu thư đi qua Lăng Vân kiếm các khảo hạch sự tình!"),t("br"),t("br"),n._v("Như vậy, vị này Tiết Cầm không những danh khí bên trên không có tổn hại, còn thành trung liệt con gái, để cho người ta khâm phục."),t("br"),t("br"),n._v("Thiện Hiểu Thiên thành mảnh vụn nam, từ hôn cũng đã thành đương nhiên."),t("br"),t("br"),n._v("Kế sách hay, mưu kế hay!"),t("br"),t("br"),n._v("Một chiêu đổi trắng thay đen, dùng thật sự là trôi chảy, không có chút nào không hài hòa."),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Thiện Hiểu Thiên cũng khí sắp nổ."),t("br"),t("br"),n._v("Chủ động từ hôn, ta đồng ý, không hài lòng, để cho ta lãng phí bản thân! Không đồng ý, liền giết người. Kết quả giết người không thành, cho ta trên người giội nước bẩn..."),t("br"),t("br"),n._v("Thật đúng là dùng bất cứ thủ đoạn nào."),t("br"),t("br"),n._v("“Tiểu thư, không nên tức giận, loại người này, không xứng thương thế của ngươi tâm!”"),t("br"),t("br"),n._v("Thấy dư luận đã bị nhóm người mình khống chế, tiết nặng biết mình kế hoạch thành công, vị này Thiện Hiểu Thiên, cũng có thể nói nhiều hơn nữa, cũng khẳng định vô dụng, lúc này mới thở phào nhẹ nhõm, hai bước đi tới trước mặt, an ủi một câu, nhìn về phía cách đó không xa nha hoàn: “Tốt, còn không mau mang tiểu thư trở về?”"),t("br"),t("br"),n._v("“Vâng!” Nha hoàn vội vã đi tới trước mặt."),t("br"),t("br"),n._v("Làm xong những này, tiết nặng lúc này mới quay đầu nhìn về phía cách đó không xa thiếu niên, trong ánh mắt mang theo băng lãnh: “Hiện tại chứng cứ vô cùng xác thực, ngươi còn có lời gì nói?”"),t("br"),t("br"),n._v("“Dựa theo ý của các ngươi, là ta, bởi vì Tiết Cầm tiểu thư, đi qua Lăng Vân kiếm các khảo hạch, lòng sinh tự ti, mới thông đồng sơn tặc, phá hoại nàng trở thành tạp dịch đệ tử đúng không?”"),t("br"),t("br"),n._v("Biết giờ phút này nói nhiều hơn nữa, đều là nguỵ biện, Thiện Hiểu Thiên thản nhiên nhìn tới."),t("br"),t("br"),n._v("“Biết là tốt rồi, làm sao, chứng cứ vô cùng xác thực phía dưới, còn muốn phủ nhận?”"),t("br"),t("br"),n._v("Tiết trầm lãnh cười."),t("br"),t("br"),n._v("“Phủ nhận?” Thiện Hiểu Thiên lắc đầu: “Ta... Muốn gặp Lăng Vân kiếm các Hoắc Giang Hà sư huynh!”"),t("br"),t("br"),n._v("“Làm càn! Ngươi một cái phế vật, có tư cách gì thấy Hoắc sư huynh?”"),t("br"),t("br"),n._v("Còn tưởng rằng hắn sẽ không thừa nhận, nói ra trước đó từ hôn sự tình, thật nói như vậy, cũng vừa chuẩn bị cẩn thận đối sách, có thể để cho trực tiếp rơi vào cạm bẫy, lại không xoay người khả năng!"),t("br"),t("br"),n._v("Nhưng đối phương, không nói chuyện này, ngược lại muốn gặp Hoắc sư huynh... Muốn làm gì?"),t("br"),t("br"),n._v("“Ta là không có tư cách... Chẳng qua, ta nghe nói, Lăng Vân kiếm các chiêu thu đệ tử, chỉ cần đi qua Hoắc sư huynh khảo hạch, coi như trở thành trong đó đệ tử... Ta thân là Huyền Giang thành người, hơn nữa tuổi tác phù hợp, cũng có tư cách tham gia khảo hạch a!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên cười nói."),t("br"),t("br"),n._v("“Bất luận người nào, đều có cơ hội, đi qua khảo hạch là có thể trở thành Lăng Vân kiếm các đệ tử... Mà ngươi, nhất không có cơ hội! Không được nói chuyện viển vông!”"),t("br"),t("br"),n._v("Nghe được lời nói của hắn, tiết nặng lập tức cười ha hả, giống như là nghe được trên thế giới êm tai nhất truyện cười."),t("br"),t("br"),n._v("Một cái chỉ có Thánh vực lục trọng phế vật, vậy mà nghĩ đến tham gia khảo hạch Lăng Vân kiếm các khảo hạch, ai cho lá gan?"),t("br"),t("br"),n._v("“Huống chi, Hoắc sư huynh, giao long nhân vật, ngươi cảm thấy sẽ đặc biệt vì ngươi một cái phế vật, tiến hành khảo hạch?”"),t("br"),t("br"),n._v("Tiết nặng tiếp tục nói."),t("br"),t("br"),n._v("“Thật sự là không biết trời cao đất rộng!”"),t("br"),t("br"),n._v("“Có thể tham gia khảo hạch, không có chỗ nào mà không phải là rồng phượng trong loài người, cái tên này bị què, còn lại không thể tu luyện... Thật muốn cười chết người ư?”"),t("br"),t("br"),n._v("“Hoắc sư huynh đang bề bộn tuyển chọn thiên tài chân chính, nào có thời gian, tới khảo hạch một cái phế vật!”"),t("br"),t("br"),n._v("“Đoán chừng là đầu óc phá hủy...”"),t("br"),t("br"),n._v("Phía dưới một hồi xôn xao, một cái lộ ra nghiền ngẫm vui vẻ."),t("br"),t("br"),n._v("“Tốt, ngoan ngoãn nhận tội, có thể miễn thu da thịt nỗi khổ... Hoắc sư huynh không có khả năng đặc biệt tới, khảo hạch ngươi dạng này một cái phế vật...”"),t("br"),t("br"),n._v("Cắt đứt mọi người thảo luận, tiết nặng hét lớn một tiếng, vừa định nói tiếp, liền nghe đến bên ngoài viện, vang lên một cái thanh âm nhàn nhạt."),t("br"),t("br"),n._v("“Ta vì sao không thể tới khảo hạch?”"),t("br"),t("br"),n._v("Ngay sau đó, một bóng người cao to, sải bước đi đi vào."),t("br"),t("br"),n._v("Chính là Hoắc Giang Hà."),t("br"),t("br"),n._v("(Đã sụp đổ, không giải thích còn tốt, một giải thích, càng nhiều người không hiểu. Một thân công lực là mười thành, cái này hiểu chưa? Một phần sáu lực lượng chính là10÷6=1.666 thành... Có vấn đề ư? Nếu như vẫn không rõ, một thân lực lượng dùng một phần năm, cũng chính là hai thành công lực... Cái này dù sao cũng nên hiểu a. Đừng có lại đơn độc tìm ta, qq, Wechat, Weibo... Không cần cùng ta giải thích, ta nói qua, ta sẽ không viết sai. Các ngươi nhất định phải tin tưởng ta phẩm đức nghề nghiệp. Tuy là ta là giáo viên thể dục, nhưng ta bốn năm đại học tổng thành tích toàn hệ đệ nhất tốt nghiệp. Thi đại học Năm đó, thể dục sinh bên trong, cũng là toàn huyện xếp hạng trước ba. Thi đại học toán học, 124 phân, lý tông270 phân, liền tiếng Anh quá kém, 40 phân, mới đi thể dục. Ghi danh lão sư, thi viết toàn tỉnh thứ hai, phỏng vấn toàn tỉnh đệ nhất, tổng thành tích toàn tỉnh đệ nhất. Mặc dù không phải học bá, chí ít không phải học mẩu vụn, sẽ không phạm loại sai lầm cấp thấp này. Cám ơn. Ps: Tính toán qua, trở lên giải thích không nhận phí.)."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1949"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1951"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);