(window.webpackJsonp=window.webpackJsonp||[]).push([[1072],{1427:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1938-đao-nguoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1938-đao-nguoc"}},[n._v("#")]),n._v(" Chương 1938: Đảo ngược")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Hoắc Giang Hà sau lưng, đi theo một vị trung niên, thân hình cao lớn, một thân giáp trụ, trong ánh mắt để lộ ra nồng đậm uy nghiêm, đang muốn rời đi Tiết Cầm nhìn thấy người này, nhất thời ngừng lại, khom mình hành lễ: “Cha! Hoắc sư huynh!”"),t("br"),t("br"),n._v("Chính là bị Trương Huyền một đao đánh chết Huyền Giang thành thành chủ, Tiết Càn."),t("br"),t("br"),n._v("Hai người sau lưng, là hơn mười vị người trẻ tuổi, đều mười sáu mười bảy tuổi hình dáng, từng cái thực lực bất phàm."),t("br"),t("br"),n._v("Đều là lần này tới tham gia khảo hạch trẻ tuổi tuấn kiệt."),t("br"),t("br"),n._v("“Giang Hà hiền chất...”"),t("br"),t("br"),n._v("Thấy đi tới nơi này, cắt đứt ngay tại thẩm vấn Tiết Trầm, Tiết Càn thành chủ nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Huyền Giang thành tuyển chọn nhân tài, làm thành chủ, muốn đích thân đi cùng, ai biết vừa mới tiến đi xong lần đầu thử nghiệm, còn không có tiến hành thi vòng hai, vị này Hoắc Giang Hà, nhất định muốn chạy đến nơi đây, để hắn không hiểu ra sao, không hiểu rõ nổi."),t("br"),t("br"),n._v("“Thi vòng hai ngay ở chỗ này tiến hành, vừa vặn nhiều người nhìn như vậy, cũng càng có thể biểu hiện công bằng, công chính!” Gật gật đầu, Hoắc Giang Hà quay người lại ngồi ở gian phòng chính giữa vị trí."),t("br"),t("br"),n._v("Tiến hành khảo hạch, hắn đại biểu là Lăng Vân kiếm các, Tiết Càn cho dù là thành chủ, cũng chỉ có thể ngồi tại hạ tay."),t("br"),t("br"),n._v("Tiết Trầm một mặt choáng váng."),t("br"),t("br"),n._v("Ngay tại thẩm vấn, chạy tới khảo hạch, phía dưới hắn nên làm cái gì?"),t("br"),t("br"),n._v("“Hoắc sư huynh, ta thân là Huyền Giang thành một thành viên, có thể hay không tham gia khảo hạch?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên cất cao giọng nói."),t("br"),t("br"),n._v("“Tự nhiên có thể!”"),t("br"),t("br"),n._v("Hoắc Giang Hà gật đầu."),t("br"),t("br"),n._v("“Hoắc công tử, vị này Thiện Hiểu Thiên, chỉ có Thánh vực lục trọng, thực lực thấp, huống chi thông đồng sơn tặc, gieo họa bách tính, nhân phẩm thấp kém, để hắn tham dự khảo hạch, quả thực chính là đối kiếm các làm nhục...”"),t("br"),t("br"),n._v("Tiết Trầm tràn đầy sốt ruột."),t("br"),t("br"),n._v("Tuy là biết rõ cái tên này, coi như khảo hạch, cũng không cách nào bị tuyển chọn bên trên, có thể... Thật nếu để cho loại nhân sâm này thêm khảo hạch, cái này thẩm vấn biết, cũng liền trở nên dở dở ương ương."),t("br"),t("br"),n._v("“Ngươi nói thực lực của ta thấp?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên nhìn qua."),t("br"),t("br"),n._v("“Không sai! Bị què, Thánh vực lục trọng, người bình thường cũng không bằng, không phải thấp là cái gì?” Tiết Trầm lông mày giương lên."),t("br"),t("br"),n._v("“Đã nói như vậy, vậy ta muốn hỏi một chút... Nhị tiểu thư, Tiết Cầm thực lực thế nào?” Thiện Hiểu Thiên thản nhiên nói."),t("br"),t("br"),n._v("“Tiểu thư nhà ta là thiên tài, năm nay chẳng qua mười sáu tuổi, đã đi đến Đại Thánh tam trọng đại viên mãn, bất cứ lúc nào cũng sẽ đột phá!” Tiết Trầm một mặt kiêu ngạo."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên lông mày đột nhiên giương lên."),t("br"),t("br"),n._v("Ong ong ong!"),t("br"),t("br"),n._v("Từng đạo kiếm khí lập tức từ đầu ngón tay bắn ra, trên cổ tay, còng xích sắt, cùng kiếm khí đụng một cái, lập tức gãy thành từng đoạn từng đoạn mảnh vỡ!"),t("br"),t("br"),n._v("Phá vỡ ràng buộc, trên người thiếu niên khí tức, không ngừng kéo lên, thời gian nháy mắt ngay tại Đại Thánh tam trọng đại viên mãn ngừng lại, cúi xuống sống lưng, cũng dần dần thẳng tắp, vừa rồi thoạt nhìn, chỉ có khoảng một mét sáu, hoàn toàn giãn ra, mặc dù không có Trương Huyền cao như vậy, nhưng cũng có 1m75 trái phải."),t("br"),t("br"),n._v("Mấu chốt nhất là, chậm rãi tiến lên, trên đùi đâu còn có bị què bộ dạng!"),t("br"),t("br"),n._v("Thậm chí, liền dung mạo, đều tựa hồ có biến hóa, càng thêm cương nghị cùng tuấn tú, trước đó mười cái thiếu niên, tới tỷ thí, vậy mà tất cả đều tự lấy làm xấu hổ."),t("br"),t("br"),n._v("“Điều đó không có khả năng...”"),t("br"),t("br"),n._v("Tròng mắt sắp rơi trên mặt đất, Tiết Cầm muốn điên rồi."),t("br"),t("br"),n._v("Cái tên này trong lòng nàng, chính là cái nhỏ yếu, vô dụng đại danh từ, nằm mơ đều không có nghĩ đến, một chút có biến hóa nghiêng trời lệch đất, quả thực cùng biến thành người khác giống như!"),t("br"),t("br"),n._v("Không để ý tới khiếp sợ nữ hài, Thiện Hiểu Thiên đi vài bước, nhìn về phía cách đó không xa Tiết Trầm, khóe miệng nâng lên: “Đồng dạng cấp bậc, ta là phế vật, nhị tiểu thư chính là thiên tài... Không biết Tiết Trầm giáo đầu, là lấy cái gì phán đoán? Đầu ngón chân vẫn là cái mông?”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Lui về sau một bước, Tiết Trầm run rẩy."),t("br"),t("br"),n._v("Cái tên này lúc nào, có thể tu luyện? Hơn nữa còn là... Đại Thánh tam trọng đại viên mãn?"),t("br"),t("br"),n._v("Không riêng gì bọn họ, bốn phía mọi người cũng lặng ngắt như tờ."),t("br"),t("br"),n._v("Trước đó, cho ra lý do, là bởi vì cái tên này, không thể tu luyện, muốn dựa dẫm phủ thành chủ bảo vệ tính mạng, mới thông đồng sơn tặc, ngăn cản Tiết Cầm trở thành kiếm các đệ tử, hiện tại... Hắn nắm giữ thực lực, hơn nữa mạnh như vậy, lý do tự nhiên cũng liền không còn sức thuyết phục!"),t("br"),t("br"),n._v("“Các ngươi đồng loạt ra tay, tới đối phó ta...”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Thiện Hiểu Thiên nhìn về phía Hoắc sư huynh mang tới hơn mười thiếu niên."),t("br"),t("br"),n._v("“Đồng loạt ra tay?”"),t("br"),t("br"),n._v("Mọi người sửng sốt một chút, thấy Hoắc sư huynh gật đầu đồng ý, tất cả đều mắt sáng lên, nắm tay bên trong trường kiếm đồng loạt đâm tới."),t("br"),t("br"),n._v("Thiện Hiểu Thiên chập ngón tay làm kiếm, đưa tay về phía trước, tựa như ưng trảo, lập tức từ một thiếu niên trong tay đem trường kiếm bóp tới, cổ tay rung lên, kéo ra kiếm hoa."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Hơn mười đạo kiếm khí, từ mũi kiếm bắn ra."),t("br"),t("br"),n._v("Tuy là chỉ cùng lão sư học tập một chiêu “Vứt kiếm”, nhưng lão sư giảng giải không ít liên quan tới kiếm đạo cơ sở, không đem tuyệt chiêu thi triển đi ra, sức chiến đấu cũng cực mạnh."),t("br"),t("br"),n._v("Liền giống như, Tiểu lý phi đao, cũng không phải là sẽ chỉ ném phi đao, không còn phi đao, đồng dạng là thiên hạ bài danh phía trên cường giả."),t("br"),t("br"),n._v("Leng keng! Leng keng! Leng keng!"),t("br"),t("br"),n._v("Kiếm khí bay ra, một hồi trường kiếm rơi xuống đất âm thanh, hơn mười thiếu niên đồng thời khoanh tay cổ tay, từng cái trong mắt tràn đầy kinh hoàng."),t("br"),t("br"),n._v("Thiện Hiểu Thiên vừa rồi tiện tay thi triển ra kiếm chiêu, vậy mà tại trong nháy mắt, liền đem bọn họ toàn bộ đâm thủng!"),t("br"),t("br"),n._v("Nhờ có hạ thủ lưu tình, không dùng lực lượng, nếu không, cổ tay chỉ sợ đều sẽ trực tiếp rơi trên mặt đất."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ngón tay búng một cái, trường kiếm như thiểm điện nghiêng cắm ở Tiết Cầm trước mặt, không ngừng lay động, phát ra nghẹn ngào vang lên, Thiện Hiểu Thiên một thân ngạo khí, tựa như cắm ở trong gió Bạch Dương: “Không biết... Thân là thiên tài nhị tiểu thư, có thể làm được hay không ta cái phế vật này vừa làm được những này?”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Một hồi yên lặng."),t("br"),t("br"),n._v("Mười mấy người vây công, tiện tay đoạt kiếm, thân thể không động đậy, một kiếm đâm thủng mọi người cổ tay... Phần này thực lực cùng kiếm thuật, quả thực nghe rợn cả người!"),t("br"),t("br"),n._v("Loại người này nếu là phế vật... Bọn họ tính là gì?"),t("br"),t("br"),n._v("Gương mặt xinh đẹp tái đi, Tiết Cầm thân thể mềm mại run rẩy."),t("br"),n._v("Tuy là đều là Đại Thánh tam trọng đại viên mãn, nhưng mà nàng tuyệt đối không làm được điểm ấy!"),t("br"),t("br"),n._v("Thật muốn xông tới, có thể sẽ giống như những người khác, trong nháy mắt bị thua."),t("br"),t("br"),n._v("“Ngươi coi như không phải phế vật... Thông đồng sơn tặc, cũng là sự thật, ngươi hàng xóm, cũng có thể làm chứng, đêm khuya có sơn tặc tại phủ đệ của ngươi ẩn hiện...”"),t("br"),t("br"),n._v("Tiết Trầm nghiến răng."),t("br"),t("br"),n._v("“Các ngươi đều tận mắt thấy, có sơn tặc, đi vào phủ đệ của ta?”"),t("br"),t("br"),n._v("Không trả lời hắn, Thiện Hiểu Thiên quay đầu nhìn về phía vừa rồi đi ra tới hai người chứng."),t("br"),t("br"),n._v("“Là... Chính xác trăm phần trăm!”"),t("br"),t("br"),n._v("“Chúng ta thật... Thấy được!”"),t("br"),t("br"),n._v("Bị ánh mắt của hắn nhìn chằm chằm, hai người đồng thời cái cổ co rụt lại."),t("br"),t("br"),n._v("Nếu như đối phương thủy chung không thể tu luyện, ngược lại cũng thôi, có thể tu luyện, hơn nữa thiên tư cao như vậy, nghĩ tới năm đó Thiện gia uy thế... Nhất thời tất cả đều có chút sợ hãi, nói chuyện cũng không có trước đó như vậy hùng hồn."),t("br"),t("br"),n._v("Liền tại bọn hắn sợ mất mật thời điểm, Thiện Hiểu Thiên âm thanh vang lên: “Nhìn thấy, vậy đúng rồi... Chứng minh các ngươi thực sự nói thật!”"),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Hai người đồng thời ngẩn ngơ."),t("br"),t("br"),n._v("Đúng?"),t("br"),t("br"),n._v("Ngươi biết bản thân đang nói cái gì sao?"),t("br"),t("br"),n._v("Như vậy chẳng khác nào thừa nhận thông đồng sơn tặc, cho dù thiên phú rất cao, thực lực rất mạnh, đạo đức có hại, Lăng Vân kiếm các, cũng không có khả năng thu làm đệ tử."),t("br"),t("br"),n._v("Những người khác cũng tất cả đều xôn xao."),t("br"),t("br"),n._v("“Ha ha, thừa nhận là tốt rồi, đã thừa nhận, vậy thì không có gì có thể nói, người tới, còn không đem cái này thông đồng sơn tặc gia hỏa, bắt lại...”"),t("br"),t("br"),n._v("Tiết Trầm cũng sửng sốt một cái, ngay sau đó một mặt mừng rỡ."),t("br"),t("br"),n._v("Vốn cho rằng đối phương triển lộ thực lực, sẽ chết sống đều không thừa nhận, như thế, kế hoạch của hắn sẽ mười phần bị động, nằm mơ cũng không ngờ tới, cái tên này thiếu thông minh đồng dạng, trực tiếp nhận!"),t("br"),t("br"),n._v("Mà nên lấy nhiều người như vậy!"),t("br"),t("br"),n._v("Lần này, lại không có đường lùi... Tương đương trên đầu chụp chụp mũ, hẳn phải chết không nghi ngờ!"),t("br"),t("br"),n._v("“Bắt lại?”"),t("br"),t("br"),n._v("Cười lạnh, Thiện Hiểu Thiên đột nhiên ôm quyền khom người: “Hoắc sư huynh, ta bây giờ muốn truyền một cái nhân chứng, mong rằng đáp ứng!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Hoắc Giang Hà gật gật đầu."),t("br"),t("br"),n._v("“Vào đi!” Thiện Hiểu Thiên nhìn ra phía ngoài."),t("br"),t("br"),n._v("Kèm theo lời nói, một người mặc hắc bào, mang theo mũ rộng vành bóng người từ bên ngoài đi vào, đi tới trước mặt mọi người, chậm rãi đem mũ hái xuống."),t("br"),t("br"),n._v("“Là Tào Thành Lập!”"),t("br"),t("br"),n._v("“Vị kia sơn tặc đầu lĩnh...”"),t("br"),t("br"),n._v("“Hắn lại dám tới phủ thành chủ, giết hắn...”"),t("br"),t("br"),n._v("“Thiện Hiểu Thiên giáng xuống hắn đi vào, xem ra hai người thật sự có cấu kết...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn rõ ràng bóng người hình dáng, phía dưới nhất thời xôn xao."),t("br"),t("br"),n._v("Không phải người khác chính là, vị kia sơn tặc đầu lĩnh... Tào Thành Lập!"),t("br"),t("br"),n._v("Cái tên này từ trước tới nay giấu ở rừng sâu núi thẳm, phủ thành chủ mấy lần vây quét đều vô công mà trở lại, giờ phút này lại dám chạy đến nơi đây, lá gan có phần quá mập!"),t("br"),t("br"),n._v("“Mọi người thấy sao? Vị này Tào Thành Lập vậy mà nghe hắn mà nói, hiển nhiên, sớm đã thông đồng...”"),t("br"),t("br"),n._v("Tiết Trầm kích động suýt chút nữa nhảy dựng lên."),t("br"),t("br"),n._v("Trước đó tất cả mọi chuyện, đều không có thực chùy, nhiều nhất chỉ là một ít ngụy tạo chứng cứ, hiện tại cái tên này vậy mà đem vị này sơn tặc đầu sỏ cho mang đến..."),t("br"),t("br"),n._v("Khẳng định cũng lại cọ rửa không rõ!"),t("br"),t("br"),n._v("“Thông đồng?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên cười nhạo một tiếng, nhìn về phía trước mắt hắc bào sơn tặc: “Đến cùng ai thông đồng ngươi... Còn xin tào trại chủ tự thân trả lời đi!”"),t("br"),t("br"),n._v("“Là hắn...”"),t("br"),t("br"),n._v("Tào Thành Lập tiến lên một bước, một chỉ Tiết Trầm cùng Tiết Cầm: “Còn có nàng...”"),t("br"),t("br"),n._v("“Ngươi nói bậy bạ gì đó? Ít ngậm máu phun người...”"),t("br"),t("br"),n._v("Tiết Trầm lông mày giương lên."),t("br"),t("br"),n._v("“Ngậm máu phun người?” Tào Thành Lập nắm đấm xiết chặt, tiến về phía trước một bước: “Hôm qua, vị này Tiết Trầm, đặc biệt tới gặp ta, ủy thác ta giết một người, chỉ cần thành công, thù lao là năm vạn thông thần tệ! Ta hỏi thăm một chút, muốn giết người này không có thực lực gì, là cái phế vật, tại chỗ đáp ứng...”"),t("br"),t("br"),n._v("“Giết người?”"),t("br"),t("br"),n._v("“Phế vật?”"),t("br"),t("br"),n._v("Trong đám người có thông minh, đã đoán đi ra, đồng loạt nhìn về phía cách đó không xa Thiện Hiểu Thiên."),t("br"),t("br"),n._v("Những năm này, cũng chỉ có hắn gánh vác lấy cái này thanh danh."),t("br"),t("br"),n._v("“Không tệ, chính là vị này Thiện Hiểu Thiên công tử!”"),t("br"),t("br"),n._v("Tào Thành Lập nói tiếp: “Ta ban đêm liền đi mai phục, kết quả... Hiểu Thiên công tử thực lực vượt qua tưởng tượng, ta tự nhiên không có khả năng thành công! Cho nên, toàn quân bị diệt, đều bị hắn chém giết...”"),t("br"),t("br"),n._v("Ngựa cùng xe đem hắn người giết chết, chuyện này thực sự nghe rợn cả người, nói ra, khẳng định không có người tin tưởng, cùng hắn như vậy, không bằng không nói."),t("br"),t("br"),n._v("“Vận khí ta tốt, chạy thoát, lần nữa tìm tới vị này Tiết Trầm, hắn chỉ cho ta năm trăm thông thần tệ... Ta lúc ấy mười phần tức giận, liền muốn nhìn một chút, đến cùng là ai, liền lặng lẽ đi theo tới, phát hiện hai người bọn họ đi vào phủ thành chủ, hơn nữa, vừa vặn thấy được cái này ngọc tinh ghi chép một màn...”"),t("br"),t("br"),n._v("Cổ tay rung lên, Tào Thành Lập đem đêm qua ghi chép hình ảnh thả ra ngoài."),t("br"),t("br"),n._v("Mọi người lập tức nhìn thấy, nghiêm ngặt trong phủ thành chủ, Tiết Trầm đứng tại trước đám người mặt, để rất nhiều hộ vệ thay đổi sơn tặc trang phục, hơn nữa nghiêm túc bàn giao, đem Thiện Hiểu Thiên một nhà, toàn bộ chém giết!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1950"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1952"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);