(window.webpackJsonp=window.webpackJsonp||[]).push([[1073],{1428:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1939-luc-truong-lao-toi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1939-luc-truong-lao-toi"}},[n._v("#")]),n._v(" Chương 1939: Lục trưởng lão tới")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1939: Lục trưởng lão đến!"),t("br"),t("br"),n._v("Chương trước trở về mục lục chương sau trở về trang sách"),t("br"),t("br"),n._v("Hình ảnh tiếp tục."),t("br"),t("br"),n._v("Đám này mặc sơn tặc trang phục hộ vệ, dọc theo đường phố lặng lẽ lẻn vào Thiện Hiểu Thiên phủ đệ."),t("br"),t("br"),n._v("“Cái này hình ảnh hình như cùng vừa rồi hắn thả ra đồng dạng...”"),t("br"),t("br"),n._v("“Đúng vậy a... Ta cho rằng thật sự là sơn tặc, không nghĩ tới lại là phủ thành chủ hộ vệ...”"),t("br"),t("br"),n._v("“Phủ thành chủ hộ vệ ngụy trang thành sơn tặc, cố ý giết người...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Rất nhanh, có người nhận ra hình ảnh bên trong ghi chép cảnh tượng, cùng trước đó nhân chứng ghi chép giống nhau như đúc."),t("br"),t("br"),n._v("Vốn cho rằng là sơn tặc, không nghĩ tới, nhìn toàn bộ nội dung, mới biết được... Lại là phủ thành chủ hộ vệ ngụy trang!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Hộ vệ đi vào phủ đệ, hình ảnh ngừng lại, Tào Thành Lập đem ghi chép ngọc tinh cầm ở lòng bàn tay, lần nữa nhìn về phía trước mắt Tiết Trầm: “Ngươi còn có lời gì nói?”"),t("br"),t("br"),n._v("“Ngươi... Đây là giả tạo!”"),t("br"),t("br"),n._v("Tiết Trầm sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Hiển nhiên nằm mơ đều không có nghĩ đến, loại sự tình này vậy mà lại bị người theo dõi, còn ghi chép xuống dưới."),t("br"),t("br"),n._v("“Giả tạo? Ngươi giả tạo một chút thử một chút!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên hướng về phía trước."),t("br"),t("br"),n._v("Ghi chép ngọc tinh, ghi chép nội dung, đều là thật sự phát sinh, không có cách nào giả tạo, nguyên nhân chính là như vậy, mới có công chính tính."),t("br"),t("br"),n._v("“Ta...” Tiết Trầm lui về sau một bước: “Ngươi cùng tam tiểu thư, có hôn ước, thực lực ngươi mạnh, tự nhiên tốt nhất, chúng ta vì sao muốn giết ngươi, làm như vậy, chẳng phải làm bẩn phủ thành chủ minh bạch...”"),t("br"),t("br"),n._v("“Cũng đúng ah...”"),t("br"),t("br"),n._v("Mọi người sững sờ, đồng loạt nhìn tới."),t("br"),t("br"),n._v("Hai bên có hôn ước, thật không thích hợp, từ hôn chính là, làm gì tìm sơn tặc giết người?"),t("br"),t("br"),n._v("Bản thân cho mình trên lưng nồi đen?"),t("br"),t("br"),n._v("“Tại sao muốn giết ta? Nếu như ngươi quên, có thể nhìn một chút cái này!”"),t("br"),t("br"),n._v("Khẽ cười một tiếng, Thiện Hiểu Thiên cũng từ trong ngực lấy ra một cái ghi chép ngọc tinh, ngón tay một chút, bên trong hình ảnh phóng thích ra ngoài."),t("br"),t("br"),n._v("“Làm sao, biết rõ, ta tới ý tứ, không bản thân đi ra, cho rằng phái cái hạ nhân, đem hôn ước lấy tới, liền muốn lừa gạt?” Hình ảnh bên trong, Tiết Cầm một mặt lãnh ngạo, cao quý như là công chúa."),t("br"),t("br"),n._v("“Không được tốt lắm, rất đơn giản, ta muốn ngươi toàn thành công bố, là ngươi không xứng với ta, chủ động yêu cầu từ hôn, ta phủ thành chủ, từ chối liên tục, sau đó tôn trọng ý kiến của ngươi, lúc này mới đem hôn ước kết thúc... Không phải, ngươi trực tiếp đem hôn ước lấy ra, là muốn nói cho tất cả mọi người, ta Tiết Cầm không muốn gả cho ngươi? Làm cho tất cả mọi người đâm phủ thành chủ cột sống ư?”"),t("br"),t("br"),n._v("“Không đáp ứng cũng rất đơn giản! Thiện gia những năm này, đắc tội không ít người, nếu như không có cùng phủ thành chủ thông gia, thêm vào bản thân ngươi chính là củi mục, hẳn là đã sớm diệt tuyệt... Chỉ cần những người này, muốn ra tay, chỉ bằng các ngươi một chủ một tớ, có thể chống đỡ được?”"),t("br"),t("br"),n._v("Tiết Trầm cười lạnh liên tục."),t("br"),t("br"),n._v("“Thong thả miệng mồm mọi người? Các ngươi sau khi chết, thành chủ cực kỳ bi thương, sẽ đích thân phái người thay các ngươi báo thù, sau đó tiểu thư, càng sẽ khóc rống thật nhiều ngày, cuối cùng thương tâm rời đi nơi đây, đi vào Lăng Vân kiếm các...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ngọc tinh bên trong, đem hai người tới Đan phủ diễu võ giương oai cảnh tượng ghi chép rõ rõ ràng ràng, không kém chút nào!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Nguyên lai nàng là muốn từ hôn, đối phương lui còn không hài lòng, còn muốn thanh danh của mình, không tiếc giết người...”"),t("br"),t("br"),n._v("“Quả thực lòng dạ rắn rết!”"),t("br"),t("br"),n._v("“Phủ thành chủ những năm này, thanh danh rất tốt, không nghĩ tới vậy mà làm ra loại sự tình này...”"),t("br"),t("br"),n._v("“Mất mặt, ta xấu hổ tại tới làm bạn...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),t("br"),n._v("Đem một màn này xem xong, tất cả mọi người rõ ràng chuyện gì xảy ra, lần nữa nhìn về phía Tiết Trầm cùng Tiết Cầm, tràn đầy địch ý."),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Gương mặt xinh đẹp trắng bệch, Tiết Cầm chỉ cảm thấy toàn thân mềm nhũn, tê liệt ngã xuống trên mặt đất."),t("br"),t("br"),n._v("Vốn nghĩ, không chỉ có thể đủ từ hôn, thoát khỏi vị này phế vật, còn có thể thu hoạch được thanh danh tốt, nằm mơ đều không có nghĩ đến... Lại bị trần trụi vạch trần tại đây a nhiều người phía trước."),t("br"),t("br"),n._v("Từ giờ khắc này, thanh danh của nàng, xem như triệt để xấu!"),t("br"),t("br"),n._v("Không muốn gả người, không tiếc thông đồng sơn tặc, tàn sát vị hôn phu..."),t("br"),t("br"),n._v("Loại này thanh danh truyền đi, ai còn dám lấy nàng?"),t("br"),t("br"),n._v("Thật muốn cưới, ngộ nhỡ có sơn tặc đập cửa... Chẳng phải nguy rồi?"),t("br"),t("br"),n._v("“Ăn nói bừa bãi, ta hôm nay sẽ dạy ngươi cái này nói vớ nói vẩn gia hỏa...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, Tiết Trầm thân thể nhảy lên, lập tức hướng Thiện Hiểu Thiên lao đến."),t("br"),t("br"),n._v("Tiếng gió rít gào, chưởng lực mạnh mẽ."),t("br"),t("br"),n._v("Hiện tại cảnh tượng như thế này, đã triệt để lâm vào bị động, duy nhất phương pháp chính là, đem Thiện Hiểu Thiên cùng Tào Thành Lập giết chết, tới cái không có chứng cứ."),t("br"),t("br"),n._v("Hắn Cổ Thánh tam trọng thực lực, vừa ra tay, gian phòng lập tức giống như là bị giam cầm ở, hai người toàn thân băng lãnh, cũng lại không thể động đậy."),t("br"),t("br"),n._v("“Làm càn!”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương lại dám khi hắn mặt giết người, Hoắc Giang Hà con mắt run lên."),t("br"),t("br"),n._v("Quả thực không đem hắn vị này Lăng Vân kiếm các đệ tử để vào mắt."),t("br"),t("br"),n._v("Cổ tay rung lên, trường kiếm xuất khiếu, đang muốn ngăn cản đối phương, liền cảm thấy tay cánh tay trầm xuống, ngồi tại ra tay bên cạnh thành chủ Tiết Càn cười khanh khách nhìn lại: “Giang Hà hiền chất không nên gấp gáp, Tiết Trầm chỉ là hù dọa một chút, sẽ không làm ẩu, ngươi thân là Kiếm Các thế hệ sau, nhúng tay ta nội thành sự tình, ngược lại mất thân phận!”"),t("br"),t("br"),n._v("Hoắc Giang Hà híp mắt lại."),t("br"),t("br"),n._v("Tiết Trầm sát khí ngang dọc, đương nhiên sẽ không là hù dọa, không tự mình ra tay, tùy ý đối phương chưởng lực tung tích, phía trước hai người khẳng định sẽ bị đồng thời đánh chết!"),t("br"),t("br"),n._v("Sau đó... Vị này Tiết Càn thành chủ liền có thể từ chối quản giáo thuộc hạ vô phương, nhiều nhất đem Tiết Trầm giam giữ, răn đe..."),t("br"),t("br"),n._v("Lúc này, Thiện Hiểu Thiên cùng Tào Thành Lập tất cả đều chết, lời nói còn không tùy ý phủ thành chủ đi nói?"),t("br"),t("br"),n._v("Không bao lâu, khẳng định liền sẽ thả ra, một chút việc cũng sẽ không phát sinh..."),t("br"),n._v(" “Không được...”"),t("br"),t("br"),n._v("Bên này Hoắc Giang Hà bị ngăn cản, nhìn thấy uy lực vô cùng chưởng lực, Tào Thành Lập khuôn mặt trắng bệch, sợ tới mức sắp ngã sấp xuống."),t("br"),t("br"),n._v("Hắn là nghe thư tịch mệnh lệnh, tới giúp Thiện Hiểu Thiên giải oan, cũng không đại biểu bản thân muốn chết ah..."),t("br"),t("br"),n._v("Ngay tại cảm giác lần này cũng không còn cách nào trốn đi, hẳn phải chết không nghi ngờ thời điểm, trước mắt chưởng lực một cái nháy mắt, tiêu tán không thấy hình bóng, vội vàng ngẩng đầu nhìn lại, ngay sau đó nhìn thấy, vị thành chủ này phủ kiếm thuật tổng giáo tập, Cổ Thánh tam trọng siêu cấp cường giả, chẳng biết lúc nào, đã té ngã trên đất, cắt đứt hô hấp, trên trán cắm một cái xanh biếc cỏ xanh."),t("br"),t("br"),n._v("“Chết?”"),t("br"),t("br"),n._v("Thân thể cứng đờ, vội vàng nhìn lại, ngay sau đó nhìn thấy cỏ xanh, từ đối phương con mắt đâm vào, lỗ tai đâm ra, đem đại não triệt để xuyên qua, trực tiếp hủy diệt sinh cơ, nhất thời mất mạng!"),t("br"),t("br"),n._v("Như vậy mềm mại cỏ xanh, đâm xuyên con mắt đầu... Chém giết một vị Cổ Thánh tam trọng cường giả..."),t("br"),t("br"),n._v("Đây là thực lực gì?"),t("br"),t("br"),n._v("Lạnh cả người, Tào Thành Lập không dám động đậy."),t("br"),t("br"),n._v("Một bên Hoắc Giang Hà, cũng đột nhiên đứng dậy, tràn đầy khó tin."),t("br"),t("br"),n._v("Hắn tu vi là Cổ Thánh bốn tầng sơ kỳ, cùng trước mắt Tiết Càn thành chủ đồng dạng, nhưng... Muốn làm được điểm ấy, cũng đồng dạng không hoàn thành được."),t("br"),t("br"),n._v("Cỏ xanh mềm mại, không thể dùng lực, cũng không thể ẩn chứa chân khí, nhưng lại có thể trong nháy mắt phá vỡ Cổ Thánh tam trọng cường giả hộ thể chân khí, đem đầu đâm xuyên..."),t("br"),t("br"),n._v("Mấu chốt nhất là..."),t("br"),t("br"),n._v("Lấy nhãn lực của hắn, vậy mà từ đầu đến cuối cũng không thấy, đến cùng ai ra tay, cỏ xanh lại là từ chỗ nào cái phương hướng xuất hiện!"),t("br"),t("br"),n._v("Liền giống như thứ này, lăng không đi ra đồng dạng."),t("br"),t("br"),n._v("Không chỉ hắn khiếp sợ, ngăn lại hắn thành chủ Tiết Càn cũng lạnh cả người."),t("br"),t("br"),n._v("Hiển nhiên, hắn đồng dạng không nhìn ra cỏ xanh từ đâu xuất hiện."),t("br"),t("br"),n._v("Cẩn thận nhớ lại một chút, đổi lại hắn, chỉ sợ cũng không cách nào ngăn cản!"),t("br"),t("br"),n._v("“Lão sư... Thật mạnh!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Lão sư để hắn không cần lo lắng, nhưng vừa rồi cảnh tượng vẫn là lau vệt mồ hôi."),t("br"),t("br"),n._v("Sớm biết lão sư khẳng định không đơn giản, chỉ là không nghĩ tới cường đại như thế!"),t("br"),t("br"),n._v("Đem khiếp sợ cùng kích động áp chế lại, Thiện Hiểu Thiên vừa định nói tiếp, chỉ thấy phía trước thành chủ Tiết Càn đột nhiên đứng dậy, một mặt lòng đầy căm phẫn: “Tiết Trầm thân là ta kiếm thuật tổng giáo tập, vậy mà làm ra loại sự tình này, là ta bỏ bê quản giáo, thẹn là đứng đầu một thành... Loại này bại hoại, nhất định phải giết chết, răn đe!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên sững sờ."),t("br"),t("br"),n._v("Nghe hắn khẩu khí... Tiết Trầm chính là hắn giết?"),t("br"),t("br"),n._v("Có xấu hổ hay không?"),t("br"),t("br"),n._v("“Hiểu Thiên hiền chất, chuyện này ta cũng không hiểu rõ tình hình, may mắn ngươi thông minh cơ cảnh, vạch trần cái tên này thủ đoạn! Mới để cho ta phủ thành chủ, bảo vệ danh dự!”"),t("br"),t("br"),n._v("Tiết Càn hai bước đi tới Thiện Hiểu Thiên phía trước, một bộ ôn hoà trưởng bối hình dáng: “Ngươi cùng tiểu nữ hôn ước, là lúc trước phụ thân ngươi đám người tự thân xác định, hiện tại tổ tiên qua đời, hậu nhân làm sao có thể vi phạm bọn họ trông đợi? Lại nói, nhỏ đàn sở dĩ làm ra cái loại này quyết định, khẳng định cũng đúng nhận lấy Tiết Trầm mê hoặc, cái tên này am hiểu nhất chính là mê hoặc nhân tâm...”"),t("br"),t("br"),n._v("Thấy hắn nói ra lời này, phản ứng cấp tốc như vậy, Thiện Hiểu Thiên cười lạnh."),t("br"),t("br"),n._v("Khó trách có thể làm thành chủ, không hổ là lão hồ ly."),t("br"),t("br"),n._v("Lão sư khiêm tốn, không nguyện ý hiển lộ ra."),t("br"),t("br"),n._v("Mà giờ khắc này, Tiết Trầm bị giết, tất cả mọi người ở đây, liền vị thành chủ này thực lực cao nhất, mọi người khẳng định cảm thấy là hắn ra tay."),t("br"),t("br"),n._v("Người đã chết, tội danh toàn bộ đẩy ngã cái tên này trên người, sau đó lại tìm lý do, liền cùng phủ thành chủ không có liên quan quá nhiều... Đến lúc đó, lại nghĩ biện pháp tạo nên ân huệ, chắc hẳn nội thành dư luận cũng duy trì liên tục không được bao lâu!"),t("br"),t("br"),n._v("Như vậy dưới tình thế xấu, có thể nhanh như vậy làm được điểm ấy, vị này Tiết Càn thật là độc ác."),t("br"),t("br"),n._v("“Thành chủ nói đùa...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, Thiện Hiểu Thiên khẽ nói: “Phủ thành chủ phái ra năm mươi vị tinh nhuệ, trong vòng một đêm toàn bộ tử vong, ngươi nói là thuộc hạ tự tiện làm, sẽ có người tin tưởng?”"),t("br"),t("br"),n._v("Năm mươi tên tinh nhuệ, đối với phủ thành chủ tới nói, cũng không phải số lượng nhỏ, một chút tất cả đều chết, làm sao có thể không biết?"),t("br"),t("br"),n._v("Thiện Hiểu Thiên nói thẳng ra, tương đương đem tấm màn che cũng xé ra."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Không nghĩ tới nói những lời này, đối phương không cảm kích, còn nói như vậy, Tiết Càn sầm mặt lại, ánh mắt híp lại, truyền âm tới: “Làm sao, Tiết Trầm chết, còn chưa đủ, muốn đem ta cũng đẩy ngã? Ngươi có chứng cứ, chứng minh ta tham dự việc này ư?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên nói không ra lời."),t("br"),t("br"),n._v("Tiết Cầm chỉ là tại từ hôn, cái khác tất cả đều Tiết Trầm tại làm, không có bất kỳ chứng cớ nào biểu hiện, cùng vị thành chủ này liên quan đến, thậm chí... Tiết Cầm cũng có thể đem sự tình từ chối đi, nhiều nhất chỉ là từ hôn!"),t("br"),t("br"),n._v("Không thích hợp bản thân, vì hạnh phúc, chạy tới từ hôn, nhiều nhất nhận đạo đức khiển trách, cũng không có xúc phạm pháp luật!"),t("br"),t("br"),n._v("“Không có chứng cứ, tốt nhất ngoan ngoãn chớ nói nhảm! Ta biết sau lưng ngươi có cường giả chỗ dựa, mới có thể tuỳ tiện chém giết Tiết Trầm, nhưng... Ta cái này thành chủ, là Lăng Vân kiếm các sắc phong! Chỉ có trong đó trưởng lão, mới có tư cách xử lý... Sau lưng ngươi cao thủ, dám động thủ với ta... Tương đương đánh Kiếm Các mặt, ngươi cảm thấy bọn họ sẽ bỏ qua khiêu chiến sáu đại tông môn quyền uy người? Vẫn cảm thấy, sau lưng ngươi người kia, có thể chống cự sáu đại tông môn áp lực?”"),t("br"),t("br"),n._v("Cười lạnh một tiếng, Tiết Càn tiếp tục truyền âm tới: “Không muốn để cho sau lưng ngươi vị kia, bởi vì chuyện của ngươi bị liên lụy, liền ngoan ngoãn nghe ta, ngày hôm nay việc này như vậy coi như thôi! Mọi người tất cả đều vui vẻ! Ngươi hôn ước, cũng sẽ như cũ, sẽ không xuất hiện biến cố...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên con mắt thấu hồng, nắm đấm bóp cục cục vang vọng, sống chết nói không ra lời."),t("br"),t("br"),n._v("Thật ra thì hắn cũng biết, cha mẹ đám người chết, cùng vị này Tiết thành chủ có cửa ải cực kỳ lớn thắt, cũng đã sớm muốn báo thù!"),t("br"),t("br"),n._v("Đáng tiếc... Đối phương nói rất đúng!"),t("br"),t("br"),n._v("Phủ thành chủ, trước mắt vị này thực lực mạnh nhất, ép, thật vận dụng binh sĩ, tiến hành đại đồ sát, hắn cùng lão sư căn bản là không có cách ngăn cản!"),t("br"),t("br"),n._v("Lão sư nếu là thật ra tay, giết đối phương, tương đương đắc tội Lăng Vân kiếm các..."),t("br"),t("br"),n._v("Lão sư thực lực cứ việc rất mạnh, hắn cũng không cho rằng, có thể đối kháng đại lục sáu đại tông môn một trong!"),t("br"),t("br"),n._v("“Tốt, không cần ta nói nhiều, ngươi cũng hẳn là rõ ràng, nên nói như thế nào a!”"),t("br"),t("br"),n._v("Thấy hắn dao động, Tiết Càn hừ một tiếng: “Muốn động ta, trừ phi Kiếm Các trưởng lão có thể nghe được cầu nguyện của ngươi, xuất hiện tại phía trước, nếu không, đừng không biết tự lượng sức mình...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lời còn chưa dứt, trên không một cái Thánh thú kêu to vang lên, ngay sau đó vài bóng người, từ lưng thú bên trên rơi xuống."),t("br"),t("br"),n._v("“Lục trưởng lão!”"),t("br"),t("br"),n._v("Nhìn thấy mấy người này, Hoắc Giang Hà ánh mắt sáng lên, vội vàng tiến về phía trước một bước, ôm quyền khom người."),t("br"),t("br"),n._v("Lục Vân trưởng lão, từ Tinh Diệu thành đến!"),t("br"),t("br"),n._v("Thân thể cứng đờ, Tiết Càn đang tại nói, lập tức nghẹn tại cổ họng.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1951"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1953"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);