(window.webpackJsonp=window.webpackJsonp||[]).push([[1074],{1429:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1940-noi-mon-đe-tu-khao-hach"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1940-noi-mon-đe-tu-khao-hach"}},[n._v("#")]),n._v(" Chương 1940: Nội môn đệ tử khảo hạch")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương trước trở về mục lục chương sau trở về trang sách"),i("br"),i("br"),n._v("Lục Vân trưởng lão tuy chỉ là phụ trách ngoại môn trưởng lão, nhưng là thực sự Hư Tiên cảnh cường giả, vốn chỉ phụ trách Tinh Diệu thành ngoại môn thế hệ sau khảo hạch, Huyền Giang thành loại này chỉ có thể thu nhận tạp dịch đệ tử địa phương, tình huống bình thường, là căn bản sẽ không tới!"),i("br"),i("br"),n._v("Mặc dù có tình huống đặc biệt, cũng đúng đi ngang qua, đem người mang đi..."),i("br"),i("br"),n._v("Làm sao đột nhiên đi tới trước mặt?"),i("br"),i("br"),n._v("Tiết Càn tràn đầy khó tin, nhưng cũng không dám thất lễ, vội vàng tiến lên, khom người bái kiến."),i("br"),i("br"),n._v("“Huyền Giang thành thành chủ, Tiết Càn, gặp qua Lục Vân trưởng lão?”"),i("br"),i("br"),n._v("“Ừm!” Lục trưởng lão không thèm để ý, tùy ý khoát tay áo, nhìn về phía trước mặt Hoắc Giang Hà: “Thế nào? Hắn nhưng xuất hiện?”"),i("br"),i("br"),n._v("“Bẩm báo trưởng lão, cụ thể là ai, cũng không rõ ràng, chẳng qua... Có lẽ cùng vị này Thiện Hiểu Thiên liên quan đến!”"),i("br"),i("br"),n._v("Hoắc Giang Hà chỉ một chút."),i("br"),i("br"),n._v("Tối hôm qua, tại thông thần điện, vị kia Thiên Nhai, để hắn hỗ trợ, chính là để hắn đúng giờ xuất hiện tại đây, kết quả, có hắn tọa trấn, Thiện Hiểu Thiên mới có thể cọ rửa oan khuất, rực rỡ hào quang... Bởi vậy, vị này coi như không phải Thiên Nhai, khẳng định cũng có cực lớn liên hệ."),i("br"),i("br"),n._v("“Hắn?”"),i("br"),i("br"),n._v("Lục Vân trưởng lão mang theo nghi ngờ."),i("br"),i("br"),n._v("Lấy nhãn lực của hắn, tự nhiên có thể liếc mắt nhìn ra, vị này thực lực cụ thể... Đại Thánh tam trọng đại viên mãn!"),i("br"),i("br"),n._v("Thực lực như vậy, liền có thể giết đến hắn rất nhiều đệ tử, toàn bộ quăng mũ cởi giáp?"),i("br"),i("br"),n._v("“Là như vậy...” Thấy đối phương không tin, Hoắc Giang Hà đem trước sự tình nói rõ chi tiết một lần."),i("br"),i("br"),n._v("Nghe xong giới thiệu, Lục Vân trưởng lão lông mày trầm xuống."),i("br"),i("br"),n._v("Thiên tài như thế, tại Lăng Vân kiếm các quản lý phạm vi bên trong, lại bị người như vậy thiết kế, nhận như vậy đãi ngộ không công bằng, tương đương trực tiếp tại đánh mặt của hắn."),i("br"),i("br"),n._v("Cố nén không vui, nhìn lại: “Ngươi là Thiện Hiểu Thiên?”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Thiện Hiểu Thiên hành lễ."),i("br"),i("br"),n._v("“Nhưng có ý đồ gia nhập Lăng Vân kiếm các?” Lục Vân trưởng lão hỏi."),i("br"),i("br"),n._v('Thiện Hiểu Thiên sửng sốt một chút, lặng lẽ quay đầu nhìn cách đó không xa lão sư liếc mắt, thấy hắn âm thầm gật đầu, vội vàng khom người: "Nguyện ý!'),i("br"),i("br"),n._v("“Rất tốt!” Lục Vân trưởng lão gật gật đầu: “Từ giờ trở đi, ngươi chính là Kiếm Các đệ tử! Ai dám bắt nạt ngươi, chính là cùng Kiếm Các đối nghịch, cần thừa nhận Kiếm Các lửa giận!”"),i("br"),i("br"),n._v("“Ách...”"),i("br"),i("br"),n._v("Thiện Hiểu Thiên ngẩn ngơ."),i("br"),i("br"),n._v("Còn tưởng rằng, cần lần nữa khảo hạch một lần, lần nữa thi triển thực lực, không ngờ tới, đơn giản như vậy."),i("br"),i("br"),n._v("Nếu là, không biết đối phương thật sự là trưởng lão, còn tưởng rằng là diễn hí kịch."),i("br"),i("br"),n._v("Sớm nghe nói, Kiếm Các chịu trách nhiệm thu nhận học sinh ngoại môn trưởng lão Lục Vân, tính cách hùng hùng hổ hổ, không nghĩ tới như vậy..."),i("br"),i("br"),n._v("“Chúc mừng Hiểu Thiên hiền chất trở thành Kiếm Các thế hệ sau...” Tiết Càn phản ứng cực nhanh, vội vàng cười ha hả đi tới trước mặt, không có chút nào trước đó khó xử."),i("br"),i("br"),n._v("Không để ý tới đối phương, hít sâu một hơi, Thiện Hiểu Thiên giống như là xuống quyết định gì đó, hít sâu một hơi, trực tiếp quỳ rạp xuống đất: “Bẩm báo trưởng lão, còn xin vì đệ tử làm chủ!”"),i("br"),i("br"),n._v("“Cứ nói đừng ngại!” Lục Vân trưởng lão bàn tay lớn vẫy một cái."),i("br"),i("br"),n._v("“Ta Thiện gia, là Huyền Giang thành đại gia tộc, bởi vì chiến lực quá mạnh, bị phủ thành chủ ham muốn, thi triển quỷ kế, dẫn đến cả nhà diệt tuyệt... Đây là chứng cứ!”"),i("br"),i("br"),n._v("Thiện Hiểu Thiên từ trong ngực lấy ra một cái ngọc bài đưa tới."),i("br"),i("br"),n._v("Gia tộc bị hủy diệt, Dịch lão vẫn chưa ngừng nghỉ, bốn phía sưu tập chứng cứ, kèm theo sưu tập càng nhiều, càng biểu hiện cùng trước mắt vị thành chủ này liên quan đến."),i("br"),i("br"),n._v("Trước đó không dám nói ra, giờ phút này, Lăng Vân kiếm các trưởng lão ở đây, lại không bất kỳ băn khoăn nào."),i("br"),i("br"),n._v("Mấu chốt, cũng đúng lão sư ý tứ."),i("br"),i("br"),n._v("Nhận lấy ngọc bài, cúi đầu liếc mắt nhìn, Lục Vân trưởng lão con mắt nhất thời híp lại."),i("br"),i("br"),n._v("Phía trên ghi lại chứng cứ, từng đầu mười phần rõ ràng, không có bất kỳ cái gì sơ xuất cùng sai lầm."),i("br"),i("br"),n._v("Mỗi một đầu đều cho thấy, Thiện gia trên trăm miệng nhân khẩu, đều cùng thành chủ Tiết Càn, có không thể dứt bỏ quan hệ."),i("br"),i("br"),n._v("“Ngươi có lời gì nói?”"),i("br"),i("br"),n._v("Quay đầu nhìn lại, tiện tay đem ngọc bài ném tới trước mặt."),i("br"),i("br"),n._v("Tiết Càn vội vàng cầm lấy liếc mắt nhìn, đồng tử đột nhiên co rút lại, quỳ mọp xuống đất: “Lục trưởng lão, oan uổng... Hai nhà chúng ta sớm tu thân, xuống tay với bọn họ, đối ta cũng không có chỗ tốt gì! Cái này nhất định là, gia nữ không hiểu chuyện, chạy tới từ hôn, dẫn đến Hiểu Thiên hiền chất không vui, mới gây ra hiểu lầm... Còn xin trưởng lão minh giám!”"),i("br"),i("br"),n._v("“Minh giám? Tốt! Ta sẽ cho ngươi thoả mãn trả lời chắc chắn!”"),i("br"),i("br"),n._v("Hừ một tiếng, Lục trưởng lão không tại nhiều nói, cũng không để ý tới quỳ trên mặt đất thành chủ, mà là nhìn về phía trước mắt Thiện Hiểu Thiên: “Ngươi chứng cớ này viết, tuy là có lý có chứng cứ, nhưng thời gian lâu dài, rất nhiều đã điều tra không ra, lại thêm, đối phương thân là thành chủ, cố ý che giấu dấu vết lời nói, liền xem như ta, cũng bất lực!”"),i("br"),i("br"),n._v("“Vâng”"),i("br"),i("br"),n._v("Thiện Hiểu Thiên cúi đầu."),i("br"),i("br"),n._v("Hắn biết điểm ấy."),i("br"),i("br"),n._v("Gia tộc thảm tao hủy diệt, đã qua mười năm, trong vòng mười năm, Huyền Giang thành không biết phát sinh bao nhiêu biến hóa, những chứng cớ này cho dù là đúng, cũng đã vật đổi sao dời, cũng lại thẩm tra không tới."),i("br"),i("br"),n._v("Huống chi, vị thành chủ này, mua danh cầu lợi, vì danh tiếng, liền lùi lại cưới sự tình, đều làm ra được, diệt môn dính dáng như vậy lớn, khẳng định đã sớm xử lý giọt nước không lọt, muốn kiểm tra cũng không thể nào tra được."),i("br"),i("br"),n._v("“Không có chứng cớ xác thật, liền muốn giết chết một vị thành chủ, tương đương miệt thị Lăng Vân kiếm các uy nghiêm!”"),i("br"),i("br"),n._v("“Chẳng qua... Bất cứ chuyện gì đều có ngoại lệ!”"),i("br"),i("br"),n._v("Lục Vân trưởng lão mắt sáng lên: “Đó chính là... Có đầy đủ thực lực! Để Lăng Vân kiếm các, cũng vì đó kiêng kị! Đương nhiên sẽ không bởi vì một cái tam lưu thành thị nho nhỏ thành chủ, mà đắc tội! Còn có... Trở thành Lăng Vân kiếm các nội môn đệ tử! Một khi thành công, Kiếm Các có nghĩa vụ, giúp mình đệ tử xử lý phiền phức, để cho hắn an tâm tu luyện.”"),i("br"),i("br"),n._v("“Nội môn đệ tử?”"),i("br"),i("br"),n._v("Thiện Hiểu Thiên sững sờ."),i("br"),i("br"),n._v("Lăng Vân kiếm các, tại Huyền Giang thành chỉ thu nhận tạp dịch đệ tử."),i("br"),i("br"),n._v("Liền như thế một cái chăm sóc người nhân vật, đi qua khảo hạch, Tiết Cầm đuôi đều muốn vểnh đến trên trời, tự cho là một bước lên trời, trông mong chạy tới từ hôn, thật muốn trở thành nội môn thế hệ sau, địa vị còn tại Hoắc Giang Hà đám người phía trên, so với Huyền Giang thành thành chủ, đều muốn trọng yếu hơn!"),i("br"),i("br"),n._v("Tự nhiên sẽ nhận Kiếm Các bảo vệ."),i("br"),n._v(" Chỉ là..."),i("br"),i("br"),n._v("Thực sự quá khó khăn."),i("br"),i("br"),n._v("Hoắc Giang Hà đường đường Cổ Thánh tầng thứ tư phá toái hư không, cũng không vào vào, hắn chỉ có Đại Thánh tam trọng đại viên mãn, làm sao có thể?"),i("br"),i("br"),n._v("“Không sai, Lăng Vân kiếm các nội môn thế hệ sau, mỗi một vị, đều là Kiếm Các của cải, địa vị so với ta vị này ngoại môn trưởng lão, đều chỉ mạnh không yếu... Chỉ cần ngươi thành công, cho dù không có chứng cứ, giết vị thành chủ này, cũng không có người sẽ nói cái gì!”"),i("br"),i("br"),n._v("Lục Vân trưởng lão thản nhiên nói."),i("br"),i("br"),n._v("Pháp luật mãi mãi cũng là giam cầm tiểu nhân vật, cường giả chân chính, quy tắc căn bản khống chế không đến, chớ nói chi là chứng cớ!"),i("br"),i("br"),n._v("Giết liền giết."),i("br"),i("br"),n._v("Một cái tam lưu thành trấn thành chủ mà thôi, chết một cái, khẳng định sẽ có người thay thế, cùng nội môn thế hệ sau so, cái nào nặng cái nào nhẹ, liếc mắt liền biết."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Không nghĩ tới Lục Vân trưởng lão nói ra lời này, Tiết Càn lông mày nhảy loạn."),i("br"),i("br"),n._v("Chẳng lẽ đối phương là muốn... Để vị này Thiện Hiểu Thiên khảo hạch trở thành nội môn thế hệ sau a?"),i("br"),i("br"),n._v("Chẳng qua, nội môn thế hệ sau khảo hạch độ khó, hắn biết, cái tên này, cứ việc thiên tư không sai, muốn thành công, cũng gần như không có khả năng!"),i("br"),i("br"),n._v("“Xin hỏi trưởng lão, nội môn thế hệ sau thế nào khảo hạch? Ta nguyện ý thử một lần!”"),i("br"),i("br"),n._v("Trầm tư phút chốc, Thiện Hiểu Thiên nói."),i("br"),i("br"),n._v("“Nội môn khảo hạch, không nhìn tu vi, mà là nhìn tiềm lực cùng thiên tư! Tiềm lực cùng thiên tư cao, dù là tu vi thấp một ít, cũng có thể trực tiếp tiến vào bên trong, không cần đi qua ngoại môn lịch luyện. Bình thường tình huống, cần khảo hạch có hai cái... Thứ nhất, mười bảy tuổi trước, trở thành Cổ Thánh cường giả! Thứ hai, đi qua nội môn kiếm thuật khảo hạch!”"),i("br"),i("br"),n._v("Lục Vân trưởng lão thản nhiên nói: “Ngươi bây giờ bao nhiêu?”"),i("br"),i("br"),n._v("“Ta mười sáu tuổi, chẳng qua...” Sửng sốt một chút, Thiện Hiểu Thiên vừa định nói tiếp, liền bị lão giả cắt đứt: “Không đến mười bảy là được, hiện tại là Đại Thánh tam trọng đại viên mãn, chứng minh mười bảy tuổi là có cơ hội đột phá đến cấp bậc này! Còn nội môn kiếm thuật khảo hạch... Hoắc Giang Hà là ngoại môn thế hệ sau đại sư huynh, kiếm thuật đã phù hợp nội môn tiêu chuẩn, chỉ cần cùng cấp bậc bên dưới, có thể đem chiến thắng... Liền đại biểu đi qua!”"),i("br"),i("br"),n._v("“Cùng cấp bậc chiến thắng?”"),i("br"),i("br"),n._v("Thiện Hiểu Thiên có chút khẩn trương."),i("br"),i("br"),n._v("Hắn chỉ cùng lão sư học qua một đêm kiếm pháp, đối phương nhưng mà Kiếm Các vô thượng cao thủ, luyện kiếm không dưới hai mươi năm."),i("br"),i("br"),n._v("Cùng cấp bậc muốn thắng được, chỉ sợ rất khó!"),i("br"),i("br"),n._v("Lục Vân trưởng lão nói: “Không sai, ngươi có dám thử một chút?”"),i("br"),i("br"),n._v("“Nguyện ý thử một lần!”"),i("br"),i("br"),n._v("Không chần chờ quá lâu, Thiện Hiểu Thiên điểm đầu."),i("br"),i("br"),n._v("Chuyện này với hắn tới nói, là một cơ hội!"),i("br"),i("br"),n._v("Chỉ cần bỏ lỡ, lại nghĩ báo thù, liền gần như không có khả năng!"),i("br"),i("br"),n._v("Dù sao, Tiết Càn biết mình muốn giết hắn, lại biết đi vào Kiếm Các, há có thể một mực đợi ở chỗ này chờ chết?"),i("br"),i("br"),n._v("Khẳng định đã sớm chạy trốn."),i("br"),i("br"),n._v("Thế giới như vậy lớn, Một khi trốn đi, lại nghĩ tìm tới, không thể nghi ngờ mò kim đáy biển!"),i("br"),i("br"),n._v("“Rất tốt!” Lục Vân trưởng lão gật gật đầu, nhìn về phía Hoắc Giang Hà: “Đè thấp tu vi cùng hắn tương đồng, dùng ra toàn lực chiến đấu, không cần thiết hạ thủ lưu tình!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Hoắc Giang Hà gật gật đầu, mấy bước đi tới trước mặt, tu vi thời gian nháy mắt liền áp chế đến Đại Thánh tam trọng."),i("br"),i("br"),n._v("“Mời!”"),i("br"),i("br"),n._v("Hai người tách ra đứng vững, riêng phần mình cầm trong tay trường kiếm."),i("br"),i("br"),n._v("“Lão sư...”"),i("br"),i("br"),n._v("Lần đầu tiên cùng loại này cường giả tỷ thí, Thiện Hiểu Thiên có chút nóng nảy, nhìn về phía lão sư, chỉ thấy người sau, trên tay vẫn như cũ mang theo xích sắt, híp mắt lại, một bộ việc không liên quan đến mình treo lên thật cao hình dáng."),i("br"),i("br"),n._v("Xem ra... Hắn căn bản cũng không quản!"),i("br"),i("br"),n._v("“Liều mạng...”"),i("br"),i("br"),n._v("Cắn răng một cái, Thiện Hiểu Thiên trong ánh mắt lộ ra kiên định."),i("br"),i("br"),n._v("Lão sư đã cho hắn nhiều như vậy, có thể hay không trở thành nội môn thế hệ sau, mấu chốt nhìn hắn, lại không có thể lùi bước."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Liền cái này một chút thời gian, đối diện Hoắc Giang Hà đã cầm trong tay trường kiếm đi tới trước mặt, kiếm mang lóe lên, hóa thành Giang Hà."),i("br"),i("br"),n._v("Vốn cho rằng, khẳng định muốn đem hết toàn lực Thiện Hiểu Thiên, nhìn rõ ràng kiếm pháp của đối phương, nhịn không được sững sờ."),i("br"),i("br"),n._v("Kiếm của đối phương chiêu biến hóa, cứ việc phức tạp, nhưng lão sư dường như tất cả đều nói qua, tại đây chút kiến thức phía trước phía trước, trước mắt hoa lệ chiêu số, liền giống như người mặc tiệc tối trang phục mỹ nữ, lại hoa lệ, lại đoan trang... Cởi xuống về sau, cũng cùng những nữ nhân khác giống nhau như đúc."),i("br"),i("br"),n._v("Sẽ không nhiều ra một cái linh kiện, cũng sẽ không thiếu một cái bộ phận."),i("br"),i("br"),n._v("Biết những này, còn lại liền đơn giản."),i("br"),i("br"),n._v("Loại bỏ Hư Vọng, nhắm thẳng vào bản chất, thời gian nháy mắt, Thiện Hiểu Thiên tìm ra bảy, tám loại phương pháp phá giải."),i("br"),i("br"),n._v("Trường kiếm hướng về phía trước một đưa."),i("br"),i("br"),n._v("Lập tức đâm vào Giang Hà bên trong một chỗ."),i("br"),i("br"),n._v("Hoắc Giang Hà sững sờ, nhịn không được hướng về phía sau nhảy ra."),i("br"),i("br"),n._v("Thấy lão sư phương pháp quả nhiên hữu hiệu, Thiện Hiểu Thiên lần nữa hướng về phía trước, lại một kiếm đâm ra."),i("br"),i("br"),n._v("Cái trước lần nữa lui về phía sau."),i("br"),i("br"),n._v("Một cái tiến lên vừa lui về phía sau, liên tục hơn hai mươi chiêu quá khứ, trường kiếm không có chạm thử, Hoắc Giang Hà lại ngay cả lui hơn bốn mươi bước!"),i("br"),i("br"),n._v("“Cái này kiếm pháp...”"),i("br"),i("br"),n._v("“Thật mạnh!”"),i("br"),i("br"),n._v("Hồ Bân, Hoàng Thao đám người, tất cả đều đợi tại nguyên chỗ, hai mặt nhìn nhau, tựa như quái lạ.."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1952"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1954"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);