(window.webpackJsonp=window.webpackJsonp||[]).push([[1075],{1432:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1941-thien-hieu-thien-sinh-nhat"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1941-thien-hieu-thien-sinh-nhat"}},[n._v("#")]),n._v(" Chương 1941: Thiện Hiểu Thiên sinh nhật")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1941: Thiện Hiểu Thiên sinh"),t("br"),t("br"),n._v("Trước đó tại thông thần điện, cùng Thiên Nhai giao đấu."),t("br"),t("br"),n._v("Kiếm pháp của đối phương, như là Thần Long, không nhìn thấy quỹ tích, tuy biết rất mạnh, nhưng cũng không biết mạnh ở nơi đó."),t("br"),t("br"),n._v("Cũng chính là cái gọi là, biết bề ngoài mà không biết bề trong!"),t("br"),t("br"),n._v("Mà trước mắt vị này Thiện Hiểu Thiên dùng kiếm, bọn họ có thể rõ ràng nhìn ra, mục đích của đối phương cùng chiêu số, mỗi một cái, đều đâm vào sư huynh Hoắc Giang Hà chiêu số thiếu hụt chỗ!"),t("br"),t("br"),n._v("Ngắn ngủi hơn mười hô hấp, đâm ra hơn hai mươi chiêu, để Hoắc sư huynh liền phản kháng cũng không thể..."),t("br"),t("br"),n._v("Coi như chân chính nội môn đệ tử, cũng không có loại kiếm thuật này a!"),t("br"),t("br"),n._v("Cái này đã không thể dùng đáng sợ để hình dung!"),t("br"),t("br"),n._v("“Có phải hay không Thiên Nhai?”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, mặt chữ quốc hỏi."),t("br"),t("br"),n._v("Hắn không cùng Thiên Nhai giao thủ qua, cũng không biết đối phương chiêu số cùng thủ đoạn, nhưng mấy người khác đều đối chiến qua, có lẽ có thể nhìn ra dấu vết để lại."),t("br"),t("br"),n._v("“Thiên Nhai vừa động thủ, chúng ta liền chết... Phổ thông chiêu số, không có thi triển qua, cho nên nhìn không ra, trừ phi... Thi triển chiêu kia, mới có thể xác nhận!”"),t("br"),t("br"),n._v("Vân Phi Dương nói."),t("br"),t("br"),n._v("Không phải bọn họ nhãn lực không được, mà là chiêu kia ném kiếm, thực sự quá mạnh."),t("br"),t("br"),n._v("Vừa ra tay liền kết thúc, nhìn không ra người thi triển thực lực. Hiện tại phổ thông kiếm thuật, cũng liền không cách nào nhìn ra."),t("br"),t("br"),n._v("“Hiện tại loại tình huống này, dường như... Hoắc sư huynh, còn không cách nào ép đối phương thi triển ra chiêu kia!”"),t("br"),t("br"),n._v("Hoàng Thao cười khổ."),t("br"),t("br"),n._v("Mọi người yên lặng."),t("br"),t("br"),n._v("Không ra chiêu kia liền ép sư huynh liên tiếp lui về phía sau, không cần thi triển, sư huynh khẳng định cũng sẽ thua, như vậy liền thủy chung không cách nào xác nhận, đối phương là, có còn hay không là."),t("br"),t("br"),n._v("“Thật ra thì cũng không phải không có cách nào, xem ta...”"),t("br"),t("br"),n._v("Buồn bực phút chốc, mặt chữ quốc đột nhiên ngẩng đầu, nhìn về phía trước, trực tiếp hô lên: “Sư huynh, dùng Nhất Nhãn Giang Hà!”"),t("br"),t("br"),n._v("Nhất Nhãn Giang Hà, không riêng gì Hoắc Giang Hà tại thông thần điện ngoại hiệu, vẫn là hắn am hiểu nhất kiếm chiêu."),t("br"),t("br"),n._v("Chiêu này thi triển, tựa như liếc mắt nhìn sang sông hồ, thao thao bất tuyệt, thanh thế kinh người."),t("br"),t("br"),n._v("Dựa dẫm chiêu kiếm pháp này, Hoắc Giang Hà ngoại môn một đường nghiền ép, hầu như không người có thể địch, cũng bởi vậy đánh ra đại sư huynh địa vị."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe được tiếng la, Hoắc Giang Hà rõ ràng các sư đệ ý tứ, lúc này hét dài một tiếng, trường kiếm lăng không, chân khí đi khắp phía dưới, phía trước phảng phất lập tức xuất hiện một đạo rộng lớn nước sông, đá vụn bắn tung trời, sóng lớn vỗ bờ."),t("br"),t("br"),n._v("Nhìn trước mắt vị này thi triển mà ra tuyệt chiêu, Thiện Hiểu Thiên không chần chờ chút nào, trường kiếm trong tay lắc một cái, lập tức huyền phù mà lên."),t("br"),t("br"),n._v("Đối phương đứng tại Giang Hà đối diện, muốn đánh bại, chỉ có thể sử dụng phi kiếm phương pháp!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một kiếm phá không."),t("br"),t("br"),n._v("Một kiếm này hắn tu luyện trọn một đêm, không dưới hơn ngàn lần, thân kiếm lay động, trong nháy mắt từ biến mất tại chỗ, xuất hiện lần nữa, đã đi tới Hoắc Giang Hà cổ họng trước mặt."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, Hoắc Giang Hà cũng không dám lại áp chế, lực lượng toàn thân lập tức bùng nổ giống như trùng kích ra tới."),t("br"),t("br"),n._v("Thánh vực bốn tầng phá toái hư không!"),t("br"),t("br"),n._v("Lực lượng khôi phục, lúc này mới đem kiếm của đối phương ngăn tại bên ngoài, sợ chảy mồ hôi lạnh ướt sũng cả người."),t("br"),t("br"),n._v("Nếu như là cùng cấp bậc, chỉ sợ một chiêu này cũng đã chết."),t("br"),t("br"),n._v("“Là hắn...”"),t("br"),t("br"),n._v("Nhìn thấy chiêu này thi triển đi ra, mọi người tất cả đều tràn đầy kích động."),t("br"),t("br"),n._v("Đã không cần đoán, vị này Thiện Hiểu Thiên chính là vị kia Thiên Nhai!"),t("br"),t("br"),n._v("Tiết Càn thân thể thoáng một cái, té ngã trên đất."),t("br"),t("br"),n._v("Từ đêm qua trở về, vẫn tại nghĩ, vị kia tên là Thiên Nhai siêu cấp cao thủ, đến cùng là ai, nằm mơ cũng không ngờ tới... Chính là vị này bị hắn ghét bỏ, xem thường con rể!"),t("br"),t("br"),n._v("Tiết Cầm càng là sắp phát điên."),t("br"),t("br"),n._v("Cái tên này là Thiên Nhai... Chẳng phải đại biểu, mình đã bại bởi đối phương làm tỳ nữ?"),t("br"),t("br"),n._v("Vị hôn thê không muốn làm, nhưng thành tỳ nữ..."),t("br"),t("br"),n._v("Hận không thể lập tức liền móc ra hai mắt!"),t("br"),t("br"),n._v("Rõ ràng là cái như vậy lớn châu ngọc, lại bị xem như gạch ngói vụn, tùy tiện vứt bỏ!"),t("br"),t("br"),n._v("“Thiện Hiểu Thiên chiến thắng...”"),t("br"),t("br"),n._v("Đem một trận chiến toàn bộ nhìn ở trong mắt, Lục Vân trưởng lão tràn đầy cao hứng gật gật đầu, tuyên bố kết quả."),t("br"),t("br"),n._v("Khó trách vị này, trước đó không có trực tiếp đáp ứng gia nhập Lăng Vân kiếm các, nguyên lai là chờ lấy hiện tại, muốn triệt để đánh vị thành chủ này mặt!"),t("br"),t("br"),n._v("Để hắn vì lúc trước làm sự tình, mà sám hối!"),t("br"),t("br"),n._v("Người trẻ tuổi, hỏa khí chính là lớn..."),t("br"),t("br"),n._v("Chẳng qua, bị hại đến cửa nát nhà tan, đổi lại ai, cũng sẽ làm như vậy a!"),t("br"),t("br"),n._v("“Thành công đánh bại Hoắc Giang Hà, nhất định có thể nhẹ nhõm đi qua nội môn đệ tử khảo hạch, cũng không đủ tài nguyên dưới tình huống, mười sáu tuổi liền đi đến Đại Thánh tam trọng đại viên mãn, đi vào tông môn, mười bảy tuổi trước tấn cấp Cổ Thánh, không khó lắm!”"),t("br"),t("br"),n._v("Nhìn về phía trước mắt vị này, Lục Vân trưởng lão càng xem càng thoả mãn: “Hiện tại, ta tuyên bố, đặc biệt trúng tuyển ngươi vì Lăng Vân kiếm các nội môn đệ tử, nắm giữ nội môn đệ tử tất cả quyền lợi...”"),t("br"),t("br"),n._v("“Đa tạ trưởng lão!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên thở phào nhẹ nhõm, trực tiếp quỳ rạp xuống đất."),t("br"),t("br"),n._v("Có cái thân phận này, cho dù không có chứng cớ xác thật, cũng có thể chém giết vị thành chủ này, vì cha mẹ đám người, báo thù!"),t("br"),t("br"),n._v("“Ngươi muốn làm gì, giữa chúng ta có quan hệ thông gia, ta là nhạc phụ ngươi...”"),t("br"),t("br"),n._v("Thấy hắn ánh mắt nhìn tới, Tiết Càn biến sắc, liên tiếp lui về phía sau."),t("br"),t("br"),n._v("“Còn xin Hoắc sư huynh ra tay, đem hắn bắt, ta muốn vì gia tộc chín mươi bảy sinh mệnh, báo thù!”"),t("br"),n._v(" Hàm răng cắn chặt, Thiện Hiểu Thiên trong ánh mắt mang theo sát ý."),t("br"),t("br"),n._v("Ngươi giết tộc nhân ta thời điểm, vì sao không nói là nhạc phụ ta?"),t("br"),t("br"),n._v("Thiết kế hại ta, suýt chút nữa bị sơn tặc giết chết thời điểm, vì sao không nói là nhạc phụ ta?"),t("br"),t("br"),n._v("Bây giờ lại muốn nói, đã chậm!"),t("br"),t("br"),n._v("Hoắc Giang Hà đám người đồng loạt ra tay, Tiết Càn cứ việc rất mạnh, nhưng Vân Phi Dương, Hoàng Thao đám người thực lực không hề yếu, lại thêm thân là Kiếm Các đệ tử, kiếm thuật hơn người, dễ như trở bàn tay liền đem nó chế ngự."),t("br"),t("br"),n._v("“Ta không giết người vô tội, nơi này không chỉ có ngươi đối phó chúng ta Thiện gia chứng cứ, còn có những năm này, làm chuyện ác!”"),t("br"),t("br"),n._v("Lần nữa lấy ra một cái ngọc bài, Thiện Hiểu Thiên chân khí tràn vào trong đó, bên trong một mảng lớn văn tự nhất thời bay ra, đem đối phương tội danh viết rõ ràng."),t("br"),t("br"),n._v("“Thu thảo nguyên sự tình lại là hắn làm? Ta vẫn cho là, thật sự là sơn tặc gây nên...”"),t("br"),t("br"),n._v("“Liễu gia chủ lại là bị hắn cướp sạch, thương hại bọn hắn một nhà hơn bốn mươi miệng!”"),t("br"),t("br"),n._v("“Trần gia vị tiểu thư kia, trước khi chết, gặp phải lăng nhục, thua thiệt hắn có mặt nói là hái hoa đạo tặc, thân là thành chủ, vậy mà làm ra như vậy rắn rết sự tình...”"),t("br"),t("br"),n._v("“Buồn cười ta một mực lấy hắn làm thần tượng, cảm thấy là cái cương trực công chính thành chủ, không nghĩ tới sau lưng vậy mà như thế che giấu chuyện xấu...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn rõ ràng phía trên văn tự, một hồi xôn xao."),t("br"),t("br"),n._v("Thành chủ Tiết Càn, những năm này, mặt mũi công việc làm rất nhiều, trong thành rất có uy tín, trên thực tế sau lưng làm sự tình, làm người ta tức lộn ruột!"),t("br"),t("br"),n._v("Giờ phút này toàn bộ vạch trần đi ra, tất cả mọi người đều có một loại lật đổ cảm giác."),t("br"),t("br"),n._v("“Ngươi còn có lời gì nói?”"),t("br"),t("br"),n._v("Thấy Tiết Càn thấy cảnh này, vẻ mặt trắng bệch, Thiện Hiểu Thiên cũng nhịn không được nữa, cầm trong tay trường kiếm đi tới trước mặt, chỉ cần mũi kiếm vươn đi ra, liền có thể nhẹ nhõm đâm xuyên đối phương cổ họng, triệt để báo thù."),t("br"),t("br"),n._v("“Ta...” Thân thể run lên, Tiết Trầm nói không ra lời."),t("br"),t("br"),n._v("Tuy là những năm này hắn nấp rất kỹ, nhưng việc này toàn bộ vạch trần đi ra, chỉ cần xem kỹ, khẳng định vẫn là có dấu vết để lại."),t("br"),t("br"),n._v("Đối phương nếu như chỉ là người bình thường, hoàn toàn có thể một tay che trời, mà bây giờ thành nội môn đệ tử, lại không xoay người khả năng."),t("br"),t("br"),n._v("“Ngươi không thể giết cha ta...”"),t("br"),t("br"),n._v("Ngay tại hắn nhắm mắt đợi chết thời điểm, một bên Tiết Cầm điên cuồng đồng dạng lao đến, cao giọng gào thét."),t("br"),t("br"),n._v("“Không thể giết?” Thiện Hiểu Thiên tròng mắt hơi híp."),t("br"),t("br"),n._v("“Không sai, ngươi không có tư cách trở thành Lăng Vân kiếm các nội môn thế hệ sau! Tự nhiên cũng liền không cách nào đối cha ta làm ra xử quyết...” Tiết Cầm cắn răng."),t("br"),t("br"),n._v("“Không có tư cách? Ngươi ý tứ, nói là khảo hạch của ta, không có công chính tính?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên còn chưa lên tiếng, một bên Lục Vân trưởng lão mặt sắc không khỏi trầm xuống."),t("br"),t("br"),n._v("Hắn ngay trước nhiều người như vậy tiến hành tỷ thí, Hoắc Giang Hà cũng thi triển ra toàn lực, sao có thể đối với hắn tiến hành hoài nghi?"),t("br"),t("br"),n._v("“Tiểu nữ không dám...” Tiết Cầm ngẩng đầu nhìn tới: “Vừa rồi Lục trưởng lão nói, muốn trở thành nội môn đệ tử, cần hai cái điều kiện, vừa rồi kiếm pháp khảo hạch, công bằng công chính, không dám có bất kỳ dị nghị, nhưng... Mười bảy tuổi trước, đi đến Cổ Thánh, cũng chính là trưởng lão nói tới điều kiện thứ nhất, ta không đồng ý!”"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nhìn tới."),t("br"),t("br"),n._v("“Ta cùng vị này Thiện Hiểu Thiên có hôn ước, cho nên biết hắn ngày sinh tháng đẻ, sinh nhật của hắn chính là hôm nay giờ Thân! Nói cách khác, qua giờ Thân liền mười bảy tuổi.”"),t("br"),t("br"),n._v("Tiết Cầm nắm đấm xiết chặt: “Bây giờ cách giờ Thân, chỉ còn lại có hơn một canh giờ, hắn chỉ có Đại Thánh tam trọng đại viên mãn, làm sao có thể tại mười bảy tuổi trước, đi đến Cổ Thánh cấp bậc? Không đạt tới, có phải hay không liền không cách nào trở thành nội môn thế hệ sau?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão sửng sốt, hướng Thiện Hiểu Thiên nhìn lại, chỉ thấy người sau hơi đỏ mặt."),t("br"),t("br"),n._v("“Sinh nhật của ta, quả thực là hôm nay giờ Thân...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên khó xử."),t("br"),t("br"),n._v("Vừa rồi hắn muốn nói, kết quả bị Lục trưởng lão cắt đứt."),t("br"),t("br"),n._v("Nghĩ đến vì có thể báo thù, có thể không câu nệ tiểu tiết, chỉ cần về sau cố gắng là được, không nghĩ tới, bị vị này Tiết Cầm, nói ra."),t("br"),t("br"),n._v("Thấy hắn thừa nhận, Lục Vân trưởng lão, nhịn không được ngẩng đầu hướng cách đó không xa bóng mặt trời nhìn lại, chỉ thấy mặt trời đã ngã, buổi trưa đã quá nửa, nói cách khác, khoảng cách đối phương mười bảy tuổi sinh nhật, đã không đủ một cái nửa canh giờ!"),t("br"),t("br"),n._v("Thời gian ngắn như vậy, từ Đại Thánh tam trọng đại viên mãn tấn cấp đến Cổ Thánh..."),t("br"),t("br"),n._v("Quả thực không có bất kỳ cái gì khả năng!"),t("br"),t("br"),n._v("Mười bảy tuổi không đạt tới Cổ Thánh, liền không có tư cách trở thành Lăng Vân kiếm các đệ tử, tự nhiên cũng liền không có tư cách xử quyết trước mắt vị thành chủ này."),t("br"),t("br"),n._v("Cái gọi là báo thù, cũng đã thành truyện cười."),t("br"),t("br"),n._v("“Nhìn tới đây là thiên ý...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão cười khổ lắc đầu."),t("br"),t("br"),n._v("Còn tưởng rằng, có thể thu nhận một vị nội môn thế hệ sau, không nghĩ tới xuất hiện loại biến cố này."),t("br"),t("br"),n._v("“Chẳng qua, ngươi cũng không cần lo lắng, hiện tại không thành được nội môn thế hệ sau, trở về chỉ cần cố gắng tu luyện, đuổi tại ba mươi tuổi trước, đi đến Cổ Thánh bốn tầng, đồng dạng có cơ hội...”"),t("br"),t("br"),n._v("Nội môn thế hệ sau, cũng không phải là vơ đũa cả nắm, một đòn chết chắc, không phải, Hoắc Giang Hà mấy người cũng không có cơ hội."),t("br"),t("br"),n._v("Chỉ cần ba mươi tuổi trước, đi đến Cổ Thánh bốn tầng, kiếm thuật đi qua khảo hạch, đồng dạng có thể tiến vào bên trong."),t("br"),t("br"),n._v("Chẳng qua, như vậy, thù khẳng định là báo không được nữa."),t("br"),t("br"),n._v("Hắn thân là trưởng lão, tuy là có thể đem vị thành chủ này việc xấu báo lên, nhưng chờ Kiếm Các phái ra đoàn điều tra, tra rõ ràng đối phương việc này, không biết năm nào tháng nào."),t("br"),t("br"),n._v("Tại trong lúc này, có thể hay không xuất hiện biến cố, ai cũng không biết."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Hốc mắt đỏ lên, Thiện Hiểu Thiên nắm đấm xiết chặt."),t("br"),t("br"),n._v("Vốn cho rằng ngày hôm nay có thể đại thù đến báo, nằm mơ đều không có nghĩ đến, cuối cùng vẫn sắp thành lại bại, thất bại trong gang tấc."),t("br"),t("br"),n._v("Nhưng vào lúc này, một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Còn có một cái nửa canh giờ, ai nói... Ngươi không cách nào đột phá đến Cổ Thánh?”"),t("br"),t("br"),n._v("Vội vàng ngẩng đầu, ngay sau đó nhìn thấy một mực tại ngủ gật lão sư, chẳng biết lúc nào đứng lên, trên tay còn mang theo xích sắt, trên mặt lộ ra lãnh đạm mà nụ cười tự tin.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1953"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1955"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);