(window.webpackJsonp=window.webpackJsonp||[]).push([[1076],{1430:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1942-thien-hieu-thien-xung-kich-co-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1942-thien-hieu-thien-xung-kich-co-thanh"}},[n._v("#")]),n._v(" Chương 1942: Thiện Hiểu Thiên xung kích Cổ Thánh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Lão...”"),t("br"),t("br"),n._v("Tràn đầy kích động, Thiện Hiểu Thiên vừa định hô lên “Lão sư”, chỉ thấy đối phương khe khẽ lắc đầu, nhịn không được ngừng lại."),t("br"),t("br"),n._v("Vừa rồi lão sư đặc biệt truyền âm nói với hắn, bản thân làm người khiêm tốn, không nguyện ý xuất đầu lộ diện, lén lút có thể lấy thầy trò chi lễ đối đãi, người ngoài phía trước, liền nói là bạn."),t("br"),t("br"),n._v("Lấy hai bọn họ tuổi tác kém, chắc hẳn không người sẽ nghi ngờ."),t("br"),t("br"),n._v("“Tại hạ Trương Huyền, là Thiện Hiểu Thiên bằng hữu, gặp qua Lục trưởng lão...”"),t("br"),t("br"),n._v("Trương Huyền chậm rãi đi tới."),t("br"),t("br"),n._v("Thấy rõ dung mạo của hắn, Hoắc Giang Hà lộ ra mất tự nhiên biểu lộ."),t("br"),t("br"),n._v("Hồng Nhạn thương hội, hai người giao thủ qua, lúc ấy đối phương bọc lại một thân băng gạc, che đậy dung mạo, bởi vậy vừa rồi không nhận ra được, giờ phút này thanh âm nói chuyện lại thêm dáng người, chỗ nào không biết là ai."),t("br"),t("br"),n._v("“Ngươi nói trong vòng một canh giờ rưỡi, Thiện Hiểu Thiên có thể đột phá Cổ Thánh?”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nhíu nhíu mày."),t("br"),t("br"),n._v("“Vâng!” Trương Huyền gật đầu: “Ta bị thương là hắn cứu, biết thực lực chân chính của hắn! Trên thực tế đã sớm đột phá đến Cổ Thánh cấp bậc, nhưng vì giúp ta chữa thương, hao tổn quá lớn, mới hạ thấp Đại Thánh tam trọng! Chỉ cần xử lý thoả đáng, một cái nửa canh giờ, khôi phục như lúc ban đầu, không khó lắm!”"),t("br"),t("br"),n._v("“Bản thân liền là Cổ Thánh?”"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, Lục Vân trưởng lão lần nữa nhìn về phía thiếu niên ở trước mắt..."),t("br"),t("br"),n._v("Cổ Thánh dài như vậy? Biên nói dối cũng phải có cái tiêu chuẩn đi... Tin miệng nói bậy, thật được không?"),t("br"),t("br"),n._v("Thiện Hiểu Thiên cũng là ngẩn ngơ, triệt để bối rối."),t("br"),t("br"),n._v("Hắn hôm qua vẫn chỉ là cái Thánh vực lục trọng tiểu nhân vật, giết chết cổ trùng phía sau, một chút bạo tăng đến Đại Thánh tam trọng đã rất nghịch thiên, lão sư lại nói, bản thân liền là Cổ Thánh..."),t("br"),t("br"),n._v("Ta lúc nào thành Cổ Thánh?"),t("br"),t("br"),n._v("Chính ta thế nào không biết?"),t("br"),t("br"),n._v("“Cho ta một cái nửa canh giờ, tuyệt đối không thể có người quấy rầy, bảo đảm để hắn khôi phục như lúc ban đầu!”"),t("br"),t("br"),n._v("Thấy mọi người không tin, Trương Huyền nói."),t("br"),t("br"),n._v("Muốn nói, một cái nửa canh giờ, cứ thế mà giúp người tăng lên hai cái đại cấp bậc, biến thành Cổ Thánh, khẳng định không ai tin... Thật muốn làm được, ánh sáng khẳng định đang ẩn núp không được, làm không cẩn thận còn biết bị người chộp tới làm chuột bạch, cẩn thận nghiên cứu."),t("br"),t("br"),n._v("Suy đi nghĩ lại, chỉ có nói dối xưng đối phương bản thân liền là Cổ Thánh!"),t("br"),t("br"),n._v("Khôi phục thực lực, khẳng định không có nhanh chóng tăng lên, như vậy để cho người ta khiếp sợ."),t("br"),t("br"),n._v("Ai, vì khiêm tốn, thật sự là phí sức khổ tâm, ta Trương Huyền, chính là như vậy làm việc tốt không lưu danh, người gặp người thích, hoa gặp hoa nở tuyệt thế mỹ nam tử."),t("br"),t("br"),n._v("“Không khiến người ta quấy rầy, lại có gì khó!” Gặp hắn nói lời thề son sắt, tuy là cho rằng không quá tin tưởng, Lục Vân trưởng lão vẫn gật đầu, nhìn về phía Hoắc Giang Hà: “Đem bọn hắn đưa đến một cái yên tĩnh vị trí, vừa vặn thừa dịp hiện tại, khảo hạch một chút cái khác thí sinh!”"),t("br"),t("br"),n._v("“Vâng!” Hoắc Giang Hà lên tiếng, làm hiện dẫn đường."),t("br"),t("br"),n._v("Hắn tại phủ thành chủ ở đã mấy ngày, hết sức quen thuộc, đối phương cho mình an bài sân nhỏ, liền mười phần yên tĩnh, người ngoài chắc chắn sẽ không tới quấy rầy."),t("br"),t("br"),n._v("Rất mau tới đến sân trong, an bài xong Hoắc Giang Hà xoay người rời đi, đi vào phòng, Thiện Hiểu Thiên đóng cửa phòng, xoay người quỳ gối trước mặt lão sư: “Lão sư, ta căn bản không phải Cổ Thánh ah...”"),t("br"),t("br"),n._v("“Có phải hay không cho rằng ta mới vừa rồi là đang nói dối?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Cái này...” Thiện Hiểu Thiên hơi đỏ mặt."),t("br"),t("br"),n._v("Thân là đệ tử, cho rằng lão sư nói dối, bản thân liền là đại bất kính."),t("br"),t("br"),n._v("“Ngươi thật sự không có đi đến Cổ Thánh, nhưng... Không có nghĩa là một canh giờ, không cách nào tấn cấp đến cấp bậc này!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Một canh giờ tấn cấp hai cái đại cấp bậc?” Thiện Hiểu Thiên tràn đầy mê man."),t("br"),t("br"),n._v("Không phải không tin, mà là khó tin."),t("br"),t("br"),n._v("Tu vi nếu như dễ dàng như vậy tấn cấp, cao thủ khẳng định đâu đâu cũng có."),t("br"),t("br"),n._v('"Từ giờ trở đi, hoàn toàn nghe theo mệnh lệnh của ta, đừng có mảy may phản kháng, ta để ngươi làm cái gì thì làm cái đó Trương Huyền nói.'),t("br"),t("br"),n._v("“Vâng!” Thiện Hiểu Thiên gật đầu."),t("br"),t("br"),n._v("Từ bái sư một khắc kia trở đi, hắn cũng đã có quyết định, tuyệt không chống lại mệnh lệnh của lão sư."),t("br"),t("br"),n._v("Trương Huyền nhìn qua, nói: “Ngươi mặc dù mười năm qua, hấp thu linh khí, cũng không chuyển hóa thành chân khí, lưu tại trong cơ thể, nhưng không ngừng rèn luyện linh khí, để ngươi thân thể cùng trong cơ thể kinh mạch, vượt xa người bình thường, đã có thể cùng Cổ Thánh so sánh, chỉ cần nghĩ biện pháp sửa đổi một chút kinh mạch bố cục, để tu vi đột phá, cũng không khó!”"),t("br"),t("br"),n._v("“Thay đổi... Kinh mạch bố cục?” Thiện Hiểu Thiên càng thêm mơ hồ."),t("br"),t("br"),n._v("Cái này cũng có thể thay đổi?"),t("br"),t("br"),n._v("Kinh mạch không phải vừa ra đời liền cố định, một khi thay đổi, rất dễ dàng tử vong sao?"),t("br"),t("br"),n._v("Cái gọi là tẩu hỏa nhập ma, trên thực tế chính là tu luyện không đúng phương pháp, chân khí đi vào sai lầm kinh mạch..."),t("br"),t("br"),n._v("Chỉ là chân khí đi nhầm liền như thế, thay đổi kinh mạch, thật có thể sống sót?"),t("br"),t("br"),n._v("“Không sai! Đây là ta vừa phát hiện, còn chưa có thử nghiệm qua, ngươi là có hay không nguyện ý thử một lần? Thành công, một bước lên trời, đi đến Cổ Thánh cấp bậc; Không thành công... Có khả năng biến thành phế nhân, lại không cách nào tu luyện!”"),t("br"),t("br"),n._v("Trương Huyền vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“...” Chỉ cảm thấy da đầu nổ tung, Thiện Hiểu Thiên khóc không ra nước mắt."),t("br"),t("br"),n._v("Náo loạn nửa ngày, những này chỉ tồn tại lý luận bên trong, căn bản không có người thí nghiệm qua..."),t("br"),t("br"),n._v("Như vậy liền để hắn thí..."),t("br"),t("br"),n._v("Vị lão sư này, tâm thật là đủ lớn!"),t("br"),t("br"),n._v("Tuy là cho rằng bái vị lão sư này có chút không đáng tin cậy, Thiện Hiểu Thiên vẫn gật đầu, trong ánh mắt không có đảm nhiệm Hà Trì nghi cùng lo nghĩ: “Ta cái mạng này đều là lão sư, không phải lão sư, đừng nói Đại Thánh cấp bậc, khả năng người đã sớm chết, có gì không dám!”"),t("br"),t("br"),n._v("“Đã dám, còn lại liền đơn giản...”"),t("br"),t("br"),n._v("Trương Huyền thoả mãn gật gật đầu."),t("br"),t("br"),n._v("Hắn cũng không nói dối, cái gọi là phương pháp, quả thực là vừa mới nghĩ đi ra."),t("br"),t("br"),n._v("Dưới tình huống bình thường, dựa theo hắn chỉ điểm tu luyện, chỉ cần tài nguyên đủ, trong vòng vài ngày đột phá đến Cổ Thánh, rất dễ dàng làm được, muốn một canh giờ... Hai cái cấp bậc, trừ phi Thiên đạo công pháp!"),t("br"),n._v(" Có thể loại công pháp này, loại trừ hắn, người bình thường căn bản tu luyện không được, coi như truyền thụ, cũng là không cách nào làm được, làm không cẩn thận còn có thể sẽ được Thiên đạo trực tiếp nghiền ép mà chết."),t("br"),t("br"),n._v("Cho nên, chỉ có thể muốn cái khác con đường."),t("br"),t("br"),n._v("Đi qua quan sát, hắn phát hiện, Thượng Thương sinh trưởng ở địa phương sinh mệnh kinh mạch, so Dị Linh tộc nhân càng thêm chính xác, nhưng vẫn như cũ không có đi đến hoàn mỹ vô khuyết tình trạng."),t("br"),t("br"),n._v("Nhân tộc kinh mạch sai lầm rất nhiều, trưởng thành chỉ là người bình thường; Chư Tử bách gia nhân loại, sai lầm thiếu chút, trưởng thành đi đến Tông Sư cảnh; Dị Linh tộc nhân sai lầm càng ít, trưởng thành đi đến hóa phàm cảnh; Thượng Thương sinh trưởng ở địa phương người, sai lầm ít nhất, trưởng thành Thánh vực cửu trọng!"),t("br"),t("br"),n._v("Đi qua những này, có thể suy đoán, nếu như đem kinh mạch thay đổi, trở nên không có thiếu hụt, lại phối hợp đủ chân khí, có thể hay không cùng Thánh thú thay đổi huyết mạch đồng dạng, từ đó tu vi nhanh chóng tăng thêm?"),t("br"),t("br"),n._v("Trước đó, Trịnh Dương đám người, cũng là bởi vì sửa lại kinh mạch, lúc này mới nhanh chóng đột phá đến phá toái hư không, phá vỡ danh sư đại lục không cách nào đột phá ràng buộc."),t("br"),t("br"),n._v("Đương nhiên, đây đều là suy luận, cũng không thành công qua, chỉ có thể lấy trước mắt vị này làm thí nghiệm."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một cái nồi lớn cùng củi xuất hiện tại gian phòng, ngay sau đó đi bên ngoài xách thùng nước, đốt sôi sùng sục."),t("br"),t("br"),n._v("Có trữ vật giới chỉ, biết về sau có thể sẽ bị thương, sớm đem nồi cùng củi các loại chuẩn bị xong."),t("br"),t("br"),n._v("“Ngoan, đi ra tắm rửa...”"),t("br"),t("br"),n._v("Tinh thần câu thông đan điền, rất nhanh một cái hồ lô xuất hiện tại phía trước, rơi vào sôi trào nước sôi bên trong."),t("br"),t("br"),n._v("Hắn Thiên đạo chân khí, cứ việc cũng có thể khôi phục nhanh chóng thương thế, nhưng... Thượng Thương sinh trưởng ở địa phương người, kinh mạch vỡ vụn, liền xem như hắn, cũng không dám bảo đảm, có thể hay không tại trong vòng một canh giờ khôi phục như lúc ban đầu."),t("br"),t("br"),n._v("Cho nên, nghĩ đến hồ lô nước tắm."),t("br"),t("br"),n._v("Cái đồ chơi này đối với hắn vị này phá toái hư không cường giả, đều muốn cực lớn công hiệu, Đại Thánh cấp bậc khẳng định càng không vấn đề."),t("br"),t("br"),n._v("Bên này đun nước, bên kia Trương Huyền nhìn lại, vẻ mặt nghiêm túc: “Chịu đựng đau đớn!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên gật gật đầu, dựa theo dặn dò khoanh chân ngồi dưới đất, không nhúc nhích."),t("br"),t("br"),n._v("Tiến về phía trước một bước, Trương Huyền bàn tay ép xuống."),t("br"),t("br"),n._v("Chân khí thời gian nháy mắt, dọc theo huyệt đạo chui vào đối phương trong cơ thể, chấn động mạnh một cái."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Thiện Hiểu Thiên máu tươi phun mạnh."),t("br"),t("br"),n._v("Trong nháy mắt, kinh mạch trong cơ thể liền bị lão sư chấn vỡ."),t("br"),t("br"),n._v("Mãnh liệt đau đớn, để trước mắt hắn tối sầm lại, suýt chút nữa ngất đi."),t("br"),t("br"),n._v("Đổi lại những người khác khẳng định đã sớm không chịu nổi, nhưng hắn sáu tuổi bị tai nạn, tâm tính sớm đã cứng rắn như sắt, mặc dù như thế, cũng cảm thấy tùy thời, đều sẽ hôn mê."),t("br"),t("br"),n._v("“Nhịn xuống, ngàn vạn không thể hôn mê!”"),t("br"),t("br"),n._v("Gặp hắn bộ dạng, Trương Huyền bàn giao, Thiên đạo chân khí, tiếp tục hướng đối phương trong cơ thể truyền vào, đem đứt gãy kinh mạch, dựa theo hoàn mỹ đường đi kết nối."),t("br"),t("br"),n._v("Rèn luyện thủy ngân linh khí, hình thành Thiên đạo chân khí, càng thêm tinh thuần, tại đối phương trọng thương trong thân thể chạy, rất nhanh liền lần nữa tạo dựng một bộ mới kinh mạch."),t("br"),t("br"),n._v("“Quả nhiên có thể thực hiện, bất quá... Thiên đạo chân khí hỗ trợ khôi phục thương thế, quả thực không có nhanh như vậy...”"),t("br"),t("br"),n._v("Kinh mạch hoàn thành, Trương Huyền thở phào nhẹ nhõm, phát hiện chính mình suy đoán là đúng, Thiên đạo chân khí khôi phục thương thế, tốc độ rất nhanh, có thể điều kiện tiên quyết là, thực lực đối phương không bị đến tổn thất quá lớn tổn thương, hiện tại vị này đồ đệ, nội tức toàn bộ hủy, tương đương với không có một chút thực lực... Loại tình huống này, cùng lúc trước biến thành khung xương tương tự, chân khí hiệu quả liền trở nên yếu đi không ít."),t("br"),t("br"),n._v("Không có mười ngày nửa tháng, không cách nào thành công!"),t("br"),t("br"),n._v("Lại quá một canh giờ, đối phương liền qua mười bảy tuổi, thời gian khẳng định không kịp."),t("br"),t("br"),n._v("Đem hồ lô nước tắm bưng tới: “Hây...”"),t("br"),t("br"),n._v("Không có quá nhiều do dự, Thiện Hiểu Thiên nghiến răng liền uống."),t("br"),t("br"),n._v("Thân thể của hắn, cùng Trương Huyền nói đồng dạng, mười năm rèn luyện, sớm đã cùng Cổ Thánh không khác, chỉ là uống nước sôi mà nói, còn không đả thương được mảy may."),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),t("br"),n._v("Nước tắm đi vào trong cơ thể, trước đó bị thương nặng, mắt thường có thể thấy được khôi phục, không đến một phút đồng hồ, khôi phục như lúc ban đầu."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên mở to hai mắt nhìn, nằm mộng cũng nghĩ không ra, một cái hồ lô nước tắm, sẽ như vậy lợi hại."),t("br"),t("br"),n._v("“Nhanh hấp thu linh khí, nhanh chóng đột phá!”"),t("br"),t("br"),n._v("Gặp hắn đến thăm lấy khiếp sợ, Trương Huyền cau mày, quát tháo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên tinh thần tập trung."),t("br"),t("br"),n._v("Vừa rồi kinh mạch vỡ vụn, lực lượng bị hủy, nhưng chân khí vẫn như cũ dự trữ trong đan điền, cũng không có một chút hao tổn, lúc này hấp thu linh khí, nhất thời đem cỗ lực lượng này kích hoạt, lần nữa khôi phục đến Đại Thánh tam trọng đại viên mãn."),t("br"),t("br"),n._v("Trước đó rất khó phá vỡ bích chướng, trong nháy mắt rục rà rục rịch, bất cứ lúc nào cũng sẽ mở ra."),t("br"),t("br"),n._v("“Phá!”"),t("br"),t("br"),n._v("Không chần chờ chút nào, vận chuyển chân khí, bỗng nhiên xung kích."),t("br"),t("br"),n._v("Trong chốc lát, phá vỡ ràng buộc, đạt đến Đại Thánh tứ trọng Bất Hủ cảnh."),t("br"),t("br"),n._v("Tu vi mau theo tiến bộ."),t("br"),t("br"),n._v("Bất Hủ cảnh sơ kỳ, trung kỳ, hậu kỳ, đỉnh phong, viên mãn, đại viên mãn..."),t("br"),t("br"),n._v("Chưa tới một canh giờ, liền khoảng cách Cổ Thánh chỉ có một chân chi kém, bất cứ lúc nào cũng sẽ đột phá."),t("br"),t("br"),n._v("Bất quá, nhưng vào lúc này, Trương Huyền rõ ràng cảm giác được Thiện Hiểu Thiên lực lượng trong cơ thể, lại không còn trước đó như vậy hung mãnh, như là thủy triều chảy trở về, chỉ kém một bước cuối cùng, lại không cách nào đi qua."),t("br"),t("br"),n._v("“Nhìn tới vẫn còn nghĩ quá đơn giản... Cổ Thánh là một cái đại cấp bậc, dù là tại Thượng Thương, cũng không dễ dàng như vậy...”"),t("br"),t("br"),n._v("Sầm mặt lại, Trương Huyền trong lòng căng thẳng."),t("br"),t("br"),n._v("(Tấu chương xong)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1954"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1956"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);