(window.webpackJsonp=window.webpackJsonp||[]).push([[1077],{1431:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1943-hieu-thien-bao-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1943-hieu-thien-bao-thu"}},[n._v("#")]),n._v(" Chương 1943: Hiểu Thiên báo thù")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Đột phá Cổ Thánh, cần Cổ Thánh lực lượng, Thượng Thương loại lực lượng này đâu đâu cũng có, cũng không lo lắng, nhưng... Cái này đại cấp bậc, tương đương với danh sư đại lục tông sư, cần đủ tích lũy!"),t("br"),t("br"),n._v("Thiện Hiểu Thiên tâm tính, thiên phú, đều không yếu, thiếu hụt chính là thời gian tích tụ."),t("br"),t("br"),n._v("Nếu như cho một tháng thời gian, không cần bảo vật, bản thân cũng có thể đột phá, có thể thời gian không đợi người, bây giờ cách cái gọi là mười bảy tuổi, đã không đến nửa canh giờ, một khi cỗ này thủy triều triệt để chảy trở về, liền lại không thể có thể!"),t("br"),t("br"),n._v("“Nuốt!”"),t("br"),t("br"),n._v("Lông mày nâng lên, Trương Huyền giữa ngón tay toát ra kiếm khí, bỗng nhiên nơi cổ tay vạch một cái, máu tươi nhất thời phun ra ngoài, hóa thành một đoàn huyết cầu, thẳng tắp hướng đối phương bay đi."),t("br"),t("br"),n._v("Mất máu quá nhiều, cả người vẻ mặt đều có chút trắng xám."),t("br"),t("br"),n._v("Thiện Hiểu Thiên hốc mắt đỏ lên, há miệng đem máu tươi nuốt vào, lập tức cảm thấy vô số linh khí ở trong người khuấy động, vốn lui bước lực lượng, trong nháy mắt, lần nữa thiêu đốt, sôi trào mãnh liệt."),t("br"),t("br"),n._v("Trương Huyền thân là Cổ Thánh tứ trọng cường giả, mỗi một giọt tinh huyết, đều ẩn chứa năng lượng to lớn, thoáng cái cho nhiều như vậy, giá trị lớn, so với Tiên Nguyên đan đều chỉ mạnh không yếu, bởi vậy, một cái hô hấp không đến, mới thu vị này đệ tử, liền lần nữa khôi phục đến đỉnh phong, chân khí càng ngày càng mạnh."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Rốt cục, Cổ Thánh bình chướng cũng lại không chịu nổi, trong nháy mắt nổ tung, nhanh chóng tăng vọt khí tức, cũng không bởi vậy ngừng, vẫn như cũ kéo lên."),t("br"),t("br"),n._v("Duy trì một phút, lúc này mới chậm rãi dừng lại."),t("br"),t("br"),n._v("Lúc này Thiện Hiểu Thiên thực lực, đã đạt đến Cổ Thánh nhất trọng huyết mạch kéo dài đỉnh phong, khoảng cách Chư Tử bách gia, cũng chỉ có kém một bước."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Đem tu vi củng cố thoáng cái, Thiện Hiểu Thiên không để ý tới kích động, vội vàng nhìn về phía thanh niên trước mắt."),t("br"),t("br"),n._v("Vì để cho hắn đột phá, lão sư, bỏ ra rất rất nhiều."),t("br"),t("br"),n._v("Cả một đời, đều không thể trả lại!"),t("br"),t("br"),n._v("“Ta không sao, thời gian lập tức liền muốn tới, mau đi ra a, thực lực của ngươi đi đến Cổ Thánh, hơn nữa còn là nhất trọng đỉnh phong, hẳn là lại không người nghi ngờ... Khụ khụ!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền nhịn không được ho khan mấy tiếng."),t("br"),t("br"),n._v("Lần này, quả thực thua thiệt lớn."),t("br"),t("br"),n._v("Chỉ riêng mất đi những này ẩn chứa năng lượng tinh huyết, không có ngoài ý muốn, liền có thể muốn cho hắn tiêu phí ít nhất thời gian mấy tháng khôi phục."),t("br"),t("br"),n._v("“Lão sư, ngươi nghỉ ngơi trước!”"),t("br"),t("br"),n._v("Gật gật đầu, Thiện Hiểu Thiên xoay người rời đi."),t("br"),t("br"),n._v("Gặp hắn đi xa, Trương Huyền lúc này mới bất đắc dĩ lắc đầu, vừa định hấp thu linh khí khôi phục lực lượng, ngay sau đó nhìn thấy cách đó không xa nồi lớn, vừa rồi nấu hồ lô nước, cũng không uống xong."),t("br"),t("br"),n._v("Chần chờ một chút, bưng lên một chén uống vào."),t("br"),t("br"),n._v("  Cô cô cô!"),t("br"),t("br"),n._v("Trong cơ thể một hồi vang lên, tràn đầy thân thể hư nhược, mắt thường có thể thấy được khôi phục, thời gian nháy mắt liền khôi phục như lúc ban đầu, hình như chưa hề bị thương qua."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trợn mắt lên, Trương Huyền ngây người."),t("br"),t("br"),n._v("Thiên đạo chân khí, có thể chữa thương, nhưng chữa trị không được thân thể thiếu hụt, hồ lô nước tắm, vậy mà nhẹ nhõm có thể làm được, như là Dragon Ball bên trong đậu tiên đồng dạng..."),t("br"),t("br"),n._v("Vậy thì có chút quá nghịch thiên."),t("br"),t("br"),n._v("“Ngươi đến cùng là cái gì?”"),t("br"),t("br"),n._v("Lần nữa kìm nén không được, nhìn về phía trước mắt ngay tại lặn hồ lô."),t("br"),t("br"),n._v("“Nói thật cho ngươi biết a, hồ lô chỉ là ta biểu tượng, ta bản thân, là ngang dọc thiên địa, quát tháo phong vân tuyệt đỉnh Thần thú, còn không nhanh lên một chút hạ bái...”"),t("br"),t("br"),n._v("Hồ lô uốn éo cái mông."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nghe đối phương thổi một hồi, không có chút nào đáng tin cậy, Trương Huyền đầy mặt bất đắc dĩ đem còn thừa lại nước, toàn bộ cất vào trong bình, thu hồi nồi lớn, đi ra ngoài."),t("br"),t("br"),n._v("Đẩy cửa phòng ra trong nháy mắt, vẻ mặt lần nữa trở nên vàng như nến, đi bộ đều không ngừng lay động, hình như bất cứ lúc nào cũng sẽ ngã sấp xuống."),t("br"),t("br"),n._v("Nói Thiện Hiểu Thiên trước đó chính là Cổ Thánh, để cho người ta tin tưởng rất khó, nhiều nhất nửa tin nửa ngờ, giờ phút này tu vi đột phá, hai người nếu là đều hoàn hảo không chút tổn hại ra ngoài... Chẳng phải dẫn tới sóng to gió lớn?"),t("br"),t("br"),n._v("Còn không bằng giả bộ như bị thương nặng, khiến người khác rõ ràng, cưỡng ép tăng thực lực lên, cần bỏ ra cực lớn đánh đổi, đánh loại tâm tư này, khẳng định cũng ít đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Ngươi cho rằng... Hắn có thể thành công hay không?”"),t("br"),t("br"),n._v("Tuyển chọn đệ tử tốc độ rất nhanh, Lục Vân trưởng lão đám người rất nhanh liền nhàn rỗi, Hoàng Thao một bên nhìn về phía Trương Huyền đám người biến mất phương hướng, một bên nhịn không được hỏi."),t("br"),t("br"),n._v("“Một cái nửa canh giờ, từ Đại Thánh tam trọng trở thành Cổ Thánh... Đây là chuyện không thể nào!”"),t("br"),t("br"),n._v("Vân Phi Dương lắc đầu."),t("br"),t("br"),n._v("“Đúng vậy a, coi như tại Lăng Vân kiếm các đều không có nghe qua loại sự tình này, dù sao ta không tin!” Mặt chữ quốc cũng nói."),t("br"),t("br"),n._v("Không riêng gì bọn họ, ngay cả Lục Vân trưởng lão vậy duy trì thái độ hoài nghi."),t("br"),t("br"),n._v("“Không cần chờ, một cái nửa canh giờ, có thể để cho hắn đột phá đến Cổ Thánh, trừ phi Thần Linh tự thân ra tay...”"),t("br"),t("br"),n._v("Biết Thiện Hiểu Thiên lập tức liền muốn tới mười bảy tuổi, trở thành nội môn đệ tử vô vọng, Tiết Càn vậy mất đi kính sợ, cười lạnh liên tục."),t("br"),t("br"),n._v("Tiếng nói còn không có kết thúc, tiếng bước chân vang lên, ngay sau đó nhìn thấy thiếu niên chậm rãi đi tới."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Lục Vân trưởng lão vội vàng nhìn tới."),t("br"),t("br"),n._v("“May mắn không làm nhục mệnh!”"),t("br"),t("br"),n._v("Mỉm cười, Thiện Hiểu Thiên vận chuyển chân khí, lực lượng trong cơ thể, lập tức nổ tung lên, khí tức bay thẳng không trung."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Chân khí xung kích quá nhanh, toàn bộ đại sảnh đều không ngừng lay động, phát ra “Kẹt kẹt!” Âm thanh."),t("br"),t("br"),n._v("Trước đó vây xem hơn nghìn người, cảm nhận được áp lực thật lớn, tất cả đều cảm thấy hai chân như nhũn ra, bất cứ lúc nào cũng sẽ quỳ xuống."),t("br"),t("br"),n._v("“Cổ Thánh nhất trọng... Đỉnh phong?”"),t("br"),t("br"),n._v("Tiết Càn cứng đờ, vừa rồi lời muốn nói, toàn bộ nuốt tại trong miệng, phảng phất sắp choáng váng đồng dạng."),t("br"),t("br"),n._v("Cách đó không xa Tiết Cầm, lần nữa “Phù phù!” Một tiếng, bại liệt trên mặt đất, bờ môi không ngừng run rẩy, một câu đều nói không ra được."),t("br"),t("br"),n._v("Nàng chỗ xem thường vị hôn phu, trong một ngày, lấy hung hăng nhất phương thức, đưa nàng tôn nghiêm đánh không giữ lại chút nào, hơn nữa, còn là qua lại đánh, qua lại chà đạp cái chủng loại kia..."),t("br"),t("br"),n._v("Sớm biết như vậy, sao lúc trước còn như thế..."),t("br"),t("br"),n._v("Nếu là hôm qua không não tàn chạy tới từ hôn, cũng sẽ không náo thành như vậy..."),t("br"),n._v(" “Hiểu Thiên hiền chất, giữa chúng ta còn có quan hệ thông gia...”"),t("br"),t("br"),n._v("Vùng vẫy một hồi, Tiết Càn mặt dạn mày dày."),t("br"),t("br"),n._v("“Quan hệ thông gia?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên cười lạnh: “Xấu hổ... Đây là ta thư bỏ vợ, Tiết Cầm hành vi không ngay thẳng, định giết ta, đã bị ta bỏ rơi!”"),t("br"),t("br"),n._v("Ngón tay búng một cái, một trang giấy lập tức bay đi, bồng bềnh trên không trung, phía trên đen kịt “Thư bỏ vợ” hai chữ, giống như là càng thêm điên cuồng đập lấy nữ hài khuôn mặt."),t("br"),t("br"),n._v("Từ hôn không được, ngược lại bị đuổi... Trên thế giới nhất mất mặt, chỉ sợ sẽ là nàng đi!"),t("br"),t("br"),n._v("Toàn tâm toàn ý muốn mặt mũi, kết quả... Nhưng thành Huyền Giang thành buồn cười lớn nhất."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, Tiết Cầm máu tươi phun mạnh."),t("br"),t("br"),n._v("“Lục trưởng lão, ta giờ phút này, xem như thông qua được nội môn đệ tử khảo hạch, xem như trở thành một thành viên?”"),t("br"),t("br"),n._v("Không tiếp tục để ý đối phương, Thiện Hiểu Thiên tiếp tục xem đi."),t("br"),t("br"),n._v("Lục Vân trưởng lão gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi, còn xin làm phiền Giang Hà sư đệ, đem vị này Tiết Càn huỷ bỏ tu vi, treo ở phủ thành chủ bên ngoài, ghi rõ tội lỗi, chém đầu răn chúng!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên thản nhiên nói."),t("br"),t("br"),n._v("“Vâng!” Hoắc Giang Hà gật đầu."),t("br"),t("br"),n._v("Đối phương là nội môn đệ tử, hắn chỉ là ngoại môn, liền biến thành sư đệ, đối với mình mà nói, phục tùng vô điều kiện."),t("br"),t("br"),n._v("“Cha, mẫu thân, tổ phụ, tổ mẫu... Ta rốt cục vì các ngươi báo thù...”"),t("br"),t("br"),n._v("Thời gian không dài, thấy Tiết Càn đầu bị đề cập qua đến, Thiện Hiểu Thiên quỳ rạp xuống đất, nước mắt chảy ròng."),t("br"),t("br"),n._v("Bất kể nói thế nào, hắn chỉ là cái vừa tròn mười bảy tuổi thiếu niên, thù hận chôn giấu trọn mười năm, một khi đến báo, không thể kiên trì được nữa."),t("br"),t("br"),n._v("Quay đầu liếc mắt nhìn, ngay sau đó nhìn thấy lão sư một mặt trắng bệch đi tới."),t("br"),t("br"),n._v("Vội vàng đứng dậy, tiến lên đón."),t("br"),t("br"),n._v("Nếu như không phải trước mắt vị này, hắn khả năng đều không gặp được mặt trời hôm nay, sớm đã bị Tiết Trầm phái tới sơn tặc giết."),t("br"),t("br"),n._v("Người đều không còn, chớ nói chi là báo thù, có thể tu luyện, đột phá Cổ Thánh..."),t("br"),t("br"),n._v("Có thể nói, hôm nay tất cả, đều là đối phương, cấp cho!"),t("br"),t("br"),n._v("Rất muốn hiện tại liền nói cho tất cả mọi người... Đây là ân sư của ta, ta Thiện Hiểu Thiên có thể có thành tựu hiện tại, đều là bái hắn ban tặng..."),t("br"),t("br"),n._v("Tràn đầy kích động, còn chưa tới đến trước mặt, chỉ thấy bản thân vị này để hắn tôn kính lão sư, đang run rẩy đứng tại Vân Phi Dương đám người phía trước, xòe bàn tay ra: “Thiện Hiểu Thiên trở thành nội môn đệ tử, các ngươi thân là ngoại môn đệ tử, chẳng lẽ không nên cho mỗi người cho mấy cái Tiên Nguyên đan, chúc mừng thoáng cái? Đây cũng không phải là thu lễ, mà là, tìm kiếm che chở! Chỉ cần hắn tại nội môn đứng vững bước chân, các ngươi được chỗ tốt càng lớn...”"),t("br"),t("br"),n._v("“...” Thiện Hiểu Thiên cứng đờ."),t("br"),t("br"),n._v("Đây là tại thu... Phí bảo hộ?"),t("br"),t("br"),n._v("Đây thật là ta vị kia, khiêm tốn, nghiêm cẩn, cương trực công chính lão sư?"),t("br"),t("br"),n._v("Làm sao cảm giác như vậy xốc nổi đâu?"),t("br"),t("br"),n._v("“...” Vân Phi Dương mấy người cũng một mặt mộng bức."),t("br"),t("br"),n._v("Vốn thấy Thiện Hiểu Thiên quả nhiên tại trong vòng một canh giờ rưỡi đột phá hai cái đại cấp bậc, suy đoán, khẳng định là vị này công lao, muốn cẩn thận hỏi thăm, gặp hắn một mặt hưng phấn mà tới đòi hỏi đan dược..."),t("br"),t("br"),n._v("Lại không có cái gì tâm tình."),t("br"),t("br"),n._v("Nhìn tới, Thiện Hiểu Thiên hẳn là thật sự là trước kia liền đi đến Cổ Thánh... Đối phương chỉ là dùng phương pháp đặc thù, cọ rửa để khôi phục thực lực thôi!"),t("br"),t("br"),n._v("Nếu không... Có thể khiến người ta nhanh chóng tấn cấp cao thủ, sẽ như vậy không biết xấu hổ, trước mặt nhiều người như vậy, công khai đòi bảo hộ phí?"),t("br"),t("br"),n._v("Quá căng đi!"),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão vậy không nhìn nổi, ho khan một tiếng, phá vỡ khó xử: “Hiểu Thiên, nội môn đệ tử, có thể mang người hầu, tạp dịch đệ tử về tông môn, theo chúng ta đi đi! Huyền Giang thành đối với ngươi mà nói, đã không có gì có thể đáng giá lưu luyến...”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên gật đầu."),t("br"),t("br"),n._v("Gia cừu đến báo, nơi này xác thực lại không có gì có thể lưu luyến, cũng nên đi càng lớn thế giới, vật lộn phong vân."),t("br"),t("br"),n._v("“Ta phải mang theo Dịch lão cùng Trương huynh! Những người khác liền thôi...”"),t("br"),t("br"),n._v("Trầm tư một chút, Thiện Hiểu Thiên làm ra quyết định."),t("br"),t("br"),n._v("Lão sư khẳng định muốn mang, đây là nhất định, Dịch lão một mực đi theo bản thân, cũng không thể bỏ xuống, thật ra thì có hai người này là đủ rồi, tạp dịch đệ tử có muốn hay không không quan trọng."),t("br"),t("br"),n._v("Dù sao, lão sư ưa thích khiêm tốn, nhiều người, quấy rầy cũng không tiện."),t("br"),t("br"),n._v("“Vậy thì tốt, ngươi trở về chuẩn bị một chút, một canh giờ sau, chúng ta đúng giờ xuất phát!” Lục Vân trưởng lão gật đầu."),t("br"),t("br"),n._v("Thiện Hiểu Thiên gật đầu, đi tới lão sư trước mặt, chỉ thấy lão sư tràn đầy bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Rất hiển nhiên, vừa rồi “Phí bảo hộ” cũng không thu đến."),t("br"),t("br"),n._v("“Lão sư, chúng ta về nhà một chuyến, trở về liền muốn đi Lăng Vân kiếm các!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên nói."),t("br"),t("br"),n._v("Bây giờ cách Lục Vân trưởng lão đám người đã xa, hạ giọng đối phương không nghe được, không cần thiết tiếp tục che giấu."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn chạy tới thu phí bảo hộ, là vì chuyển di lực chú ý, hiện tại xem ra hiệu quả cũng không tệ lắm."),t("br"),t("br"),n._v("“Cũng không biết có thể che giấu bao lâu... Quá ưu tú, cũng có chút ưu sầu người!”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, trong lòng tràn đầy buồn phiền."),t("br"),t("br"),n._v("Quá ưu tú có đôi khi cũng là rất bất đắc dĩ, nói thật, hắn thật không quá muốn làm cho người chú ý, có thể vừa ra tay cứ như vậy kinh diễm... Cũng là hắn, khó chịu nhất địa phương đi!"),t("br"),t("br"),n._v("Ngay tại cảm xúc, liền thấy cách đó không xa, sơn tặc Tào Thành Lập bước nhanh đi tới trước mặt, “Phù phù!” Quỳ rạp xuống đất, trong mắt mang theo chân thành cùng cực nóng."),t("br"),t("br"),n._v("“Thiếu gia, xin nhận lấy ta, ta nguyện ý làm ngươi tôi tớ, vĩnh viễn không nuốt lời!”"),t("br"),t("br"),n._v("“Ngươi nhìn... Quả nhiên quá ưu tú, liền sơn tặc, đều kìm lòng không được...”"),t("br"),t("br"),n._v("Trương Huyền càng thêm buồn phiền."),t("br"),t("br"),n._v("(Hài tử lại sốt...)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1955"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1957"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);