(window.webpackJsonp=window.webpackJsonp||[]).push([[1078],{1433:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1944-lang-van-kiem-cac-đen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1944-lang-van-kiem-cac-đen"}},[n._v("#")]),n._v(" Chương 1944: Lăng Vân kiếm các đến")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Người khác không biết vị này Trương Huyền thân phận, Tào Thành Lập nhưng mà tận mắt thấy, Thiện Hiểu Thiên xưng hô lão sư."),t("br"),t("br"),n._v("Để một cái Thánh vực lục trọng không thể tu luyện phế vật, ngắn ngủi không tới một ngày, đi đến Cổ Thánh nhất trọng đỉnh phong... Tận mắt thấy, đều cảm giác giống giống như nằm mơ khó tin."),t("br"),t("br"),n._v("Đi theo loại người này sau lưng, tuyệt đối có thể nhất phi trùng thiên!"),t("br"),t("br"),n._v("Huống chi, hắn hiện tại thuộc hạ, hầu như đều chết hết, xung quanh vây quanh nhiều người như vậy, dám rời đi, rất có thể sẽ bị dân chúng tức giận tại chỗ đánh chết."),t("br"),t("br"),n._v("Những năm này, chiếm núi làm vua, chuyện xấu, làm không ít, không biết bao nhiêu người, muốn giết chi cho thống khoái!"),t("br"),t("br"),n._v("“Làm người hầu của ta?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày: “Tỉnh lại đi ngươi! Hảo hảo mà làm ngươi sơn tặc phần này rất có tiền đồ chức nghiệp đi đi!”"),t("br"),t("br"),n._v("Lưu hắn không chết, chỉ là muốn mượn tay của đối phương, vạch trần Tiết Trầm đám người âm mưu, chuyện bây giờ kết thúc, một cái không chuyện ác nào không làm sơn tặc mà thôi, chết thì chết mà thôi, liên quan gì đến hắn?"),t("br"),t("br"),n._v("“Chủ nhân không nhận ta... Ta tình nguyện quỳ chết ở chỗ này...”"),t("br"),t("br"),n._v("Tào Thành Lập nghiến răng."),t("br"),t("br"),n._v("“Vậy thì quỳ đi...”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("“Chủ nhân...”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương như vậy kiên quyết, Tào Thành Lập đầy mặt đau buồn: “Thật ra thì, ta cũng là có nỗi khổ tâm, ta vốn là một cái làm ăn thiện nhân, một lần uống say, lão bà không cho vào cửa, ở bên ngoài trên xe ngựa ngủ, nửa đêm rét tỉnh, mới không có cách nào đi thanh lâu, kết quả, đồng thời muốn bảy cái nữ nhân, cảm giác kia thật sự sảng khoái, về sau...”"),t("br"),t("br"),n._v("“Tốt, không cần bán thảm rồi, lão sư làm người thánh khiết, cương trực công chính, ngươi những năm này làm đủ trò xấu, thu ngươi làm tôi tớ, chỉ biết ảnh hưởng tới danh dự... Cút đi! Nếu không, tin hay không hiện tại liền giết ngươi?”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên lông mày giương lên."),t("br"),t("br"),n._v("Lão sư loại nhân vật nào?"),t("br"),t("br"),n._v("Trên trời giao long đồng dạng, thật thu ngươi một cái sơn tặc vì hạ nhân, về sau thanh danh chẳng phải nhận tổn thương?"),t("br"),t("br"),n._v("Lại nói, ngươi đây là bán thảm ư?"),t("br"),t("br"),n._v("Làm sao giống như là đang khoe khoang?"),t("br"),t("br"),n._v("Thấy Thiện Hiểu Thiên bất cứ lúc nào cũng sẽ động thủ, Tào Thành Lập sắc mặt trắng nhợt, biết muốn trở thành đối phương tôi tớ, khẳng định không có cơ hội, xòe bàn tay ra: “Nếu như chủ nhân không nhận ta vì tôi tớ, ta khẳng định sẽ bị những người này đánh chết tươi, dù sao cũng là một lần chết, những năm này ta làm sơn tặc, cướp cơ bản đều là có tiền nhà giàu, thu hoạch tương đối khá, cái này viên trữ vật giới chỉ bên trong, có mười cái Tiên Nguyên đan, là những năm này để dành tới tích góp... Nguyện ý dâng hiến cho chủ nhân...”"),t("br"),t("br"),n._v("“Ít cầm những vật này hối lộ lão sư, không mắc bẫy này...”"),t("br"),t("br"),n._v("Thấy cái tên này, bán thảm không được, lại lấy tiền hối lộ, Thiện Hiểu Thiên cau mày, vừa định động thủ đem hắn đuổi đi, chỉ thấy trước mắt một mặt trắng bệch lão sư, đột nhiên tránh thoát hắn đỡ, đứng thẳng người, lấy sét đánh không kịp bưng tai trộm chuông nhi vang đinh việc nhân đức không nhường ai thế giới tràn ngập lực bất tòng tâm người làm vui chi thế, nhanh chóng đem hắn trong tay trữ vật giới chỉ cầm ở lòng bàn tay."),t("br"),t("br"),n._v("Xóa đi linh hồn ấn ký, liếc mắt nhìn, Trương Huyền không tự chủ được gật gật đầu, một mặt thoả mãn."),t("br"),t("br"),n._v("“Tốt, từ hôm nay trở đi, ngươi chính là của ta tôi tớ, đi theo đi!”"),t("br"),t("br"),n._v("Một cái Tiên Nguyên đan mười vạn thông thần tệ, mười cái chính là một trăm vạn, lại thêm trữ vật giới chỉ... Không nghĩ tới tên sơn tặc này, thu hoạch thật là phong phú."),t("br"),t("br"),n._v("Nhiều tiền như vậy, chỉ là làm cái tôi tớ mà lấy, không có gì."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên bối rối."),t("br"),t("br"),n._v("Đây quả thật là lão sư ta?"),t("br"),t("br"),n._v("Làm sao cảm giác... Mất mặt như vậy đâu?"),t("br"),t("br"),n._v("Tuỳ để cho người ta tấn cấp hai cái đại cấp bậc, lại có cao thâm khó dò kiếm thuật cùng y thuật... Như vậy thanh cao người, tuỳ mấy cái đan dược liền lấy lòng..."),t("br"),t("br"),n._v("Điểm mấu chốt đâu?"),t("br"),t("br"),n._v("Cao thủ tôn nghiêm đâu?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trong tộc không có gì có thể thu thập, thời gian không dài, Thiện Hiểu Thiên liền mang theo Dịch lão đám người lần nữa trở lại phủ thành chủ."),t("br"),t("br"),n._v("Con ngựa kia cùng chiếc xe kia, liều mạng muốn đi theo, cuối cùng đều bị cự tuyệt, phóng thích hoang dã."),t("br"),t("br"),n._v("Lần này đi Lăng Vân kiếm các, khẳng định là muốn ngồi phi hành Thánh thú, mang cái xe ngựa, thực sự có chút không tiện lắm."),t("br"),t("br"),n._v("Còn Tiết Cầm, tạp dịch đệ tử thân phận, tự nhiên bị thủ tiêu, hiện tại cha tử vong, nàng bình thường chưa làm qua cái gì ác, miễn trừ tai nạn, nhưng mất đi phủ thành chủ che chở, chắc hẳn thời gian vậy không tốt lắm."),t("br"),t("br"),n._v("Loại tình huống này, Thiện Hiểu Thiên lại không có đi truy cứu."),t("br"),t("br"),n._v("Mặc dù không có tình cảm, dù sao hai người từng có hôn ước, đuổi tận giết tuyệt mà nói, đối với hắn vậy không có gì tốt chỗ."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Chuẩn bị ổn thoả, to lớn phi hành Thánh thú cánh vũ động, mang theo Trương Huyền đám người rời đi Huyền Giang thành, hướng Lăng Vân kiếm các phương hướng bay lượn mà đi."),t("br"),t("br"),n._v("“Lăng Vân kiếm các cách nơi này, đại khái muốn phi hành thời gian mười ngày!”"),t("br"),t("br"),n._v("Lưng thú phong kín gian phòng bên trong, Thiện Hiểu Thiên ngồi tại trước mặt lão sư, thân là Thượng Thương sinh trưởng ở địa phương sinh mệnh, Lăng Vân kiếm các tuy là thần bí, vẫn là biết không ít."),t("br"),t("br"),n._v("Dưới thân phi hành Thánh thú, đã đạt đến Cổ Thánh tứ trọng phá toái hư không cảnh giới!"),t("br"),t("br"),n._v("Cường đại như thế Thánh thú, một ngày có thể nhẹ nhõm bay ra mấy chục vạn cây số... Như vậy đều muốn phi hành mười ngày, Lăng Vân kiếm các khoảng cách, thật là xa."),t("br"),t("br"),n._v("Xem ra, cái này cái gọi là Thần Linh vứt bỏ thế giới diện tích, so với danh sư đại lục, đều chỉ đại không nhỏ."),t("br"),t("br"),n._v("Phi hành trong lúc đó, Trương Huyền đem Tào Thành Lập cho Tiên Nguyên đan nuốt hai cái, tu vi xem như triệt để khôi phục, hơn nữa còn loáng thoáng, đạt đến phá toái hư không sơ kỳ đỉnh phong, bất cứ lúc nào cũng sẽ đột phá."),t("br"),t("br"),n._v("Bởi vì không có thích hợp tu luyện công pháp, cưỡng ép áp chế xuống."),t("br"),t("br"),n._v("Trải qua mấy ngày nữa nghiên cứu, hắn vậy phát hiện, Thượng Thương tốc độ thời gian trôi qua cùng danh sư đại lục, quả thực cũng không tương đồng, đại khái tại 1: 10."),t("br"),t("br"),n._v("Nói cách khác, Thượng Thương một ngày, danh sư đại lục mười ngày!"),t("br"),t("br"),n._v("Danh sư đại lục vài vạn năm, nơi này cũng mới mấy ngàn năm, vừa vặn đối ứng bên trên Khổng sư sáng lập Thông Thần điện."),t("br"),n._v("Tốc độ thời gian trôi qua khác biệt, thêm vào thủy ngân linh khí nặng nề, tu luyện giả tuổi thọ, cũng nhận hạn chế."),t("br"),t("br"),n._v("Cường giả Thánh vực, tại danh sư đại lục, có thể sống hơn ngàn năm, mà ở đây, một trăm tuổi, chính là đại nạn."),t("br"),t("br"),n._v("Cổ Thánh cường giả, có thể sống hai ngàn năm trái phải, mà ở đây, hai trăm tuổi, là đến phần cuối."),t("br"),t("br"),n._v("Còn Lục Vân trưởng lão như vậy Hư Tiên, tuổi thọ cũng chỉ có ba trăm tuổi."),t("br"),t("br"),n._v("Những ngày gần đây, Trương Huyền loại trừ tự mình tu luyện, chính là chỉ điểm Thiện Hiểu Thiên cùng Tào Thành Lập, còn Dịch lão, lớn tuổi, lại thêm trước đó thiếu hụt quá lợi hại, cần thời gian dài điều dưỡng, không thích hợp tu luyện."),t("br"),t("br"),n._v("Thời gian mười ngày, thoáng một cái đã qua."),t("br"),t("br"),n._v("Hôm nay mặt trời mới từ đường chân trời bay lên, mọi người ngay sau đó nhìn thấy một cái to lớn sơn môn, xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Chừng hơn vạn mét cao, độ rộng cũng có mấy ngàn gạo khoảng cách, đứng sừng sững ở trong tầng mây, tựa như to lớn Nam Thiên môn."),t("br"),t("br"),n._v("Cửa chính chính giữa, một thanh từ gạch đá tạo dựng to lớn trường kiếm, đem xuyên qua."),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, liền có thể cảm thấy kiếm khí gào thét, loáng thoáng, muốn đem không gian đều xé nát, cho người ta một loại, tinh thần áp bức, khó mà động đậy cảm giác."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Trương Huyền có chút ngây người."),t("br"),t("br"),n._v("Không hổ là Thượng Thương sáu đại tông môn một trong, chỉ bằng vào cánh cửa này cùng thanh trường kiếm này, danh sư đại lục, liền không khả năng kiến tạo đi ra, chớ nói chi là, ẩn chứa mạnh mẽ kiếm ý."),t("br"),t("br"),n._v("Đạo kiếm ý này, so với hắn tu luyện kiếm đạo chân giải, không biết cường đại hơn không biết gấp bao nhiêu lần, có thể đoán được, thật nếu gặp phải sử dụng loại kiếm pháp này cao thủ, sẽ bị tuỳ tiện bị đánh bại."),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhúc nhích, hướng trước mắt trường kiếm nhìn lại, tựa như thấy được một thanh giả lập trường kiếm, che giấu trong đó."),t("br"),t("br"),n._v("Chính là cầm tới kiếm ý."),t("br"),t("br"),n._v("Thanh trường kiếm này hình thành ý cảnh cùng hắn tu luyện Thiên đạo kiếm pháp có chút tương tự, chẳng qua càng cao minh hơn, cho người ta một loại càng thêm siêu thoát mùi vị."),t("br"),t("br"),n._v("Vừa quan sát, một bên cảm thụ, Trương Huyền nhất thời cảm thấy mình đối kiếm đạo lý giải, lại có hiểu mới, trở nên càng thêm cường đại."),t("br"),t("br"),n._v("Tuy là chỉ nhìn bên trên liếc mắt, không có khả năng trực tiếp lý giải, nhưng mà ẩn chứa trong đó ý vị, cùng Thiên đạo kiếm pháp nhất mạch tương thừa, chỉ cần cấp cho đủ thời gian, hoàn toàn có thể hòa vào trong đó, để kiếm thuật phát sinh bay vọt về chất!"),t("br"),t("br"),n._v("“Đây là Lăng Vân kiếm các! Chuôi kiếm này, là khai phái tổ sư lưu lại, ẩn chứa trong đó hắn đối kiếm đạo lĩnh ngộ cùng kiếm ý, vô số hậu nhân, cũng vì đó sợ hãi thán phục!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão đi tới trước mặt, nhìn về phía trước mắt cánh cửa khổng lồ cùng trường kiếm, ánh mắt lộ ra thành kính cùng tôn kính."),t("br"),t("br"),n._v("Thiện Hiểu Thiên, vậy thân thể cứng nhắc, tròng mắt đỏ hoe."),t("br"),t("br"),n._v("Lúc trước luyện kiếm thời điểm, nằm mộng cũng muốn đi tới nơi này, ngày hôm nay cuối cùng đã tới."),t("br"),t("br"),n._v("“Đi vào tông môn phía trước, trước cùng các ngươi nói một ít quy củ tông môn!”"),t("br"),t("br"),n._v("Thấy Thánh thú bay vào sơn môn còn cần một đoạn thời gian, Lục Vân trưởng lão nhìn lại."),t("br"),t("br"),n._v("“Lăng Vân kiếm các, chia làm tạp dịch đệ tử, ngoại môn đệ tử, nội môn đệ tử cùng hạch tâm đệ tử bốn loại! Nội môn đệ tử, tuy là xem như rất mạnh mẽ, nhưng hạch tâm mới là toàn bộ kiếm các, chói mắt nhất tồn tại!”"),t("br"),t("br"),n._v("“Hạch tâm đệ tử?”"),t("br"),t("br"),n._v("Con mắt lóe lên một cái, Thiện Hiểu Thiên nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Kiếm các thế hệ sau, chỉ cần khảo hạch đi qua, cơ bản cũng không có cái gì chuyện, bình thường đều tại chỗ ở của mình luyện kiếm, tu hành, nếu như cần học tập, có thể đi tàng thư các mượn đọc, cũng có thể đặc biệt viếng thăm sư huynh, trưởng lão. Đương nhiên, những này, đều cần tiêu phí kiếm các tệ!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nói."),t("br"),t("br"),n._v("“Kiếm các tệ?”"),t("br"),t("br"),n._v("“Là kiếm các phát hành một loại chỉ có thể ở Kiếm các nội bộ lưu truyền tiền ảo, có thể tại trong tông môn, mua sắm tất cả ngươi muốn đồ vật, đan dược, vũ khí, công pháp, thậm chí trưởng lão giảng bài, cái gì cần có đều có! Bất quá, kiếm lấy lên khá là phiền toái, hoặc là tham gia công khai thi đấu, hoặc là, làm nhiệm vụ, hoặc là, giúp người giải đáp vấn đề, chỉ điểm tu vi của người khác... Tóm lại, ở đây, cái gì đều cần kiếm các tệ, không có tiền, nửa bước khó đi. Mà có tiền, tài nguyên tu luyện, sẽ lấy không hết, dùng mãi không cạn. Tốc độ tu luyện, cũng phải so người khác càng nhanh!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão tiếp tục nói."),t("br"),t("br"),n._v("Thiện Hiểu Thiên khiếp sợ."),t("br"),t("br"),n._v("Vốn cho rằng, Lăng Vân kiếm các loại này thế lực lớn, đại tông môn sẽ chuyên tâm tu luyện, không nghĩ tới, vậy như vậy tham tiền..."),t("br"),t("br"),n._v("“Có thể tham gia công khai thi đấu, trưởng lão ý là... Lăng Vân kiếm các, vậy có Thông Thần điện tồn tại?”"),t("br"),t("br"),n._v("Công khai thi đấu, là xuất hiện ở Thông Thần điện giả lập chiến đấu, chẳng lẽ, sáu đại tông môn một trong Lăng Vân kiếm các, vậy có loại vật này tồn tại?"),t("br"),t("br"),n._v("“Lăng Vân kiếm các, tự nhiên không có Thông Thần điện, có điều, nhưng cũng có thông thần ngọc phù, có thể ở bên trong chiến đấu, giao dịch, tiến hành mọi chuyện, cùng ngoại giới Thông Thần điện tương tự! Bất quá, từ kiếm các khống chế, cùng ngoại giới không thể kết nối!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão gật đầu."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Thật ra thì chính là cái càng lớn mạng máy tính cục bộ."),t("br"),t("br"),n._v("Nơi này đệ tử, có thể tu luyện, có thể chiến đấu, thậm chí mua sắm bán ra dược liệu, nhưng không nhận Thông Thần điện quản lý, vậy không cùng đối phương có kết nối."),t("br"),t("br"),n._v("“Còn có, tại trên thực lực vậy có rất lớn khác biệt! Nơi này thông thần ngọc phù, chỉ có nội môn trở lên thế hệ sau, mới có thể sử dụng. Bởi vậy... Ý niệm tiến vào bên trong, cơ sở thực lực, không còn là Thánh vực nhất trọng, mà là... Cổ Thánh nhất trọng!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nói tiếp."),t("br"),t("br"),n._v("Tinh Diệu thành thông thần ngọc phù, chỉ cần đi vào, mặc kệ tu vi mạnh cỡ nào, đều là Thánh vực nhất trọng, nơi này không giống, mặc kệ mạnh cỡ nào, tiến vào bên trong, đều là Cổ Thánh nhất trọng."),t("br"),t("br"),n._v("Chỉ là Thánh vực nhất trọng mà nói, không ít tuyệt diệu kiếm pháp không có cách nào thi triển, công khai thi đấu, cũng liền không còn ý nghĩa."),t("br"),t("br"),n._v("“Đương nhiên, cùng địa phương khác Thông Thần điện đồng dạng, đi vào nơi này, đồng dạng có thể mai danh ẩn tích, để người khác không biết là ai, có điều, người khác không biết ngươi là ai, ngươi cũng không biết đối phương là ai... Có khả năng, cùng ngươi tán gẫu, là một vị trưởng lão, thậm chí... Tông chủ! Bởi vậy... Ở đây, tuyệt đối không nên làm càn, cứ việc trưởng lão, tông chủ sẽ không thù dai, đắc tội, khẳng định vậy phiền phức không ít!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão trịnh trọng bàn giao."),t("br"),t("br"),n._v("“Trưởng lão, tông chủ?”"),t("br"),t("br"),n._v("Nghe được giải thích của hắn, Trương Huyền chẳng những không có căng thẳng, con mắt ngược lại sáng lên, trong lòng tràn đầy mặc sức tưởng tượng: “Loại người này đánh một trận... Có thể hay không so đánh người bình thường, thoải mái hơn một ít?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1956"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1958"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);