(window.webpackJsonp=window.webpackJsonp||[]).push([[1079],{1436:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1945-thap-ly-kiem-than"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1945-thap-ly-kiem-than"}},[n._v("#")]),n._v(" Chương 1945: Thập Lý kiếm thần")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Cùng cấp bậc, Trương Huyền hầu như vô địch."),t("br"),t("br"),n._v("Đồng dạng áp chế ở Cổ Thánh nhất trọng, coi như đối phương là tông chủ, hoặc là trưởng lão, có thể thắng hay không qua bản thân, đều không nhất định!"),t("br"),t("br"),n._v("Đã như vậy, hoàn toàn có thể tìm bọn họ cuồng loạn một hồi... Những người này đồ tốt không ít, biết tin tức khẳng định cũng nhiều, đánh bại đối phương, liền có thể cẩn thận hỏi thăm!"),t("br"),t("br"),n._v("Có lẽ Lạc Nhược Hi tin tức, liền có phương pháp."),t("br"),t("br"),n._v("Nhìn tới, đến tông môn phía sau, nhất định phải nhanh tìm tới thích hợp thông thần ngọc phù, đi vào đối phương hệ thống, sau đó... Đặc biệt tìm tông chủ hoặc là cao cấp nhất trưởng lão đi đánh!"),t("br"),t("br"),n._v("Nghĩ đến cái này, Trương Huyền nhịn không được vỗ đùi, đứng dậy: “Quá tốt rồi, cứ làm như thế!”"),t("br"),t("br"),n._v("“Làm cái gì?”"),t("br"),t("br"),n._v("Lục Vân trưởng lão một mặt nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Ta liền cảnh cáo ngươi một câu, nói gặp được tông chủ và trưởng lão... Ngươi hưng phấn cái gì?"),t("br"),t("br"),n._v("Cái tên này sẽ không phải là đầu óc tối dạ a?"),t("br"),t("br"),n._v("Trước đó còn nghĩ lấy, có muốn hay không đem hắn cũng thu vào kiếm các, ở chung mấy ngày, phát hiện đối phương thỉnh thoảng bạo phát bệnh tâm thần... Ý nghĩ thế này liền dần dần bỏ đi."),t("br"),t("br"),n._v("“Ách, không có gì... Thuận miệng nói một chút!” Khó xử gãi đầu một cái, Trương Huyền cười ha ha."),t("br"),t("br"),n._v("Thiện Hiểu Thiên bưng kín mặt."),t("br"),t("br"),n._v("Phi hành Thánh thú rất nhanh rơi vào một cái cao lớn sơn phong, từ lưng thú bên trên xuống tới, Lục Vân trưởng lão an bài một tòa phủ đệ."),t("br"),t("br"),n._v("“Các ngươi trước tiên ở nơi này nghỉ ngơi, Thiện Hiểu Thiên cùng ta tới, ta mang ngươi tới khảo hạch!”"),t("br"),t("br"),n._v("Để Trương Huyền, Tào Thành Lập và Dịch lão, ba người ở lại, Lục Vân trưởng lão nói."),t("br"),t("br"),n._v("“Khảo hạch?”"),t("br"),t("br"),n._v("“Trước đó khảo hạch của ta, chỉ nói là ngươi có tư cách trở thành nội môn đệ tử, chân chính tiến vào nội môn, còn cần đi Kiếm Thần đường thử kiếm!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nói."),t("br"),t("br"),n._v("“Kiếm Thần đường?”"),t("br"),t("br"),n._v("“Là đệ tử khảo hạch địa phương, bất luận là tạp dịch đệ tử muốn tiến vào nội môn, vẫn là nội môn đệ tử muốn trở thành thân truyền, đều cần đi qua kiểm tra, cùng ta trước đó để ngươi cùng Hoắc Giang Hà tỷ thí một cái đạo lý, chỉ là, nơi này khảo nghiệm càng thêm tiêu chuẩn! So chiến đấu, càng thêm phù hợp thực lực bản thân.”"),t("br"),t("br"),n._v("Lục Vân trưởng lão cười giải thích: “Chiến đấu, bởi vì trạng thái, tâm cảnh, lâm trận phản ứng các loại, có thể sẽ xuất hiện chênh lệch, nhưng Kiếm Thần đường khảo hạch, sẽ không! Chỉ cần ngươi đối với kiếm pháp lĩnh ngộ đủ, đi đến cấp bậc, liền có thể tuỳ tiện đo ra.”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên giật mình."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nói xong cũng muốn dẫn lấy Thiện Hiểu Thiên rời đi."),t("br"),t("br"),n._v("“Lục trưởng lão, ta cũng muốn đi xem nhìn, không biết có thể?”"),t("br"),t("br"),n._v("Trương Huyền đi tới."),t("br"),t("br"),n._v("Kiếm Thần đường, lại có thể kiểm tra đối kiếm đạo lý giải, hắn đối kiếm thuật cũng coi như có nhất định lý giải, vừa vặn có thể đi nhìn một chút."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão chần chờ một chút: “Cũng được!”"),t("br"),t("br"),n._v("Dưới tình huống bình thường, Kiếm Thần đường là không cho phép người ngoài tiến vào, có điều, tạp dịch đệ tử có thể ở đây khảo hạch, nội môn đệ tử cũng có thể khảo hạch, vị này Trương Huyền, cứ việc không phải kiếm các đệ tử, nhưng là Thiện Hiểu Thiên bằng hữu, đi qua nhìn một chút, cũng không sao."),t("br"),t("br"),n._v("Lần nữa ngồi lên lưng thú, phi hành một hồi, đi tới mặt khác một tòa đồ sộ sơn phong."),t("br"),t("br"),n._v("Chính giữa, một thanh trường kiếm giống như kiến trúc, cắm trên mặt đất, như là cao lâu."),t("br"),t("br"),n._v("“Nơi này chính là Kiếm Thần đường, mỗi ngày đều sẽ có không ít tạp dịch đệ tử, ngoại môn đệ tử, nội môn đệ tử tới kiểm tra!”"),t("br"),t("br"),n._v("Đi vào cửa chính, quả nhiên thấy không ít người đứng tại trong đó, tất cả đều cõng trường kiếm, trên người kiếm khí mơ hồ, phần lớn thực lực, vậy mà đều không thể so với Trương Huyền yếu."),t("br"),t("br"),n._v("“Không hổ là Thượng Thương sáu đại thế lực một trong, cao thủ quả nhiên không ít...”"),t("br"),t("br"),n._v("Nhịn không được cảm xúc."),t("br"),t("br"),n._v("Hắn Cổ Thánh tứ trọng sơ kỳ đỉnh phong thực lực, vốn cho rằng không tệ, không nghĩ tới ở đây, thế mà liền tên đều không xếp hạng tới."),t("br"),t("br"),n._v("Đây vẫn chỉ là tới tìm vận may, khảo nghiệm, chân chính bế quan cường giả, đến cùng có bao nhiêu, lại có thực lực gì, có thể tưởng tượng được."),t("br"),t("br"),n._v("“Những này đâu đâu cũng có gian phòng, mỗi một cái gian phòng có một tòa kiếm thần đài, chỉ cần cấp cho đủ kiếm các tệ, liền có thể mở ra cửa phòng đi vào kiểm tra!”"),t("br"),t("br"),n._v("Biết bọn họ ngày đầu tiên đến, đối Kiếm Thần đường cũng không hiểu rõ tình hình, Lục Vân trưởng lão tiếp tục giới thiệu: “Đương nhiên, lần đầu tiên khảo hạch là miễn phí, phía sau khảo hạch mới cần dùng tiền!”"),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Hai người đồng thời thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Bọn họ mới đến kiếm các, liền kiếm các tệ, bề ngoài ra sao cũng không biết, thật muốn dùng tiền, cũng không bỏ ra nổi tới."),t("br"),t("br"),n._v("“Ta cho các ngươi mở một cái phòng, có thể kiểm tra hai lần, Thiện Hiểu Thiên đi vào trước, Trương Huyền tiểu huynh đệ, cũng muốn thử một chút mà nói, chờ hắn đi ra, lại đi! Kiếm thần đài, một lần chỉ có thể kiểm tra một người, nhiều sẽ làm liên quan kiếm ý sưu tập!”"),t("br"),t("br"),n._v("Giới thiệu xong xuôi, Lục Vân trưởng lão đi tới, thời gian không dài, mang theo hai người tới một cái phòng trước mặt: “Sau khi tiến vào, nắm chặt trường kiếm, lập tức phóng thích trong cơ thể kiếm ý, vận chuyển đối kiếm đạo lý giải... Kiếm ý vượt qua một mét, liền có thể trở thành tạp dịch đệ tử, mười mét, có thể trở thành ngoại môn đệ tử, ba mươi mét, liền có tư cách trở thành nội môn đệ tử! Hiện tại mục tiêu của ngươi chính là vượt qua khoảng cách này!”"),t("br"),t("br"),n._v("“Ba mươi mét?” Thiện Hiểu Thiên ngây thơ gật đầu."),t("br"),t("br"),n._v("“Ba mươi mét, biểu thị đối kiếm đạo lý giải, cũng không phải là kiếm khí, lấy tu vi của ngươi, kiếm khí không có khả năng lan ra dài như vậy...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão cười nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thiện Hiểu Thiên khó xử."),t("br"),t("br"),n._v("Lấy hắn đối kiếm thuật lý giải, phóng thích mà ra kiếm khí, hai, ba mét có thể hoàn thành, ba mươi mét, khẳng định không làm được."),t("br"),t("br"),n._v("Cái gọi là kiếm ý, hẳn không phải là cái này."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Thiện Hiểu Thiên đẩy cửa đi vào."),t("br"),t("br"),n._v("Gặp hắn rời đi, Trương Huyền tràn đầy tò mò nhìn qua: “Kiếm ý chiều dài, nội môn đệ tử cực hạn là bao nhiêu?”"),t("br"),t("br"),n._v("Ba mươi mét đi qua tuyển chọn, cái kia cực hạn lại là bao nhiêu?"),t("br"),t("br"),n._v("“Dưới tình huống bình thường, chỉ cần kiếm ý, đi đến một trăm mét, liền có tư cách trở thành hạch tâm đệ tử! Nhưng không ít nội môn đệ tử, đã vượt qua khoảng cách này. Đứng đầu nhất những cái kia, đều vượt qua hai trăm mét, ba trăm mét, thậm chí càng xa!”"),t("br"),t("br"),n._v("Lục Vân trưởng lão nói."),t("br"),n._v(" “Ba trăm mét?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Mặc dù không biết cái này kiếm ý chiều dài, đến cùng làm sao tính toán, nhưng... Bình thường nội môn đệ tử, ba mươi mét cũng khó khăn, có ít người lại có thể đi đến gấp mười lần khoảng cách, sự đáng sợ có thể tưởng tượng được."),t("br"),t("br"),n._v("“Đúng vậy a, có điều, hạch tâm đệ tử bên trong, có ít người càng thêm cường đại, vượt qua năm trăm mét, vượt qua loại này khoảng cách, liền có thể gọi là Kiếm Thần! Bởi vậy, cũng liền có Nhất Lý kiếm thần, Nhị Lý kiếm thần các loại xưng hào... Nghe nói kiếm các trong lịch sử, thiên tài nhất một vị, đã từng đem kiếm ý, đi đến mười dặm, bởi vậy, cũng được xưng là Thập Lý kiếm thần!”"),t("br"),t("br"),n._v("Con mắt thả ra tinh quang, Lục Vân trưởng lão tràn đầy tôn sùng."),t("br"),t("br"),n._v("“Thập Lý kiếm thần?”"),t("br"),t("br"),n._v("Trương Huyền khiếp sợ."),t("br"),t("br"),n._v("Người bình thường còn vì mấy chục mét sầu muộn thời điểm, vị thiên tài này, vậy mà đạt đến mười dặm... Suy nghĩ một chút đều để người cho rằng khó tin."),t("br"),t("br"),n._v("“Không biết... Lục trưởng lão kiếm ý, có thể lan ra bao xa?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, Trương Huyền tràn ngập tò mò."),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Nghe được hỏi thăm, Lục Vân trưởng lão cười khổ một tiếng: “Ta thiên phú không tốt, lúc còn trẻ, khảo hạch nội môn đệ tử, đều không có làm được, dựa vào sau trời cố gắng, mới đạt tới tu vi hiện tại, lăn lộn một cái ngoại môn trưởng lão vị trí... Mặc dù luyện kiếm nhiều năm, vẫn như cũ không cách nào đột phá một trăm mét, đến bây giờ cũng chỉ có chín mươi bảy mét thôi!”"),t("br"),t("br"),n._v("“Chín mươi bảy mét? Đã rất lợi hại...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, nhịn không được hướng trước mắt cửa phòng nhìn lại..."),t("br"),t("br"),n._v("Vị này đồ đệ, chỉ cùng mình học tập một đêm kiếm pháp, chỉ là nhớ kiếm chiêu, đối kiếm đạo, kiếm ý lý giải cũng không sâu... Không biết có thể đi đến bao nhiêu mét?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thiện Hiểu Thiên đi vào phòng, lập tức nhìn thấy trước mắt một cái không lớn viên đài, trung tâm cắm một thanh trường kiếm."),t("br"),t("br"),n._v("Hẳn là cái gọi là kiếm thần đài."),t("br"),t("br"),n._v("Ngay phía trước, là một mảnh mênh mông vùng quê."),t("br"),t("br"),n._v("Nguyên lai gian phòng này không có bức tường, khó trách có thể làm cho kiếm ý lan ra mấy chục mét, nếu không, xây dựng như vậy lớn, hơn nữa mênh mông, không cách nào ngăn cản kiếm ý gian phòng, cũng cần tiêu phí không biết bao nhiêu công phu."),t("br"),t("br"),n._v("Nhấc chân bước lên, đem trường kiếm rút lên, cầm tại lòng bàn tay, hít một hơi thật sâu, Thiện Hiểu Thiên tinh thần cao độ tập trung, vận chuyển lực lượng trong cơ thể."),t("br"),t("br"),n._v("Cái gọi là kiếm ý, chính là mình đối với kiếm pháp lý giải."),t("br"),t("br"),n._v("Hắn từ nhỏ đến lớn, một mực luyện kiếm, khổ tu hơn mười năm, sớm đã đối với kiếm pháp có thuộc về mình lĩnh ngộ cùng tình cảm."),t("br"),t("br"),n._v("Trường kiếm nắm tại lòng bàn tay, nhất thời có một loại, nước sữa hòa nhau cảm giác, hình như cánh tay kéo dài."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Cảm nhận được ý cảnh của hắn, trường kiếm phát ra nghẹn ngào vang lên, một đạo trắng tinh quang mang, tại kiếm thần đài vận chuyển bên dưới, chậm chạp hội tụ, hướng về phía trước lan ra."),t("br"),t("br"),n._v("Híp mắt lại, không ngừng vận chuyển lực lượng."),t("br"),t("br"),n._v("Ba!"),t("br"),t("br"),n._v("Hướng về phía trước lan ra kiếm ý, ngừng lại, nhìn rõ ràng khoảng cách, Thiện Hiểu Thiên vẻ mặt cứng đờ."),t("br"),t("br"),n._v("“Một thước? Cũng chính là... Một phần ba mét?”"),t("br"),t("br"),n._v("Thi triển lực lượng toàn thân, kiếm ý vậy mà chỉ có một phần ba mét khoảng cách... Nói như vậy lên, chẳng phải tương đương, hắn đối kiếm đạo lý giải, liền tên tạp dịch đệ tử đều không đạt tới?"),t("br"),t("br"),n._v("“Không có khả năng... Cùng cấp bậc Hoắc Giang Hà cũng không phải đối thủ của ta...”"),t("br"),t("br"),n._v("Có chút choáng váng, Thiện Hiểu Thiên cho rằng đầu lâu sắp nổ tung."),t("br"),t("br"),n._v("Lục Vân trưởng lão một mực nói, Kiếm Thần đường thế nào thế nào công bằng, bản thân nhưng mà thử qua kiếm pháp, cùng cấp bậc Hoắc Giang Hà đều không đỡ được, tới kiểm tra, làm sao có thể liền một mét đều không đạt tới?"),t("br"),t("br"),n._v("“Ta là vứt kiếm đánh bại Hoắc Giang Hà, thử một chút chiêu này...”"),t("br"),t("br"),n._v("Rầu rĩ một hồi, đột nhiên nghĩ tới cường đại nhất một chiêu, hít sâu một hơi, ngón tay nhẹ nhàng điểm một cái."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trường kiếm rời tay huyền phù."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trường kiếm vừa rời đi bàn tay, một cỗ mạnh mẽ kiếm ý, lập tức từ trên người hắn thả ra ngoài, cả phòng hội tụ ra vô số sương mù màu trắng, điên cuồng phun trào, giống như gió lốc."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Thiện Hiểu Thiên ngây người."),t("br"),t("br"),n._v("Vừa rồi trong cơ thể kiếm ý, dùng hết toàn lực, cũng mới lan ra một phần ba mét, hiện tại sử dụng lão sư truyền thụ cho vứt kiếm, kiếm còn không có rời đi, giống như này hung mãnh, triệt để đem chiêu số thi triển đi ra, nên có bao nhiêu lợi hại?"),t("br"),t("br"),n._v("Lão sư truyền thụ cho cùng mình suy nghĩ ra được... Sẽ không kém đừng như vậy đại đi..."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Khuôn mặt khó xử, ngón tay một điểm, trường kiếm thẳng tắp bay tới đằng trước."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Bạch sắc kiếm ý, nhanh chóng tuôn ra, thời gian nháy mắt, liền biến mất tại tầm mắt, không thấy tung tích."),t("br"),t("br"),n._v("Cái này..."),t("br"),t("br"),n._v("Thiện Hiểu Thiên nháy con mắt."),t("br"),t("br"),n._v("Hắn bản thân kiếm ý, một thước đến đỉnh, thi triển lão sư kiếm pháp, bay ra mất đi tung tích... Nhìn bằng mắt thường không thấy..."),t("br"),t("br"),n._v("Cái này rõ ràng vượt qua ba mươi mét, năm mươi mét thậm chí một trăm mét đi!"),t("br"),t("br"),n._v("“Còn tại bay?”"),t("br"),t("br"),n._v("Chờ một hồi, kiếm ý dường như còn không có phi hành đến phần cuối, Thiện Hiểu Thiên thấu không biết như thế nào cho phải."),t("br"),t("br"),n._v("Một mực bay xuống đi, có phải hay không đại biểu, bản thân không thành tích?"),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngay tại đầy mặt xoắn xuýt thời điểm, bên cạnh bức tường lắc lư một cái, xuất hiện thành tích."),t("br"),t("br"),n._v("“49 9 m!”"),t("br"),t("br"),n._v("49 9 m, kém một bước, kiếm thần!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1957"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1959"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);