(window.webpackJsonp=window.webpackJsonp||[]).push([[1083],{1439:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1948-nguoi-khao-hach-thoi-điem-co-kiem"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1948-nguoi-khao-hach-thoi-điem-co-kiem"}},[n._v("#")]),n._v(" Chương 1948: Ngươi khảo hạch thời điểm có kiếm?")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Mở cửa phòng, lập tức nhìn thấy Lục Vân trưởng lão cùng Thiện Hiểu Thiên tràn đầy khẩn trương nhìn tới."),i("br"),i("br"),n._v("“Làm sao vậy?”"),i("br"),i("br"),n._v("Thấy bọn họ bộ dáng này, Trương Huyền nhíu nhíu mày."),i("br"),i("br"),n._v("Chẳng phải khảo nghiệm một hồi ư? Còn khẩn trương như vậy ư?"),i("br"),i("br"),n._v("“Ngươi không bị tổn thương?”"),i("br"),i("br"),n._v("Gặp hắn bình yên vô sự, Lục Vân trưởng lão thở phào nhẹ nhõm, tràn đầy nghi ngờ."),i("br"),i("br"),n._v("“Bị thương?”"),i("br"),i("br"),n._v("Trương Huyền yên lặng: “Kiểm tra kiếm ý... Cần bị thương sao?”"),i("br"),i("br"),n._v("Chẳng lẽ, cái này kiếm ý kiểm tra, còn có cái gì bị thương kỹ xảo, không bị thương, liền làm không xa?"),i("br"),i("br"),n._v("“Không phải, ngươi nhìn...”"),i("br"),i("br"),n._v("Thấy lão sư một mặt ngỡ ngàng, Thiện Hiểu Thiên gãi đầu một cái, một chỉ bốn phía."),i("br"),i("br"),n._v("Trương Huyền ngẩng đầu nhìn lại, xem xét phía dưới, không khỏi ngẩn ngơ."),i("br"),i("br"),n._v("Chỉ thấy bốn phía kiểm tra gian phòng, mỗi một cái đi ra tu luyện giả, đều từng ngụm từng ngụm hộc máu, khuôn mặt trắng bệch, giống như là tinh khí thần đều bị nghiền ép đồng dạng."),i("br"),i("br"),n._v("Trương Huyền ngẩn ngơ."),i("br"),i("br"),n._v("“Kiểm tra kiếm ý... Cần khoa trương như vậy ư? Ngươi vừa rồi hình như không có hộc máu ah...” Trương Huyền nhịn không được nhìn qua."),i("br"),i("br"),n._v("Thiện Hiểu Thiên khảo nghiệm thời điểm, liền không có hộc máu, làm sao nhiều người như vậy, đều bộ dáng này?"),i("br"),i("br"),n._v("“Không phải khảo nghiệm nguyên nhân, là vừa rồi, khảo nghiệm thời điểm, đột nhiên xuất hiện một đạo cực kỳ cường đại kiếm ý! Những người khác bởi vì quá yếu, nhận lấy áp chế, mới xuất hiện loại tình huống này!” Lục Vân trưởng lão nói."),i("br"),i("br"),n._v("Hắn thân là ngoại môn trưởng lão, đối với kiếm pháp lĩnh ngộ cực cao, biết cũng nhiều, cứ việc không tiến vào khảo nghiệm qua, cái kia cỗ kiếm ý mạnh mẽ, vẫn như cũ rõ ràng cảm nhận được."),i("br"),i("br"),n._v("Giống như giao long bao quát chúng sinh, ở đây nhiều như vậy lĩnh ngộ kiếm ý đệ tử... Tại trong mắt đối phương, đều như sâu kiến đồng dạng."),i("br"),i("br"),n._v("“Mạnh mẽ kiếm ý? Chẳng lẽ... Có thể đi đến cấp bậc Kiếm Thần?”"),i("br"),i("br"),n._v("Trương Huyền sững sờ, nhịn không được nhìn tới."),i("br"),i("br"),n._v("“Đâu chỉ, đáng sợ đều có thể đi đến mười dặm!” Lục Vân trưởng lão gật đầu."),i("br"),i("br"),n._v("Hắn chưa bao giờ thấy qua Thập Lý Kiếm Thần, nhưng tông môn lịch sử ghi chép lợi hại nhất chính là như vậy trị số, vừa rồi cảm giác áp bách, một đạo kiếm ý, khiêu chiến tất cả mọi người, không có người có thể chống lại... Đủ để chứng minh mạnh mẽ!"),i("br"),i("br"),n._v("Đoán chừng cho dù không phải Thập Lý Kiếm Thần, cũng không kém nhiều!"),i("br"),i("br"),n._v("Còn cao hơn... Nghĩ cũng không dám nghĩ."),i("br"),i("br"),n._v("Dù sao, Lăng Vân kiếm các mấy ngàn năm lịch sử, vượt qua cấp bậc này, một vị cũng không có xuất hiện qua."),i("br"),i("br"),n._v("“Thập Lý Kiếm Thần?”"),i("br"),i("br"),n._v("Trương Huyền líu lưỡi, trong mắt thả ra tinh quang: “Trưởng lão có biết là vị nào?”"),i("br"),i("br"),n._v("Hắn liền một mét đều không đạt tới, đối phương nhưng có thể để cho kiếm ý, đi đến mười dặm... Thực lực mạnh mẽ, có thể xưng kinh khủng!"),i("br"),i("br"),n._v("Loại nhân vật này, nhất định phải nhìn một chút!"),i("br"),i("br"),n._v("Trước đó còn nghĩ lấy, dựa vào hắn cùng cấp bậc vô địch năng lực, đi vào Thông Thần điện, liền lập tức quét ngang, tới cái “Hoành Tảo Thiên Nhai” ngoại hiệu, hiện tại xem ra, đáng sợ không dễ dàng như vậy!"),i("br"),i("br"),n._v("Thập Lý Kiếm Thần ah..."),i("br"),i("br"),n._v("Đây chính là có thể ngộ nhưng không thể cầu tuyệt thế thiên tài, coi như mình Hoành Tảo Thiên Nhai, gặp được Thập Lý Kiếm Thần, đoán chừng cũng phải quỳ!"),i("br"),i("br"),n._v("“Không biết...”"),i("br"),i("br"),n._v("Lục Vân trưởng lão lắc đầu."),i("br"),i("br"),n._v("Kiểm tra kiếm ý, trừ phi tận mắt nhìn thấy, cầm tới ngọc bài, cũng chỉ có liền nhau có lẽ có thể nhìn thấy từng người thành tích, người ngoài không có khả năng biết."),i("br"),i("br"),n._v("“Đúng rồi, ngươi... Vừa rồi kiểm tra, thành tích là bao nhiêu?”"),i("br"),i("br"),n._v("Nhịn không được nhìn lại."),i("br"),i("br"),n._v("Hắn đối vị này Trương Huyền, vẫn còn có chút tò mò."),i("br"),i("br"),n._v("Thiện Hiểu Thiên đối hắn nói gì nghe nấy, coi như hai người là bạn tốt, cũng không đến mức như vậy nghe lời đi!"),i("br"),i("br"),n._v("Mấu chốt nhất là, Tào Thành Lập cùng vị kia Dịch lão, đồng dạng vô cùng tôn trọng."),i("br"),i("br"),n._v("Chỉ bất quá, vị này Trương Huyền, vẫn luôn rất biết điều, chụp vào mấy câu, đều bộ không ra được, làm việc lại có lúc đợi mơ mơ hồ hồ, hình như đối đại lục không ít chuyện, đều hoàn toàn không biết gì cả, cũng liền không tại hỏi tới."),i("br"),i("br"),n._v("Lần này nhất định muốn tới kiểm tra, hắn cũng là mang theo khảo nghiệm thoáng cái tâm tư, mới đáp ứng."),i("br"),i("br"),n._v("Nếu không, người người đều muốn tới kiểm tra, liền xem như ngoại môn trưởng lão, cũng chi không nổi tiền, tốn hao không thể hơn tinh lực ah!"),i("br"),i("br"),n._v("“Ta?”"),i("br"),i("br"),n._v("Trương Huyền mặt thoáng cái xanh biếc: “Không đề cập tới việc này, chúng ta vẫn là bạn bè!”"),i("br"),i("br"),n._v("“Ngươi không nói chính ta nhìn một chút liền biết...”"),i("br"),i("br"),n._v("Gặp hắn không muốn nói, Lục Vân trưởng lão lắc đầu, đẩy cửa đi vào, một lát sau, cầm một cái ngọc bài đi ra, phía trên viết đối phương vừa mới khảo nghiệm thành tích."),i("br"),i("br"),n._v("Mỉm cười, cúi đầu nhìn lại, liếc mắt nhìn, khóe miệng co giật."),i("br"),i("br"),n._v("“Hai thước năm? Không đến... Một mét?” Lục Vân trưởng lão đầy mặt xấu hổ."),i("br"),i("br"),n._v("Khó trách đối phương không muốn nói, thành tích này, quả thực nói không nên lời!"),i("br"),i("br"),n._v("Một bên Thiện Hiểu Thiên liếc mắt nhìn, lần nữa nhìn về phía trước mắt lão sư, khâm phục chi ý càng đậm."),i("br"),i("br"),n._v("Lão sư một mực nói mình khiêm tốn, vốn đang cho rằng, có phải hay không ngoài miệng nói một chút, nhìn thấy giờ mới hiểu được, là thật khiêm tốn ah!"),i("br"),i("br"),n._v("Tuỳ truyền thụ bản thân kiếm pháp, đều có thể làm ra 49 9 m, bản thân đi lên, liền một mét, đều không có lấy ra... Cái này muốn bao nhiêu mạnh mẽ trái tim, cỡ nào cao thượng phẩm chất, đối danh dự cỡ nào không quan tâm..."),i("br"),i("br"),n._v("Lão sư chính là lão sư!"),i("br"),i("br"),n._v("Không hề giống bản thân như vậy xốc nổi!"),i("br"),i("br"),n._v("Bản thân rõ ràng không làm được, ngược lại giả vờ giả vịt... Là thật thật ngượng ngùng ah!"),i("br"),n._v(" Thấy cái tên này, đem ngọc bài lấy ra, ngay trước đồ đệ trước mặt, Trương Huyền hận không thể đập đầu chết."),i("br"),i("br"),n._v("Danh sư đại lục thu nhiều như vậy đồ đệ, đều không có ném qua như vậy lớn người!"),i("br"),i("br"),n._v("Tràn đầy tâm tắc."),i("br"),i("br"),n._v("“Trở về đi!” Lục Vân trưởng lão đánh vỡ xấu hổ."),i("br"),i("br"),n._v("Vừa rồi nhìn thấy nhiều người như vậy đều hộc máu, liền vị này không có việc gì, còn nghĩ lấy, có phải là hắn hay không chính là vị kia Thập Lý Kiếm Thần, nhìn thấy trên ngọc bài ghi chép, mới hiểu được... Cả nghĩ quá rồi!"),i("br"),i("br"),n._v("Liền một mét cũng chưa tới kiếm ý, tương đương với không có, không có kiếm ý, tự nhiên cũng không cảm ứng được đối phương mạnh mẽ, cũng sẽ không bị thương!"),i("br"),i("br"),n._v("Còn... Đến cùng ai là vị này kiếm thần, hắn chỉ là cái ngoại môn trưởng lão, cấp độ quá cao, đã tiếp xúc không lên, tra như thế nào hỏi ý kiến, tự nhiên sẽ có tông môn cao tầng quyết định."),i("br"),i("br"),n._v("Lần nữa trở lại chỗ ở, Lục Vân trưởng lão xoay người cáo từ."),i("br"),i("br"),n._v("Thiện Hiểu Thiên khảo hạch hết, hắn còn muốn mang theo ngọc bài, đi trưởng lão hội, hỗ trợ xin nội môn đệ tử lệnh bài, thoáng cái gọi đến có thể đem kiếm ý lan ra đến 49 9 m siêu cấp thiên tài, tông môn ban thưởng, có thể tưởng tượng được."),i("br"),i("br"),n._v("Có lẽ, có thể nhờ vào đó, đột phá hiện tại ràng buộc, tấn cấp cao hơn thực lực, từ đó trở thành nội môn trưởng lão!"),i("br"),i("br"),n._v("Ngồi phi hành Thánh thú một đường phi nhanh, thời gian không dài đi tới trưởng lão điện."),i("br"),i("br"),n._v("Còn chưa tới đến trước mặt, liền thấy mấy vị quen thuộc trưởng lão, tất cả đều gấp gáp hướng bên này chạy tới."),i("br"),i("br"),n._v("Mấy vị này, đều là nội môn trưởng lão, từng cùng hắn là bạn, chỉ bất quá, đột phá đạo kia ràng buộc, tấn cấp cao hơn thực lực, tiếp xúc liền càng ngày càng ít."),i("br"),i("br"),n._v("“Mộc trưởng lão, gấp gáp như vậy làm gì? Chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Nhìn mỗi người đều vội vã, một mặt lo lắng, cũng nhịn không được nữa Lục Vân trưởng lão, ngăn lại trong đó một vị, trước đó quan hệ tốt nhất, mở miệng hỏi."),i("br"),i("br"),n._v("“Xảy ra chuyện lớn như vậy, ngươi không biết?” Mộc trưởng lão là cái hơi khô gầy lão nhân, màu xanh sợi râu, màu xám nhạt trường bào: “A, cũng đúng, ngươi chỉ là cái ngoại môn trưởng lão, là không có tư cách tham gia hội nghị trưởng lão, không biết cũng rất bình thường...”"),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Lục Vân trưởng lão xấu hổ."),i("br"),i("br"),n._v("“Không nên tức giận, ngươi cũng biết, ta nói chuyện từ trước tới nay thẳng như vậy, có cái gì thì nói cái đó...”"),i("br"),i("br"),n._v("Mộc trưởng lão mặt không hề cảm xúc: “Đại trưởng lão Hà Thiên, ngươi nghe nói qua chứ!”"),i("br"),i("br"),n._v("“Ừm!” Lục Vân trưởng lão gật đầu."),i("br"),i("br"),n._v("Hà Thiên là tông môn hạch tâm nhất trưởng lão, hơn nữa còn là xếp hàng thứ nhất đại trưởng lão, tông chủ không tại tông môn thời điểm, nắm giữ mọi chuyện xử lý quyền."),i("br"),i("br"),n._v("“Không biết cũng không quan hệ, không cần xấu hổ, dù sao thân phận của ngươi, ở nơi đó bày biện, không cần thiết cứng rắn chống đỡ...” Mộc trưởng lão nói tiếp."),i("br"),i("br"),n._v("“Khụ khụ, ngươi vẫn là nói một chút, Hà Thiên trưởng lão chuyện gì đi!” Biết vị bằng hữu này, nói chuyện dường như lảm nhảm, cũng không có ác ý, Lục Vân trưởng lão vội vàng cắt đứt."),i("br"),i("br"),n._v("“Là như vậy, Hà trưởng lão, vừa cho chúng ta tất cả nội môn trở lên trưởng lão đưa tin, nói... Sơn môn cùng cự kiếm... Sập!”"),i("br"),i("br"),n._v("Mộc trưởng lão gật đầu: “Ngươi cấp bậc quá thấp, không có tư cách đạt được thông báo...”"),i("br"),i("br"),n._v("“Sơn môn sập?”"),i("br"),i("br"),n._v("Phía sau đối phương mà nói, Lục Vân trưởng lão không có tiếp tục nghe tiếp, mà là mở to hai mắt nhìn."),i("br"),i("br"),n._v("Sơn môn cùng cự kiếm, là Lăng Vân kiếm các tượng trưng, đại biểu cho “Lăng vân chi chí, thẳng phá cửu thiên!”"),i("br"),i("br"),n._v("Sập..."),i("br"),i("br"),n._v("Đối tông môn uy nghiêm, sẽ là rất lớn tổn thương!"),i("br"),i("br"),n._v("“Tại sao lại sập?” Cắt đứt đối phương nói, vội vàng nhìn qua."),i("br"),i("br"),n._v("“Ta cũng không biết, vừa rồi một cỗ cường đại vô song kiếm ý, đột nhiên xuất hiện, ngươi hẳn là cũng cảm nhận được a, hình như đạo kiếm ý này, trao đổi cự kiếm phía trong kiếm ý, vì vậy dẫn đến sụp đổ...” Mộc trưởng lão cũng không hiểu ra sao, lắc đầu: “Tốt, chuyện này không nói, ta hiện tại muốn đi tham gia trưởng lão hội, không cùng ngươi trò chuyện...”"),i("br"),i("br"),n._v("Nói xong, đầu đều không chuyển, thời gian nháy mắt biến mất tại nguyên chỗ."),i("br"),i("br"),n._v("“Cái tên này, vẫn giống như trước kia, một chút cũng không thay đổi...”"),i("br"),i("br"),n._v("Lục Vân trưởng lão đầy mặt bất đắc dĩ."),i("br"),i("br"),n._v("Cái tên này, bởi vì miệng, đắc tội không ít người, không nghĩ tới nhiều năm không thấy, vẫn giống như trước kia, vẫn như cũ không như vậy lọt tai."),i("br"),i("br"),n._v("Bất quá, đi qua miệng của hắn, cũng biết sơn môn sập, cái này nghe rợn cả người tin tức!"),i("br"),i("br"),n._v("“Kiếm ý, chẳng lẽ là vừa rồi vị kia... Thập Lý Kiếm Thần?”"),i("br"),i("br"),n._v("Trong lòng hơi động."),i("br"),i("br"),n._v("Cái kia cỗ kiếm ý, hắn cũng rõ ràng cảm nhận được, biết bất phàm, thật không nghĩ đến như vậy bất phàm!"),i("br"),i("br"),n._v("Liền sơn môn cự kiếm bên trong khóa lại kiếm ý, đều có thể câu thông..."),i("br"),i("br"),n._v("Đến cùng là ai?"),i("br"),i("br"),n._v("“Các trưởng lão đều đang họp, ta bây giờ đi qua báo nội môn đệ tử, có thể hay không không thích hợp?”"),i("br"),i("br"),n._v("Tất cả mọi người tại thảo luận việc lớn, hắn một cái ngoại môn trưởng lão, đột nhiên xông vào, chỉ là vì báo một cái nội môn đệ tử, dường như không quá phù hợp nên."),i("br"),i("br"),n._v("“Mặc kệ, vẫn là đi vào một chuyến a, Thiện Hiểu Thiên lần đầu tiên kiểm tra, 49 9 m khoảng cách, so với nhiều hạch tâm đệ tử cũng mạnh hơn không biết gấp bao nhiêu lần, quan trọng hắn tu vi hiện tại mới Cổ Thánh nhất trọng... Tất cả trưởng lão đều tại, khẳng định sẽ càng thêm coi trọng, ta được đến ban thưởng, cũng liền càng nhiều!”"),i("br"),i("br"),n._v("Chần chờ một chút, Lục Vân trưởng lão nghiến răng."),i("br"),i("br"),n._v("Trưởng lão nhiều, cũng được, hắn báo chính là thiên tài chân chính, cũng không phải cái gì giả, có lẽ có thể bởi vậy đạt được càng lớn coi trọng!"),i("br"),i("br"),n._v("Lại không rầu rĩ, vội vã hướng trưởng lão điện phương hướng đi tới."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Lục Vân trưởng lão đi trưởng lão điện, Trương Huyền nhìn trước mắt đệ tử, cũng lại kìm nén không được nghi ngờ trong lòng."),i("br"),i("br"),n._v("“Ngươi... Là thế nào đem kiếm ý, làm ra hơn bốn trăm mét?”"),i("br"),i("br"),n._v("Hắn liền một mét cũng chưa tới, cái này nửa vời học sinh nhưng có thể làm được xa như vậy, thực sự không nghĩ ra."),i("br"),i("br"),n._v("“Ta...” Nghe được lão sư hỏi thăm, Thiện Hiểu Thiên một mặt xấu hổ: “Ta dùng lão sư truyền thụ cho kiếm pháp, trực tiếp thanh kiếm ném ra ngoài, cũng không nghĩ tới kiếm ý... Sẽ chạy xa như vậy!”"),i("br"),i("br"),n._v("“Vứt kiếm? Chiêu này kiếm ý, quả thực không kém... Chờ một chút!”"),i("br"),i("br"),n._v("Trương Huyền khóe miệng giật một cái, nhìn lại: “Ngươi nói... Ngươi khảo hạch thời điểm, có kiếm?”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1960"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1962"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);