(window.webpackJsonp=window.webpackJsonp||[]).push([[1084],{1438:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1949-truong-huyen-tau-hoa-nhap-ma"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1949-truong-huyen-tau-hoa-nhap-ma"}},[n._v("#")]),n._v(" Chương 1949: Trương Huyền tẩu hỏa nhập ma")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đương nhiên là có kiếm ah, không có kiếm làm sao thi triển kiếm ý, tiến hành khảo hạch?”"),t("br"),t("br"),n._v("Tiếng nói không có kết thúc, Thiện Hiểu Thiên đột nhiên con mắt trợn tròn, miệng co lại: “Ta sau khi vào phòng, đem kiếm ném đi... Lão sư sẽ không không có kiếm liền khảo nghiệm a?”"),t("br"),t("br"),n._v("Hắn bởi vì thi triển bình thường kiếm pháp, không cách nào phát huy ra mạnh mẽ kiếm ý, cho nên... Đi vào phòng không lâu, liền đem trường kiếm ném đi, bởi vì phía trước không có tường, kiếm cũng không biết bay đến đi đâu, không có cách nào thu về."),t("br"),t("br"),n._v("Lúc đó không muốn nhiều như vậy, lão sư đột nhiên hỏi như thế, để hắn triệt để bối rối..."),t("br"),t("br"),n._v("Không có kiếm làm sao thi triển kiếm ý?"),t("br"),t("br"),n._v("Vậy thì cùng hướng về phía trâu đực vắt sữa, sữa đều không có, làm sao lách vào?"),t("br"),t("br"),n._v("Có thể... Cứ việc như vậy, lão sư đồng dạng có thành tích, làm ra cái hai phần ba mét..."),t("br"),t("br"),n._v("Tương đương với trâu đực, cứ thế mà ôm lấy gần nửa thùng sữa..."),t("br"),t("br"),n._v("Làm sao làm được..."),t("br"),t("br"),n._v("Thiện Hiểu Thiên cho rằng muốn điên rồi."),t("br"),t("br"),n._v("“Khụ khụ...”"),t("br"),t("br"),n._v("Gặp hắn xác nhận, Trương Huyền ngực đau."),t("br"),t("br"),n._v("Lơ là, người khác đều là hố cha, ngươi đây là hố lão sư ah!"),t("br"),t("br"),n._v("Làm sao thu cái như vậy không đáng tin cậy học sinh!"),t("br"),t("br"),n._v("Khó trách làm sao đều không vượt qua được một mét, náo loạn nửa ngày, chuyện như vậy..."),t("br"),t("br"),n._v("“Chuyện này không cần truyền đi... Ta muốn bế quan tu luyện!”"),t("br"),t("br"),n._v("Biết nói tiếp, sư đạo tôn nghiêm không còn sót lại chút gì, Trương Huyền khoát tay áo, không tiếp tục để ý đối phương, xoay người trở về gian phòng của mình."),t("br"),t("br"),n._v("Điều chỉnh một hồi, đem buồn bực trong lòng áp chế xuống, ngón tay một điểm, Thiên đạo chi sách lại xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Lãng phí một quyển sách, ngăn lại một đạo kiếm ý, ngược lại muốn xem xem, đến cùng chuyện gì xảy ra!"),t("br"),t("br"),n._v("Nhẹ nhàng đem sách mở ra, lần nữa nhìn thấy đạo kiếm ý kia ở trong đó trườn, nhẹ nhàng điểm một cái."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một cỗ đối kiếm đạo lý giải, theo đầu ngón tay đi vào mi tâm, trong tích tắc, liền xâm nhập thức hải, tựa hồ muốn đầu của hắn căng nứt, đồng thời, một đạo hoàng chung đại lữ giống như âm thanh, lập tức vang vọng trong óc."),t("br"),t("br"),n._v("“Kiếm đạo, kiếm chi đạo vậy, không thuộc về thiên địa đại đạo phạm trù, mà là con người làm ra sáng lập ra một loại con đường...”"),t("br"),t("br"),n._v("Âm thanh vang lên, Trương Huyền nghe một hồi, lập tức chìm đắm trong đó."),t("br"),t("br"),n._v("Cùng cái này kiếm đạo so sánh, trước hắn tu luyện Thiên đạo kiếm pháp, quả thực chính là trò trẻ con, tiểu vu gặp đại vu."),t("br"),t("br"),n._v("Tuy là không sai, nhưng hắn luyện, nhiều nhất là danh sư đại lục Thiên đạo công nhận không sai, ở trước mắt kiếm pháp này phía trước, trăm ngàn chỗ hở."),t("br"),t("br"),n._v("Liền giống như, ngươi tiểu học học kiến thức, lại chuẩn xác, đến đại học, liền nhất định chính xác ư?"),t("br"),t("br"),n._v("Chưa hẳn!"),t("br"),t("br"),n._v("Newton cơ học, để cho người ta tín ngưỡng ba trăm năm, đồng dạng bị nghi ngờ, như vậy, xuất hiện thuyết tương đối."),t("br"),t("br"),n._v("Cổ đại, trời tròn đất vuông, người người đều cho rằng là chân lý, nhật tâm thuyết phía sau, trở nên buồn cười lên, cho rằng mặt trời mới là trung tâm."),t("br"),t("br"),n._v("Sau đó... Phát hiện nói mặt trời là trung tâm, đồng dạng buồn cười."),t("br"),t("br"),n._v("Kiến thức tại tiến bộ, Thiên đạo cũng là như vậy."),t("br"),t("br"),n._v("Cái gọi là chân lý, trăm năm phía sau, có lẽ chính là cái gọi là chê cười."),t("br"),t("br"),n._v("Mấy trăm năm trước, tất cả mọi người cho rằng, không có khả năng có đồ vật tốc độ, vượt qua âm thanh, giờ phút này đâu?"),t("br"),t("br"),n._v("Danh sư đại lục Thiên đạo công nhận, không có chút nào sai lầm kiếm pháp, tại Thượng Thương, cũng không phải là như vậy."),t("br"),t("br"),n._v("“Khó trách... Ta cho dù tu luyện Thiên đạo công pháp, cũng không cách nào thích ứng Thượng Thương linh khí, nguyên lai... Đối với Thượng Thương Thiên đạo tới nói, ta tu luyện công pháp, cũng không có như vậy chính xác, thậm chí trăm ngàn chỗ hở!”"),t("br"),t("br"),n._v("“Nói như vậy lên, mỗi cái Thiên đạo, đều có thuộc về mình đúng sai, như vậy.. Đến cùng cái gì là thật, cái gì là giả?”"),t("br"),t("br"),n._v("Thân thể chấn động, Trương Huyền có chút mê man."),t("br"),t("br"),n._v("Một quốc gia có một quốc gia luật pháp, ở quốc gia này, có thể là phạm pháp, sẽ bị xử bắn, nhưng mà tại mặt khác quốc gia, làm chuyện giống vậy, khả năng không những không có việc gì, sẽ còn nhận khen ngợi."),t("br"),t("br"),n._v("Thiên đạo chẳng lẽ cũng là như vậy?"),t("br"),t("br"),n._v("Danh sư đại lục Thiên đạo cho rằng chính xác, Thượng Thương Thiên đạo chưa hẳn công nhận?"),t("br"),t("br"),n._v("“Chẳng lẽ, ta tự tu luyện... Đều là sai?”"),t("br"),t("br"),n._v("Trong đầu một hồi đau đớn, Trương Huyền không biết làm sao."),t("br"),t("br"),n._v("Từ khi hai năm trước bắt đầu tu luyện, một mực đối thiên đạo công pháp tin tưởng không nghi ngờ, nội tâm một mực tin tưởng vững chắc, chính xác vô cùng, bây giờ lại đột nhiên phát hiện, chỉ là danh sư đại lục Thiên đạo cho rằng chính xác, cũng không phải là chân chính chính xác..."),t("br"),t("br"),n._v("Để hắn lâm vào khủng hoảng."),t("br"),t("br"),n._v("Trong cơ thể công pháp, vừa định vận chuyển, liền trở nên hoảng hốt, tựa như có cái thanh âm đang không ngừng nhắc nhở: Đây là sai!"),t("br"),t("br"),n._v("Võ kỹ vừa định thi triển, đồng dạng có âm thanh vang lên: Sai, sai, đều là sai!"),t("br"),t("br"),n._v("Con mắt lập tức đỏ lên, trên đầu hơi nước sôi trào, chân khí trong cơ thể, không ngừng lay động, tựa như sắp nổ tung, bất cứ lúc nào cũng sẽ không chịu nổi."),t("br"),t("br"),n._v("Nếu có người thấy cảnh này, tất nhiên sẽ rõ ràng..."),t("br"),t("br"),n._v("Tẩu hỏa nhập ma!"),t("br"),t("br"),n._v("Một mực tu luyện Thiên đạo công pháp Trương Huyền, vậy mà đi tới Thượng Thương, không đến nửa tháng, liền lâm vào tẩu hỏa nhập ma cảnh giới!"),t("br"),t("br"),n._v("Hơn nữa nhìn bộ dáng, bất cứ lúc nào cũng sẽ không chịu nổi, triệt để sụp đổ."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Kèm theo hắn ý niệm không hiểu, toàn bộ Thiên đạo thư viện vậy không ngừng đung đưa, hình như đã trải qua động đất, bất cứ lúc nào cũng sẽ sụp đổ."),t("br"),n._v(" Hắn nghi ngờ chân khí trong cơ thể, chẳng khác nào đang chất vấn Thiên đạo thư viện, nghi ngờ trước kia tất cả."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Thư viện chấn động, thân thể cũng lại không chịu nổi, một ngụm máu tươi phun mạnh."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngay tại hắn nội tức hỗn loạn, chân khí nổi khùng, bất cứ lúc nào cũng sẽ không chịu nổi thời điểm, trên cổ mang theo một cái hồng sắc mặt dây chuyền, đột nhiên tỏa ra ôn nhuận quang mang, đem hắn bao phủ ở bên trong."),t("br"),t("br"),n._v("Bị cỗ này ánh sáng bao phủ, phân loạn tâm cảnh, lập tức trầm ổn xuống, Trương Huyền huyết hồng sắc con mắt, cũng chầm chậm khôi phục như lúc ban đầu."),t("br"),t("br"),n._v("Lay động thư viện, phảng phất cũng bị một loại nào đó cự lực trấn an, triệt để ổn định xuống."),t("br"),t("br"),n._v("Ý thức thanh tỉnh, phảng phất đạt được một loại nào đó hiểu ra, trước đó lo nghĩ vậy nhìn rõ ràng hơn."),t("br"),t("br"),n._v("“Thì ra là thế, cũng không phải là sai lầm, mà là... Có vật tham chiếu, trở nên càng thêm vĩ mô, nhìn vấn đề càng thêm toàn diện...”"),t("br"),t("br"),n._v("Người mù sờ voi, mò tới bắp đùi, nói voi là cây cột, mò tới cái bụng nói là mặt tường, mò tới đuôi là đầu rắn..."),t("br"),t("br"),n._v("Sai lầm rồi sao?"),t("br"),t("br"),n._v("Nhằm vào người tới nói, không sai, nhưng mà chân chính đáp án nhưng là sai."),t("br"),t("br"),n._v("Đây là không cách nào càng vĩ mô đi đối đãi vấn đề."),t("br"),t("br"),n._v("Đem danh sư đại lục Thiên đạo, so sánh Newton cơ học mà nói, Thượng Thương Thiên đạo chính là thuyết tương đối, thích hợp với càng lớn phạm vi, tốt hơn giải thích, cơ học tại vĩ mô vũ trụ cùng vi mô trong vũ trụ sinh ra nghịch lý."),t("br"),t("br"),n._v("Lấy một thí dụ, dựa theo Newton cơ học phân tích, một đoàn tàu lửa lấy một phần ba tốc độ ánh sáng san sẻ tốc độ tiến lên, ngoài xe người thắp sáng nguồn sáng, trong xe người đi nhìn lại, tốc độ ánh sáng nhất định phải tăng tốc, trở nên trước ba phần chi bốn lần."),t("br"),t("br"),n._v("Mà sự thật nhưng là, tốc độ ánh sáng cố định không thay đổi, bất luận tốc độ xe lại nhanh, khảo nghiệm kết quả, đều hoàn toàn tương đồng."),t("br"),t("br"),n._v("Trước đó, chính xác quy luật, vào lúc này cũng đã không thể dùng, nhưng cẩn thận nói đến, đều không có sai lầm, chỉ là... Liên lụy đến càng thêm hùng vĩ thế giới, cùng với càng thâm ảo hơn kiến thức."),t("br"),t("br"),n._v("Như cái này kiếm ý."),t("br"),t("br"),n._v("So với hắn trước đó lĩnh ngộ cao minh không biết bao nhiêu, cũng không phải là trước hắn kiếm pháp phạm sai lầm, mà là... Đạo kiếm ý này, chỗ đứng cao hơn! Nhìn cũng liền rõ ràng hơn."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Hiểu ra trong lòng, đạo kiếm ý này cùng hắn cả người triệt để dung hợp, đối với kiếm đạo lý giải, càng thêm cường đại."),t("br"),t("br"),n._v("Trước đó cái gọi là tốc độ chân giải, tấn công chân giải, phòng ngự chân giải... Như nước chảy trong đầu dung hợp, trở nên lại không phân lẫn nhau."),t("br"),t("br"),n._v("“Khó trách danh sư đại lục, không người có thể đem rất nhiều chân giải, dung hợp lại cùng nhau, bởi vì rất nhiều thuộc tính hoàn toàn khác biệt... Nguyên lai, không phải thiên tư, mà là đứng độ cao quá thấp!”"),t("br"),t("br"),n._v("Đối kiếm đạo lý giải tiến thêm một bước, Trương Huyền giật mình."),t("br"),t("br"),n._v("Trước đó tại danh sư đại lục, cha giương chấn hưng, từng đặc biệt nói qua, lĩnh ngộ hai loại chân giải rất ít người, ba loại, hầu như không tồn tại, chớ nói chi là, có thể đem dung hợp."),t("br"),t("br"),n._v("Trước đó vẫn cho là không làm được, là bởi vì thiên phú, không có đối ứng thư tịch..."),t("br"),t("br"),n._v("Hiện tại xem ra, tại danh sư đại lục, quả thực không hoàn thành được, cùng thiên phú không quan hệ, mà là độ cao chưa đủ!"),t("br"),t("br"),n._v("Newton, có thể phát hiện lực vạn vật hấp dẫn định luật, là bởi vì táo đập ở trên đầu?"),t("br"),t("br"),n._v("Không đúng, là bởi vì, hắn đứng ở cự nhân bả vai, trước mặt hắn có vô số nhà khoa học, đã đem kiến thức tích tụ đến trình độ này, chỉ kém hắn đem cửa đẩy ra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Lĩnh ngộ rất nhiều chân giải ở trong người hoàn mỹ dung hợp, khí tức cả người, nhanh chóng tăng lên, giống như là biến thành một thanh sắc bén trường kiếm, bất luận thân thể, chân khí vẫn là linh hồn đều có to lớn rèn luyện cùng rèn luyện."),t("br"),t("br"),n._v("“Quá tốt rồi, lĩnh ngộ loại kiếm đạo này, ta mới có thể chân chính cùng cấp bậc vô địch!”"),t("br"),t("br"),n._v("Trước đó, Thông Thần điện không có người có thể chặn, là bởi vì, Tinh Diệu thành loại địa phương này, quá mức xa xôi."),t("br"),t("br"),n._v("Đi vào Lăng Vân kiếm các, cảm nhận được sơn môn bên trên thanh cự kiếm kia bên trong ý cảnh mới hiểu được, thực lực cũng không tưởng tượng bên trong mạnh như vậy... Mà giờ khắc này, triệt để đem kiếm đạo chân giải dung hợp, coi như vị kia Thập Lý Kiếm Thần, thật xuất hiện, cùng cấp bậc, cũng chưa chắc có thể là đối thủ!"),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Kiếm ý cùng hắn triệt để dung hợp, bị hắn triệt để lĩnh ngộ, phong ấn Thiên đạo chi sách, khói mỏng đồng dạng thiêu đốt, sau một khắc liền mất đi tung tích."),t("br"),t("br"),n._v("“Không những không chịu thiệt, còn kiếm lời...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền mở mắt ra."),t("br"),t("br"),n._v("Vừa rồi vẫn cảm thấy, Thiên đạo chi sách bị lãng phí, buồn bực muốn chết, chân chính lĩnh ngộ đạo kiếm ý này, tựa như thoáng cái, nhảy tới cự nhân bả vai... Lúc này mới hiểu được, không những không có lãng phí, ngược lại kiếm lợi lớn!"),t("br"),t("br"),n._v("Nếu không, chỉ bằng vào hắn đi lĩnh ngộ, loại cấp bậc này kiếm ý, mười năm, trăm năm, cũng chưa chắc có thể làm được."),t("br"),t("br"),n._v("“Nhược Hi, cám ơn...”"),t("br"),t("br"),n._v("Đem mặt dây chuyền cầm tại lòng bàn tay, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Vừa rồi tẩu hỏa nhập ma, ý thức hỗn loạn, thư viện suýt chút nữa không có chịu đựng lấy, thời khắc mấu chốt, là Nhược Hi đưa tặng mặt dây chuyền bảo vệ một mạng."),t("br"),t("br"),n._v("Đối phương có thể đem Xuân Thu đại điển đặt ở trong thư viện, có thể hay không, đã sớm đoán ra, bản thân sẽ xuất hiện loại tình huống này?"),t("br"),t("br"),n._v("Sớm làm ra dự phán?"),t("br"),t("br"),n._v("Cúi đầu nhìn lại, như thủy tinh mặt dây chuyền bên trong, huyết dịch đồng dạng đỏ tươi, vẫn như cũ sặc sỡ loá mắt, lại không biết vì sao, cảm giác dường như so trước kia ảm đạm một chút."),t("br"),t("br"),n._v("“Ngươi đến cùng thân phận gì?”"),t("br"),t("br"),n._v("Biết thư viện, hơn nữa dự phán đến loại tình huống này, Nhược Hi đến cùng là ai?"),t("br"),t("br"),n._v("“Không thể chậm trễ, nhất định phải tìm tông chủ, trưởng lão các loại khiêu chiến, nhanh lên một chút tìm hiểu Linh Thần Cung tin tức...”"),t("br"),t("br"),n._v("Nghiên cứu phút chốc, Minh Lý Chi Nhãn thư viện, đều đối cái này mặt dây chuyền, không có bất kỳ biện pháp nào, Trương Huyền cũng lại kìm nén không được, đứng dậy đi ra cửa bên ngoài."),t("br"),t("br"),n._v("“Tào Thành Lập, ta cho ngươi nhiệm vụ thứ nhất, đi tìm hiểu thoáng cái, thế nào đạt được Lăng Vân kiếm các thông thần ngọc phù, thuận tiện, làm mấy cái tới...”"),t("br"),t("br"),n._v("Trở lại sân trong, tìm tới cái này chủ động hiệu trung thuộc hạ, Trương Huyền đưa ra nhiệm vụ thứ nhất."),t("br"),t("br"),n._v("(Tấu chương xong)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1961"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1963"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);