(window.webpackJsonp=window.webpackJsonp||[]).push([[1085],{1440:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1950-khong-su-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1950-khong-su-thuong"}},[n._v("#")]),n._v(" Chương 1950: Khổng sư? (Thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trưởng lão điện, kiếm các sở hữu nội môn trở lên trưởng lão, ngồi vây quanh tại gian phòng, bầu không khí ngưng trọng."),t("br"),t("br"),n._v("“Kiếm thư đường, thật vất vả chỉnh lý tốt thư tịch, cảm ứng được kiếm ý, đồng thời xuất hiện triều bái động tác, từ trên giá sách đổ sụp xuống...” Vi trưởng lão nói."),t("br"),t("br"),n._v("“Sơn môn khai phái tổ sư lưu lại cự kiếm, triệt để sụp đổ, dẫn đến sơn môn cũng không chịu nổi...” Vẻ mặt mượt mà lão giả nói."),t("br"),t("br"),n._v("“Kiếm Thần đường ngay tại khảo nghiệm rất nhiều đệ tử, trong cơ thể kiếm ý, tất cả đều bị áp bức, bị thương...” Lại một vị trưởng lão ôm quyền."),t("br"),t("br"),n._v("“Chắc hẳn chư vị, đối chuyện phát sinh, đều có nhất định tìm hiểu...”"),t("br"),t("br"),n._v("Đợi mọi người đem chuyện phát sinh, toàn bộ hồi báo xong, ở chính giữa Hà trưởng lão đứng dậy, nhìn quanh một vòng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("“Ta Lăng Vân kiếm các, từ thành lập đến nay, loại trừ khai phái tổ sư, có thể lĩnh ngộ ‘Thần chi kiếm ý’, còn lại cực kỳ có thiên tư hậu bối, cũng nhiều nhất chỉ có thể để kiếm ý mười dặm! Hiện tại, có người dám ngộ tiên tổ kiếm ý, để sơn môn cự kiếm, không chịu nổi, sở hữu kiếm thư, toàn bộ thần phục... Chư vị, có gì có thể nói?”"),t("br"),t("br"),n._v("Hà trưởng lão nói."),t("br"),t("br"),n._v("“Xuất hiện dạng này thiên tài, là ta Lăng Vân kiếm các chuyện may mắn! Năm đó khai phái tổ sư, đột nhiên xuất hiện, một trận chiến mà vang danh thiên hạ, từ Thần điện, cướp đoạt nửa chữ, như vậy mới có , mới có ‘Kiếm thần’ cái danh xưng này!”"),t("br"),t("br"),n._v("Một vị trưởng lão đứng dậy, tràn đầy kích động: “Mấy ngàn năm qua, hậu bối đều muốn khôi phục vinh quang, đáng tiếc, một mực không có tạo thành, hiện tại có người lĩnh ngộ tiền bối kiếm ý, chứng minh... Ta kiếm các, lần nữa quật khởi, chỉ là vấn đề thời gian!”"),t("br"),t("br"),n._v("“Không sai, sáu đại tông môn, cho dù là có thể câu thông Thần Linh , trong tông môn, cũng không dám dùng ‘Thần’ chữ, duy ta kiếm các có Kiếm Thần đường! Hiện tại, có người đồng dạng lĩnh ngộ ‘Thần chi kiếm ý’, chỉ cần hảo hảo bồi dưỡng, ta kiếm các, có lẽ có thể đem cái này nửa chữ bù đắp, kiếm các, vô cùng có khả năng trở thành Kiếm Thần các, cùng Thông Thần điện, cân bằng!”"),t("br"),t("br"),n._v("“Vì cái này nửa chữ, ta kiếm các vô số hậu bối, anh dũng phấn đấu, hao hết máu tươi, từ đầu đến cuối không có thành công, hiện tại... Đã thấy hi vọng...”"),t("br"),t("br"),n._v("Mấy vị trưởng lão đồng thời đứng dậy, từng cái tràn đầy kích động."),t("br"),t("br"),n._v("“Lĩnh ngộ Thần chi kiếm ý, mới có thể từ Thần điện cướp đoạt còn lại nửa chữ, để tông môn, mang theo ‘Thần’ chữ! Đây là ta kiếm các, sở hữu hậu bối vinh dự!”"),t("br"),t("br"),n._v("Hà trưởng lão gật đầu, nắm đấm không tự chủ được xiết chặt."),t("br"),t("br"),n._v("Di Khí đại lục, là Thần Linh vứt bỏ vị trí, muốn thu hoạch được Thần Linh công nhận, chỉ có một cái phương pháp, đó chính là... Thu hoạch được Thần Linh ban cho chữ!"),t("br"),t("br"),n._v("Lăng Vân kiếm các, là sáu đại tông môn duy nhất được ban cho chữ, chỉ bất quá... Chỉ là nửa cái, không có tư cách đặt ở tông môn phía trên, chỉ có thể đặt ở một cái bình thường kiểm tra cung điện."),t("br"),t("br"),n._v("Hắn đứng tại trước sơn môn, hơn ba mươi năm, chẳng phân biệt được nóng lạnh, chính là vì trải nghiệm tiên tổ ý vị, nắm giữ loại thiên hạ này vô địch kiếm ý."),t("br"),t("br"),n._v("Đáng tiếc, thủy chung không bắt được trọng điểm."),t("br"),t("br"),n._v("Hiện tại có người thành công, cho dù không phải hắn, đối với toàn bộ tông môn tới nói, cũng là chuyện may mắn lớn nhất!"),t("br"),t("br"),n._v("“Truyền lệnh xuống, lập tức tỉ mỉ điều tra, cỗ kiếm ý này, đến cùng là ai lĩnh ngộ! Tra ra người này, lập tức trình báo, không được sai sót!”"),t("br"),t("br"),n._v("Mắt sáng lên, Hà trưởng lão dặn dò: “Mặt khác, chuyện này, thuộc về tuyệt đối cơ mật, ai cũng không thể truyền ra ngoài, như có tiết lộ, coi là phản bội tông môn, gặp phải kiếm các tru sát!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người toàn thân biến sắc."),t("br"),t("br"),n._v("Hà trưởng lão gật đầu: “Tản đi đi, lặng lẽ đi thăm dò là vị nào đệ tử, ai trước điều tra ra, ta sẽ đem trong chuyện này báo tông chủ, để lão nhân gia ông ta, tự thân cấp cho ban thưởng!”"),t("br"),t("br"),n._v("“Tông chủ tự thân ban thưởng?”"),t("br"),t("br"),n._v("Mọi người con mắt lập tức đỏ lên."),t("br"),t("br"),n._v("Tông chủ thực lực sâu không lường được, hắn tự thân ban thưởng, chỗ tốt lớn, không cần nghĩ đều biết."),t("br"),t("br"),n._v("Mọi người ở đây tràn đầy kích động thời điểm, một giọng nói vang lên."),t("br"),t("br"),n._v("“Hà trưởng lão, ngoại môn trưởng lão, Lục Vân cầu kiến!”"),t("br"),t("br"),n._v("“Lục Vân?”"),t("br"),t("br"),n._v("Hà trưởng lão nhíu nhíu mày."),t("br"),t("br"),n._v("Lăng Vân kiếm các trưởng lão phần đông, ngoại môn trưởng lão địa vị quá thấp, cơ bản không có gặp mặt tư cách."),t("br"),t("br"),n._v("“Chuyện gì?”"),t("br"),t("br"),n._v("“Hắn nói... Phát hiện một vị kiếm đạo thiên tài, chuyên tới để báo, hỗ trợ xin, nội môn đệ tử danh ngạch!”"),t("br"),t("br"),n._v("Thanh âm nói."),t("br"),t("br"),n._v("“Kiếm đạo thiên tài?” Hà trưởng lão lông mày giương lên: “Để hắn đi vào!”"),t("br"),t("br"),n._v("Để mọi người tới họp mục đích, chính là vì tìm kiếm vị kia lĩnh ngộ “Thần chi kiếm ý” siêu cấp thiên tài, vị này đột nhiên xuất hiện, nói phát hiện một vị, lập tức tràn đầy kích động."),t("br"),t("br"),n._v("Rất nhanh, Lục Vân trưởng lão, đi tới trước mặt."),t("br"),t("br"),n._v("“Ngươi nói kiếm đạo thiên tài là có ý gì?”"),t("br"),t("br"),n._v("Hà trưởng lão nhìn qua."),t("br"),t("br"),n._v("“Đúng như vậy, ta chịu trách nhiệm ngoại môn thế hệ sau cùng tạp dịch đệ tử thu nhận, đi ngang qua Tinh Diệu thành thời điểm...”"),t("br"),t("br"),n._v("Lục Vân trưởng lão đem tất cả mọi chuyện, tất cả đều nói một lần, bao quát vị kia quét ngang tất cả mọi người “Thiên Nhai”, cùng với Thiện Hiểu Thiên nhẹ nhõm đánh bại Hoắc Giang Hà, hơn nữa để kiếm ý, lan ra đến 49 9 m."),t("br"),t("br"),n._v("“Đây là hắn tại Kiếm Thần đường lưu lại ngọc phù...”"),t("br"),t("br"),n._v("“Mới Cổ Thánh nhất trọng, liền có thể đem kiếm ý, lan ra tiếp cận một dặm?”"),t("br"),t("br"),n._v("Hà trưởng lão chấn động."),t("br"),t("br"),n._v("Không chỉ có là hắn, bên trong đại sảnh sở hữu trưởng lão, cũng tất cả đều trợn mắt lên, tràn đầy khó tin."),t("br"),n._v(" Kiếm đạo ý cảnh, lĩnh ngộ cao thâm hay không, cùng người thiên phú, cùng với năng lực liên quan đến, nhưng không có siêu cường thực lực, khẳng định cũng không cách nào đem kiếm ý khóa ở trong người... Một cái Cổ Thánh nhất trọng tiểu nhân vật, đối kiếm ý lĩnh ngộ, tiếp cận một dặm..."),t("br"),t("br"),n._v("So nhiều hạch tâm đệ tử đều cường đại hơn, thật là đáng sợ đi!"),t("br"),t("br"),n._v("“Có phải hay không là hắn?”"),t("br"),t("br"),n._v("Vẻ mặt mượt mà lão giả nhìn lại."),t("br"),t("br"),n._v("“Cổ Thánh nhất trọng, mười bảy tuổi, phải không cách nào lĩnh ngộ Thần chi kiếm ý, coi như có thể lĩnh ngộ, cũng không chịu nổi cường đại như thế lực lượng... Nhưng, không thể không thừa nhận, là cái tuyệt đỉnh thiên tài!”"),t("br"),t("br"),n._v("Hà trưởng lão gật đầu."),t("br"),t("br"),n._v("Thần chi kiếm ý, hắn loại cảnh giới này, cũng chưa chắc có thể thừa nhận được, đối phương một cái mười bảy tuổi Cổ Thánh nhất trọng thiếu niên, thế nào làm được?"),t("br"),t("br"),n._v("Hẳn không phải là."),t("br"),t("br"),n._v("“Nếu là thiên tài, ta kiếm các sẽ không bạc đãi, vị này Thiện Hiểu Thiên, lập tức trở thành nội môn đệ tử, hơn nữa lấy hạch tâm đệ tử đãi ngộ đối đãi, đối đãi hắn đi đến Hư Tiên cảnh, có thể không thông qua khảo hạch, trực tiếp tấn thăng!”"),t("br"),t("br"),n._v("Dừng lại một chút, Hà trưởng lão nói: “Lục Vân trưởng lão vì tông môn phát hiện nhân tài như vậy, không thể bỏ qua công lao, ban tặng Chân Tiên đan một cái, Ngô trưởng lão, Bạch trưởng lão hai vị hộ pháp, trợ giúp xung kích Chân Tiên cảnh! Thành công, lập tức tấn cấp nội môn trưởng lão!”"),t("br"),t("br"),n._v("“Đa tạ Hà trưởng lão...”"),t("br"),t("br"),n._v("Nghe được to lớn như vậy ban thưởng, cũng nhịn không được nữa, Lục Vân trưởng lão lập tức quỳ rạp xuống đất."),t("br"),t("br"),n._v("Chân Tiên đan, là tông môn quý báu nhất đan dược một trong, mỗi một viên, đều trân quý vô cùng, không phải dùng tiền liền có thể mua sắm."),t("br"),t("br"),n._v("Hư Tiên cảnh cường giả đỉnh phong, dùng một cái, có thể gia tăng hai mươi phần trăm đột phá cơ hội, tu vi của hắn đã sớm đi đến phần cuối, nếu có thứ này phụ trợ, đột phá tỷ lệ tuyệt đối gia tăng thật lớn!"),t("br"),t("br"),n._v("Huống chi... Còn có hai vị trưởng lão hộ pháp!"),t("br"),t("br"),n._v("Có thể nói, thực lực tăng vọt, trở thành nội môn trưởng lão, đã không còn bất kỳ khó khăn!"),t("br"),t("br"),n._v("“Có công liền thưởng, có tội tất phạt, đây là kiếm các quy củ, không cần khách khí! Đợi ngươi trở thành nội môn trưởng lão, vị này Thiện Hiểu Thiên, vẫn như cũ từ ngươi liên hệ cùng tiếp xúc, lại có cái gì đặc thù, lập tức trình báo!”"),t("br"),t("br"),n._v("Hà trưởng lão bàn giao."),t("br"),t("br"),n._v("“Vâng!” Lục Vân giương tới tràn đầy hưng phấn mà lui xuống."),t("br"),t("br"),n._v("“Ừm!” Gặp hắn rời đi, Hà trưởng lão lần nữa nhìn quanh: “Tốt, chư vị tất cả giải tán đi, ghi nhớ ta lời mới vừa nói, chỉ cần tìm được vị kia thiên tài, ban thưởng tuyệt đối so với cho Lục trưởng lão còn muốn phong phú!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người đồng loạt lui ra ngoài."),t("br"),t("br"),n._v("Đối với mọi người tới nói, Thiện Hiểu Thiên cứ việc thiên tài, có thể Lăng Vân kiếm các, không thiếu nhất chính là loại người này, hạch tâm đệ tử bên trong, vượt qua một dặm, hai dặm đều có khối người."),t("br"),t("br"),n._v("Tuy là khiếp sợ, nhưng cũng không có dẫn tới quá lớn chú ý."),t("br"),t("br"),n._v("Mọi người đều rời đi, rất nhanh gian phòng chỉ còn lại, Hà trưởng lão cùng khuôn mặt mượt mà lão giả."),t("br"),t("br"),n._v("“Ngươi muốn đưa tin cho tông chủ?”"),t("br"),t("br"),n._v("Mượt mà lão giả nhìn qua."),t("br"),t("br"),n._v("“Chuyện này, can hệ trọng đại, nhất định phải nhanh báo tin tông chủ...” Hà trưởng lão gật đầu."),t("br"),t("br"),n._v("“Tông chủ hiện tại đang cùng những tông môn khác tông chủ cùng một chỗ, thảo luận trăm năm một lần ! Lúc này đưa tin, chẳng lẽ quấy rầy đi...”"),t("br"),t("br"),n._v("Mượt mà trưởng lão có chút lo lắng."),t("br"),t("br"),n._v("“Hẳn là sẽ không... Loại sự tình này, chúng ta không truyền đi qua, những tông môn khác khẳng định cũng sẽ truyền lại, đợi người khác đuổi tới phía trước, tông chủ không biết, liền phiền toái!” Hà trưởng lão gật gật đầu, không nói thêm lời, lấy ra một cái đặc thù ngọc bài nhẹ nhàng điểm một cái, một đạo tin tức lập tức in lên, tựa như thiêu đốt đồng dạng, biến mất ở trước mắt."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một cái không biết cao bao nhiêu trên ngọn núi, mây trắng lượn lờ, tiên hạc bay lượn, thương tùng phía dưới vài bóng người ngồi ngay ngắn thành một vòng tròn."),t("br"),t("br"),n._v("Dòng nước vang lên, bốn phía mùi thơm vây quanh, từ xa nhìn lại, như là tiên cảnh."),t("br"),t("br"),n._v("“Ta cảm thấy dựa theo trước kia quy luật là được, không cần thiết lần nữa chơi đùa!”"),t("br"),t("br"),n._v("Một cái dung mạo mỹ lệ nữ tử, nâng ly trà lên, thản nhiên nói."),t("br"),t("br"),n._v("“Trước kia quy luật, là rất không tệ, cũng rất hợp lý, nhưng... Nhiều năm như vậy chưa hề thành công qua, tiếp tục sử dụng, ta sợ cùng giải quyết dạng giẫm lên vết xe đổ!”"),t("br"),t("br"),n._v("Một vị lão giả nói."),t("br"),t("br"),n._v("“Không dựa theo trước kia đi, các ngươi có biện pháp gì tốt? Không ngại nói ra nghe một chút!”"),t("br"),t("br"),n._v("Nữ tử cười nhạo một tiếng."),t("br"),t("br"),n._v("“Đỗ Thanh Diên, các ngươi Trục Tinh cung, là sáu đại tông môn duy nhất có thể câu thông Thần điện vị trí, các ngươi phải đối hắn hiểu rõ nhất, mọi người tập hợp ở đây, thật ra thì chính là muốn nghe ngươi ý tứ, ngươi là không muốn nói, vẫn là muốn ở lại chỗ này, một điểm đường ra đều không muốn? Nếu như ta tính toán không sai, tuổi thọ của ngươi, hẳn là cũng sắp đi đến cực hạn đi!”"),t("br"),t("br"),n._v("Đối diện một người trung niên nhíu nhíu mày."),t("br"),t("br"),n._v("“Chúng ta là có thể câu thông Thần điện, nhưng các ngươi phải rõ ràng, Thần điện là dạng gì tồn tại, có thể câu thông không có nghĩa là tìm hiểu... Mặt khác, ta tuổi thọ là nhanh đến cực hạn, nhưng... Nếu như ngươi cho rằng, ta Trục Tinh cung nhất định phải có biện pháp, muốn bắt nạt thoáng cái, không ngại ngươi ta chiến đấu một hồi! Người nào thắng, lại nói tiếp!”"),t("br"),t("br"),n._v("Nữ tử Đỗ Thanh Diên tú mi hơi giương, nhìn lại."),t("br"),t("br"),n._v("“Tốt, mọi người tập hợp một chỗ là thảo luận chuyện, không cần cãi lộn!”"),t("br"),t("br"),n._v("Trước đó lão giả nói chuyện, lắc đầu: “Tần môn chủ, ngươi nói ít đi một câu. Đỗ cung chủ, ngươi cũng biết, Tần môn chủ nói chuyện từ trước tới nay như vậy, không nên tức giận! Chuyện này, chúng ta như vậy thảo luận, khẳng định không có kết quả... Ta vừa rồi đã liên hệ Thông Thần điện Khổng điện chủ, không có gì bất ngờ xảy ra, phải rất nhanh cũng sẽ tới...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, một người mặc trường bào, khuôn mặt nho nhã trung niên nhân, bất ngờ xuất hiện ở trước mặt mọi người, trên mặt lộ ra nụ cười thản nhiên."),t("br"),t("br"),n._v("“Nhìn tới vẫn là Hàn huynh hiểu ta...”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1962"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1964"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);