(window.webpackJsonp=window.webpackJsonp||[]).push([[1086],{1442:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1951-khong-su-ha"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1951-khong-su-ha"}},[n._v("#")]),n._v(" Chương 1951: Khổng sư? (Hạ)")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Nhìn thấy trước mắt vị này, mọi người tất cả đều đồng loạt đứng dậy."),i("br"),i("br"),n._v("Bọn họ tuy là thân là sáu đại tông môn môn chủ, nhưng cùng trước mắt vị này so, hay là kém một đoạn dài."),i("br"),i("br"),n._v("Sáu đại tông môn, như là sáu cái to lớn quốc gia, từng người khống chế một phương, nhưng Thông Thần điện nhưng trải rộng thiên hạ, nơi có người, liền có Thông Thần điện, thông thần ngọc phù..."),i("br"),i("br"),n._v("Cứ việc không tranh tấc đất, trên thực tế nhưng nắm trong tay thiên hạ."),i("br"),i("br"),n._v("Huống chi, người trước mắt này thực lực, rốt cục mạnh đến mức nào, không có người biết."),i("br"),i("br"),n._v("Bình thường tu luyện giả, đều có tuổi thọ hạn chế, trước mắt vị này, nhưng thủy chung sẽ không thay đổi lần trước giống như, vài ngàn năm trước, liền uy danh hiển hách, thiên hạ không ai không biết, hiện tại dung nhan, vậy mà không thay đổi chút nào."),i("br"),i("br"),n._v("Cho dù mấy người sớm đã đứng tại đại lục đỉnh phong nhất, vẫn như cũ mang trong lòng tôn kính cùng kiêng kị."),i("br"),i("br"),n._v("“Gặp qua Khổng điện chủ!”"),i("br"),i("br"),n._v("Mấy người ôm quyền."),i("br"),i("br"),n._v("“Không cần khách khí!” Thông Thần điện điện chủ cười cười, ngồi xuống."),i("br"),i("br"),n._v("“Lần này phiền phức điện chủ tới, chính là muốn thương thảo thoáng cái, trăm năm một lần Thông Thiên kiều, nên làm như thế nào, tiếp tục tuân thủ trước kia ước định đây, hay là đánh vỡ quy luật...”"),i("br"),i("br"),n._v("Được xưng “Hàn huynh” lão giả, mở miệng nói."),i("br"),i("br"),n._v("“Đúng vậy a, điện chủ hơn bốn ngàn năm trước, lẻ loi một mình, xông qua Thông Thiên kiều, đánh bại Thần điện cường giả, một mình đạt được một chữ, sáng lập Thông Thần điện, nên đối Thần điện cực kỳ thấu hiểu, ngươi có thể đến cho chúng ta giải thích nghi hoặc, không thể thích hợp hơn!”"),i("br"),i("br"),n._v("Vừa rồi Tần môn chủ nói."),i("br"),i("br"),n._v("“Ta là từng tiến vào Thần điện, nhưng trong đó đáng sợ, không phải một lời hai loiwf liền có thể miêu tả, Thông Thiên kiều, trăm năm xuất hiện một lần, nhiều năm như vậy đều không có thành công, cũng nên biến thoáng cái...”"),i("br"),i("br"),n._v("Thông Thần điện điện chủ cười cười, vừa định tiếp tục nói chuyện, chỉ thấy “Hàn huynh” cổ tay khẽ đảo, một cái ngọc phù xuất hiện tại lòng bàn tay."),i("br"),i("br"),n._v("Cúi đầu liếc mắt nhìn, Hàn huynh con mắt nhịn không được sáng lên."),i("br"),i("br"),n._v("“Làm sao vậy, Hàn huynh?” Tần môn chủ nhìn qua."),i("br"),i("br"),n._v("“Là trong tông môn người báo, nói kiếm các xuất hiện một vị thiên tài, lĩnh ngộ tiên tổ lưu lại kiếm ý!” Hàn huynh cười nói."),i("br"),i("br"),n._v("“Tiên tổ kiếm ý? Chẳng lẽ là... Thần chi kiếm ý?”"),i("br"),i("br"),n._v("Thông Thần điện điện chủ sững sờ."),i("br"),i("br"),n._v("“Đúng!” Hàn huynh không có không thừa nhận, mà là gật gật đầu."),i("br"),i("br"),n._v("“Thần chi kiếm ý, nghe nói Thần Linh mới có thể lĩnh ngộ cùng thi triển, lĩnh ngộ loại này... Về sau nhất định bất phàm! Không biết... Hàn huynh vị này môn nhân, họ gì tên gì?”"),i("br"),i("br"),n._v("Mắt sáng lên, Thông Thần điện điện chủ cười nhìn qua."),i("br"),i("br"),n._v("“Cái này... Ta cũng không rõ lắm, thuộc hạ người cũng chưa điều tra ra, chỉ là tiên tổ kiếm ý, xuất hiện cộng hưởng!” Hàn huynh gật đầu."),i("br"),i("br"),n._v("“Ah!” Thông Thần điện điện chủ cười cười, không tại tiếp tục truy vấn: “Đã xuất hiện người tài giỏi như thế, Thông Thiên kiều, có lẽ thật muốn thay đổi thoáng cái, bất quá nếu như thay đổi, còn cần Hàn huynh đem vị thiên tài này tìm tới lại nói, xác thực biết là ai, nắm giữ dạng gì năng lực cùng thực lực, mới có thể kế hoạch tốt hơn!”"),i("br"),i("br"),n._v("“Cũng phải...”"),i("br"),i("br"),n._v("Mọi người đồng thời gật đầu."),i("br"),i("br"),n._v("“Vậy thì tốt, liền như thế kết thúc, lần sau lại tụ họp đi!”"),i("br"),i("br"),n._v("Thông Thần điện chủ nói."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Thảo luận cố định, mọi người không nói thêm lời, đều rời đi, Hàn huynh vừa bay xa, liền cảm thấy sau lưng tiếng gió rít gào, Thông Thần điện chủ ngăn tại phía trước."),i("br"),i("br"),n._v("“Khổng điện chủ!”"),i("br"),i("br"),n._v("Hàn huynh vội vàng ôm quyền."),i("br"),i("br"),n._v("“Hàn huynh đừng có gấp, có thể lĩnh ngộ Thần chi kiếm ý người, về sau nhất định đối Thông Thiên kiều có cực kỳ trọng yếu ảnh hưởng, mặc dù không biết bây giờ thực lực thế nào, nhưng thân là trưởng bối, đã biết, dìu dắt thoáng cái, ắt không thể thiếu... Đây là đại biểu thân phận ta lệnh bài, nếu như tìm tới người này, xin ban cho hắn, có thể tại Thông Thần điện mua sắm tùy ý tấn cấp tu vi bảo vật, không cần dùng tiền!”"),i("br"),i("br"),n._v("Ngón tay một điểm, một cái ngọc phù xuất hiện tại phía trước, Thông Thần điện chủ mỉm cười."),i("br"),i("br"),n._v("“Đa tạ Khổng điện chủ!”"),i("br"),i("br"),n._v("Hàn huynh ánh mắt sáng lên."),i("br"),i("br"),n._v("Thông Thần điện dày đặc thiên hạ, cái này viên ngọc bài có thể tùy ý mua sắm tấn cấp tu vi đồ vật không cần dùng tiền, giá trị lớn, không cho phép đánh giá!"),i("br"),i("br"),n._v("Có thể nói, nắm giữ cái này ngọc phù, chẳng khác nào nắm giữ thiên hạ lớn nhất của cải, tu luyện không cần tiếp tục lo lắng!"),i("br"),i("br"),n._v("Ân tình thực sự quá lớn!"),i("br"),i("br"),n._v("“Khách khí! Ngươi hẳn là cũng biết, Thông Thần điện tồn tại ý nghĩa, chính là vì để tu luyện giả càng thêm cường đại, loại thiên tài này xuất hiện, ta tự sẽ ra tay trợ giúp...”"),i("br"),i("br"),n._v("Thông Thần điện chủ cười cười, trên mặt lộ ra ôn hoà chi sắc."),i("br"),i("br"),n._v("“Khổng điện chủ đại nghĩa, ta một mực lòng sinh khâm phục! Nếu như không có chuyện gì khác, trước hết cáo từ...”"),i("br"),i("br"),n._v("Hàn huynh nói xong, thân thể run rẩy, biến mất tại nguyên chỗ."),i("br"),i("br"),n._v("Nhìn hắn rời đi, Thông Thần điện điện chủ, hai tay chắp sau lưng, ánh mắt thiểm thước, không biết nghĩ cái gì."),i("br"),i("br"),n._v("Một lát sau, ngẩng đầu nhìn về phía bầu trời, giống như là đang cùng người khác nói chuyện, lại giống là tự lẩm bẩm: “Nhiều năm như vậy, ngươi rốt cục xuất hiện, hơn nữa... Loại bỏ không gian bích chướng, đến nơi này... Nhanh lên một chút xuất hiện đi, ta đã phải đợi quá lâu...”"),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Cảm xúc hết, Thông Thần điện chủ biến mất không thấy gì nữa."),i("br"),i("br"),n._v("Nếu như Trương Huyền tại đây, nhất định có thể nhận ra, vị điện chủ này dung mạo, cùng hắn thấy qua Khổng sư, giống nhau như đúc, không có chút nào khác biệt."),i("br"),i("br"),n._v("Chẳng lẽ vài ngàn năm trước, Khổng sư đi tới Thượng Thương, một mực tại cái này?"),i("br"),i("br"),n._v("Đã như vậy, vì sao không trở về danh sư đại lục?"),i("br"),i("br"),n._v("Thậm chí liền một chút tin tức đều không có?"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Trưởng lão điện cùng trên ngọn núi phát sinh tất cả, Trương Huyền đều không biết, giờ phút này đang nhìn trước mắt Tào Thành Lập."),i("br"),i("br"),n._v("Phái cái tên này đi tìm hiểu tin tức, kết quả, cái này hai hàng, vậy mà sưng mặt sưng mũi trở về, nếu không phải trước đó ban cho một cỗ Thiên đạo chân khí, khả năng cũng đã chết rồi."),i("br"),i("br"),n._v("“Liền hỏi thăm cái tin tức, tại sao có thể như vậy?”"),i("br"),i("br"),n._v("Kìm nén không được."),i("br"),i("br"),n._v("Trước đó quản gia Tôn Cường, chỉ cần ra ngoài làm việc, khẳng định có thể hoàn thành, tuy là có đôi khi ưa thích trang bức, không quá đáng tin cậy, có thể chí ít không có ra ngoài không đến nửa canh giờ bị người đánh thành như vậy ah!"),i("br"),i("br"),n._v("“Cái này... Ta...” Tào Thành Lập đầy mặt xấu hổ."),i("br"),n._v("Rất nhanh nghe rõ đối phương giải thích, Trương Huyền không còn gì để nói."),i("br"),i("br"),n._v("Cái tên này sơn tặc xuất thân, đến chết không đổi, tìm người hỏi thăm tin tức liền hỏi thăm, kết quả nhìn thấy một vị tạp dịch đệ tử hết sức xinh đẹp, liền tràn đầy phấn khởi đi qua đùa giỡn..."),i("br"),i("br"),n._v("Sau đó, có thể tưởng tượng được, không có bị tại chỗ đánh chết là tốt lắm rồi."),i("br"),i("br"),n._v("“Chữ sắc một cây đao, nơi này là Lăng Vân kiếm các, không phải Huyền Giang thành cái loại này địa phương nhỏ...” Trương Huyền im lặng."),i("br"),i("br"),n._v("Cái này hai hàng lá gan cũng không tránh khỏi quá lớn!"),i("br"),i("br"),n._v("Lăng Vân kiếm các tạp dịch đệ tử, ít nhất cũng phải Đại Thánh tam trọng thực lực, liền như ngươi loại này thực lực, cũng dám đùa giỡn... Thật sự là không biết sống chết."),i("br"),i("br"),n._v("Nếu không phải người khác biết, hắn là Thiện Hiểu Thiên người, đoán chừng hiện tại đã có thể nhặt xác."),i("br"),i("br"),n._v("“Có thể thiếu gia... Ngươi cũng biết ta, liền tốt cái này một cái, cái này đều nhẫn nhịn hơn mười ngày...”"),i("br"),i("br"),n._v("Tào Thành Lập hơi đỏ mặt."),i("br"),i("br"),n._v("Hắn lúc trước đi thanh lâu có thể thoáng cái tìm bảy cái, chứng minh “Rất mạnh”, trở thành sơn tặc phía sau, càng mỗi ngày không gái không vui... Cứ việc tìm đều là chút gái lầu xanh, hoặc là cam tâm tình nguyện, thế nhưng không có nhàn rỗi..."),i("br"),i("br"),n._v("Cùng thiếu gia đi tới nơi này, trên đường mười ngày, đã sớm kìm nén hung ác."),i("br"),i("br"),n._v("Vốn cho rằng, dựa vào hắn bộ mặt gặp, có thể dụ dỗ một cái trở về, ai biết mới mở miệng liền bị đánh... Không nói ra được đau buồn."),i("br"),i("br"),n._v("Thấy cái tên này hùng hồn, Trương Huyền che cái trán."),i("br"),i("br"),n._v("Thiện Hiểu Thiên nói rất đúng, lúc trước liền không nên thu cái tên này..."),i("br"),i("br"),n._v("Mất mặt ah!"),i("br"),i("br"),n._v("“Ngậm miệng, để ngươi làm sự tình thế nào rồi?”"),i("br"),i("br"),n._v("“Cái này... Bẩm báo thiếu gia, ta đã nghe ngóng, Lăng Vân kiếm các thông thần ngọc phù, tại nội môn đệ tử bản thân thị trường giao dịch liền có bán ra, mỗi một viên giá cả đều tại hai mươi kiếm các tệ trái phải!”"),i("br"),i("br"),n._v("Thấy thiếu gia tức giận, Tào Thành Lập không dám nói nhiều, xấu hổ gãi đầu một cái, nói."),i("br"),i("br"),n._v("“Hai mươi kiếm các tệ?” Trương Huyền cau mày."),i("br"),i("br"),n._v("Thoạt nhìn số lượng không lớn, nhưng... Kiếm các tệ mua sắm giá trị, hắn cũng không rõ ràng."),i("br"),i("br"),n._v("“Một cái hạ phẩm Tiên Nguyên đan, đại khái giá trị bao nhiêu kiếm các tệ?”"),i("br"),i("br"),n._v("Nhịn không được hỏi."),i("br"),i("br"),n._v("“Cái này... Ta còn không có hỏi thăm!” Tào Thành Lập xấu hổ cười một tiếng."),i("br"),i("br"),n._v("“Thị trường giao dịch ở đâu?” Trương Huyền tiếp tục hỏi."),i("br"),i("br"),n._v("“Cái này... Ta cũng không có hỏi thăm!” Tào Thành Lập khóe miệng co quắp nhúc nhích một chút."),i("br"),i("br"),n._v("“Vậy ngươi hỏi thăm cái gì?” Trương Huyền sắc mặt dần dần xanh lại."),i("br"),i("br"),n._v("“Vừa rồi đánh ta cái cô nương kia, là một vị kêu Lưu Lộ Kiệt nội môn đệ tử thuộc hạ, người cao 1,70 m, ba vòng 85, 74, 88, chân ít nhất một mét, vừa mảnh lại trắng, quan trọng còn thẳng... Thiếu gia, ta nói cho ngươi, ta duyệt nữ vô số, đây là ta gặp qua tốt nhất chân, không có cái thứ hai...”"),i("br"),i("br"),n._v("Tào Thành Lập con mắt sáng lên."),i("br"),i("br"),n._v("“Đủ rồi...”"),i("br"),i("br"),n._v("Trương Huyền suýt chút nữa nổ tung."),i("br"),i("br"),n._v("Cái gì ngoạn ý nha..."),i("br"),i("br"),n._v("Để hắn hỏi thăm chính sự, một chút cũng không có làm, cái đồ chơi này ngược lại làm rất rõ ràng..."),i("br"),i("br"),n._v("Em gái ngươi ah..."),i("br"),i("br"),n._v("Ghi nhớ thân phận, tới là tới tu luyện, hầu hạ thiếu gia, không phải tán gái!"),i("br"),i("br"),n._v("“Hiện tại ra ngoài, tìm kiếm nội môn đệ tử giao dịch địa phương, tìm không thấy, ta liền đem ngươi thiến!”"),i("br"),i("br"),n._v("Hừ lạnh một tiếng, Trương Huyền cong ngón tay búng một cái, một đạo kiếm khí từ ngón tay bay ra, trong không khí không ngừng xé rách."),i("br"),i("br"),n._v("“Vâng...”"),i("br"),i("br"),n._v("Cảm nhận được kiếm khí, mang theo lạnh lẽo thấu xương, không ngừng tại hắn dưới hông loạn chuyển, Tào Thành Lập sợ tới mức sắc mặt trắng nhợt, vội vàng kẹp chặt hai chân."),i("br"),i("br"),n._v("“Buổi tối hôm nay, còn tìm không thấy... Ta liền đi tìm một con ngựa thuần phục, để nó đem ngươi cưỡng gian!”"),i("br"),i("br"),n._v("Trương Huyền nói tiếp."),i("br"),i("br"),n._v("Tào Thành Lập hoa cúc xiết chặt, mồ hôi lạnh ứa ra."),i("br"),i("br"),n._v("Chính mình cái này chủ nhân thoạt nhìn, rất nghiêm chỉnh, làm sao một khi quyết tâm... Gay gắt như vậy?"),i("br"),i("br"),n._v("Thuần phục ngựa đối với hắn làm bạo..."),i("br"),i("br"),n._v("Nghĩ tới, đêm hôm đó, đầu kia biết võ công, biết bưng trà rót nước ngựa... Thật muốn đối với hắn làm bạo, khả năng thật đúng là chống lại không được!"),i("br"),i("br"),n._v("Tuyệt không thể như vậy!"),i("br"),i("br"),n._v("“Ta lập tức liền đi...”"),i("br"),i("br"),n._v("Không dám tiếp tục nói nhảm, xoay người bỏ chạy ra ngoài."),i("br"),i("br"),n._v("Lần này có uy hiếp, thời gian không dài liền đem tin tức hỏi thăm rõ ràng."),i("br"),i("br"),n._v("“Thiếu gia, nội môn đệ tử giao dịch nơi, ngay tại ngọn núi này dưới chân, hạ phẩm Tiên Nguyên đan giá cả, ta cũng đã hỏi, một cái đại khái hai cái kiếm các tệ!”"),i("br"),i("br"),n._v("Tào Thành Lập nói."),i("br"),i("br"),n._v("“Tiên Nguyên đan hai cái kiếm các tệ? Thông thần ngọc phù... Muốn hai mươi viên? Cái này... Giá cả khác biệt thực sự quá lớn đi!”"),i("br"),i("br"),n._v("Trương Huyền ngây người."),i("br"),i("br"),n._v("Tiên Nguyên đan, Tinh Diệu thành Thông Thần điện, mười vạn thông thần tệ một cái, thông thần ngọc phù, mới hai vạn!"),i("br"),i("br"),n._v("Đến nơi này, ngọc phù hai mươi, Tiên Nguyên đan mới hai cái... Chênh lệch gấp mười lần."),i("br"),i("br"),n._v("Nói cách khác, thứ nhất vừa đi, đồng dạng Tiên Nguyên đan, giá cả kém trọn năm mươi lần... Thật hay giả!"),i("br"),i("br"),n._v("“Ta cũng không biết chuyện gì xảy ra, dù sao ta hỏi thăm giá cả, chính là như vậy...” Tào Thành Lập vội vàng giải thích, đồng thời kẹp chặt hai chân, sợ vị thiếu chủ này một không vui vẻ, giúp hắn cắt."),i("br"),i("br"),n._v("“Mang ta đi nội môn đệ tử nơi giao dịch tại, ta muốn đích thân đi xem một chút!”"),i("br"),i("br"),n._v("Biết ở đây nói nhiều như vậy đều không có cái gì dùng, Trương Huyền đứng dậy, nói một tiếng, hướng ra phía ngoài đi ra ngoài."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1963"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1965"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);