(window.webpackJsonp=window.webpackJsonp||[]).push([[1087],{1441:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1952-ban-nuoc-tam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1952-ban-nuoc-tam"}},[n._v("#")]),n._v(" Chương 1952: Bán nước tắm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nội môn đệ tử chợ giao dịch, là nội môn đệ tử, tự phát hình thành một cái bên trong tổ chức, có thể ở đây bán ra cùng mua sắm, tu luyện cần thiết bảo vật, mười phần thuận tiện."),t("br"),t("br"),n._v("Tại Tào Thành Lập dẫn dắt bên dưới, rất nhanh tới đến thẳng địa phương."),t("br"),t("br"),n._v("Thoạt nhìn mười phần đơn sơ, chỉ là dưới núi một cái không lớn sân nhỏ, đưa ra lệnh bài, tiến vào bên trong, ngay sau đó nhìn thấy không ít quầy hàng bày ở trên mặt đất, lẻ tà lẻ tẻ bày đặt một ít vật tư."),t("br"),t("br"),n._v("Trường kiếm, công pháp bí tịch, thiên tài địa bảo, đan dược sủng vật... Cái gì cần có đều có!"),t("br"),t("br"),n._v("Bất quá, những này cũng không phải tông môn, mà là nội môn đệ tử thí luyện, hoặc là gia tộc đồ vật."),t("br"),t("br"),n._v("“Chúng ta nơi này, chỉ lấy kiếm các tệ, không nhận Tiên Nguyên đan...”"),t("br"),t("br"),n._v("Hỏi mấy cái quầy hàng, Trương Huyền đạt được tương đồng trả lời."),t("br"),t("br"),n._v("Đối phương bán ra những bảo bối này mục đích, chính là vì đi vào kiếm thư đường, thử kiếm đường... Học tập cao minh hơn kiếm thuật, mà muốn đi vào những địa phương này, chỉ có kiếm các tệ mới được."),t("br"),t("br"),n._v("“Không có kiếm các tệ làm sao bây giờ?”"),t("br"),t("br"),n._v("Trương Huyền một hồi buồn bực."),t("br"),t("br"),n._v("Vốn cho rằng, Tào Thành Lập cống hiến mấy cái Tiên Nguyên đan, có thể coi như tiền bạc hối đoái, không nghĩ tới, cái đồ chơi này, nhân gia căn bản không nhận!"),t("br"),t("br"),n._v("Lăng Vân kiếm các, tu vi cũng không trọng yếu, trọng yếu là, lĩnh ngộ kiếm ý, kiếm pháp, chỉ cần hai thứ này mạnh lên, cùng cấp bậc nổi bật, tự nhiên sẽ đạt được tông môn dốc sức nâng đỡ, tài nguyên không thiếu."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Tiên Nguyên đan loại này tăng cao tu vi dược vật, ở đây ngược lại cũng không trân quý."),t("br"),t("br"),n._v("“Thiếu gia, nếu không... Chúng ta cướp a, vừa rồi ta xem mấy người, đều không có thực lực của ngươi mạnh, chỉ cần nghĩ biện pháp đem bọn hắn lừa gạt đến xó xỉnh, giết chết cướp sạch, cái gì đều có...”"),t("br"),t("br"),n._v("Nhìn thấy thiếu gia một mặt ưu sầu, Tào Thành Lập đi tới trước mặt, hạ giọng."),t("br"),t("br"),n._v("“Cướp... Cướp em gái ngươi!”"),t("br"),t("br"),n._v("Trương Huyền suýt chút nữa không có tức ngất đi."),t("br"),t("br"),n._v("Có thể sử dụng bình thường tư duy cân nhắc sự tình không?"),t("br"),t("br"),n._v("Nơi này là Lăng Vân kiếm các, môn quy không cho phép cướp đoạt... Thực có can đảm động thủ, chết cũng không biết chết như thế nào!"),t("br"),t("br"),n._v("Còn lừa gạt đến xó xỉnh... Ngươi cho rằng nội môn đệ tử đều là thằng ngốc a?"),t("br"),t("br"),n._v("Có thể trở thành Lăng Vân kiếm các tu luyện giả, cái nào không có chút thủ đoạn, cướp người khác, không bị người khác đoạt cũng rất không tệ..."),t("br"),t("br"),n._v("“Nếu không như vậy... Vừa rồi ta nhìn thấy một cái xinh đẹp nội môn đệ tử ngay tại bán ra đồ vật, ta đi thi triển mỹ nam kế...”"),t("br"),t("br"),n._v("Tào Thành Lập lời thề son sắt."),t("br"),t("br"),n._v("Trương Huyền che cái trán, thật hận không thể một chân đem cái này hai hàng tại chỗ đạp chết."),t("br"),t("br"),n._v("“Ngươi đi thi triển a, đừng bị đánh chết là được...”"),t("br"),t("br"),n._v("Không thèm để ý cái này hai hàng, dọc theo quầy hàng đi thẳng về phía trước."),t("br"),t("br"),n._v("Lại hỏi thăm mấy nhà, nơi này thông thần ngọc phù, cùng đối phương nói giá cả đồng dạng, đại khái hai mươi cái kiếm các tệ, Tiên Nguyên đan cũng quả thực vì hai cái kiếm các tệ, cả hai chênh lệch trọn vẹn gấp mười lần."),t("br"),t("br"),n._v("Nói cách khác, coi như có thể lấy vật đổi vật, trong tay hắn Tiên Nguyên đan, cũng không đủ mua sắm một khối thông thần ngọc phù."),t("br"),t("br"),n._v("“Lấy thông thần ngọc phù là tiêu chuẩn lời nói, vì sao Tiên Nguyên đan giá cả chênh lệch như vậy lớn?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, hỏi hướng trong đó một vị ngay tại bán ra vật tư nội môn thế hệ sau."),t("br"),t("br"),n._v("“Ngươi nói là địa phương nhỏ thông thần ngọc phù đi! Cái loại này thông thần ngọc phù, ngầm thừa nhận thực lực, là Đại Thánh nhất trọng, giá cả rất thấp! Mà trong Kiếm các cửa đệ tử bán ra những này, thừa nhận làm Cổ Thánh nhất trọng, giá cả tự nhiên muốn cao gấp mấy chục lần! Đến hạch tâm, hạch tâm đệ tử sử dụng thông thần ngọc phù, ngầm thừa nhận tu vi vì Hư Tiên cảnh... Giá cả cao hơn!”"),t("br"),t("br"),n._v("Vị này nội môn đệ tử, lắc đầu, giải thích nói."),t("br"),t("br"),n._v("“Nha...”"),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Một mực nghi ngờ, không nghĩ tới đáp án đơn giản như vậy."),t("br"),t("br"),n._v("Hắn lấy thông thần ngọc phù giá cả là tiêu chuẩn, cân nhắc Tiên Nguyên đan giá cả, lại không biết, nơi này thông thần ngọc phù cùng ngoại giới đã khác biệt."),t("br"),t("br"),n._v("Đồng dạng tài khoản, một cái vương giả, một cái thanh đồng, giá cả có thể giống nhau ư?"),t("br"),t("br"),n._v("Lại hỏi thăm một hồi, quen thuộc một ít, Trương Huyền nhịn không được hỏi: “Ta muốn mua sắm một cái thông thần ngọc phù, không biết nhưng có cái gì tốt kiếm lấy kiếm các tệ phương pháp?”"),t("br"),t("br"),n._v("“Phương pháp tốt? Ta cũng đang tìm... Có thể tìm tới, liền không ở nơi này bày quầy bán hàng...” Đối diện nội môn đệ tử mí mắt một phen."),t("br"),t("br"),n._v("Trương Huyền xấu hổ."),t("br"),t("br"),n._v("Còn tưởng rằng sẽ cùng một ít thương hội đồng dạng, trả lời cái vấn đề loại liền có thể kiếm tiền, hiện tại xem ra, cả nghĩ quá rồi."),t("br"),t("br"),n._v("“Hiện tại chỉ có bảy viên Tiên Nguyên đan, một cái trữ vật giới chỉ... Dựa vào bán thứ này, kiếm lấy hai mươi viên kiếm các tệ, đoán chừng không làm được...”"),t("br"),t("br"),n._v("Thực sự không được, liền giống như những người khác bày quầy bán hàng, có thể... Trên người hắn không có bảo vật ah!"),t("br"),t("br"),n._v("Trữ vật giới chỉ, tại Huyền Giang thành thuộc về tư nguyên khan hiếm, nhưng ở nơi này, hầu như mỗi cái nội môn đệ tử đều nhân thủ một cái, bây giờ không có, thậm chí có thể tại tông môn thuê... Căn bản cũng không có thị trường!"),t("br"),t("br"),n._v("Còn Tiên Nguyên đan... Hết thảy bảy viên, toàn bộ bán, cũng mua không nổi thông thần ngọc phù!"),t("br"),t("br"),n._v("“Đúng rồi, hồ lô nước tắm, đối trị liệu thương thế có hiệu quả, nếu như có thể đem thứ này bán đi, nên... Có giá trị không nhỏ!”"),t("br"),t("br"),n._v("Rầu rĩ rất lâu, đột nhiên ánh mắt sáng lên."),t("br"),t("br"),n._v("Đi tới Thượng Thương hết thảy chừng mười ngày công phu, chưa hề từng đi ra ngoài, cũng hầu như không cùng người chiến đấu qua, nghèo rớt mùng tơi, muốn nói còn có cái gì đồ vật, lần trước nấu hồ lô lưu lại nước tắm, còn có mấy bình tử."),t("br"),t("br"),n._v("Thứ này, dùng để chữa thương, nắm giữ kỳ hiệu, thậm chí so một ít đan dược, cũng phải có dùng nhiều lắm, nếu là có thể bán đi, làm không cẩn thận, có thể mua được thông thần ngọc phù."),t("br"),n._v(" “Liền như thế!”"),t("br"),t("br"),n._v("Nghĩ đến cái này, lại không do dự, từ trong giới chỉ lấy chút vải vóc, tạm thời dựng cái quầy hàng, đem chứa đựng hồ lô nước tắm bình ngọc, lấy ra ngoài, bày ở bên trên, dừng lại một chút, lại lấy ra một tấm vải, viết “Chữa thương thần dược” bốn chữ lớn, ngồi xuống."),t("br"),t("br"),n._v("“Chữa thương thần dược? Huynh đệ, ngươi thật sự là không sợ chết... ‘Thần’ cái chữ này, không phải là tuỳ có thể dùng! Nếu không, một khi bị Thần Linh phát hiện, sẽ trực tiếp bị tại chỗ xoá bỏ!”"),t("br"),t("br"),n._v("Bên cạnh nội môn đệ tử, nhìn thấy hắn làm ra như vậy một cái bảng hiệu, nhịn không được lắc đầu."),t("br"),t("br"),n._v("“Không thể tuỳ sử dụng? Cái kia... Kiếm các còn có Kiếm Thần đường!” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Kiếm Thần đường cái này ‘Thần’ chữ, là năm đó kiếm các khai phái tổ sư, từ Thần điện dựa thực lực đoạt tới, bởi vì chỉ là nửa chữ, cho nên, chỉ có thể mệnh danh một cái bình thường phân đường, mà không thể dùng tại tông môn! Đại lục ở bên trên, chỉ có Thông Thần điện, mới có tư cách, nghe nói là năm đó vị điện chủ này, một người đơn thương độc mã, xông vào Thần điện, đối mặt Thần Linh, cứng rắn cướp lại!”"),t("br"),t("br"),n._v("Vị này nội môn đệ tử giải thích."),t("br"),t("br"),n._v("“Chữ... Còn cần đoạt?”"),t("br"),t("br"),n._v("Tràn đầy kỳ lạ, Trương Huyền hỏi thăm một hồi, giờ mới hiểu được chuyện gì xảy ra, nhịn không được hít khí lạnh."),t("br"),t("br"),n._v("Trong mắt của hắn Thượng Thương, mệnh danh Di Khí đại lục, nghe nói là gặp Thần Linh vứt bỏ, bởi vậy, bất luận là sáu đại tông môn, hay là người bình thường, cũng không có tư cách sử dụng “Thần” chữ, muốn sử dụng, chỉ có một cái phương pháp, đó chính là... Nghĩ biện pháp đi vào Thần điện, từ Thần Linh trong tay, cứ thế mà đem chữ đoạt lại!"),t("br"),t("br"),n._v("Mấy ngàn năm qua, sáu đại tông môn thêm vào Thông Thần điện, cũng chỉ có Lăng Vân kiếm các, từng chiếm được nửa chữ, Thông Thần điện từng chiếm được một chữ... Những tông môn khác, cho dù cũng rất mạnh mẽ, nhưng chưa hề thành công qua!"),t("br"),t("br"),n._v("Đoạt cái chữ khó như vậy, Thần điện mạnh mẽ, có thể tưởng tượng được."),t("br"),t("br"),n._v("Nhìn tới “Thần” chữ, quả thực không phải dùng linh tinh."),t("br"),t("br"),n._v("Đem “Thần dược” hai chữ, đổi thành “Tiên dược”, lại tại phía dưới viết một chuyến “Đủ loại thương thế, thuốc đến bệnh trừ!”, lúc này mới lần nữa đợi chờ khách hàng."),t("br"),t("br"),n._v("Tiếp cận một canh giờ, không ít người sang đây xem qua, nhưng mở ra nắp bình, liền tất cả đều lắc đầu rời đi."),t("br"),t("br"),n._v("Người khác thuốc chữa thương, đều linh khí bức người, hắn giống như là phổ thông nước sôi, có thể có người mua mới kêu kì quái."),t("br"),t("br"),n._v("Thiên tài dần dần đen xuống, ngay tại Trương Huyền cảm giác, bán đồ quả nhiên không có gì tiền đồ thời điểm, một cái nữ hài đi tới."),t("br"),t("br"),n._v("Hai mươi tuổi hình dáng, dáng người cao gầy, dung mạo cực kỳ mỹ lệ, mặc dù so ra kém Triệu Nhã đám người, nhưng cũng xem như trong trăm có một mỹ nữ, chỉ là cau mày, tựa hồ tại vì sự tình gì lo lắng."),t("br"),t("br"),n._v("“Ngươi thuốc chữa thương, thật có hiệu quả?”"),t("br"),t("br"),n._v("Chuyển vài vòng, đi tới Trương Huyền trước mặt, ánh mắt sáng rực nhìn lại."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật đầu: “Bất kỳ thương thế đều có thể chữa khỏi! Trị không khỏi, giá gốc trả lại...”"),t("br"),t("br"),n._v("“Ta một vị người thân, bị tiên thú gây thương tích, nuốt vào không ít chữa thương dược vật, đều vô dụng chỗ, ngươi cái này... Bán thế nào?” Nữ hài dừng lại một chút, nói."),t("br"),t("br"),n._v("Thật ra thì, nàng cũng là cái gì cũng có thể thử khi tuyệt vọng, dùng không biết bao nhiêu đan dược đều vô dụng, nhìn thấy vị này, vậy mà viết ra chữa thương tiên dược, hơn nữa thuốc đến bệnh trừ chữ, ôm may mắn tâm lý, tới tìm hiểu."),t("br"),t("br"),n._v("Dù sao chính là thử một chút, không được vậy thì thôi."),t("br"),t("br"),n._v("“Một bình, hai mươi viên kiếm các tệ!” Trương Huyền nói."),t("br"),t("br"),n._v("“Hai mươi kiếm các tệ? Ngươi tại sao không đi cướp?”"),t("br"),t("br"),n._v("Nữ hài giật nảy mình."),t("br"),t("br"),n._v("Phổ thông chữa thương đan dược, cho dù là Hư Tiên cảnh, cũng không cao hơn mười cái kiếm các tệ, như vậy một bình, cùng nước sạch không khác đồ vật, vậy mà trực tiếp ra giá hai mươi... Quá đen đi!"),t("br"),t("br"),n._v("“Tiền nào đồ nấy, đều là nội môn đệ tử, ta dám gọi cái giá tiền này, khẳng định có đặc thù hiệu quả trị liệu, nếu không... Còn không bị tươi sống giết?” Trương Huyền mí mắt vừa nhấc: “Ngươi có thể lựa chọn không mua, nhưng cái giá tiền này, sẽ không thay đổi!”"),t("br"),t("br"),n._v("Nói xong, con mắt đóng lại, một bộ có thích mua hay không, không mua cuốn xéo hình dáng."),t("br"),t("br"),n._v("Tuy là chưa làm qua buôn bán, nhưng nhìn qua vô số thư tịch, biết thương nhân sáo lộ cùng mua đồ trong lòng, đuổi theo vội vàng mua, đối phương chưa hẳn muốn, thậm chí còn có thể nửa tin nửa ngờ, cho rằng có phải hay không gạt người đồ vật."),t("br"),t("br"),n._v("Bắt được giá đỡ, cho người ta một loại cảm giác cao thâm khó lường, ngược lại càng khiến người ta tin tưởng."),t("br"),t("br"),n._v("Huống chi, hắn thứ này, vốn cũng không phải là giả."),t("br"),t("br"),n._v("Quả nhiên, nhìn thấy hắn bộ dáng này, nữ hài dừng lại."),t("br"),t("br"),n._v("Nếu như không còn thích hợp dược vật trị liệu, nàng vị kia người thân, có thể sẽ tử vong... Bằng không thì cũng sẽ không gấp gáp chạy đến nội môn đệ tử giao dịch địa phương đến mua thuốc..."),t("br"),t("br"),n._v("Có thể, lấy nàng nhãn lực, thực sự nhìn không ra cái bình này nước, có cái gì công hiệu."),t("br"),t("br"),n._v("“Tốt, ta mua, nếu như không có công hiệu, sẽ tới tìm ngươi, để ngươi biết, lừa gạt ta kết quả!” Chần chờ phút chốc, nữ hài cầm lấy một bình, khẽ nói."),t("br"),t("br"),n._v("“Yên tâm, không có công hiệu, không cần ngươi nói chuyện, ta cũng sẽ phá hủy bản thân sạp hàng...”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Dù sao hắn cũng chỉ là định bán một lần, có thông thần ngọc phù, còn ưu sầu tiền sao?"),t("br"),t("br"),n._v("Nói đùa!"),t("br"),t("br"),n._v("Công khai thi đấu cày tiền, hắn tuyệt đối là chuyên nghiệp!"),t("br"),t("br"),n._v("“Vậy thì tốt...”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, nữ hài cầm lấy bình ngọc, lấy ra một cái túi tiện tay ném tới: “Đây là 20 kiếm các tệ!”"),t("br"),t("br"),n._v("Nói xong, thân thể mềm mại chuyển một cái, trực tiếp rời đi."),t("br"),t("br"),n._v("Mở túi ra, phát hiện bên trong quả nhiên có 20 hình kiếm kim tệ, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Còn tưởng rằng, còn cần chờ lâu chút thời gian, không nghĩ tới dễ dàng như vậy."),t("br"),t("br"),n._v("Đứng dậy, đang muốn thu quán, tìm địa phương mua cái ngọc phù, chỉ thấy bên cạnh nội môn đệ tử một mặt đồng tình nhìn lại: “Nhanh lên một chút cút đi, nghĩ biện pháp rời đi kiếm các a, nếu không, ta sợ ngươi rất nhanh sẽ bị đánh thành thịt vụn!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1964"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1966"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);