(window.webpackJsonp=window.webpackJsonp||[]).push([[1088],{1445:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1953-bach-diep-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1953-bach-diep-truong-lao"}},[n._v("#")]),n._v(" Chương 1953: Bạch Diệp trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Bạch Diệp trưởng lão"),t("br"),t("br"),n._v("“Huynh đệ cớ gì nói ra lời ấy?”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("“Cớ gì nói ra lời ấy?” Vị này nội môn đệ tử, một mặt đồng cảm: “Ngươi cũng đã biết vừa rồi nữ hài kia là ai?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Tông môn tam đại trưởng lão một trong, Bạch Diệp trưởng lão cháu gái ruột, Bạch Nguyễn Khanh!”"),t("br"),t("br"),n._v("Vị này nội môn đệ tử, lắc đầu: “Nói thật cho ngươi biết, vừa rồi thoạt nhìn rất dịu dàng, trên thực tế nhưng là nữ bạo long, hạch tâm đệ tử bên trong, đều tên tuổi lẫy lừng, không ai dám trêu chọc! Dám tốn tiền nhiều như vậy, mua ngươi một bình phá nước, chỉ cần ngươi còn tại tông môn, liền chạy không xong... Không rời đi tông môn, tốt nhất lập tức về nhà, đem tang sự chuẩn bị một chút, nếu không... Ai cũng cứu không được ngươi!”"),t("br"),t("br"),n._v("“Nữ bạo long?” Trương Huyền lông mày nhảy dựng."),t("br"),t("br"),n._v("Vừa rồi đối phương thái độ ôn hoà, mảy may không nhìn ra ah!"),t("br"),t("br"),n._v("“Làm sao, không tin? Năm ngoái hạch tâm đệ tử Tô Thông, chạy tới hướng nàng thổ lộ, bị kéo qua đi khiêu chiến, phía dưới tại chỗ liền bị đá bể nát, hiện tại cũng không có khôi phục! Hạch tâm đệ tử tấm vui mừng, bởi vì cái gì sự tình đắc tội, cũng bị đánh một tháng không xuống giường được, muốn bao nhiêu thê thảm liền nhiều thê thảm; A, đúng, hạch tâm đệ tử Tiết Hải, bởi vì bán thuốc giả, bị nhét vào trong nước sông, cứ thế mà chìm đến mất đi nửa cái mạng, còn không có kết thúc, thậm chí chém xuống nửa cái đầu lưỡi... Hạch tâm đệ tử đều như vậy, ngươi một cái nội môn đệ tử, lại dám lừa nàng, không phải tự tìm cái chết là làm gì?”"),t("br"),t("br"),n._v("Bên cạnh nội môn đệ tử hừ lạnh."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền vò đầu."),t("br"),t("br"),n._v("Chỉ xem đến đối phương chân rất dài, rất thẳng, lớn lên cũng rất đẹp, thật đúng là không nhìn ra, tàn nhẫn như vậy."),t("br"),t("br"),n._v("“Nàng thực lực gì? Ngươi biết không?”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, hỏi."),t("br"),t("br"),n._v("Thực lực nếu là cùng mình tương đương, thật cũng không sợ! Nói thật, cùng cấp bậc, loại trừ phân thân, thật đúng là chưa sợ qua ai!"),t("br"),t("br"),n._v("“Nàng năm ngoái cùng Tô Thông tỷ thí thời điểm, liền đi đến Hư Tiên cảnh đỉnh phong, năm nay cảnh giới gì, không rõ ràng!” Bên cạnh nội môn đệ tử, đem quầy hàng kéo xa xa: “Ta trước cách ngươi xa một chút, ngộ nhỡ đến tìm phiền phức, cũng có thể trốn được, không bị tai bay vạ gió! Ai, thời đại này, buôn bán càng ngày càng khó làm, bản thân khiêm tốn, nhưng muốn đề phòng người khác tìm đường chết... Thật sự là ưu sầu người ah!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Trương Huyền im lặng."),t("br"),t("br"),n._v("“Coi như, không ở nơi này đợi, ta mua sắm xong thông thần ngọc phù, cũng quả thực muốn rời khỏi...”"),t("br"),t("br"),n._v("Chẳng muốn cùng đối phương tiếp tục nói nhiều, đem quầy hàng thu lại, Trương Huyền đứng dậy."),t("br"),t("br"),n._v("Chạy đến giao dịch này thị trường, mục đích lớn nhất chính là kiếm tiền mua sắm thông thần ngọc phù, hiện tại tiền đủ, mua lấy đồ vật, liền có thể trở về."),t("br"),t("br"),n._v("“Bán một cái thông thần ngọc phù cho ta, đây là hai mươi Kiếm Các tệ!”"),t("br"),t("br"),n._v("Đi tới bên cạnh nội môn đệ tử trước mặt, đem túi tiền ném xuống rồi."),t("br"),t("br"),n._v("“Hai mươi không bán... Lên giá, muốn hai mươi lăm!” Vị này nội môn đệ tử lắc đầu."),t("br"),t("br"),n._v("“Tăng giá? Vừa rồi ngươi không vừa nói hai mươi ư?” Trương Huyền nhịn không được cau mày."),t("br"),t("br"),n._v("“Mới vừa rồi là hai mươi, nhưng bây giờ ngươi đắc tội Bạch Nguyễn Khanh, đã đợi tại phản chỗ tử hình... Bán cho ngươi đồ vật, ngộ nhỡ bị nàng biết, tìm ta phiền phức làm cái gì? Tăng thêm năm cái, là tâm lý an ủi phí, cũng là mua thuốc trị thương tiền, cũng chính là ta và ngươi quen, mới thêm tiền bán cho ngươi, đổi lại người khác, cũng chưa chắc chịu bán!” Vị này nội môn đệ tử lý trực khí tráng nói."),t("br"),t("br"),n._v("“Ngươi...” Trương Huyền da mặt co rúm."),t("br"),t("br"),n._v("Vị này nữ bạo long, thật có đáng sợ như vậy?"),t("br"),t("br"),n._v("Tràn đầy không tin, xoay người hướng cái khác có thông thần ngọc phù quầy hàng đi đến, kết quả một vòng xuống, quả nhiên, lại không có một người nguyện ý bán ra."),t("br"),t("br"),n._v("Rất hiển nhiên, bản thân đắc tội vị kia nữ bạo long tin tức, đã truyền ra, vì phòng ngừa ngoài ý muốn, lại không có người cùng hắn làm ăn."),t("br"),t("br"),n._v("“Đám người kia...”"),t("br"),t("br"),n._v("Hàm răng ngứa ngáy, Trương Huyền thật hận không thể cùng Tào Thành Lập nói đồng dạng, đem những này người đoạt."),t("br"),t("br"),n._v("Còn tưởng rằng tiến đến hai mươi Kiếm Các tệ, có thể mua, không nghĩ tới, đồng dạng lấy không đến tay."),t("br"),t("br"),n._v("“Coi như...”"),t("br"),t("br"),n._v("Đầy mặt bất đắc dĩ, đang lo lắng có muốn hay không tìm những biện pháp khác, liền nhìn vừa đến liền bị hắn đuổi đi Tào Thành Lập đi tới trước mặt: “Thiếu gia, đây là ngươi muốn thông thần ngọc phù, cho...”"),t("br"),t("br"),n._v("Nói xong, đưa hai cái tới."),t("br"),t("br"),n._v("Nắm lại vật trong tay, Trương Huyền con mắt trợn tròn."),t("br"),t("br"),n._v("Hắn bày quầy bán hàng, suy nghĩ cả nửa ngày, liền một cái đều không có mua lại, cái này không đáng tin cậy thuộc hạ, làm sao một chút làm hai cái?"),t("br"),t("br"),n._v("“Không phải mới vừa cùng ngươi nói ư? Ta nhìn trúng một cái xinh đẹp nội môn đệ tử...” Gãi gãi đầu, Tào Thành Lập cười hắc hắc, vừa định giải thích, Trương Huyền liền thấy một thứ đại khái chừng ba trăm cân nữ tử đi tới: “Thân yêu, chúng ta nhanh lên một chút a, nhà ta giường vừa lớn vừa tròn, mấu chốt còn rất mềm a...”"),t("br"),t("br"),n._v("“Tốt, lập tức...” Tào Thành Lập liên tục gật đầu, ngay sau đó khoát tay áo: “Thiếu gia, ta trước đi qua một chuyến, quay đầu lại cùng ngươi nói rõ chi tiết...”"),t("br"),t("br"),n._v("Nói xong, một mặt hưng phấn đi theo nữ tử đi ra ngoài."),t("br"),t("br"),n._v("“...” Trương Huyền lần nữa che cái trán."),t("br"),t("br"),n._v("Mẹ nó..."),t("br"),t("br"),n._v("Cái gì cùng cái gì ah!"),t("br"),t("br"),n._v("Cái này gọi đẹp đẽ?"),t("br"),t("br"),n._v("Thế nào cảm giác, trước đó tại Huyền Giang thành thuần phục con ngựa kia, cũng so cái này đẹp mắt?"),t("br"),t("br"),n._v("Háo sắc liền háo sắc, chí ít có điểm phẩm vị ah?"),t("br"),t("br"),n._v("Chẳng qua, tuy là thưởng thức không được tốt lắm, hiệu suất làm việc, vẫn là rất mạnh, bản thân chơi đùa hơn một canh giờ, liền một cái thông thần ngọc phù đều không có được, đối phương không chỉ được hai cái, liền giường đều lên..."),t("br"),t("br"),n._v("Người so với người, tức chết người!"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền đầy mặt bất đắc dĩ, chậm rãi hướng chỗ ở đi đến."),t("br"),t("br"),n._v("..."),t("br"),n._v("Bạch Diệp trưởng lão, cư trú sân nhỏ."),t("br"),t("br"),n._v("Một tiếng sắc nhọn thú minh, Bạch Nguyễn Khanh từ lưng thú bên trên nhảy xuống tới, đẩy cửa đi vào đại điện."),t("br"),t("br"),n._v("“Tiểu thư...”"),t("br"),t("br"),n._v("Một cái lão giả, tiến lên đón."),t("br"),t("br"),n._v("Bạch Diệp trưởng lão người hầu, Bạch Phong."),t("br"),t("br"),n._v("Cùng Bạch Diệp trưởng lão cùng nhau lớn lên, đồng thời đi vào Lăng Vân kiếm các, mặc dù tại tông môn không có danh khí gì, nhưng một thân tu vi, ai cũng không biết rất cao thâm."),t("br"),t("br"),n._v("Biết tiểu thư, lại đi ra ngoài tìm kiếm chữa thương dược vật, than thở một tiếng, Bạch Phong đi tới trước mặt: “Tâm tình của tiểu thư, ta cũng biết, nhưng lão gia tổn thương, là tại Hãm Không chi thành lưu lại, mang theo quỷ dị sức mạnh khó lường, tông môn tất cả phương pháp đều dùng qua, tiểu thư tại nội môn, ngoại môn mua những thuốc này, có thể có cái gì hiệu quả?”"),t("br"),t("br"),n._v("Nhịn không được lắc đầu."),t("br"),t("br"),n._v("Hãm Không chi thành, đại lục nơi thần bí nhất một trong, bảo vật mặc dù nhiều, nhưng nguy hiểm cũng lớn."),t("br"),t("br"),n._v("Lão gia của mình, chỉ là phổ thông thương thế, dựa vào hắn tam đại trưởng lão thân phận, cho dù trị không khỏi, chí ít có thể bảo đảm, qua cuộc sống của người bình thường, đáng tiếc thương thế quá nặng, đã dùng hết tất cả phương pháp, đều không có hiệu quả, hơn nữa càng ngày càng nghiêm trọng, không có gì bất ngờ xảy ra, cũng lại không kiên trì được mấy ngày."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, tiểu thư mỗi ngày đều ra ngoài tìm kiếm đủ loại cổ phương, kỳ trân diệu dược, để xuất hiện kỳ tích... Trên thực tế, chỉ là tốn công vô ích thôi!"),t("br"),t("br"),n._v("“Ta biết những này, chỉ là...” Bạch Nguyễn Khanh tú quyền xiết chặt."),t("br"),t("br"),n._v("Đối phương nói những này, nàng làm sao không biết được."),t("br"),t("br"),n._v("Có thể để nàng trơ mắt nhìn thân nhân duy nhất, liền như thế chết đi, thực sự không cam tâm."),t("br"),t("br"),n._v("“Ai!”"),t("br"),t("br"),n._v("Biết tiểu thư tâm tư, Bạch Phong than thở một tiếng: “Tiểu thư lần này lại tìm đến thuốc gì, có thể hay không để cho ta nhìn một chút!”"),t("br"),t("br"),n._v("“Là cái này...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Bạch Nguyễn Khanh đem vừa mua được nước tắm đưa tới."),t("br"),t("br"),n._v("Tiện tay nhận lấy, mở ra nắp bình, Bạch Phong ngửi một cái, nhịn không được lắc đầu: “Tiểu thư, đây là thuốc? Ta sống một trăm năm mươi tuổi, đủ loại dược vật, gặp qua không ít... Điểm này linh khí đều không có, sẽ không phải là có người cố ý lừa gạt a!”"),t("br"),t("br"),n._v("Dược vật, muốn có dược hiệu, nhất định muốn có linh khí ở bên trong, trước mắt trong bình ngọc đồ vật, cùng nước sạch đồng dạng, một chút linh lực đều không có, làm sao có thể là thuốc?"),t("br"),t("br"),n._v("Tiểu thư sẽ không phải là quá mức gấp gáp, bị người lừa a!"),t("br"),t("br"),n._v("“Là một vị nội môn đệ tử bán cho ta, nghe nói là chữa thương tiên dược, đối bất kỳ thương thế đều có rất tốt hiệu quả...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh gật gật đầu."),t("br"),t("br"),n._v("Nàng cầm tới thời điểm cũng hoài nghi tới, nhưng gia gia sắp không chịu nổi, dù là có một chút hi vọng, cũng không nguyện ý bỏ qua."),t("br"),t("br"),n._v("Huống chi, bán thuốc người, một mặt tự tin, không giống nói dối!"),t("br"),t("br"),n._v("Dù sao, danh tiếng của nàng như vậy lớn, nội môn, hạch tâm, không ai không biết, không người không biết... Chỉ cần nhìn thấy, nên rõ ràng lừa gạt kết quả, biết những này, còn dám như vậy khoe khoang khoác lác! Khẳng định có nhất định dựa dẫm!"),t("br"),t("br"),n._v("“Hiệu quả? Một chút linh khí đều không có, liền dám bán cho ngươi... Người này lá gan thật đúng là đủ lớn! Bao nhiêu tiền?”"),t("br"),t("br"),n._v("Bạch Phong khẽ nói."),t("br"),t("br"),n._v("“20 Kiếm Các tệ!” Bạch Nguyễn Khanh nói."),t("br"),t("br"),n._v("“20?”"),t("br"),t("br"),n._v("Bạch Phong sững sờ, ngay sau đó nổi giận đùng đùng: “Tiểu thư, ta cơ bản có thể xác nhận, ngươi khẳng định bị lừa! 20 Kiếm Các tệ, hạ phẩm tiên nguyên đan cũng có thể mua sắm mười cái, chỉ mua như vậy một bình đồ vật, đối phương là cái nào trưởng lão quản lý nội môn đệ tử? Ta hiện tại liền đi tìm bọn hắn, để cho bọn họ cho cái bàn giao! Chẳng lẽ, Bạch trưởng lão bị thương, liền có thể tùy tiện bắt nạt?”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Cơn giận dữ bên dưới, lực lượng hùng hồn từ huyệt đạo toả ra, toàn bộ phủ đệ, đều đang không ngừng lay động, tựa như lúc nào cũng sẽ không chịu nổi hắn bạo ngược lực lượng."),t("br"),t("br"),n._v("Tuy là Bạch trưởng lão bị thương nặng, bất cứ lúc nào cũng sẽ không chịu nổi, nhưng còn có hắn tại, dám khi dễ như vậy tiểu thư, chính là tự tìm cái chết!"),t("br"),t("br"),n._v("“Ta... Ta là tự nguyện mua, cũng không nhận lừa gạt...”"),t("br"),t("br"),n._v("Bạch Nguyễn Khanh hơi đỏ mặt."),t("br"),t("br"),n._v("Đối phương rõ ràng nói, có thích mua hay không, cũng không ép buộc, cũng không nhiều lời."),t("br"),t("br"),n._v("“Tự nguyện? Khẳng định là hoa ngôn xảo ngữ...” Bạch Phong vẫn như cũ lửa giận ngập trời."),t("br"),t("br"),n._v("“Tốt, mặc kệ có hay không linh khí, nếu đã mua được, liền cho gia gia thử một chút...”"),t("br"),t("br"),n._v("Cắt đứt đối phương, Bạch Nguyễn Khanh nhấc chân đi về phòng, chỉ chốc lát đi tới trước giường."),t("br"),t("br"),n._v("Nằm trên giường một cái lão giả, khuôn mặt đã có vẻ hơi tiều tụy, ngực một cái to bằng cái bát vết thương, đã bắt đầu thối rữa, tản mát ra khó ngửi khí tức."),t("br"),t("br"),n._v("Vị lão giả này, giờ phút này chính hôn mê bất tỉnh, dường như hai người vừa rồi đối thoại, một chút cũng không nghe thấy."),t("br"),t("br"),n._v("“Gia gia, ngươi nhất định phải tốt lên!”"),t("br"),t("br"),n._v("Đã không có biện pháp nào khác, cũng không quan trọng thí nghiệm thuốc các loại bước đi, đem lão giả đỡ dậy, Bạch Nguyễn Khanh đem trong bình ngọc chất lỏng, từng chút một rót vào đối phương cổ họng."),t("br"),t("br"),n._v("Uống xong, đợi một hồi, thấy vết thương vẫn như cũ thối rữa, không có bất kỳ cái gì khởi sắc, Bạch Nguyễn Khanh vẻ mặt ảm đạm xuống."),t("br"),t("br"),n._v("“Một chút linh khí đều không có, khẳng định là giả... Tiểu thư, về sau tuyệt đối không nên tin tưởng những này, trong tông môn am hiểu y thuật trưởng lão đều thử qua, không hiệu quả gì, một ít phổ thông đệ tử bán ra đồ vật, làm sao có thể tin tưởng...”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Bạch Phong lắc đầu hừ lạnh, tiếng nói còn không có kết thúc, liền nghe đến một cái hư nhược thanh âm chậm rãi vang lên."),t("br"),t("br"),n._v("“Khụ khụ... Các ngươi cho ta uống cái gì?”"),t("br"),t("br"),n._v("Thân thể đồng thời cứng đờ, Bạch Nguyễn Khanh cùng Bạch Phong vội vàng nhìn lại, chỉ thấy hôn mê bất tỉnh Bạch Diệp trưởng lão, chẳng biết lúc nào mở mắt."),t("br"),t("br"),n._v("Cứ việc vẫn như cũ suy yếu, cũng đã tỉnh táo lại."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1965"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1967"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);